(window.webpackJsonp=window.webpackJsonp||[]).push([[2004],{2359:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-726-lac-nhuoc-hi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-726-lac-nhuoc-hi"}},[n._v("#")]),n._v(" Chương 726: Lạc Nhược Hi")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 726: Lạc Nhược Hi"),t("br"),t("br"),n._v("“Tốt lắm, an bài bọn họ đi học viện ở lại, ngày mai bắt đầu lựa chọn lão sư...”"),t("br"),t("br"),n._v("Đem sự tình nói xong, an bài rất nhiều lão sinh, dẫn đầu tân sinh đi học viện ở lại, Mi trưởng lão lúc này mới thân thể nhoáng một cái, lần nữa trở lại tiểu đình."),t("br"),t("br"),n._v("“Trương sư, ngươi cũng đừng cùng bọn hắn cùng một chỗ, loại kia tập thể ký túc xá, cũng không cách nào tu luyện, trước cùng ta về học viện, ta an bài cho ngươi một cái đơn độc chỗ ở!”"),t("br"),t("br"),n._v("Làm thập đại trường lão, có tư cách an bài học sinh dừng chân điều kiện."),t("br"),t("br"),n._v("Trương sư dạng này tuyệt đỉnh thiên tài, tự nhiên không thể giống như những người khác, ở tại pha trộn trong túc xá."),t("br"),t("br"),n._v("“Đa tạ Mi trưởng lão...”"),t("br"),t("br"),n._v("Biết đối phương ý tứ, Trương Huyền vội vàng ôm quyền."),t("br"),t("br"),n._v("Bí mật của hắn quá nhiều, có thể đơn độc ở, tự nhiên tốt nhất."),t("br"),t("br"),n._v("Mạc Cao Viễn để cho mình Lang Gia Thánh thú tới, ba người đạp vào lưng thú, thẳng tắp hướng học viện bay đi."),t("br"),t("br"),n._v("Chuyện bên này, có rất nhiều lão sinh đi an bài, còn có Bạch sư tại, không có gì có thể lo lắng."),t("br"),t("br"),n._v("Thánh thú tốc độ cực nhanh, không lâu sau, liền thấy một cái cự đại sân trường xuất hiện tại tầm mắt."),t("br"),t("br"),n._v("Hồng Viễn danh sư học viện, dung nạp mười vạn danh sư, trong sân trường có hồ nước, sông núi, từ không trung nhìn lại, tựa như một tòa vương thành."),t("br"),t("br"),n._v("Thậm chí vạn quốc liên minh Vạn Quốc thành, tới so sánh, đều kém không chỉ một đoạn."),t("br"),t("br"),n._v("Khó trách nói nơi này hội tụ không biết bao nhiêu đế quốc tuyệt đỉnh thiên tài, chỉ từ diện tích bên trên nhìn, chính là những cái kia tiểu vương quốc, đế quốc không cách nào so sánh."),t("br"),t("br"),n._v("“Nơi này là tinh anh khu, là học viện mỗi cái niên cấp, ưu tú nhất danh sư mới có thể chỗ ở, ngươi cầm thủ lệnh của ta, tìm phụ trách học viên an bài ở lại, ta cùng Mạc đường chủ, đi đem sự kiện kia bẩm báo các trưởng lão khác!”"),t("br"),t("br"),n._v("t r ｕ y e n c u a t u i n e t"),t("br"),n._v("Chỉ chốc lát, Lang Gia Thánh thú tại một phiến khu vực trước mặt ngừng lại."),t("br"),t("br"),n._v("Mi trưởng lão cổ tay rung lên, một cái lệnh bài rơi vào Trương Huyền lòng bàn tay."),t("br"),t("br"),n._v("Tiếp nhận lệnh bài, Trương Huyền cúi đầu nhìn lại, phía dưới non xanh nước biếc, nguyên một đám tinh xảo tiểu viện song song đứng sừng sững."),t("br"),t("br"),n._v("Mỗi cái sân nhỏ, đều bố trí có Tụ Linh trận, linh khí vây quanh, còn không có tiến vào bên trong, liền cho người ta một loại sảng khoái tinh thần, tinh lực dồi dào cảm giác."),t("br"),t("br"),n._v("“Không hổ là tinh anh khu, quả nhiên không đơn giản!”"),t("br"),t("br"),n._v("Nhìn thoáng qua, Trương Huyền không khỏi gật đầu."),t("br"),t("br"),n._v("Những này Tụ Linh trận, so với hắn khắc hoạ trận bàn cao cấp hơn cỡ nào, tất cả đều đạt đến lục tinh, lại thêm đủ loại thủ đoạn, ở đây tu luyện, tốc độ tuyệt đối vượt xa địa phương khác."),t("br"),t("br"),n._v("Xem ra, có thể ở lại nơi này, đều là học viện ưu tú nhất thiên tài, nếu không, coi như danh sư học viện cùng Danh Sư đường tài đại khí thô, cũng cung ứng không nổi."),t("br"),t("br"),n._v("“Tốt lắm, ta đi xuống!”"),t("br"),t("br"),n._v("Không nói thêm lời, thân thể nhảy lên, từ Lang Gia Thánh thú trên lưng nhảy xuống."),t("br"),t("br"),n._v("Mi trưởng lão cùng Mạc Cao Viễn, thì liếc mắt nhìn nhau, dưới thân Thánh thú lóe lên, thẳng tắp bay tới đằng trước."),t("br"),t("br"),n._v("Dị Linh tộc nhân tin tức can hệ trọng đại, bọn họ chậm trễ không nổi."),t("br"),t("br"),n._v("Mấy phút đồng hồ sau, hai người tới một cái phòng kín mít."),t("br"),t("br"),n._v("Gian phòng này, tổng cộng mười một cái chỗ ngồi, mỗi một vị trí, đều đại biểu thân phận."),t("br"),t("br"),n._v("Chính là thập đại danh sư cùng lúc trước lão viện trưởng vị trí."),t("br"),t("br"),n._v("Gian phòng bên trong có trận pháp cường đại phong bế, bất kể đàm luận cái gì, cũng sẽ không tiết lộ ra ngoài."),t("br"),t("br"),n._v("“Khẩn cấp vời chúng ta tới, đến cùng chuyện gì?”"),t("br"),t("br"),n._v("Hai người mới vừa ngồi xuống, chỉ thấy mấy cái ông lão đẩy cửa đi đến, mỗi một cái đều cau mày."),t("br"),t("br"),n._v("Lần trước như vậy khẩn cấp triệu hoán, vẫn là lão viện trưởng mất tích, lần này lại đã xảy ra chuyện gì, để Mi trưởng lão cùng Mạc đường chủ hưng sư động chúng như vậy?"),t("br"),t("br"),n._v("“Ngồi trước, chờ tất cả mọi người đến lại nói!”"),t("br"),t("br"),n._v("Mi trưởng lão khoát tay áo."),t("br"),t("br"),n._v("Vừa mới tiến tới mấy cái ông lão đồng thời gật đầu, ngồi xuống."),t("br"),t("br"),n._v("Không đợi bao lâu, cửa phòng lần nữa đẩy ra, lại có mấy cái ông lão đi vào."),t("br"),t("br"),n._v("Chỉ chốc lát, mười một cái chỗ ngồi toàn bộ ngồi đầy."),t("br"),t("br"),n._v("“Mi trưởng lão, Mạc đường chủ, có chuyện gì mau chóng nói đi!”"),t("br"),t("br"),n._v("Một cái thoạt nhìn tuổi tác lớn nhất lão giả râu bạc trắng khoát tay áo."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Mi trưởng lão gật gật đầu cổ tay khẽ đảo, một cái ghi chép ngọc tinh xuất hiện tại lòng bàn tay: “Tại ta trước khi nói, mấy vị xem trước một chút cái này...”"),t("br"),t("br"),n._v("Nói xong ngón tay một chút, một mảnh vừa vặn chiến đấu lưu lại phế tích xuất hiện ở trước mặt mọi người."),t("br"),t("br"),n._v("Ở trên núi thời điểm, hắn liền sẽ thấy dấu vết, ghi xuống."),t("br"),t("br"),n._v("Nhìn thấy ngọc tinh bên trong cho thấy một vùng phế tích, tất cả trưởng lão đều có chút kỳ quái, có điều, nhìn một hồi, nguyên một đám lông mày nhăn lại, vẻ mặt trầm thấp."),t("br"),t("br"),n._v("“Đây là... Dị Linh tộc nhân chiến đấu dấu vết lưu lại? Tử Dương tiền bối thế nào?”"),t("br"),t("br"),n._v("Lão giả râu bạc trắng nhịn không được mở miệng."),t("br"),t("br"),n._v("Mọi người đồng loạt nhìn qua."),t("br"),t("br"),n._v("Thân là thập đại trường lão, lục tinh đỉnh phong danh sư, mặc dù chỉ là nhìn ghi chép ngọc tinh dấu vết lưu lại, vẫn như cũ có thể suy đoán ra chiến đấu hai bên là ai."),t("br"),t("br"),n._v("“Tử Dương tiền bối... Mất tích!”"),t("br"),t("br"),n._v("Mi trưởng lão nói."),t("br"),t("br"),n._v("“Mất tích?” Rất nhiều danh sư đồng thời sửng sốt, ngay cả Mạc Cao Viễn đều tràn đầy không thể tin được."),t("br"),t("br"),n._v("Trước đó lúc ở bên ngoài, Mi trưởng lão chỉ nói là phát hiện Dị Linh tộc nhân tung tích, cũng không nói tỉ mỉ, liền xem như hắn, đều không nghĩ tới, Tử Dương thú bị Dị Linh tộc nhân vây công, không rõ sống chết."),t("br"),t("br"),n._v("“Ừm, chuyện là như thế này...”"),t("br"),t("br"),n._v("Mi trưởng lão không dám giấu diếm, đem cùng Tử Dương thú ước định, cùng trên núi tình huống nói một lần."),t("br"),n._v("“Dám đến đến Hồng Viễn thành bắt đi Tử Dương Thánh thú, thật là thật to gan! Nếu như không đem những này Dị Linh tộc nhân bắt lấy, chúng ta danh sư học viện mặt mũi để ở nơi nào?”"),t("br"),t("br"),n._v("Lão giả râu bạc trắng khoát tay chặn lại, ánh mắt lấp lóe."),t("br"),t("br"),n._v("Thập đại danh sư, thống lĩnh toàn bộ học viện, cũng từng người phân quản một cái viện hệ, tựa như Mi trưởng lão, không riêng gì học viện trưởng lão, vẫn là Thuần Thú viện viện trưởng."),t("br"),t("br"),n._v("Mà vị này lão giả râu bạc trắng, chính là Vũ Kỹ viện viện trưởng, cần Trường Thanh."),t("br"),t("br"),n._v("“Bây giờ không phải là mặt mũi vấn đề, mà là Tử Dương tiền bối an toàn!” Một nữ tính trưởng lão khẽ nói."),t("br"),t("br"),n._v("Kinh Hồng viện viện trưởng, Vệ nhiễm tuyết."),t("br"),t("br"),n._v("“Tử Dương tiền bối, là lão viện trưởng thú sủng, từng cùng chúng ta cùng nhau xuất sinh nhập tử, giết chết qua vô số Dị Linh tộc nhân, công huân hiển hách, thật muốn xảy ra chuyện, về sau để cho chúng ta có mặt mũi nào, đi gặp viện trưởng? Lại có gì mặt mũi, tiếp tục đợi ở chỗ này, đứng hàng thập đại danh sư?”"),t("br"),t("br"),n._v("“Đúng vậy a! Tử Dương tiền bối an toàn không được có vấn đề, chúng ta hiện tại đầu mối gì đều không có, không thể lỗ mãng!”"),t("br"),t("br"),n._v("Mi trưởng lão khoát tay áo: “Hiện tại tốt nhất là mau chóng truy xét, dò xét tra rõ ràng tiền bối tung tích, nếu như còn sống, nghĩ biện pháp cứu ra, thật muốn bị giết, coi như đánh bạc tính mạng của ta cũng phải báo thù cho nó!”"),t("br"),t("br"),n._v("“Ta đồng ý, có điều, ta cảm thấy chuyện này tốt nhất là thầm tiến hành, một khi động tác quá lớn, rút dây động rừng, ta sợ ngược lại sẽ rơi vào đối phương bẫy rập.”"),t("br"),t("br"),n._v("“Đúng vậy a, Dị Linh tộc nhân xin đối Tử Dương tiền bối ra tay, rất có thể đối học viện chúng ta ra tay, truy tra đồng thời, tốt nhất cũng phải cẩn thận, tuyệt đối đừng trúng đối phương kế điệu hổ ly sơn!”"),t("br"),t("br"),n._v("Lại có hai vị trưởng lão nói."),t("br"),t("br"),n._v("“Các ngươi nói cũng chính là ta lo lắng, ta sợ bọn họ cố ý lưu lại dấu vết, chính là dẫn chúng ta mắc lừa...” Mi trưởng lão cau mày."),t("br"),t("br"),n._v("Lôi Viễn phong bên trên dấu vết lưu lại rõ ràng như thế, hắn một mực lo lắng là Dị Linh tộc nhân âm mưu, không dám coi thường làm bậy, nếu không, khẳng định khắp nơi dò xét, làm sao có thể chờ tới bây giờ."),t("br"),t("br"),n._v("“Tử Dương tiền bối trải qua vô số gian khổ, thực lực lại so chúng ta đều mạnh, nếu như nó đều đối đỡ không nổi, coi như tìm tới, cũng sợ là rất khó đem hắn cứu ra!”"),t("br"),t("br"),n._v("Thương nghị một lát, trong lúc nhất thời cũng tìm không thấy đầu mối, Đan viện viện trưởng mở miệng cắt ngang mọi người thảo luận."),t("br"),t("br"),n._v("Mọi người gật đầu."),t("br"),t("br"),n._v("Tử Dương Thánh thú là Thánh giả nhất trọng đỉnh phong, cùng cấp bậc của bọn hắn tương đồng, nhưng thân là Thánh thú, sức chiến đấu vượt xa mọi người, liền nó đều không chống đỡ được, liền dự cảnh công phu đều không có, đủ thấy đối thủ đáng sợ."),t("br"),t("br"),n._v("Như thế đối thủ cường đại, thật muốn tìm tới, liền coi như bọn họ lại có thể thế nào?"),t("br"),t("br"),n._v("“Qua mấy ngày, phong hào đế quốc Mộc sư sẽ đến chuyến này, xác lập mới viện trưởng, đồng thời điều tra lão viện trưởng nguyên nhân cái chết, đến lúc đó, chúng ta có thể đem chuyện này lặng lẽ báo cáo, để hắn hỗ trợ dò xét!”"),t("br"),t("br"),n._v("Đan viện viện trưởng nói."),t("br"),t("br"),n._v("“Cái này...” Mọi người chần chờ."),t("br"),t("br"),n._v("Gặp được sự tình liền lên báo, há không lộ vẻ bọn họ thập đại danh sư, không có năng lực?"),t("br"),t("br"),n._v("“Ta biết các ngươi lo lắng cái gì, có thể lặng yên không tiếng động vây khốn Tử Dương tiền bối, thực lực của đối phương đã vượt qua tưởng tượng của chúng ta, chuyện này nhất định phải lên báo!”"),t("br"),t("br"),n._v("Đan viện viện trưởng bàn tay vung lên."),t("br"),t("br"),n._v("Đan viện là danh sư học viện đệ nhất viện hệ, cùng là thập đại danh sư, hắn nói chuyện cường độ, rõ ràng cao hơn tại những người khác."),t("br"),t("br"),n._v("“Cũng tốt...”"),t("br"),t("br"),n._v("Mọi người gật đầu, chính muốn tiếp tục thương nghị phía dưới nên làm như thế nào, chỉ thấy Đan viện viện trưởng lật bàn tay một cái, một cái đưa tin ngọc phù xuất hiện tại lòng bàn tay, cúi đầu nhìn thoáng qua, lập tức lông mày giương lên."),t("br"),t("br"),n._v("“Mọi người đi theo ta, Mộc sư đã đến!”"),t("br"),t("br"),n._v("“Đã đến? Làm sao nhanh như vậy?”"),t("br"),t("br"),n._v("“Không phải nói còn có mấy ngày ư?”"),t("br"),t("br"),n._v("“Cao nhân tiền bối làm việc, chúng ta không hiểu rõ, các ngươi quên, hơn một tháng trước, liền nói có thể tới, kết quả nhưng thật lâu đều không có chờ đến...”"),t("br"),t("br"),n._v("“Cũng là!”"),t("br"),t("br"),n._v("Mọi người tất cả đều tràn đầy kỳ quái."),t("br"),t("br"),n._v("Vị này phong hào đế quốc tới Mộc sư, hơn một tháng trước liền nói từ tổng bộ đi ra, kết quả nhưng bên trái chờ không được, bên phải chờ không được, mới vừa nói xong qua mấy ngày, kết quả, hiện tại đã đến."),t("br"),t("br"),n._v("Quá mức khác với sự dự liệu của người ta."),t("br"),t("br"),n._v("Tiếng nghị luận bên trong, mọi người đi ra ngoài, đi tới trưởng lão hội khách đại sảnh, quả nhiên thấy một cái lão giả râu bạc trắng, ngồi ngay ngắn trong đó, mà hắn cách đó không xa còn ngồi một người, là cái chừng hai mươi tuổi nữ tử."),t("br"),t("br"),n._v("“Mộc sư!”"),t("br"),t("br"),n._v("Đi vào phòng, mọi người ôm quyền, thần thái cung kính."),t("br"),t("br"),n._v("Vị này Mộc sư, là thất tinh danh sư, chân chính đỉnh phong nhân vật, liền xem như bọn họ, cũng không dám có chút bất kính."),t("br"),t("br"),n._v("“Ừm, ta trước giới thiệu cho các ngươi một chút, vị này Lạc sư, là... Ta một cái vãn bối, đã đi đến lục tinh cấp bậc, muốn tới đây dạy thay rèn luyện một chút, khả năng còn làm phiền ngươi bọn họ, hỗ trợ an bài một chút!”"),t("br"),t("br"),n._v("Mộc sư đứng dậy, một chỉ bên người nữ tử, giới thiệu nói."),t("br"),t("br"),n._v("“Vãn bối?”"),t("br"),t("br"),n._v("Mọi người sững sờ, đồng loạt hướng nữ tử nhìn lại, chỉ thấy nàng đứng dậy, đối rất nhiều danh sư thi lễ một cái."),t("br"),t("br"),n._v("“Nhược Hi gặp qua chư vị trưởng lão!”"),t("br"),t("br"),n._v("Nữ tử ôm quyền."),t("br"),t("br"),n._v("“Lục tinh danh sư? Nhỏ như vậy tuổi tác liền đạt đến lục tinh cấp bậc?”"),t("br"),t("br"),n._v("Mọi người tất cả đều sững sờ."),t("br"),t("br"),n._v("Vị này Lạc Nhược Hi thoạt nhìn chẳng qua chừng hai mươi tuổi, liền khảo hạch lục tinh danh sư thành công, thân thế cùng địa vị có thể nói kinh khủng!"),t("br"),t("br"),n._v("Xem ra Mộc sư nói là hắn hậu bối, hơn phân nửa cũng là nói dối."),t("br"),t("br"),n._v("Chỉ bằng vào thất tinh danh sư, rất khó bồi dưỡng được một vị chừng hai mươi tuổi lục tinh danh sư."),t("br"),t("br"),n._v("Đoán chừng là một vị nào đó Thánh Nhân quý tộc hậu bối, đặc biệt tới đánh bóng lịch luyện."),t("br"),t("br"),n._v("Cũng chỉ có loại gia tộc này, mới có thể nuôi dưỡng được thiên tài như thế."),t("br"),t("br"),n._v("Loại sự tình này trước kia cũng không phải chưa từng xảy ra."),t("br"),t("br"),n._v("“Lạc sư tuổi còn trẻ liền có loại này thành tựu, để cho chúng ta xấu hổ, như vậy, ta hiện tại liền an bài cho ngươi, không biết... Ngươi am hiểu cái gì, muốn đi cái gì viện hệ?”"),t("br"),t("br"),n._v("Đan viện viện trưởng cười nói."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-734"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-736"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);