(window.webpackJsonp=window.webpackJsonp||[]).push([[2008],{2363:function(n,h,i){"use strict";i.r(h);var t=i(44),c=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-730-hoc-phan-tam-quan-trong"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-730-hoc-phan-tam-quan-trong"}},[n._v("#")]),n._v(" Chương 730: Học phần tầm quan trọng")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("Chương 730: Học phần tầm quan trọng"),i("br"),i("br"),n._v("Không thẻ học viên, cũng không phải là học viện học viên... Cái kia chạy danh sư học viện làm gì?"),i("br"),i("br"),n._v("Làm phong hào đế quốc xuống, duy nhất một chỗ to lớn danh sư môi trường nuôi cấy, có thể đi vào nơi này, đều có cực kỳ kiêu ngạo thiên phú và vốn liếng, không phải tùy tiện người nào liền có thể trà trộn vào tới."),i("br"),i("br"),n._v("“Ta là năm nay tới tân sinh... Thẻ học viên còn không có làm!”"),i("br"),i("br"),n._v("Thấy đối phương lầm sẽ tự mình có thể là kẻ ngoại lai, Trương Huyền giải thích."),i("br"),i("br"),n._v("“Tân sinh?” Nữ tử sửng sốt một chút, lúc này mới nhớ tới, hôm nay là chiêu sinh khảo hạch thời gian, nhịn không được lắc đầu."),i("br"),i("br"),n._v("Cái này hơn nửa đêm, mới vừa đi tới tân sinh, không đi nghỉ ngơi, kiểm tra cái gì ngũ tinh luyện khí sư..."),i("br"),i("br"),n._v("“Nói thật cho ngươi biết, muốn muốn khảo hạch luyện khí sư, đầu tiên muốn có thẻ học viên, thứ yếu, còn cần có đẳng cấp huy chương, cuối cùng, còn muốn có học phần, không có học phần, ai có công phu vì ngươi khảo hạch?”"),i("br"),i("br"),n._v("Biết hắn là tân sinh, lại không học trưởng xưng hô, nữ tử giải thích."),i("br"),i("br"),n._v("“Còn muốn học phần?”"),i("br"),i("br"),n._v("Trương Huyền cười khổ."),i("br"),i("br"),n._v("“Đúng vậy a, cái khác công hội, khảo hạch thời điểm, cũng cần linh thạch xem như tiền thế chấp. Không có học phần, không có chế ước, ngày ngày có người tới thử lấy khảo hạch, còn không đem người vội vàng chết?”"),i("br"),i("br"),n._v("Nữ tử hừ một tiếng."),i("br"),i("br"),n._v("Một cái tân sinh, cái gì quy củ đều không có biết rõ ràng, liền chạy tới khảo hạch, thật là không biết gan lớn, vẫn là thiếu thông minh."),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Trương Huyền vò đầu."),i("br"),i("br"),n._v("Quả thực."),i("br"),i("br"),n._v("Cái khác công sẽ khảo hạch đẳng cấp thời điểm, cũng không phải tùy tiện đi, cũng cần trước giao nạp nhất định phí tổn, thông qua được liền có thể lui về, không cách nào thông qua, sẽ bị mất, dùng để ngăn cản những cái kia rõ ràng thực lực chênh lệch rất lớn, nhưng không biết tự lượng sức mình người."),i("br"),i("br"),n._v("Rất hiển nhiên, nơi này cũng là như vậy, chỉ bất quá điều kiện hạn chế, đổi thành học phần mà thôi."),i("br"),i("br"),n._v("“Khảo hạch một cái ngũ tinh luyện khí sư, đại khái cần bao nhiêu học phần?”"),i("br"),i("br"),n._v("Trương Huyền hỏi."),i("br"),i("br"),n._v("“Học viện xin ngũ tinh cấp bậc phụ tu chức nghiệp, bình thường đều là hai cái học phần, lục tinh cấp bậc, thì là bốn cái học phần.” Nữ tử nói."),i("br"),i("br"),n._v("“Hai cái, bốn cái?” Trương Huyền líu lưỡi, hỏi tiếp: “Cái khác công hội, chỉ muốn khảo hạch thông qua, sẽ đem những này hạn chế phí tổn trả lại, nếu như thông qua được ngũ tinh luyện khí sư khảo hạch, học phần cũng sẽ trả lại ư?”"),i("br"),i("br"),n._v("Mới vừa mới đi một vòng, biết học phần kiếm lấy độ khó, giúp người luyện chế Linh cấp trung phẩm vũ khí, ngay cả nửa cái học phần đều không đáng, hai cái, bốn cái, đối học sinh tới nói, tuyệt đối là giá trên trời."),i("br"),i("br"),n._v("Nếu như khảo hạch thông qua có thể trả còn tốt, không cách nào trả lại, chỉ riêng tiêu hao tại khảo hạch những này phụ tu trên chức nghiệp, liền cần tốn hao không biết bao nhiêu học phần."),i("br"),i("br"),n._v("“Trả? Ngươi suy nghĩ nhiều, làm sao có thể trả!”"),i("br"),i("br"),n._v("Nữ tử lắc đầu: “Ngươi liền nhớ kỹ, muốn tại học viện sinh tồn được, nhất định phải có học phần, nếu không có nhiều tiền hơn nữa đều không dùng! Mà muốn kiếm lấy học phần, liền cần làm đủ loại nhiệm vụ, hoàn thành đủ loại việc học, hoặc là đánh vỡ người khác không có ghi chép! Tốt, trở về đi, không thẻ học viên, chỉ cần là vốn học viện học viên, dàn xếp một chút, không tính là gì, nhưng không có học phần, ngay cả kích hoạt  tư cách đều không có, như thế nào khảo hạch?”"),i("br"),i("br"),n._v("“Kích hoạt khí hải?”"),i("br"),i("br"),n._v("“Khí hải là Khí viện cùng Thiên Công viện, hao phí vô số nhân loại vật lực chế tạo tự động khảo hạch thông đạo, người tham gia khảo hạch chỉ cần kích hoạt, liền có thể đi vào giả lập cảnh tượng, mượn nhờ khôi lỗi luyện khí, chỉ cần thành công, liền có thể thông qua khảo hạch.”"),i("br"),i("br"),n._v("Thấy hắn một khiếu không hiểu, nữ tử mặc dù lòng tràn đầy không kiên nhẫn, vẫn là giải thích cặn kẽ."),i("br"),i("br"),n._v("“Giả lập cảnh tượng, khôi lỗi? Khảo hạch luyện khí, không cần chân thực cảm thụ ư? Giả lập cảnh tượng như thế nào khảo hạch?” Trương Huyền kỳ quái."),i("br"),i("br"),n._v("Hắn đi qua không ít công hội, đều cần phát huy chân chính bản lĩnh, đối phương nói giả lập cảnh tượng có ý tứ gì?"),i("br"),i("br"),n._v("Chẳng lẽ, không cần chân chính luyện khí, liền có thể thuận lợi qua ải?"),i("br"),i("br"),n._v("“Chân thực cảm thụ là có thể, nhưng ngươi cũng đã biết vật liệu luyện khí trân quý? Học viện hơn mười vạn danh sư, phụ tu luyện khí không dưới năm vạn, mỗi ngày đến đây khảo hạch đẳng cấp có bao nhiêu? Toàn bộ chân thực khảo hạch, mỗi ngày có bao nhiêu khoáng thạch, kim loại hiếm bị lãng phí?”"),i("br"),i("br"),n._v("Nữ tử khẽ nói: “Yên tâm đi, mặc dù là giả lập cảnh tượng, lại là học viện thành lập đến nay, đào được mấy trăm vị lục tinh đỉnh phong luyện khí sư, kết hợp mấy vạn lần luyện khí cảnh tượng, cô đọng mà thành, chỉ cần ngươi luyện khí thủ đoạn phù hợp chuẩn mực, liền nhất định có thể khảo hạch thông qua! Cùng chân thực luyện khí khác biệt, không cao hơn một phần ngàn tỉ!”"),i("br"),i("br"),n._v("Mặc dù khí hải, là giả lập cảnh tượng luyện khí, nhưng đi qua không biết bao nhiêu vị lục tinh luyện khí sư nghiệm chứng, cùng chân thực luyện đan không khác chút nào, chỉ cần ở bên trong có thể luyện chế thành công, chân thực bên trong cũng có thể tuyệt đối thành công, không có bất cứ vấn đề gì."),i("br"),i("br"),n._v("“Ah!”"),i("br"),i("br"),i("br"),n._v("Trương Huyền hiểu được."),i("br"),i("br"),n._v("Cũng đúng, vượt qua năm vạn luyện khí sư, một năm trôi qua, mỗi ngày đến đây khảo hạch, ít nhất cũng phải mấy trăm, nếu như đều chân chính luyện chế, cần bao nhiêu khoáng thạch, tinh thiết?"),i("br"),i("br"),n._v("Luyện chế thành công ngược lại cũng thôi, phải biết đại bộ phận đều là khó mà thông qua, một khi thất bại, những quáng thạch này liền lại không lần thứ hai sử dụng khả năng, loại tổn thất này, coi như danh sư học viện mạnh mẽ, cũng không chịu nổi."),i("br"),i("br"),n._v("Khoáng thạch không là dược liệu, có thể tái sinh, có thể trồng trọt, có chút trân quý khoáng thạch, chỉ cần tiêu hao sạch sẽ, liền lại không tìm tới khả năng."),i("br"),i("br"),n._v("Do đó, dùng giả lập cảnh tượng đến khảo hạch, quả thực là biện pháp tốt nhất."),i("br"),i("br"),n._v("“Trở về đi, coi như ngươi có khảo hạch ngũ tinh luyện khí sư năng lực, cũng tốt nhất trước kiếm lấy đến hai cái học phần lại nói, không có học phần, căn bản hết cách rồi, nghĩ thông suốt dung cũng dàn xếp không được!”"),i("br"),i("br"),n._v("Nữ tử lần nữa vẫy tay."),i("br"),i("br"),n._v("“Ta nghe ngươi mới vừa nói... Kiếm lấy học phần, cần phải hoàn thành nhiệm vụ, việc học, hoặc là... Đánh phá kỉ lục? Xin hỏi đánh phá kỉ lục làm sao làm?”"),i("br"),i("br"),n._v("Biết không học phần, chuyện gì đều không làm được, Trương Huyền tiếp tục hỏi."),i("br"),i("br"),n._v("“Đánh phá kỉ lục?” Con mắt xoay loạn, nữ tử suýt chút nữa không hộc máu."),i("br"),n._v(" Không hổ là người mới, thật đúng là sẽ ý nghĩ hão huyền."),i("br"),i("br"),n._v("Đã xưng là ghi chép, cũng là cho thấy, học viện không biết bao nhiêu danh sư tới thử qua, đều không thành công, ngươi một cái mới vừa vào học viện, cái gì ngoạn ý không hiểu gia hỏa, hỏi tới hỏi lui, ta cũng là nhịn, còn muốn đánh vỡ ghi chép lợi nhuận học phần?"),i("br"),i("br"),n._v("Làm cái gì nằm mơ ban ngày?"),i("br"),i("br"),n._v("Nếu là cái này học phần tốt như vậy lợi nhuận, ai không đến?"),i("br"),i("br"),n._v("Thật là khờ có thể!"),i("br"),i("br"),n._v("“Đúng vậy a, nơi này có thể có cái gì ghi chép, ta có thể đi thử một chút...” Trương Huyền nói."),i("br"),i("br"),n._v("Có thể xưng là thí luyện, khẳng định đều khá là phiền toái, đơn giản căn bản đợi không được ngươi ra tay... Mới vừa vào học, ngay cả lão sư đều không có nhận, việc học lại càng không cần phải nói, đối với hắn mà nói, duy nhất muốn nhanh chóng kiếm lấy điểm tích lũy phương pháp chính là đánh phá kỉ lục."),i("br"),i("br"),n._v("Đương nhiên, ghi chép nếu là quá khó, liền nhìn nhiều vài cuốn sách, lời đơn giản, liền đi qua thử một chút, trước kiếm chút lại nói."),i("br"),i("br"),n._v("Một phân tiền khó chết anh hùng nam tử, không có học phần, coi như năng lực hơn người, cũng không có bất kỳ biện pháp nào."),i("br"),i("br"),n._v("“Ngươi...”"),i("br"),i("br"),n._v("Thấy cái tên này mở miệng một tiếng ghi chép, nữ tử lắc đầu, tràn đầy mất hứng hưng."),i("br"),i("br"),n._v("Ngươi biết ghi chép là cái gì a? Liền muốn đánh vỡ?"),i("br"),i("br"),n._v("Vừa mới bắt đầu, còn cảm thấy người mới này có thể có chút tài hoa, giá trị phải tôn trọng, lúc này nhìn thấy càng ngày càng cuồng vọng, càng ngày càng phách lối, đã có chút chán ghét."),i("br"),i("br"),n._v("Bất quá, cùng là danh sư, cũng không tiện chân chính biểu hiện ra ngoài, đành phải cố nén lửa giận, tiện tay vẫy một cái: “Ngươi muốn đánh vỡ cũng không có gì, bên kia có cái ghi chép thông đạo, có thể bản thân đi qua nhìn một chút, nhưng ta khuyên ngươi tốt nhất đừng đi vào, nếu không, rất dễ dàng bị vứt ra...”"),i("br"),i("br"),n._v("Ghi chép, đại biểu vinh dự, cũng đại biểu tôn nghiêm, không phá được tùy tiện xông loạn, tự nhiên muốn chịu đến trừng phạt."),i("br"),i("br"),n._v("Mất mặt xấu hổ là chuyện nhỏ, làm không cẩn thận sẽ bị thông đạo trực tiếp ném ra, dằn vặt bản thân bị trọng thương."),i("br"),i("br"),n._v("“Bên kia?” Trương Huyền nhìn sang, chỉ thấy dày đặc dòng người che chắn xuống, cái gì đều không nhìn thấy."),i("br"),i("br"),n._v("“Ta hiện tại cũng không có việc gì, mang ngươi tới...”"),i("br"),i("br"),n._v("Hừ một tiếng, nữ tử ngẩng đầu lên sọ, trước đi đến."),i("br"),i("br"),n._v("Đã ngươi chưa từ bỏ ý định, vậy ta liền tận mắt nhìn, ngươi là như thế nào mất mặt."),i("br"),i("br"),n._v("Mới tới học viên, không biết điều ngược lại cũng thôi, còn muốn phá kỷ lục? Ngươi cho rằng ghi chép tốt như vậy phá a?"),i("br"),i("br"),n._v("Thật muốn tốt như vậy phá, ai còn ở nơi này ngồi xổm giúp người luyện khí, bán ra đủ loại vật phẩm? Thậm chí bất kể sống chết, đi ra ngoài tiến hành đủ loại thí luyện?"),i("br"),i("br"),n._v("Gặp qua tâm lớn, chưa thấy qua tâm như thế lớn!"),i("br"),i("br"),n._v("Đoán chừng là cái không bị qua cái gì thất bại thiên tài, địa phương nhỏ xưng hùng thói quen, căn bản không biết trời cao bao nhiêu, đất dày bao nhiêu."),i("br"),i("br"),n._v("Không biết nữ tử suy nghĩ trong lòng, đã cho đối phương lưu lại hại vô cùng ấn tượng, Trương Huyền theo sát ở phía sau, hướng một bên đi đến."),i("br"),i("br"),n._v("Vòng qua nguyên một đám đám người, không lâu sau, đi tới một cánh cửa trước mặt."),i("br"),i("br"),n._v("Cái đại môn này cùng trước đó khảo hạch danh sư lúc đồng dạng, bên ngoài là trắng như tuyết mặt tường, ghi chú đủ loại ghi chép."),i("br"),i("br"),n._v("“Mười phút đồng hồ hòa tan thiết hắc tinh thạch ba cái, hoàn thành người lục tinh luyện khí sư Trịnh Vinh, danh sư học viện thứ 3,784 năm...”"),i("br"),i("br"),n._v("“Tiếp Dẫn địa hỏa, hòa tan hàn băng ngân thiết, trong vòng một canh giờ, để hắn chảy xuôi trở thành chất lỏng. Hoàn thành người lục tinh luyện khí sư...”"),i("br"),i("br"),n._v("“Địa hỏa thông đạo, đợi mười bảy phút, hoàn thành người, lục tinh luyện khí sư Lưu Hàm...”"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Phía trên ghi chép, không chỉ kỹ càng viết kết quả, còn viết ai hoàn thành, đại khái vào giờ nào, cùng thành tích."),i("br"),i("br"),n._v("Tùy ý nhìn lướt qua, Trương Huyền nhịn không được mặt vẻ mặt nghiêm túc."),i("br"),i("br"),n._v("Không thể không nói, những này ghi chép xác thực kinh người."),i("br"),i("br"),n._v("Không nói cái khác, liền lấy thiết hắc tinh thạch tới nói, thứ này là khó khăn nhất luyện hóa mấy loại khoáng thạch một trong, coi như thất tinh luyện khí sư, muốn luyện hóa, không có nửa canh giờ, đều khó mà làm đến, vị này Trịnh Vinh, vậy mà mười phút đồng hồ liền hoàn thành, đáng sợ!"),i("br"),i("br"),n._v("Liền xem như hắn, khẳng định đều làm không được."),i("br"),i("br"),n._v("Không phải hắn hiểu biết cùng năng lực không được, mà là thực lực không đủ."),i("br"),i("br"),n._v("Còn có cái kia hàn băng ngân thiết, càng thêm khoa trương, đơn thuần lò lửa luyện chế, gần như không có khả năng làm đến, chỉ có thể Tiếp Dẫn địa hỏa trường kỳ rèn luyện, dưới tình huống bình thường, cần phải hao phí địa hỏa ôn dưỡng mấy tháng mới có thể luyện chế vũ khí, đối phương một canh giờ liền để hắn hòa tan thành chất lỏng, không phải bình thường đáng sợ."),i("br"),i("br"),n._v("“Những này ngươi có thể làm được?”"),i("br"),i("br"),n._v("Thấy vị này tự biên tự diễn thanh niên mặt mũi kinh ngạc, nữ tử cười nhạo."),i("br"),i("br"),n._v("“Làm đến là có thể làm được... Liền là có chút phiền phức!”"),i("br"),i("br"),n._v("Trương Huyền vò đầu."),i("br"),i("br"),n._v("Thật muốn đi làm, nhất định có thể vượt qua, nhưng cần phải hao phí không ít đánh đổi, dùng những này đánh đổi đem đổi lấy rất ít học phần, không đáng."),i("br"),i("br"),n._v("Thật giống như dùng Địa Tâm hỏa hòa tan hàn băng ngân thiết, chỉ cần dùng Thiên Đạo chân khí ôn dưỡng, lại dung hợp Địa Tâm hỏa, nên nửa canh giờ liền có thể để cho hòa tan... Nhưng cần tiêu hao chân khí bao lớn, có thể nói kinh khủng."),i("br"),i("br"),n._v("Hơn nữa, làm như vậy, Thiên Đạo chân khí bí mật, rất dễ dàng rò rỉ."),i("br"),i("br"),n._v("“Có thể làm được? Còn có chút phiền phức? Khẩu khí thật lớn!”"),i("br"),i("br"),n._v("Nghe được hai người nói chuyện, một cái luyện khí sư nhịn không được tiến về phía trước một bước, xùy cười một tiếng: “Lý tuyền, ngươi cái này là từ đâu tìm ra một cái như thế cuồng vọng gia hỏa?”"),i("br"),i("br"),n._v("Convert by: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-738"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-740"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);