(window.webpackJsonp=window.webpackJsonp||[]).push([[2009],{2365:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-65-muoi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-65-muoi"}},[n._v("#")]),n._v(" Chương 65: Mười?")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v(" “Không sai, muốn sát hạch danh sư, học sinh sự tin tưởng hắn độ, đầu tiên muốn vượt qua sáu mươi, đây là cứng nhắc tiêu chuẩn! Bằng không, không thể thành công!” Mạc Tường trưởng lão gật đầu."),t("br"),t("br"),n._v(" “Sáu mươi...” Thượng Thần trưởng lão không nhịn được tặc lưỡi: “Cao như vậy độ tín nhiệm, ở học sinh trong lòng dĩ nhiên có thể so với cha mẹ!” "),t("br"),t("br"),n._v(" Cha mẹ cùng nhi nữ, từ nhỏ đã cần phải cùng nhau, độ tín nhiệm cũng bất quá sáu mươi tả hữu, lão sư cùng học sinh quan hệ cũng nghĩ đạt đến trình độ như thế này, khó khăn chi lớn, có thể tưởng tượng được!"),t("br"),t("br"),n._v(" Chẳng trách toàn bộ Thiên Huyền vương quốc đều không có một vị danh sư, thực sự quá khó khăn!"),t("br"),t("br"),n._v(" “Các ngươi học viện Lục Tầm, Vương Siêu, nếu như sẽ dạy thụ ba mươi năm, còn có thể duy trì trình độ loại này, ngược lại có cơ hội trở thành trợ giáo!” Mạc trưởng lão vuốt vuốt chòm râu."),t("br"),t("br"),n._v(" Danh sư cũng không phải là việc phải tự làm, không ít chuyện muốn từ trợ giáo đi làm, cùng học đồ giống như vậy, muốn trở thành danh sư, trợ giáo là nhất định phải đi đường."),t("br"),t("br"),n._v(" “Trợ giáo?” "),t("br"),t("br"),n._v(" Thượng Thần trưởng lão thán phục."),t("br"),t("br"),n._v(" Có cơ hội trở thành trợ giáo, đủ để chứng minh Lục Tầm, Vương Siêu hai người trình độ."),t("br"),t("br"),n._v(" “Không biết hai người bọn họ giáo sư học sinh, có thể có bao nhiêu độ tín nhiệm...” Không nhịn được nói."),t("br"),t("br"),n._v(" “Bọn họ mấy năm qua kiếm lời không ít tiếng tăm, giáo sư nhiều năm học viên cũ, lẽ ra có thể đạt đến bốn tầm mười giờ! Lên trên nữa liền khó khăn!” Mạc trưởng lão lắc đầu."),t("br"),t("br"),n._v(" Hắn là giáo sư công hội trưởng lão, đối không ít lão sư tình huống đều rõ rõ ràng ràng."),t("br"),t("br"),n._v(" “Này ngược lại là!” "),t("br"),t("br"),n._v(" Thượng Thần gật đầu."),t("br"),t("br"),n._v(" Tín nhiệm tích lũy khó, đi lên cũng rất nhanh, có lúc một chuyện nhỏ liền có thể triệt để hủy diệt, không phải vậy, danh sư cũng là không lớn như vậy tiếng tăm!"),t("br"),t("br"),n._v(" “Ta nghe nói... Chúng ta giáo sư công hội, ra một thiên tài thiếu nữ, năm nay bất quá mười tám, mười chín tuổi, đã trở thành cấp thấp trợ giáo rồi?” Nhớ tới một cái tin, Thượng Thần trưởng lão không nhịn được hỏi."),t("br"),t("br"),n._v(" “Là hội trưởng chúng ta con gái, đích thật là thiên tài!” Nói tới người này, Mạc trưởng lão vuốt râu, tràn đầy tán dương gật đầu."),t("br"),t("br"),n._v(" Trợ giáo chia làm thấp, trung, lớp 12 cái cấp bậc, không tới hai mươi liền trở thành một thành viên trong đó, dù cho chỉ là cấp thấp, cũng rất khủng bố!"),t("br"),t("br"),n._v(" “Lợi hại!” "),t("br"),t("br"),n._v(" Thấy đối phương xác nhận, Thượng Thần trưởng lão không nhịn được than thở."),t("br"),t("br"),n._v(" Hắn mặc dù là Hồng Thiên học viện thầy chủ nhiệm, trên thực tế cũng chỉ là một cao cấp giáo sư, làm danh sư cấp thấp trợ giáo căn bản không đủ tư cách."),t("br"),t("br"),n._v(" Cảm khái xong, Thượng Thần không tại nhiều nói, không nhịn được nhìn về phía Trương Huyền cùng Tào Hùng: “Mạc trưởng lão, ngươi nhìn hai người bọn họ đại khái có thể có bao nhiêu độ tín nhiệm?” "),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v(" “Ngươi... Vẫn là nhân lúc hiện tại đi đi, rời đi Hồng Thiên học viện, cũng lại đừng trở về...” "),t("br"),t("br"),n._v(" Thấy thiếu niên còn đần độn chờ học tâm tra hỏi kết quả, Trầm Bích Như không nhịn được đi tới trước mặt, nhỏ giọng."),t("br"),t("br"),n._v(" Dưới cái nhìn của nàng, Trương Huyền cùng Thượng Bân là người của hai thế giới, nếu như không phải nàng, hoàn toàn không cùng xuất hiện."),t("br"),t("br"),n._v(" Hiện tại gây ra cục diện này, Trương Huyền thật bị đánh một trăm sát thần côn, khai trừ giáo sư tư cách, nàng lương tâm trên khẳng định băn khoăn."),t("br"),t("br"),n._v(" “Bây giờ nghĩ đi? Hơi trễ đi!” "),t("br"),t("br"),n._v(" Thấy nữ thần của mình lại quan tâm nam nhân khác, Thượng Bân cắn răng hừ nói."),t("br"),t("br"),n._v(" Ta đều bị đánh thành như vậy, ngươi cũng mặc kệ, nhưng quan tâm một sư tư sát hạch là không phân gia hỏa, đáng ghét, đáng ghét!"),t("br"),t("br"),n._v(" Sắc mặt hắn dữ tợn, tức giận đến phổi đều sắp nổ."),t("br"),t("br"),n._v(" “Không sai, học tâm tra hỏi cũng bắt đầu, còn muốn đi? Nằm mơ đi!” Tào Hùng cũng bước lên trước, trên mặt lộ ra tràn đầy hưng phấn dữ tợn: “Lập tức liền có kết quả rồi, ngươi sẽ biết, vừa nãy làm ra quyết định, sẽ là cỡ nào ngu xuẩn!” "),t("br"),t("br"),n._v(" “Ngươi cứ như vậy xác nhận tự mình sẽ thắng lợi?” "),t("br"),t("br"),n._v(" Thấy đối phương trí cuốn tại nắm, phảng phất đã ăn chắc tự mình, Trương Huyền lắc đầu một cái, nói."),t("br"),t("br"),n._v(" “Khà khà!” Như là nhìn kẻ ngu si như thế nhìn về phía thiếu niên, Tào Hùng cười gằn: “Nói thật cho ngươi biết đi, vị này Lưu Dương, là ta chuyên khai ra, cùng ta ở chung được tiếp cận nửa tháng, giữa chúng ta độ tín nhiệm, e sợ đều vượt qua hai mươi! Cho tới ngươi đây? Có thể có mười điểm sao? Mạnh mẽ dựa dẫm vào ta đem hắn đã lừa gạt đi, ta sợ số âm cũng rất có thể đi!” "),t("br"),t("br"),n._v(" Không điểm là không có tín nhiệm, số âm đại biểu căm ghét, trị số càng lớn, căm ghét độ cũng là càng lớn."),t("br"),t("br"),n._v(" Ngày hôm trước Lưu Dương từ bên cạnh hắn lúc đi, lưu luyến không rời, hắn còn rõ ràng trước mắt, một cái có thể đem người dạy thành tẩu hỏa nhập ma gia hỏa, làm sao có khả năng để vị này kiêu ngạo gia hỏa chịu thua!"),t("br"),t("br"),n._v(" E sợ hai người quan hệ vẫn là băng điểm, Lưu Dương không biết làm sao ghi hận cái tên này đây!"),t("br"),n._v("  “Hồng Thiên học viện chính là Thiên Huyền vương quốc đệ nhất học viện, thật muốn học sinh đối với lão sư tín nhiệm ở mười điểm trở xuống, sợ rằng sẽ sẽ trở thành trò cười, truyền vang toàn bộ vương quốc!” Thượng Bân nở nụ cười, một mặt chế nhạo: “Há, đúng, ta đã quên, ngược lại ngươi danh tiếng đều thúi như vậy, cũng không sợ lần thứ hai trở thành trò cười...” "),t("br"),t("br"),n._v(" [ trｕyen cu"),t("br"),n._v("a tui ʘʘ net ]  “Các ngươi...” "),t("br"),t("br"),n._v(" Thấy hai người bỏ đá xuống giếng, Trầm Bích Như tức giận mặt cười phấn hồng, đang muốn phản bác hai câu, đã thấy làm như người trong cuộc Trương Huyền, khắp khuôn mặt là hờ hững, tựa hồ đối với hai người cười nhạo căn bản không để ở trong lòng."),t("br"),t("br"),n._v(" Nói thật, Trương Huyền xác thực không để ý."),t("br"),t("br"),n._v(" Hai người này cũng là bây giờ có thể qua loa vài câu, một lúc nữa kết quả đi ra, còn không biết làm sao khóc đây..."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v(" “Hai người bọn họ?” "),t("br"),t("br"),n._v(" Nghe được Thượng Thần trưởng lão câu hỏi, Mạc Tường trưởng lão do dự một chút, cẩn thận cân nhắc: “Vị này Lưu Dương là bị Tào Hùng lão sư khai ra, sự tin tưởng hắn độ lẽ ra có thể cao một chút, bất quá theo ta thấy, cũng là mười tám, mười chín tả hữu, hai mươi là một đại cửa ải khó! Cho tới Trương Huyền... Nếu như đúng là cưỡng bức, sợ rằng sẽ là số âm!” "),t("br"),t("br"),n._v(" “Độ tín nhiệm là số âm, Hồng Thiên học viện đều đem hổ thẹn, đến thời điểm liền làm phiền Mạc trưởng lão phí tâm!” "),t("br"),t("br"),n._v(" Thượng Thần trưởng lão nói."),t("br"),t("br"),n._v(" “Đó là tự nhiên!” Mạc trưởng lão ánh mắt lấp lánh: “Làm như giáo sư công hội trưởng lão, ta quyết không cho phép có thứ bại hoại như vậy xuất hiện! Nếu thật là số âm, có thể xác định cái này Lưu Dương bị cưỡng bức không thể nghi ngờ, ta sẽ lập tức đăng báo công hội, kiên quyết khai trừ cái này Trương Huyền giáo sư tư cách!” "),t("br"),t("br"),n._v(' "Nghe được đối phương hứa hẹn, Thượng Thần trưởng lão cười cợt, con mắt lóe lên, chỉ về phía trước: "Mau nhìn, kết quả lập tức đi ra!"'),t("br"),t("br"),n._v(" Vừa dứt lời, chỉ thấy gian phòng chính giữa hai cái Thủy Tinh Cầu tán phát ánh sáng liên tục lay động, từ từ yếu bớt hạ xuống."),t("br"),t("br"),n._v(" Học tâm tra hỏi, tiến hành tra hỏi người, cần kinh nghiệm đủ loại ảo cảnh, mới có thể từ nội tâm làm ra chân chính lựa chọn, giờ khắc này ánh sáng biến mất, đại biểu các loại trải qua dĩ nhiên hoàn thành, lập tức liền muốn ra thành tích."),t("br"),t("br"),n._v(" Vù!"),t("br"),t("br"),n._v(" Bên tay trái Thủy Tinh Cầu, nhẹ nhàng loáng một cái xuất hiện một loạt con số."),t("br"),t("br"),n._v(" “Kết quả đi ra, ngươi lập tức liền phải xui xẻo...” "),t("br"),t("br"),n._v(" Nhìn Thủy Tinh Cầu trên chậm rãi hiện lên con số, Tào Hùng ánh mắt hưng phấn liên tục lấp loé."),t("br"),t("br"),n._v(" Lần này chẳng những có thể đem Lưu Dương một lần nữa thu về môn hạ của chính mình, càng có thể đem Trương Huyền đuổi đi, lấy lòng Thượng Bân thiếu gia niềm vui, có căn này trụ cột, sau đó ở học viện còn không thuận buồm xuôi gió?"),t("br"),t("br"),n._v(" E sợ không dùng đến mấy năm, liền có thể thuận lợi lên cấp, trở thành cao cấp lão sư."),t("br"),t("br"),n._v(" Trong lòng đang hưng phấn ý Thủy Tinh Cầu rốt cục đình chỉ lay động, con số hiện lên ở trước mắt mọi người."),t("br"),t("br"),n._v(" “Hắn đối với sự tin tưởng của ta độ, khẳng định vượt qua hai mươi...” "),t("br"),t("br"),n._v(" Rít lên một tiếng, Tào Hùng vội vàng nhìn lại, chỉ liếc mắt nhìn, hưng phấn lời nói lập tức câm ở yết hầu, như là một chỉ bị ngắt cổ con vịt, con ngươi trợn lên sắp rơi trên mặt đất: “Chuyện này... Này làm sao có khả năng?” "),t("br"),t("br"),n._v(" Trước mắt thành tích để hắn sắp điên rồi!"),t("br"),t("br"),n._v(" Đây là trước tuyệt không dám tưởng tượng!"),t("br"),t("br"),n._v(" Lưu Dương nhưng là hắn tự mình chiêu tới được, hai người ở chung được còn chí ít nửa tháng, quan hệ phi thường ăn ý, làm sao có khả năng liền điểm ấy độ tín nhiệm?"),t("br"),t("br"),n._v(" Chỉ thấy Thủy Tinh Cầu cái trước to lớn con số, rõ ràng chói mắt, dường như trường kiếm bình thường đâm vào trái tim của hắn. Mười!"),t("br"),t("br"),n._v(" Lưu Dương sự tin tưởng hắn độ, lại chỉ có mười điểm!"),t("br"),t("br"),n._v(" Mười điểm trở xuống, đại biểu không tín nhiệm, mười tuy rằng bị xử giới hạn vị trí, nhưng cũng không xê xích gì nhiều!"),t("br"),t("br"),n._v(" Vừa còn tự luyến nói, khẳng định ở hai mươi trở lên, kết quả lại là cái này, khác nào mạnh mẽ cho một cái tát."),t("br"),t("br"),n._v(" “Đây chính là ngươi tự tin khởi nguồn?” "),t("br"),t("br"),n._v(" Trương Huyền nhìn sang."),t("br"),t("br"),n._v(" “Ta...” Tào Hùng kém chút không một hơi tới, tại chỗ tức đến ngất đi."),t("br"),t("br"),n._v(" Ta nhọc nhằn khổ sở đem ngươi từ đằng xa đi tìm đến, lại ở chung lâu như vậy, làm sao độ tín nhiệm chỉ có ngần ấy... Đại ca, ngươi đến cùng xảy ra chuyện gì..."),t("br"),t("br"),n._v(" Nhìn về phía Lưu Dương, Tào Hùng muốn tự tử đều có."),t("br"),t("br"),n._v(" Tín nhiệm đây?"),t("br"),t("br"),n._v(" Giữa người và người cơ bản nhất tín nhiệm đây?"),t("br"),t("br"),n._v("Convert by: Hoang Chau"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-73"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-75"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);