(window.webpackJsonp=window.webpackJsonp||[]).push([[201],{553:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1158-luyen-tam-cau"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1158-luyen-tam-cau"}},[n._v("#")]),n._v(" Chương 1158: Luyện tâm cầu")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Thánh Nhân Trương gia?” Trương Huyền sầm mặt lại."),t("br"),t("br"),n._v("Lúc trước Ngô sư cùng Hàn hội trưởng từng đã nói với hắn, Lạc gia vị tiểu công chúa kia cùng một cái lớn Thánh Nhân quý tộc có hôn ước... Mà gia tộc này, liền là Thánh Nhân Trương gia."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, vừa nghe đến cái tên này, trong lòng liền vô cùng không thoải mái."),t("br"),t("br"),n._v("Chuyện khác bên trên, có thể làm được tâm như chỉ thủy, gợn sóng không sinh, nhưng ở trong chuyện này, tuyệt đối làm không được."),t("br"),t("br"),n._v("Nếu không phải như vậy, lúc trước cũng không trở thành nghe được Phùng Huân thổ lộ, liền đánh hắn hai bữa."),t("br"),t("br"),n._v("“Không sai! Bất quá, ta cũng chỉ là suy đoán, trên thực tế không cách nào xác định.” Mộc sư gật đầu."),t("br"),t("br"),n._v("“Không đúng sao!” Trương Huyền chần chờ một chút, nói: “Đã cái này Thánh tử điện đã dung nạp Thánh Nhân quý tộc hậu nhân, Trương gia người hẳn là có thể trực tiếp đi vào, vì cái gì chạy đến Thanh Nguyên đế quốc đoạt cái này chỉ có một cái danh ngạch?”"),t("br"),t("br"),n._v("“Thánh tử điện đối Thánh Nhân quý tộc, quả thực muốn so đế quốc càng thêm rộng rãi, phong hào đế quốc Danh Sư đường bình thường chỉ có một cái danh ngạch, mà một cái Thánh Nhân quý tộc, thấp nhất đều có mười cái. Giống Trương gia loại này thế lực bá chủ tồn tại, chỉ sợ hàng năm có thể cầm tới ít nhất 30 cái...”"),t("br"),t("br"),n._v("Mộc sư nhẹ gật đầu, tiếp tục nói: “Bất quá, đối với loại này đại gia tộc, 30 cái danh ngạch, không có khả năng bao gồm tất cả thiên tài, nhiều nhất chỉ có thể cam đoan hắn huyết mạch tinh thuần hạch tâm con cháu, có thể đi vào, mà đối với những cái kia chi thứ con cháu, liền không có cơ hội...”"),t("br"),t("br"),n._v("“Cho nên những người này, bình thường đều sẽ phân tán đến cấp dưới phong hào đế quốc, tranh thủ danh ngạch, một khi đi vào Thánh tử điện, liền có thể nhận được tốt nhất tài nguyên cùng ưu tú nhất truyền thừa, đối với đại gia tộc chi thứ tới nói, tuyệt đối là thay đổi vận mệnh cơ hội tốt nhất, không có người sẽ buông tha cho...”"),t("br"),t("br"),n._v("“Vậy bọn hắn liền đến nơi này? Có bọn họ, phong hào đế quốc thiên tài, chẳng phải liền không có cơ hội?”"),t("br"),t("br"),n._v("Trương Huyền không vui."),t("br"),t("br"),n._v("Một cái phong hào đế quốc chỉ có một cái danh ngạch, đối phương chạy tới chiếm, cái trước chẳng phải liền mất đi?"),t("br"),t("br"),n._v("“Mạnh được yếu thua, đây cũng là chuyện không có cách nào khác, nếu như phong hào đế quốc thiên tài có thể vượt qua hắn, cũng không dám làm loạn, nhưng mà không vượt qua được, chỉ có thể trơ mắt nhìn danh ngạch bị cướp đi, nhưng cũng bất lực.”"),t("br"),t("br"),n._v("Mộc sư lắc đầu."),t("br"),t("br"),n._v("Bất kể ở nơi nào, chỉ cần có danh sư đường, liền xem như Thánh Nhân quý tộc, cũng không dám làm được quá phận."),t("br"),t("br"),n._v("Nhưng mà của ngươi thiên tài không bằng ta, vậy liền thật không tiện."),t("br"),t("br"),n._v("Thánh tử điện chỉ cần người mạnh nhất, không bằng người khác, liền là không bằng, nói nhiều hơn nữa cũng vô dụng."),t("br"),t("br"),n._v("“Điều này cũng đúng...” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Thật giống như Chiến sư đường tuyển chọn, bọn họ Hồng Viễn đế quốc học sinh, sức chiến đấu mạnh mẽ, tự nhiên mà vậy liền bị toàn bộ tuyển chọn, cái khác ba cái học viện, chỉ có thể khôn sống mống chết, vốn rất nhiều rất có hi vọng, cũng không có tư cách."),t("br"),t("br"),n._v("Tu luyện liền là như thế tàn khốc."),t("br"),t("br"),n._v("Bất quá, có danh sư đường tại, rất nhiều quy tắc xem như công bằng, liền xem như bình thường thân phận, chỉ cần có đầy đủ thiên phú, đồng dạng có thể ra mặt, tựa như hắn... Bất quá là Thiên Huyền vương quốc một đứa cô nhi mà thôi, lại nương tựa theo cố gắng của mình, từng bước một đi tới hiện tại, thậm chí còn thành danh sư học viện viện trưởng."),t("br"),t("br"),n._v("“Vị này Trương Cửu Tiêu, cứ việc chỉ là Trương gia chi thứ, nhưng mà thiên phú quả thực rất đáng sợ, năm nay vừa mới hai mươi bảy tuổi, đã đạt đến thất tinh trung phẩm danh sư, Thánh vực tam trọng đỉnh phong, nghiền ép Thanh Nguyên đế quốc Danh Sư đường tất cả danh sư, tự nhiên không hề nghi ngờ liền được danh ngạch.”"),t("br"),t("br"),n._v("Mộc sư cảm khái: “Thật ra thì đại gia tộc chi thứ, địa vị cũng không cao, có thể hưởng dụng tài nguyên cũng không nhiều, bằng chừng ấy tuổi, có thể đạt tới loại thực lực này, ném đi một bộ phận huyết mạch năng lực, thiên phú và cố gắng cũng làm cho người không thể không phục.”"),t("br"),t("br"),n._v("“Ừm!” Trương Huyền cảm động lây."),t("br"),t("br"),n._v("Đúng là như thế, bất kể lại có thiên phú người, nếu như không đủ cố gắng, cũng đều là uổng công."),t("br"),t("br"),n._v("Thật giống như hắn, nhọc nhằn khổ sở tu luyện tiếp cận thời gian một năm, cũng mới đạt tới loại thực lực này, trong đó chua xót một lời khó nói hết, không đủ ngoại nhân nói vậy."),t("br"),t("br"),n._v("“Thánh tử điện ta khẳng định muốn đi, nên như thế nào mới có thể có đến cái này danh ngạch?”"),t("br"),t("br"),n._v("Trương Huyền nhìn qua."),t("br"),t("br"),n._v("Đã Lạc Nhược Hi tại đây cái Thánh tử điện, như vậy hắn khẳng định muốn đi, không có tác dụng phương thức gì, đều nhất định muốn nhận được danh ngạch."),t("br"),t("br"),n._v("“Chỉ có một lần cơ hội, sau 20 ngày, Thánh tử điện sẽ chuyên môn phái tới tiếp dẫn sứ giả, tiếp đón thiên tài đi tới, nếu như trước lúc này, thực lực của ngươi có thể đạt tới Thánh vực tam trọng đỉnh phong, thuận lợi trở thành thất tinh danh sư, các hạng thiên phú đều vượt qua vị này Trương Cửu Tiêu, tự nhiên là có thể thu hoạch được tư cách, ta biết thời gian có thể sẽ có chút gấp gáp, nhưng mà cũng không có cái khác lựa chọn...”"),t("br"),t("br"),n._v("Mộc sư nói."),t("br"),t("br"),n._v("“20 ngày?” Trương Huyền nhẹ gật đầu: “Tốt!”"),t("br"),t("br"),n._v("Nếu như chỉ là hai, ba ngày thời gian, khả năng còn có chút gấp gáp, 20 ngày đối với hắn mà nói tuyệt đối đủ dùng."),t("br"),t("br"),n._v("“Tốt?” Mộc sư nghi ngờ nhìn lại: “Ta nhớ được không sai, thực lực của ngươi hẳn là chỉ có Thánh vực nhất trọng đỉnh phong đi, khoảng cách tam trọng, còn có trọn hai cái đại cấp bậc...”"),t("br"),t("br"),n._v("“A, đêm qua không cẩn thận đột phá Thánh vực nhị trọng đỉnh phong!”"),t("br"),t("br"),n._v("Trương Huyền cười cười."),t("br"),t("br"),n._v("Hắn không có cố ý biểu hiện ra tu vi, liền xem như Mộc sư cũng nhìn không ra đến, lúc này nói hết, hơi hơi tiết lộ một tia."),t("br"),t("br"),n._v("“Không cẩn thận? Thánh vực nhị trọng... Đỉnh phong?” Mộc sư khóe miệng giật một cái, một hơi giấu ở ngực, thật lâu mới chậm tới, nhịn không được cảm khái: “Trương sư thiên phú quả nhiên lợi hại!”"),t("br"),t("br"),n._v("Tại Khưu Ngô cung thời điểm, đối phương vẫn chỉ là Thánh vực nhất trọng đỉnh phong, một tháng thời gian liền trọn tăng lên, một cái đại cấp bậc, tốc độ tiến bộ nhanh chóng, quả thực nghe rợn cả người."),t("br"),t("br"),n._v("“Chẳng qua chỉ là như vậy lời nói, chỉ sợ vẫn là không đủ... Không nói trước Thánh vực tam trọng Thai Anh cảnh, cùng Thần Thức cảnh có khác biệt cực lớn, chỉ đem linh hồn cùng nguyên thai hoàn mỹ dung hợp, mặc dù có thiên phú, chỉ sợ cũng cần tiêu tốn mấy năm thời gian, mới có thể hoàn thành.”"),t("br"),t("br"),n._v("Mộc sư cũng không xem trọng."),t("br"),t("br"),n._v("Tu vi đạt tới Thánh vực về sau, nhất trọng nhất trọng thiên, cho dù thiên phú tuyệt cao người, cũng không biết phải hao phí bao nhiêu thời gian mới có thể đi vào bước một điểm, muốn tại trong vòng 20 ngày đột phá, nói thật... Có chút làm khó."),t("br"),t("br"),n._v("“Mấu chốt nhất là, Trương Cửu Tiêu cũng không phải là bình thường Thánh vực tam trọng đỉnh phong, mà là tích lũy vô cùng hùng hậu, có thể vượt cấp chiến đấu, bình thường tứ trọng cường giả đều rất khó vượt qua, liền xem như ta, đã đột phá, muốn vượt qua, cũng là năm năm số lượng...” Mộc sư cảm khái."),t("br"),t("br"),n._v("Hắn đã từng là Mộc gia hạch tâm con cháu, nguyên nhân chính là như vậy, mới có thể bởi vì rò rỉ công pháp mà bị trục xuất gia tộc, đột phá đến tứ trọng về sau, mới có thể cùng đối phương tam trọng đỉnh phong, lực lượng ngang nhau, đủ thấy đáng sợ."),t("br"),n._v("“Ngươi cùng hắn năm năm số lượng?”"),t("br"),t("br"),n._v("Trương Huyền nhíu nhíu mày: “Mộc sư, có thể hay không kiểm tra một chút thực lực của ta?”"),t("br"),t("br"),n._v("“Kiểm tra?” Biết hắn ý tứ, Mộc sư cười cười, mang theo hắn đi tới Danh Sư đường một chỗ luyện võ trường, hai tay khoanh ở trước ngực, dưới chân không khép không hở: “Ngươi có thể xuất thủ, hôm qua mới vừa mới đột phá, hẳn là tu vi còn không có hoàn toàn củng cố, yên tâm đi, ta sẽ không dùng toàn lực, chỉ là cho ngươi trải nghiệm một chút, Nguyên Thần cảnh cường giả đáng sợ...”"),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Mộc sư tiếng nói chưa hạ thấp, liền thấy một thân ảnh bất ngờ xuất hiện trước mắt, ngay sau đó một cái nắm đấm, trực tiếp đối với ngực đập tới."),t("br"),t("br"),n._v("“Ừm?”"),t("br"),t("br"),n._v("Không nghĩ tới vị này tốc độ nhanh như vậy, lông mày nhịn không được nhảy lên, Mộc sư đem lực lượng toàn thân tập trung lại, đang định đối kháng, lập tức cảm thấy một cỗ áp bức linh hồn lực lượng, phô thiên cái địa tập kích tới."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Còn chưa kịp phản ứng, cả người lập tức bay ngược ra ngoài, trực tiếp bay ra luyện võ trường, sống lưng tầng tầng dán tại trên vách tường, từng ngụm từng ngụm phun máu tươi."),t("br"),t("br"),n._v("Ngay sau đó, bên tai vang lên thanh niên tràn đầy giọng áy náy: “Thật không tiện, Mộc sư, ngươi còn tốt chứ? Ta vừa rồi đã rất khắc chế, chỉ dùng một phần năm lực lượng...”"),t("br"),t("br"),n._v("“Phốc!”"),t("br"),t("br"),n._v("Sắc mặt trắng nhợt, một ngụm máu tươi phun ra, Mộc sư suýt chút nữa tại chỗ đã hôn mê."),t("br"),t("br"),n._v("Vừa rồi trong nháy mắt, hắn thật hoài nghi mình là chống lại Thánh vực tứ trọng cường giả đỉnh phong, mà không phải nhị trọng."),t("br"),t("br"),n._v("Loại thực lực này liền xem như năm cái hắn, cũng không phải đối thủ."),t("br"),t("br"),n._v("Vội vã đem Mộc sư theo trên tường bóc đi, Trương Huyền mặt mũi áy náy, đồng thời mang theo nghi ngờ: “Ngươi nói vị kia Trương Cửu Tiêu danh sư, cũng chỉ có chút thực lực ấy?”"),t("br"),t("br"),n._v("“Khụ khụ!”"),t("br"),t("br"),n._v("Lần nữa ho ra một ngụm máu tươi, Mộc sư hơi đỏ mặt."),t("br"),t("br"),n._v("Mặc dù Mộc gia công pháp không am hiểu chiến đấu, nhưng mà hắn dù sao từng là nhận được đích truyền hạch tâm con cháu, sức chiến đấu so cùng cấp bậc liền muốn cường đại không ít... Kết quả không nghĩ tới liền đối phương một quyền đều không có chống đỡ, liền bị đánh đến trên tường."),t("br"),t("br"),n._v("Suy nghĩ một chút đều tràn đầy tâm tắc."),t("br"),t("br"),n._v("“Trên thực tế, Trương Cửu Tiêu thực lực, so ta hơi mạnh một chút... Bất quá, như ngươi loại này sức chiến đấu, cho dù không có đột phá, hẳn là cũng đầy đủ!”"),t("br"),t("br"),n._v("Chậm nửa ngày mới khôi phục nguyên khí, Mộc sư có chút lúng túng nói."),t("br"),t("br"),n._v("Chỉ là Thánh vực nhị trọng giống như này mạnh mẽ, một khi đột phá, Trương Cửu Tiêu liền xem như Trương gia con cháu, chỉ sợ cũng rất khó chống lại."),t("br"),t("br"),n._v("“Loại trừ đối thực lực cùng danh sư cấp bậc có yêu cầu, Thánh tử điện còn cần tiến hành dạng gì khảo hạch? Không bằng Mộc sư tiếp tục theo ta luyện một chút...” Trương Huyền nói."),t("br"),t("br"),n._v("“Không cần!” Biến sắc, Mộc sư liền vội vàng lắc đầu."),t("br"),t("br"),n._v("Chỉ thử một chút tu vi, thiếu chút nữa bỏ mình, hắn sợ tiếp tục nữa, tâm ma sinh sôi, vừa mới khôi phục tự tin lần nữa tan rã."),t("br"),t("br"),n._v("“Ngươi bây giờ phải nhanh một chút khảo hạch thất tinh danh sư, Thánh tử điện yêu cầu thấp nhất, cần đạt tới cấp bậc này... Làm không được, cho dù sức chiến đấu mạnh hơn, cũng không có phương pháp!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Hai ngày này hắn mục tiêu lớn nhất, chính là muốn khảo hạch thất tinh danh sư thành công."),t("br"),t("br"),n._v("Cho dù không cần đối phương nói, cũng sẽ cố gắng."),t("br"),t("br"),n._v("“Nên nói ta cũng nói kết thúc, ta còn muốn đi đường, như vậy cáo từ.”"),t("br"),t("br"),n._v("Giao phó xong Mộc sư không nói thêm lời, xoay người vội vã mà đi."),t("br"),t("br"),n._v("Nói thật, cùng cái tên này thời gian chung đụng càng dài, chịu đến tiến công càng nhiều, thật sợ thật vất vả gom góp tích luỹ lòng tin, vì vậy mà tan vỡ."),t("br"),t("br"),n._v("“Ây...”"),t("br"),t("br"),n._v("Không nghĩ tới Mộc sư nói đi là đi, không chút nào dây dưa dài dòng, Trương Huyền lắc đầu, đi ra luyện võ trường, tiếp tục hướng Ngô sư chỗ ở địa phương đi đến."),t("br"),t("br"),n._v("“Trương sư, cố lên!”"),t("br"),t("br"),n._v("“Trương sư, chúng ta tin tưởng ngươi, khẳng định có thể sáng chế ra một cái mới ghi chép...”"),t("br"),t("br"),n._v("Mới đi không xa, liền nghe đến liên tiếp, điên cuồng tiếng thét chói tai."),t("br"),t("br"),n._v("Xuyên qua dòng người, Trương Huyền nhìn sang, lập tức nhìn thấy một cái thật dài cầu đá xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Một người đang đứng tại trên cầu đá, trên đầu mồ hôi phả ra, tựa hồ tại khổ sở kiên trì."),t("br"),t("br"),n._v("Chính là vị kia Thánh Nhân Trương gia hậu nhân, Trương Cửu Tiêu."),t("br"),t("br"),n._v("“Đây là tại làm gì?”"),t("br"),t("br"),n._v("Trương Huyền mặt mũi khó hiểu."),t("br"),t("br"),n._v("“Xông luyện tâm cầu!”"),t("br"),t("br"),n._v("Cách đó không xa một cái danh sư mở miệng."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1166"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1168"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);