(window.webpackJsonp=window.webpackJsonp||[]).push([[2019],{2374:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-740-cong-than"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-740-cong-than"}},[n._v("#")]),n._v(" Chương 740: Công thần")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 740: Công thần"),t("br"),t("br"),n._v("Nghe được Nguyên phó viện trưởng đột nhiên nói như vậy, Lạc Thất Thất chần chờ một chút, gật gật đầu: “Lão sư xác thực chỉ có hai mươi tuổi, thoạt nhìn cũng có chút trắng nõn...”"),t("br"),t("br"),n._v("Trương sư tu luyện Ngũ Diệu Kim Thân, làn da như là như trẻ con, không tỳ vết chút nào, ngoại nhân thoạt nhìn thật có chút trắng nõn."),t("br"),t("br"),n._v("“Vừa tới đến học viện, lại gọi Trương Huyền, hai mươi tuổi...” Nguyên Hồng phát điên."),t("br"),t("br"),n._v("Dường như, vị kia bị hắn phái người coi chừng, ý định thật tốt bồi thường gia hỏa, cũng gọi cái tên này, hơn nữa hết thảy đều ăn khớp được..."),t("br"),t("br"),n._v("“Nguyên phó viện trưởng, làm sao vậy?”"),t("br"),t("br"),n._v("Thấy mình vị này phó viện trưởng, sau khi hỏi xong, liền một bộ thần bất thủ xá bộ dáng, Triệu Bính Tuất nhịn không được nhìn lại, mang theo nghi hoặc: “Chẳng lẽ ngươi biết vị này Trương Huyền Trương sư?”"),t("br"),t("br"),n._v("“Ta...” Nguyên Hồng không biết trả lời như thế nào, lần nữa nhìn về phía Lạc Thất Thất, không dám tin tưởng hỏi: “Ta nghe nói vị này Trương Huyền Trương sư có sư thừa lai lịch, lão sư của hắn hình như là một vị siêu việt lục tinh danh sư!”"),t("br"),t("br"),n._v("“A, sư tổ Dương Huyền, cụ thể cấp bậc ta cũng không biết, có điều là nên vượt qua lục tinh!” Lạc Thất Thất gật đầu."),t("br"),t("br"),n._v("“Dương Huyền...”"),t("br"),t("br"),n._v("Nguyên Hồng nhoáng một cái."),t("br"),t("br"),n._v("Cái này không cần tiếp tục hỏi, cũng có thể xác nhận."),t("br"),t("br"),n._v("Tên kia từng tự giới thiệu, nói là cái gì Dương Huyền học sinh, tên của hai người tương đồng, tuổi tác cũng tương đương, cũng đều là tân sinh... Thật sự nếu không có thể xác nhận, cũng không cần sống."),t("br"),t("br"),n._v("Cái này gọi chuyện gì!"),t("br"),t("br"),n._v("Tên kia vừa đến học viện, liền làm hỏng địa hỏa thông đạo, sau đó phá vỡ ghi chép thông đạo, làm toàn bộ danh sư học viện phân bộ, vô cùng thê thảm, không cách nào gặp người!"),t("br"),t("br"),n._v("Vốn cho rằng là học viện khác cố ý phái từng tới quấy rối, nằm mơ đều không nghĩ tới, có như thế lai lịch!"),t("br"),t("br"),n._v("Liều mình cứu người, độc lập đối kháng Dị Linh tộc nhân, cam tâm tình nguyện dâng ra Ngô Dương Tử truyền thừa... Mỗi một dạng, đều là công đức vô lượng, để cho người ta bội phục."),t("br"),t("br"),n._v("Một cái cái gì cũng đều không hiểu, không biết tự lượng sức mình phá hư chi vương, một cái đại công vô tư, dám vì nhân loại không kịp sống chết danh sư..."),t("br"),t("br"),n._v("Làm sao có thể là cùng một cái!"),t("br"),t("br"),n._v("Cái này... Nhân cách cũng quá phân liệt đi!"),t("br"),t("br"),n._v("“Đến cùng chuyện gì xảy ra?” Thấy Nguyên Hồng không trả lời bản thân, ngược lại một bộ sắp khóc biểu lộ, Triệu Bính Tuất lại nhịn không được, tiếp tục hỏi."),t("br"),t("br"),n._v("Bất kể nói thế nào, đều là lục tinh danh sư, đạt tới hỉ nộ không lộ trình độ đi, làm sao còn bộ dáng này, như cha mẹ chết?"),t("br"),t("br"),n._v("“Là như vậy, ta vừa rồi... Không phải nói có chuyện muốn hướng ngươi bẩm báo ư?”"),t("br"),t("br"),n._v("Nghe được viện trưởng lời nói, Nguyên Hồng cái này mới phản ứng được, vội vàng ôm quyền."),t("br"),t("br"),n._v("“Ừm!” Triệu Bính Tuất gật đầu: “Chuyện gì?”"),t("br"),t("br"),n._v("Mới vừa ở Hùng viện phó mở miệng thời điểm, quả thực cũng đã nói có việc muốn bẩm báo."),t("br"),t("br"),n._v("“Là... Ghi chép thông đạo đổ sụp, phân bộ đại điện cũng sập một nửa...” Nguyên Hồng hơi đỏ mặt."),t("br"),t("br"),n._v("Hắn thân là luyện khí học viện phó viện trưởng, chưởng quản phân bộ đại điện, kết quả hang ổ bị người chơi sập một nửa, nói ra, đoán chừng đều muốn bị mấy vị khác phó viện trưởng tươi sống chết cười."),t("br"),t("br"),n._v("“Ghi chép thông đạo cùng phân bộ đại điện đều sập? Chẳng lẽ Dị Linh tộc nhân công đã tới?”"),t("br"),t("br"),n._v("Triệu Bính Tuất lông mày giương lên."),t("br"),t("br"),n._v("Bọn họ vừa rồi tại nơi này thương nghị sự tình, phong bế cùng phía ngoài giao lưu, chuyện này cũng không biết rõ tình hình."),t("br"),t("br"),n._v("Bất quá, những thông đạo này chắc chắn cực kỳ, sừng sững ngàn năm không ngã, đột nhiên đổ sụp, trừ phi những cái kia Dị Linh tộc nhân xâm lấn, nếu không làm sao có thể?"),t("br"),t("br"),n._v("“Không phải... Dị Linh tộc!”"),t("br"),t("br"),n._v("Không nghĩ tới viện trưởng vậy mà nghĩ đến điểm này, Nguyên Hồng tràn đầy xấu hổ, ngay cả vội khoát khoát tay: “Là có người từng tới khiêu chiến địa hỏa thông đạo...”"),t("br"),t("br"),n._v("Nói đem trước chuyện phát sinh kỹ càng nói một lần."),t("br"),t("br"),n._v("“Ngươi nói... Có người tại địa hỏa thông đạo chờ đợi hơn một canh giờ? Lúc đi ra, thông đạo đổ sụp, địa tâm Hồng nham bị hấp thu sạch sẽ, biến thành phế phẩm?”"),t("br"),t("br"),n._v("“Hắn còn mượn nhờ Địa Tâm hỏa tạo thành một cái mới thông đạo, chỉ bất quá ngươi đi vào... Nổ tung?”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nghe xong hắn giới thiệu, cái khác mấy cái phó viện trưởng cũng giống là gặp quỷ đồng dạng biểu lộ nhìn qua."),t("br"),t("br"),n._v("Tại địa hỏa thông đạo đợi hơn một canh giờ, thậm chí còn có thể sử dụng Địa Tâm hỏa tạo ra một cái mới thông đạo... Thật hay giả?"),t("br"),t("br"),n._v("Đây thật là tứ tinh danh sư có thể làm sự tình?"),t("br"),t("br"),n._v("Phải biết liền xem như bọn họ đều làm không được!"),t("br"),t("br"),n._v("“Đúng...” Nguyên Hồng gật đầu: “Cái này náo ra động tĩnh lớn như vậy người, cũng gọi... Trương Huyền, cũng là một vị mới vừa thông qua khảo hạch tân sinh!”"),t("br"),t("br"),n._v("“Cũng gọi Trương Huyền? Chẳng lẽ là một người?”"),t("br"),t("br"),n._v("“Có thể theo Dị Linh tộc khôi lỗi trong tay đem người cứu ra, thuận tiện làm hư Địa Cung, dũng cảm túc trí, có lẽ, thật chỉ có vị này nhân vật truyền kỳ, mới có loại này bản lĩnh!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Mấy vị khác phó viện trưởng tất cả đều sững sờ, nhịn không được nói."),t("br"),t("br"),n._v("Nếu như trước đó cùng bọn hắn nói, có người tại địa hỏa thông đạo chờ đợi một canh giờ, đồng thời làm toàn bộ luyện khí sư công hội danh sư học viện phân bộ, huyên náo gà bay chó chạy, tuyệt đối không thể tin được."),t("br"),t("br"),n._v("Mà bây giờ, nghe được vị này Trương sư truyền kỳ trải qua, những sự tình này đã không tính là để cho người ta khiếp sợ, chỉ có thể nói tiểu đả tiểu nháo."),t("br"),t("br"),n._v("“Địa hỏa thông đạo, đo lường thân thể cường độ, càng cường đại, đợi đến thời gian càng dài, vị này Trương sư, nếu thật là bọn họ trong miệng vị kia, nhận được Ngô Dương Tử tiền bối truyền thừa, tu luyện qua Ngũ Diệu Kim Thân, có thể kiên trì một canh giờ, quả thực không phải việc khó!”"),t("br"),t("br"),n._v("Triệu Bính Tuất nhẹ gật đầu."),t("br"),t("br"),n._v("Nghe được đợi một canh giờ, hắn cũng không thể tin được, có điều, vừa nghĩ tới từng chiếm được Ngô Dương Tử truyền thừa, tu luyện qua Ngũ Diệu Kim Thân, liền không nhịn được gật đầu."),t("br"),n._v(" Bộ này luyện thể công pháp, mặc dù không tu luyện qua, cũng chưa từng thấy qua, cũng tuyệt đối là toàn bộ Hồng Viễn đế quốc phạm vi bên trong, đỉnh phong nhất luyện thể pháp quyết một trong."),t("br"),t("br"),n._v("Bằng không thì cũng không có khả năng để Ngô Dương Tử đạt tới loại kia độ cao."),t("br"),t("br"),n._v("“Ta... Không biết, còn tưởng rằng là hắn cố ý, để hắn bồi thường...”"),t("br"),t("br"),n._v("Nguyên Hồng hơi đỏ mặt."),t("br"),t("br"),n._v("Nếu là sớm biết hắn thật sự kiên trì lâu như vậy, không gian lận, cũng là không gào to bồi thường!"),t("br"),t("br"),n._v("Đã mệnh danh khiêu chiến, liền có đủ loại hao tổn, người ta tiếp tục kiên trì, thông đạo không tiếp tục kiên trì, là chất lượng vấn đề... Loại tình huống này, đi tìm người khiêu chiến bắt đền, truyền đi đều không đủ mất mặt!"),t("br"),t("br"),n._v("Không bồi thường, chuyện về sau tự nhiên cũng sẽ không phát sinh, càng không có hiện tại không trên không dưới cục diện khó xử."),t("br"),t("br"),n._v("“Xuất hiện loại tình huống này, không thể trách đối phương, chỉ có thể nói cái này địa hỏa thông đạo vẫn là có rất lớn thiếu hụt, gặp được cao thủ chân chính, lại không được...”"),t("br"),t("br"),n._v("Triệu Bính Tuất nhẹ gật đầu."),t("br"),t("br"),n._v("“Đúng vậy a!” Nguyên Hồng nhẹ gật đầu: “Cái kia... Hiện tại đã không phải là chỉ riêng địa hỏa thông đạo vấn đề, toàn bộ ghi chép thông đạo cùng nửa cái phân bộ tổn thất, làm sao bây giờ?”"),t("br"),t("br"),n._v("Nếu như chỉ là địa hỏa thông đạo không kiên trì nổi phá hư ngược lại cũng thôi, hiện tại là toàn bộ ghi chép thông đạo đều phế đi."),t("br"),t("br"),n._v("“Tổn thất... Chuyện này như vậy không tiến hành nữa!” Triệu Bính Tuất dừng lại một chút, vung vung tay."),t("br"),t("br"),n._v("“Không tiến hành nữa, như vậy sao được...”"),t("br"),t("br"),n._v("Nguyên Hồng sững sờ, nhịn không được nhìn qua."),t("br"),t("br"),n._v("Thật muốn không tiến hành nữa, chẳng khác nào toàn bộ luyện khí sư học viện, trắng mất không mấy ngàn linh thạch thượng phẩm, liền coi như bọn họ tài đại khí thô, cũng có chút không chịu đựng nổi đi!"),t("br"),t("br"),n._v("“Tổn thất? Đem Phủ Cầm Luyện Khí pháp giao cho học viện cống hiến, cũng đã vượt xa mấy ngàn linh thạch thượng phẩm!” Triệu Bính Tuất lắc đầu."),t("br"),t("br"),n._v("Ngô Dương Tử truyền thừa, có thể để cho vô số luyện khí sư đối luyện khí lý giải tăng nhiều, đối luyện khí học viện cống hiến ở chỗ liên tục không ngừng, tuyệt không phải mấy ngàn linh thạch thượng phẩm có thể so sánh."),t("br"),t("br"),n._v("Thậm chí, không chỉ mấy ngàn linh thạch, coi như hơn vạn, mấy vạn đều không cách nào so sánh."),t("br"),t("br"),n._v("Phủ Cầm Luyện Khí pháp, luyện chế vũ khí giống như vuốt ve dây đàn, không chỉ cho người ta khả năng thưởng thức cái đẹp, càng có thể để cho luyện khí thành làm một loại nghệ thuật, để càng nhiều người trầm mê trong đó, không tự giác tấn thăng đối luyện khí lý giải!"),t("br"),t("br"),n._v("Có thể đoán được, có cái này bí tịch, luyện khí học viện, không dùng đến mười năm liền sẽ vượt qua Đan viện, trở thành danh xứng với thực thứ nhất viện!"),t("br"),t("br"),n._v("Loại này cống hiến, coi như hắn viện trưởng này đều làm không được, như thế nào mấy ngàn linh thạch thượng phẩm liền có thể sánh ngang?"),t("br"),t("br"),n._v("Căn bản không phải cùng một cái khái niệm."),t("br"),t("br"),n._v("Vì học viện làm ra to lớn như vậy cống hiến, còn muốn lấy hỏi đối phương muốn lông gà vỏ tỏi bồi thường, chẳng phải rét lạnh thiên tài chân chính tâm?"),t("br"),t("br"),n._v("“Không sai, không chỉ không thể để cho hắn bồi thường, còn muốn thưởng!”"),t("br"),t("br"),n._v("“Đúng vậy a, loại thiên tài này nhất định phải thật tốt ban thưởng, để hắn lưu tại luyện khí học viện mới được!”"),t("br"),t("br"),n._v("Cái khác mấy cái phó viện trưởng nhẫn gật đầu không ngừng."),t("br"),t("br"),n._v("Danh sư đều có bao nhiêu cái phụ trợ chức nghiệp, loại thiên tài này nhất định phải lưu tại luyện khí học viện, để luyện khí trở thành hắn thứ nhất phụ tu, một khi danh chấn thiên hạ, bọn họ học viện đều có vinh yên!"),t("br"),t("br"),n._v("Nguyên Hồng khóe miệng giật một cái."),t("br"),t("br"),n._v("Tên kia đem trọn cái phân bộ, làm không sai biệt lắm phế bỏ, không bồi thường, còn muốn thưởng... Về sau hắn cái này phó viện trưởng lời nói, còn có người nghe ư?"),t("br"),t("br"),n._v("Bất quá, hắn cũng biết, viện trưởng nói là tình hình thực tế."),t("br"),t("br"),n._v("Đem Phủ Cầm Luyện Khí pháp giao lên cái này cống hiến thực sự quá lớn, đừng nói chỉ là một cái luyện khí sư công hội danh sư học viện phân bộ, coi như đem trọn cái luyện khí học viện lật cái lộn chổng vó lên trời, chỉ cần không làm bị thương học sinh, cũng không tính là gì."),t("br"),t("br"),n._v("Danh sư học viện, trọng yếu nhất chính là người, là rất nhiều danh sư, còn kiến trúc, bảo vật, coi như hủy đi, cũng có thể một lần nữa xây dựng, chẳng có gì lạ."),t("br"),t("br"),n._v("Đừng nói học viện, coi như cao hơn quốc gia Danh Sư đường, cũng từng chịu đựng Dị Linh tộc không biết bao nhiêu lần đánh lén, đủ loại kiến trúc, cổ tịch phá hủy không biết bao nhiêu lần, nhưng danh sư hương hỏa không ngừng, vẫn như cũ nhân khí cường thịnh, là vô số người hướng tới Thánh địa."),t("br"),t("br"),n._v("“Tốt, quyết định như vậy đi, đúng, ngươi không nói hắn định thi hạch ngũ tinh luyện khí sư ư? Để hắn đi thi, chỉ cần thông qua, liền nói ta nguyện ý thu hắn làm học sinh...”"),t("br"),t("br"),n._v("Triệu Bính Tuất cười cười."),t("br"),t("br"),n._v("Thiên tài như thế nếu như có thể thu vì học sinh, tuyệt đối sẽ là một đại mỹ nói."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Nguyên Hồng gật đầu."),t("br"),t("br"),n._v("“Đến cho các ngươi, cũng có công lao rất lớn, ta sẽ đi Đan viện cho các ngươi muốn mấy viên thuốc, cũng tự mình ra tay thay các ngươi tẩy kinh phạt tủy!”"),t("br"),t("br"),n._v("An bài xong Nguyên phó viện trưởng, Triệu Bính Tuất cười nhìn về phía Lạc Thất Thất đám người."),t("br"),t("br"),n._v("“Tẩy kinh phạt tủy? Đa tạ Triệu viện trưởng!”"),t("br"),t("br"),n._v("Nghe nói như thế, Lạc Thất Thất, Hình Viễn đám người tất cả đều kích động hơi đỏ mặt."),t("br"),t("br"),n._v("Thập đại trưởng lão Triệu Bính Tuất, tự mình tẩy kinh phạt tủy, một khi thành công, liền đại biểu bọn họ từ giờ trở đi, đến Bán Thánh cấp bậc, đều đem một đường thông suốt, lại không cần lo lắng!"),t("br"),t("br"),n._v("Tẩy kinh phạt tủy, tiêu hao rất nhiều, liền xem như cường giả Thánh vực, đều sẽ không dễ dàng đi làm, đối bọn hắn như vậy, tuyệt đối coi là lớn nhất phần thưởng!"),t("br"),t("br"),n._v("So học phần đều muốn tốt hơn nhiều!"),t("br"),t("br"),n._v("“Tốt, Nguyên Hồng, ngươi đi thay Trương sư khảo hạch đi, đúng, nhất định phải có cái tốt thái độ, không nên để cho hắn cho là chúng ta luyện khí học viện, có lỗi với có công chi thần!”"),t("br"),t("br"),n._v("Triệu Bính Tuất trịnh trọng bàn giao."),t("br"),t("br"),n._v("“Vâng!” Cúi đầu lui xuống, Nguyên Hồng đi ra phòng làm việc của viện trưởng, nhịn không được một mặt cười khổ."),t("br"),t("br"),n._v("Vừa tới thời điểm, hận không thể đem tiểu tử kia giết cho chó ăn, hơn nữa lời thề son sắt nói muốn bẩm báo viện trưởng, tốt dễ xử lý... Kết quả, liền xuất hiện loại này nghịch chuyển!"),t("br"),t("br"),n._v("Chuyện này là sao!"),t("br"),t("br"),n._v("Suy nghĩ một chút đều tràn đầy tâm tắc."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-748"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-750"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);