(window.webpackJsonp=window.webpackJsonp||[]).push([[2022],{2378:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-742-khao-hach-luyen-khi-su"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-742-khao-hach-luyen-khi-su"}},[n._v("#")]),n._v(" Chương 742: Khảo hạch luyện khí sư")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 742: Khảo hạch luyện khí sư"),t("br"),t("br"),n._v("“Chưa hề kiểm tra qua?”"),t("br"),t("br"),n._v("Nguyên Hồng thân thể nhoáng một cái, suýt chút nữa không một cái lão huyết phun ra."),t("br"),t("br"),n._v("Chung quanh theo tới rất nhiều danh sư cũng tròng mắt sắp rơi trên mặt đất."),t("br"),t("br"),n._v("Còn chưa phải là luyện khí sư, liền chạy tới kiểm tra ngũ tinh... Còn nói không phải tới quấy rối?"),t("br"),t("br"),n._v("Luyện khí sư, thượng cửu lưu, đại lục đỉnh phong nhất chức nghiệp một trong, ở đây mỗi một cái, vì trở thành một thành viên trong đó, đều mất ăn mất ngủ học tập, cái nào không hao tốn mấy năm thời gian, mới lấy nhập môn."),t("br"),t("br"),n._v("Coi như nhanh nhất, đều ít nhất phải mười năm trở lên khổ học, chậm, không có ba, năm mươi năm, không có khả năng thành công!"),t("br"),t("br"),n._v("Ngươi một cái chừng hai mươi tuổi, liền nhất tinh luyện khí sư đều không phải là gia hỏa, chạy tới mới mở miệng liền muốn khảo hạch ngũ tinh?"),t("br"),t("br"),n._v("Thật chẳng lẽ cho rằng... Đây là một loại theo liền có thể hoàn thành chức nghiệp?"),t("br"),t("br"),n._v("Quá trò đùa đi!"),t("br"),t("br"),n._v("“Ta tự mình chuyên môn học qua luyện khí, chỉ là chưa kịp khảo hạch đẳng cấp...”"),t("br"),t("br"),n._v("Nhìn ra mọi người khiếp sợ, Trương Huyền vội vã giải thích một câu, hỏi tiếp: “Nếu không như vậy đi, ta theo nhất tinh bắt đầu kiểm tra! Nơi này... Có thể khảo hạch nhất tinh ư? Còn có, nhất tinh khảo hạch trước, cần tiên khảo hạch học đồ ư?”"),t("br"),t("br"),n._v("Mặc dù hắn đối luyện khí nắm giữ so với Ngô Dương Tử đều chỉ mạnh không yếu, nhưng đi chưa hề khảo hạch qua, cẩn thận nói đến, hiện tại liền một cái học đồ đều không phải là, chớ nói chi là, chính thức luyện khí sư."),t("br"),t("br"),n._v("“Học đồ...”"),t("br"),t("br"),n._v("  Khóe miệng giật một cái, Nguyên Hồng che cái trán."),t("br"),t("br"),n._v("Không phải tận mắt nhìn thấy, đều không thể tin được đây là sự thực."),t("br"),t("br"),n._v("Một cái liền học đồ đều không phải là người, chạy đến luyện khí học viện, luôn mồm muốn khảo hạch ngũ tinh luyện khí sư, không chỉ có như thế, còn làm toàn bộ học viện gà bay chó chạy, ghi chép thông đạo tính cả đại điện đều cả phế đi..."),t("br"),t("br"),n._v("Mấu chốt nhất là... Đường đường viện trưởng, thập đại danh sư một trong Triệu Bính Tuất, còn muốn thu hắn làm học sinh!"),t("br"),t("br"),n._v("Coi như tự mình trải qua, đều cảm thấy cùng nằm mơ giống như."),t("br"),t("br"),n._v("“Học đồ ngươi không cần nghiên cứu, trực tiếp khảo hạch nhất tinh luyện khí sư... Tranh thủ hôm nay thành công, còn ngũ tinh... Sau này hãy nói đi!”"),t("br"),t("br"),n._v("Đem buồn bực trong lòng nén ở trong lòng, Nguyên Hồng quay đầu nhìn về phía trước mắt một đống danh sư: “Các ngươi ai có học đồ lệnh bài?”"),t("br"),t("br"),n._v("Hắn loại cấp bậc này luyện khí sư, cho dù có người hỗ trợ làm việc vặt, cũng sẽ không thấp hơn ngũ tinh, luyện khí học đồ, cấp bậc quá thấp, không có khả năng sử dụng, thời gian dài không tiếp xúc, tự nhiên không có loại lệnh bài này."),t("br"),t("br"),n._v("“Học đồ lệnh bài?”"),t("br"),t("br"),n._v("“Giúp ta một tay đều là tứ tinh luyện khí sư, cái đồ chơi này cấp quá thấp, không có!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Mọi người chung quanh, tất cả đều lắc đầu."),t("br"),t("br"),n._v("Tại đại điện hầu như đều là ngũ tinh luyện khí sư, kém cỏi nhất đều đạt đến tứ tinh, ở đây tìm học đồ lệnh bài, rất khó làm được."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Nguyên Hồng mặt mũi xấu hổ."),t("br"),t("br"),n._v("Cái này gọi chuyện gì!"),t("br"),t("br"),n._v("Bản thân đường đường lục tinh đỉnh phong luyện khí sư, giúp người tìm học đồ lệnh bài, kết quả... Còn tìm không thấy, suy nghĩ một chút cũng là say."),t("br"),t("br"),n._v("“Phó viện trưởng, ta chỗ này... Có một cái!”"),t("br"),t("br"),n._v("Ngay tại không biết nên làm cái gì thời điểm, một cái rụt rè tiếng âm vang lên, Trương Huyền quay đầu nhìn lại, đang là trước kia tiếp đãi hắn cái kia Lý Tuyền."),t("br"),t("br"),n._v("Nàng mặc dù tại luyện khí học viện làm công, nhưng cấp bậc không cao, trên người vừa vặn có một cái đưa cho học đồ lệnh bài."),t("br"),t("br"),n._v("“Quá tốt rồi...”"),t("br"),t("br"),n._v("Gật gật đầu, Nguyên Hồng thở phào nhẹ nhõm, tiếp nhận lệnh bài tinh thần khẽ động, ở phía trên in lên Trương Huyền tên, đưa tới."),t("br"),t("br"),n._v("“Trương sư, đây là học đồ lệnh bài, ngươi cầm, phối hợp học phần liền có thể khởi động khí hải, khảo hạch nhất tinh luyện khí sư!”"),t("br"),t("br"),n._v("“Đa tạ!”"),t("br"),t("br"),n._v("Tiếp nhận lệnh bài, Trương Huyền gật gật đầu, đi về phía trước mấy bước, đột nhiên nhớ tới cái gì, ngừng lại: “Hiện tại khảo hạch nhất tinh luyện khí sư, vậy nếu như thông qua khảo hạch, muốn tiếp tục khảo hạch nhị tinh lời nói, còn cần tiếp tục tốn hao học phần ư?”"),t("br"),t("br"),n._v("“Một mực tại khí hải bên trong, là không cần, có điều, một khi đi ra, lại muốn đi vào, còn cần tiếp tục tiêu hao!” Nguyên Hồng nói."),t("br"),t("br"),n._v("Học phần là khởi động khí hải điều kiện, một mực đợi ở bên trong không ra, hai cái học phần liền đủ, chỉ cần đi ra, lại muốn đi vào, lại cần phải hao phí hai cái học phần."),t("br"),t("br"),n._v("Liền cùng một chút cảnh khu vé vào cửa đồng dạng, sau khi ra ngoài lại muốn đi vào, liền cần một lần nữa mua phiếu."),t("br"),t("br"),n._v('"Không ra là được? Cái kia... Ta có thể hay không thi xong nhất tinh luyện khí sư về sau,'),t("br"),t("br"),n._v('Ở bên trong tiếp tục khảo hạch nhị tinh?"'),t("br"),t("br"),n._v("Nghe được sau khi ra ngoài lại muốn đi vào còn cần tiếp tục tiêu hao học phần, Trương Huyền có chút nhức đầu, nhịn không được nói."),t("br"),t("br"),n._v("Hắn hai cái này học phần, vẫn là vị lão giả này, cầm bản thân học sinh, vì khảo hạch ngũ tinh luyện khí sư, cũng không thể một mực để người khác đưa học phần đi!"),t("br"),t("br"),n._v("Trân quý như thế đồ vật, không nói trước người khác có hay không, cho dù có, lão để cho người ta đưa, ân tình cũng chiếm quá lớn."),t("br"),n._v("Ân tình, liền là nhân quả."),t("br"),t("br"),n._v("Cùng nhau đi tới, Trương Huyền luôn luôn không nợ người ân tình, chính là không muốn chạm phải những vật này."),t("br"),t("br"),n._v("“Đương nhiên có thể, chỉ là... Hai cái học phần khởi động khí hải, mặc kệ khảo hạch cái nào cấp bậc, nhiều nhất chỉ có thể ở bên trong đợi hai canh giờ, nói cách khác... Hai canh giờ phía trong, bất kể ngươi có thể hay không luyện thành vũ khí, đều sẽ bị tự động đưa ra... Ngắn như vậy thời gian, ta sợ cũng là chỉ đủ khảo hạch nhất tinh luyện khí sư, coi như muốn khảo hạch cấp bậc cao hơn, cũng rất khó làm đến!”"),t("br"),t("br"),n._v("Nguyên Hồng giải thích."),t("br"),t("br"),n._v("Khí hải bị tạo ra mục đích, chính là vì thuận tiện càng nhiều người khảo hạch luyện khí sư, nâng hiệu suất cao, đã như vậy, nếu có người cầm hai cái học phần trở ra, bất luận thế nào không ra, một đợi nửa năm, còn thế nào tăng lên hiệu suất?"),t("br"),t("br"),n._v("Do đó, tiến đi khảo hạch, có thời gian giới hạn, bình thường là hai canh giờ."),t("br"),t("br"),n._v("Nói cách khác, một cái học phần một canh giờ, nếu như cảm thấy mình rất khó thông qua, liền cần tốn hao càng nhiều học phần mua sắm."),t("br"),t("br"),n._v("Mà tại mua sắm đoạn thời gian phía trong, mặc kệ khảo hạch cấp bậc gì, bất kể thành công hay không, chỉ cần đã đến giờ, đều sẽ bị tự động đưa ra."),t("br"),t("br"),n._v("Thành công, khí hải sẽ căn cứ trước ngươi đưa ra lệnh bài tin tức, tạo ra mới lệnh bài phóng xuất ra, không thành công, thì tương đương uổng phí hết học phần, không công mà lui."),t("br"),t("br"),n._v("Vị này Trương sư, có hai cái học phần, cũng liền tại bên trong có thể đợi hai canh giờ, thời gian ngắn như vậy, có thể khảo hạch nhất tinh thông qua, là tốt lắm rồi, tiếp tục kiểm tra nhị tinh... Khả năng không lớn!"),t("br"),t("br"),n._v("Lại nói, luyện khí đối chân khí tiêu hao cực lớn, một người chân khí trong cơ thể số lượng có hạn, luyện chế xong một kiện binh khí, liền sẽ tiêu hao hơn phân nửa khí lực, coi như cho ra đầy đủ thời gian, cũng chưa chắc có thể có tinh lực tiếp tục nữa ah!"),t("br"),t("br"),n._v("Do đó, hắn thấy, vị này Trương sư nghĩ tới mặc dù rất tốt, cũng rất khó xử đến."),t("br"),t("br"),n._v("Không nói đối phương, coi như hắn loại này lục tinh đỉnh phong cấp bậc luyện khí sư, hai canh giờ phía trong, cũng rất luyện chế nhiều hai, ba món binh khí mà thôi, nhiều hơn nữa, cũng sẽ không kiên trì nổi."),t("br"),t("br"),n._v("Vũ khí, cùng đan dược không giống, coi như lại thấp cấp, cũng cần vô số lần gõ, rèn luyện, nếu không, cũng không có khả năng theo hình dáng gì đều không có khí thai, biến thành vũ khí bộ dáng!"),t("br"),t("br"),n._v("“Hai canh giờ, được rồi!”"),t("br"),t("br"),n._v("Nghe được chỉ có ngắn như vậy thời gian, Trương Huyền gật gật đầu, lại không rầu rĩ, đem học đồ lệnh bài cùng thẻ học phần để ở trước cửa trên bệ đá, ánh sáng lóe lên, trước mắt khí hải từ từ mở ra."),t("br"),t("br"),n._v("Nhấc chân đi vào."),t("br"),t("br"),n._v("Khí hải gian phòng cùng trước đó ghi chép thông đạo có chút tương tự, tràn đầy đen kịt, ngay sau đó dưới chân lay động, băng chuyền đồng dạng được đưa đến một cái đại sảnh."),t("br"),t("br"),n._v("Trong sảnh, bày đầy đủ loại khoáng thạch cùng kim loại, còn có một cái cự đại lô đỉnh, luyện khí đài cao."),t("br"),t("br"),n._v("“Cái này... Đều là giả?”"),t("br"),t("br"),n._v("Nhìn chung quanh một vòng, phát hiện cảnh tượng trước mắt, cùng chân thực không khác chút nào, Trương Huyền nhịn không được tán thưởng."),t("br"),t("br"),n._v("Trước đó, cái kia gọi Lý Tuyền nữ hài đã nói, khí hải là mượn nhờ huyễn trận công hiệu, cho người ta một loại chân thực luyện khí hiệu quả, mà trên thực tế hết thảy trước mắt đều là giả."),t("br"),t("br"),n._v("Ngẩng đầu nhìn quanh một vòng, cách đó không xa trên mặt bàn thả một đống thư tịch, tiện tay mở ra, trên đó viết đủ loại binh khí phương pháp luyện chế."),t("br"),t("br"),n._v("Đến đây khảo hạch người, có thể tùy ý tuyển một loại tiến hành, chỉ cần thành công, coi như thông qua."),t("br"),t("br"),n._v("“Ta chỉ có hai cái canh giờ, nhất định phải tranh thủ khảo hạch xong ngũ tinh...”"),t("br"),t("br"),n._v("Biết học phần kiếm không dễ, thật vất vả có lần cơ hội, tự nhiên không vừa lòng chỉ khảo hạch nhất tinh liền thôi, tốt nhất có thể một hơi khảo hạch thành năm tinh luyện khí sư, đằng sau cũng sẽ không cần lại đến, miễn cho phiền phức."),t("br"),t("br"),n._v("“Cho nên... Luyện chế vũ khí càng đơn giản càng tốt, như vậy tiết kiệm thời gian cũng càng nhiều!”"),t("br"),t("br"),n._v("Đem khí thai, rèn luyện thành một thanh trường kiếm, cùng gõ ra một cái thiết chùy, rõ ràng độ khó khác biệt, tiêu tan tốn thời gian cũng không giống."),t("br"),t("br"),n._v("“Khảo hạch luyện khí sư, cùng khảo hạch luyện đan sư đồng dạng, cần đem riêng biệt tài liệu, luyện chế đến cấp bậc nhất định, mới có thể qua ải, luyện chế chính xác hay không, nhiều nhất chỉ là gia tăng chút nhỏ xíu cấp bậc, không cần phải làm quá mức tinh mỹ...”"),t("br"),t("br"),n._v("Binh khí cấp bậc, theo cứng rắn, mềm dẻo, sắc bén, đối chân khí truyền lại... Mấy người rất nhiều phương diện khảo giáo."),t("br"),t("br"),n._v("Cũng không phải là đánh càng hoa lệ, càng xinh đẹp, liền càng cao cấp hơn."),t("br"),t("br"),n._v("Mặc dù luyện chế tinh tế, khinh bạc, đối vũ khí có nhất định gia trì tác dụng, lại không phải khảo hạch luyện khí sư mấu chốt."),t("br"),t("br"),n._v("Đã như vậy... Cũng không cần phải tại khí thai bên trên dùng nhiều phí công phu."),t("br"),t("br"),n._v("Chỉ cần dung hợp tỉ lệ phù hợp, tôi thép thành công, coi như cấp bậc thấp chất liệu, khẳng định cũng có thể luyện chế ra không kém vũ khí."),t("br"),t("br"),n._v("“Những binh khí này, rèn luyện lên quá chậm...”"),t("br"),t("br"),n._v("Rất mau đem thư tịch lật một lần, biết nhờ vào hắn hiện tại rèn luyện trình độ luyện chế không ra ngược lại cũng thôi, mấu chốt nhất là, thời gian cũng chậm trễ không nổi."),t("br"),t("br"),n._v("Nói cách khác, tùy tiện lựa chọn một cái luyện chế, dù là hắn luyện khí trình độ lại cao hơn, không có hơn một canh giờ cũng không thể hoàn thành."),t("br"),t("br"),n._v("Như vậy trải qua, còn thế nào khảo hạch nhị tinh luyện khí sư?"),t("br"),t("br"),n._v("“Không cần quan tâm nhiều, bắt đầu đi!”"),t("br"),t("br"),n._v("Không chọn những sách vở này, Trương Huyền dọc theo gian phòng đi một vòng, rất nhanh tập trung bảy, tám loại kim loại cùng khoáng thạch, một cái ý nghĩ tại trong óc hình thành."),t("br"),t("br"),n._v("Học qua Thiên Đạo luyện khí, đối luyện khí lý giải, đã vượt qua một chút lục tinh đỉnh phong luyện khí sư, trước mắt những kim loại này cùng khoáng thạch, mặc dù chỉ là rất bình thường, luyện chế cấp thấp binh khí, trong tay hắn, chỉ phải phối hợp thoả đáng, cũng có thể tạo được không giống hiệu quả cùng tác dụng."),t("br"),t("br"),n._v("“Cứ như vậy đi!”"),t("br"),t("br"),n._v("Khóe miệng vung lên, bàn tay nhẹ nhàng vồ một cái, chân khí chảy xuôi như nước, vừa rồi nhìn kỹ bảy, tám loại kim loại cùng khoáng thạch bay lên, đồng thời hướng cách đó không xa nóng bỏng lò lửa bay đi."),t("br"),t("br"),n._v("Xì xì xì xì...!"),t("br"),t("br"),n._v("Chân khí truyền vào xuống, lò lửa thiêu đốt thấu đỏ, những kim loại này cùng khoáng thạch lập tức toàn bộ hòa tan ra, biến thành chất lỏng, khắp nơi chảy xuôi."),t("br"),t("br"),n._v("“Dung hợp!”"),t("br"),t("br"),n._v("Nhướng mày, Trương Huyền nhẹ nhàng cười một tiếng, bàn tay bỗng nhiên đối với mấy cái này chất lỏng bắt tới, bảy, tám loại khác biệt chất lỏng, lập tức phân tán ra đến, lấy khác biệt phương thức tiến hành dung hợp."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-750"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-752"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);