(window.webpackJsonp=window.webpackJsonp||[]).push([[203],{557:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1160-bao-lanh"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1160-bao-lanh"}},[n._v("#")]),n._v(" Chương 1160: Bảo lãnh")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Ai, còn tưởng rằng có thể có chút khiêu chiến...”"),t("br"),t("br"),n._v("Rời khỏi luyện tâm cầu, Trương Huyền than thở."),t("br"),t("br"),n._v("Còn tưởng rằng, cái này cầu, chân cùng đối phương nói như vậy, khó mà đi lại, đối tâm cảnh có cực mạnh khảo nghiệm tác dụng, chân chính đi qua, mới biết được... Căn bản không phải có chuyện như vậy, cái gì cảm giác đều không có, thậm chí tinh thần liền gợn sóng đều không sinh một tia."),t("br"),t("br"),n._v("Đi cái tâm ma thông đạo, tâm ma không ra, qua cái luyện tâm cầu, cũng không có cảm giác, muốn rèn luyện một chút tâm cảnh, đều làm không được... Thật tịch mịch, tốt cô độc ah!"),t("br"),t("br"),n._v("Lắc đầu, Trương Huyền tràn đầy tiếc hận, đi tới vừa rồi Ngô sư nói chuyện gian phòng, gõ cửa đi vào."),t("br"),t("br"),n._v("Tiến vào bên trong, khi thấy Ngô sư ngồi ngay ngắn ở ở giữa, sắc mặt tái xanh, có chút khó coi."),t("br"),t("br"),n._v("“Chuyện gì xảy ra?” Trương Huyền khó hiểu."),t("br"),t("br"),n._v("Cùng vị này lúc chia tay, còn một mặt vui vẻ, làm sao không bao dài thời gian, bộ dáng này?"),t("br"),t("br"),n._v("Nghe được hỏi thăm, Ngô sư xoa xoa mi tâm: “Vừa rồi hai vị kia là Thanh Nguyên đế quốc người, nói dâng mệnh lệnh của bệ hạ, hy vọng có thể đem Trung Thanh Vương mang đi, mới vừa bị ta đuổi ra ngoài!”"),t("br"),t("br"),n._v("“Đem Trung Thanh Vương mang đi?” Trương Huyền nhíu nhíu mày: “Chẳng lẽ vị hoàng đế bệ hạ này, không biết mưu hại một vị danh sư tội danh?”"),t("br"),t("br"),n._v("Mặc dù hắn không phải Thanh Nguyên đế quốc danh sư, cũng không có đạt tới thất tinh, nhưng dù sao cũng là danh sư học viện viện trưởng, đứng phía sau hơn mười vạn danh sư... Đối phương mưu hại mình bị bắt, còn không có thẩm vấn, liền muốn mang đi, vị hoàng đế bệ hạ này, có phần quá đề cao bản thân đi!"),t("br"),t("br"),n._v("Thật nếu để cho hắn nhẹ nhõm nhận đi, Danh Sư đường mặt mũi đem đặt chỗ nào?"),t("br"),t("br"),n._v("“Chuyện này, đem Trung Thanh Vương một trảo, ta cũng làm người ta đi qua giải thích!” Ngô sư nói."),t("br"),t("br"),n._v("Trung Thanh Vương là Thanh Nguyên đế quốc đầu tiên vương gia, Danh Sư đường dốc toàn bộ lực lượng, đem hắn bắt, không thông báo một tiếng, khó tránh khỏi sẽ tạo thành ảnh hưởng."),t("br"),t("br"),n._v("“Đều nói rõ ràng, vị hoàng đế bệ hạ này, còn muốn tới muốn người, lá gan khó tránh khỏi có chút quá lớn đi!” Trương Huyền khó hiểu."),t("br"),t("br"),n._v("Một cái vương gia, tìm tới một vị Độc điện phó điện chủ, liền đến tìm bản thân phiền phức, đường đường Hoàng đế... Biết sẽ đắc tội Danh Sư đường, còn muốn tới muốn người, Thanh Nguyên đế quốc quyền quý, đầu óc đều nghĩ như thế nào?"),t("br"),t("br"),n._v("Chẳng lẽ không biết, liền xem như phong hào đế quốc hoàng thất, mất đi Danh Sư đường che chở, hủy diệt cũng chỉ tại trong khoảnh khắc ư?"),t("br"),t("br"),n._v("“Ta cũng không rõ lắm, có điều, ta cảm thấy nguyên nhân khẳng định tại Điền phó đường chủ trên thân...”"),t("br"),t("br"),n._v("Ngô sư cũng có chút khó hiểu, lắc đầu."),t("br"),t("br"),n._v("Danh Sư đường chủ quản quyền lợi phó đường chủ, không có điều tra rõ ràng phía trước, liền cùng một cái vương gia, đi mưu hại một vị danh sư, rõ ràng là không khôn ngoan hành động, nói thật, ngay cả Ngô sư, cũng cảm thấy, xuống một chiêu bất tỉnh cờ."),t("br"),t("br"),n._v("Đương nhiên, sở dĩ không có việc gì, vẫn là bọn hắn chạy đến kịp thời, nếu không, chỉ cần bị bắt, chẳng khác nào trên thớt ức hiếp, đối phương một khi sử dụng thủ đoạn, chỉ sợ lại không cách nào tẩy thoát tội danh."),t("br"),t("br"),n._v("Loại sự tình này đã gặp rất nhiều."),t("br"),t("br"),n._v("Tẩy thoát tội danh rất khó, vu oan tội danh, còn là rất dễ dàng, huống chi, hai vị Thanh Nguyên đế quốc, người có quyền thế nhất liên thủ."),t("br"),t("br"),n._v("“Ừm!” Trương Huyền nhẹ gật đầu, đang nghĩ đem suy đoán của chính mình nói ra, liền nghe đến ngoài cửa vang lên tiếng đập cửa, ngay sau đó một cái danh sư đi đến."),t("br"),t("br"),n._v("“Lão sư, Thanh Nguyên đế quốc, Hoàng đế Sở Thiên Hành đến đây cầu kiến!”"),t("br"),t("br"),n._v("“Nhanh như vậy liền đến? Để hắn đi vào!”"),t("br"),t("br"),n._v("Lông mày nhíu một cái, Ngô sư khoát tay áo."),t("br"),t("br"),n._v("Mới vừa đuổi đi đối phương sứ giả, không đủ năm phút đồng hồ, người sau liền tự mình tới thăm hỏi, xem ra trước đó cũng đã chờ ở bên ngoài đợi."),t("br"),t("br"),n._v("Danh sư lui xuống, thời gian không dài, một người mặc hoàng bào trung niên nhân, đi nhanh tới."),t("br"),t("br"),n._v("Vị này trung niên nhân, thực lực cùng Ngô sư đồng dạng cũng đạt tới nửa bước xuất khiếu, một đôi mắt, mang theo như rồng giống như hổ uy thế, vừa nhìn liền biết quanh năm thân cư cao vị, dưỡng thành khí thế đặc biệt."),t("br"),t("br"),n._v("“Ngô sư!”"),t("br"),t("br"),n._v("Vừa vào cửa, vội vàng ôm quyền, thái độ cung kính."),t("br"),t("br"),n._v("Hắn mặc dù là Thanh Nguyên đế quốc một nước chi chủ, nhưng cùng Ngô sư vị danh sư này đường phó đường chủ so, địa vị còn là kém một chút."),t("br"),t("br"),n._v("“Ừm, ngồi!”"),t("br"),t("br"),n._v("Ngô sư nhẹ gật đầu, con mắt lạnh nhạt nhìn qua: “Bệ hạ nếu như tới vẫn là vì mang đi Trung Thanh Vương, còn xin miễn mở hắn miệng! Mưu hại danh sư, tội ác tày trời, ta đã đem trong chuyện này báo danh tổng bộ, không riêng gì hắn, chỉ sợ toàn bộ Thanh Nguyên đế quốc đều phải bị thẩm tra, còn xin ngươi nghĩ lại!”"),t("br"),t("br"),n._v("Lời mặc dù không nghiêm khắc, lại cho người ta một loại lạnh lùng đến cực điểm, giải quyết việc chung cảm giác."),t("br"),t("br"),n._v("Mới mở miệng, liền trước thời hạn phủ kín đối phương muốn nói lời nói."),t("br"),t("br"),n._v("“Trung Thanh Vương làm ra loại sự tình này, quả thực làm ta đau lòng, bất quá... Ngô sư cũng biết, nếu như không phải hắn, ta khả năng đã sớm chết, cho nên, mặc kệ hắn làm bao nhiêu chuyện sai lầm, ta đều bằng lòng đền bù! Mong rằng Danh Sư đường cho cái cơ hội, ta một lát nữa, sẽ đích thân hướng bị hại Trương sư xin lỗi, khẩn cầu hắn tha thứ...”"),t("br"),t("br"),n._v("Sở Thiên Hành cũng không ngồi xuống, mà là ôm quyền, một mặt thành khẩn."),t("br"),t("br"),n._v("“Xin lỗi? Xin lỗi hữu dụng, muốn chúng ta Danh Sư đường làm cái gì?” Ngô sư khoát tay áo: “Tiễn khách!”"),t("br"),t("br"),n._v("“Vâng!” Mang Hoàng đế bệ hạ đi vào danh sư, nhẹ gật đầu, đi tới trước mặt: “Bệ hạ, mời đi!”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Sở Thiên Hành vẻ mặt xấu hổ, muốn nói cái gì, thấy đối phương căn bản không bồi thường đáp cơ hội, đành phải than thở một tiếng, đang định rời khỏi gian phòng, liền nghe đến một cái thanh âm nhàn nhạt vang lên."),t("br"),t("br"),n._v("“Ngô sư, trước không vội đuổi đi, hắn không phải phải bồi thường ư? Để cho ta nghe một chút, có thể vì vị này Trung Thanh Vương lấy ra bao nhiêu hạn mức!”"),t("br"),n._v("Quay đầu nhìn lại, nói chuyện chính là người hai mươi tuổi khoảng chừng thanh niên, người mặc lục tinh danh sư trang phục, ngồi tại Ngô sư cách đó không xa quý vị khách quan bên trên."),t("br"),t("br"),n._v("Chính là Trương Huyền."),t("br"),t("br"),n._v("“Trương sư...”"),t("br"),t("br"),n._v("Không nghĩ tới hắn lại như vậy nói, Ngô sư kỳ quái nhìn một cái, đành phải nhẹ gật đầu: “Đã Trương sư đã nói như vậy, ngươi liền nói một chút làm sao đền bù đi!”"),t("br"),t("br"),n._v("“Trương sư?”"),t("br"),t("br"),n._v("Sở Thiên Hành lập tức hiểu được, đi tới trước mặt khom người: “Trung Thanh Vương đắc tội Trương sư, có mắt không tròng, nếu như Trương sư có thể tha thứ, ta bằng lòng làm tên sư học viện rất nhiều học sinh, xây dựng một tòa tân phủ đệ, vô điều kiện cung cấp 100 năm tài nguyên tu luyện! Hơn nữa, từ nay về sau, chỉ cần là Hồng Viễn danh sư học viện học sinh, đi tới Thanh Nguyên thành, đều có thể nhận lấy một phần công pháp và tiền biếu, sẽ không để cho hắn tay không mà về.”"),t("br"),t("br"),n._v("Biết trước mắt vị này, là Trương viện trưởng, Sở Thiên Hành mới mở miệng, liền nói ra điều kiện của mình."),t("br"),t("br"),n._v("Không hổ là làm Hoàng đế người, biết điều kiện gì đối phương không cách nào cự tuyệt."),t("br"),t("br"),n._v("Nếu là viện trưởng, khẳng định muốn vì học sinh cân nhắc, điều kiện này, có thể nói mười phần ưu hậu."),t("br"),t("br"),n._v("Có đường đường phong hào đế quốc dốc sức ủng hộ, Hồng Viễn danh sư học viện, trở thành tứ đại học viện đứng đầu, chỉ là vấn đề thời gian."),t("br"),t("br"),n._v("“Chưa đủ!”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("“Cái này...” Sở Thiên Hành sửng sốt một chút: “Mong rằng Trương sư danh ngôn, chỉ cần ta hoàng thất có thể chịu được, tuyệt đối không nhăn nửa cái lông mày!”"),t("br"),t("br"),n._v("“Rất đơn giản, vừa rồi điều kiện, lại thêm 100 viên tinh nguyên linh thạch thượng phẩm! Trung Thanh Vương, ngươi liền có thể trực tiếp mang đi!” Trương Huyền nhìn qua."),t("br"),t("br"),n._v("Hắn hiện tại đang cần tài nguyên tu luyện, nếu như có thể từ đối phương trong tay nhận được, tuyệt đối so với giam một cái vương gia phải tốt hơn nhiều."),t("br"),t("br"),n._v("Dù sao muốn giết chết cái tên này, cũng liền chuyện trong nháy mắt, không tính là gì."),t("br"),t("br"),n._v("“100 viên?” Sở Thiên Hành sững sờ, lập tức mặt mũi cười khổ: “Trương sư nói giỡn, nhiều linh thạch như vậy hoàng thất chúng ta, cho dù đem tất cả mọi thứ đều bán đi, cũng không bỏ ra nổi tới...”"),t("br"),t("br"),n._v("Tinh nguyên linh thạch thượng phẩm, vốn là thưa thớt, liên chiến sư đường, loại này vật tư không thiếu địa phương, cũng chỉ có mười cái khoảng chừng, Thanh Nguyên hoàng thất, cho dù mạnh hơn, cũng không có khả năng một hơi lấy ra vượt qua gấp mười lần số lượng tới."),t("br"),t("br"),n._v("“Vậy thì tốt, cho ngươi thư thả một chút, 50 viên! Lấy ra, đem Trung Thanh Vương mang đi, cầm không đến, mời trở về đi!” Trương Huyền xua tay."),t("br"),t("br"),n._v("Hắn cũng biết đối phương khẳng định không bỏ ra nổi một trăm viên, rao giá trên trời, ngay tại chỗ trả tiền thôi."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Sở Thiên Hành khóe miệng co quắp một chút."),t("br"),t("br"),n._v("Năm mươi viên tinh nguyên linh thạch thượng phẩm, cũng không phải hắn có thể tiếp nhận."),t("br"),t("br"),n._v("“Trương sư, nói thật với ngươi, chúng ta Thanh Nguyên hoàng thất, tích lũy trên vạn năm, cũng chỉ có 17 viên tinh nguyên linh thạch thượng phẩm... Vốn có không ít, có điều, đều ban cho hoàng tử, hoặc là có công đại thần... Ta đem cái này 17 viên toàn bộ cho ngươi, sau đó cái khác dùng trân quý dược liệu, khoáng thạch thay thế làm sao?”"),t("br"),t("br"),n._v("Chần chờ một chút, Sở Thiên Hành cắn răng nói."),t("br"),t("br"),n._v("50 viên tinh nguyên linh thạch thượng phẩm, khẳng định là không bỏ ra nổi đến, bất quá... Chỉ cần đối phương bằng lòng, vật thay thế phẩm, ngược lại là có thể lấy ra không ít."),t("br"),t("br"),n._v("“Có thể, đây là ta cần khoáng thạch cùng dược vật danh sách, ngươi có thể toàn bộ tìm đến, 17 viên tinh nguyên linh thạch thượng phẩm, cũng không sao. Mặt khác, ta còn muốn tất cả Thánh vực tam trọng, Thánh vực tứ trọng tu luyện công pháp, càng nhiều càng tốt, đem bọn ngươi hoàng thất Tàng Thư khố bên trong, toàn bộ sao chép một phần cho ta!”"),t("br"),t("br"),n._v("Lấy ra bút lông, tiện tay viết cái danh sách, Trương Huyền nói."),t("br"),t("br"),n._v("Hắn đang định có cơ hội, giúp Kim Nguyên đỉnh cùng Băng Vũ kiếm tăng lên một chút cấp bậc, tài liệu chính có, còn thiếu khuyết không ít phụ trợ vật phẩm, chỉ vào hắn tìm kiếm không biết năm nào tháng nào, trước mắt vị này, có thể hỗ trợ, không còn gì tốt hơn."),t("br"),t("br"),n._v("“Được...”"),t("br"),t("br"),n._v("Nhận lấy danh sách nhìn một cái, mặc dù có chút vật phẩm rất ít thấy, nhưng bằng cho bọn hắn mượn phong hào đế quốc thực lực, hẳn là trong thời gian ngắn, có thể gom góp, Sở Thiên Hành nhẹ gật đầu, cổ tay khẽ đảo, trước đem tinh nguyên linh thạch thượng phẩm, đưa tới."),t("br"),t("br"),n._v("“Đây là 17 viên tinh nguyên linh thạch thượng phẩm, còn xin Trương sư kiểm tra!”"),t("br"),t("br"),n._v("Thần thức quét qua, Trương Huyền cũng đã biết, đối phương không có nói dối, nhẹ gật đầu, tiện tay thu vào nhẫn."),t("br"),t("br"),n._v("“Còn lại, xin Trương sư cùng Ngô sư chờ!”"),t("br"),t("br"),n._v("Nói hết, Sở Thiên Hành xoay người phân phó một câu."),t("br"),t("br"),n._v("Không hổ là Thanh Nguyên đế quốc Hoàng đế bệ hạ, động tác cực nhanh, dùng không đến hai canh giờ, Trương Huyền muốn tất cả khoáng thạch, dược liệu liền toàn bộ sưu tập hoàn chỉnh."),t("br"),t("br"),n._v("Thậm chí, hắn muốn sách, cũng tất cả đều dò xét một phần."),t("br"),t("br"),n._v("Rất nhiều chưa kịp chép, đem nguyên bản đều cầm tới, lít nha lít nhít tính gộp lại, Thánh vực tam trọng, chừng mấy trăm ngàn bản, tứ trọng, cũng có hơn vạn bản nhiều."),t("br"),t("br"),n._v("Đem những vật này, thu sạch tiến trữ vật giới chỉ, Trương Huyền lúc này mới thoả mãn nhẹ gật đầu: “Tốt rồi, Trung Thanh Vương ngươi có thể mang đi!”"),t("br"),t("br"),n._v("“Đa tạ Trương sư!”"),t("br"),t("br"),n._v("Thở phào nhẹ nhõm, Sở Thiên Hành vội vàng nhìn về phía Ngô sư, thấy hắn gật đầu, đồng ý Trương sư lời nói, lúc này mới đi theo một vị danh sư sau lưng, hướng giam giữ Trung Thanh Vương địa phương đi đến."),t("br"),t("br"),n._v("“Trương sư...”"),t("br"),t("br"),n._v("Đối phương vừa đi, Ngô sư tràn đầy nghi ngờ nhìn lại, không biết Trương Huyền đến cùng có chủ ý gì."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1168"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1170"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);