(window.webpackJsonp=window.webpackJsonp||[]).push([[2033],{2388:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-752-truong-huyen-mua-đo"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-752-truong-huyen-mua-đo"}},[n._v("#")]),n._v(" Chương 752: Trương Huyền mua đồ")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 752: Trương Huyền mua đồ"),t("br"),t("br"),n._v("Chỉ chốc lát, Tôn Cường liền gọi tới một cái phục vụ viên, khi trở lại tại chỗ thời điểm, nhịn không được giật nảy mình."),t("br"),t("br"),n._v("Chỉ thấy thiếu gia chẳng biết lúc nào đổi một bộ quần áo, toàn thân phục trang đẹp đẽ, trong tay một chuôi quạt xếp, không ngừng lay động, một mặt ngạo nghễ, từ xa nhìn lại, hiển nhiên một cái hoàn khố phú nhị đại."),t("br"),t("br"),n._v("“Đây là muốn làm gì?”"),t("br"),t("br"),n._v("Trong lòng nghi hoặc, lời đến khóe miệng lại nuốt xuống, ngoan ngoãn đi tới trước mặt, liền ôm quyền: “Thiếu gia!”"),t("br"),t("br"),n._v("Lúc trước lão gia dẫn hắn đi thiên vũ thương hội thời điểm, cũng cử chỉ quái dị, kết quả nhưng làm Lăng Thiên Vũ chuyên môn chạy tới yết kiến, cam tâm đưa tiền, có thể hay không thiếu gia cũng muốn chơi một bộ này?"),t("br"),t("br"),n._v("“Ừ!”"),t("br"),t("br"),n._v("Nhẹ gật đầu, Trương Huyền liếc mắt nhìn thoáng qua theo ở phía sau đi tới phục vụ viên: “Ngươi là nơi này quản sự?”"),t("br"),t("br"),n._v("“Quản sự không dám nhận, có thể làm điểm chủ...”"),t("br"),t("br"),n._v("Phục vụ viên cười nói."),t("br"),t("br"),n._v("Người bán hàng này là cái hai mươi tuổi thanh niên, thoạt nhìn Hóa Phàm ngũ trọng tu vi, chịu trách nhiệm giới thiệu, bán ra lắc để ở chỗ này thương phẩm, có nhất định quyền lợi."),t("br"),t("br"),n._v("“Có thể làm chủ là tốt rồi, ta muốn tại ngươi nơi này mua vài món đồ, ngươi liền tìm quý cho ta đề cử mấy thứ!”"),t("br"),t("br"),n._v("Cây quạt vẫy một cái, Trương Huyền thản nhiên nói."),t("br"),t("br"),n._v("“Tìm quý?”"),t("br"),t("br"),n._v("Tôn Cường nheo mắt."),t("br"),t("br"),n._v("Hắn làm qua buôn bán, biết buôn bán trên trận quy củ, người khác mua đồ sợ chịu mổ, đều lựa chọn khiêm tốn, giả bộ không có tiền, thiếu gia ngược lại tốt, chọn quý đề cử... Cái này chẳng phải bày tỏ, bản thân nhiều tiền không có chỗ xài?"),t("br"),t("br"),n._v("Gặp được dạng này khách hàng, không theo chết bên trong mổ mới là lạ!"),t("br"),t("br"),n._v("Không phải qua đến báo thù sao?"),t("br"),t("br"),n._v("Thế nào thấy ngược lại giống như làm cho đối phương kiếm tiền?"),t("br"),t("br"),n._v("Quả nhiên, nghe được hắn, đối diện phục vụ viên, ánh mắt sáng lên, tiến về phía trước một bước: “Vị thiếu gia này, cái này Thanh Đồng giản cũng rất không tệ, là lục tinh luyện khí sư luyện chế mà thành, đã đạt tới Linh cấp trung phẩm đỉnh phong...”"),t("br"),t("br"),n._v("Trước mắt trên quầy, vừa vặn trưng bày một cây Thanh Đồng giản, tản mát ra hàn mang."),t("br"),t("br"),n._v("“Cho ta xem một chút!”"),t("br"),t("br"),n._v("Nghe được giới thiệu, Trương Huyền cũng không chối từ, nhàn nhạt khoát tay áo."),t("br"),t("br"),n._v("“Được rồi!”"),t("br"),t("br"),n._v("Con mắt lóe lên, phục vụ viên vội vàng đem Thanh Đồng giản theo trên quầy lấy ra ngoài, hai tay đưa qua."),t("br"),t("br"),n._v("Trương Huyền tiện tay tiếp nhận, bàn tay nhẹ nhàng lắc một cái."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Thanh Đồng giản phát ra một tiếng kêu khẽ, mang theo phần phật tiếng gió, cho người ta một loại cực mạnh cảm giác áp bách."),t("br"),t("br"),n._v("“Đồ tốt...”"),t("br"),t("br"),n._v("Hài lòng gật đầu, tiện tay ném cho Tôn Cường, Trương Huyền mí mắt vừa nhấc, nhìn về phía phục vụ viên: “Bao nhiêu tiền?”"),t("br"),t("br"),n._v("“Bẩm báo thiếu gia, cái này Thanh Đồng giản, là lục tinh luyện khí đại sư Chu Quyền, tự mình luyện chế mà thành, đã hơn một trăm năm, không chỉ có giá trị sử dụng, còn có kỷ niệm ý nghĩa, là chúng ta Linh Tài các trấn điếm bảo bối một trong, giá cả nha... Có thể sẽ đắt một chút!”"),t("br"),t("br"),n._v("Chần chờ một chút, phục vụ viên nói."),t("br"),t("br"),n._v("“Tới...”"),t("br"),t("br"),n._v("Nghe nói như thế cùng ngữ khí, Tôn Cường lắc đầu."),t("br"),t("br"),n._v("Cái tên này rất gà tặc, dường như nhìn ra thiếu gia của mình là cái “Chày gỗ”, cố ý dùng loại giọng nói này nâng giá."),t("br"),t("br"),n._v("Đang suy nghĩ cần ứng đối ra sao, liền nghe đến luôn luôn trầm ổn thiếu gia lạnh hừ một tiếng: “Quý? Làm sao, ngươi sợ ta mua không nổi?”"),t("br"),t("br"),n._v("Che cái trán, Tôn Cường suýt chút nữa không khóc."),t("br"),t("br"),n._v("Thiếu gia... Ngươi đây thật là đến báo thù?"),t("br"),t("br"),n._v("Làm sao cảm giác cùng nhà giàu mới nổi vào thành, để cho người ta xâm lược?"),t("br"),t("br"),n._v("“Dĩ nhiên không phải, ta biết thiếu gia thân phận cao quý, khẳng định mua được...”"),t("br"),t("br"),n._v("Cười xòa một tiếng, phục vụ viên một cắn răng, giống như là kiếm ít rất nhiều tiền đồng dạng: “Căn này Thanh Đồng giản, nguyên bản năm ngàn linh thạch trung phẩm, thiếu gia sảng khoái như vậy, thật muốn mua lời nói, ta có thể xin một cái, ba ngàn bán ra!”"),t("br"),t("br"),n._v("“Ba ngàn?”"),t("br"),t("br"),n._v("Tôn Cường hộc máu."),t("br"),t("br"),n._v("Cái này mẹ nó ăn cướp trắng trợn ah!"),t("br"),t("br"),n._v("Hôm qua hắn đến thời điểm, cũng nhìn qua thứ này, chào giá mới một ngàn, kết quả không có người mua, hôm nay mới mở miệng lại muốn đến ba ngàn! Còn ba ngàn liền có thể bán ra..."),t("br"),t("br"),n._v("Thật khi chúng ta là hai đồ đần?"),t("br"),t("br"),n._v("Đang định nhắc nhở một câu, chỉ thấy thiếu gia hài lòng nhẹ gật đầu: “Lục phẩm luyện khí đại sư luyện chế vũ khí, đáng cái giá này! Tôn Cường, bao lên, thứ này bản thiếu gia muốn!”"),t("br"),t("br"),n._v("“Muốn?”"),t("br"),t("br"),n._v("Tôn Cường nhoáng một cái."),t("br"),t("br"),n._v("Thiếu gia đây là điên rồi vẫn là thế nào?"),t("br"),t("br"),n._v("Không nói trước ba ngàn linh thạch trung phẩm có hay không,"),t("br"),t("br"),n._v("Cho dù có, cũng không mua cái này phá ngoạn ý ah!"),t("br"),t("br"),n._v("Còn lục tinh luyện khí sư, còn cái gì Chu Quyền, nghe xong liền biết là lừa dối."),t("br"),t("br"),n._v("Có nhiều như vậy tiền, đều có thể mua một chuôi Linh cấp thượng phẩm binh khí, ai sẽ mua thứ này?"),t("br"),t("br"),n._v("Bất quá, thiếu gia đã nói mua, hắn một cái hạ nhân, không dám phản bác, đành phải đem căn này Thanh Đồng giản cầm trong tay."),t("br"),t("br"),n._v("Phục vụ viên hiển nhiên cũng không nghĩ tới, trước mắt vị này sẽ thống khoái như vậy, cũng kinh ngạc mở to hai mắt nhìn."),t("br"),t("br"),n._v("Cái này phá ngoạn ý, trần liệt không biết bao nhiêu ngày, bán không được, đối phương không chút do dự mua sắm, thoạt nhìn, thật đúng là người có tiền chày gỗ!"),t("br"),t("br"),n._v("“Ừ, còn có hay không đắt hơn, thứ càng tốt, mang ta xem một chút, chẳng lẽ các ngươi Linh Tài các, vật trân quý nhất, cũng chỉ là cái này Linh cấp trung phẩm đỉnh phong Thanh Đồng giản?”"),t("br"),t("br"),n._v("Một cái tay vắt chéo sau lưng, một cái tay nắm lấy quạt xếp, Trương Huyền mặt mũi bực mình, thật giống như vừa rồi mua đồ vật, quá mức tiện nghi, không xứng với thân phận."),t("br"),t("br"),n._v("“Có, đương nhiên là có đắt hơn!”"),t("br"),t("br"),n._v("Phục vụ viên con mắt tỏa ánh sáng, đang muốn phía trước dẫn đường, chần chờ một chút: “Thiếu gia, cái này Thanh Đồng giản tiền...”"),t("br"),t("br"),n._v("Không phải hắn không tin đối phương, làm ăn nhiều năm như vậy, hạng người gì đều gặp, ổn thỏa lý do, trước gặp đến tiền lại nói."),t("br"),n._v(" “Cảm thấy bản thiếu gia sẽ quỵt nợ? Thật to gan!”"),t("br"),t("br"),n._v("Dường như tôn nghiêm nhận lấy vũ nhục, Trương Huyền hừ một tiếng, đang muốn nổi giận, lập tức lắc đầu: “Cũng tốt, không cho ngươi chút, ngươi cũng không tận tâm giúp ta tìm đồ!”"),t("br"),t("br"),n._v("Nói xong cổ tay khẽ đảo, một cái linh thạch ném tới."),t("br"),t("br"),n._v("Phục vụ viên tiện tay tiếp nhận, nhìn thấy lòng bàn tay đồ vật, lập tức cứng ngắc tại nguyên chỗ: “Linh thạch thượng phẩm...”"),t("br"),t("br"),n._v("Đối phương tiện tay ném tới, linh khí đầy đủ, tản mát ra để làn da đau nhức tinh thuần linh khí."),t("br"),t("br"),n._v("Lại là một cái linh thạch thượng phẩm!"),t("br"),t("br"),n._v("Loại cấp bậc này linh thạch, chỉ có cường giả Thánh vực, ít nhất cũng là Tằm Phong cảnh cường giả mới có thể giao dịch, đối phương tiện tay ném ra, dùng tới mua một cái Thanh Đồng giản..."),t("br"),t("br"),n._v("Để hắn yết hầu phát khô."),t("br"),t("br"),n._v("“Không sai, một cái linh thạch thượng phẩm, giá trị một vạn viên trung phẩm, có lẽ đầy đủ căn này Thanh Đồng giản đi?” Trương Huyền hừ một tiếng."),t("br"),t("br"),n._v("“Đầy đủ, đầy đủ... Còn có còn thừa!”"),t("br"),t("br"),n._v("Phục vụ viên liền vội vàng gật đầu."),t("br"),t("br"),n._v("Mới vừa rồi còn có hoài nghi, hiện tại cái này vẻ hoài nghi lập tức tan thành mây khói."),t("br"),t("br"),n._v("Có thể tùy tiện lấy ra một cái linh thạch thượng phẩm người, làm sao có thể mua không nổi đồ vật? Không cần đoán, khẳng định là đại gia tộc thiếu gia, dùng tiền hoa đã quen!"),t("br"),t("br"),n._v("“Ừ, còn không mau một chút dẫn đường!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Có cái này viên linh thạch thượng phẩm làm nền, phục vụ viên quả nhiên so vừa rồi thái độ cung kính không ít, vội vã ở phía trước dẫn đường, chỉ chốc lát ba người liền đến đến đại sảnh chỗ sâu."),t("br"),t("br"),n._v("Người bên trong này rõ ràng ít đi rất nhiều, có điều, có thể tới đây, hầu như đều là có thân phận quan lại quyền quý, hơn nữa bên trong trưng bày đồ vật, cũng hiển nhiên càng thêm trân quý."),t("br"),t("br"),n._v("Trương Huyền ngẩng đầu nhìn qua."),t("br"),t("br"),n._v("“Linh cấp thượng phẩm vũ khí, trân quý khoáng thạch, có thể khiến người ta kéo dài tuổi thọ linh quả...”"),t("br"),t("br"),n._v("Minh Lý Chi Nhãn lấp lóe, bảo khí bắn ra bốn phía, chiếu rọi có chút hoa mắt."),t("br"),t("br"),n._v("Không hổ là Hồng Viễn thành Linh Tài các, coi như hắn ánh mắt cực cao, nhìn thấy những bảo vật này, cũng nhịn không được cảm thấy có chút động tâm."),t("br"),t("br"),n._v("Nhìn một vòng, khóe miệng chậm rãi vung lên, hướng cách đó không xa một cái hộp ngọc đi tới."),t("br"),t("br"),n._v("“Đây là cái gì?”"),t("br"),t("br"),n._v("Đi tới trước mặt, Trương Huyền hỏi."),t("br"),t("br"),n._v("“Đây là cấp sáu phá chướng đan! Chỉ cần dùng, có thể để cho Hóa Phàm cửu trọng cường giả, vô điều kiện tấn thăng một cái tiểu cấp bậc! Danh sư học viện, luyện đan học viện, La viện phó tự mình luyện chế, giá cả cực cao!”"),t("br"),t("br"),n._v("Phục vụ viên vội nói."),t("br"),t("br"),n._v("“Cấp sáu phá chướng đan?”"),t("br"),t("br"),n._v("Tu vi càng cao, tu luyện càng khó, nhất là đạt tới Hóa Phàm cửu trọng, trong cơ thể ngưng tụ Kim Đan, sau khi phục dụng có thể vô điều kiện tăng lên một cái tiểu cấp bậc, thứ này công hiệu cùng giá trị, có thể nói có thể nói kinh khủng."),t("br"),t("br"),n._v("“Ta có thể hay không nhìn một chút?”"),t("br"),t("br"),n._v("Trương Huyền mở miệng."),t("br"),t("br"),n._v("“Loại đan dược này dựa theo lẽ thường, là không cho phép người khác đụng vào, có điều, thiếu gia muốn nhìn, ta có thể lấy ra...” Biết trước mắt vị này là khách hàng lớn, phục vụ viên cười nói."),t("br"),t("br"),n._v("“Ừ!” Trương Huyền nhẹ gật đầu."),t("br"),t("br"),n._v("Phục vụ viên tiến về phía trước một bước, mở ra quầy hàng, đem hộp ngọc lấy ra ngoài, nhẹ nhàng mở ra."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Một cỗ tinh thuần linh lực từ đó truyền lại mà đến, để cho người ta toàn thân lỗ chân lông mở rộng, dường như chỉ cần nuốt vào cái này viên thuốc, liền có thể khiến người ta tu vi tăng nhiều, thực lực có bay vọt về chất."),t("br"),t("br"),n._v("Đan dược phía trên, tựa như có khói xanh vây quanh, bất cứ lúc nào cũng sẽ Hóa Long, thoát ly khống chế."),t("br"),t("br"),n._v("Viên đan dược này cứ việc chỉ là thành đan cấp bậc, nhưng bởi vì luyện chế dược liệu trân quý, cấp bậc quá cao, vẫn như cũ có giá trị không nhỏ."),t("br"),t("br"),n._v("“Thuốc tốt...”"),t("br"),t("br"),n._v("“Cấp sáu đan dược, loại thuốc này vật, tính toán muốn mua, cũng mua không nổi!”"),t("br"),t("br"),n._v("“Đúng vậy a, quá trân quý...”"),t("br"),t("br"),n._v("Hộp ngọc mở ra, linh lực xao động, cũng đưa tới không ít người vây xem."),t("br"),t("br"),n._v("Trước đó ở trong đại điện nhìn những bảo vật khác rất nhiều người tu luyện, toàn bộ đều đem ánh mắt tập trung tới."),t("br"),t("br"),n._v("Thấy mọi người bị thu hút, Trương Huyền khoát tay áo: “Che lên đi, thuốc này không sai, bao nhiêu tiền, ta mua!”"),t("br"),t("br"),n._v("Nghe được vị thiếu gia này muốn mua, phục vụ viên vội vàng đem ngọc trên nắp hộp, một mặt hưng phấn mở miệng: “Đây là cấp sáu đan dược, càng là luyện đan học viện phó viện trưởng dùng chín chín tám mươi mốt loại quý báu dược liệu luyện chế mà thành, nếu như thiếu gia thật muốn mua, hai cái linh thạch thượng phẩm!”"),t("br"),t("br"),n._v("“Hai cái linh thạch thượng phẩm?”"),t("br"),t("br"),n._v("“Đây là cướp chứ?”"),t("br"),t("br"),n._v("“Trước mấy ngày thuốc này không phải 5000 linh thạch trung phẩm ư? Lúc nào tăng tới hai cái linh thạch thượng phẩm?”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Chung quanh một mảnh xôn xao."),t("br"),t("br"),n._v("Thường xuyên đến đi dạo Linh Tài các, đối với nơi này giá tiền của vật phẩm đều biết một chút."),t("br"),t("br"),n._v("Cái này viên dược liệu, mặc dù đạt đến cấp sáu, lại cũng chỉ là hạ phẩm, hơn nữa vừa mới thành đan, giá cả năm ngàn linh thạch trung phẩm, coi như rất công đạo, hai cái linh thạch thượng phẩm... Có thể nói cùng đoạt không có gì khác biệt."),t("br"),t("br"),n._v("Mọi người ở đây cảm thấy vị thiếu gia này, khẳng định sẽ cảm thấy quá đắt thời điểm, chỉ thấy hắn mí mắt vừa nhấc: “Cấp sáu đan dược, hai cái linh thạch thượng phẩm, không đắt lắm, mua!”"),t("br"),t("br"),n._v("“Mua?”"),t("br"),t("br"),n._v("Mọi người tất cả đều ngẩn ngơ, gặp qua ngốc, chưa thấy qua ngu như vậy."),t("br"),t("br"),n._v("Ngươi mua đồ không cần mặc cả ư?"),t("br"),t("br"),n._v("“Vậy thì tốt, ta hiện tại liền cho thiếu gia bọc lại...”"),t("br"),t("br"),n._v("Nghe hắn muốn mua, phục vụ viên hưng phấn mà lần nữa nhảy lên, vội vàng cầm hộp ngọc liền muốn đặt vào Tôn Cường trong tay."),t("br"),t("br"),n._v("Bất quá, còn không có đặt vào trước mặt, chỉ thấy trước mắt vị này hào sảng thiếu gia, nhìn lại, mày nhăn lại, lộ ra một tia lo lắng: “Các ngươi thứ này, không có hàng giả chứ?”"),t("br"),t("br"),n._v("“Hàng giả? Thiếu gia nói giỡn!”"),t("br"),t("br"),n._v("Phục vụ viên xấu hổ cười một tiếng: “Chúng ta Linh Tài các đồ vật, đều có chuyên môn giám bảo đại sư giám định, tuyệt sẽ không sai, chỉ cần phát hiện có vấn đề, nghỉ ngơi một đền mười!”"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-760"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-762"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);