(window.webpackJsonp=window.webpackJsonp||[]).push([[2035],{2390:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-754-đay-đeu-la-hang-gia-ha"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-754-đay-đeu-la-hang-gia-ha"}},[n._v("#")]),n._v(" Chương 754: Đây đều là hàng giả (hạ)")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("Chương 754: Đây đều là hàng giả (hạ)"),i("br"),i("br"),n._v("“Cái gì?”"),i("br"),i("br"),n._v("“Chết rồi?”"),i("br"),i("br"),n._v("“Chẳng lẽ... Viên đan dược này thật không phải là phá chướng đan, mà là độc dược?”"),i("br"),i("br"),n._v("“Cái này cũng thật là đáng sợ đi! Hai cái linh thạch thượng phẩm mua một cái thuốc giả trở về?”"),i("br"),i("br"),n._v("“Linh Tài các cũng quá nói nhảm đi, hố người không phải như vậy lừa bịp!”"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Nhìn thấy thử đan thú tại chỗ tử vong, chung quanh tất cả mọi người xôn xao."),i("br"),i("br"),n._v("Bỏ ra nhiều tiền mua đan dược mục đích, là vì tấn thăng, kết quả ăn xong không những không tăng cao tu vi, ngược lại hạ độc chết, đây là bất kỳ người tu luyện nào cũng không nguyện ý nhìn thấy."),i("br"),i("br"),n._v("Vị này Mặc Phi luôn miệng nói đây là chính phẩm, hàng thật, kết quả vừa xuất hiện liền đem thử đan thú hạ độc chết... Còn thế nào để cho người ta tin tưởng?"),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Mặc Phi cũng là sững sờ, cứng ngắc tại chỗ."),i("br"),i("br"),n._v("Thử đan thú ăn lượng thuốc loại hình, hắn chuyên môn tính toán qua, tuyệt đối không vượt chỉ tiêu."),i("br"),i("br"),n._v("Bây giờ lại bị tại chỗ hạ độc chết, chẳng lẽ... Thật cùng đối phương nói đồng dạng, viên đan dược này, cũng không phải là phá chướng đan, mà là luyện chế báo phế dược vật?"),i("br"),i("br"),n._v("“Cái này có tính không thuốc giả?”"),i("br"),i("br"),n._v("Trương Huyền nhẹ nhàng cười một tiếng."),i("br"),i("br"),n._v("“Cái này... Tốt, viên đan dược này, coi như ta nhìn lầm, là ta sơ sẩy!” Thấy thử đan thú tại chỗ bị độc chết, hiểu biết Đạo Giải thích nhiều hơn nữa cũng vô dụng, Mặc Phi hừ một tiếng."),i("br"),i("br"),n._v("“Nhìn nhầm? Ngươi đâu chỉ nhìn lầm đồng dạng!”"),i("br"),i("br"),n._v("Trương Huyền tiện tay trảo một cái, cái thứ nhất mua được Thanh Đồng giản, cũng xuất hiện tại lòng bàn tay: “Ngươi giám định thư cho ra nội dung, thứ này là lục phẩm luyện khí đại sư luyện chế mà thành, có diệt hồn công hiệu, coi như vu hồn sư đi tới trước mặt, đều có thể một giản giết chết!”"),i("br"),i("br"),n._v("“Không sai, chuôi này Thanh Đồng giản mặc dù chỉ là Linh cấp trung phẩm đỉnh phong, nhưng mang theo lịch sử nặng nề, ẩn chứa trong đó lực lượng sấm sét, giết chết Vu hồn cũng không thành vấn đề!”"),i("br"),i("br"),n._v("Mặc Phi khẽ nói."),i("br"),i("br"),n._v("Chuôi này Thanh Đồng giản, hắn nhưng là chuyên môn nhìn rất lâu, cũng nghiên cứu qua, không có khả năng có lỗi."),i("br"),i("br"),n._v("“Đã ngươi tự tin như vậy, thử một chút liền hiểu biết!” Trương Huyền nhàn nhạt nhìn về phía cách đó không xa phục vụ viên: “Ngươi là Hợp Linh cảnh, linh hồn cũng bắt đầu tu luyện, nếu như kiện binh khí này, có diệt hồn công hiệu, đối ngươi thi triển, khẳng định sẽ cảm thấy linh hồn đâm đau.”"),i("br"),i("br"),n._v("Nói đến đây, nhìn về phía Mặc Phi: “Điểm ấy không có gì dị nghị chứ?”"),i("br"),i("br"),n._v("Có diệt hồn công năng vũ khí, đối người tu luyện linh hồn có cực lớn tổn thương tác dụng."),i("br"),i("br"),n._v("Hợp Linh cảnh cường giả, linh hồn mới vừa mới bắt đầu tu luyện, linh nhục vẫn chưa hoàn toàn thống nhất, gặp được loại binh khí này, rất dễ dàng bị thương."),i("br"),i("br"),n._v("“Không ý kiến!” Mặc Phi lên tiếng."),i("br"),i("br"),n._v("Chung quanh xem náo nhiệt mọi người cũng đều gật đầu."),i("br"),i("br"),n._v("Vu hồn sư biến mất tại dòng sông lịch sử, tìm không thấy, nhưng Hợp Linh cảnh cường giả rất nhiều, loại người này dùng để đo lường cái này Thanh Đồng giản, không có vấn đề chút nào."),i("br"),i("br"),n._v("“Ngươi tới đi...” Trương Huyền nhẹ nhàng cười một tiếng, đem Thanh Đồng giản đưa cho Mặc Phi."),i("br"),i("br"),n._v("Mặc Phi lông mày vung lên, chân khí trong cơ thể phun trào, mãnh liệt mà đối với phục vụ viên tuỳ ý tới."),i("br"),i("br"),n._v("Rất nhanh đo lường đi ra, Thanh Đồng giản rơi vào trên người, cũng không có linh hồn đau đớn, nói cách khác, không có diệt hồn công hiệu!"),i("br"),i("br"),n._v("“Cái này... Làm sao có thể?”"),i("br"),i("br"),n._v("Mặc Phi khuôn mặt tái đi."),i("br"),i("br"),n._v("Chuôi này Thanh Đồng giản, chuyên môn thử qua, bên trong ẩn chứa lực lượng sấm sét, đối linh hồn có cực lớn áp bách, có thể giết chết vu hồn sư hồn phách, hiệu quả cực mạnh."),i("br"),i("br"),n._v("Làm sao đột nhiên liền không có?"),i("br"),i("br"),n._v("Không có cái hiệu quả này, chỉ là cái bình thường Thanh Đồng giản lời nói, căn bản không đáng tiền!"),i("br"),i("br"),n._v("“Có diệt hồn công năng vũ khí, có thể đối phó linh hồn, đồng dạng, loại năng lực này, cũng chỉ có vu hồn sư mới có thể tiêu trừ đi, có thể vu hồn sư sớm đã thất truyền không biết bao nhiêu năm, chẳng lẽ... Ta trước kia thật nhìn lầm?”"),i("br"),i("br"),n._v("Thân thể nhoáng một cái, vẻ mặt như là giấy trắng."),i("br"),i("br"),n._v("Thanh Đồng giản có diệt hồn hiệu quả, mà loại hiệu quả này, cũng chỉ có vu hồn sư mới có thể tiêu trừ đi, những người khác cho dù là Thánh giả, đều làm không được."),i("br"),i("br"),n._v("Hiện tại Vu hồn chức nghiệp đều biến mất, thứ này không có diệt hồn công năng, há không cho thấy, bản thân trước đó giám định đều là sai?"),i("br"),i("br"),n._v("Giám định một sai lầm, ngược lại cũng thôi, liên tục hai cái, để hắn không thể tin được, đều có chút hoài nghi nhân sinh."),i("br"),i("br"),n._v("Giám bảo sư, cả đời chỉ có thể xuất hiện ba chỗ sai lầm, một khi vượt qua liền sẽ thu về và huỷ tư cách, chẳng lẽ, một thế anh danh muốn hủy ở đây?"),i("br"),i("br"),n._v("“Phía dưới cái này cái này đỉnh đồng, ngươi giám định trên sách viết là Cửu Khúc đỉnh, xuất từ lục tinh luyện khí đại sư tay, ưu thế là có thể tạo ra cửu khúc hỏa, để luyện đan thời điểm, dược liệu dung hợp càng thêm đều đặn. Nhưng thiếu hụt là khó mà thuần phục trong đó linh tính, khó mà để hắn nhận chủ... Lời này có thể hay không đúng đắn?”"),i("br"),i("br"),n._v("Không để ý tới đã hoài nghi mình Mặc Phi, Trương Huyền tiếp tục xem tới, bàn tay lắc một cái, một cái lô đỉnh đi tới trước mặt."),i("br"),i("br"),n._v("Cũng là Linh Tài các bảo vật, trước đó đã giám định qua."),i("br"),i("br"),n._v("“Đúng...”"),i("br"),i("br"),n._v("Mặc Phi gật đầu."),i("br"),i("br"),n._v("Ngữ khí rất rõ ràng không trước đó tự tin như vậy."),i("br"),i("br"),n._v("“Cửu Khúc đỉnh, mặc dù luyện đan xác suất thành công cao, nhưng trong đó linh tính cửu khúc, nhận chủ rất khó, đây là người người đều biết sự thật, đây không sai lầm gì!”"),i("br"),i("br"),n._v("Mặc Phi cắn răng."),i("br"),i("br"),n._v("“Đây không phải Cửu Khúc đỉnh, cũng không có phức tạp như vậy linh tính, ta muốn để hắn nhận chủ, sẽ không vượt qua một phút đồng hồ!” Trương Huyền thản nhiên nói."),i("br"),i("br"),n._v("“Không đến một phút đồng hồ? Cái này sao có thể? Nếu quả thật có thể nhanh như vậy nhận chủ, ta liền thừa nhận bản thân nhìn lầm, cái này là hàng giả!”"),i("br"),i("br"),n._v("Mặc Phi cười lạnh."),i("br"),i("br"),n._v("Nói đùa cái gì, Cửu Khúc đỉnh hiếm thấy nhất chính là nhận chủ, đây là người người đều biết sự thật, không đến một phút đồng hồ liền nhận chủ?"),i("br"),i("br"),n._v("Nằm mơ!"),i("br"),i("br"),n._v("Cường giả Thánh vực đều làm không được."),i("br"),n._v("“Đây chính là ngươi thừa nhận!”"),i("br"),i("br"),n._v("Cười một tiếng, Trương Huyền hai bước đi tới lô đỉnh trước mặt bàn tay ở phía trên tùy tiện đập mấy lần, ngay sau đó đứng tại chỗ: “Còn không nhận chủ!”"),i("br"),i("br"),n._v("Ông!"),i("br"),i("br"),n._v("Tiếng nói kết thúc, nguyên bản đứng sừng sững ở tại chỗ không nhúc nhích lô đỉnh, đột nhiên phát ra một tiếng vang lên, hình như gặp chủ nhân hưng phấn."),i("br"),i("br"),n._v("“Cái gì?”"),i("br"),i("br"),n._v("Mặc Phi thân thể nhoáng một cái, suýt chút nữa không hộc máu."),i("br"),i("br"),n._v("Đây là lô đỉnh nhận chủ thanh âm, hắn rõ rõ ràng ràng, tuyệt sẽ không sai."),i("br"),i("br"),n._v("Chân chính Cửu Khúc đỉnh, linh tính phân tán, mười năm đều khó mà nhận chủ, trước mắt cái này, liền nửa phút không đến liền hoàn toàn thành công... Chẳng lẽ lần nữa nhìn lầm?"),i("br"),i("br"),n._v("Cái đồ chơi này cũng là hàng giả?"),i("br"),i("br"),n._v("Đến cùng chuyện gì xảy ra?"),i("br"),i("br"),n._v("“Viên này Tử Đàn châu, đeo ở trên người, có thể tĩnh tâm an thần, tẩm bổ tâm tính, nhưng lại bị Vu hồn nguyền rủa, mang ở trên người, chẳng những không có tĩnh tâm hiệu quả, chỉ sợ sẽ còn tẩu hỏa nhập ma, sống chết không phải do bản thân!”"),i("br"),i("br"),n._v("“Bình này dược dịch, ngươi phân tích là dùng mấy chục loại độc dược dung hợp mà thành, đối thân thể rèn luyện có chỗ tốt cực lớn, nhưng ta mới vừa mới nhìn, độc tính căn bản không trúng hòa, một khi dùng, chẳng mấy chốc sẽ đem người hạ độc chết!”"),i("br"),i("br"),n._v("“Cái này khoáng thạch, mệnh danh dung nhập vũ khí, có thể để hắn gia tăng một cái cấp bậc, nhưng lại căn bản là không có cách dung luyện, đừng nói tan vào vũ khí, liền mài giũa cũng khó khăn...”"),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("“...”"),i("br"),i("br"),n._v("Liên tục chỉ ra ba loại sai lầm, cũng tự mình thí nghiệm, Trương Huyền vắng mặt nói nhảm, đem vừa rồi phục vụ viên lấy tới chư nhiều bảo vật lấy tới, mỗi lấy đồng dạng, cũng sẽ ở phía trên đập mấy lần, làm sạch sẽ rơi ở phía trên bụi bặm, sau đó lại từng kiện từng kiện liệt kê ra tới."),i("br"),i("br"),n._v("Nói một cái, Mặc Phi thân thể liền lắc động một cái."),i("br"),i("br"),n._v("Liên tục nói mấy chục mọi thứ, đã mặt như đáy nồi, nhanh muốn điên rồi."),i("br"),i("br"),n._v("Đối phương chỉ ra những này, không có sai lầm chút nào, bất kể tìm người thí nghiệm vẫn là tự thể nghiệm, kết quả đều giống nhau như đúc."),i("br"),i("br"),n._v("Trước đó bảo vật này tất cả đều nhìn qua, phù hợp đo lường đặc tính, làm sao một cái nháy mắt, toàn bộ cũng thay đổi?"),i("br"),i("br"),n._v("Nhất là đo lường mấy thanh trường kiếm, không những khó mà thuần phục, còn có ác linh ở bên trong, tùy ý đả thương người, thứ này thật muốn mua ra ngoài, cũng không phải là bảo mệnh mà là giết người!"),i("br"),i("br"),n._v("“Nhiều như vậy hàng giả?”"),i("br"),i("br"),n._v("“Linh Tài các bảo vật, ta trước đó luôn luôn rất yên tâm, như thế hiện tại nhiều như vậy giả?”"),i("br"),i("br"),n._v("“Nhiều như vậy gia hỏa, về sau còn thế nào tin tưởng?”"),i("br"),i("br"),n._v("“Xem ra không thể ở đây mua đồ, hàng giả nhiều như vậy, tiện nghi hơn cũng không cần ah...”"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Xem náo nhiệt mọi người, đem những này nhìn ở trong mắt, tất cả đều trong lòng run lên."),i("br"),i("br"),n._v("Đây không phải giàu nghèo vấn đề."),i("br"),i("br"),n._v("Đắt, chỉ cần có thể mua được chính phẩm, cũng không có gì, tiện nghi hơn, mua đến hàng giả cũng không nguyện ý ah!"),i("br"),i("br"),n._v("Không nói cái khác, liền nói cái kia đan dược, mua giả, không chỉ không thể để cho người đột phá, sẽ còn đem người hạ độc chết... Dùng tiền tự tìm cái chết, ai sẽ đi làm?"),i("br"),i("br"),n._v("Trước đó, mọi người biết rõ Linh Tài các đồ vật lai lịch bất chính, còn tất cả đều tới, vừa đến, tiện nghi, thứ hai, vật phẩm chất lượng có cam đoan, hiện tại liền chất lượng đều không cách nào bảo đảm, ai còn dám mua?"),i("br"),i("br"),n._v("“... Cho nên, cái này cũng là giả, nói cách khác... Đây đều là hàng giả!”"),i("br"),i("br"),n._v("Giải thích một hồi, Trương Huyền nhàn nhạt nhìn về phía cách đó không xa phục vụ viên cùng Mặc Phi: “Những hàng hóa này, các ngươi bán cho ta hai trăm ba mươi bảy viên linh thạch thượng phẩm, giả một đền mười, hiện tại nhiều như vậy đều là giả... Đền ta 2,370 viên linh thạch thượng phẩm là được!”"),i("br"),i("br"),n._v("“Bồi thường hơn hai ngàn viên linh thạch thượng phẩm?”"),i("br"),i("br"),n._v("“Giả một đền mười, người ta mua nhiều như vậy, lại giám định ra đều là giả, tự nhiên muốn bồi thường!”"),i("br"),i("br"),n._v("“Điên rồi... Nhiều như vậy linh thạch thượng phẩm, coi như đem Linh Tài các, toàn bộ bán, cũng không thường nổi đi!”"),i("br"),i("br"),n._v("“Bồi thường nổi không thường nổi là một chuyện, ai bảo ngươi gào to giả một đền mười? Hiện tại phát hiện nhiều như vậy giả, ai cũng không có cách nào...”"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Nghe được thanh niên lời nói, tất cả mọi người giật nảy mình."),i("br"),i("br"),n._v("Nhiều như vậy hàng giả, thật muốn giả một đền mười, hơn hai ngàn viên linh thạch thượng phẩm, đừng nói Linh Tài các không bỏ ra nổi đến, coi như Hồng Viễn đế quốc hoàng thất cũng quá sức có thể làm đến!"),i("br"),i("br"),n._v("Nhưng khi nhiều người như vậy bị phát hiện nhiều như vậy hàng giả, không bồi thường, Linh Tài các danh dự tất nhiên tổn hao nhiều, về sau cũng không cần tiếp tục mở đi xuống."),i("br"),i("br"),n._v("“Lợi hại...”"),i("br"),i("br"),n._v("Tôn Cường hai mắt tỏa ánh sáng."),i("br"),i("br"),n._v("Trước đó nói muốn tìm phiền toái, hắn một mực không biết làm sao bây giờ, sợ sẽ bị tại chỗ ném đi, nhìn thấy giờ mới hiểu được thiếu gia đáng sợ!"),i("br"),i("br"),n._v("Nếu quả như thật thoáng qua một cái đến, liền chém chém giết giết, không có chút nào chiếm lý, đối phương xử trí như thế nào đều không đủ, mà bây giờ, tìm ra nhiều như vậy hàng giả, ngươi lại nói giả một đền mười... Hết thảy theo quy tắc lại đi."),i("br"),i("br"),n._v("Nhìn ngươi như thế nào giải quyết!"),i("br"),i("br"),n._v("Coi như chuyện này, tìm tới Danh Sư đường, cũng là đối phương có lỗi!"),i("br"),i("br"),n._v("Đền khẳng định không thường nổi, coi như đền lên, cái này Linh Tài các cũng khẳng định hoàn toàn xong."),i("br"),i("br"),n._v("Không bồi thường danh khí lớn tổn hại, nhiều như vậy hàng giả, đoán chừng về sau cũng không có người còn dám qua tới mua đồ."),i("br"),i("br"),n._v("“Ngươi... Ngươi đến cùng là ai? Đến cùng muốn làm gì?”"),i("br"),i("br"),n._v("Những đạo lý này rất dễ dàng nghĩ thông suốt, phục vụ viên cũng hiểu được, nhanh muốn khóc."),i("br"),i("br"),n._v("Vốn cho rằng là cái khách hàng lớn, về sau có thể áo cơm không lo, hiện tại xem ra, quả thực chính là tai tinh, làm không cẩn thận bát ăn cơm của hắn đều sẽ mất."),i("br"),i("br"),n._v("“Ta là ai? Ta chính là tới mua đồ, các ngươi bán cho ta nhiều như vậy hàng giả, chẳng lẽ không cần đền?”"),i("br"),i("br"),n._v("Trương Huyền mí mắt vừa nhấc, cười vô cùng hòa khí."),i("br"),i("br"),n._v("(Gần đây mọi người đối đổi mới có chút lên án, lão nhai tại công chúng hiệu thượng truyền gần đây trạng thái, lý do cùng tồn cảo trạng thái, mọi người có thể chú ý nhìn một chút. Wechat lục soát “Hoành Tảo Thiên Nhai”, tăng thêm chú ý, thẩm tra lịch sử ghi chép là đủ.)"),i("br"),i("br"),n._v("Convert by: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-762"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-764"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);