(window.webpackJsonp=window.webpackJsonp||[]).push([[2036],{2392:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-755-ta-bao-vat-o-đau"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-755-ta-bao-vat-o-đau"}},[n._v("#")]),n._v(" Chương 755: Ta bảo vật ở đâu?")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("Chương 755: Ta bảo vật ở đâu?"),i("br"),i("br"),n._v("“Ta...”"),i("br"),i("br"),n._v("Phục vụ viên thân thể run rẩy."),i("br"),i("br"),n._v("Linh Tài các, bán nhiều năm như vậy đồ vật, đều không có người nói qua hàng giả nhiều, đến cái này nhân khẩu bên trong rõ ràng đều là giả..."),i("br"),i("br"),n._v("Vốn nghĩ là cái gì cũng đều không hiểu chày gỗ, mặc hắn xẻ thịt, hiện tại mới hiểu được, đây là giả heo ăn thịt hổ, cố ý tới quấy rối!"),i("br"),i("br"),n._v("“Nhanh đi đem Các chủ mời đến, chuyện này, chỉ có hắn mới có thể giải quyết...”"),i("br"),i("br"),n._v("Mặc Phi như là già mười mấy tuổi, nhịn không được nói."),i("br"),i("br"),n._v("Hắn thân là giám bảo sư, biết đối phương nói những cái kia không có sai lầm."),i("br"),i("br"),n._v("Nghiêm ngặt dựa theo giám định trên sách ghi chú đi cân nhắc, quả thực có lừa gạt người mua hiềm nghi, nói là hàng giả cũng không đủ!"),i("br"),i("br"),n._v("Nói cách khác, đối phương thật muốn ấn định cùng giám định thư khác biệt bắt đền, bọn họ ai cũng không giải quyết được, chỉ có Các chủ mới có thể giải quyết!"),i("br"),i("br"),n._v("“Vâng!” Phục vụ viên cái này mới phản ứng được, vội vã lui ra ngoài."),i("br"),i("br"),n._v("Không lâu sau, một cái lông mi lạnh lùng trung niên nhân đi nhanh tới."),i("br"),i("br"),n._v("Người này thân hình cao lớn, hình thể hơi béo, hai mắt mang theo u ám, vừa nhìn liền biết là cái lạnh lùng hung ác chủ."),i("br"),i("br"),n._v("Khí tức trên thân mặc dù giương cung mà không phát, nhưng cho người ta một loại siêu phàm thoát Thánh cảm giác, chỉ sợ đã đạt đến Thánh vực, ít nhất cũng là theo Thánh cấp đừng!"),i("br"),i("br"),n._v("Cũng đúng, không phải loại này cường giả tọa trấn, chỉ bằng vào Linh Tài các, khắp nơi cướp đoạt, thu mua hàng lậu, làm sao có thể tại Hồng Viễn thành ở lại đi xuống."),i("br"),i("br"),n._v("“Là ngươi nói, những vật này đều là hàng giả?”"),i("br"),i("br"),n._v("Đi tới trước mặt, lạnh lùng nhìn về phía cách đó không xa thanh niên, trung niên nhân híp mắt lại."),i("br"),i("br"),n._v("Xem ra trên đường tới, đã nghe phục vụ viên nói tỉ mỉ."),i("br"),i("br"),n._v("Không để ý tới đối phương thái độ, Trương Huyền bàn tay vẫy một cái: “Nhanh quyết định làm sao đền đi, nếu không, hôm nay chuyện này, có lẽ không bao lâu, liền sẽ truyền khắp cả tòa Hồng Viễn thành!”"),i("br"),i("br"),n._v("“Ngươi!”"),i("br"),i("br"),n._v("Nắm đấm xiết chặt, Linh Tài các Các chủ, đen nhánh hai mắt bên trong từng đạo sát cơ chảy xuôi."),i("br"),i("br"),n._v("Luôn luôn đều là bọn họ Linh Tài các đánh người khác chủ ý, lại có thể có người dám đánh bọn hắn, còn phải bồi thường, thật không biết trời cao đất rộng."),i("br"),i("br"),n._v("“Vị bằng hữu này, ta không biết ngươi lai lịch ra sao, có điều, làm người tốt nhất lưu một đường, nếu không, ta sợ cuộc sống sau này, không như vậy suôn sẻ!”"),i("br"),i("br"),n._v("Híp mắt lại, Linh Tài các Các chủ khẽ nói."),i("br"),i("br"),n._v("“Ngươi đây là đang uy hiếp ta?”"),i("br"),i("br"),n._v("Nhẹ nhàng cười một tiếng, Trương Huyền nhìn qua: “Giả một đền mười, đây là các ngươi Linh Tài các quyết định quy củ, làm sao, thật ra hàng giả, còn muốn đem người mua giết?”"),i("br"),i("br"),n._v("“Ta không phải uy hiếp, chỉ là nhắc nhở, đường tu hành khó, cẩn thận tẩu hỏa nhập ma!”"),i("br"),i("br"),n._v("Thấy đối phương không có sợ hãi, căn bản không quan tâm uy hiếp của hắn, Linh Tài các Các chủ tức giận sắp nổ tung, nhưng không có biện pháp nào."),i("br"),i("br"),n._v("Nếu như thật sự ở nơi này động thủ, không cần nghĩ, bọn họ Linh Tài các, chỗ có danh dự đều đem hủy hoại chỉ trong chốc lát."),i("br"),i("br"),n._v("Tích lũy nhiều năm như vậy danh vọng cũng đem trôi theo nước chảy."),i("br"),i("br"),n._v("“Thật hay giả hàng?”"),i("br"),i("br"),n._v("Quay đầu nhìn về phía cách đó không xa Mặc Phi, truyền âm qua."),i("br"),i("br"),n._v("“Ta cũng không biết, bất quá... Hắn nói qua những cái kia bảo vật, cơ bản đều thay đổi thuộc tính, cùng ta giám định lúc, hoàn toàn khác biệt... Coi như nói không là hàng giả, cũng không có người tin tưởng...”"),i("br"),i("br"),n._v("Mặc Phi nhịn không được nói."),i("br"),i("br"),n._v("Trước mặt nhiều người như vậy, tại chỗ giám định, đem bảo vật lai lịch, thiếu hụt nói rõ rõ ràng ràng, liền xem như thật, đoán chừng cũng không ai muốn!"),i("br"),i("br"),n._v("Thật giống như một cái giá trị cực cao Linh cấp thượng phẩm phòng ngự khôi giáp, phòng ngự vô địch, chỉ cần mặc vào, có thể chống lại Hóa Phàm cửu trọng cường giả công kích, nhưng thiếu sót bị cái tên này nói, còn nói làm sao có thể phá vỡ phòng ngự... Như vậy trải qua, còn có ai lại đi mua một cái trăm ngàn chỗ hở đồ vật?"),i("br"),i("br"),n._v("Nói cách khác, những bảo vật này, coi như không giả, đủ loại sơ hở, thiếu sót bạo lộ ra, cũng thay đổi thành giả."),i("br"),i("br"),n._v("“Đáng giận!”"),i("br"),i("br"),n._v("Nghe được Mặc Phi thừa nhận, Linh Tài các Các chủ thị lực trầm thấp đáng sợ."),i("br"),i("br"),n._v("“Thương nghị xong chưa? Xong liền bồi thường tiền, những bảo vật này, giá trị hai trăm ba mươi bảy viên linh thạch thượng phẩm, giả một đền mười, chính là 2,370 viên, như vậy, ta cho các ngươi điểm ưu đãi, số lẻ xóa đi, cho ta hai ngàn viên linh thạch thượng phẩm, chuyện này, coi như xong. Nếu không... Ta sẽ lập tức xin Danh Sư đường tới chủ trì công đạo! Nói các ngươi lừa gạt khách hàng, dùng hàng giả hại một vị danh sư!”"),i("br"),i("br"),n._v("Trương Huyền thản nhiên nói."),i("br"),i("br"),n._v("“Ngươi là danh sư?”"),i("br"),i("br"),n._v("Linh Tài các Các chủ con ngươi co rụt lại."),i("br"),i("br"),n._v("Danh Sư đường trấn thủ một phương, thủ hộ một phương bình yên, dựa theo bình thường đạo lý, chỉ cần không làm ra vi phạm nhân tộc sự tình, căn bản sẽ không thụ lí, nhưng nếu như liên lụy đến một vị danh sư, liền khẳng định sẽ tham gia."),i("br"),i("br"),n._v("Đây là danh sư cần hưởng thụ quyền lợi."),i("br"),i("br"),n._v("Một khi Danh Sư đường tới, bọn họ ép mua ép bán sự tình, làm không cẩn thận liền sẽ rò rỉ, toàn bộ Linh Tài các, đều lại bởi vậy gặp tai nạn."),i("br"),i("br"),n._v("Trước đó không ít người mặc dù biết bọn họ hành động, thứ nhất không chứng cứ, thứ hai bị đánh chọn quan hệ, không muốn truy đến cùng mà thôi."),i("br"),i("br"),n._v("Danh Sư đường thật muốn động thủ, lại bí ẩn, cũng tất nhiên có thể tra rõ ràng, khó mà đào thoát."),i("br"),i("br"),n._v("“Không sai!” Trương Huyền thản nhiên nói."),i("br"),i("br"),n._v("“Bằng chừng ấy tuổi, liền xem như danh sư, đoán chừng cũng học viện học viên chứ?”"),i("br"),i("br"),n._v("Sau khi hết khiếp sợ, nhớ tới cái gì, Linh Tài các Các chủ hừ một tiếng: “Học viện nhiều như vậy học viên, ra ngoài thí luyện, mất tích một hai cái, chỉ sợ cũng không người biết được!”"),i("br"),i("br"),n._v("Danh sư học viện học viên, mặc dù cũng là danh sư, nhưng học viện vì bồi dưỡng học sinh, quy tắc đặt rất cường thế, cho dù có nguy hiểm, cũng rất ít ra mặt."),i("br"),i("br"),n._v("“Học viện học viên đông đảo, mất tích một hai cái, quả thực không tính là gì, có điều, thật không tiện, thuần thú học viện Mi viện trưởng cùng luyện khí học viện Triệu viện trưởng, chỉ sợ sẽ không nghĩ như vậy!”"),i("br"),n._v("Cười nhạt một tiếng, Trương Huyền cổ tay khẽ đảo, hai vị trưởng lão cho lệnh bài xuất hiện tại lòng bàn tay."),i("br"),i("br"),n._v("Dù sao có thứ này, dựa thế một cái cũng không sao."),i("br"),i("br"),n._v("“Là Mi viện trưởng, Triệu viện trưởng tư nhân lệnh bài... Thật!”"),i("br"),i("br"),n._v("Nhìn thoáng qua, một bên Mặc Phi liền thân thể chấn động, vội vã truyền âm."),i("br"),i("br"),n._v("Mi viện trưởng, Triệu viện trưởng thập đại trưởng lão một trong, không chỉ tại Hồng Viễn đế quốc, tại nhất đẳng trong đế quốc, đều uy danh hiển hách, đồng thời đem loại này tư cách cá nhân lệnh bài đưa cho vị này... Có thể thấy được quan hệ."),i("br"),i("br"),n._v("“Thật?”"),i("br"),i("br"),n._v("Vốn cho rằng đối phương chỉ là cái bình thường học viên, uy hiếp một cái liền thôi, không nghĩ tới, người mang hai đại viện trưởng tư nhân lệnh bài, Linh Tài các Các chủ tức giận sắp nổ tung, nhưng không có biện pháp nào."),i("br"),i("br"),n._v("“Tốt, ta cho ngươi một thời gian uống cạn chung trà suy nghĩ, không bồi thường tiền, hiện tại ta liền đi Danh Sư đường, vừa vặn Mạc đường chủ cùng ta quan hệ cũng không tệ, ta để hắn giúp ta mở rộng công đạo, hẳn là sẽ rất vui vẻ đáp ứng!”"),i("br"),i("br"),n._v("Tướng lệnh biển hiệu thu hồi, Trương Huyền duỗi lưng một cái."),i("br"),i("br"),n._v("Đối phương đã dám đối phó hắn người, vậy liền muốn tiếp nhận bản thân mang tới áp bách."),i("br"),i("br"),n._v("Ngươi không phải không kiêng nể gì cả ư?"),i("br"),i("br"),n._v("Vậy thì tốt, ta liền dùng danh sư thân phận tới dọa ngươi, để ngươi ngươi quân lính tan rã!"),i("br"),i("br"),n._v("“Một chén trà...”"),i("br"),i("br"),n._v("Linh Tài các Các chủ muốn nổi giận lại không dám."),i("br"),i("br"),n._v("Một bên phục vụ viên, dường như nghĩ tới điều gì vội vàng đi tới trước mặt, truyền âm nói vài câu."),i("br"),i("br"),n._v("Nghe được hắn, Các chủ thở phào nhẹ nhõm, lần nữa nhìn lại: “Không cần chờ một chén trà, chúng ta đền!”"),i("br"),i("br"),n._v("“Đền?”"),i("br"),i("br"),n._v("“Thật đền a?”"),i("br"),i("br"),n._v("“Hai ngàn viên linh thạch thượng phẩm, đừng nói Linh Tài các, toàn bộ Hồng Viễn đế quốc cầm ra được ư?”"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Nghe được Linh Tài các Các chủ đột nhiên cải biến thái độ, quyết định phải bồi thường, mọi người tất cả đều sững sờ."),i("br"),i("br"),n._v("Không chỉ có như thế, ngay cả Trương Huyền đều có chút ngoài ý muốn."),i("br"),i("br"),n._v("Hắn kỳ thật đều làm ra đối phương không bồi thường tiền ứng đối, biết có thể sẽ hao tổn một đoạn thời gian, nằm mơ đều không nghĩ tới, vậy mà trực tiếp đáp ứng."),i("br"),i("br"),n._v("“Ngươi mới vừa mới cho một cái linh thạch thượng phẩm, mua một chuôi Thanh Đồng giản, hắn bảo vật của hắn mặc dù nói muốn mua, nhưng không giao tiền... Nói cách khác, chỉ hoàn thành một vụ giao dịch! Chúng ta giả một đền mười, cho ngươi mười cái linh thạch thượng phẩm là được!”"),i("br"),i("br"),n._v("Linh Tài các Các chủ khoát tay chặn lại, nói một tiếng: “Còn không mau cầm mười cái linh thạch thượng phẩm tới?”"),i("br"),i("br"),n._v("“Vâng!” Phục vụ viên nhẹ gật đầu, gấp vội vàng lui xuống, không lâu sau lấy tới mười cái linh thạch thượng phẩm, linh khí chớp động, tản mát ra kinh người ánh sáng, hướng Tôn Cường đưa tới."),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Tôn Cường sững sờ, thu cũng không phải không thu cũng không phải."),i("br"),i("br"),n._v("Thiếu gia vừa rồi mặc dù nói muốn mua nhiều như vậy bảo vật, lại không trả tiền, từ đầu đến cuối, cũng chỉ thanh toán xong một cái linh thạch thượng phẩm."),i("br"),i("br"),n._v("Đối phương chui chỗ sơ hở này, giả một đền mười, chỉ giao dịch một cái, đền ngươi mười cái, mặc dù cũng rất buồn bực, nhưng còn có thể trả nổi."),i("br"),i("br"),n._v("Có thể nói, đối phương làm như vậy, không có chút nào coi là không tuân theo quy định."),i("br"),i("br"),n._v("“Thiếu gia...”"),i("br"),i("br"),n._v("Không biết như thế nào cho phải, quay đầu nhìn về phía Trương Huyền, lại thấy hắn cũng không tức giận, ngược lại cười nhạt một tiếng: “Ngươi khẳng định muốn cùng ta chơi như vậy?”"),i("br"),i("br"),n._v("“Vị danh sư này nói gì vậy, chúng ta Linh Tài các mặc dù kém, nhưng cũng tuân thủ quy củ, ngươi chỉ cho một cái linh thạch thượng phẩm, chúng ta dựa theo ước định bồi thường mười cái, đã công bằng công chính, chuyện này coi như về sau nháo đến Danh Sư đường, chúng ta cũng không có không tuân theo quy định.”"),i("br"),i("br"),n._v("Linh Tài các Các chủ ngoài cười nhưng trong không cười: “Ngược lại là ngươi, thân là danh sư, không mua đồ, liền muốn lừa bịp lừa chúng ta hai ngàn linh thạch thượng phẩm, truyền đi, thanh danh cũng không tốt lắm đâu!”"),i("br"),i("br"),n._v("Theo phục vụ viên trong miệng biết, đối phương chỉ thanh toán một cái linh thạch tiền, hắn liền có kế hoạch."),i("br"),i("br"),n._v("Nếu là giao dịch, tự nhiên là mua bán hai Thanh Tài coi xong thành, liền coi như chúng ta cái này là hàng giả, ngươi không đưa tiền, vẫn như cũ là chúng ta Linh Tài các, chúng ta không bán cho ngươi, cũng sẽ không cần bồi thường tiền."),i("br"),i("br"),n._v("“Xem ra ngươi là hoàn toàn định đem số tiền này lại mất, đã như vậy...”"),i("br"),i("br"),n._v("Trương Huyền nhàn nhạt nhìn qua: “Các ngươi đem ta những bảo vật này lấy tới bán ra, lâu như vậy cũng bán không được, ta đem mang đi, không tính là quá mức chứ?”"),i("br"),i("br"),n._v("“Bảo vật? Chúng ta lúc nào bắt ngươi bảo vật bán ra?”"),i("br"),i("br"),n._v("Linh Tài các Các chủ, nhịn không được sững sờ, cảm giác chẳng biết tại sao."),i("br"),i("br"),n._v("Ngươi người này, ta là lần đầu tiên gặp, lúc nào, cầm qua ngươi bảo vật?"),i("br"),i("br"),n._v("Ngậm máu phun người cũng không phải như vậy phun a!"),i("br"),i("br"),n._v("“Không cầm qua?” Trương Huyền lắc đầu: “Bảo vật đều có linh tính, nhận chủ không phải một sớm một chiều sự tình, loại sự tình này, người người đều biết, chắc hẳn Các chủ cũng không xa lạ gì đi!”"),i("br"),i("br"),n._v("Linh Tài các Các chủ gật đầu, mọi người chung quanh cũng lên tiếng."),i("br"),i("br"),n._v("Bảo vật nhận chủ, khá là phiền toái, có ít người đem bảo bối mua đến tay bên trong, thật nhiều năm, cũng không thể để hắn ngoan ngoãn nghe lời."),i("br"),i("br"),n._v("“Vậy liền đơn giản... Ta bảo vật, tự nhiên sẽ nghe lời của ta, điểm ấy không cần giải thích, mọi người cũng có thể nhìn ra được!”"),i("br"),i("br"),n._v("Thân thể đứng thẳng tắp, Trương Huyền lông mày vung lên, mang theo một cỗ đâm thủng bầu trời khí thế, đột nhiên mở miệng, tựa như chuông lớn."),i("br"),i("br"),n._v("“Bảo bối của ta ở đâu? Còn không qua đây bái kiến chủ nhân!”"),i("br"),i("br"),n._v("Soạt!"),i("br"),i("br"),n._v("Tiếng nói kết thúc, chung quanh một đống lớn vũ khí, nhao nhao bay lên, đồng loạt đem hắn vây lại."),i("br"),i("br"),n._v("Convert by: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-763"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-765"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);