(window.webpackJsonp=window.webpackJsonp||[]).push([[2040],{2395:function(n,h,i){"use strict";i.r(h);var t=i(44),c=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-759-thuong-nguyet-hoi"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-759-thuong-nguyet-hoi"}},[n._v("#")]),n._v(" Chương 759: Thương Nguyệt hội")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("Chương 759: Thương Nguyệt hội"),i("br"),i("br"),n._v("“Dạng này, hôm nay mười đại học viện tuyển nhận học sinh lão sư, đều sẽ đi  bên trên một tiết công khai khóa, chúng ta hi vọng Trương sư có thể cùng chúng ta cùng đi xem nhìn, tầm mắt của ngươi so sánh mở rộng, giúp chúng ta lựa chọn, có thể chọn so sánh phù hợp...”"),i("br"),i("br"),n._v("Nhược Hoan công tử có chút lúng túng mở miệng."),i("br"),i("br"),n._v("Bị trước mắt vị này liên tục đả kích đã quen, biết cái tên này bản lĩnh cùng nhãn lực."),i("br"),i("br"),n._v("Nếu để cho hắn hỗ trợ chọn, khẳng định so nhóm người mình chọn càng tốt hơn."),i("br"),i("br"),n._v("Học viện bái sư chỉ có ba ngày thời gian, một khi tìm không thấy thích hợp, sau này thành liền sẽ nhận hạn chế, suy đi nghĩ lại, mấy người vẫn cảm thấy phiền phức Trương Huyền một chút."),i("br"),i("br"),n._v("Dù sao đều là cùng một nơi tới, xem như quen thuộc."),i("br"),i("br"),n._v("Vị này Trương sư nếu bàn về nhãn lực lời nói, nhưng mà so Hồng sư đám người đều cao minh hơn không ít."),i("br"),i("br"),n._v("“Giảng bài đài? Công khai khóa?” Trương Huyền sững sờ."),i("br"),i("br"),n._v("Hắn đi tới học viện, vào xem lấy khảo hạch lục tinh luyện khí sư, cũng không rõ ràng còn có chuyện như vậy."),i("br"),i("br"),n._v("Giảng bài đài là danh sư học viện trung tâm nhất địa phương, tiến hành một ít thi đấu loại hình đều sẽ tới đây. Cũng là không ít học sinh giải quyết mâu thuẫn nơi chốn, cùng một vài chỗ tỷ thí đài tương tự."),i("br"),i("br"),n._v("Còn công khai khóa, trước đó tại Hồng Thiên học viện làm lão sư thời điểm, liền nghe nói qua."),i("br"),i("br"),n._v("Chính là bên trên một tiết ai cũng có thể tới quan sát chương trình học, như vậy cũng có thể biểu hiện ra lão sư giảng bài năng lực."),i("br"),i("br"),n._v("Trước đó công khai khóa, cơ bản đều là lão sư quan sát lão sư, lẫn nhau học tập, cái này học viện thế mà để lão sư trực tiếp đi học, học sinh quan sát sau lại tuyển chọn..."),i("br"),i("br"),n._v("Nghe mười phần không sai."),i("br"),i("br"),n._v("Như vậy trải qua, lão sư tốt, thu học sinh sẽ càng ngày càng nhiều, mà kém lão sư, có khả năng một cái đều không thu được."),i("br"),i("br"),n._v("Xem như một loại tốt cạnh tranh."),i("br"),i("br"),n._v("“Đúng vậy a, Trương sư muốn đi qua ư?” Mấy người trông mong nhìn qua."),i("br"),i("br"),n._v("Thấy mấy người cái dạng này, biết là muốn nhường cho mình đi qua, giúp bọn hắn nắm cái biện pháp, dù sao, nhiều như vậy danh sư đồng thời giảng bài, đổi lại ai cũng sẽ hoa mắt, khó mà phân biệt."),i("br"),i("br"),n._v("“Tốt, cùng đi xem nhìn, đúng lúc cũng nhìn xem danh sư học viện giảng bài trình độ...”"),i("br"),i("br"),n._v("Suy tư một chút, Trương Huyền nhẹ gật đầu."),i("br"),i("br"),n._v("Đã không sai biệt lắm tất cả lão sư, đều chạy đến bên trên công khai khóa, Kinh Hồng viện, Ma âm viện, khẳng định đi cũng không có người nào, muốn tìm thư nhìn, đoán chừng cũng không rất dễ dàng, còn không bằng đi nhìn một chút đối phương khóa như thế nào bên trên, cũng tốt làm so sánh."),i("br"),i("br"),n._v("Có quyết định, không có gì có thể xoắn xuýt, rời đi sân nhỏ mấy người hướng giảng bài đài phương hướng đi đến."),i("br"),i("br"),n._v("Giảng bài đài khoảng cách tinh anh khu có chút khoảng cách, bất quá mọi người thực lực đều không yếu, không tiền điện thoại quá nhiều thời gian, liền đến đến gần."),i("br"),i("br"),n._v("Từ xa nhìn lại, dòng người cuồn cuộn, người ta tấp nập."),i("br"),i("br"),n._v("Ba vạn tân sinh hôm nay đều sẽ tới nơi này nghe giảng bài, lựa chọn lão sư, cũng không ít lão sinh, chạy tới nhân lúc còn nóng náo, rộng lớn giảng bài đài, giương mắt nhìn lên, bị bầy người chiếm hết, lít nha lít nhít, lần đầu tiên tới lời nói, tìm không rõ phương hướng."),i("br"),i("br"),n._v("“Vì để cho tân sinh tốt hơn nghe lão sư giảng bài, học viện chuyên môn đem giảng bài đài chia cắt thành một cái hẹp dài thông đạo, chỉ cần dọc theo đường hướng phía trong đi, liền có thể nhìn thấy tất cả lão sư, nghe được tất cả giảng bài nội dung!”"),i("br"),i("br"),n._v("Nhược Hoan công tử sớm liền đánh nghe cho kỹ, còn chưa tới đến gần liền hướng mọi người giới thiệu."),i("br"),i("br"),n._v("Theo hắn, Trương Huyền nhìn lại, quả nhiên thấy nơi xa một cái thô to dòng người, dọc theo nào đó cái thông đạo chậm rãi hướng về phía trước."),i("br"),i("br"),n._v("Mà hai bên đài cao, lại có lão sư giảng bài, khoảng cách xa, nghe không được nội dung, nhưng có thể nhìn thấy dưới đài mọi người nghe như si như say, không cách nào tự kềm chế."),i("br"),i("br"),n._v("Danh sư học viện lão sư, hầu như đều đạt đến lục tinh cấp bậc, có thể trở thành loại cấp bậc này danh sư, làm sao có thể có kẻ yếu. Coi như giảng bài nội dung không sinh động, khẳng định cũng đối tu luyện lý giải cực sâu, từng câu từng chữ, đều tự mang chuẩn mực, để cho người ta vừa nghe xong, liền tình khó chính mình, được lợi rất nhiều."),i("br"),i("br"),n._v("“Các vị học đệ, có muốn hay không 《 Giáo Chức Công Tế Giải 》?”"),i("br"),i("br"),n._v("Đang định đi theo đám người đằng sau đi vào, chỉ thấy một thanh niên tiến lên đón, ngăn tại trước mặt."),i("br"),i("br"),n._v("Chừng ba mươi tuổi, mặc ngũ tinh danh sư trang phục, dáng người cao gầy, như cùng một căn thân trúc, thị lực rời rạc, cười rạng rỡ, vừa nhìn liền biết là cái kẻ già đời."),i("br"),i("br"),n._v("“Giáo Chức Công Tế Giải?” Trương Huyền mày nhăn lại."),i("br"),i("br"),n._v("“Đúng vậy a, là chúng ta Thương Nguyệt hội, chuyên môn biên soạn, phía trên có chúng ta học viện tất cả lão sư tin tức cặn kẽ, giảng bài đặc điểm, hứng thú yêu thích kỳ trước dạy học thành tích... Mua lấy một bản, lại đi nghe công khai khóa, có thể tốt hơn so sánh, làm ra lựa chọn...”"),i("br"),i("br"),n._v("Thanh niên nhìn chung quanh một vòng, phát hiện không có người chú ý lúc này mới từ trong ngực móc ra một quyển sách, đưa tới, hạ giọng nói."),i("br"),i("br"),n._v("“Còn có thứ này?”"),i("br"),i("br"),n._v("Tràn đầy kỳ quái, Trương Huyền đưa tay tiếp nhận thư tịch, đang muốn mở ra, liền bị thanh niên duỗi tay đè chặt: “Tất cả mọi người là danh sư, nắm giữ đã gặp qua là không quên được năng lực, ngươi mở ra xem một lần, ta còn bán cho ai? Một câu nói, muốn nhìn có thể, lấy tiền mua sắm...”"),i("br"),i("br"),n._v("Danh sư đi đến nửa bước ngũ tinh, ngũ tinh cấp bậc, cơ bản đều có đã gặp qua là không quên được năng lực, cái này Giáo Chức Công Tế Giải trân quý liền trân quý tại tư liệu hiếm có, mở ra nhìn một chút học thuộc, khẳng định liền sẽ không mua."),i("br"),i("br"),n._v("“Bao nhiêu tiền?”"),i("br"),i("br"),n._v("Nhược Hoan công tử nhịn không được mở miệng."),i("br"),i("br"),n._v("Thứ này nếu là lão sinh chuẩn bị, khẳng định hết sức chính xác, có thể mua lấy một cái, lại lựa chọn lão sư thời điểm, có dựa dẫm, dễ dàng không ít, liền không đến mức người mù sờ voi."),i("br"),i("br"),n._v("“Những tài liệu này là chúng ta Thương Nguyệt hội tốn hao to lớn đánh đổi mới lấy được, văn tự không quý, chi phí quý, một cái một trăm viên linh thạch trung phẩm! Tổng thể không mặc cả!”"),i("br"),i("br"),n._v("Thanh niên nói."),i("br"),n._v("“Một trăm viên linh thạch trung phẩm?” Tất cả mọi người giật nảy mình."),i("br"),i("br"),n._v("Nhược Hoan công tử đến từ vạn quốc liên minh, tổng cộng đều chưa thấy qua mấy cái linh thạch trung phẩm, La Tuyền đến từ tông môn, hơi rất nhiều, nhưng có thể lấy ra mấy chục viên là tốt lắm rồi, một trăm viên tuyệt đối không thể có thể."),i("br"),i("br"),n._v("Còn Tống Siêu, lúc trước Trương Huyền lừa dối xuống, lấy ra một cái linh thạch trung phẩm, cũng không dễ dàng, làm sao có thể một hơi lấy ra một trăm cái."),i("br"),i("br"),n._v("Nói cách khác, đối phương giá cả, loại trừ Trương Huyền có thể thanh toán, những người khác ai cũng không bỏ ra nổi tới."),i("br"),i("br"),n._v("Thật là đen!"),i("br"),i("br"),n._v("“Chê đắt? Trong này nhưng mà ghi tất cả lão sư tin tức cặn kẽ, chỉ cần tìm đúng lão sư, thành công đột phá đến ngũ tinh danh sư, linh thạch còn không là muốn muốn bao nhiêu thì có bấy nhiêu?”"),i("br"),i("br"),n._v("Thấy vẻ mặt của mọi người, thanh niên vội nói."),i("br"),i("br"),n._v("Danh sư kém một cái cấp bậc còn kém cách rất lớn, tứ tinh danh sư kiếm lấy linh thạch trung phẩm khả năng còn có chút khó khăn, đi đến ngũ tinh, muốn kiếm tiền, liền dễ dàng hơn nhiều."),i("br"),i("br"),n._v("“Đa tạ, chúng ta không cần cái này!”"),i("br"),i("br"),n._v("Thấy hắn tiếp tục chào hàng, Trương Huyền lắc đầu."),i("br"),i("br"),n._v("Một trăm linh thạch, mua cái cái này... Đối với hắn mà nói còn thật không cần!"),i("br"),i("br"),n._v("Chủ yếu nhất là... Vừa rồi bàn tay tiếp xúc một chút, trong đầu đã có một bản, thậm chí càng kỹ càng, biết hắn tin tức kia là đúng, là sai."),i("br"),i("br"),n._v("“Không cần? Sao có thể không cần đâu? Vào học viện tìm tới thích hợp lão sư của mình là vô cùng trọng yếu, tìm không thấy rất có thể lại không có cách nào tiến triển, cả một đời đều vây ở năm nhất, cuối cùng bị đuổi ra sân trường...”"),i("br"),i("br"),n._v("Không nghĩ tới trước mắt những học sinh mới này, trực tiếp cự tuyệt, thanh niên ngẩn ngơ, gấp vội mở miệng."),i("br"),i("br"),n._v("Mới vừa tiến vào học viện học sinh, đối lão sư không hiểu rõ, rất dễ dàng bái sai môn đình, đến lúc đó, coi như còn muốn sửa cũng không thể nào."),i("br"),i("br"),n._v("“Thật không cần, vừa rồi chúng ta đã mua một bản, không cần thiết lại mua cuốn thứ hai!” Trương Huyền nói."),i("br"),i("br"),n._v("“Mua một bản?”"),i("br"),i("br"),n._v("Thanh niên sửng sốt một chút, lập tức nhớ tới cái gì, nhịn không được nói: “Các ngươi chẳng lẽ mua Chân Dương hội? Tin tức của bọn hắn không được đầy đủ, cùng chúng ta cái này không cách nào so sánh được...”"),i("br"),i("br"),n._v("“Ngươi nói cái này Chân Dương hội, Thương Nguyệt hội đến cùng là cái gì?”"),i("br"),i("br"),n._v("Trương Huyền nghi ngờ nhìn qua."),i("br"),i("br"),n._v("“Ngươi không biết?”"),i("br"),i("br"),n._v("Thanh niên chần chờ một chút, lập tức nhẹ gật đầu: “Cũng đúng, các ngươi vừa tới học viện, không biết cũng rất bình thường. Học viện có rất nhiều học sinh tự phát hình thành tổ chức, đều là có học viện lợi hại nhất học trưởng thành lập. Giống chúng ta Thương Nguyệt hội, chính là Long Thương Nguyệt học trưởng một tay sáng lập. Mà Chân Dương hội, thì lại lấy Tiết Chân Dương học trưởng vi tôn!”"),i("br"),i("br"),n._v("“Long Thương Nguyệt? Tiết Chân Dương?”"),i("br"),i("br"),n._v("“Ừm, hai vị này đều là học viện lớp năm học trưởng, mỗi một cái đều có cực mạnh thực lực, chỉ muốn gia nhập cái tổ chức này, liền có cơ hội lấy được học trưởng khóa ngoại phụ đạo!” Thanh niên mặt mũi tự hào mà nói."),i("br"),i("br"),n._v("“Khóa ngoại phụ đạo?” Trương Huyền sửng sốt một chút, lập tức hiểu được."),i("br"),i("br"),n._v("Có thể là lớp năm học viên, chỉ sợ danh sư đẳng cấp, đã đạt đến lục tinh hạ phẩm."),i("br"),i("br"),n._v("(Tứ tinh đỉnh phong, nửa bước ngũ tinh là năm nhất, ngũ tinh hạ phẩm năm thứ hai, ngũ tinh trung phẩm năm thứ ba, ngũ tinh thượng phẩm năm thứ tư, lục tinh hạ phẩm lớp năm.)"),i("br"),i("br"),n._v("Học viện lão sư đi học, bình thường đều là một đôi nhiều, học sinh nhiều, khó tránh khỏi chiếu cố không được đầy đủ, có thể được đến cho rằng lục tinh hạ phẩm danh sư khóa ngoại chỉ điểm, quả thực sẽ đối với tu vi tấn cấp có trợ giúp rất lớn."),i("br"),i("br"),n._v("“Đúng vậy a, trong học viện đâu đâu cũng có thiên tài, đều là danh sư, ngươi không trổ hết tài năng, cũng chỉ có thể rớt lại phía sau, không chiếm được thí luyện cơ hội cùng tài nguyên... Chỉ có gia nhập những tổ chức này, mới có thể tốt hơn tiến bộ!” Thanh niên nói."),i("br"),i("br"),n._v("Nhược Hoan công tử, La Tuyền đám người nghe nói như thế, tất cả đều nghiêm nghị."),i("br"),i("br"),n._v("Bọn họ không có vị này Trương sư thiên phú và năng lực, muốn tại tất cả đều là thiên tài trong lớp làm cho người chú ý, là căn bản làm không được."),i("br"),i("br"),n._v("Không có chú ý, liền không chiếm được tốt hơn tài nguyên, không chiếm được càng cơ hội tốt, làm không cẩn thận liền sẽ phí thời gian một đời."),i("br"),i("br"),n._v("Cho nên, gia nhập những học sinh này tổ chức, cũng là chuyện không có cách nào khác."),i("br"),i("br"),n._v("“Được rồi, những này các ngươi về sau khẳng định đều sẽ biết, Chân Dương hội làm cái kia Giáo Chức Công Tế Giải tin tức không được đầy đủ, vẫn là mua chúng ta Thương Nguyệt hội a, chúng ta Long Thương Nguyệt học trưởng tại lớp năm thi đấu bài danh bên trong, nhưng mà so Tiết Chân Dương học trưởng cao hơn nhiều...”"),i("br"),i("br"),n._v("Lắc đầu không nói thêm lời chuyện này, thanh niên tiếp tục chào hàng sổ tay của hắn."),i("br"),i("br"),n._v("“Đa tạ, thực tình không cần...”"),i("br"),i("br"),n._v("Nếu như thứ này một, hai cái linh thạch trung phẩm, hắn cũng liền mua, một trăm cái, rõ ràng là cứng rắn đoạt, làm sao có thể đáp ứng?"),i("br"),i("br"),n._v("Suy nghĩ một chút vẫn là quên đi."),i("br"),i("br"),n._v("“Không mua? Vậy không được! Các ngươi đã mua Chân Dương hội, nhất định phải mua chúng ta Thương Nguyệt hội, nếu không, chẳng khác nào đắc tội chúng ta Thương Nguyệt hội, về sau ở trong học viện, phiền phức chỉ sợ không thể thiếu!”"),i("br"),i("br"),n._v("Thấy nói nhiều như vậy, mấy người đều không mua, thanh niên sầm mặt lại, thái độ lại không vừa rồi nhiệt liệt, ngược lại để lộ ra uy hiếp."),i("br"),i("br"),n._v("“Phiền phức không ít? Làm sao, ngươi đang uy hiếp chúng ta?” Trương Huyền sững sờ."),i("br"),i("br"),n._v("“Uy hiếp? Này cũng không tính là, nhưng mà hảo tâm nhắc nhở, danh sư học viện mặc dù đâu đâu cũng có danh sư, nhưng cũng không phải thuận buồm xuôi gió, không giống trong tưởng tượng như vậy hòa bình!”"),i("br"),i("br"),n._v("Thanh niên khẽ nói."),i("br"),i("br"),n._v("Convert by: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-767"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-769"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);