(window.webpackJsonp=window.webpackJsonp||[]).push([[2047],{2402:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-765-noi-đieu-kien"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-765-noi-đieu-kien"}},[n._v("#")]),n._v(" Chương 765: Nói điều kiện")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("Chương 765: Nói điều kiện"),i("br"),i("br"),n._v("“Phốc!”"),i("br"),i("br"),n._v("Lạc Thất Thất một hơi thở không đi lên, suýt chút nữa đã hôn mê."),i("br"),i("br"),n._v("Bất quá, nàng cực kì thông minh, một cái chớp mắt liền hiểu được đối phương ý tứ, trên gương mặt xinh đẹp tràn đầy cười khổ."),i("br"),i("br"),n._v("Chính mình cái này lão sư, cái gì đều tốt, liền là một số thời khắc ý nghĩ tương đối quái sinh, để nàng đều cảm thấy dở khóc dở cười."),i("br"),i("br"),n._v("“Huyền huyền?” Một bên Hồ Yêu Yêu còn chưa hiểu, có chút ngẩn người."),i("br"),i("br"),n._v("“Hắn là của ta luyện đan lão sư, Trương Huyền!” Sợ đối phương hiểu lầm, Lạc Thất Thất vội vàng trả lời."),i("br"),i("br"),n._v("“Trương Huyền? Ngươi chính là cái kia phá hủy chúng ta rất nhiều học hội buôn bán tân sinh?”"),i("br"),i("br"),n._v("Nghe được cái tên này, Hồ Yêu Yêu cái này mới phản ứng được, đen nhánh hai mắt lóe lên, không tỳ vết chút nào trên mặt lộ ra giống như cười mà không phải cười thần thái, dường như muốn muốn quan sát tỉ mỉ kẻ trước mắt này, đến cùng có cái gì đặc thù, để bọn hắn nhiều như vậy lão sinh một chút bị động như thế."),i("br"),i("br"),n._v("Có thể nói, danh sư học viện thành lập nhiều năm như vậy, trước đến giờ còn không có cái nào tân sinh, vừa đến liền đánh vỡ quy củ, để vô số học hội, một phân tiền đều không có kiếm được."),i("br"),i("br"),n._v("Cẩn thận nhìn chằm chằm một hồi, phát hiện người thanh niên này, ánh mắt thuần khiết, dung mạo chất phác, cùng trong truyền thuyết hoàn toàn khác biệt, nhịn không được có chút kỳ quái."),i("br"),i("br"),n._v("“Ngươi mới vừa nói... Là ngươi luyện đan lão sư? Chuyện gì xảy ra?”"),i("br"),i("br"),n._v("Quay đầu nhìn về phía Lạc Thất Thất."),i("br"),i("br"),n._v("Vị này Lạc Thất Thất cùng nàng cùng là hoa khôi của trường nhân vật trên bảng, hiện tại bài danh khả năng không bằng, không phải là bởi vì không xinh đẹp, mà là tuổi tác cùng tu vi không bằng, không có loại kia hồng nhan họa thủy khí chất."),i("br"),i("br"),n._v("Chỉ cần mặc nàng trưởng thành, có thể đoán được, vượt qua nàng, nhưng mà vấn đề thời gian."),i("br"),i("br"),n._v("Do đó, thời gian rất sớm, liền chú ý tới cô gái này."),i("br"),i("br"),n._v("Như thế ưu tú người, lại còn nói cái này Trương Huyền là nàng luyện đan lão sư, để cho người ta tràn đầy ngạc nhiên."),i("br"),i("br"),n._v("“Trương sư truyền thụ cho ta luyện đan!” Lạc Thất Thất nhẹ gật đầu, không trong vấn đề này tiếp tục dây dưa: “Đã ngươi biết Ngô Dương Tử tiền bối chỗ ở cũ chỗ trên mặt đất, có thể hay không kỹ càng báo cho, chỉ cần xác định là thật, ta sẽ lấy hai cái học phần làm đại giá tiến hành trao đổi.”"),i("br"),i("br"),n._v("“Hai cái học phần, ta không cần!”"),i("br"),i("br"),n._v("Thấy nàng không muốn nói, biết tiếp tục đuổi mò xuống đi, cũng không có gì tốt chỗ, Hồ Yêu Yêu lắc đầu."),i("br"),i("br"),n._v("“Không cần?” Lạc Thất Thất cau mày."),i("br"),i("br"),n._v("Nhờ giúp đỡ tháp, cũng là một loại giao dịch, ngươi đã tiếp, liền đại biểu công nhận cái này thù lao, không cần là có ý gì?"),i("br"),i("br"),n._v("“Đúng, ta có thể nói cho các ngươi biết Ngô Dương Tử tiền bối chỗ ở cũ địa phương, bất quá... Các ngươi nhất định phải nói cho ta biết, tốn hao như thế lớn đánh đổi, tìm nơi này làm cái gì!”"),i("br"),i("br"),n._v("Vuốt ve một chút trước ngực tóc đen, Hồ Yêu Yêu cười khanh khách nhìn qua."),i("br"),i("br"),n._v("Ngô Dương Tử đã biến mất hơn hai nghìn năm, hắn chỗ ở cũ những năm qua này, đã sớm thay đổi không biết bao nhiêu người, cho dù có bảo bối gì, cũng khẳng định sớm đã bị lục soát cạo sạch sẽ."),i("br"),i("br"),n._v("Trước mắt vị này Lạc Thất Thất, lại muốn tốn hao hai cái học phần mua sắm tin tức, để nàng tràn đầy nghi hoặc, muốn biết vì cái gì."),i("br"),i("br"),n._v("“Cái này, thứ cho khó trả lời!”"),i("br"),i("br"),n._v("Lạc Thất Thất lắc đầu."),i("br"),i("br"),n._v("Ngô Dương Tử là Hồng Viễn đế quốc, từ trước tới nay nổi danh nhất luyện khí đại tông sư, một đời sưu tập bảo vật sao nó trân quý."),i("br"),i("br"),n._v("Tin tức này một khi truyền đi, đừng nói bọn họ không bảo vệ, coi như Triệu viện trưởng đi ra, đều chưa hẳn có thể đối phó được áp lực."),i("br"),i("br"),n._v("“Ồ?”"),i("br"),i("br"),n._v("Thấy nàng không nói, Hồ Yêu Yêu sửng sốt một chút, nhãn châu xoay động: “Ngươi không nói... Liền đại biểu ngươi không đồng ý giao dịch, ta cũng sẽ không nói cho ngươi chỗ ở cũ vị trí. Nói thật với ngươi đi, nơi này chỉ có ta biết, không giao dịch với ta, coi như ngươi tìm đọc hoàn chỉnh cái học viện thư tịch, cũng tìm không thấy!”"),i("br"),i("br"),n._v("“Vậy liền không có cách nào giao dịch!” Lạc Thất Thất cự tuyệt."),i("br"),i("br"),n._v("Sau khi nói xong, nhìn thoáng qua Trương Huyền, thấy hắn cũng nhẹ gật đầu, lúc này mới liền ôm quyền: “Thực sự thật không tiện, quấy rầy học trưởng, chúng ta cáo từ!”"),i("br"),i("br"),n._v("Nói xong, cùng Trương Huyền nhấc chân hướng lương đình đi ra ngoài."),i("br"),i("br"),n._v("Ngô Dương Tử bảo tàng sự tình, không thể nói, cái này là ranh giới cuối cùng."),i("br"),i("br"),n._v("Tình nguyện tìm không thấy chỗ ở cũ, cũng không có khả năng nói ra."),i("br"),i("br"),n._v("“Chậm rãi...”"),i("br"),i("br"),n._v("Thấy tình nguyện từ bỏ giao dịch, cũng không nói, Hồ Yêu Yêu tươi đẹp môi đỏ vểnh lên lên, lần nữa cười một tiếng: “Đã thật không muốn nói, ta liền đổi một cái điều kiện!”"),i("br"),i("br"),n._v("“Xin lắng tai nghe!” Lạc Thất Thất dừng lại."),i("br"),i("br"),n._v("“Như vậy... Vị này Trương Huyền đắc tội ta, chỉ cần đem hắn giao cho ta xử lý, ta liền mang các ngươi đi Ngô Dương Tử chỗ ở cũ!”"),i("br"),i("br"),n._v("Hồ Yêu Yêu nói."),i("br"),i("br"),n._v("“Giao cho ngươi xử lý?”"),i("br"),i("br"),n._v("Đôi mi thanh tú nhăn lại, Lạc Thất Thất vẻ mặt một bên dưới trầm xuống: “Hồ Yêu Yêu, ta kính trọng ngươi là học trưởng, đối ngươi lễ phép có gia! Ngươi nhưng vũ nhục lão sư ta, là dụng ý gì?”"),i("br"),i("br"),n._v("Nàng vừa rồi đã giới thiệu, Trương Huyền là lão sư của nàng, đối phương nhưng còn muốn cho hắn giao ra, quả thực chính là ở trước mặt đánh mặt."),i("br"),i("br"),n._v("Một ngày làm thầy, cả đời là cha!"),i("br"),i("br"),n._v("Làm học sinh, nếu như ngay cả lão sư tôn nghiêm cũng không bảo vệ được, còn có tư cách gì còn sống?"),i("br"),i("br"),n._v("“Nha... Đừng hiểu lầm, Trương Huyền mặc dù là ngươi lão sư, nhưng cũng là học viện năm nhất tân sinh... Mới vừa rồi là ta mạo phạm!”"),i("br"),i("br"),n._v("Cũng biết lời nói mới rồi có vấn đề, khuôn mặt nghiêm, Hồ Yêu Yêu vội vàng nói xin lỗi."),i("br"),n._v("Danh sư đại lục, coi trọng sư đạo truyền thừa, “Sư” địa vị cực cao."),i("br"),i("br"),n._v("Thiên địa thân sư quân."),i("br"),i("br"),n._v("Dưới trời đất, lão sư địa vị có thể so với song thân, ngay cả Quân Vương đều muốn sắp xếp ở phía sau."),i("br"),i("br"),n._v("Nàng vừa rồi chỉ nghĩ, vị này Trương Huyền là năm nhất tân sinh, nhưng quên là vị này Lạc Thất Thất lão sư, đem học sinh trước mặt, vũ nhục lão sư, đây là cực lớn không tôn trọng."),i("br"),i("br"),n._v("Thân là danh sư, nhưng cố ý xúc phạm, truyền đi, thanh danh của nàng cũng sẽ cực kì bị hao tổn."),i("br"),i("br"),n._v("Nói xong, nhìn về phía cách đó không xa Trương Huyền: “Trương sư, đã ngươi cùng Lạc Thất Thất cùng lúc xuất hiện, chắc hẳn cũng muốn biết Ngô Dương Tử tiền bối chỗ ở cũ vị trí... Chỉ muốn đáp ứng điều kiện của ta, ta sẽ nói cho ngươi biết vị trí, như thế nào?”"),i("br"),i("br"),n._v("“Điều kiện? Điều kiện gì?”"),i("br"),i("br"),n._v("Không nghĩ tới, cái tên này ở trên người hắn nghĩ cách, Trương Huyền một mặt hiền lành cười cười, thoạt nhìn mười phần thẳng thắn."),i("br"),i("br"),n._v("“Rất đơn giản, ngươi phá hủy Yêu Nghiệt hội thừa cơ cơ hội kiếm tiền, chỉ muốn đáp ứng gia nhập chúng ta, sau đó trong một năm, hỗ trợ kiếm lấy mười vạn viên linh thạch trung phẩm! Ta sẽ nói cho ngươi biết, ngươi muốn biết sự tình.”"),i("br"),i("br"),n._v("Hồ Yêu Yêu cười nói."),i("br"),i("br"),n._v("“Yêu Nghiệt hội?” Trương Huyền sửng sốt một chút: “Đây là ngươi học hội?”"),i("br"),i("br"),n._v("Học viện rất nhiều lớp năm học trưởng, đều sẽ thành lập học hội, trước đó liền nghe qua cái gì Yêu Nghiệt hội, chẳng qua là cảm thấy có chút kỳ quái, nghe trước mắt cô gái này khẩu khí, phải cùng nàng có quan hệ."),i("br"),i("br"),n._v("“Hồ Yêu Yêu, tên mang yêu, lại lớn lên hồng nhan họa thủy, như là yêu nghiệt, cho nên... Nàng học hội tự xưng Yêu Nghiệt hội!” Thấy lão sư không hiểu, Lạc Thất Thất truyền âm qua."),i("br"),i("br"),n._v("“Không sai!” Thấy cái tên này thế mà không biết, Hồ Yêu Yêu có chút im lặng."),i("br"),i("br"),n._v("Liền hội trưởng cũng không biết là ai, liền dám đắc tội nhiều như vậy học hội, kẻ trước mắt này, lá gan thật là lớn."),i("br"),i("br"),n._v("“Ah!”"),i("br"),i("br"),n._v("Giờ mới hiểu được tới, Trương Huyền nhẹ gật đầu, nhìn về phía cách đó không xa tư thái xinh đẹp, để cho người ta nhìn nhiều một hồi, liền hô hấp dồn dập nữ hài: “Gia nhập học hội, ta không hứng thú gì... Còn kiếm tiền, mười vạn viên linh thạch trung phẩm, chính ta đều không có... Làm sao cho ngươi lợi nhuận! Đổi điều kiện đi!”"),i("br"),i("br"),n._v("Hắn là cái đi đường không chiếm tiền coi như thâm hụt tiền gia hỏa, đòi hắn tiền, mở cái gì trò đùa!"),i("br"),i("br"),n._v("“Đổi điều kiện? Cũng tốt ah!” Duỗi ra lưng mỏi, Hồ Yêu Yêu phác hoạ ra một cái động lòng người ưu mỹ đường vòng cung: “Ngươi làm ta một năm hạ nhân, ta liền tha thứ ngươi đắc tội chúng ta Yêu Nghiệt hội sự tình, cũng có thể thuận tiện đem Ngô Dương Tử chỗ ở cũ sự tình, nói cho ngươi nghe!”"),i("br"),i("br"),n._v("“Hạ nhân?”"),i("br"),i("br"),n._v("Trương Huyền không còn gì để nói, nữ nhân này thật là càng nghĩ càng ngây thơ, càng nghĩ càng vui vẻ."),i("br"),i("br"),n._v("Lắc đầu: “Ngươi làm ta tỳ nữ, ta cũng còn không muốn, để cho ta làm ngươi hạ nhân, thực có can đảm muốn...”"),i("br"),i("br"),n._v("Hắn thực sự nói thật, đối phương cũng là dáng dấp xinh đẹp chút, nhưng là cùng Lạc Nhược Hi so, cũng kém không biết bao nhiêu vạn dặm."),i("br"),i("br"),n._v("Nhân vật như vậy, làm hắn tỳ nữ, cũng sẽ không đồng ý, còn muốn để mình làm hạ nhân, thực biết nằm mơ."),i("br"),i("br"),n._v("“Vậy liền không có biện pháp giao dịch... Hi vọng các ngươi bản thân có thể tìm tới địa phương!”"),i("br"),i("br"),n._v("Thấy cái tên này không những không đáp ứng, còn nói mình làm tỳ nữ không đáp ứng, Hồ Yêu Yêu tức giận khuôn mặt đỏ lên, tay ngọc hất lên."),i("br"),i("br"),n._v("Nàng là thiên chi kiêu nữ, thiên phú cao, người xinh đẹp, chịu vô số danh sư vây đỡ, luôn luôn tự ngạo, vốn cho rằng thu đối phương là dưới người, đã là nể tình, không nghĩ tới tên này như thế không biết tốt xấu."),i("br"),i("br"),n._v("Quả thực không biết điều."),i("br"),i("br"),n._v("Nói xong, thân thể mềm mại xoay một cái, bàn chân tại trong sảnh đạp mạnh, thẳng tắp hướng nơi xa bay vút đi."),i("br"),i("br"),n._v("Nàng tu vi đạt đến Hóa Phàm cửu trọng Tằm Phong cảnh, một thân lực lượng như là dải lụa, lan tràn ra, thân ảnh như điện, thời gian nháy mắt liền biến mất tại hai người trước mặt."),i("br"),i("br"),n._v("Yêu Nghiệt hội, là danh sư học viện học hội bên trong, tương đối lớn tồn tại, làm hội trưởng, học phần, linh thạch loại hình căn bản không thiếu."),i("br"),i("br"),n._v("Sở dĩ để Trương Huyền gia nhập các nàng, hoặc là làm nàng hạ nhân, là bởi vì cái tên này để bọn hắn lão sinh mất hết mặt mũi, nếu như có thể thành công, khẳng định danh tiếng vang xa, để vô số người hâm mộ."),i("br"),i("br"),n._v("Nhưng mà... Cái tên này không biết điều."),i("br"),i("br"),n._v("Đường đường Yêu Nghiệt hội hội trưởng, Hóa Phàm cửu trọng cường giả, học viện lớp năm học viên, trực tiếp cự tuyệt, liền không có nói chuyện."),i("br"),i("br"),n._v("Thấy cái tên này rời đi, Lạc Thất Thất nhìn qua: “Nàng là Yêu Nghiệt hội hội trưởng, lần này nghe nói vì sửa sang lại giáo công nhân tư liệu, hao tốn cực lớn đánh đổi, kết quả không thu hoạch được một hạt nào, khẳng định đối ngươi ôm có thành kiến... Mới vừa rồi là có ý định muốn nhục nhã lão sư, mong rằng lão sư không cần để ở trong lòng...”"),i("br"),i("br"),n._v("“Không có việc gì!”"),i("br"),i("br"),n._v("Lắc đầu, Trương Huyền cười cười: “Cái tên này đủ phách lối, như vậy đi, giao cho ta xử lý, ta sẽ nghĩ biện pháp để nàng ngoan ngoãn nói ra Ngô Dương Tử chỗ ở cũ vị trí!”"),i("br"),i("br"),n._v("“Giao cho ngươi xử lý?”"),i("br"),i("br"),n._v("Lạc Thất Thất cau mày: “Hồ Yêu Yêu cực kỳ kiêu ngạo, hơn nữa mưu trí đa dạng, muốn cho nàng nói ra, ta sợ rất khó...”"),i("br"),i("br"),n._v("Vị này Hồ Yêu Yêu, tính cách cực kỳ kiêu ngạo, hơn nữa, người cũng rất yêu tính, không ít người đi tìm nàng phiền phức, cũng bị thiệt lớn."),i("br"),i("br"),n._v("Lão sư muốn cho hắn ngoan ngoãn nói ra Ngô Dương Tử chỗ ở cũ chỗ trên mặt đất... Chỉ sợ rất khó đi!"),i("br"),i("br"),n._v("“Không có việc gì, ngươi về trước tinh anh khu chỗ ở của ta chờ lấy, một hồi ta liền trở lại!”"),i("br"),i("br"),n._v("Cười nhạt một tiếng, Trương Huyền bàn giao một câu, thân thể nhảy lên, Minh Lý Chi Nhãn vận chuyển, theo sát tại vị kia Hồ Yêu Yêu dấu vết lưu lại, đuổi theo."),i("br"),i("br"),n._v("Dù sao Yêu Nghiệt hội đã ghi hận hắn, lại thế nào làm, quan hệ cũng làm không cẩn thận, không bằng còn muốn những biện pháp khác, có lẽ sẽ có không giống kết quả."),i("br"),i("br"),n._v("“Lão sư...”"),i("br"),i("br"),n._v("Thấy Trương Huyền xoay người rời đi, Lạc Thất Thất sửng sốt một chút, vội vàng hô: “Yêu yêu học trưởng là Kinh Hồng viện... Các nàng am hiểu công kích linh hồn, ngươi phải cẩn thận!”"),i("br"),i("br"),n._v("Thanh âm hô lên, chỉ thấy Trương Huyền đã đi xa, đoán chừng căn bản không có nghe được."),i("br"),i("br"),n._v("Convert by: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-773"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-775"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);