(window.webpackJsonp=window.webpackJsonp||[]).push([[205],{556:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1161-tran-triet-chet"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1161-tran-triet-chet"}},[n._v("#")]),n._v(" Chương 1161: Trần Triết chết")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Là không phải cảm thấy, không nên đem vị này Trung Thanh Vương thả đi?”"),t("br"),t("br"),n._v("Nhìn hắn biểu lộ, Trương Huyền nhẹ nhàng cười một tiếng."),t("br"),t("br"),n._v("“Vâng!” Ngô sư gật đầu: “Danh sư không được nhục, đây không phải bồi thường sự tình, mà là sư đạo tôn nghiêm, tiền lệ vừa mở, ta sợ về sau, tất cả mọi người dám sỉ nhục danh sư...”"),t("br"),t("br"),n._v("Mặc dù hắn không đồng ý đối phương cách làm, nhưng trải qua Khưu Ngô cung, hắn biết vị này tuổi không lớn lắm Trương sư, sức phán đoán kinh người, làm như vậy, tất nhiên có mục đích của mình, bởi vậy, cũng không có chen vào nói, giờ phút này cũng nhịn không được nữa."),t("br"),t("br"),n._v("“Ta sẽ giải thích, có điều, trước đó, ta muốn hỏi Ngô sư một câu... Trời nhận danh sư sự tình, ngươi có thể hay không cùng cái khác danh sư nói thẳng ra?” Trương Huyền nói."),t("br"),t("br"),n._v("“Trời nhận danh sư, nói năng thận trọng, ta tuyệt đối sẽ không cùng người nhấc lên, cho dù cấp bậc cao Danh Sư đường, cũng sẽ không nói...” Ngô sư thần sắc cứng lại, vội nói."),t("br"),t("br"),n._v("Hắn biết trước mắt vị này là trời nhận danh sư."),t("br"),t("br"),n._v("Bất quá, chuyện này, không dám nói ra, cũng không thể nói ra, nếu không, tất nhiên sẽ gây nên toàn bộ Dị Linh tộc nhân dốc toàn bộ lực lượng, sinh linh đồ thán không nói, càng có khả năng, dẫn đến vị danh sư này, trước thời hạn chết yểu."),t("br"),t("br"),n._v("Nếu thực như thế, hắn liền là lịch sử tội nhân."),t("br"),t("br"),n._v("“Nhưng mà... Điền phó đường chủ nói!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Trước đó tại Trung Thanh Vương phủ thẩm vấn hắn thời điểm, đối phương từng nói: Trời nhận danh sư đều xuất hiện, Độc điện tổ sư còn sống cũng không tính là gì..."),t("br"),t("br"),n._v("Lúc đó liền cảm thấy không đúng."),t("br"),t("br"),n._v("Chỉ cần là danh sư, đều biết bốn chữ này đại biểu cái gì, đối phương lần thứ nhất nhìn thấy bản thân, ở trước mặt người ngoài, nói như vậy... Hiển nhiên vi phạm với danh sư cái kia có lễ nghi."),t("br"),t("br"),n._v("Cũng là bởi vì như vậy, hắn mới không hề cố kỵ, đánh đối phương dục tiên dục tử."),t("br"),t("br"),n._v("“Hắn... Biết ngươi là trời nhận danh sư?” Con ngươi co rụt lại, Ngô sư sầm mặt lại."),t("br"),t("br"),n._v("“Xem ra, cũng không hiểu rõ tình hình, hẳn là chỉ là thăm dò!” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Đối phương giọng nói, hẳn còn chưa biết, có điều, cố ý nói như vậy, đoán chừng là có chút hoài nghi, có điều, tâm hắn cảnh mạnh mẽ, sắc mặt như thường, cũng không có khẩn trương chút nào, đối phương sẽ không có nhìn ra cái gì."),t("br"),t("br"),n._v("“Vậy là tốt rồi, có điều, Trương sư ngươi phải cẩn thận một chút, về sau đi nơi nào, mang theo ta hoặc là Hình đường chủ...” Ngô sư vẻ mặt nghiêm túc."),t("br"),t("br"),n._v("Tin tức không có rò rỉ, vị này Trương sư, bất quá là cái siêu cấp thiên tài thôi, loại người này mặc dù ít, nhưng cũng tồn tại."),t("br"),t("br"),n._v("Có thể... Một khi để người ta biết trời nhận danh sư tin tức, vậy liền nguy rồi."),t("br"),t("br"),n._v("“Còn không cần, hiện nay sẽ không có chuyện gì, thật muốn làm như vậy, ngược lại càng khiến người ta hoài nghi, chuyện không chắc chắn, cũng liền xác định được...” Trương Huyền lắc đầu cự tuyệt."),t("br"),t("br"),n._v("Hắn tất cả thủ đoạn đều thi triển đi ra, so hai người này bảo hộ phải mạnh mẽ hơn nhiều, có bọn họ trước mặt lời nói, ngược lại không tiện thi triển, càng không an toàn."),t("br"),t("br"),n._v("“Cái này...” Ngô sư chần chừ."),t("br"),t("br"),n._v("Cũng đúng, thật muốn hắn cùng Hình đường chủ ngày đêm không rời đi theo, người khác không nghi ngờ cũng muốn hoài nghi, ngược lại rõ ràng hơn."),t("br"),t("br"),n._v("“Điền phó đường chủ có vấn đề, Trung Thanh Vương lại cấu kết Độc điện, luyện chế độc dược, vấn đề lớn hơn... Vị hoàng đế bệ hạ này, tự mình tới, tiêu tốn như thế đại đánh đổi đảm bảo... Nếu như nói, chỉ là bởi vì cứu mạng của hắn, ta không tin!”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Ngô sư bắt đầu sững sờ."),t("br"),t("br"),n._v("Hoàng thất vô tình, vì quyền lợi, thân huynh đệ đều có thể giết chết, hiện tại đường đường bệ hạ, vì một cái đại thần, bốc lên đắc tội Danh Sư đường nguy hiểm... Không có vấn đề mới là lạ!"),t("br"),t("br"),n._v("“Đã có vấn đề, cùng hắn giấu ở nơi này, không tốt thẩm vấn, còn không bằng đem hắn thả đi, tương đương với đem dây thừng nút chết mở ra, đằng sau cũng liền dễ làm...” Trương Huyền mỉm cười."),t("br"),t("br"),n._v("Đem Trung Thanh Vương bắt lấy, chẳng khác nào đem dây thừng chụp thành nút chết, để hắn mang đi... Có lẽ sẽ đưa đến không tưởng tượng được hiệu quả."),t("br"),t("br"),n._v("“Không sai, cái này đích xác là biện pháp tốt...”"),t("br"),t("br"),n._v("Ngô sư bừng tỉnh hiểu ra, trong lòng bội phục không thôi."),t("br"),t("br"),n._v("Không hổ là Trương sư, quả nhiên nhìn vấn đề càng thêm thông suốt."),t("br"),t("br"),n._v("Thẩm vấn, đối phương khăng khăng không có nói, liền xem như Danh Sư đường cũng hết cách rồi, nhưng... Buông ra đầu dây, để hắn lộ ra chân tướng, liền dễ dàng hơn nhiều."),t("br"),t("br"),n._v("“Ừm, tốt rồi, chuyện còn lại, giao cho ngươi. Đến tối còn có chút thời gian, nếu như thuận tiện, ta muốn đi Danh Sư đường Tàng Thư khố nhìn một chút...”"),t("br"),t("br"),n._v("Chuyện có phương pháp, còn lại Ngô sư toàn quyền xử lý liền tốt, hắn tham dự không tham dự đều không quan trọng."),t("br"),t("br"),n._v("Hiện tại trọng yếu nhất chính là, mau chóng ngưng tụ Thánh vực tam trọng Thiên Đạo công pháp, đem thực lực tăng lên đi lên, tốt vượt qua vị kia Trương Cửu Tiêu, thuận lợi đi vào Thánh tử điện."),t("br"),t("br"),n._v("“Ta mang ngươi tới...”"),t("br"),t("br"),n._v("Biết vị này Trương sư thích xem sách, Ngô sư cười cười, mới vừa đứng dậy, liền nghe đến ngoài cửa vang lên tiếng gõ cửa dồn dập."),t("br"),t("br"),n._v("“Tiến!”"),t("br"),t("br"),n._v("Lông mày nhíu một cái, ngay sau đó liền thấy trước đó đi ra vị danh sư kia, mặt mũi lo lắng."),t("br"),t("br"),n._v("“Làm sao vậy?”"),t("br"),t("br"),n._v("Có thể để cho đường đường lục tinh danh sư thái độ như thế, biết chắc phát sinh việc lớn, Ngô sư mở miệng hỏi."),t("br"),t("br"),n._v("“Lão sư... Không xong, Trần Triết... Hắn chết!” Vị danh sư này nuốt ngụm nước bọt, nói."),t("br"),t("br"),n._v("“Trần Triết chết rồi?”"),t("br"),t("br"),n._v("Con ngươi co rụt lại, Ngô sư đột nhiên đứng dậy."),t("br"),t("br"),n._v("Vị này Trần Triết, là nhúng tay vào điểm mấu chốt, đúng là hắn, đi Hồng Viễn đế quốc, lấy đi Chương Dẫn Khâu tư liệu, cuối cùng đưa đến tin tức rò rỉ... Một mực tạm giam tại Danh Sư đường, liền tu vi đều bị giam cầm, làm sao sẽ đột nhiên chết?"),t("br"),t("br"),n._v("“Cụ thể nguyên nhân cái chết ta cũng không biết, trước đó ta đi xem một lần, còn rất tốt... Vừa rồi thả đi Trung Thanh Vương, nhìn một cái, liền phát hiện đã cắt đứt hô hấp...”"),t("br"),t("br"),n._v("Vị danh sư này mặt mũi khẩn trương."),t("br"),t("br"),n._v("Trần Triết, Điền Thanh phó đường chủ học sinh, Thất Tinh sơ kỳ danh sư... Bất kể cái nào thân phận, đột nhiên chết đi, đều ảnh hưởng cực lớn."),t("br"),t("br"),n._v("“Đi qua nhìn một chút!”"),t("br"),n._v(" Vẻ mặt nghiêm túc, Ngô sư đi một bước, nhìn về phía cách đó không xa Trương Huyền: “Trương sư... Có thể hay không cùng ta cùng đi?”"),t("br"),t("br"),n._v("Trước mắt vị này, ánh mắt so với hắn muốn sắc bén nhiều lắm, cùng đi, có lẽ có thể nhìn ra vấn đề gì."),t("br"),t("br"),n._v("“Tốt!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Hắn cũng rất là khó hiểu."),t("br"),t("br"),n._v("Dựa theo đạo lý, Danh Sư đường tạm giam, đừng nói giết người, tự sát đều không được, nhưng bây giờ lại thực sự chết rồi... Quỷ dị không nói lên lời."),t("br"),t("br"),n._v("Đi theo lục tinh danh sư sau lưng, đi một hồi, đi tới một cái căn phòng đơn độc, quả nhiên thấy một người trung niên nằm trên mặt đất, lạnh cả người, đã cắt đứt hô hấp."),t("br"),t("br"),n._v("Không đi đi qua, Trương Huyền mà là nhìn chung quanh."),t("br"),t("br"),n._v("Gian phòng bên trong sắp đặt có nghiêm mật trận pháp, tin tức truyền lại không đi ra, thần thức đều không thể lan tràn, chỉ cần đi vào trong đó, là không có cách nào cùng liên lạc với bên ngoài."),t("br"),t("br"),n._v("Đi tới thi thể trước mặt, Minh Lý Chi Nhãn lóe lên, nhìn kỹ đi qua."),t("br"),t("br"),n._v("“Thân thể không có chút nào tổn hại, mà... Linh hồn hoàn toàn biến mất!”"),t("br"),t("br"),n._v("Con ngươi co rụt lại."),t("br"),t("br"),n._v("Hắn đã nhìn ra đối phương nguyên nhân cái chết... Hồn phi phách tán!"),t("br"),t("br"),n._v("“Linh hồn biến mất?” Sửng sốt một chút, Ngô sư cũng vội vàng nhìn lại, một lát sau, khuôn mặt tái mét."),t("br"),t("br"),n._v("Rất hiển nhiên, tại nhắc nhở bên dưới, cũng nhìn ra."),t("br"),t("br"),n._v("“Chẳng lẽ là... Cái loại này câu hồn phương pháp?”"),t("br"),t("br"),n._v("Lông mày nâng lên, một việc tại trong óc xông ra."),t("br"),t("br"),n._v("Trước đó, vị kia Tề Bắc song hùng một trong Tàng Hư, liền định dùng câu hồn phương thức, đem bản thân gạt bỏ, chỉ tiếc bị nhà tại chỗ giẫm chết, không thành công."),t("br"),t("br"),n._v("Cái loại này phương pháp, thông qua sưu hồn, hắn nhìn kỹ, cùng vu hồn sư thủ đoạn có chút tương tự, đem người linh hồn bắt đi qua, tuỳ tiện giết chết..."),t("br"),t("br"),n._v("Một khi thành công, thân thể cũng sẽ hoàn toàn tử vong, không lưu mảy may dấu vết."),t("br"),t("br"),n._v("Chẳng lẽ... Cái tên này liền là bị loại phương pháp này giết chết?"),t("br"),t("br"),n._v("“Chẳng lẽ... Ngoan Nhân, loại trừ đem phương pháp này, truyền thụ cho Tàng Hư, còn truyền thụ cho người khác, đem Trần Triết giết chết?” Trương Huyền tràn đầy nghi ngờ, có chút nghĩ không thông."),t("br"),t("br"),n._v("Vị này Trần Triết, cùng Ngoan Nhân thoạt nhìn, hoàn toàn không treo câu, vì cái gì có thể nối liền cùng nhau?"),t("br"),t("br"),n._v("“Đem thi thể đưa đến Chiến sư đường Thu điện chủ chỗ đó, để hắn giúp một tay nhìn một chút, đến cùng chuyện gì xảy ra!”"),t("br"),t("br"),n._v("Nghiên cứu một hồi, không rõ ràng cho lắm, Ngô sư đứng dậy, nói."),t("br"),t("br"),n._v("“Vâng!” Vị danh sư kia gật đầu."),t("br"),t("br"),n._v("Thu điện chủ là Chiến sư đường Hồn điện điện chủ, đối linh hồn nghiên cứu cực sâu, Ngô sư nhìn không ra, đối phương tất nhiên có thể phát giác cái gì."),t("br"),t("br"),n._v("“Không cần!”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Cỗ thi thể này, hồn phách đều không thấy, tìm Thu điện chủ cũng vô dụng."),t("br"),t("br"),n._v("“Trương sư...”"),t("br"),t("br"),n._v("Ngô sư tràn đầy cuống cuồng: “Vị này Trần Triết tử vong, mất đi chứng cứ, Điền phó đường chủ, cho dù ra tay với ngươi, cũng là lấy thất tinh danh sư thân phận, dạy bảo vãn bối, nhiều nhất là nhận lấy tiểu nhân che đậy... Thật muốn như vậy, ta liền lại không có tư cách giam giữ!”"),t("br"),t("br"),n._v("Điền phó đường chủ, bất kể nói thế nào đều là Thất Tinh đỉnh phong cấp bậc danh sư, bắt đối phương đệ tử, có chứng cứ cho thấy, tin tức rò rỉ cùng hắn có quan hệ lời nói, có thể giam giữ, hiện tại chứng nhân chết rồi... Liền không có lý do."),t("br"),t("br"),n._v("Mưu hại Trương Huyền... Có thể trốn tránh cho Trung Thanh Vương, còn lại điểm tội danh, nhờ vào thân phận hoàn toàn không cần quan tâm!"),t("br"),t("br"),n._v("“Không có tư cách giam giữ, vậy liền thả!”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Nếu không phải thông qua Thiên Đạo thư viện, biết vị này Điền Thanh phó đường chủ, nguyên thần có thiếu hụt, cũng hoài nghi có phải hay không cái tên này đem học sinh của mình giết chết."),t("br"),t("br"),n._v("“Thả?” Ngô sư sửng sốt: “Cái kia Trương sư, chịu đến khuất nhục...”"),t("br"),t("br"),n._v("“Ta khuất nhục không tính là gì, thân phận đối phương đặc thù, cũng không đủ chứng cứ, một mực giam giữ, đối Danh Sư đường ảnh hưởng cực lớn, ngươi vừa mới trở thành phó đường chủ, cũng sẽ đứng ngồi bất ổn, lời như vậy, sự tình phía sau đối với chúng ta càng thêm không có lợi!”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu, hắn chịu gập loại hình không tính là gì, một khi Ngô sư bên này bị người khiêu động, liền thật bị động."),t("br"),t("br"),n._v("“Tốt a!”"),t("br"),t("br"),n._v("Biết đối phương nói có đạo lý, Ngô sư đành phải nhẹ gật đầu, lần nữa nhìn qua: “Vậy chúng ta... Phía dưới nên làm cái gì?”"),t("br"),t("br"),n._v("“Ngươi cảm thấy phải làm gì?”"),t("br"),t("br"),n._v("Trương Huyền nhẹ nhàng cười một tiếng."),t("br"),t("br"),n._v("“Ta cảm thấy, hẳn là đem Trần Triết tội danh công bố tại thế, làm cho tất cả mọi người hoài nghi Điền phó đường chủ, lời như vậy, đối phương làm chuyện gì, đều rất bị động, chúng ta cũng liền dễ làm!” Trầm tư một chút, Ngô sư nói."),t("br"),t("br"),n._v("Trần Triết rò rỉ Khưu Ngô cung tin tức sự tình, hiện nay rất ít người biết, chỉ có đem tội danh xác định đến, mới có thể để cho Điền phó đường chủ bị động."),t("br"),t("br"),n._v("Nếu không, người đã chết, hắn trong sạch, trước đó tất cả sự tình, đều là toi công bận rộn."),t("br"),t("br"),n._v("“Sai! Như vậy, không được!” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("“Không được? Cái kia hẳn là làm sao bây giờ?” Ngô sư sững sờ."),t("br"),t("br"),n._v("“Rất đơn giản, đem Trần Triết dựa theo danh sư lễ tiết hậu táng, hơn nữa dốc sức ngợi khen người nhà của hắn, cũng cấp cho khen ngợi! Tán thưởng hắn là chân chính danh sư... Hiệu triệu tất cả mọi người hướng hắn học tập.”"),t("br"),t("br"),n._v("Trương Huyền nở nụ cười."),t("br"),t("br"),n._v("“Cái này... Tính là gì phương pháp?” Ngô sư ngốc tại chỗ, tràn đầy không hiểu."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1169"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1171"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);