(window.webpackJsonp=window.webpackJsonp||[]).push([[2050],{2405:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-768-kinh-hong-vien"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-768-kinh-hong-vien"}},[n._v("#")]),n._v(" Chương 768: Kinh Hồng viện")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 768: Kinh Hồng viện"),t("br"),t("br"),n._v("“Nghe đạo có tuần tự, thuật nghiệp có chuyên về một môn, người thành đạt làm thầy, điểm ấy đối tại chúng ta danh sư tới nói, không tính là gì! Hắn cùng Lạc Thất Thất cùng một chỗ, bình tĩnh, không có chút nào gấp rút, có thể thấy được lão sư xưng hô danh xứng với thực, cũng không phải là làm bộ, khẳng định là có nào đó loại năng lực, có thể chỉ điểm đối phương.”"),t("br"),t("br"),n._v("Hồ Yêu Yêu nói."),t("br"),t("br"),n._v("Thân là danh sư, tự nhiên rõ ràng, người không thể là toàn tài, lợi hại hơn nữa người tu luyện, cũng có không bạc nhược khâu."),t("br"),t("br"),n._v("Thực lực mạnh, bái thực lực yếu làm thầy, cũng có khối người."),t("br"),t("br"),n._v("Ngay cả lúc trước thiên nhân Khổng sư, cũng không chuyên môn hỏi qua Đàm sư ư?"),t("br"),t("br"),n._v("Lúc đó Đàm sư, không có danh tiếng gì, bất quá là cái an phận tại  một vị ngũ tinh danh sư mà thôi."),t("br"),t("br"),n._v("Mọi người cũng rõ ràng điểm ấy, đồng thời gật đầu, không còn xoắn xuýt vấn đề này, Đổng Hân hỏi: “Ngươi đã gặp qua, người này tâm tính như thế nào? Có thể hay không có khuyết điểm gì có thể lợi dụng?”"),t("br"),t("br"),n._v("“Ta chỉ gặp mặt một lần, hàn huyên vài câu, người này thoạt nhìn so sánh chất phác, đơn thuần, không giống có tâm kế bộ dạng, ý rất nghiêm, làm việc cũng rất có chuẩn tắc! Chính là... Nói chuyện so sánh làm người tức giận!” Hồ Yêu Yêu nói."),t("br"),t("br"),n._v("Mặc dù nàng là lục tinh hạ phẩm danh sư, nhãn lực kinh người, nhưng vị này Trương Huyền, nhưng trên cơ bản không nhìn ra cái gì, ngược lại bị đối phương tức giận gần chết."),t("br"),t("br"),n._v("“Nói chuyện làm người tức giận?”"),t("br"),t("br"),n._v("ngantruyen.com"),t("br"),n._v("Mọi người nháy con mắt."),t("br"),t("br"),n._v("Có thể để cho chung quanh yêu nghiệt đồng dạng Hồ Yêu Yêu tức giận, cái tên này cũng quả thực là một nhân tài."),t("br"),t("br"),n._v("“Ừm, đúng, cái tên này hình như đối Ngô Dương Tử tiền bối chỗ ở cũ vị trí so sánh cảm thấy hứng thú, muốn muốn giáo huấn hắn, ta cảm thấy có thể dùng cái này làm đột phá khẩu!” Suy nghĩ một chút, Hồ Yêu Yêu nói tiếp."),t("br"),t("br"),n._v("Mặc dù trong mắt người ngoài, tứ đại học hội cạnh tranh không ngừng, kỳ thật mấy người bọn họ cùng là danh sư, quan hệ cũng cũng không tệ lắm."),t("br"),t("br"),n._v("“Ngô Dương Tử chỗ ở cũ? Có cảm thấy hứng thú là tốt rồi, ta nhìn không bằng như vậy, chúng ta cho hắn cái giả địa chỉ, sau đó bố trí xuống Thiên La Địa Võng, thật tốt giáo huấn một lần! Cho hắn biết, cái gì là quy củ!” Tiết Chân Dương con mắt tỏa ánh sáng."),t("br"),t("br"),n._v("Hắn tu luyện Chân Dương công pháp, lực lượng cương mãnh, làm việc cũng so sánh quả quyết, có thể xuất thủ, tuyệt đối không nét bút."),t("br"),t("br"),n._v("“Như vậy không ổn, vẫn là câu nói kia, chúng ta ngang nhiên ra tay, bị Mi viện trưởng bọn họ biết, tất nhiên trách tội! Nhất định phải nghĩ cái để hắn ăn thiệt thòi lại không dám nói, tốt nhất có nhược điểm gì lưu trong tay!”"),t("br"),t("br"),n._v("Long Thương Nguyệt nói."),t("br"),t("br"),n._v("Hắn cùng Tiết Chân Dương khác biệt, làm việc càng thêm quỷ quyệt, âm hiểm."),t("br"),t("br"),n._v("Vị này Trương Huyền nếu là Mi viện trưởng, Triệu viện trưởng thượng khách, ra tay đối phó liền phải cẩn thận, nếu không, coi như không cáo trạng, có người thông báo một tiếng, bọn họ cũng đem đối mặt phiền phức."),t("br"),t("br"),n._v("Trừ phi... Bắt đến đối phương nhược điểm gì, để hắn ăn thiệt thòi lại không dám lời."),t("br"),t("br"),n._v("“Ăn thiệt thòi lại không dám nói? Như thế biện pháp tốt, Yêu Yêu, chuyện này ngươi tương đối am hiểu đi, ta nhớ được không ít người trong tay ngươi thua thiệt qua, trở về lại ngay cả lời cũng không dám nói?”"),t("br"),t("br"),n._v("Nghĩ đến cái gì, ánh mắt sáng lên, Đổng Hân nói."),t("br"),t("br"),n._v("Hồ Yêu Yêu yêu nghiệt tên tại học viện đây chính là rất vang dội, không chỉ tu luyện, thiên phú yêu nghiệt, chủ yếu nhất là, làm việc bất an lẽ thường ra bài, trước kia rất nhiều tìm phiền toái danh sư, cũng bị xảo diệu hóa giải, sau đó mạnh mẽ làm nhục."),t("br"),t("br"),n._v("“Ừm, các ngươi vừa nói như vậy, ta cũng nghĩ đến một cái biện pháp, cái tên này không là ưa thích lấy lòng ư? Ta sẽ để hắn danh dự sạch không, để hắn biết đắc tội ta Hồ Yêu Yêu đánh đổi!”"),t("br"),t("br"),n._v("Hồ Yêu Yêu đen nhánh hai mắt lóe lên, nhẹ nhàng cười một tiếng, lộ ra một cái làm lòng người rét lạnh nụ cười: “Còn có Lạc Thất Thất, đối nàng vị lão sư này bảo vệ rất nhanh... Nếu như cho nàng biết, vị lão sư này, hạ lưu vô sỉ, làm người không chịu nổi... Cũng không biết vẫn sẽ hay không như thế tôn trọng!”"),t("br"),t("br"),n._v("Biết Hồ Yêu Yêu không đánh không nắm chắc trận, mọi người tất cả đều ánh mắt sáng lên."),t("br"),t("br"),n._v("“Chuyện này giao cho ta xử lý tốt, chờ ta xử lý vị này Trương Huyền, thay các ngươi trút giận, nhận được nắm đồ vật về sau, ta muốn thêm một thành, không tính là quá mức chứ?”"),t("br"),t("br"),n._v("Hồ Yêu Yêu nói."),t("br"),t("br"),n._v("“Muốn thêm một thành?”"),t("br"),t("br"),n._v("“Không có vấn đề, chỉ cần ngươi có thể đối phó được cái tên này, một thành không tính là gì!”"),t("br"),t("br"),n._v("Mọi người gật đầu."),t("br"),t("br"),n._v("“Vậy là tốt rồi... Chúng ta việc cấp bách, hay là chuẩn bị Nhất Nguyên Trọng Thủy, chuẩn bị chỉnh tề liền xuất phát. Còn cái này Trương Huyền, các ngươi liền nghe tin tức tốt của ta đi! Chỉ cần hắn còn muốn biết Ngô Dương Tử chỗ ở cũ chỗ trên mặt đất, liền muốn ngoan ngoãn nghe lời của ta, chịu ta định đoạt...”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Hồ Yêu Yêu thân ảnh hóa thành một cái uyển chuyển độ cong, môi đỏ vung lên."),t("br"),t("br"),n._v("“Chịu nàng định đoạt? Cái tên này suy nghĩ biện pháp gì?”"),t("br"),t("br"),n._v("Một bên Trương Huyền, không rõ ràng cho lắm."),t("br"),t("br"),n._v("Có thể để cho cái này nha đầu này tự tin như vậy, khẳng định có niềm tin rất lớn."),t("br"),t("br"),n._v("Nghĩ một lát nghĩ không ra, đối phương cũng không tỉ mỉ nói, Trương Huyền đành phải lắc đầu."),t("br"),t("br"),n._v("Đối phương muốn thực tình muốn tìm hắn để gây sự, cũng không có cách, chỉ có thể binh đến tướng chắn, nước đến đất chặn."),t("br"),t("br"),n._v("Dù sao sớm biết tính toán của đối phương, cũng có thể sớm chuẩn bị, không đến mức bị động."),t("br"),t("br"),n._v("Hồ Yêu Yêu đám người thương nghị xong xuôi, từng người xoay người rời đi, hướng dưới núi bay vút đi."),t("br"),t("br"),n._v("Thấy mọi người rời đi, Trương Huyền lúc này mới đứng dậy."),t("br"),t("br"),n._v("“Trước mặc kệ, vẫn là đi hỏi thăm Ngô Dương Tử chỗ ở cũ vị trí đi!”"),t("br"),t("br"),n._v("Chần chờ một chút, Minh Lý Chi Nhãn lấp lánh, dọc theo Hồ Yêu Yêu biến mất phương hướng thẳng tắp đuổi tới."),t("br"),t("br"),n._v("Đối phương tìm bảo vật gì sự tình, cùng hắn không bất kỳ quan hệ gì, cũng không thèm để ý, còn Nhất Nguyên Trọng Thủy, biết giá cả, về sau nghĩ biện pháp kiếm tiền mua sắm là được."),t("br"),t("br"),n._v("Hiện tại việc cấp bách tiếp tục dò xét Ngô Dương Tử chỗ ở cũ chỗ trên mặt đất."),t("br"),t("br"),n._v("Chỉ cần tìm được, có lẽ liền có thể nhận được vị này một đời luyện khí đại sư lưu lại bảo tàng, tu luyện về sau liền không cần tiếp tục buồn."),t("br"),t("br"),n._v("Dọc theo dấu vết truy tìm, tốc độ cực nhanh, không lâu sau, đi tới một cái rộng lớn sân nhỏ."),t("br"),n._v("“Thật nhiều nữ tử...”"),t("br"),t("br"),n._v("Dọc theo sân nhỏ thông đạo chậm rãi hướng về phía trước, rất nhanh phát hiện không đúng."),t("br"),t("br"),n._v("Nơi này nữ học sinh số lượng, rõ ràng muốn so nam học sinh nhiều hơn nhiều, hơn nữa, nguyên một đám tư thái ưu mỹ, dung mạo có chút không tầm thường."),t("br"),t("br"),n._v("Mặc dù cùng Hồ Yêu Yêu, Lạc Thất Thất đám người so, kém rất nhiều, nhưng cũng cũng cùng Nhiễm Tiếu Tiếu tương đương."),t("br"),t("br"),n._v("Danh sư bên trong, luôn luôn nam nhiều nữ ít, nơi này nhưng hầu như cũng là nữ tử, để hắn nhịn không được kỳ quái."),t("br"),t("br"),n._v("Nương theo người càng ngày càng nhiều, hành động cũng càng ngày càng bị hạn chế, tốc độ trở nên chậm không ít, may mắn có Minh Lý Chi Nhãn, có thể sớm làm ra dự phán, lại thêm Thiên Đạo chân khí thu liễm khí tức, Hồng Trần Đạp Thiên bộ phi hành, lúc này mới không bị phát hiện."),t("br"),t("br"),n._v("Sau nửa canh giờ."),t("br"),t("br"),n._v("Đi theo dấu vết đằng sau, đã đến đại viện lạc chỗ sâu."),t("br"),t("br"),n._v("“Tâm Như, ngươi giúp ta trông coi, ta chuẩn bị vài thứ, không thể bị quấy rầy!”"),t("br"),t("br"),n._v("Lại đi một hồi, phía trước một cái thanh âm thanh thúy vang lên, nhận ra là Hồ Yêu Yêu, vội vàng bay vút đi, rơi vào sân nhỏ một cái cao lớn cây cối bên trong."),t("br"),t("br"),n._v("Theo tiếng âm vang lên phương hướng nhìn lại, quả nhiên thấy Hồ Yêu Yêu đứng tại một cái phòng gần."),t("br"),t("br"),n._v("“Vâng!” Gọi là Tâm Như nữ tử, gật đầu lên tiếng."),t("br"),t("br"),n._v("Là cái hơn hai mươi tuổi nữ hài, tư thái cùng dung mạo, cũng là không yếu, được cho trong trăm có một mỹ nhân."),t("br"),t("br"),n._v("“Chẳng lẽ muốn chuẩn bị tìm ta phiền phức đồ vật?”"),t("br"),t("br"),n._v("Trước đó nghe qua đối thoại của bọn họ, biết cô gái này muốn tìm bản thân phiền phức, mới vừa dễ dàng nhìn xem rốt cục chuẩn bị vật gì, cũng tốt tương kế tựu kế."),t("br"),t("br"),n._v("Muốn đến nơi này, nhẹ nhàng cười một tiếng, thân ảnh khẽ động, chậm rãi bay về phía đối phương nóc phòng."),t("br"),t("br"),n._v("Hắn động tác nhẹ nhàng, tựa như một mảnh lông hồng, lặng yên không một tiếng động."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Rơi vào nóc phòng, vận chuyển chân khí lòng bàn tay, nhẹ nhàng đem trên phòng mảnh ngói để lộ, Minh Lý Chi Nhãn nhúc nhích, hướng phía dưới nhìn lại."),t("br"),t("br"),n._v("Ánh mắt rơi vào giữa phòng, quả nhiên thấy Hồ Yêu Yêu đi đến, trong phòng khảm nạm nhiều viên Dạ Minh Châu, ôn nhuận ánh sáng chiếu xuống, màu đỏ tươi tơ lụa quần áo, đem hoàn mỹ dáng người phác hoạ ra đến, để cho người ta sợ hãi thán phục."),t("br"),t("br"),n._v("Da thịt tuyết trắng, như thiên nga cái cổ, tinh xảo vành tai, mái tóc đen nhánh... Bất kể từ phương diện nào đi nữa nhìn, vị này Hồ Yêu Yêu cũng là yêu nghiệt trong yêu nghiệt, để cho người ta khó mà cự tuyệt."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Chính muốn nhìn một chút cái tên này, đến cùng lấy vật gì, muốn tìm bản thân phiền phức, chỉ thấy nữ hài đi tới gian phòng mềm sập gần, tay ngọc tại bên hông nhẹ nhàng kéo một phát."),t("br"),t("br"),n._v("Hệ ở trên người váy đỏ, lập tức trượt xuống, lộ ra tơ lụa đồng dạng trắng tinh làn da."),t("br"),t("br"),n._v("Từ trên nhìn xuống, không có quần áo che lấp, đối phương hoàn mỹ thân hình, lập tức hiện ra ở trước mặt."),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Trương Huyền không còn gì để nói."),t("br"),t("br"),n._v("Còn tưởng rằng cái tên này sẽ chuẩn bị bảo vật gì, hoặc là cái gì bẫy rập loại hình, sớm quan sát, cũng có thể tương kế tựu kế, thuận tiện bức hắn nói ra Ngô Dương Tử chỗ ở cũ vị trí."),t("br"),t("br"),n._v("Ai ngờ cái tên này về đến phòng liền cởi quần áo... Cũng quá không theo lẽ thường ra bài!"),t("br"),t("br"),n._v("Cái này trời mới đen không lâu, lại không đi ngủ, cởi cái gì quần áo... Thật là khó có thể lý giải được."),t("br"),t("br"),n._v("“Được rồi, phi lễ chớ nhìn!”"),t("br"),t("br"),n._v("Biết tiếp tục xem tiếp, cũng có chút tiểu nhân hành kính, đem mảnh ngói một lần nữa đắp lên, Trương Huyền tràn đầy buồn bực."),t("br"),t("br"),n._v("Nhà này chuyện gì, hắn là muốn tới đây dò xét Ngô Dương Tử chỗ ở cũ, kết quả biến thành kẻ nhìn trộm... Suy nghĩ một chút cũng là mất mặt."),t("br"),t("br"),n._v("May mắn không bị người phát hiện, nếu không, cái gọi là sư đạo tôn nghiêm, cái gọi là thanh danh, tại không cách nào nhấc lên nửa điểm."),t("br"),t("br"),n._v("Phía dưới không thể nhìn, nhân gia cởi quần áo, tự nhiên càng không thể xông đi vào ép hỏi, ngồi tại nóc phòng, Trương Huyền đành phải hướng nhìn bốn phía."),t("br"),t("br"),n._v("Hồ Yêu Yêu sân nhỏ, hẳn là chung quanh cao lớn nhất, rộng lớn, ngồi ở phía trên, bốn phía cảnh trí, cũng rơi vào trong mắt, quay đầu hướng cái khác sân nhỏ nhìn lại, lập tức nhìn thấy mấy nữ tử, thân mang lộng lẫy trang phục, ở bên trong nhảy lên."),t("br"),t("br"),n._v("Xinh đẹp quần áo, yêu diễm dáng múa, để cho người ta nhìn lên một cái, liền kìm lòng không được trầm mê trong đó, khó mà tự kềm chế."),t("br"),t("br"),n._v("Chân khí xoay một cái, Trương Huyền tỉnh táo lại, nhịn không được sững sờ."),t("br"),t("br"),n._v("“Chẳng lẽ... Đây là Kinh Hồng viện?”"),t("br"),t("br"),n._v("Nhờ vào dáng múa có thể để cho hắn thất thần, nói rõ tất nhiên có mê huyễn linh hồn năng lực, mà có loại năng lực này, toàn bộ học viện, chỉ có Kinh Hồng viện mới có."),t("br"),t("br"),n._v("Lại thêm dọc đường gặp rất nhiều tư thái không sai nữ danh sư, hơi cân nhắc liền có thể cân nhắc đi ra."),t("br"),t("br"),n._v("“Hồ Yêu Yêu đang tại cởi quần áo, tiếp tục đợi ở chỗ này, rất là xấu hổ... Đã đến Kinh Hồng viện, vừa vặn đi xem một chút Tàng Thư khố ở nơi nào, tìm chút thư nhìn, cũng không uổng công qua tới một lần!”"),t("br"),t("br"),n._v("Tiếp tục đợi ở chỗ này, khó tránh khỏi có tình ngay lý gian chi ngại, còn không bằng đi xem sách, cũng tốt nghĩ biện pháp giải quyết trong linh hồn Tiên Thiên thai độc."),t("br"),t("br"),n._v("Muốn đến nơi này, cũng không do dự, Minh Lý Chi Nhãn nhúc nhích, hướng nhìn bốn phía, rất nhanh một cái cao lớn kiến trúc xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Phía trên ba chữ to, rồng bay phượng múa, ngân câu thiết họa, để lộ ra một loại khả năng thưởng thức cái đẹp cùng lịch sử tang thương —— Tàng Thư các!"),t("br"),t("br"),n._v("“Đi qua nhìn một chút!”"),t("br"),t("br"),n._v("Con mắt tỏa ánh sáng, thân thể nhoáng một cái, tựa như một dãy khói xanh, Trương Huyền bay vút đi."),t("br"),t("br"),n._v("(Công chúng số phát Hồ Yêu Yêu ảnh chụp mọi người có thể chú ý nhìn xem. Wechat lục soát “Hoành Tảo Thiên Nhai” tăng thêm chú ý là được)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-776"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-778"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);