(window.webpackJsonp=window.webpackJsonp||[]).push([[2052],{2407:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-770-luc-tinh-kinh-hong-su"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-770-luc-tinh-kinh-hong-su"}},[n._v("#")]),n._v(" Chương 770: Lục tinh kinh hồng sư")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 770: Lục tinh kinh hồng sư"),t("br"),t("br"),n._v("Thừa dịp bóng đêm, phi hành loại hình người khác rất khó phát hiện, ban ngày liền không đồng dạng."),t("br"),t("br"),n._v("Không nắm chặt thời gian trở về lời nói, bị người khác phát hiện, muốn giải thích cũng giải thích không rõ."),t("br"),t("br"),n._v("Nghĩ đến cái này không chút do dự, Trương Huyền Vu hồn trôi nổi, dọc theo cầu thang hướng tầng thứ nhất bay lượn mà tới."),t("br"),t("br"),n._v("Trước đó hắn theo ở phía sau đi vào học viên, vẫn tại bên trong tìm sách, xem ra, mặc dù chơi đùa một đêm, lại không tìm tới muốn, mày nhíu lại thành mụn nhọt."),t("br"),t("br"),n._v("Không biết nàng muốn tìm cái gì, coi như muốn giúp cũng không giúp được một tay, Trương Huyền vắng mặt dừng lại, lúc này hướng gian phòng đại môn đi đến."),t("br"),t("br"),n._v("“Ừm?”"),t("br"),t("br"),n._v("Đi tới trước cửa, đẩy một cái, lại phát hiện, căn bản đẩy không ra."),t("br"),t("br"),n._v("Tàng Thư khố vì phòng ngừa người khác loạn nhập, chuyên môn thiết trí đặc thù cơ quan, trừ phi có thẻ học viên cùng học phần, nếu không, coi như Hóa Phàm cửu trọng thậm chí Bán Thánh thực lực, đều khó mà phá vỡ!"),t("br"),t("br"),n._v("Trước đó, có thể đi vào, là bởi vì cái này học viên dẫn đầu, hiện tại nàng không đi ra, cũng chỉ có thể bị nhốt trong đó, không cách nào rời đi."),t("br"),t("br"),n._v("“Này làm sao xử lý?”"),t("br"),t("br"),n._v("Trong lòng tràn đầy buồn bực."),t("br"),t("br"),n._v("Hiện tại ngày mới sáng lên, rời giường người không nhiều, lặng lẽ rời đi, ai cũng không phát hiện được, một khi tất cả mọi người đi lên, một người xa lạ chạy đến Kinh Hồng viện trọng yếu như vậy địa phương, muốn giải thích cũng giải thích không rõ ah!"),t("br"),t("br"),n._v("Có thể... Không mở cửa lời nói, đủ loại trận pháp cơ quan, coi như Vu hồn, cũng có thể chặn lại, không cách nào tránh né."),t("br"),t("br"),n._v("Quay đầu nhìn thoáng qua, phát hiện cái kia tìm sách học viên, căn bản không rời đi ý tứ, Trương Huyền lắc đầu, đành phải ngoan ngoãn chờ lấy."),t("br"),t("br"),n._v("Đối phương không ra, hoặc là có người đi vào, hắn liền không cách nào thoát thân, đây là chuyện không có cách nào khác."),t("br"),t("br"),n._v("“Được rồi, xoắn xuýt cũng vô dụng, vừa vặn thừa cơ nhìn xem kinh hồng sư thư tịch...”"),t("br"),t("br"),n._v("Đã ra không được, cũng không có gì có thể xoắn xuýt, lúc này tìm một chỗ yên tĩnh ngồi xuống, tâm thần đắm chìm trong trong tiệm sách, quan sát vừa rồi tạo ra kinh hồng sư bí tịch."),t("br"),t("br"),n._v("Một đến lục tinh cũng có, lít nha lít nhít ghi chép phương pháp tu luyện cùng luyện thành sau hiệu quả."),t("br"),t("br"),n._v("Dù sao nhàn rỗi cũng là nhàn rỗi, Trương Huyền tiện tay mở ra nhất tinh cấp bậc pháp quyết tu luyện, nghiêm túc nhìn sang."),t("br"),t("br"),n._v("Thiên Đạo thư viện tấn cấp, thư tịch chỉ cần mở ra, nội dung liền sẽ tự động chảy vào trong óc, trở thành kiến thức của hắn, do đó, lý giải lên nhanh hơn không ít."),t("br"),t("br"),n._v("“Muốn trở thành kinh hồng sư, đầu tiên hồn lực muốn mạnh, sau đó lấy gần như tại đạo dáng múa đem hắn diễn dịch đi ra... Vũ điệu mục đích, là để lực lượng linh hồn, phát huy đến cực hạn, phát huy ra lớn nhất hiệu quả...”"),t("br"),t("br"),n._v("Kinh hồng sư tri thức trong đầu chảy xuôi."),t("br"),t("br"),n._v("Không biết qua bao lâu, Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("Nhất tinh cấp bậc công pháp bí tịch, đã hoàn toàn thông hiểu đạo lí."),t("br"),t("br"),n._v("Nói cách khác, hắn hiện tại đi khảo hạch lời nói, có thể nhẹ nhõm thông qua nhất tinh kinh hồng sư khảo hạch."),t("br"),t("br"),n._v("Kinh hồng sư đối hồn lực yêu cầu cực mạnh, hắn tu luyện Vu hồn, hồn thể càng là vượt qua mười mét, hồn lực mạnh không thể nghi ngờ, người khác học tập cả đời mà không cách nào đi đến cảnh giới, đối với hắn mà nói, ăn cơm uống nước đồng dạng đơn giản."),t("br"),t("br"),n._v("“Tiếp tục!”"),t("br"),t("br"),n._v("Thấy cái kia học viên, vẫn không có rời đi ý tứ, biết đi không được, Trương Huyền tiếp tục hướng nhị tinh thư tịch nhìn sang."),t("br"),t("br"),n._v("“Võ giả có võ kỹ, kinh hồng sư cũng có vũ kỹ. Lời ít mà ý nhiều, lấy múa làm kỹ, lấy múa làm giết địch chi thuật. Khẽ múa ra, mà thiên địa lay động, khẽ múa ra, mà thần hồn diệt! Múa làm công kích linh hồn môi giới...”"),t("br"),t("br"),n._v("Nhìn thấy thư tịch đã nói lời nói, Trương Huyền nhịn không được gật đầu."),t("br"),t("br"),n._v("Kinh hồng sư vũ kỹ, cùng Vu hồn, sư ngôn thiên bẩm đồng dạng, là một loại hồn phách bên trên công kích, cần lấy vũ điệu làm môi giới, mới có thể phát huy lực lượng mạnh nhất năng lực đặc thù."),t("br"),t("br"),n._v("“... Nếu là môi giới, tu luyện tới chỗ cao thâm, có thể hay không cùng công kích linh hồn đồng dạng, vũ điệu cũng không cần?”"),t("br"),t("br"),n._v("Tiếp tục hướng xuống đọc qua, một cái nội dung đi vào tầm mắt."),t("br"),t("br"),n._v("“Không cần khiêu vũ? Chẳng lẽ... Là vô kiếm chi cảnh?”"),t("br"),t("br"),n._v("Trong lòng kích động."),t("br"),t("br"),n._v("Vũ kỹ hắn không hiểu, nhưng kiếm pháp lại hết sức am hiểu, căn cứ miêu tả, kiếm pháp tu luyện tới cảnh giới cao thâm, đem không trệ tại vật, cỏ cây trúc đá đều có thể làm kiếm, tiếp tục tinh tu lời nói, thậm chí còn có thể đi đến không có kiếm thắng có kiếm chi cảnh."),t("br"),t("br"),n._v("Nói cách khác, kiếm nhưng mà thi triển lực lượng môi giới mà thôi, không cần kiếm, đồng dạng có thể thi triển kiếm pháp, thậm chí càng thêm tự nhiên, mượt mà, mau lẹ."),t("br"),t("br"),n._v("Quyển bí tịch này bên trên ghi những này, rất rõ ràng, cùng cái khác kinh hồng sư phương thức tu luyện khác biệt, hướng cuối cùng đại đạo, không có kiếm thắng có kiếm, vô chiêu thắng hữu chiêu tiến lên."),t("br"),t("br"),n._v("Tiếp tục xem đi."),t("br"),t("br"),n._v("“... Đã lấy động tác phát huy công kích linh hồn lực, nói như vậy là động tác, đi đường là động tác, giơ tay nhấc chân là động tác, hết thảy trong lúc lơ đãng cử chỉ cũng là động tác, hẳn là phù hợp đại đạo, đều thuộc về vũ điệu...”"),t("br"),t("br"),n._v("“Lấy chân chính tự nhiên, thay thế tận lực vũ điệu, mới thật sự là kinh hồng sư...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Đem sách lật một lần."),t("br"),t("br"),n._v("Trương Huyền nhắm chặt hai mắt, lý giải trong đó tri thức."),t("br"),t("br"),n._v("Kẽo kẹt! Kẽo kẹt!"),t("br"),t("br"),n._v("Linh hồn một hồi giòn vang, phảng phất cả người đi vào cảnh giới hoàn toàn mới."),t("br"),t("br"),n._v("Nhị tinh kinh hồng sư, đến!"),t("br"),t("br"),n._v("“Tiếp tục!”"),t("br"),t("br"),n._v("Liên tục học tập, để hắn như uống cam nhưỡng, càng ngày càng hưng phấn hắn, tiếp tục hướng còn lại mấy quyển nhìn lại."),t("br"),t("br"),n._v("Sau hai canh giờ."),t("br"),t("br"),n._v("Tất cả bí tịch tất cả đều nhìn một lần, hoàn toàn lý giải, dung hội quán thông."),t("br"),t("br"),n._v("To lớn hồn thể, giống như là nhận được rèn luyện, càng thêm tinh thuần thấu triệt."),t("br"),t("br"),n._v("Kinh hồng sư, lấy vũ điệu dẫn động linh hồn, tiến hành công kích, mê huyễn, quyến luyến, chém giết... Có được không hiểu uy lực, Trương Huyền mặc dù không học qua vũ kỹ, nhưng đem bên trong cao thâm nhất, nhắm thẳng vào đại đạo năng lực học được, hắn hiện tại, đối cái nghề nghiệp này lý giải, thất tinh xuống, có thể nói không người có thể đưa ra phải."),t("br"),t("br"),n._v("Nói cách khác, hắn hiện tại, chỉ cần khảo hạch, tuyệt đối có thể đi đến lục tinh đỉnh phong."),t("br"),t("br"),n._v("“Thật nhanh!”"),t("br"),t("br"),n._v("Thư viện sau khi tấn cấp, học tập tốc độ cũng thay đổi nhanh"),t("br"),t("br"),n._v("Trước kia, loại này đặc thù chức nghiệp, học tập nhất tinh, không sai biệt lắm một canh giờ, hiện tại giảm bớt đến nửa cái."),t("br"),t("br"),n._v("Từ đối kinh hồng sư một chữ cũng không biết, đến thất tinh phía dưới vô địch, cũng chỉ dùng ba canh giờ mà thôi."),t("br"),n._v("“Người kia đâu?”"),t("br"),t("br"),n._v("Đứng dậy, Trương Huyền hướng về hai bên phải trái nhìn lại, xem xét phía dưới nhịn không được sững sờ."),t("br"),t("br"),n._v("Lớn như vậy Tàng Thư khố mênh mông không người, đâu còn có nửa cái bóng người."),t("br"),t("br"),n._v("“Ta đi... Người lúc nào không thấy?” Trừng to mắt, Trương Huyền suýt chút nữa không đã hôn mê."),t("br"),t("br"),n._v("Vừa rồi tu luyện quá mức tập trung tinh thần, không nghĩ tới dẫn hắn đi vào cái kia học viên, đã rời đi."),t("br"),t("br"),n._v("Tàng Thư các, đi vào cần tiêu hao học phần, bình thường cũng không có nhiều người, từ hôm qua ban đêm đến bây giờ, cũng chỉ có cái này một người học viên... Nàng không thấy, bản thân làm sao ra ngoài?"),t("br"),t("br"),n._v("Sẽ không bị đóng lại vài ngày chứ?"),t("br"),t("br"),n._v("Trương Huyền tràn đầy buồn bực."),t("br"),t("br"),n._v("Cái này gọi chuyện gì, sớm biết liền không tu luyện như thế đầu nhập vào."),t("br"),t("br"),n._v("“Một mực bị giam trong này không có gì, thân thể còn ở bên ngoài, một khi bị phát hiện, phiền phức liền lớn...”"),t("br"),t("br"),n._v("Hắn là hồn thể, nhốt ở trong phòng, không ăn không uống mấy ngày cũng không tính là cái gì, mấu chốt là thân thể, còn giấu ở phía ngoài trên đại thụ."),t("br"),t("br"),n._v("Mặc dù nấp rất kỹ, ban đêm rất khó phát hiện, nhưng ban ngày liền không nói được rồi."),t("br"),t("br"),n._v("Bị phát hiện, thật sự phiền toái."),t("br"),t("br"),n._v("“Nhìn có hay không đi ra biện pháp...”"),t("br"),t("br"),n._v("Biết hiện tại đã không sai biệt lắm đến trưa, Trương Huyền rốt cuộc đợi không được, vội vàng đi tới đại môn gần, nhìn xem có thể hay không tìm khe hở, hoặc là những phương pháp khác rời đi."),t("br"),t("br"),n._v("Dạo qua một vòng, tràn đầy bất đắc dĩ."),t("br"),t("br"),n._v("Những này cơ quan cấu tạo phi thường nghiêm mật, không có Thánh vực lực lượng căn bản mở không ra."),t("br"),t("br"),n._v("Nếu như thân thể tại đây, dùng ngón tay chạm đến một cái, Thiên Đạo thư viện tạo ra thư tịch, liền có thể nhẹ nhõm tìm tới thiếu sót, thừa cơ phá giải."),t("br"),t("br"),n._v("Mà bây giờ chỉ có hồn thể, liền không có biện pháp."),t("br"),t("br"),n._v("Loại này tử vật, không cách nào chạm đến, không thể tạo ra thư tịch, Minh Lý Chi Nhãn cũng nhìn không thấu... Chẳng lẽ chỉ có thể làm chờ ở chỗ này?"),t("br"),t("br"),n._v("Mí mắt xoay loạn, Trương Huyền một hồi buồn bực."),t("br"),t("br"),n._v("“Được rồi, chờ xem, cuối cùng có người đến Tàng Thư các đi...”"),t("br"),t("br"),n._v("Tìm không thấy đường ra, đành phải từ bỏ, đang định tìm một chỗ nấp đi, thật tốt chờ lấy, liền nghe đến đại môn “Kẹt kẹt!” Một tiếng, từ bên ngoài từ từ mở ra."),t("br"),t("br"),n._v("“Ừm, có người đến?”"),t("br"),t("br"),n._v("Không nghĩ tới nhanh như vậy người tới, hưng phấn mà trực tiếp nhảy lên, Trương Huyền thân ảnh vọt tới, đi tới sau cửa lớn chờ lấy chạy đi."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Một bóng người đi đến, là cái hơn ba mươi tuổi nữ tử, quần áo lộng lẫy, mang theo khí chất tao nhã."),t("br"),t("br"),n._v("Vừa vào cửa, nữ tử đang muốn đi vào trong, đột nhiên nhướng mày, ánh mắt thẳng tắp hướng Trương Huyền vị trí nhìn lại."),t("br"),t("br"),n._v("“Người nào? Đi ra!”"),t("br"),t("br"),n._v("Nghe nói như thế, thấy được nàng ánh mắt, Trương Huyền toàn thân cứng đờ."),t("br"),t("br"),n._v("Hắn hiện tại là Vu hồn, nói cách khác, vô hình vô chất, người bình thường là không nhìn ra, trốn ở chỗ này không nhúc nhích, bị đối phương một cái phát hiện, làm sao có thể?"),t("br"),t("br"),n._v("“Thánh vực... Cường giả?”"),t("br"),t("br"),n._v("Con ngươi co rụt lại."),t("br"),t("br"),n._v("Có thể làm được điểm ấy, chỉ có một khả năng... Đối phương là Thánh vực!"),t("br"),t("br"),n._v("Tu vi đi đến Thánh vực, siêu phàm thoát tục, đối không gian có lĩnh Ngộ Năng lực, bản thân mặc dù giấu rất bí ẩn, nhưng bị giới hạn tu vi, hồn thể vị trí chỗ ở, sóng linh khí tại địa phương khác khác biệt, vẫn như cũ sẽ bị phát hiện."),t("br"),t("br"),n._v("“Thế nào, không muốn đi ra?”"),t("br"),t("br"),n._v("Đôi mi thanh tú nhăn lại, lộng lẫy nữ tử hừ lạnh một tiếng, lật bàn tay một cái, bỗng nhiên đối Trương Huyền vị trí vồ tới."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Chân khí cổn động, cả không gian tựa hồ cũng bị phong tỏa, Trương Huyền chỉ cảm thấy toàn thân cứng đờ, giống như là bị người cầm cổ, bất cứ lúc nào cũng sẽ bị nghiền ép hồn phi phách tán."),t("br"),t("br"),n._v("Hắn hồn thể mặc dù to lớn, có thể thực lực chỉ có Hóa Phàm ngũ trọng Hợp Linh cảnh đỉnh phong, cùng cường giả Thánh vực so, khác biệt thực sự quá lớn!"),t("br"),t("br"),n._v("Căn bản vắng mặt một cái cấp bậc."),t("br"),t("br"),n._v("“Trốn!”"),t("br"),t("br"),n._v("Biết không tránh né, tùy ý đối phương công kích đánh tới lời nói, vô cùng có khả năng chết ngay tại chỗ, Vu hồn vận chuyển, Trương Huyền thẳng tắp phóng ra ngoài."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("To lớn hồn thể, kích thích từng đợt tiếng gió, gào thét lên hướng ngoài cửa quét sạch."),t("br"),t("br"),n._v("“Là... Vu hồn?”"),t("br"),t("br"),n._v("Nữ tử vẻ mặt ngưng lại."),t("br"),t("br"),n._v("Vừa rồi nàng cảm thấy có người trong phòng, còn tưởng rằng là dùng cái gì chướng nhãn pháp, không nghĩ tới lại là cái Vu hồn."),t("br"),t("br"),n._v("Vu hồn sư chức nghiệp, mấy vạn năm trước liền biến mất, Kinh Hồng viện Tàng Thư khố lại đột nhiên toát ra cái này lớn một cái, lai lịch gì?"),t("br"),t("br"),n._v("“Trốn chỗ nào...”"),t("br"),t("br"),n._v("Một tiếng khẽ kêu, thân ảnh một trảo, lăng không đối chạy trốn hồn thể bắt tới."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Cường giả Thánh vực toàn lực ra tay, phương viên mấy ngàn mét không gian giống như là lập tức bị chân khí phong tỏa, không khí cũng phát ra oanh minh."),t("br"),t("br"),n._v("“Nguy rồi...”"),t("br"),t("br"),n._v("Trương Huyền hồn thể mới vừa chạy ra Tàng Thư khố, vốn định nhảy vào không trung, liền cảm thấy thân thể một cứng rắn, giống như là lọt vào đầm lầy, muốn chạy trốn, nhưng bất luận thế nào đều không thể hành động."),t("br"),t("br"),n._v("“Đây là cường giả Thánh vực ?”"),t("br"),t("br"),n._v("Toàn thân xiết chặt, Trương Huyền toàn thân cứng ngắc."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-778"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-780"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);