(window.webpackJsonp=window.webpackJsonp||[]).push([[2058],{2415:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-775-lac-that-that-te-xiu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-775-lac-that-that-te-xiu"}},[n._v("#")]),n._v(" Chương 775: Lạc Thất Thất té xỉu")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 775: Lạc Thất Thất té xỉu"),t("br"),t("br"),n._v("Tùy tiện hàn huyên hai câu, gian phòng an tĩnh lại, Trương Huyền bưng chén rượu lên, Minh Lý Chi Nhãn nhúc nhích, lặng lẽ hướng nhìn bốn phía."),t("br"),t("br"),n._v("Một lát sau, lộ ra kỳ quái vẻ."),t("br"),t("br"),n._v("Chung quanh loại trừ có Tụ Linh trận bên ngoài, cũng không có cái gì cơ quan cùng trận pháp."),t("br"),t("br"),n._v("Vị này Hồ Yêu Yêu, không phải muốn tìm bản thân phiền phức ư?"),t("br"),t("br"),n._v("Vì cái gì cái gì đều không định?"),t("br"),t("br"),n._v("Thật chẳng lẽ cho rằng, cái gì đều không có, liền có thể làm cho mình xui xẻo?"),t("br"),t("br"),n._v("“Lão sư, ngươi nhìn bên kia!”"),t("br"),t("br"),n._v("Đang đang nghi ngờ, hơi nghiêng Lạc Thất Thất tiếng âm vang lên."),t("br"),t("br"),n._v("Thuận ngón tay của nàng nhìn lại, đại sảnh hơi nghiêng, bày ra tại một cái cao hơn hai mét giá binh khí, phía trên treo một cái màu vàng nhạt lá chắn, vừa sừng mang theo lỗi thời bao tương, vừa nhìn liền biết có tuổi rồi."),t("br"),t("br"),n._v("“Đoán chừng là vật phẩm trang sức đi!”"),t("br"),t("br"),n._v("Nhìn giá binh khí tạo dáng, hẳn là dùng để chứa sức môi trường, rất nhiều đại gia tộc phòng khách đều có, dùng để hiển lộ rõ ràng võ lực, không tính là gì."),t("br"),t("br"),n._v("“Ta nghiên cứu qua liên quan tới Ngô Dương Tử lão sư thư tịch, căn cứ tư liệu lịch sử ghi chép, hắn một đời từng luyện chế 124 kiện vũ khí, lá chắn bộ dáng vũ khí mười bảy kiện, từng có một cái , phía trên điêu khắc có kỳ quái viêm nguyệt, tạo dáng kỳ lạ, cùng trước mắt cái này tương tự...”"),t("br"),t("br"),n._v("Đôi mi thanh tú nhăn lại, Lạc Thất Thất nói."),t("br"),t("br"),n._v("“Viêm Nguyệt thuẫn?” Trương Huyền nhìn sang."),t("br"),t("br"),n._v("Mặc dù khoảng cách hai người xa xôi, nhưng bằng mượn nhãn lực của bọn hắn, có thể nhẹ nhõm nhìn thấy, cách đó không xa trên tấm chắn, quả thực điêu khắc một cái uyển như là mặt trời chói chang viêm nguyệt, tạo dáng bên trên cũng cùng bình thường lá chắn khác biệt, tựa như một cái không theo quy tắc hình sáu cạnh, để lộ ra một loại cổ quái khí tức."),t("br"),t("br"),n._v("“Ngươi ý tứ... Đây chính là Thanh Đồng Viêm Nguyệt thuẫn?”"),t("br"),t("br"),n._v("Trong lòng hơi động, Trương Huyền hỏi."),t("br"),t("br"),n._v("“Ừm!” Lạc Thất Thất gật đầu, đứng lên, hướng đại sảnh góc đi đến, rất mau tới đến lá chắn gần, nhìn kỹ đi qua."),t("br"),t("br"),n._v("Càng xem thần sắc càng ngưng trọng."),t("br"),t("br"),n._v("Trương Huyền cũng nhìn qua, không thể không nói, cái này lá chắn mặc dù thoạt nhìn cũ kỹ, công nghệ nhưng cực kỳ phức tạp, dựa theo hắn đối luyện khí lý giải, luyện chế thủ pháp, quả thực cùng Phủ Cầm Luyện Khí pháp có cực lớn chỗ tương tự."),t("br"),t("br"),n._v("“Minh Lý Chi Nhãn!”"),t("br"),t("br"),n._v("Hoa văn nhúc nhích, lá chắn tình huống cụ thể lập tức xuất hiện ở trước mắt."),t("br"),t("br"),n._v("“Thật là Ngô Dương Tử lưu lại...”"),t("br"),t("br"),n._v("[ truyen cuatu"),t("br"),n._v("i đốt net ]  Trương Huyền sững sờ."),t("br"),t("br"),n._v("Cho rằng nhưng mà cái hàng giả, không nghĩ tới, lại là thật."),t("br"),t("br"),n._v("“Thanh Đồng Viêm Nguyệt thuẫn nghe nói là Ngô Dương Tử lão sư, vì kỷ niệm vong thê luyện chế mà thành, chưa hề bán ra, vốn cho rằng đã nương theo hắn nhà cũ biến mất, không nghĩ tới xuất hiện ở đây...”"),t("br"),t("br"),n._v("Lạc Thất Thất cũng xác nhận xuống, nhịn không được cảm khái, còn chưa nói xong, tú quyền xiết chặt: “Nhìn đến, Yêu Yêu học trưởng, nhất định biết nhà cũ vị trí, thứ này, thậm chí cũng có thể là từ nơi đó lấy được!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Nếu như thứ này thật không có bán ra qua, vô cùng có khả năng giấu ở nhà cũ, hơn nữa vị này Hồ Yêu Yêu còn đi qua, từ ở bên trong lấy được."),t("br"),t("br"),n._v("“Còn một chuôi kiếm!”"),t("br"),t("br"),n._v("Xem hết lá chắn, đang định đi về tới, Lạc Thất Thất lần nữa sửng sốt."),t("br"),t("br"),n._v("Chỉ thấy cách đó không xa trên vách tường, lại còn mang theo một thanh trường kiếm, mới vừa rồi bị giá binh khí che chắn, cũng không trông thấy, đi đến lúc này mới phát hiện."),t("br"),t("br"),n._v("Chuôi kiếm này cùng lá chắn đồng dạng, đồng dạng mang theo cổ xưa khí tức, xem xét liền có năm tháng, mấy bước đi tới gần, Lạc Thất Thất càng xem lông mày nhíu càng chặt."),t("br"),t("br"),n._v("Lập tức, từ trên tường bẻ xuống."),t("br"),t("br"),n._v("“Làm sao vậy, chuôi kiếm này, chẳng lẽ cũng là Ngô Dương Tử luyện chế?”"),t("br"),t("br"),n._v("Biết mình người học sinh này, dưới tình huống bình thường sẽ không loạn động người khác đồ vật, chủ động lấy xuống, khẳng định là phát hiện cái gì, Trương Huyền nhịn không được hỏi."),t("br"),t("br"),n._v("“Ừm, nếu như ta không có nhận lầm, đây cũng là Ngô Dương Tử tiền bối lưu cho hắn nữ nhi , chuôi kiếm này, cũng là chưa hề bán ra qua, nếu như trên thân kiếm cũng có viêm nguyệt hình vẽ lời nói, đã nói lên thật sự...”"),t("br"),t("br"),n._v("Giải thích một câu, nắm chặt chuôi kiếm, Lạc Thất Thất nhẹ nhàng kéo một phát."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Hàn mang bắn ra bốn phía, một đạo hàn khí, lôi kéo lấy làn da, để cho người ta toàn thân trở nên cứng."),t("br"),t("br"),n._v("“Kiếm tốt!”"),t("br"),t("br"),n._v("Trương Huyền âm thầm gật đầu, chuôi kiếm này mặc dù không kịp hắn Băng Vũ kiếm trình độ, nhưng cũng đạt tới tuyệt phẩm cấp bậc, quả nhiên bất phàm."),t("br"),t("br"),n._v("“Quả nhiên có viêm nguyệt...”"),t("br"),t("br"),n._v("Ánh mắt tập trung ở trên thân kiếm, Lạc Thất Thất trắng tinh trên khuôn mặt, một hồi mừng rỡ."),t("br"),t("br"),n._v("Bất quá, cái này sợi mừng rỡ vẫn chưa hoàn toàn nở rộ, liền nghe đến trường kiếm một tiếng kêu khẽ."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Tựa như có linh tính nhúc nhích, phát ra nổi giận gào thét."),t("br"),t("br"),n._v("“Nguy rồi!”"),t("br"),t("br"),n._v("Nghe được thanh âm này, Trương Huyền con ngươi co rụt lại, vội vàng đứng lên, thân thể nhoáng một cái liền đến đến gần, có điều, tốc độ của hắn mặc dù nhanh, vẫn như cũ đã chậm."),t("br"),n._v("Lạc Thất Thất thân thể mềm mại nhoáng một cái, liền muốn té ngã trên đất."),t("br"),t("br"),n._v("Chân khí xao động, đem hắn đỡ lấy, Trương Huyền ngón tay đáp tới, lập tức thở phào nhẹ nhõm."),t("br"),t("br"),n._v("“Còn tốt, nhưng mà bình thường hôn mê...”"),t("br"),t("br"),n._v("Hắn đi tới về sau, sợ Hồ Yêu Yêu làm xuất cái gì thủ đoạn, một mực ngồi tại nguyên chỗ, dùng bất biến ứng vạn biến, vốn cho rằng, đối phương khẳng định biết làm chút trận pháp, cơ quan loại hình, nằm mơ đều không nghĩ tới, tại trong kiếm không biết thả cái gì, một cái liền để Lạc Thất Thất hôn mê."),t("br"),t("br"),n._v("Biết nàng không nghiêm trọng lắm, Trương Huyền ngón tay tại trên trường kiếm nhẹ nhàng đặt mấy lần, lúc này mới đem chuôi này Viêm Nguyệt kiếm cầm tại lòng bàn tay, nhìn sang."),t("br"),t("br"),n._v("“Không có độc, mà là bị trong đó khác linh tính công kích, không nghĩ tới kiếm này, lại có chấn động linh hồn công hiệu!”"),t("br"),t("br"),n._v("Pháp bảo lợi hại, đều có đặc thù công hiệu, thật giống như trước đó tại linh tài các nhìn thấy Thanh Đồng giản, nắm giữ diệt hồn hiệu quả."),t("br"),t("br"),n._v("Một giản đánh xuống, bất kể thân thể còn là linh hồn, đều phải bị cực lớn tổn thương."),t("br"),t("br"),n._v("Chuôi này Viêm Nguyệt kiếm, thì có xao động hồn phách năng lực, Lạc Thất Thất lấy tay sờ, bị trong đó linh tính công kích, một lúc không quan sát, lúc này mới ngất đi."),t("br"),t("br"),n._v("“Ra đi!”"),t("br"),t("br"),n._v("Nhìn ra chuyện gì xảy ra, Trương Huyền trên bàn tay di động, đem Lạc Thất Thất, đặt ngang ở cách đó không xa trên một cái bàn, lúc này mới lạnh hừ một tiếng."),t("br"),t("br"),n._v("Vô chủ pháp bảo cho dù có lực công kích, cũng không phải quá mạnh, thật giống như trước đó, hắn cứ việc để linh tài các vô số bảo vật nhận chủ, nhưng cũng khó có thể chống chọi Ngụy Trường Phong đồng dạng."),t("br"),t("br"),n._v("Chuôi này Viêm Nguyệt kiếm, mặc dù đạt đến tuyệt phẩm cấp bậc, bên trong linh tính, Hóa Phàm cửu trọng cường giả, muốn hàng phục cũng khó khăn, nhưng muốn tuỳ tiện đem Lạc Thất Thất chấn choáng, rõ ràng vẫn là làm không được."),t("br"),t("br"),n._v("Chỉ sợ là vị kia Hồ Yêu Yêu, lấy cái gì không muốn người biết thủ đoạn."),t("br"),t("br"),n._v("Đối phương mượn nhờ hai người muốn tìm được Ngô Dương Tử chỗ ở cũ tâm lý, cố ý dùng lá chắn thu hút, tâm cảnh rung động phía dưới, lại tiến hành công kích, lúc này mới một lần hành động được như ý!"),t("br"),t("br"),n._v("Nhìn đến, nàng là muốn cho bản thân trúng kế, kết quả, lại bị Lạc Thất Thất khi phát hiện ra trước."),t("br"),t("br"),n._v("Đã chuyên môn thiết kế, chịu chắc chắn lúc chung quanh cất giấu, lặng lẽ quan sát, nếu không, chẳng phải không có một chút hiệu quả?"),t("br"),t("br"),n._v("“Khó trách có thể để chúng ta rất nhiều học hội, bị thiệt lớn, Trương sư quả nhiên mắt sáng như đuốc!”"),t("br"),t("br"),n._v("Không có chờ quá lâu, liền nghe đến một cái nhàn nhạt cười tiếng vang lên, cửa phòng mở ra, Hồ Yêu Yêu thon dài thẳng thân ảnh, đi tới."),t("br"),t("br"),n._v("Một thân màu đỏ tím váy dài, đem sung mãn dáng người hoàn mỹ phác hoạ ra tới."),t("br"),t("br"),n._v("Bất kể là quần áo vẫn là khuôn mặt, tựa hồ cũng là tỉ mỉ ăn mặc qua, so đêm qua nhìn thấy, càng thêm tươi đẹp, cũng càng thêm vũ mị."),t("br"),t("br"),n._v("“Đường đường lớp năm học viên, đối phó một cái tân sinh, truyền đi, thanh danh chỉ sợ không tốt lắm đâu?”"),t("br"),t("br"),n._v("Trương Huyền mí mắt vừa nhấc."),t("br"),t("br"),n._v("“Thanh danh? Ta hảo tâm mời các ngươi đến dự tiệc, các ngươi nhưng tùy ý cầm bảo kiếm của ta, thật muốn truyền đi, thanh danh bất hảo chỉ sợ là các ngươi!”"),t("br"),t("br"),n._v("Mấy bước đi tới gần, một hồi nhàn nhạt mùi thơm ngát truyền vào tị khẩu, lỗ mũi, Hồ Yêu Yêu cười khanh khách nói."),t("br"),t("br"),n._v("Khó trách xưng hô nàng là yêu nghiệt, nhất cử nhất động, tự mang ý vị, giống như một đầu hồ ly, vẩy tâm hồn người, câu hồn phách người."),t("br"),t("br"),n._v("Có thể đoán được, chỉ cần nàng ra lệnh một tiếng, khẳng định học viện biết có không ít danh sư, nguyện ý vì đó ném đầu lâu tung nhiệt huyết, lông mày không nháy mắt."),t("br"),t("br"),n._v("Bất quá, loại này mị hoặc, đối với những khác người hữu dụng, đối Trương Huyền tới nói, không bị ảnh hưởng chút nào, lắc đầu, thần sắc lạnh nhạt nhìn qua."),t("br"),t("br"),n._v("“Học đệ nhìn thấy đồ tốt nóng lòng không đợi được, quan sát một cái không đủ chứ? Ngược lại là ngươi, mời người đến đây, nhưng cố ý giấu ở hơi nghiêng, vừa nhìn liền biết không có lòng tốt! Được rồi, cũng không cùng ngươi nói nhảm, hôm nay sở dĩ đã đáp ứng đến, chính là muốn hỏi ngươi, Ngô Dương Tử tiền bối chỗ ở cũ ở đâu?”"),t("br"),t("br"),n._v("“Ừm?”"),t("br"),t("br"),n._v("Vốn cho rằng thân mang hoa phục đi tới, lại thêm nói mê hoặc, một cái Kiều Thiên cảnh, coi như không say mê, cũng gần như tinh thần tan rã, không nghĩ tới không những không có việc gì, còn đối nàng chất vấn, Hồ Yêu Yêu lông mày nhíu một cái."),t("br"),t("br"),n._v("Nàng không chỉ thân cư kinh hồng sư chức nghiệp, còn có Ma âm sư chức nghiệp, nói như đàn, thanh âm giống như vui, cả hai phối hợp, coi như Hóa Phàm cửu trọng cường giả, đều khó mà chống chọi, cái tên này làm sao sẽ một chút việc đều không có?"),t("br"),t("br"),n._v("“Chỗ ở cũ sự tình, chỉ cần ngươi đáp ứng điều kiện, ta tự nhiên sẽ nói!”"),t("br"),t("br"),n._v("Trong lòng nghi hoặc, trên mặt nhưng nhẹ nhàng cười một tiếng, mang theo rung động lòng người vui vẻ: “Gia nhập Yêu Nghiệt hội, ngoan ngoãn nghe lời của ta, ta chẳng những sẽ nói vị trí tại đâu, còn sẽ đích thân mang các ngươi đi qua!”"),t("br"),t("br"),n._v("“Ngoan ngoãn nghe lời?” Trương Huyền nhìn qua, trong mắt mang theo nghiền ngẫm: “Chỉ bằng ngươi? Chỉ bằng ngươi kinh hồng sư thủ đoạn?”"),t("br"),t("br"),n._v("“Trương sư có thể để cho Mi viện trưởng, Triệu viện trưởng tranh nhau thu đồ, một hơi thuần phục mấy trăm con linh thú, hồn lực khẳng định rất cường đại, coi như tay ta đoạn toàn bộ ra, cũng không cách nào làm đến... Kinh hồng sư thủ đoạn, liền muốn để ngươi ngoan ngoãn nghe lời, khẳng định làm không được! Điểm ấy, ta vẫn là có tự biết rõ.”"),t("br"),t("br"),n._v("Cười khanh khách nhìn qua, Hồ Yêu Yêu môi đỏ có chút nhếch lên: “Bất quá... Nếu như thêm vào Lạc Thất Thất ư?”"),t("br"),t("br"),n._v("“Thất Thất? Có ý tứ gì?” Trương Huyền lông mày nhíu một cái."),t("br"),t("br"),n._v("“Lạc Thất Thất bị Viêm Nguyệt kiếm linh tính công kích, nếu như không nhanh chút cứu tỉnh, rất có thể thương tới căn bản, dẫn đến về sau tu luyện, đều khó mà tiến bộ! Trương sư, làm lão sư của nàng, cũng không thể đưa học sinh mà không để ý đi!”"),t("br"),t("br"),n._v("Hồ Yêu Yêu cười nói."),t("br"),t("br"),n._v("“Thương tới căn bản?” Trương Huyền sững sờ."),t("br"),t("br"),n._v("Liền linh tính bên trong chấn động, để cho người ta bất tỉnh mà thôi, tu vi không cách nào tiến bộ, còn thương tới căn bản, cái tên này nói như thế hùng hồn, lấy ở đâu đến tự tin?"),t("br"),t("br"),n._v("Nghi ngờ một cái, ngay sau đó cũng là giật mình."),t("br"),t("br"),n._v("Đối phương khẳng định là cảm thấy hắn, đối linh hồn hiểu cực ít, lại thêm quan tâm sẽ bị loạn, chỉ cần nói nghiêm trọng một chút, liền sẽ ngoan ngoãn nghe lời, đáp ứng điều kiện."),t("br"),t("br"),n._v("Một khi không đáp ứng, khẳng định cũng chuẩn bị ghi chép thủy tinh, đến lúc đó, cho tỉnh lại Lạc Thất Thất quan sát, rất dễ dàng để hắn lòng sinh oán hận, sư đồ tầm đó quan hệ, liền sẽ không lại giống bây giờ như vậy hòa thuận."),t("br"),t("br"),n._v("Có thể nói, một chiêu tướng quân, để hắn vô luận như thế nào lựa chọn, đều mười phần gian nan."),t("br"),t("br"),n._v("Lựa chọn cứu người, liền muốn đáp ứng điều kiện của nàng, không đồng ý, liền sẽ để Lạc Thất Thất thương tâm, quan hệ thầy trò không hòa thuận."),t("br"),t("br"),n._v("Là chính là ngược lại, đều là nàng thắng!"),t("br"),t("br"),n._v("Không hổ là Yêu Nghiệt hội hội trưởng, quả nhiên lợi hại."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-783"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-785"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);