(window.webpackJsonp=window.webpackJsonp||[]).push([[206],{559:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1162-trung-thanh-vuong-trung-đoc"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1162-trung-thanh-vuong-trung-đoc"}},[n._v("#")]),n._v(" Chương 1162: Trung Thanh Vương trúng độc")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Vị này Trần Triết, đem Chương Dẫn Khâu trong tay tin tức, rò rỉ cho Dị Linh tộc nhân, để bọn hắn tại Khưu Ngô cung suýt chút nữa về không được, sớm đã vi phạm với danh sư chuẩn tắc, làm sao còn cần ngợi khen?"),t("br"),t("br"),n._v("Cũng quá trái ngược lẽ thường đi!"),t("br"),t("br"),n._v("Thấy hắn không hiểu, Trương Huyền lắc đầu, giải thích nói: “Trần Triết bị bắt sau khi trở về, theo trong miệng hắn thẩm vấn xảy ra điều gì, lại dặn dò cái gì, không có người biết! Nếu như vậy định tội... Chứng cứ đâu? Chứng cứ ở đâu? Không có chứng cứ, liền định tội, hơn nữa người này còn chết tại Danh Sư đường... Ngươi cảm thấy những người khác sẽ nghĩ như thế nào?”"),t("br"),t("br"),n._v("“Cái này...” Ngô sư yên lặng."),t("br"),t("br"),n._v("Người người đều có đồng tình người yếu tâm, nhưng, có phải hay không người yếu, cũng không phải là mặt ngoài nhìn thấy."),t("br"),t("br"),n._v("Thật cho Trần Triết định tội, hắn lại vừa khéo chết tại Danh Sư đường nhà giam bên trong... Chỉ cần có người trợ giúp, đủ loại “Màn đen” tin tức tất nhiên theo nhau mà đến, ai cũng không có cách nào phân biệt chính xác còn là sai lầm."),t("br"),t("br"),n._v("Đến lúc đó, vốn đối bọn hắn có lợi cục diện, liền sẽ một chút trở nên không có lợi, lại không có biện pháp."),t("br"),t("br"),n._v("“Tuyên dương hắn vì nhân tộc mà chết, định công lao, mà không phải tội danh... Như vậy trải qua, giết hắn người, tất nhiên sẽ cảm thấy tin tức rò rỉ, có chút hoảng hốt, đến lúc đó... Sẽ tự nhiên mà nhiên lộ ra chân tướng. Chỉ cần đối phương động thủ, còn lại liền dễ dàng, một lần nữa định tội cũng không tính là gì!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("“Thì ra là thế...” Giờ mới hiểu được tới, Ngô sư bừng tỉnh hiểu ra."),t("br"),t("br"),n._v("Cái này đích xác là biện pháp tốt nhất."),t("br"),t("br"),n._v("Đã có thể hóa giải đối phương giết chết Trần Triết mang tới ảnh hưởng, lại có thể dụ dỗ trên đó câu... Nhất cử lưỡng tiện."),t("br"),t("br"),n._v("“Tốt, ta hiện tại sẽ làm!”"),t("br"),t("br"),n._v("Hiểu được, Ngô sư gật đầu, xoay người phân phó một câu."),t("br"),t("br"),n._v("“Tiếp đó, nên làm như thế nào?”"),t("br"),t("br"),n._v("Tất cả an bài ổn thoả, Ngô sư lần nữa nói."),t("br"),t("br"),n._v("Đối với trước mắt vị này, hắn phục sát đất, chuyện về sau, cũng liền lười đi muốn."),t("br"),t("br"),n._v("“Đằng sau, cũng không cần quản, tối nay vẫn như cũ tham gia ngươi nói cái kia phẩm giám hội... Mồi câu buông xuống, là cá lời nói, kiểu gì cũng sẽ mắc câu.” Trương Huyền nở nụ cười."),t("br"),t("br"),n._v("Ngô sư gật đầu, không hỏi thêm nữa."),t("br"),t("br"),n._v("Rời khỏi giam giữ Trần Triết gian phòng, Trương Huyền không tại dừng lại, trực tiếp đi Danh Sư đường Tàng Thư khố."),t("br"),t("br"),n._v("Nắm giữ Ngô sư thân phận lệnh bài, Thánh vực tam trọng, tứ trọng công pháp, đều có tư cách quan sát, chưa tới một canh giờ, liền thu sạch vào thư viện."),t("br"),t("br"),n._v("Sưu tập xong xuôi, đem Sở Thiên Hành bệ hạ cho sách cũng lấy ra ngoài, khắc sâu vào thức hải."),t("br"),t("br"),n._v("“Chính xác!”"),t("br"),t("br"),n._v("Thư viện một hồi lay động, hai quyển sách xuất hiện."),t("br"),t("br"),n._v("Chính là Thánh vực tam trọng cùng tứ trọng Thiên Đạo công pháp."),t("br"),t("br"),n._v("Mở ra nhìn một lần, Trương Huyền con mắt tỏa ánh sáng."),t("br"),t("br"),n._v("“Đều đạt đến Thiên Đạo cấp bậc, có thể trực tiếp tu luyện...”"),t("br"),t("br"),n._v("Đây là hắn lần thứ nhất, trước thời hạn sưu tập ra siêu việt hai cái cấp bậc công pháp... Có cái này hai quyển sách, hoàn toàn có thể một lần đột phá, trực tiếp trùng kích Thánh vực tứ trọng Nguyên Thần cảnh!"),t("br"),t("br"),n._v("“Trời đã tối, trước cùng Ngô sư đi cái kia phẩm giám hội, sau đó lại tu luyện...”"),t("br"),t("br"),n._v("Hướng ra phía ngoài nhìn một cái, mỗi ngày đã hoàn toàn đen, Ngô sư nói tới cái kia phẩm giám hội, lập tức bắt đầu, không kịp tu luyện, Trương Huyền duỗi lưng một cái, đi ra Tàng Thư khố."),t("br"),t("br"),n._v("Có công pháp, tu luyện liền đơn giản, một canh giờ liền có thể giải quyết, thực sự không giải quyết được, vậy liền hai canh giờ... Không có khác nhau quá nhiều."),t("br"),t("br"),n._v("Dù sao đối với hắn mà nói, chỉ cần tu luyện, liền là đại thành... Không tồn tại cái gì tu luyện bình chướng."),t("br"),t("br"),n._v("Còn là trước hết nghĩ phương pháp, đem phụ tu toàn bộ khảo hạch đến thất tinh, trở thành thất tinh danh sư lại nói."),t("br"),t("br"),n._v("“Trương sư, chúng ta lên đường đi!”"),t("br"),t("br"),n._v("Thấy hắn đi tới, Ngô sư cười cười, hai người đồng loạt đi ra ngoài."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Trương Huyền bên này, đi tham gia cái gọi là phẩm giám hội, hoàng cung một cái rộng lớn đại điện bên trong."),t("br"),t("br"),n._v("“Đáng ghét, đáng ghét...”"),t("br"),t("br"),n._v("Trung Thanh Vương đứng dậy, hàm răng cắn khanh khách vang vọng, trong mắt hận ý như là nước chảy, tựa như muốn đem người thiêu đốt."),t("br"),t("br"),n._v("“Tốt rồi, cái gì đều không có chuẩn bị, liền ra tay đối phó một vị danh sư, lá gan thật là lớn!”"),t("br"),t("br"),n._v("Sở Thiên Hành hừ một tiếng, nói."),t("br"),t("br"),n._v("“Bệ hạ, ta...” Trung Thanh Vương ôm quyền."),t("br"),t("br"),n._v("“Quên đi, không cần nói, ngươi còn là hấp thụ dạy bảo đi, lần này vì đưa ngươi bảo vệ đi ra, ta thế nhưng là hao tốn cực lớn đánh đổi!”"),t("br"),t("br"),n._v("Sở Thiên Hành khoát tay áo."),t("br"),t("br"),n._v("“Vi thần biết sai rồi...” Trung Thanh Vương liền vội vàng gật đầu."),t("br"),t("br"),n._v("“Biết liền tốt... Ngươi mấy ngày nay trước không vội về vương phủ, ngay tại ta chỗ này, hảo hảo tu luyện, nhớ lại có hay không phạm sai lầm địa phương, đừng để người nắm được cán! Nếu không, ai cũng cứu không được ngươi!”"),t("br"),t("br"),n._v("Sở Thiên Hành nói."),t("br"),t("br"),n._v("“Vâng!” Trung Thanh Vương nhẹ gật đầu: “Yên tâm đi, sẽ không có xảy ra vấn đề gì, chủ yếu là cái kia Trương Huyền, không theo lẽ thường ra bài, hơn nữa thực lực của hắn, dường như so biết đến chỗ cao quá nhiều...”"),t("br"),t("br"),n._v("Khắp khuôn mặt là hận ý, nói đến vị kia Trương Huyền, Trung Thanh Vương liền khí cắn răng, có điều, lời nói còn chưa hạ thấp, vẻ mặt đột nhiên tái đi, thân thể không tự chủ được đung đưa."),t("br"),t("br"),n._v("“Nguy rồi...”"),t("br"),t("br"),n._v("Con ngươi co rụt lại, Trung Thanh Vương da đầu một chút nổ tung."),t("br"),t("br"),n._v("“Làm sao vậy?”"),t("br"),t("br"),n._v("Sở Thiên Hành cau mày."),t("br"),t("br"),n._v("“Ta... Ta trúng độc, phốc!”"),t("br"),t("br"),n._v("Trung Thanh Vương trên mặt đất không ngừng co quắp, từng ngụm từng ngụm phun máu tươi, cảm thấy cả người ngũ tạng lục phủ, dường như tất cả đều sắp bể nát."),t("br"),t("br"),n._v("“Trúng độc? Làm sao sẽ trúng độc?”"),t("br"),t("br"),n._v("Sở Thiên Hành con ngươi co rụt lại."),t("br"),t("br"),n._v("Vị này Trung Thanh Vương, liền đi theo bên cạnh hắn, trước đó không có bất kỳ cái gì báo hiệu, làm sao sẽ đột nhiên ở giữa độc đây?"),t("br"),t("br"),n._v("“Ta cũng không biết...”"),t("br"),t("br"),n._v("Đau đớn kịch liệt bên dưới, trên mặt đất không ngừng lăn lộn, Trung Thanh Vương trên đầu mồ hôi phả ra, nhưng thủy chung nghĩ không ra, lúc nào trúng độc."),t("br"),n._v(" “Ngươi hôm nay có hay không ăn sai đồ vật?”"),t("br"),t("br"),n._v("Sở Thiên Hành vội vàng hỏi."),t("br"),t("br"),n._v("Trung Thanh Vương là Thánh vực tứ trọng cường giả đỉnh phong, thực lực như thế, đều không chịu nổi, độc này thực sự thật là đáng sợ."),t("br"),t("br"),n._v("Có thể hạ độc, hơn nữa để hắn không có chút nào phòng bị, ít nhất đều là thất tinh đỉnh phong độc sư."),t("br"),t("br"),n._v("Loại người này, thật muốn xuất hiện, làm sao có thể một chút ấn tượng đều không có?"),t("br"),t("br"),n._v("“Ngày hôm nay...” Trung Thanh Vương sửng sốt một chút: “Vị kia Trương Huyền, từng cho ăn qua ta mấy hồ lô rượu ngon, cái kia kêu Tôn Cường, tại trên người của ta rắc chút cây ớt cùng muối...”"),t("br"),t("br"),n._v("Chính hắn thức ăn, khẳng định không có vấn đề, đi Danh Sư đường, cũng chưa ăn qua đồ vật, duy nhất không minh không trắng, liền là Trương Huyền cho ăn qua rượu ngon, Tôn Cường vung qua gia vị."),t("br"),t("br"),n._v("“Vung cây ớt cùng muối?” Sở Thiên Hành ngẩn ngơ."),t("br"),t("br"),n._v("Vung cái đồ chơi này làm gì?"),t("br"),t("br"),n._v("Bất quá, biết lúc này không phải hỏi thăm thời điểm, nhướng mày: “Nhìn tới, khẳng định là vị này Trương Huyền hạ độc... Chỉ là, đây rốt cuộc cái gì độc? Người tới, đi mời cháu y sư tới!”"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Một cái hộ vệ vội vã đi ra ngoài."),t("br"),t("br"),n._v("Thời gian không dài, một vị lão giả theo ở phía sau đi đến."),t("br"),t("br"),n._v("“Bệ hạ!”"),t("br"),t("br"),n._v("“Ừm, cháu y sư, Trung Thanh Vương trúng độc, ngươi xem một chút, là cái gì độc, có thể hay không giúp một tay giải hết...” Sở Thiên Hành phân phó."),t("br"),t("br"),n._v("Cháu y sư là Thanh Nguyên hoàng cung ngự dụng y sư, sớm đã đạt đến thất tinh đỉnh phong cấp bậc, đối y đạo đã hiểu, xuất thần nhập hóa, muốn nói ai có thể cứu chữa, nhất định là hắn."),t("br"),t("br"),n._v("“Bệ hạ yên tâm, chỉ cần là không cao hơn thất tinh độc, ta đều có thể tuỳ tiện giải hết!”"),t("br"),t("br"),n._v("Nhếch miệng cười một tiếng, cháu y sư lộ ra miệng đầy răng vàng, ngón tay một phen, chân khí trong cơ thể ngưng tụ thành tơ, lập tức đem Trung Thanh Vương bao phủ ở bên trong, tựa như một cái viên cầu."),t("br"),t("br"),n._v("Hắn độc môn chẩn bệnh pháp... Tằm âm bắt mạch!"),t("br"),t("br"),n._v("Sử dụng chân khí bao phủ bệnh người toàn thân huyệt đạo, cẩn thận quan sát, điều tra xuất cụ thể triệu chứng, sau đó lại đúng bệnh hốt thuốc."),t("br"),t("br"),n._v("“Ừm? Độc này có chút kỳ quái ah...”"),t("br"),t("br"),n._v("Bao phủ một hồi, cháu y sư cau mày."),t("br"),t("br"),n._v("“Khó hiểu?” Sở Thiên Hành nhìn qua: “Có thể hay không chữa khỏi?”"),t("br"),t("br"),n._v("“Bệ hạ yên tâm, độc này mặc dù khó hiểu, để cho người ta khó mà phát giác, có điều, chỉ cần ta chân khí đi vào Trung Thanh Vương trong cơ thể, sử dụng dây tóc điều tra pháp, tất nhiên có thể biết chuyện gì xảy ra, thuận lợi giải quyết tai hoạ ngầm!”"),t("br"),t("br"),n._v("Cháu y sư một mặt tự tin."),t("br"),t("br"),n._v("“Ừm, vậy liền nhanh lên đi!”"),t("br"),t("br"),n._v("Thấy Trung Thanh Vương sắc mặt tái nhợt, tựa như lúc nào cũng sẽ không chịu nổi tử vong, Sở Thiên Hành vội nói."),t("br"),t("br"),n._v("“Được!” Cháu y sư nhẹ gật đầu, hít sâu một hơi, bàn tay lăng không một trảo."),t("br"),t("br"),n._v("Xì xì xì!"),t("br"),t("br"),n._v("Một đạo chân khí theo đầu ngón tay bắn ra, rơi vào Trung Thanh Vương mi tâm, dọc theo kinh mạch liền chui đi vào."),t("br"),t("br"),n._v("Dây tóc điều tra pháp, là đem chân khí ngưng tụ thành tơ, đi vào người bệnh trong cơ thể, nhờ vào đó điều tra tổn thương bệnh căn nguyên, sau đó lại đúng bệnh hốt thuốc."),t("br"),t("br"),n._v("Vừa rồi thi triển tằm âm bắt mạch, dò xét không ra trên người đối phương kịch độc là cái gì, chỉ có thể dùng loại phương pháp này."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Rất nhanh dò xét một lần, vẫn như cũ không có phát hiện cái gì độc, cháu y sư đem chân khí thu hồi, mày nhíu lại thành viên."),t("br"),t("br"),n._v("Đối phương trong cơ thể, không có chút nào độc tính, thế nhưng là nội tạng lại giống như là bị kịch độc hủ thực, để hắn trăm mối vẫn không có cách giải."),t("br"),t("br"),n._v("“Thế nào?”"),t("br"),t("br"),n._v("Sở Thiên Hành lần nữa nhìn tới."),t("br"),t("br"),n._v("“Độc này, có chút kỳ lạ, ta đang suy nghĩ phương pháp...”"),t("br"),t("br"),n._v("Đang nghĩ nói tiếp, Tôn sư đột nhiên sắc mặt trắng nhợt, lập tức ngã trên mặt đất, không ngừng run rẩy, máu tươi từng ngụm từng ngụm phun ra: “Nguy rồi, ta cũng trúng độc...”"),t("br"),t("br"),n._v("Nói hết nằm trên mặt đất, co quắp mấy lần, liền hôn mê bất tỉnh."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Sở Thiên Hành giật nảy mình."),t("br"),t("br"),n._v("Mới vừa còn lời thề son sắt mà nói, độc này có thể giải, kết quả, đều không có xác định là cái gì độc, liền ngất đi..."),t("br"),t("br"),n._v("Vị này đã là hoàng cung lợi hại nhất y sư, lúc này cũng đã trúng độc, cái này nên làm cái gì?"),t("br"),t("br"),n._v("Lại là loại nào kịch độc, liền y sư đều không có phát giác ở giữa lên?"),t("br"),t("br"),n._v("“Bệ hạ... Phủ đệ của ta, giam giữ lấy không ít độc sư... Bắt một cái tới...” Trung Thanh Vương hữu khí vô lực ngẩng đầu."),t("br"),t("br"),n._v("“Ngươi chịu đựng, ta vậy thì phái người tới!”"),t("br"),t("br"),n._v("Sở Thiên Hành lần nữa quay đầu phân phó."),t("br"),t("br"),n._v("Nương theo Hoàng đế hạ lệnh, hộ vệ rất nhanh liền liền xông ra ngoài, thời gian không dài, liền mang theo một cái thanh niên mặc áo đen đi tới trước mặt."),t("br"),t("br"),n._v("Chính là trước đó Tần quản gia!"),t("br"),t("br"),n._v("“Vương gia, phủ đệ độc sư, đều bị vị kia Trương Huyền thả đi...”"),t("br"),t("br"),n._v("“Thả đi? Phốc!”"),t("br"),t("br"),n._v("Trung Thanh Vương thần thái uể oải, lúc này đã uể oải, tùy thời đều không chịu nổi."),t("br"),t("br"),n._v("“Không có độc sư, y sư lại không thể hiểu... Ta đi tìm Trương sư, đường đường danh sư, thế mà dùng độc...” Sở Thiên Hành khuôn mặt tái mét."),t("br"),t("br"),n._v("Tốn như thế đại đánh đổi, mới đưa vị này vương gia bảo lãnh đi ra, như vậy bị trực tiếp hạ độc chết... Hắn Thanh Nguyên hoàng thất, sẽ bị người xem như chê cười."),t("br"),t("br"),n._v("“Trương sư? Dùng độc?”"),t("br"),t("br"),n._v("Một bên Tần quản gia nghe được lời nói, nhịn không được nhìn lại: “Bệ hạ, nhà chúng ta vương gia, là bị cái kia Trương Huyền hạ độc?”"),t("br"),t("br"),n._v("“Khẳng định là hắn, loại trừ hắn, không có người có cơ hội!” Sở Thiên Hành khẽ nói."),t("br"),t("br"),n._v("“Nếu như là hắn hạ độc...”"),t("br"),t("br"),n._v("Tần quản gia chần chờ một chút, một mặt chắc chắn cùng tự tin: “Ta biết làm sao giải!”"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1170"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1172"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);