(window.webpackJsonp=window.webpackJsonp||[]).push([[2061],{2414:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-778-hoc-đo-ho-yeu-yeu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-778-hoc-đo-ho-yeu-yeu"}},[n._v("#")]),n._v(" Chương 778: Học đồ Hồ Yêu Yêu")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 778: Học đồ Hồ Yêu Yêu"),t("br"),t("br"),n._v("“Lão sư...”"),t("br"),t("br"),n._v("Hồ Yêu Yêu hốc mắt đỏ lên, răng cắn môi đỏ, chần chờ một chút, quỳ rạp xuống đất."),t("br"),t("br"),n._v("Mặc dù đối vị này Trương Huyền, hận thấu xương, nhưng không dám vi phạm mệnh lệnh của lão sư."),t("br"),t("br"),n._v("“Tiểu Vệ, được rồi... Nàng cũng là nhất thời hồ đồ!” Một mặt hư nhược Trương Huyền mở miệng."),t("br"),t("br"),n._v("“Tiểu Vệ?”"),t("br"),t("br"),n._v("Hồ Yêu Yêu phát điên."),t("br"),t("br"),n._v("Lão sư Vệ Nhiễm Tuyết, thập đại danh sư một trong, lục tinh đỉnh phong danh sư, đường đường Kinh Hồng viện viện trưởng, ngươi một cái tân sinh, chừng hai mươi tuổi tiểu gia hỏa, thế mà xưng hô tiểu Vệ..."),t("br"),t("br"),n._v("“Ta cái này học sinh luôn luôn ngang bướng, bình thường thích làm sao chơi đùa liền mặc cho nàng đi, hôm nay nhưng đối Trương sư ngươi tìm phiền toái, không hảo hảo giáo huấn một lần, chỉ sợ còn không biết chuyện nghiêm trọng!”"),t("br"),t("br"),n._v("Vệ Nhiễm Tuyết nói."),t("br"),t("br"),n._v("Dùng nhãn lực của nàng, đương nhiên có thể nhìn ra một vài vấn đề, chỉ sợ không giống vị này Trương sư nói đơn giản như vậy, có điều, nàng người học sinh này, cũng quả thực đủ bướng bỉnh, trước kia một mực chưa nói qua cái gì, vừa vặn hiện tại giáo huấn một lần, để tránh xông ra càng lớn tai hoạ."),t("br"),t("br"),n._v("Không nói cái khác, vị này Trương sư đối kinh hồng sư lý giải, chính là nàng xa kém xa! Nàng còn muốn lấy, như thế nào mời đối phương cho các nàng học viện giảng bài, kết quả cái tên này liền tìm người ta phiền phức!"),t("br"),t("br"),n._v("Đây không phải tìm kích động ư? Còn để cho ta như thế nào mở miệng?"),t("br"),t("br"),n._v("“Lão sư...”"),t("br"),t("br"),n._v("Không nghĩ tới lão sư tin tưởng đối phương, mà không tin mình, Hồ Yêu Yêu mặt mũi lo lắng."),t("br"),t("br"),n._v("“Đủ rồi, ta cùng Trương sư nói chuyện, thuộc về trưởng bối ở giữa giao lưu, còn chưa tới phiên ngươi chen vào nói! Còn không quỳ trên mặt đất thật tốt tự kiểm điểm!”"),t("br"),t("br"),n._v("Vệ Nhiễm Tuyết hơi vung tay."),t("br"),t("br"),n._v("“Trưởng bối...”"),t("br"),t("br"),n._v("Hồ Yêu Yêu không ngừng run rẩy."),t("br"),t("br"),n._v("Cái tên này... Không phải tân sinh ư? Lúc nào lại thành trưởng bối?"),t("br"),t("br"),n._v("“Trương sư, đồ đệ của ta ngang bướng, để ngươi bị thương, cái này là một cái khôi phục thương thế tiểu hoàn đan, đối ngươi hẳn là hữu dụng!” Cổ tay khẽ đảo, Vệ Nhiễm Tuyết lấy ra một cái bình ngọc đưa tới."),t("br"),t("br"),n._v("“Tiểu hoàn đan? Cấp sáu đan dược?” Trương Huyền líu lưỡi."),t("br"),t("br"),n._v("Tiểu hoàn đan, cấp sáu đan dược, coi như Thánh vực cường giả bị thương, đều có cực lớn hiệu quả trị liệu, bán ra lời nói, ít nhất giá trị mấy mai linh thạch thượng phẩm!"),t("br"),t("br"),n._v("Vừa ra tay liền đưa tiễn... Đều làm cho hắn có chút ngượng ngùng."),t("br"),t("br"),n._v("“Cái này liệt đồ xử trí như thế nào? Trương sư nói ra, ta lập tức làm theo!” Thấy hắn tiếp nhận đan dược, Vệ Nhiễm Tuyết thở phào nhẹ nhõm, nói tiếp."),t("br"),t("br"),n._v("Chỉ cần đối phương thu đồ vật, liền hẳn là sẽ không từ trọng phát rơi."),t("br"),t("br"),n._v("“Xử trí?” Trương Huyền chần chờ."),t("br"),t("br"),n._v("Kỳ thật nữ nhân này, không bắt hắn cùng Lạc Thất Thất làm văn chương, cũng sẽ không tức giận."),t("br"),t("br"),n._v("Dùng thuần khiết thầy trò quan hệ làm uy hiếp, này mới khiến hắn dự định thật tốt giáo huấn một phen, để nó biết, làm người muốn có điểm mấu chốt, không là chuyện gì đều có thể dùng để làm văn chương."),t("br"),t("br"),n._v("“Như vậy đi, ta vừa vặn thiếu một cái học đồ, để nàng cùng sau lưng ta ba tháng, chuyện ngày hôm nay như vậy coi như không có gì!”"),t("br"),t("br"),n._v("Suy nghĩ một chút, Trương Huyền nói."),t("br"),t("br"),n._v("Ngô Dương Tử di tích sự tình, còn không nói ra đây, cùng cái tên này còn có gặp nhau, không bằng trước thu làm học đồ, đến lúc đó muốn còn muốn hỏi cũng là đơn giản."),t("br"),t("br"),n._v("“Học đồ?” Nghe được yêu cầu này, Hồ Yêu Yêu suýt chút nữa không có đã hôn mê."),t("br"),t("br"),n._v("Nàng đường đường lục tinh danh sư, làm cái tên này học đồ?"),t("br"),t("br"),n._v("Mở cái gì trò đùa?"),t("br"),t("br"),n._v("Lão sư khẳng định sẽ không đồng ý!"),t("br"),t("br"),n._v("Nếu không, lão sư mặt mũi ở đâu?"),t("br"),t("br"),n._v("Đang cảm thấy Vệ viện trưởng khẳng định sẽ lập tức cự tuyệt, chỉ thấy ánh mắt của nàng một cái sáng lên, tràn đầy kích động: “Quá tốt rồi, ta vậy thì thay Yêu Yêu cám ơn Trương sư!”"),t("br"),t("br"),n._v("Vị này Trương sư đối kinh hồng sư lý giải đến cực kỳ cao thâm cảnh giới, làm hắn học đồ, chỉ cần học tập cho giỏi, khẳng định rất nhanh tiến bộ, càng ngày càng mạnh!"),t("br"),t("br"),n._v("Nói thật, cái thân phận này, liền xem như nàng, đều mười phần hâm mộ, làm sao có thể cự tuyệt."),t("br"),t("br"),n._v("“Lão sư...”"),t("br"),t("br"),n._v("Hốc mắt đỏ lên, Hồ Yêu Yêu vội vàng hô."),t("br"),t("br"),n._v("“Tốt rồi, có thể để cho Trương sư thu làm học đồ, là ngươi đã tu luyện mấy đời phúc phận! Từ hôm nay trở đi, ngươi liền rời đi cái viện này, đi theo Trương sư đằng sau, học tập cho giỏi, tranh thủ việc học có thành tựu, sớm ngày trở về!”"),t("br"),t("br"),n._v("Cắt ngang đối phương, Vệ Nhiễm Tuyết nói."),t("br"),t("br"),n._v("Làm đối phương thụ nghiệp ân sư, điểm ấy quyền quyết định vẫn phải có."),t("br"),t("br"),n._v("“Học đồ...”"),t("br"),t("br"),n._v("Hồ Yêu Yêu răng cắn chặt, cảm thấy nước mắt chảy ra."),t("br"),t("br"),n._v("Lão sư thế mà trực tiếp đáp ứng? Vì cái gì?"),t("br"),t("br"),n._v("Bản thân đem cái tên này đi tìm đến, vốn là muốn cho cái ra oai phủ đầu, thu phục thành vì chính mình thuộc hạ, cái này tốt rồi... Biến thành đối phương học đồ!"),t("br"),t("br"),n._v("Cái này gọi chuyện gì!"),t("br"),t("br"),n._v("“Đa tạ tiểu Vệ!” Nghe được đối phương đáp ứng, Trương Huyền cũng thở phào nhẹ nhõm."),t("br"),t("br"),n._v("“Trương sư, đây là đẳng cấp của ngươi huy chương, ta đã giúp ngươi xin! Còn có của ta trưởng lão huy chương, trong học viện, quy tắc rất nhiều, phiền phức cũng không ít, có cái này, có thể” giải quyết “rất nhiều!” Cổ tay khẽ đảo, Vệ Nhiễm Tuyết đem một cái cấp bậc huy chương, cùng một trưởng lão lệnh bài đưa tới."),t("br"),t("br"),n._v("Nàng giữa trưa sau khi trở về, liền hướng tổng bộ xin, rất nhanh liền nhận được hồi phục, đem huy chương chế tác tốt, đến tìm Hồ Yêu Yêu, không nghĩ tới vừa đến liền nhìn thấy màn này."),t("br"),t("br"),n._v("“Đa tạ!” Tiếp nhận huy chương cùng lệnh bài, Trương Huyền nhẹ gật đầu."),t("br"),t("br"),n._v("Không thể không nói vị này tiểu Vệ, vẫn là tốc độ thật mau."),t("br"),t("br"),n._v("“Vậy ta sẽ không quấy rầy, cáo từ trước!”"),t("br"),t("br"),n._v("Lại nói vài câu, để Hồ Yêu Yêu ngày mai đi qua tìm hắn đưa tin, Trương Huyền lúc này mới mang theo Lạc Thất Thất xoay người rời đi."),t("br"),t("br"),n._v("Nhìn thấy hai người rời đi, Hồ Yêu Yêu lúc này mới nhìn hướng lão sư, nhịn không được hỏi: “Lão sư... Vì cái gì?”"),t("br"),n._v(" Nàng bất luận thế nào đều không hiểu, bản thân vị lão sư này, đường đường thập đại danh sư một trong Vệ viện trưởng, vì sao phải bản thân đem cái tên này học đồ."),t("br"),t("br"),n._v("“Trương sư đối kinh hồng sư lý giải, ta xưng hô một tiếng lão sư, đều không đủ! Trước ngươi nhảy qua Nghê Thường Liên Y vũ, có mười bảy chỗ thiếu hụt, hắn từng cái bổ sung hoàn chỉnh! Đây là ta căn cứ hắn sửa sang lại, xem thật kỹ một chút, xem hết liền biết hắn đến cùng có bao nhiêu lợi hại!”"),t("br"),t("br"),n._v("Cổ tay khẽ đảo, lấy ra một quyển sách."),t("br"),t("br"),n._v("Nghe xong Trương sư giảng giải, nàng liền đem học được tri thức sửa sang lại thành sách, lập tức cảm thấy được lợi rất nhiều."),t("br"),t("br"),n._v("Không hổ là so với nàng đều cao minh hơn kinh hồng sư, dựa theo cái này tu luyện, Nghê Thường Liên Y vũ, liền không chỉ là bay thấp vài đầu chim sơn ca mà thôi, chỉ sợ Phi Long đều có thể đánh rơi."),t("br"),t("br"),n._v("Ý thức được những này, lúc này mới đến tìm Hồ Yêu Yêu, để nàng đưa huy chương, vừa đến, có thể giữ gìn mối quan hệ, thứ hai, cũng có thể làm cho mình người học sinh này, biết nhân ngoại hữu nhân, sơn ngoại hữu sơn."),t("br"),t("br"),n._v("Nhưng mà không nghĩ tới, vừa đến liền phát hiện, hai người đã giao thủ, đã như vậy, cũng là làm cái thuận nước giong thuyền, để Hồ Yêu Yêu làm đối phương học đồ."),t("br"),t("br"),n._v("Như vậy cũng có thể học tập cho giỏi, có lẽ có một ngày, liền có thể siêu việt bản thân, xung kích Thất Tinh thậm chí cấp bậc cao hơn kinh hồng sư."),t("br"),t("br"),n._v("“Bổ sung Nghê Thường Liên Y vũ? Cái này vũ có thiếu hụt?” Hồ Yêu Yêu có chút không dám tin tưởng."),t("br"),t("br"),n._v("Bộ này vũ kỹ, truyền thừa không biết đã bao nhiêu năm, nàng cả ngày tu luyện, không có cảm thấy không đúng, tại sao có thể có nhiều như vậy thiếu hụt?"),t("br"),t("br"),n._v("Trong lòng nghi hoặc, tiện tay đem thư tịch mở ra, chỉ nhìn thoáng qua, lập tức bị nội dung trong đó thu hút."),t("br"),t("br"),n._v("Cẩn thận đọc xong đi."),t("br"),t("br"),n._v("Vẻ mặt càng ngày càng trắng."),t("br"),t("br"),n._v("Nàng là thiên tài, lại là lục tinh kinh hồng sư, đối vũ kỹ lý giải mặc dù không bằng Vệ Nhiễm Tuyết viện trưởng, nhưng cũng không yếu."),t("br"),t("br"),n._v("Lão sư không chỉ đem thiếu hụt ghi chú đi ra, còn kỹ càng ghi mấu chốt, vừa nhìn liền biết, toàn bộ đều là thật, không có sai lầm chút nào."),t("br"),t("br"),n._v("“Lão sư... Những này, thật là hắn sửa chữa?”"),t("br"),t("br"),n._v("Không biết qua bao lâu, nuốt ngụm nước bọt, Hồ Yêu Yêu trừng to mắt, nhìn lại."),t("br"),t("br"),n._v("“Ừm!” Vệ Nhiễm Tuyết gật đầu."),t("br"),t("br"),n._v("Nếu không phải tận mắt nhìn thấy, nàng cũng không tin."),t("br"),t("br"),n._v("Tự mình tới trò chuyện, mới biết được vị này Trương sư, đối kinh hồng chức nghiệp hiểu rõ sâu bao nhiêu."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Nghe được xác nhận, Hồ Yêu Yêu lúc này mới nhớ tới, linh hồn của nàng bị đối phương xung kích, xuất hiện qua mê muội."),t("br"),t("br"),n._v("Lúc đó, đối phương căn bản là không có khiêu vũ... Mà là giơ tay nhấc chân, tự thành vũ kỹ!"),t("br"),t("br"),n._v("Điểm ấy, liền xem như trước mắt vị lão sư này, đều làm không được đi!"),t("br"),t("br"),n._v("“Tốt rồi, ngươi thật tốt nghiên cứu, nhớ kỹ lão sư lời nói, đối vị này Trương sư, thái độ tốt một chút, không cần lãng phí cơ hội!” Biết người học sinh này, khả năng phải cần một khoảng thời gian tiếp nhận, Vệ Nhiễm Tuyết nhẹ gật đầu, xoay người rời đi."),t("br"),t("br"),n._v("Nàng người học sinh này, cái gì cũng tốt, chính là quá mức ngạo khí, lần này bị thiệt lớn, hẳn là có thể đủ giác ngộ!"),t("br"),t("br"),n._v("Thấy lão sư rời đi, Hồ Yêu Yêu lần nữa hướng quyển sách trên tay tịch nhìn lại, một lát sau, trong phòng uyển chuyển nhảy múa."),t("br"),t("br"),n._v("Nàng muốn thử nghiệm một cái, sửa chữa sau nội dung, đến cùng đúng đắn không chính xác!"),t("br"),t("br"),n._v("Nàng bản thân liền cực đẹp, lại phối hợp trên người lộng lẫy quần áo, vũ động, cả phòng, như là hoa tươi nở, một cỗ khiến người ta say mê khí tức, không ngừng hướng ra phía ngoài phất phới."),t("br"),t("br"),n._v("Trong hồ nước cá chép, ngửi được cỗ khí tức này, quá mức ngu ngốc, “Phù phù! Phù phù!” Chìm vào đáy ao, trong viện đóa hoa cũng dường như vong tình theo nhịp không ngừng nhảy múa, cánh hoa theo gió tung bay."),t("br"),t("br"),n._v("Nếu có người tại đây, khẳng định biết trầm mê trong đó, không cách nào tự kềm chế, coi như Bán Thánh cường giả, đều khó mà đào thoát."),t("br"),t("br"),n._v("Không biết qua bao lâu."),t("br"),t("br"),n._v("Hồ Yêu Yêu rốt cuộc minh bạch... Đây mới là vũ kỹ, chân chính vũ kỹ!"),t("br"),t("br"),n._v("Trước đó học những cái kia, cùng cái này so sánh, cái gì cũng không tính!"),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Ngừng lại, lúc này mới phát hiện, chẳng biết lúc nào, đã lệ rơi đầy mặt."),t("br"),t("br"),n._v("“Ta vậy mà... Khóc!”"),t("br"),t("br"),n._v("Sờ lấy nước mắt trên mặt, Hồ Yêu Yêu như si như ngốc."),t("br"),t("br"),n._v("Nàng trước kia nhảy cái này vũ, lờ mờ không hiểu trong đó ý vị, mặc dù có thể phát huy không kém uy lực, nhưng khoảng cách hoàn toàn lý giải, vẫn là kém rất nhiều."),t("br"),t("br"),n._v("Mà bây giờ... Dường như cảm giác bộ này vũ kỹ đã dung nhập linh hồn, hoàn toàn cùng nàng tương dung, thành thân thể một bộ phận."),t("br"),t("br"),n._v("Trước đó nhiều nhất là, biết nó hình, mà không biết ý nghĩa, mà bây giờ, mới thật sự là lý giải!"),t("br"),t("br"),n._v("“Hắn... Đến cùng đối kinh hồng sư sâu bao nhiêu lý giải? Mới có thể đem bộ này vũ kỹ sửa chữa hoàn mỹ như vậy?”"),t("br"),t("br"),n._v("Thí nghiệm qua mới biết được, thư tịch bên trên nội dung, không có sai lầm chút nào, thở ra một hơi, Hồ Yêu Yêu trong lòng nhịn không được nói."),t("br"),t("br"),n._v("“Hội trưởng, Đổng Hân, Tiết Chân Dương, Long Thương Nguyệt ba vị học trưởng cầu kiến!”"),t("br"),t("br"),n._v("Đang trầm tư, chỉ thấy Tâm Như đi tới."),t("br"),t("br"),n._v("“Để bọn hắn vào!”"),t("br"),t("br"),n._v("Hồ Yêu Yêu nhẹ gật đầu."),t("br"),t("br"),n._v("Vốn cho rằng đêm nay không có gì bất ngờ xảy ra, khẳng định đem vị này Trương Huyền cầm xuống, cho nên, trước thời hạn liền đã hẹn mấy người, dự định khoe khoang một chút."),t("br"),t("br"),n._v("Nằm mơ đều không nghĩ tới, cầm xuống là cầm xuống... Chẳng qua bị cầm xuống chính là mình mà thôi."),t("br"),t("br"),n._v("“Thế nào? Trương Huyền ư? Ngươi xử trí như thế nào?”"),t("br"),t("br"),n._v("Sải bước đi đi vào, Tiết Chân Dương thanh âm trách trách vù vù."),t("br"),t("br"),n._v("“Đúng vậy a, mau nói ngươi thế nào giáo huấn, có phải hay không đã quỳ ngươi dưới gấu quần, ngoan ngoãn nghe lời?” Long Thương Nguyệt cũng cười nói."),t("br"),t("br"),n._v("Bọn họ xem ra, đã Hồ Yêu Yêu đánh cược nói có thể giáo huấn, vậy liền nhất định có thể đi!"),t("br"),t("br"),n._v("Tên yêu nghiệt này đồng dạng nữ nhân, liền xem như bọn họ, đều vô cùng kiêng kỵ."),t("br"),t("br"),n._v("“Giáo huấn?”"),t("br"),t("br"),n._v("Hồ Yêu Yêu quay mặt lại, nhẹ nhàng khom người, một mặt thản nhiên: “Trương Huyền tọa hạ học đồ Hồ Yêu Yêu, gặp qua các vị!”"),t("br"),t("br"),n._v("(Gửi cho bạn bè quyển sách 《 trùng sinh chi hoàn mỹ tương lai 》, đại Thần Mã giáp. Nhưng nhìn!)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-786"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-788"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);