(window.webpackJsonp=window.webpackJsonp||[]).push([[2062],{2417:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-779-thanh-lap-hoc-hoi"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-779-thanh-lap-hoc-hoi"}},[n._v("#")]),n._v(" Chương 779: Thành lập học hội")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("Chương 779: Thành lập học hội"),i("br"),i("br"),n._v("“Học, học... Học đồ?”"),i("br"),i("br"),n._v("“Trương Huyền học đồ?”"),i("br"),i("br"),n._v("Tiết Chân Dương đám người ách ngay tại chỗ, nguyên một đám gặp quỷ đồng dạng."),i("br"),i("br"),n._v("Tình huống gì?"),i("br"),i("br"),n._v("Ngươi không phải luôn mồm muốn giáo huấn Trương Huyền sao? Làm sao một cái chớp mắt biến thành hắn học đồ?"),i("br"),i("br"),n._v("Học đồ chia làm hai cái chủng loại, một loại là mới tiếp xúc cái nghề nghiệp này người mới, liền nhất tinh chức nghiệp đều không có khảo hạch thành công, đi theo chính thức chức nghiệp giả đằng sau theo học tập."),i("br"),i("br"),n._v("Một loại khác chính là cấp thấp chức nghiệp cho nghề nghiệp cấp cao đem học đồ."),i("br"),i("br"),n._v("Nói là học tập tri thức, kỳ thật liền đệ tử cũng không bằng, phụ trách chân chạy làm việc vặt."),i("br"),i("br"),n._v("Mỗi người bọn họ cũng đều có dạng này học đồ, thật giống như cách đó không xa cái này kêu Tâm Như nữ hài."),i("br"),i("br"),n._v("Bởi vì cùng hạ nhân không kém nhiều, vì cùng loại thứ nhất học đồ phân chia, lại được xưng là tọa hạ học đồ."),i("br"),i("br"),n._v("Ngươi không phải muốn cho Trương sư thần phục ư?"),i("br"),i("br"),n._v("Làm sao thời gian nháy mắt, biến thành hắn học đồ?"),i("br"),i("br"),n._v("“Ta thất bại, không phải Trương sư đối thủ... Chư vị mời về đi!”"),i("br"),i("br"),n._v("Giới thiệu xong bản thân, Hồ Yêu Yêu không nói thêm lời, khoát tay áo."),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Vốn là nghĩ ăn mừng, nghe nói như thế, Đổng Hân, Tiết Chân Dương đám người nguyên một đám kìm nén sắc mặt đỏ lên, đành phải lui ra ngoài."),i("br"),i("br"),n._v("“Chuyện gì xảy ra?”"),i("br"),i("br"),n._v("Đi ra sân nhỏ, ba người nhìn nhau, tất cả đều là tràn đầy sương mù."),i("br"),i("br"),n._v("“Xem bộ dáng là... Nàng không có giáo huấn vị này Trương Huyền, ngược lại bị đối phương dạy dỗ!” Đổng Hân nhịn không được nói."),i("br"),i("br"),n._v("Căn cứ nàng đối Hồ Yêu Yêu lý giải, chỉ có cái này một loại giải thích."),i("br"),i("br"),n._v("“Nhìn tình huống là...”"),i("br"),i("br"),n._v("Tiết Chân Dương vò đầu: “Đây chính là Hồ Yêu Yêu ah, ta cùng với nàng giao thủ mấy lần đều bị thiệt thòi, thế mà thua ở một cái tân sinh, ngay cả mình đều góp đi vào...”"),i("br"),i("br"),n._v("“Cái này tân sinh thật rất tà tính... Nhưng chẳng biết tại sao, càng tà tính, ta ngược lại càng có hứng thú!”"),i("br"),i("br"),n._v("Mắt sáng lên, Long Thương Nguyệt nói."),i("br"),i("br"),n._v("Bọn họ đều là siêu cấp thiên tài, từ danh sư học viện lan truyền ra siêu cường nhân vật, lúc nào sợ qua người khác."),i("br"),i("br"),n._v("Một cái tân sinh, vừa đến liền độc cổ phong tao, liền việc buôn bán của bọn hắn đều đoạt, quả thực gan lớn đến nhất định cực hạn."),i("br"),i("br"),n._v("“Nói thật, ta cũng đầy là hứng thú.”"),i("br"),i("br"),n._v("Đổng Hân cười cười, nhớ tới sự kiện, quay đầu nhìn về phía cách đó không xa Tiết Chân Dương: “Ngươi không nói đã phái người đi tìm những học sinh mới phiền toái ư? Hiện tại xử lý thế nào rồi?”"),i("br"),i("br"),n._v("“Ta còn tưởng rằng, hôm nay Yêu Yêu có thể đắc thủ, liền trước thời hạn phái người hợp nhất những cái kia nhảy nhót tân sinh... Dựa theo thời gian suy tính, nên mạnh mẽ đem bọn hắn giáo huấn một trận!”"),i("br"),i("br"),n._v("Tiết Chân Dương nói."),i("br"),i("br"),n._v("“Ngươi mấy cái thuộc hạ thực lực, ta rất yên tâm, nhưng mà... Những học sinh mới này là dùng vị này Trương Huyền làm chỗ dựa, làm như vậy, ta sợ chính diện đắc tội, dẫn tới càng nhiều phiền phức?”"),i("br"),i("br"),n._v("Đổng Hân trầm tư một chút, nói."),i("br"),i("br"),n._v("“Chính diện đắc tội? Vậy thì sao? Từ khi hắn xấu chúng ta buôn bán bắt đầu, cũng đã là địch nhân rồi, ngươi chẳng lẽ còn trông cậy vào kẻ địch nhân từ? Nói đùa cái gì! Lại nói, người của ta là quang minh chính đại đi khiêu chiến, phù hợp học viện quy củ, coi như vị này Trương Huyền cảm thấy khó chịu, lại có thể làm gì được ta? Phù hợp quy củ, liền xem như Mi viện trưởng, Triệu viện trưởng đến rồi, cũng nói không là cái gì đi!”"),i("br"),i("br"),n._v("Nhẹ nhàng cười một tiếng, Tiết Chân Dương trên mặt lộ ra tràn đầy tự tin: “Trừ phi... Hắn võ kỹ bên trên có thể đủ thắng quá ta mấy cái thuộc hạ, nếu không, liền đợi đến bị đánh đi!”"),i("br"),i("br"),n._v("“Ngươi đi khiêu chiến võ kỹ?”"),i("br"),i("br"),n._v("Long Thương Nguyệt sững sờ, nhịn không được cười lên: “Cũng đúng, ngươi là võ kỹ học viện hoàn toàn xứng đáng đại sư huynh, viện trưởng cao túc! Đối thương pháp lý giải, càng là đạt đến cao thâm cảnh giới khó lường. Mấy cái kia thuộc hạ, đạt được ngươi chỉ điểm cùng truyền thụ, đối võ kỹ lĩnh ngộ, cũng là nhất đẳng. Cái này Trương sư coi như lại có thiên phú, nhiều nhất luyện khí, thuần thú bên trên bất phàm, võ kỹ loại này cần thời gian rèn luyện, đạt tới tâm thần hợp nhất năng lực, còn là rất khó đi đến!”"),i("br"),i("br"),n._v("Võ kỹ không giống với đặc trợ chức nghiệp, cần tích lũy tháng ngày không ngừng tôi luyện, mới có hiệu quả."),i("br"),i("br"),n._v("Tiết Chân Dương mặc dù làm việc nóng nảy, tính khí ngay thẳng, nhưng đối võ kỹ lý giải, nhất là thương pháp, tuyệt không phải người bình thường có thể so sánh."),i("br"),i("br"),n._v("Nghe nói đã lĩnh ngộ vũ khí chi tâm, đạt đến nhân thương hợp nhất cảnh giới."),i("br"),i("br"),n._v("“Đúng vậy a, chúng ta tuy là danh sư, nhưng cũng là võ giả, không để bọn hắn biết lão sinh lợi hại, chỉ sợ còn thật sự cho rằng, những năm này tại danh sư học viện phí công chờ đợi!” Tiết Chân Dương sống lưng ngừng như là một cây giáo, mặt bên trên tán phát ra làm cho không người nào có thể kháng cự tự tin."),i("br"),i("br"),n._v("Âm mưu quỷ kế, hắn chơi không lại Hồ Yêu Yêu, mỗi lần đều mắc lừa, nhưng muốn nói chiến đấu chân chính lực, người sau khẳng định không phải là đối thủ."),i("br"),i("br"),n._v("Trường thương phát huy, uyển như du long, lớp năm có thể đủ thắng quá, đều không có mấy cái."),i("br"),i("br"),n._v("“Không sai, quan hệ bất quá là phụ trợ, thực lực mới là vương đạo! Đi, đi qua nhìn một chút, ngươi thế nào giáo huấn đám kia tân sinh!” Long Thương Nguyệt nhẹ nhàng cười một tiếng."),i("br"),i("br"),n._v("“Được...” Tiết Chân Dương gật gật đầu."),i("br"),i("br"),n._v("Ba người cùng một chỗ hướng tân sinh ở lại khu ký túc xá nhanh chân đi đi."),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Cùng Lạc Thất Thất rời đi Hồ Yêu Yêu nơi ở, mới vừa trở lại tinh anh khu, liền thấy Nhược Hoan công tử một mặt lo lắng đứng ở ngoài cửa."),i("br"),i("br"),n._v("“Trương sư, Thất Thất học trưởng!”"),i("br"),i("br"),n._v("Nhìn thấy bọn họ chạy tới, ánh mắt sáng lên, vội vàng đi tới gần."),i("br"),i("br"),n._v("“Ta đang thật muốn đi tìm ngươi!” Trương Huyền hừ một tiếng."),i("br"),i("br"),n._v("“Tìm ta?” Nhược Hoan công tử sững sờ."),i("br"),i("br"),n._v("“Giáo Chức Công Tế Giải là ngươi miễn phí đưa cho những người khác a?” Trương Huyền nói."),i("br"),n._v(" Cái tên này đem vật kia đưa người, kết quả bản thân một cái không biết chút nào, thành rất nhiều học được cái đinh trong mắt, thịt xông vào, có thể nói người trong nhà nằm, họa từ trên trời rơi xuống... Còn không có tìm hắn tính sổ, lại dám qua tìm đến mình, lá gan thật là lớn."),i("br"),i("br"),n._v("“Đây là ta phải làm! Trương sư không cần nhớ ở trong lòng!”"),i("br"),i("br"),n._v("Còn tưởng rằng là chuyện gì, Nhược Hoan công tử một mặt thở mạnh, lập tức khoát tay áo: “Hôm nay tới, là có chuyện muốn Trương sư ra tay...”"),i("br"),i("br"),n._v("Phải làm?"),i("br"),i("br"),n._v("Không cần để tâm bên trên?"),i("br"),i("br"),n._v("Trương Huyền suýt chút nữa sặc nước bọt mà chết."),i("br"),i("br"),n._v("Làm hại ta thảm như vậy, làm sao cảm giác cùng làm chuyện tốt, không muốn để lại họ tên đồng dạng?"),i("br"),i("br"),n._v("Mặc dù buồn bực, nhưng đối phương vẻ mặt nghiêm túc, Trương Huyền vẫn là không nhịn được nhìn lại: “Xảy ra chuyện gì?”"),i("br"),i("br"),n._v("“Bởi vì không có mua sắm những này công hội Giáo Chức Công Tế Giải sự tình, hai ngày này, một mực có lão sinh qua tới khiêu chiến. Hôm nay càng đã tới ba vị cao thủ, nói cực kỳ khó nghe, mọi người không cam lòng, tới chiến đấu, kết quả... Bị đánh rất thảm, không ít học viên, đều bị thương!”"),i("br"),i("br"),n._v("Nhược Hoan công tử nhịn không được nói."),i("br"),i("br"),n._v("“Khiêu chiến?” Trương Huyền nghi ngờ."),i("br"),i("br"),n._v("Tân sinh thực lực lớn bộ phận đều tại Hóa Phàm tứ trọng, đạt tới ngũ trọng đều rất ít, mà lão sinh, thấp nhất đều có Kiều Thiên cảnh thực lực đi, đánh như thế nào?"),i("br"),i("br"),n._v("“Bọn họ đem tu vi đè thấp, cùng người đối chiến tương đồng, đơn thuần dùng võ kỹ năng chiến đấu...” Nhược Hoan công tử nói."),i("br"),i("br"),n._v("“Võ kỹ?” Trương Huyền nhẹ gật đầu."),i("br"),i("br"),n._v("Đem tu vi đè thấp cùng đối thủ tương đồng, không coi là lấy lớn hiếp nhỏ, tái chiến đấu, liền nhìn từng người đối võ kỹ khống chế cùng vận dụng, nói như vậy, ngược lại cũng không tính là khi dễ người."),i("br"),i("br"),n._v("“Đúng vậy a, bọn họ ngăn ở bên ngoài cuủa túc xá, nói trong hôm nay, có người có thể vượt qua bọn họ, sẽ tự động thối lui, nếu không, liền đánh chúng ta không dám ra ngoài!”"),i("br"),i("br"),n._v("Ngẩng đầu nhìn một cái thanh niên trước mặt, Nhược Hoan công tử mặt mũi xoắn xuýt: “Còn nói, nếu như... Trong một ngày không có người có thể đủ thắng quá, muốn Trương sư ngươi... Công khai xin lỗi, sau đó, tất cả tân sinh, một lần nữa mua sắm giáo công nhân tường hiểu!”"),i("br"),i("br"),n._v("“Công khai xin lỗi? Đâu có chuyện gì liên quan tới ta?” Trương Huyền im lặng."),i("br"),i("br"),n._v("Khiêu chiến tân sinh liền khiêu chiến tân sinh, cùng hắn có nửa xu quan hệ?"),i("br"),i("br"),n._v("Bản thân xin lỗi... Phạm lấy ư?"),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Nhược Hoan công tử vò đầu, trên mặt có chút xấu hổ: “Ngươi cũng biết, gia nhập lão sinh học hội, cần giao nộp hội phí, chúng ta mới tiến vào học viện, nào có tiền nộp lên? Cho nên... Liền toàn bộ gia nhập ngươi tổ chức học hội...”"),i("br"),i("br"),n._v("“Ta tổ chức học hội? Ta tổ chức cái gì?” Trương Huyền mí mắt xoay loạn."),i("br"),i("br"),n._v("Bản thân lúc nào tổ chức cái gì học được?"),i("br"),i("br"),n._v("“Trương sư đem nhiều như vậy điểm tích lũy miễn phí đưa người, mang theo lôi kéo ý tứ, ta nghĩ lấy, khẳng định là nghĩ tổ chức thế lực. Lại nói, chúng ta tất cả tân sinh, liền bội phục ngươi một cái, nguyện ý nghe lời ngươi, cho nên, liền mọi người thương nghị phía dưới, thành lập học hội, ngươi là hội trưởng...”"),i("br"),i("br"),n._v("Nhược Hoan công tử nói."),i("br"),i("br"),n._v("“Hội trưởng?” Trương Huyền điên rồi."),i("br"),i("br"),n._v("Cái gì đều không biết dưới tình huống, thế mà bị hội trưởng... Quá hố đi!"),i("br"),i("br"),n._v("“Đúng vậy a, Trương sư, mong rằng không muốn từ chối, không có học hội, tân sinh nghĩ tại học viện trở nên nổi bật rất khó, thậm chí, làm chuyện gì, đều chịu đến cản tay, khó mà hoàn thành. Ba vạn tân sinh, chỉ có ngươi mới có loại này uy tín, làm cho tất cả mọi người đều bội phục! Ngươi không thích đáng hội trưởng, ai làm?”"),i("br"),i("br"),n._v("Nói đến đây, Nhược Hoan công tử mặt mũi lo lắng: “Lại nói, lão sinh dựa dẫm tại học viện ở lại thời gian lâu dài, muốn làm gì thì làm, tùy ý áp bách, nếu là chúng ta lại không liên hợp lại, còn không mặc người chém giết?”"),i("br"),i("br"),n._v("“Cái này...” Trương Huyền dừng lại."),i("br"),i("br"),n._v("Đối phương nói không sai."),i("br"),i("br"),n._v("Không có học hội, tân sinh quả thực bước đi liên tục khó khăn."),i("br"),i("br"),n._v("Bởi vì miễn phí Giáo Chức Công Tế Giải sự tình, không ít lão sinh đã đối giới này tân sinh có mang địch ý, bọn họ nếu là lại không liên hợp, chỉ sợ thật lại nhận ngăn cản, bước đi liên tục khó khăn."),i("br"),i("br"),n._v("“Trước ngươi, đem Giáo Chức Công Tế Giải miễn phí cấp cho, có phải hay không cũng đã đoán được sẽ xuất hiện loại tình huống này?”"),i("br"),i("br"),n._v("Trương Huyền nhịn không được nhìn qua."),i("br"),i("br"),n._v("Cái tên này cùng Tống Siêu, La Tuyền đám người, đem bản thân đưa đồ vật, miễn phí đưa người, làm danh sư, làm sao có thể không biết làm như vậy, sẽ tới đắc tội lão sinh?"),i("br"),i("br"),n._v("“Lúc ấy là không cam lòng lão sinh cố ý cố tình nâng giá, hơn nữa ác ý bán ra, không ít tân sinh không nguyện ý mua, đều bị đánh! Không chỉ có như thế... Mua một nhà, chẳng khác nào đắc tội nhà thứ hai, đồng dạng sẽ gặp phải khi dễ của bọn hắn, cho nên... Chúng ta mấy cái thương nghị một cái, cảm thấy cùng hắn chờ lấy bị sỉ nhục, còn không bằng chủ động xuất kích, liền đem ngươi cho chúng ta tế giải, cho mọi người phát xuống dưới.”"),i("br"),i("br"),n._v("Nhược Hoan công tử hơi đỏ mặt: “Chuyện này mong rằng Trương sư thứ lỗi, chúng ta cũng là bị buộc không có cách nào!”"),i("br"),i("br"),n._v("“Ai...”"),i("br"),i("br"),n._v("Trương Huyền than thở một tiếng."),i("br"),i("br"),n._v("Cũng đúng."),i("br"),i("br"),n._v("Công khai khóa thời điểm, hắn bị cái gì Chân Dương hội người ngăn lại qua, đối phương thái độ quả thực rất phách lối, có loại không mua ta đồ vật, mà đắc tội cảm giác của ta."),i("br"),i("br"),n._v("Không có bất kỳ cái gì thế lực, bối cảnh tân sinh, muốn dưới loại tình huống này trở nên nổi bật, quả thực rất khó."),i("br"),i("br"),n._v("Hắn vừa đến liền đạt được Mi viện trưởng coi trọng, sau đó bị phân phối đến tinh anh khu, rất nhiều lão sinh không dám tìm phiền phức, những người khác liền không đồng dạng, chỉ sợ cũng chỉ có thành lập học hội, mới có thể cùng chi đối kháng."),i("br"),i("br"),n._v("“Được rồi, đã thành lập học hội, ta là hội trưởng, chúng ta cái này học hội tên gọi là gì, ta hẳn phải biết chứ?” Rõ ràng những này, Trương Huyền nhìn lại."),i("br"),i("br"),n._v("“Chân Dương hội, là bởi vì hội trưởng kêu Tiết Chân Dương, Thương Nguyệt hội là bởi vì hội trưởng tên gọi Long Thương Nguyệt... Cho nên, chúng ta học hội cũng dùng tên của ngươi mệnh danh, dễ hiểu!”"),i("br"),i("br"),n._v("Thấy hắn không có quá lớn mâu thuẫn, Nhược Hoan công tử thở phào nhẹ nhõm, vội nói."),i("br"),i("br"),n._v("“Tên của ta? Trương Huyền hội?”"),i("br"),i("br"),n._v("Trương Huyền nhìn qua."),i("br"),i("br"),n._v("“Không phải, là...”"),i("br"),i("br"),n._v("Nhược Hoan công tử một mặt hưng phấn: “Huyền Huyền hội!”"),i("br"),i("br"),n._v("Convert by: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-787"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-789"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);