(window.webpackJsonp=window.webpackJsonp||[]).push([[2064],{2421:function(n,h,g){"use strict";g.r(h);var t=g(44),i=Object(t.a)({},(function(){var n=this,h=n.$createElement,g=n._self._c||h;return g("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[g("h1",{attrs:{id:"chuong-70-nghi-van"}},[g("a",{staticClass:"header-anchor",attrs:{href:"#chuong-70-nghi-van"}},[n._v("#")]),n._v(" Chương 70: Nghi vấn")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("br"),g("br"),n._v("Chương 70: Nghi vấn"),g("br"),g("br"),n._v(" “Ta, đệt!” "),g("br"),g("br"),n._v(" “Không thu? Đại ca, ngươi là thật lòng?” "),g("br"),g("br"),n._v(" Thượng Bân, Tào Hùng, Trầm Bích Như các loại người cầm lấy tóc, mỗi một người đều sắp điên rồi."),g("br"),g("br"),n._v(" Ngày hôm nay kích thích, so với bọn họ cả đời gặp đều nhiều hơn."),g("br"),g("br"),n._v(" Vương Đào là ai? Học viện lớp xếp hạng thứ mười thiên tài siêu cấp, lão sư nào không tranh đoạt thu hắn khi học sinh, Thượng Thần trưởng lão cũng là dựa vào chính mình thầy chủ nhiệm thân phận, mới thành công thu được..."),g("br"),g("br"),n._v(" Thiên tài như thế, hơn nữa đường đường chủ nhà họ Vương chính mồm thỉnh cầu, ngươi... Mẹ nhà hắn dĩ nhiên từ chối?"),g("br"),g("br"),n._v(" Thượng Bân các loại người chỉ cảm thấy chịu đến 10 ngàn bị thương hại, đều sắp không thở nổi."),g("br"),g("br"),n._v(" Trước mới vừa mắng xong đối phương chiêu không tới học sinh, kết quả... Nhân gia Vương gia tộc trưởng liền đem nhi tử đưa tới, quan trọng hơn chính là... Hắn còn không thu!"),g("br"),g("br"),n._v(" “Trái tim của ta, ta can...” "),g("br"),g("br"),n._v(" Thượng Thần trưởng lão càng là máu tươi ở yết hầu bên trong chuyển loạn, bất cứ lúc nào cũng sẽ phun mạnh mà ra."),g("br"),g("br"),n._v(" Còn có thiên lý sao? Còn có vương pháp sao?"),g("br"),g("br"),n._v(" Ta là trưởng lão, giáo đạo xử chủ nhiệm, các ngươi lùi đi ta khóa, đi tìm một sư tư sát hạch là số không phân gia hỏa ngược lại cũng thôi, then chốt hắn còn không thu..."),g("br"),g("br"),n._v(" Thượng Thần trưởng lão liền giác đến mặt mũi của chính mình bị người mạnh mẽ kéo xuống đến, vò nát ném xuống đất, hận không thể có cái khe nứt lập tức chui vào."),g("br"),g("br"),n._v(" Quá mất mặt rồi!"),g("br"),g("br"),n._v(" Ném đến mỗ mỗ nhà!"),g("br"),g("br"),n._v(" Một bên mạc tường trưởng lão cũng con ngươi nhanh rơi trên mặt đất."),g("br"),g("br"),n._v(" Vừa nãy đã đối với cái này Trương lão sư có đổi mới, bây giờ nhìn lại, còn chưa đủ!"),g("br"),g("br"),n._v(" “Không thu?” Vương Hoằng tộc trưởng cũng tỏ rõ vẻ lúng túng, lập tức cho nhi tử nháy mắt."),g("br"),g("br"),n._v(" “Kính xin Trương lão sư thu ta vì là học sinh!” "),g("br"),g("br"),n._v(" Vương Đào liền vội vàng tiến lên, ngã quỵ ở mặt đất, một mặt thành khẩn."),g("br"),g("br"),n._v(" Trước hắn xác thực xem thường vị này Trương lão sư, nhưng tận mắt đến muội muội hai chân bị chữa khỏi, sức mạnh tăng lên dữ dội, lại nghĩ lên ngày hôm qua chính mình một chiêu kiếm bị đối phương hai ngón tay kẹp lấy, đáy lòng dĩ nhiên triệt để bội phục."),g("br"),g("br"),n._v(" “Không thu!” Trương Huyền xua tay."),g("br"),g("br"),n._v(" Học sinh lựa chọn lão sư, lão sư cũng muốn tuyển chọn học sinh!"),g("br"),g("br"),n._v(" Cái này Vương Đào ngày hôm trước vọt thẳng tiến vào phòng học, không coi bề trên ra gì, thậm chí còn đối với mình động binh khí, làm sao có khả năng thu làm học sinh!"),g("br"),g("br"),n._v(" Coi như lúc trước Triệu Nhã, chỉ là chất vấn hắn vài câu, như trước bị phạt quét tước vệ sinh!"),g("br"),g("br"),n._v(" Làm lão sư, nếu như không còn uy nghiêm, học sinh đều sẽ xem thường, làm sao có khả năng giáo sư ra thành tích?"),g("br"),g("br"),n._v(" “Ta biết là ta lỗ mãng, xông tới lão sư, còn hi vọng Trương lão sư nể tình ta tuổi trẻ, không hiểu lễ nghi tình huống dưới, không muốn chấp nhặt với ta!” Vương Đào cũng biết nguyên nhân gì, vội hỏi."),g("br"),g("br"),n._v(" Sư nói uy nghiêm không thể nhục, hắn cầm kiếm muốn đi chém người ta, đối phương không tính toán ngược lại cũng thôi, giờ khắc này còn muốn bái ông ta làm thầy, gặp phải từ chối cũng rất bình thường!"),g("br"),g("br"),n._v(" “Đúng đấy, tiểu nhi lúc trước không biết lão sư đại tài, vì lẽ đó có thể có chút kích động, hi vọng lão sư thứ lỗi...” Vương Hoằng tộc trưởng cũng liền bận bịu giải thích."),g("br"),g("br"),n._v(" “Vương tộc trưởng không cần phải nói những này, cái này Trương lão sư không thu, ta cảm thấy không phải là bởi vì cái này!” "),g("br"),g("br"),n._v(" Đang lúc này, một thanh âm không đúng lúc hưởng lên."),g("br"),g("br"),n._v(" Theo âm thanh nhìn lại, chính là trước học tâm tra hỏi thất bại Tào Hùng."),g("br"),g("br"),n._v(" Nếu như là vừa nãy, dựa vào Thượng Thần trưởng lão uy nghiêm, có thể giúp hắn trung hoà xử phạt, thậm chí nghĩ biện pháp một tha lại tha, đem cá cược làm nhạt, mà hiện tại, đường đường Vương Hoằng tộc trưởng đều lại đây cầu Trương Huyền, học tâm tra hỏi thất bại tiền đặt cược, e sợ lại không xong rồi!"),g("br"),g("br"),n._v(" Ngược lại đều thành kết quả này, hắn cũng không để ý đem da mặt xé càng phá."),g("br"),g("br"),n._v(" “Hả?” Nghe có người nói chen vào, Vương Hoằng tộc trưởng sầm mặt lại, có chút không cao hứng."),g("br"),g("br"),n._v(" “Cái này Trương Huyền bản thân thực lực chỉ có võ giả ba tầng Chân Khí cảnh, mà Vương Đào thiếu gia, nhưng là bốn tầng bì cốt cảnh cường giả, thực lực cũng không bằng đối phương, làm sao giáo sư, làm sao chỉ điểm? Hắn không dám nhận được, là có tự mình biết mình!” "),g("br"),g("br"),n._v(" Tào Hùng khuôn mặt dữ tợn quát."),g("br"),n._v("  “Không sai, hắn liền thực lực không bằng Vương Đào thiếu gia, còn thu hắn làm học sinh, đùa gì thế!” Thượng Bân cũng phản ứng lại, vội vàng nói."),g("br"),g("br"),n._v(" “Vương tộc trưởng cân nhắc, ngươi lùi đi ta khóa không quan trọng lắm, lại làm cho Vương Đào thiếu gia bái một cái thực lực so với hắn còn thấp người sư phụ, sau đó tu luyện tới e sợ cũng không dễ như vậy!” Thượng Thần trưởng lão ánh mắt cũng là lóe lên."),g("br"),g("br"),n._v(" Đúng vậy, trước mắt cái này Trương Huyền, thực lực chỉ có võ giả ba tầng Chân Khí cảnh, đây là toàn bộ học viện người người đều biết sự thực."),g("br"),g("br"),n._v(" Đường đường bốn tầng cường giả, đi bái một cái tu vi so với chính mình thấp người sư phụ, bản thân liền là trò cười!"),g("br"),g("br"),n._v(" “Chuyện này...” "),g("br"),g("br"),n._v(" Vương Hoằng tộc trưởng trong lòng “Hồi hộp!” Một thoáng."),g("br"),g("br"),n._v(" Hắn chỉ từ Vương Dĩnh trong miệng nghe nói Trương Huyền dạy học lý luận kinh người, cũng không biết hắn xác thực thiết tu vi."),g("br"),g("br"),n._v(" Vương Đào mặc dù đối với từng ra tay, nhưng từ không quá, hắn cũng không rõ ràng."),g("br"),g("br"),n._v(" Đối phương nói nếu như là thật sự, vị này Trương lão sư thật sự chỉ có võ giả ba tầng Chân Khí cảnh thực lực, vẫn đúng là không cách nào giáo dục Vương Đào!"),g("br"),g("br"),n._v(" Lão sư tuy rằng nắm giữ lý luận liền có thể truyền thụ, nhưng tu luyện chỉ có tự mình trải qua tài năng cảm ngộ càng sâu, nếu như lão sư chính mình cũng không đạt đến, e sợ cũng dạy không ra thật phương pháp."),g("br"),g("br"),n._v(" Đây giống như là Trương Huyền kiếp trước giáo sư thụ đề, này nói đề, lão sư đều sẽ không làm, làm sao có khả năng Giáo Hội học sinh?"),g("br"),g("br"),n._v(" Không ít chuyện cần lão sư tiên tri, mới có thể để học sinh sau biết."),g("br"),g("br"),n._v(" Bằng không, cho dù tốt lý luận, cũng là hoa trong gương, trăng trong nước, không có một chút tác dụng nào."),g("br"),g("br"),n._v(" “Trương Huyền, ngươi coi như muốn giáo Vương Đào thiếu gia, có thể ngươi có thực lực này sao? Không có liền thiếu giả vờ giả vịt, võ giả ba tầng, lừa gạt một ít mới vừa vào học tân sinh còn có thể, đối mặt học sinh cũ liền không xong rồi!” Thấy Trương Huyền không lên tiếng, Tào Hùng hưng phấn ánh sáng lấp loé, trong mắt tràn đầy dữ tợn."),g("br"),g("br"),n._v(" “Đúng đấy, Vương tộc trưởng, Tào Hùng lão sư nói rất có lý, Trương Huyền chính mình cũng không đột phá võ giả bốn tầng, tự nhiên cũng không biết làm sao tu luyện, làm sao có thể chỉ điểm bì cốt cảnh tu luyện!” Thượng Thần trưởng lão lần thứ hai tìm về tự tin, vuốt râu, nói."),g("br"),g("br"),n._v(" “Không đúng, các ngươi nói đều không đúng!” Nghe được mọi người cười nhạo, Vương Đào không làm, liền vội vàng đứng dậy giải thích: “Ai nói Trương lão sư thực lực không bằng ta? Ngày hôm qua ta vô ý mạo phạm, lúc đó toàn lực một chiêu kiếm đánh xuống, bị hắn hai ngón tay nhẹ nhàng kẹp lấy, nếu như chỉ là Chân Khí cảnh, khẳng định không làm được!” "),g("br"),g("br"),n._v(" “Hai ngón tay kẹp lấy? Ha ha, Vương Đào thiếu gia, ngươi coi như muốn cho hắn lưu chút mặt mũi, cũng phải nói cái đáng tin một điểm có được hay không?” "),g("br"),g("br"),n._v(" Tào Hùng cười gằn: “Ngươi là bốn tầng bì cốt cảnh hậu kỳ cường giả, sức mạnh vượt quá 700 kg, hơn nữa kiếm tốc độ cùng trọng lượng, một chiêu đánh xuống, coi như ích huyệt cảnh sơ kỳ cường giả muốn tay không tiếp được, đều rất khó khăn, chớ nói chi là hai ngón tay... Ngươi sẽ không nói, chúng ta vị thầy giáo này sát hạch đến Linh phân Trương lão sư, đã đạt đến ích huyệt cảnh, cùng trưởng lão như thế đi!” "),g("br"),g("br"),n._v(" Kiếm tuy rằng nhẹ nhàng, nhưng bì cốt cảnh hậu kỳ cường giả toàn lực đánh xuống, tốc độ nhanh như chớp giật, không đối với kiếm pháp của đối phương hiểu rõ cực sâu, biết công kích xác thực thiết phương vị, ích huyệt cảnh sơ kỳ cường giả đều rất khó tiếp được... Vương Đào lại còn nói học viện kém cỏi nhất lão sư tiện tay tiếp được... Đùa gì thế!"),g("br"),g("br"),n._v(" “Ta cũng nhìn thấy, lão sư là dùng hai ngón tay nắm!” Lưu Dương cũng gấp bận bịu giải thích."),g("br"),g("br"),n._v(" Ngày đó cảnh tượng, tận mắt nhìn thấy, không có nửa phần giả tạo."),g("br"),g("br"),n._v(" Thế nhưng, hắn đối với Trương Huyền độ tín nhiệm như thế cao, nói chưa dứt lời, nói chuyện, người ở bên ngoài xem ra, chính là đang giúp che lấp."),g("br"),g("br"),n._v(" “Vương Hoằng tộc trưởng, ngươi là tu luyện đại gia!” Không để ý tới hai người giải thích, Thượng Thần trưởng lão cười lạnh: “Ngươi giác không chiếm được ích huyệt cảnh trung kỳ, có thể sử dụng hai ngón tay tiếp được Vương Đào thiếu gia toàn lực một chiêu kiếm?” "),g("br"),g("br"),n._v(" “Chuyện này...” Vương Hoằng tộc trưởng dừng lại."),g("br"),g("br"),n._v(" Hai ngón tay nắm Vương Đào toàn lực một chiêu kiếm, bình thường ích huyệt cảnh cường giả, xác thực rất khó hoàn thành!"),g("br"),g("br"),n._v(" Cái này Trương lão sư tuy rằng lý luận rất tốt, nhưng trẻ tuổi như vậy liền đạt đến ích huyệt cảnh trung kỳ... Liền ngay cả hắn cũng không tin."),g("br"),g("br"),n._v(" Dù sao, quá mức kinh thế hãi tục rồi!"),g("br"),g("br"),n._v(" “Vương Đào thiếu gia, ta biết ngươi muốn bái Trương Huyền sư phụ, lúc này mới cố ý nói những này tăng cao hắn danh vọng, phàm là sự phải có độ, vượt quá quá nhiều, cũng không tốt...” Thấy Vương Hoằng ách trụ, Thượng Thần trưởng lão bàn tay lớn vẫy một cái, khí độ Lăng Vân, chỉ trích phương tù, chính muốn tiếp tục chậm rãi mà nói, nói một ít cao thâm lý luận, chứng minh hắn là nói dối, liền nghe đến bên ngoài phòng, trách nhiệm tiếng của lão sư lần thứ hai hưởng lên."),g("br"),g("br"),n._v(" “Thượng Trường Lão!” "),g("br"),g("br"),n._v(" “Chuyện gì?” Hơi nhướng mày, Thượng Thần trưởng lão tràn đầy không cao hứng."),g("br"),g("br"),n._v(" Không thấy ta chính đang tinh tướng sao?"),g("br"),g("br"),n._v(" Thật không ánh mắt!"),g("br"),g("br"),n._v(" “Bên ngoài có người cầu kiến Trương Huyền lão sư, tìm tới đây rồi...” Trách nhiệm lão sư vội hỏi."),g("br"),g("br"),n._v(" “Tìm Trương Huyền? Ai?” "),g("br"),g("br"),n._v(" Ngày hôm nay đều là làm sao?"),g("br"),g("br"),n._v(" Cái này cũng tìm, cái kia cũng tìm?"),g("br"),g("br"),n._v(" “Vâng... Trấn Nam vương Thế Tử, Bạch Tốn Tiểu vương gia!” Trách nhiệm lão sư nói."),g("br"),g("br"),n._v("Convert by: Yuruki"),g("br"),g("br"),g("br"),g("br")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("a",{attrs:{href:"./chap-78"}},[n._v("Chương trước")]),n._v("  |  "),g("a",{attrs:{href:"./chap-80"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);