(window.webpackJsonp=window.webpackJsonp||[]).push([[2065],{2419:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-781-truyen-thu-đao-phap"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-781-truyen-thu-đao-phap"}},[n._v("#")]),n._v(" Chương 781: Truyền thụ đao pháp")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 781: Truyền thụ đao pháp"),t("br"),t("br"),n._v("“Canh cổng?” Ưng Cần, Bạch Diện đám người tất cả đều nắm đấm xiết chặt, tức giận đến sắp nổ tung."),t("br"),t("br"),n._v("Bọn họ là năm thứ tư học viên, Đạp Hư cảnh cường giả đỉnh phong, một cái tân sinh thế mà để bọn hắn canh cổng?"),t("br"),t("br"),n._v("Đây là tiền đặt cược ư?"),t("br"),t("br"),n._v("Cố ý đến làm nhục chúng ta đi!"),t("br"),t("br"),n._v("“Vừa rồi không còn nói sẽ không thua ư? Không thua cũng sẽ không cần lo lắng giữ cửa sự tình, làm sao, vậy thì không có tự tin?” Mỉm cười, Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Đổi điều kiện đi!”"),t("br"),t("br"),n._v("Khuôn mặt xanh xám, Ưng Cần hất lên ống tay áo."),t("br"),t("br"),n._v("Coi như sẽ không thua, cùng người đánh cái này cược, cũng mất mặt ah!"),t("br"),t("br"),n._v("“Không cần xấu hổ, sợ thua cũng rất bình thường, không có gì có thể mất mặt, nhân chi thường tình.” Trương Huyền trấn an nói."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Không nghe lời này còn tốt, nghe được về sau, Ưng Cần, Bạch Diện các loại người tức thiếu chút nữa không có hộc máu."),t("br"),t("br"),n._v("Cái gì gọi là xấu hổ? Cái gì gọi sợ thua cũng bình thường?"),t("br"),t("br"),n._v("Chúng ta là sẽ không thua, chẳng qua là cảm thấy điều kiện của ngươi có chút quá mức..."),t("br"),t("br"),n._v("Chính khí muốn phản bác hai câu, liền thấy phía trước thanh niên, cúi đầu trầm tư một chút: “Như vậy đi, nếu như các ngươi vẫn là sợ thua, ta không ra tay, tùy ý chỉ điểm mấy cái tân sinh cùng các ngươi chiến đấu, không được nữa, liền chỉ điểm... Mới vừa rồi bị ngươi đánh bại mấy người, như vậy, cuối cùng không cần lo lắng đi!”"),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Thấy cái tên này, càng nói càng thái quá, tựa hồ là đang cố ý nhục nhã bọn họ, Ưng Cần cũng nhịn không được nữa, cắn răng một cái: “Tốt, chúng ta đáp ứng! Chỉ mong thực lực của ngươi, cũng cùng miệng lưỡi đồng dạng lợi hại!”"),t("br"),t("br"),n._v("“Đáp ứng là tốt rồi, Nhược Hoan, Tống Siêu, phái mấy người qua bên kia tu đại môn, nếu không canh cổng đến có, không có đại môn, có thể không làm được!” Thấy đối phương đồng ý, Trương Huyền hài lòng gật đầu, phân phó một câu."),t("br"),t("br"),n._v("Khu tân sinh, mặc dù có ra vào cửa, nhưng không có đúng nghĩa đại môn, đều đánh cược, mấy tên này, một khi thua, không có vị trí an bài, nhiều xấu hổ ah."),t("br"),t("br"),n._v("“Vâng!” Nhược Hoan, Tống Siêu đám người, liền vội vàng gật đầu đáp ứng."),t("br"),t("br"),n._v("“Tự tìm cái chết!”"),t("br"),t("br"),n._v("Thấy cái tên này miệt thị như vậy bọn họ, vừa bắt đầu đánh cược, đã cảm thấy bọn họ nhất định phải thua, Bạch Diện cũng nhịn không được nữa, đen kịt mặt, trở nên có chút dữ tợn, có chút chòm râu cũng vểnh lên lên, đại đao trong tay lắc một cái, phát ra ông ông nổ vang, nhìn về phía Ưng Cần: “Để cho ta tới giáo huấn cái tên này một trận!”"),t("br"),t("br"),n._v("Nói xong, liền trực tiếp nhảy đến chính giữa đài cao, ánh mắt như điện nhìn lại: “Trương Huyền, trận đầu ta cùng ngươi chiến đấu, có thể dám đánh với ta một trận?”"),t("br"),t("br"),n._v("“Đừng có gấp! Người trẻ tuổi chính là không giữ được bình tĩnh!” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("“Ngươi...” Lần nữa tức giận nhoáng một cái, Bạch Diện mặt trở nên càng thêm ngăm đen, đều nhanh thành đáy nồi."),t("br"),t("br"),n._v("Chúng ta thoạt nhìn tuổi trẻ, đều vượt qua năm mươi tuổi được không? Ngược lại là ngươi, chừng hai mươi tuổi, thế mà trực tiếp mở miệng xưng hô học trưởng là người trẻ tuổi, có xấu hổ hay không?"),t("br"),t("br"),n._v("“Ngươi chẳng lẽ chỉ có miệng lưỡi? Là nam nhân liền lên đến!”"),t("br"),t("br"),n._v("Biết miệng lưỡi khẳng định không bằng người thanh niên này, Bạch Diện răng cắn chặt."),t("br"),t("br"),n._v("“Ta đi lên ngươi khẳng định thất bại, ta không khi dễ người, vừa rồi đều nói rồi, tìm mấy cái tân sinh cùng ngươi đối chiến, cũng sẽ không béo nhờ nuốt lời!”"),t("br"),t("br"),n._v("Trương Huyền khoát tay áo, không tiếp tục để ý trên đài sắp nổi điên Bạch Diện, nhìn quanh một tuần, hướng trước mắt rất nhiều tân sinh nhìn sang: “Trong các ngươi ai dùng đao? Hơn nữa vừa rồi thua ở cái tên này?”"),t("br"),t("br"),n._v("Nghe được tra hỏi, mọi người tất cả đều hai mặt nhìn nhau."),t("br"),t("br"),n._v("Vốn cho rằng Trương sư là cố ý tức giận đối phương, chẳng lẽ, đến thật?"),t("br"),t("br"),n._v("Vừa rồi đều đã thua, coi như thương thế không nặng, cũng đánh không lại ah!"),t("br"),t("br"),n._v("“Ta... Vừa rồi thua!”"),t("br"),t("br"),n._v("Dừng lại một lát, một thanh niên đi ra."),t("br"),t("br"),n._v("Là cái hai lăm hai sáu tuổi tân sinh, chỉ có Hợp Linh cảnh sơ kỳ, trên người một vết đao chém, mặc dù không nghiêm trọng, nhưng thoạt nhìn dữ tợn đáng sợ, tựa hồ đối phương hoàn toàn có thể đem hắn chém thành hai khúc, hạ thủ lưu tình, mới dùng may mắn thoát khỏi."),t("br"),t("br"),n._v("“Không sai!” Trương tử quan sát kỹ một vòng, huyền nhẹ gật đầu: “Liền ngươi, một lát nữa, đi lên cùng cái tên này lại đánh một trận, đem hắn đánh bại!”"),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Thanh niên giật nảy mình, mặt mũi xoắn xuýt: “Ta không phải là đối thủ, liền một chiêu đều không có tiếp lấy!”"),t("br"),t("br"),n._v("Vừa rồi cùng đối phương chiến đấu, vừa lên đài, liền đao đều không có giơ lên, liền bị trực tiếp bổ xuống, lại đánh cũng là thua ah!"),t("br"),t("br"),n._v("“Không có việc gì, ta hiện đang dạy ngươi một chiêu đao pháp, một lát nữa lên đài liền phát huy, tranh thủ một chiêu liền đem nó bổ xuống!” Trương Huyền nói."),t("br"),t("br"),n._v("“Một chiêu đao pháp?” Thanh niên sửng sốt."),t("br"),t("br"),n._v("Một chiêu có cái gì dùng?"),t("br"),t("br"),n._v("Đối phương cường đại là võ kỹ, là đối đao pháp lý giải, đừng nói dạy một chiêu, coi như sẽ dạy mười chiêu, một trăm chiêu, nên không thắng được cũng không thắng được ah!"),t("br"),t("br"),n._v("“Không sai, đao cho ta!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Vâng!” Không biết vị hội trưởng này đến cùng làm cái gì, thanh niên mặc dù mặt mũi không rõ ràng cho lắm, vẫn như cũ đem trường đao trong tay đưa tới."),t("br"),t("br"),n._v("“Đáng giận...”"),t("br"),t("br"),n._v("Phía dưới muốn dạy đao pháp, trên đài Bạch Diện tức giận nhanh muốn điên rồi."),t("br"),t("br"),n._v("Một cái tân sinh, lại còn nói đi lên hắn khẳng định thua... Cái này ngược lại cũng thôi, tùy tiện tìm cái bại tướng dưới tay, dạy một chiêu đao pháp, liền muốn vượt qua bản thân?"),t("br"),t("br"),n._v("Nói đùa cái gì?"),t("br"),t("br"),n._v("Bản thân đường đường võ kỹ học viện cao tài sinh, dễ dàng như vậy bị đánh bại, còn có tư cách gì, mệnh danh cường giả?"),t("br"),t("br"),n._v("Không coi ai ra gì!"),t("br"),t("br"),n._v("Quá phách lối, quá cuồng vọng!"),t("br"),t("br"),n._v("Đừng nói một cái tân sinh, coi như võ kỹ học viện viện trưởng, cũng không dám nói như vậy ah!"),t("br"),n._v("“Bạch Diện, đã vị này Trương sư, muốn dạy người mới đao pháp cùng ngươi chiến đấu, chúng ta cũng không chiếm tiện nghi, ngươi đóng lại lục thức, không đi quan sát, đợi hắn truyền thụ hết đao pháp lại nói!”"),t("br"),t("br"),n._v("Ưng Cần cũng tức giận khuôn mặt khó coi, chẳng qua thân là danh sư, không thể rơi mất thân phận, khoát tay áo nói."),t("br"),t("br"),n._v("Đối phương nói muốn dạy đao pháp, bằng cho bọn hắn mượn võ kỹ học viện thiên tài thân phận, thật phải ngay mặt dạy, chỉ sợ cái kia tân sinh, còn không hiểu, bọn họ liền học được, còn thế nào đánh?"),t("br"),t("br"),n._v("Cho dù thắng, cũng sẽ cho người cảm thấy thắng mà không võ."),t("br"),t("br"),n._v("Không bằng không nhìn, không nghe, như vậy trải qua, cái tên này thua, cũng lại nói không nên lời nói nhảm."),t("br"),t("br"),n._v("“Được!” Bạch Diện gật gật đầu, đang muốn đóng kín lục thức, chỉ thấy dưới đài Trương Huyền nhìn lại: “Không cần phiền toái như vậy, các ngươi nếu là võ kỹ học viện mọi người, chắc hẳn đối võ kỹ hiểu rõ cũng rất nhiều, giáo ta thời điểm, đang ngắm nghía cẩn thận, cũng chỉ ra chỗ sai một cái!”"),t("br"),t("br"),n._v("Bạch Diện cùng Ưng Cần nhìn nhau, từng người nháy con mắt."),t("br"),t("br"),n._v("Cái tên này... Đầu óc mới vừa bị cửa chen lấn vẫn là bị lừa đá? Ngươi truyền thụ đao pháp cùng ta chiến đấu, còn để cho ta nhìn?"),t("br"),t("br"),n._v("Còn thế nào đánh?"),t("br"),t("br"),n._v("“Đã hắn để nhìn, vậy liền nhìn, đến lúc đó thua, nhìn hắn còn có lời gì nói!” Đang không biết như thế nào cho phải, Bạch Diện lập tức nghe được Ưng Cần truyền âm."),t("br"),t("br"),n._v("Ba người bọn họ, Ưng Cần cầm đầu, đã hắn nói như vậy, Bạch Diện đành phải gật đầu đồng ý, ánh mắt tập trung, hướng dưới đài đang đang truyền thụ đao pháp thanh niên nhìn sang."),t("br"),t("br"),n._v("“Liền một chiêu, nhìn kỹ!”"),t("br"),t("br"),n._v("Không để ý tới một mặt mộng bức mọi người, Trương Huyền cười cười, trường đao trong tay lắc một cái, nghiêng hướng phía dưới bổ xuống."),t("br"),t("br"),n._v("Đao phát huy rất chậm, như là dùng bút lông trên giấy vẽ tranh."),t("br"),t("br"),n._v("“Cái này gọi đao pháp?”"),t("br"),t("br"),n._v("Bạch Diện ngốc như gà gỗ."),t("br"),t("br"),n._v("Từ khi năm tuổi học đao bắt đầu, liền không có thi triển qua khó coi như vậy chiêu số, cái này căn bản cũng không phải là đao pháp, bình thường chẻ củi, đều muốn so cái này chiêu này tinh diệu thật nhiều lần!"),t("br"),t("br"),n._v("Còn tưởng rằng có lợi hại gì đao pháp, một chiêu có thể chiến thắng bản thân, nằm mơ đều không nghĩ tới, thi triển ra thứ này..."),t("br"),t("br"),n._v("Ngươi đến đùa nghịch ta chơi chứ?"),t("br"),t("br"),n._v("Không chỉ hắn bối rối, chờ lấy Trương Huyền truyền thụ võ kỹ thanh niên, cũng sắp nhanh khóc."),t("br"),t("br"),n._v("Còn tưởng rằng hội trưởng đã tính trước, truyền thụ lợi hại gì chiêu số, không nghĩ tới, liền cái đồ chơi này..."),t("br"),t("br"),n._v("Dùng ánh mắt của hắn, cũng có thể nhìn ra, đao này, cái gì chiêu số đều không có... Liền loại này chém loạn, liền con kiến đều giết không được, thật có thể chiến thắng vừa rồi một chiêu đều không tiếp nổi năm thứ tư học trưởng..."),t("br"),t("br"),n._v("Hắn không có một chút lòng tin."),t("br"),t("br"),n._v("“Đao pháp của ta trọng ý không trọng hình, đây là đao pháp tinh yếu cùng bí ẩn, thật tốt lĩnh ngộ!”"),t("br"),t("br"),n._v("Đang tại buồn bực, không biết Trương sư đang làm cái gì, liền nghe đến một cái thanh âm nhàn nhạt tại vang lên bên tai, ngay sau đó thân thể nhoáng một cái, một cỗ ý niệm truyền lại đi vào trong óc."),t("br"),t("br"),n._v("“Đây là... Linh hồn truyền công?”"),t("br"),t("br"),n._v("Cảm nhận được trong đầu đột nhiên thêm ra tới pháp quyết tu luyện, thanh niên chấn động toàn thân."),t("br"),t("br"),n._v("Linh hồn truyền công nghe nói là Thất Tinh danh sư mới có năng lực, hội trưởng làm sao có thể phát huy?"),t("br"),t("br"),n._v("“Chớ suy nghĩ lung tung, nắm chặt thời gian nhớ kỹ công pháp, nhớ kỹ ta cho ngươi truyền lại đao ý, một lát nữa, dựa theo cái này đao ý phát huy!”"),t("br"),t("br"),n._v("Thấy hắn ngẩn người, Trương Huyền truyền âm trách mắng."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Thanh niên cái này mới phản ứng được, vội vàng hướng trong đầu đao pháp nhìn lại, quả nhiên thấy ẩn chứa trong đó một cỗ đao ý, dâng trào như điện, mang theo sắc bén để cho người ta khó mà chống lại khí tức."),t("br"),t("br"),n._v("“Cái này...” Con ngươi co rụt lại."),t("br"),t("br"),n._v("Hắn mặc dù không có Bạch Diện hiểu biết, nhưng cũng là tu luyện đao pháp danh sư, đương nhiên có thể một cái nhìn ra, cỗ này đao ý mạnh mẽ."),t("br"),t("br"),n._v("Chỉ cần bắt chước, tuyệt đối có thể để cho hắn đối đao lý giải, tiến thêm mấy bước, đạt tới để cho người ta sợ hãi than tình trạng."),t("br"),t("br"),n._v("Đem công pháp tại thể nội vận dạo qua một vòng, đao ý điều động, hai mắt như là có lưỡi đao tránh qua, thanh niên hít sâu một hơi, khom người đến cùng: “Đa tạ hội trưởng!”"),t("br"),t("br"),n._v("“Ừm, đi thôi, đem cái tên này đánh bại!”"),t("br"),t("br"),n._v("Trương Huyền khoát tay áo."),t("br"),t("br"),n._v("Bạch Diện đắm chìm trong đao pháp bên trong không biết bao nhiêu năm, coi như truyền thụ bản rút gọn Thiên Đạo đao pháp, muốn thời gian ngắn vượt qua, cũng rất khó, không có cách, đành phải truyền thụ đao ý."),t("br"),t("br"),n._v("Vũ khí chân ý, vừa ý sẽ không thể nói bằng lời, đổi lại những người khác, coi như lĩnh ngộ, cũng khó có thể truyền lại."),t("br"),t("br"),n._v("Nhưng Trương Huyền tu luyện chính là trời đạo đao pháp, đại đạo chí giản, đao ý Hỗn Nguyên, không có chút nào tì vết, lại thêm vu hồn sư thủ đoạn, truyền đưa tới, làm cho đối phương mượn nhờ sử dụng, còn có thể làm được."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Hít sâu một hơi, thanh niên nhẹ gật đầu, thân thể nhảy lên, đi tới đài cao."),t("br"),t("br"),n._v("Nếu như nói vừa rồi đối mặt vị này Bạch Diện học trưởng, còn có chút nhát, cảm thấy không có khả năng chiến thắng, mà bây giờ, trong cơ thể dựng dục Trương Huyền đao ý, không còn chút nào nữa e ngại."),t("br"),t("br"),n._v("“Liền ngươi cũng muốn thắng ta?”"),t("br"),t("br"),n._v("Thấy cái tên này, thật học được một chiêu đốn củi cũng không bằng đao pháp, liền lên tới khiêu chiến, Bạch Diện tức giận sắp nổ tung, cố nén lửa giận, khẽ nói."),t("br"),t("br"),n._v("“Đắc tội!”"),t("br"),t("br"),n._v("Thanh niên gật gật đầu, trường đao lắc một cái, một cỗ muốn cắt ra Trường Thiên khí tức, đột nhiên sinh ra, ngay sau đó một đao bổ xuống."),t("br"),t("br"),n._v("Ào!"),t("br"),t("br"),n._v("Hắn cứ việc nhưng mà Hợp Linh cảnh sơ kỳ thực lực, đao mang nhưng như là hồ quang điện, trong nháy mắt lan tràn mấy trượng khoảng cách, thời gian nháy mắt đi tới Bạch Diện gần, giống như là muốn đem không khí đều xé rách."),t("br"),t("br"),n._v("“Đao sinh điện mang... Đây là, đây là lĩnh ngộ đao tâm, mới có năng lực? Làm sao có thể...”"),t("br"),t("br"),n._v("Bạch Diện thân thể run lên, con mắt một cái trừng sắp rơi ra hốc mắt, lập tức cảm thấy trong tai một hồi nổ vang, tựa như kinh lôi tại vang lên bên tai, bất cứ lúc nào cũng sẽ nổ tung."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-789"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-791"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);