(window.webpackJsonp=window.webpackJsonp||[]).push([[207],{561:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1163-tan-quan-gia-giai-đoc-phuong-phap"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1163-tan-quan-gia-giai-đoc-phuong-phap"}},[n._v("#")]),n._v(" Chương 1163: Tần quản gia giải độc phương pháp")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Ngươi biết?” Sở Thiên Hành cùng Trung Thanh Vương có chút kỳ quái nhìn qua."),t("br"),t("br"),n._v("Đường đường thất tinh đỉnh phong Tôn y sư đều không có tìm tới phương pháp, bản thân ngược lại bị độc choáng, ngươi một cái cái gì y thuật cũng đều không hiểu quản gia, làm sao có thể biết?"),t("br"),t("br"),n._v("“Ừm, trước đó ta cũng trúng qua loại độc này, lúc này đã hoàn toàn chế trụ...” Tần quản gia gật đầu."),t("br"),t("br"),n._v("“Vậy thì tốt quá, còn không gấp mau giúp ta trị liệu...”"),t("br"),t("br"),n._v("Nghe được chính mình cái này thuộc hạ, thế mà cũng bị Trương Huyền hạ độc, còn chữa khỏi, Trung Thanh Vương vội vàng hô."),t("br"),t("br"),n._v("“Vâng! Có điều, ta trị liệu, có thể sẽ tương đối đau, mong rằng vương gia kiềm chế một chút...” Tiến về phía trước một bước, Tần quản gia một mặt ngưng trọng."),t("br"),t("br"),n._v("“Bổn vương kinh nghiệm sinh tử, dạng gì thống khổ không bị qua, yên tâm!” Trung Thanh Vương lông mi bên trong mang theo tự tin và uy nghiêm."),t("br"),t("br"),n._v("Năm đó tại chiến trường cửu tử nhất sinh còn không sợ, giải độc mà thôi, lại thống khổ, còn có thể thống khổ đi nơi nào?"),t("br"),t("br"),n._v("“Chỉ cần có thể giải độc, ta lập tức phong ngươi quản gia của vương phủ, quản lý tất cả mọi người...”"),t("br"),t("br"),n._v("Trung Thanh Vương nói tiếp, lời còn chưa dứt, ngay sau đó liền thấy một cái bàn tay phá không mà tới."),t("br"),t("br"),n._v("Đùng!"),t("br"),t("br"),n._v("Một chút quất vào trên mặt của mình, nóng bỏng giống như là muốn đem khuôn mặt xé rách thành hai nửa."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Không nghĩ tới cái này thuộc hạ, không giúp đỡ trị liệu, ngược lại trực tiếp tát bạt tai, Trung Thanh Vương sắp điên rồi, tức giận đến một tiếng giận dữ, thanh âm còn không có kết thúc, lại nhìn thấy đối phương một cái khác bàn tay mạnh mẽ rút xuống."),t("br"),t("br"),n._v("Đùng! Đùng! Đùng! Đùng!"),t("br"),t("br"),n._v("Liên tục mấy bàn tay, Trung Thanh Vương khuôn mặt, lập tức sưng đỏ lên, tròn như là bột lên men màn thầu, tràn đầy máu."),t("br"),t("br"),n._v("“Vương gia, cảm giác khá hơn chút nào không?”"),t("br"),t("br"),n._v("Liên tục mấy bàn tay, đã dùng hết toàn lực, quất Tần quản gia, mệt thở hồng hộc, thận trọng nhìn qua."),t("br"),t("br"),n._v("“Tốt em gái ngươi, lão tử, muốn giết ngươi...”"),t("br"),t("br"),n._v("Ánh mắt đỏ như máu, Trung Thanh Vương đột nhiên đứng dậy, một bộ muốn giết người biểu lộ."),t("br"),t("br"),n._v("Thanh Nguyên đế quốc đầu tiên vương gia, cao như vậy thân phận, thế mà bị thuộc hạ của mình, ngay trước Hoàng đế mặt tát bạt tai, mấu chốt hút xong, còn hỏi... Tốt một chút ư?"),t("br"),t("br"),n._v("Thật giống như hiếp dâm kết thúc, đang hỏi “Thoải mái ư”."),t("br"),t("br"),n._v("Thoải mái mẹ ngươi ah!"),t("br"),t("br"),n._v("Lão tử đánh chết ngươi cái cháu con rùa..."),t("br"),t("br"),n._v("Trung Thanh Vương khí sắp nổ."),t("br"),t("br"),n._v("“Còn chưa được? Vương gia đừng có gấp, ta còn có biện pháp tốt hơn...”"),t("br"),t("br"),n._v("Ổn định nổi giận Trung Thanh Vương, Tần quản gia cắn răng một cái."),t("br"),t("br"),n._v("“Càng dễ làm hơn pháp?” Trung Thanh Vương sửng sốt một chút, đang nghĩ nói chuyện, liền thấy Tần quản gia đã thoát giày, nâng lên tản ra đặc thù mùi bàn chân, trực tiếp đối với khuôn mặt đạp tới."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Còn chưa kịp phản ứng, toàn bộ mặt liền quay cong thành bánh quai chèo, lăng không xoay tròn hai vòng nửa, tầng tầng ngã xuống đất."),t("br"),t("br"),n._v("Đây là Tần quản gia, quất mặt hiệu quả trị liệu không tốt về sau, tìm người thí nghiệm qua, hiệu quả hết sức rõ ràng, một chân đi xuống, liền có hiệu quả."),t("br"),t("br"),n._v("Vốn thuộc về bí pháp, không dám nói cho người khác biết, đã vương gia cũng trúng độc, tự nhiên mà vậy cầm đi ra."),t("br"),t("br"),n._v("“Ta giết ngươi...”"),t("br"),t("br"),n._v("Nhìn xem trên mặt đất ngã xuống hàm răng, ngửi được trên mặt thối hoắc hương vị, Trung Thanh Vương điên thật rồi."),t("br"),t("br"),n._v("Để cái này thuộc hạ giải độc, không những không cho dược vật, ngược lại lại là bàn tay, lại là bàn chân... Quả thực tội không thể tha!"),t("br"),t("br"),n._v("Đột nhiên đứng dậy, khí tức trên thân như rồng như hổ, hai mắt như điện, hận không thể lập tức liền đem trước mắt vị này cứ thế mà xé thành hai nửa."),t("br"),t("br"),n._v("Còn chưa kịp động thủ, liền nghe đến một bên Hoàng đế bệ hạ thanh âm kinh ngạc vang lên: “Trung Thanh Vương, ngươi độc... Giải?”"),t("br"),t("br"),n._v("“Độc?”"),t("br"),t("br"),n._v("Nhịn không được sửng sốt một chút, hình như bị đối phương bàn tay cùng bàn chân liên tục công kích đến, kịch độc mang tới đau đớn, dường như thật yếu bớt, hơn nữa càng ngày càng ít."),t("br"),t("br"),n._v("“Cái này... Đến cùng chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Cố nén đem trước mắt vị này Tần quản gia, xé rách thành hai nửa xúc động, Trung Thanh Vương hỏi."),t("br"),t("br"),n._v("“Bẩm báo vương gia, ngày hôm nay ngươi để cho ta đi đem vị kia Trương Huyền mang tới, trong lời nói xuất hiện chút mâu thuẫn, hắn liền lặng lẽ cho ta hạ độc... Loại độc này, ta tìm người nhìn, không phát hiện được không nói, còn có thể ăn mòn nội tạng, để cho người ta thống khổ kinh khủng... Bất quá, hắn nói với ta giải độc phương pháp... Tát bạt tai! Quất càng hung ác, hiệu quả trị liệu càng nhanh, cho nên... Thuộc hạ mới cả gan đối vương gia ra tay, còn xin vương gia thứ lỗi!”"),t("br"),t("br"),n._v("Tần quản gia ôm quyền, giải thích cặn kẽ một lần."),t("br"),t("br"),t("br"),n._v("“Tát bạt tai giải độc? Còn có loại độc dược này?”"),t("br"),t("br"),n._v("Trung Thanh Vương cùng Sở Thiên Hành liếc mắt nhìn nhau, mặt mũi khiếp sợ, như là gặp quỷ đồng dạng biểu lộ."),t("br"),t("br"),n._v("Loại độc này, đừng nói chưa thấy qua, nghe đều không có nghe qua."),t("br"),t("br"),n._v("“Vâng!” Tần quản gia gật đầu."),t("br"),t("br"),n._v("“Chế biến ra loại độc dược này, cái này Trương Huyền quả thực đáng ghét...”"),t("br"),t("br"),n._v("Khí bờ môi run rẩy, Trung Thanh Vương đang nghĩ tiếp tục nhục mạ, lần nữa cảm thấy thân thể run lên, kịch liệt đau nhức lần nữa đánh tới."),t("br"),t("br"),n._v("“Không tốt, lại muốn phát tác... Nhanh, nhanh lên một chút!”"),t("br"),t("br"),n._v("Vừa nghĩ tới kịch độc mang tới đau đớn, Trung Thanh Vương liền toàn thân phát run, vội vàng đi tới Tần quản gia trước mặt, đem mặt duỗi tới, hàm răng cắn chặt, tràn đầy khát vọng: “Dùng sức!”"),t("br"),n._v(" ..."),t("br"),t("br"),n._v("“Quả nhiên hữu dụng...”"),t("br"),t("br"),n._v("Vừa đi ra Danh Sư đường Trương Huyền, nhẹ nhàng cười một tiếng."),t("br"),t("br"),n._v("Cho Trung Thanh Vương rót rượu ngon thời điểm, liền lặng lẽ lưu lại một đạo ý thức, giấu ở đối phương trong cơ thể, núp ở Thiên Đạo chân khí bên trong, liền xem như Nguyên Thần cảnh cường giả cũng không cách nào phát giác."),t("br"),t("br"),n._v("Tại đối phương nhục mạ mình thời điểm phát động, để Thiên Đạo chân khí biến thành kịch độc, ở trong người tàn phá bừa bãi, nguyên nhân chính là lưu lại như vậy hậu chiêu, mới không chậm trễ chút nào đem hắn giao cho Hoàng đế bệ hạ."),t("br"),t("br"),n._v("Dù sao sinh tử đều tại bản thân một ý niệm, mang đi không mang đi cũng không tính là cái gì."),t("br"),t("br"),n._v("Vốn, lưu lại cái kia Tần quản gia chỉ là làm một quân cờ sử dụng, không có ý định có cái gì tác dụng quá lớn, không nghĩ tới cái tên này thế mà tin tưởng bản thân “Quất mặt trị liệu pháp”..."),t("br"),t("br"),n._v("Như vậy ngược lại làm cho bản thân tiết kiệm không ít phiền phức, trước hết để cho vị này Trung Thanh Vương ăn nhiều chút đau khổ lại nói."),t("br"),t("br"),n._v("Tại ý thức của hắn khống chế bên dưới, mặc dù quất mặt trị liệu pháp, thoạt nhìn có thể trị Thiên Đạo chân khí mang tới ăn mòn, nhưng cũng là có thể lặp đi lặp lại..."),t("br"),t("br"),n._v("Quất thời điểm “Giải độc”, không quất thời điểm phát tác... Như vậy chuyện thú vị, hắn có thể chơi tốt nhất mấy ngày."),t("br"),t("br"),n._v("Bất quá, cũng không biết vị này Trung Thanh Vương, có thể hay không no lâu như vậy..."),t("br"),t("br"),n._v("Cùng giấu ở đối phương trong cơ thể đạo ý thức kia trao đổi một hồi, Trương Huyền liền không tiếp tục để ý, theo sát tại Ngô sư sau lưng, thời gian không dài, đi tới một cái to lớn chợ giao dịch chỗ."),t("br"),t("br"),n._v("“Đây không phải giám bảo sư công hội vị trí ư?”"),t("br"),t("br"),n._v("Liếc mắt nhìn hai phía, xác định vị trí, Trương Huyền khó hiểu."),t("br"),t("br"),n._v("Căn cứ Hình đường chủ nói với hắn vị trí, nơi này hẳn là giám bảo sư công hội vị trí, thế nào lại là cái đại thương tràng?"),t("br"),t("br"),n._v("“Giám bảo sư công hội đặc tính ngươi cũng rõ ràng, chỉ cần thành lập, chung quanh khẳng định sẽ diễn biến thành thị trường, đây là chuyện không có cách nào khác!”"),t("br"),t("br"),n._v("Ngô sư cười nói."),t("br"),t("br"),n._v("“Như thế...”"),t("br"),t("br"),n._v("Trương Huyền nhẹ gật đầu."),t("br"),t("br"),n._v("Giám bảo sư có thể phân biệt bảo vật là thật hay giả, chức nghiệp đặc thù, công hội vừa thành lập, liền có liên tục không ngừng người tới giám định, nhận được giấy chứng nhận, tương đương với có bảo đảm chất lượng, dễ dàng bán ra, bởi vậy, tới đây mua đồ, so với trước địa phương khác càng nhiều... Dần dà, công hội chung quanh, liền hình thành to lớn thương hội, tạo thành mậu dịch trung tâm."),t("br"),t("br"),n._v("“Đúng rồi, ngươi nói phẩm giám hội, là chuyện gì xảy ra? Vì cái gì thư họa sư công hội hội trưởng cũng tới?”"),t("br"),t("br"),n._v("Trương Huyền hỏi tiếp."),t("br"),t("br"),n._v("Nếu như chỉ là đánh giá bảo vật, giám bảo sư công hội sẽ mọc ra hiện cũng không khó hiểu, thư họa sư công hội hội trưởng cũng xuất hiện có ý tứ gì?"),t("br"),t("br"),n._v("“Muốn đánh giá bảo vật, là hai ngàn năm trước không buồn lão nhân, lưu lại một bức tranh chữ.”"),t("br"),t("br"),n._v("Ngô sư nói."),t("br"),t("br"),n._v("“Không buồn lão nhân?”"),t("br"),t("br"),n._v("“Hắn là Thanh Nguyên đế quốc, từ trước tới nay nổi danh nhất thư họa sư, thiên phú cực cao, không đủ ba trăm tuổi, đã đạt đến bát tinh cấp bậc... Chỉ tiếc, tại vừa mới ăn mừng hết ba trăm tuổi thọ đản về sau ngày thứ ba, biến mất không thấy hình bóng, cụ thể đi nơi nào, ai cũng không biết! Đoán chừng là bị người hại.”"),t("br"),t("br"),n._v("Ngô sư lắc đầu."),t("br"),t("br"),n._v("“Ba trăm tuổi? Loại này tuổi tác đối với cường giả Thánh vực tới nói, không tính lớn đi, vì sao phải kêu... Không buồn lão nhân?” Trương Huyền không hiểu."),t("br"),t("br"),n._v("Bất kể Hình đường chủ còn là Ngô sư, đều ít nhất vượt qua năm trăm tuổi, ba trăm tuổi... Tại Thánh vực bên trong xem như rất trẻ trung, xưng hô thế này..."),t("br"),t("br"),n._v("“Sở dĩ tự xưng không buồn lão nhân, cùng bản thân kinh nghiệm liên quan đến, nghe nói, lúc còn trẻ, thiên phú, không lộ ra trước mắt người đời, mãi cho đến hơn một trăm tám mươi tuổi, Thánh vực đều không có đạt tới, vốn cho rằng chẳng mấy chốc sẽ tử vong, ai biết không biết làm sao đột nhiên khai khiếu, thư hoạ thiên phú như là quỷ thần phụ thể, ngắn ngủi mười năm công phu, liền đạt đến thất tinh cấp bậc, nhờ vào đó, tu vi cũng đột phá Thánh vực... Bởi vì đột phá thời điểm lớn tuổi, thoạt nhìn mười phần già nua, toại lấy lão nhân tự cho mình là.”"),t("br"),t("br"),n._v("Nhớ tới vị này thư hoạ đại tông sư cuộc đời, Ngô sư giải thích."),t("br"),t("br"),n._v("“Thì ra là thế...”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Hóa Phàm cảnh tuổi thọ, cũng liền tại hai trăm khoảng chừng, một trăm tám mươi tuổi còn tại Hóa Phàm, cơ bản liền lại không cách nào đột phá, bằng chừng ấy tuổi, lại có thể lấy vẽ nhập đạo, ung dung thành Thánh... Không thể không nói, là một cái truyền kỳ."),t("br"),t("br"),n._v("Hai người đang khi nói chuyện, đi vào thị trường."),t("br"),t("br"),n._v("Thị trường so với trong tưởng tượng còn muốn hùng vĩ, bảo vật cái gì cần có đều có, không ít người ở bên trong xuyên tới xuyên lui, lựa chọn cần vật phẩm."),t("br"),t("br"),n._v("Minh Lý Chi Nhãn chuyển động, Trương Huyền nhìn lướt qua, nhịn không được lắc đầu."),t("br"),t("br"),n._v("Nơi này bảo bối cứ việc không ít, nhưng đều là rất bình thường, đối Hóa Phàm cường giả có lẽ tác dụng không nhỏ, nhưng đối với hắn mà nói, căn bản không cần."),t("br"),t("br"),n._v("“Thị trường lầu một vật phẩm, đều là tạp hoá, giá cả không tính quá cao, tới mua sắm, hầu như đều mang đào bảo tâm lý, có thể đãi đến tốt nhất, đãi không đến, cũng sẽ không thua thiệt quá nhiều! Lầu hai bắt đầu, rất nhiều bảo vật, liền có giám bảo sư giám định giấy chứng nhận, cứ việc giá cả hơi cao, nhưng có thể cam đoan, mua không phải hàng giả!”"),t("br"),t("br"),n._v("Ngô sư giới thiệu một chút: “Trương sư có muốn hay không tại trong thương trường đi dạo, tùy tiện nhìn một chút?”"),t("br"),t("br"),n._v("“Không cần...”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Hắn hiện tại thiếu nhất chính là tinh nguyên linh thạch thượng phẩm, mà thứ này, nơi này khẳng định không có."),t("br"),t("br"),n._v("Còn dược liệu, khoáng thạch loại hình, Sở Thiên Hành bệ hạ đã toàn bộ lấy ra, không cần mua sắm."),t("br"),t("br"),n._v("Cái gì cũng không thiếu, cũng không cần thiết ở đây loạn chuyển."),t("br"),t("br"),n._v("“Vậy thì tốt, chúng ta trực tiếp đi lầu năm đi, phẩm giám hội ở nơi đó tổ chức...”"),t("br"),t("br"),n._v("Ngô sư cười cười, đi đầu dẫn đường."),t("br"),t("br"),n._v("Vòng qua đám người, dọc theo cầu thang đi tới lầu năm."),t("br"),t("br"),n._v("Nơi này không còn bán ra bảo vật địa phương, mà là một cái rộng lớn đại sảnh."),t("br"),t("br"),n._v("Đi thẳng về phía trước, hai người còn không có đi vào phòng, liền thấy hai người cũng đi tới, nhìn một chút đến bên trong một cái, Trương Huyền nhịn không được sững sờ."),t("br"),t("br"),n._v("Không phải người khác, chính là để hắn “Cố gắng lên” vị kia, Trương gia hậu nhân... Trương Cửu Tiêu!"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1171"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1173"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);