(window.webpackJsonp=window.webpackJsonp||[]).push([[2070],{2425:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-786-tiet-chan-duong-quyet-tam"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-786-tiet-chan-duong-quyet-tam"}},[n._v("#")]),n._v(" Chương 786: Tiết Chân Dương quyết tâm")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 786: Tiết Chân Dương quyết tâm"),t("br"),t("br"),n._v("“Đứng lên đi! Chỉ cần ngươi ngoan ngoãn vì học biết làm việc, ta sẽ chỉ điểm!”"),t("br"),t("br"),n._v("Trương Huyền khoát tay áo."),t("br"),t("br"),n._v("Huyền Huyền hội vừa mới thành lập, hắn không có khả năng ngày ngày tại đây trông coi, vì phòng ngừa cái khác lão sinh làm loạn, nhất định phải có người tọa trấn."),t("br"),t("br"),n._v("Cái này ba cái, có thể áp chế tất cả tân sinh, không ngóc đầu lên được, lại là năm thứ tư cao tài sinh, chính là người chọn lựa thích hợp nhất."),t("br"),t("br"),n._v("Cho nên, vừa đến liền đánh cược để hắn lưu tại đến xem cánh cửa."),t("br"),t("br"),n._v("Ngược lại không phải cố ý nhục nhã đối phương, mà là, từ bọn họ trông coi, cái khác học hội, thậm chí lão sinh muốn tới đây làm loạn, sợ rằng cũng phải cân nhắc một chút, mới dám hành động."),t("br"),t("br"),n._v("“Vâng, ba người chúng ta, trở về liền rời khỏi Chân Dương hội, gia nhập Huyền Huyền hội!” Đứng dậy, Ưng Cần nói."),t("br"),t("br"),n._v("Trước mắt vị thanh niên này, không chỉ đem hắn bên trên đánh bại, trên tinh thần, cũng hoàn toàn tâm phục khẩu phục."),t("br"),t("br"),n._v("Đối phương cố ý dùng chổi lông gà, hướng hắn biểu hiện ra thương pháp cũng không phải là chỉ có thương mới có thể thi triển đạo lý, được lợi rất nhiều."),t("br"),t("br"),n._v("Mặc dù bây giờ không có thay đổi gì, tu vi cũng không có gì tăng trưởng, nhưng chỉ cần thật tốt tu luyện, thật tốt cảm ngộ, đối thương lý giải, tất nhiên đột nhiên tăng mạnh, thực lực tăng nhiều!"),t("br"),t("br"),n._v("“Ừm!” Trương Huyền nhẹ gật đầu, không nói thêm lời, đứng tại đài cao, hướng nhìn bốn phía."),t("br"),t("br"),n._v("“Hội trưởng, hội trưởng!”"),t("br"),t("br"),n._v("“Hội trưởng, hội trưởng!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Thấy hắn nhìn đến, dưới đài rất nhiều tân sinh, không biết ai hô một tiếng “Hội trưởng!”, lập tức, thanh âm hình thành hồng lưu, càng ngày càng vang, càng ngày càng sáng, tất cả mọi người hô lên, nhìn về phía trên đài thanh niên, nguyên một đám lộ ra nồng đậm hưng phấn cùng sùng bái."),t("br"),t("br"),n._v("Nếu như nói trước đó, còn có người hoài nghi, cùng tại vị này Trương sư sau lưng, có thể không thể chống đối những học sinh cũ kia, hiện tại rốt cuộc biết... Khẳng định có thể."),t("br"),t("br"),n._v("Chân Dương hội cao thủ đứng đầu nhất, võ kỹ học viện thiên tài, đều bị nhẹ nhõm đánh bại, chỉ cần có hắn tại, còn có cái gì có thể sợ?"),t("br"),t("br"),n._v("Đây là hội trưởng của chúng ta!"),t("br"),t("br"),n._v("Chúng ta tân sinh hậu thuẫn, có hắn tại, chúng ta liền không cần sợ hãi!"),t("br"),t("br"),n._v("Cảm nhận được mọi người ánh mắt chân thành, Trương Huyền cũng có chút cảm động, nhưng vào lúc này, Nhược Hoan công tử đi lên phía trước, đưa tay yếu ớt ép."),t("br"),t("br"),n._v("Thanh âm an tĩnh lại."),t("br"),t("br"),n._v("“Danh sư học viện, vừa tới tân sinh, đều lại nhận lão sinh áp bách, chịu đến bọn họ bóc lột. Đây là vì bỏ đi tân sinh, tự đại, tự ngạo tính cách, người biết chuyện bên trên có người, thiên ngoại hữu thiên! Vốn là, là một chuyện tốt, nhưng... Nương theo thời gian trôi qua, lão sinh càng ngày càng quá mức, càng ngày càng cao cao tại thượng, để chuyện này, dần dần đã mất đi chủ ý!”"),t("br"),t("br"),n._v("Nhược Hoan công tử thanh âm lang lãng, vang vọng toàn bộ khu tân sinh vực."),t("br"),t("br"),n._v("“Bọn họ buộc chúng ta tốn giá cao mua bọn họ đồ vật; Buộc chúng ta gia nhập bọn họ học hội, thành vì bọn họ phát tài cùng kiếm lấy học phần công cụ! Không đồng ý, liền đủ loại uy hiếp, dựa vào đủ loại quan hệ, để chúng ta vô cùng vụ nhưng nối! Chúng ta tân sinh... Thật chẳng lẽ muốn một mực ngồi chờ chết, tùy ý bóc lột?”"),t("br"),t("br"),n._v("“Không thể!”"),t("br"),t("br"),n._v("“Chúng ta không thể, cũng không nguyện ý! Không nguyện ý người khác cưỡi đến trên đầu chúng ta; Không nguyện ý, bị bọn họ áp chế, trở thành kiếm tiền phát tài khôi lỗi; Không nguyện ý, tôn nghiêm của chúng ta, tùy ý bọn họ tùy ý đạp lên! Chúng ta là danh sư, muốn đào lý cả vườn, sư truyền thiên hạ, không bị áp bách, bị khi phụ kẻ đáng thương!”"),t("br"),t("br"),n._v("“Bất quá, chúng ta lực lượng một người, mười phần yếu ớt, nhưng tập hợp chúng nhân chi lực, liền có thể hội tụ thành uông dương đại hải! Cho nên...”"),t("br"),t("br"),n._v("“Chúng ta muốn liên hợp!”"),t("br"),t("br"),n._v("“Trương sư, vì có thể để cho rất nhiều bạn bè, tiến vào học viện, cam tâm tản mất bản thân kiếm lấy điểm tích lũy! Vì không cho mọi người bị lão sinh khi dễ, sưu tập chư nhiều vị lão sư tường hiểu, miễn phí cấp cho; Càng là vì mọi người, ra tay đánh nhau, để năm thứ tư học viên, đều cam tâm tình nguyện lưu lại...”"),t("br"),t("br"),n._v("“Phần này khí phách, phần này thực lực cùng khí độ, đúng là chúng ta tân sinh, cần thiết học tập! Do đó, ta tự nguyện gia nhập Huyền Huyền hội, trở thành một thành viên trong đó, nguyện ý nghe theo phân phó của hắn, một đường hướng về phía trước!”"),t("br"),t("br"),n._v("“Chư vị, ý như thế nào?”"),t("br"),t("br"),n._v("Nhược Hoan công tử, nhìn quanh một vòng, thanh âm vang tận mây xanh: “Có bằng lòng hay không hay không?”"),t("br"),t("br"),n._v("“Chúng ta cũng nguyện ý!”"),t("br"),t("br"),n._v("“Chúng ta đều nguyện ý gia nhập Huyền Huyền hội!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Ba vạn tân sinh, đồng thời gào thét, thanh âm hình như muốn đem chung quanh kiến trúc rung sụp."),t("br"),t("br"),n._v('"Rất tốt, đã tất cả mọi người đồng ý,'),t("br"),t("br"),n._v('Trương sư, chính là Huyền Huyền hội hội trưởng, thuộc tại chúng ta tân sinh học hội, chính thức thành lập!" Nhược Hoan công tử hô lên.'),t("br"),t("br"),n._v("Trước đó mặc dù nói Huyền Huyền hội, vẫn còn không chính thức xác định, nhưng mà phần lớn người biết, mà bây giờ, mượn cơ hội này thành lập, làm cho tất cả mọi người đều biết, học viện lớn nhất học hội, không phải cái gì Chân Dương hội, càng không phải là cái gì Yêu Nghiệt hội, mà là Huyền Huyền hội!"),t("br"),t("br"),n._v("Từ Trương Huyền, Trương sư, vị này vừa tới học viện, vẫn chưa tới năm ngày tân sinh, tạo thành siêu cấp học hội!"),t("br"),t("br"),n._v("“Huyền Huyền hội! Huyền Huyền hội!”"),t("br"),t("br"),n._v("“Huyền Huyền hội! Huyền Huyền hội!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Thanh âm như nước thủy triều, mọi người nguyên một đám mặt mũi kích động."),t("br"),t("br"),n._v("Tân sinh gia nhập lão sinh học hội, khẳng định muốn chịu đến áp bách, chỉ có gia nhập tổ chức của mình, mới có thể tốt hơn học tập!"),t("br"),t("br"),n._v("Huống chi, vị này Trương sư, nhân phẩm cao thượng, nghĩa bạc vân thiên, tựa như bạch ngọc, không tỳ vết chút nào!"),t("br"),t("br"),n._v("Không có quá nhiều do dự, tất cả mọi người sẽ đồng ý yêu cầu."),t("br"),t("br"),n._v("“Đã tất cả mọi người đồng ý, hiện tại cho mời hội trưởng, cho mọi người nói hai câu!” Cắt ngang hưng phấn mà la lên, Nhược Hoan công tử quay đầu nhìn lại."),t("br"),t("br"),n._v("“Ta?”"),t("br"),t("br"),n._v("Không nghĩ tới, đến cuối cùng, vẫn là bị bức làm hội trưởng, Trương Huyền đành phải tiến về phía trước một bước, nhìn quanh một vòng: “Ta cũng không có gì a có thể nói, chỉ có một câu nói: Chúng ta mặc dù là tân sinh, nhưng không thể so với lão sinh yếu! Lão sinh có thể làm được, chúng ta vì cái gì không thể? Người chỉ có tự cường, người khác mới có thể lấy cường giả gọi là! Chính mình cũng không tôn trọng bản thân, ai lại sẽ tôn trọng? Cho nên, ta hi vọng Huyền Huyền hội tất cả mọi người, không nên chủ động trêu chọc người khác, nhưng... Không người nào dám tới làm loạn, cũng không cần lùi bước!”"),t("br"),t("br"),n._v("“Được!”"),t("br"),n._v(" “Để bọn hắn biết lợi hại!”"),t("br"),t("br"),n._v("Tất cả mọi người sôi trào lên."),t("br"),t("br"),n._v("Trước đó bị lão sinh áp bách, chịu không ít oan ức, hiện tại thành lập Huyền Huyền hội, như thế khí phách, để bọn hắn kích động không thôi."),t("br"),t("br"),n._v("Đem một màn này nhìn ở trong mắt, một bên Ưng Cần, Viên Cương đám người, cũng tất cả đều nhiệt huyết sôi trào, lần thứ nhất chân chính cảm thấy, gia nhập cái này học hội, có lẽ... Không tính là thập sai lầm."),t("br"),t("br"),n._v("Cái khác học hội, đều là ép buộc gia nhập, không ít hội viên, đều ngoài hợp mà trong xa, mà cái này, đồng lòng nhất trí, có lẽ thật có thể đi ra cái khác học hội, hoàn toàn không giống con đường cùng thành tựu."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Bên này thành lập học hội, một bên khác, Tiết Chân Dương, Long Thương Nguyệt, Đổng Hân đám người, ngay lúc sắp đi tới khu tân sinh vực."),t("br"),t("br"),n._v("Bọn họ từ Hồ Yêu Yêu nơi đó rời đi, liền hướng nơi này, nhưng mà lưỡng địa khoảng cách hơi xa, còn chưa đi đến mà thôi."),t("br"),t("br"),n._v("“Hội trưởng!”"),t("br"),t("br"),n._v("Nhìn thấy từng dãy kiến trúc cao lớn xuất hiện ở trước mắt, đang muốn thừa dịp đêm tối đi qua nhìn một chút Ưng Cần đám người đem tân sinh giáo huấn thế nào, chỉ thấy một bóng người gấp gáp lao đến, đi tới trước mặt, ngừng lại."),t("br"),t("br"),n._v("“Ưng Cần bên kia thế nào?”"),t("br"),t("br"),n._v("Tiết Chân Dương cười hỏi."),t("br"),t("br"),n._v("Đúng là hắn phái đi dò xét tin tức thuộc hạ."),t("br"),t("br"),n._v("“Cái này...” Bóng người chần chờ một chút: “Ưng Cần, Viên Cương, Bạch Diện ba người, đã... Bị Trương sư đánh bại, hoàn toàn thuyết phục, rời khỏi Chân Dương hội, gia nhập Huyền Huyền hội!”"),t("br"),t("br"),n._v("“Đánh bại? Rời khỏi... Chân Dương hội?”"),t("br"),t("br"),n._v("Tiết Chân Dương ngẩn ngơ."),t("br"),t("br"),n._v("Trước khi đến, hắn lời thề son sắt mà nói, Ưng Cần đi qua khẳng định giáo huấn đối phương không có bất kỳ biện pháp nào, nằm mơ đều không nghĩ tới, lại là tin tức này."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Bóng người gật đầu."),t("br"),t("br"),n._v("“Chuyện gì xảy ra? Viên Cương, Bạch Diện yếu một ít ngược lại cũng thôi, Ưng Cần thế nhưng là từng chiếm được chỉ điểm của ta, làm sao có thể thua?”"),t("br"),t("br"),n._v("Tiết Chân Dương không thể tin vào tai của mình."),t("br"),t("br"),n._v("Viên Cương, Bạch Diện, tại năm thứ tư bên trong, không tính là tài năng xuất chúng, nhưng Ưng Cần, thế nhưng là năm thứ tư đỉnh phong nhất tồn tại, đi theo phía sau hắn thật nhiều năm, bản thân cũng không giữ lại chút nào truyền thụ thương kỹ... Làm sao có thể thua ở một cái mới vừa vào học tân sinh?"),t("br"),t("br"),n._v("Đổng Hân, Long Thương Nguyệt cũng nháy con mắt, có chút bối rối."),t("br"),t("br"),n._v("Ưng Cần mặc dù không phải bọn họ học được người, nhưng làm Chân Dương hội số một số hai người tài, cũng là nhận biết, Đạp Hư cảnh cường giả đỉnh phong, ngũ tinh thượng phẩm danh sư... Khiêu chiến võ kỹ, thế mà thua?"),t("br"),t("br"),n._v("Nghe lầm chứ?"),t("br"),t("br"),n._v("“Vừa rồi tỷ thí cảnh, ta đều ghi xuống, ngươi xem đi thì biết...”"),t("br"),t("br"),n._v("Biết khó mà giải thích, bóng người cổ tay khẽ đảo, một cái ghi chép ngọc tinh xuất hiện tại lòng bàn tay, đưa tới."),t("br"),t("br"),n._v("Tiết Chân Dương vội vàng tiếp nhận, chân khí ở bên trong truyền vào, thời gian nháy mắt, vừa rồi tỷ thí cảnh liền xuất hiện ở trước mặt mọi người."),t("br"),t("br"),n._v("“Tùy tiện chỉ điểm bị thua tân sinh, đánh bại dễ dàng Bạch Diện, Viên Cương?”"),t("br"),t("br"),n._v("“Dùng một cây chổi lông gà, đánh Ưng Cần không có cách nào đánh trả?”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nhìn thấy ngọc tinh bên trong xuất hiện nội dung, ba người tất cả đều sững sờ."),t("br"),t("br"),n._v("Tùy tiện chỉ điểm một người, liền đánh bại năm thứ tư học viên... Loại năng lực này, liền xem như bọn họ, cũng làm không được!"),t("br"),t("br"),n._v("“Hắn mặc dù chỉ là dùng một cây chổi lông gà, nhưng nhưng nhìn ra Ưng Cần thương pháp bên trong tất cả sơ hở, phần này nhãn lực... Muốn đối thương pháp lý hiểu bao sâu, mới có thể làm đến?”"),t("br"),t("br"),n._v("Yết hầu phát khô, Tiết Chân Dương thân thể có chút run rẩy."),t("br"),t("br"),n._v("Làm dùng thương cường giả, hắn biết Ưng Cần cái kia đâm một cái uy lực, liền xem như hắn, đều muốn tạm thời tránh né, không dám đón đỡ!"),t("br"),t("br"),n._v("Mà đối phương, không chỉ tiếp, còn theo tay nắm lấy, giống như là xoay người trên mặt đất nhặt lên một hòn đá dễ dàng..."),t("br"),t("br"),n._v("Nói rõ... Đã xem thấu thương pháp bên trong sơ hở cùng thiếu sót, mới có thể tiến hành lợi dụng, làm được đơn giản như vậy."),t("br"),t("br"),n._v("Đây là đáng sợ cỡ nào nhãn lực?"),t("br"),t("br"),n._v("“Khó trách, Hồ Yêu Yêu đều thành hắn ngồi xuống học đồ, người này đối thương pháp lý giải, đã đạt đến trình độ đăng phong tạo cực... Chân Dương, ngươi tới tỷ thí, có thể thắng được?”"),t("br"),t("br"),n._v("Đổng Hân quay đầu nhìn lại."),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Tiết Chân Dương nắm đấm xiết chặt, thở ra một hơi, ánh mắt hơi dao động một chút, lập tức lần nữa kiên định xuống: “Ta hiện tại khả năng không cách nào vượt qua, có điều, chỉ cần... Lạc Diệp thương pháp tu luyện tới đệ thập trọng, đem hắn đánh bại, không khó!”"),t("br"),t("br"),n._v("Đường đường danh sư, Chân Dương hội hội trưởng, võ kỹ học viện nổi danh nhất thiên tài, tự nhiên không có khả năng bị tuỳ tiện hù sợ."),t("br"),t("br"),n._v("“Ta nhớ được ngươi ba năm trước đây liền đạt tới đệ cửu trọng đi! Đệ thập trọng nếu như dễ dàng như vậy đạt tới, lại làm sao có thể khốn lâu như vậy!” Đổng Hân lắc đầu, cũng không cảm thấy hắn có thể thành công."),t("br"),t("br"),n._v("“Yên tâm đi, ta đã lĩnh ngộ đệ thập trọng tinh túy, còn kém một cái thời cơ đột phá!”"),t("br"),t("br"),n._v("Chần chờ một chút, Tiết Chân Dương ánh mắt lộ ra vẻ kiên định, dường như có loại ý niệm không thể lay động: “Bắt đầu từ ngày mai, ta rời đi học viện, đi chung quanh một chút, lĩnh ngộ nhân sinh muôn màu, xung kích đệ thập trọng! Không đột phá, liền không trở lại!”"),t("br"),t("br"),n._v("Năm đó Súng Vương lư mới xanh, tu vi đạt tới ràng buộc, liền từng đi khắp hang cùng ngõ hẻm, trải nghiệm người bình thường nhất cử nhất động, từ đó một lần hành động sáng chế ra thập trọng Lạc Diệp thương, danh chấn thiên hạ."),t("br"),t("br"),n._v("Hắn cũng muốn thử xem."),t("br"),t("br"),n._v("“Các ngươi yên tâm, đợi ta trở về, chính là thương chọc vị này Trương Huyền thời điểm, cho hắn biết... Võ kỹ, ta mới thật sự là Vương Giả!”"),t("br"),t("br"),n._v("(Bốn mươi mét đại đao hình ảnh, chú ý công chúng số kiểm tra lịch sử tin tức. Có rất nhiều hình ảnh, còn có tác dụng thương, chơi rất vui."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-794"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-796"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);