(window.webpackJsonp=window.webpackJsonp||[]).push([[2071],{2426:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-787-nguy-truong-phong-nu-nhi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-787-nguy-truong-phong-nu-nhi"}},[n._v("#")]),n._v(" Chương 787: Ngụy Trường Phong nữ nhi")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 787: Ngụy Trường Phong nữ nhi"),t("br"),t("br"),n._v("Học hội thành lập, đủ loại điều lệ, quy tắc, thủ tục, thảo luận thương định hết, không sai biệt lắm trời cũng nhanh sáng lên."),t("br"),t("br"),n._v("Huyền Huyền hội nhưng mà tân sinh học hội, cũng không cần giao nạp hội phí cùng học phần, có điều, nhưng lại có một bộ nghiêm khắc tổ chức, chỉ cần vận chuyển, có thể vì toàn bộ học hội, kiếm lấy càng nhiều lợi ích."),t("br"),t("br"),n._v("Đương nhiên, những này đều tại thương định bên trong, còn không có hoàn toàn thành hình."),t("br"),t("br"),n._v("Trương Huyền chẳng muốn lo nghĩ, trực tiếp vung tay cho Nhược Hoan công tử, La Tuyền đám người, trở về tinh anh khu."),t("br"),t("br"),n._v("Mới vừa trở lại chỗ ở, còn chưa kịp nghỉ ngơi, liền nghe đến tiếng gõ cửa, phụ trách tinh anh khu quản lý một cái lão sinh, đứng ở trước cửa."),t("br"),t("br"),n._v("“Trương sư, có người nói muốn đi qua tìm ngươi, ta liền mang đi qua!”"),t("br"),t("br"),n._v("Trương Huyền hướng (về) sau nhìn lại, chỉ thấy một tên mập tiến lên đón: “Thiếu gia!”"),t("br"),t("br"),n._v("“Làm phiền vị học trưởng này!” Thấy là Tôn Cường, Trương Huyền gọi một câu, đem hắn mang vào sân nhỏ."),t("br"),t("br"),n._v("“Chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Trước mấy ngày an bài để Tôn Cường đám người đợi tại sân nhỏ, giờ phút này đột nhiên đi tìm đến, chẳng lẽ xảy ra chuyện?"),t("br"),t("br"),n._v("“Là Ngụy Trường Phong Các chủ, xử lý xong linh tài các sự tình, đến tìm thiếu gia.” Tôn Cường nói."),t("br"),t("br"),n._v("“Tìm ta? Ta không phải để hắn nghe theo sắp xếp của ngươi ư? Chẳng lẽ hắn không nghe lời?” Trương Huyền cau mày."),t("br"),t("br"),n._v("Bản thân đại náo linh tài các về sau, đem Ngụy Trường Phong thu phục, để hắn xử lý xong sự tình về sau, tới đón chịu Tôn Cường an bài, chẳng lẽ, không nghe lời?"),t("br"),t("br"),n._v("“Không phải, là hắn... Nhất định phải thấy thiếu gia, ta không có cách, cũng chỉ có thể đã tới!” Tôn Cường nói."),t("br"),t("br"),n._v("Thiếu gia tại danh sư học viện, không tốt lắm vào, coi như hắn nói muốn tìm người, cũng hao tốn không ít trắc trở."),t("br"),t("br"),n._v("“Nhất định phải thấy ta?” Trương Huyền cau mày."),t("br"),t("br"),n._v("“Đúng vậy a, hỏi là chuyện gì, làm sao đều không nói! Nói muốn gặp được ngươi mới có thể mở miệng, ta cũng không có cách nào.” Tôn Cường cười khổ."),t("br"),t("br"),n._v("“Cái này... Vậy thì tốt, đi qua nhìn một chút!”"),t("br"),t("br"),n._v("Hiểu Ngụy Trường Phong loại này Thánh vực cường giả, đã như vậy yêu cầu, khẳng định có chuyện quan trọng, Trương Huyền không nói thêm lời, cùng Tôn Cường cùng một chỗ hướng học viện đi ra ngoài."),t("br"),t("br"),n._v("Không dùng quá nhiều thời gian, liền trở lại Tôn Cường đám người ở tiểu viện."),t("br"),t("br"),n._v("“Thiếu gia!”"),t("br"),t("br"),n._v("Thấy hắn đi tới, Ngụy Trường Phong lập tức tiến lên đón."),t("br"),t("br"),n._v("Mấy ngày không thấy, vị này linh tài các Các chủ giống như là già nua mấy tuổi, trong mắt cũng che kín tơ máu, không nói ra được tang thương."),t("br"),t("br"),n._v("“Ngươi đây là... Làm sao vậy?” Trương Huyền nghi hoặc."),t("br"),t("br"),n._v("Đối phương là Thánh vực cường giả, thọ nguyên đạt đến một ngàn tuổi, chỉ cần không phải làm ra có hại sinh mệnh sự tình, không có khả năng già yếu nhanh như vậy!"),t("br"),t("br"),n._v("Mặt ngoài nhìn già mấy tuổi, chỉ sợ ít nhất đại mấy chục năm thọ nguyên tiêu hao."),t("br"),t("br"),n._v("Phù phù!"),t("br"),t("br"),n._v("Cũng không nhiều lời, Ngụy Trường Phong đầu gối mềm nhũn, quỳ rạp xuống đất: “Còn xin thiếu gia, mau cứu con gái của ta, nàng... Sắp không được!”"),t("br"),t("br"),n._v("“Ừm?” Trương Huyền sững sờ."),t("br"),t("br"),n._v("Trước đó thông qua thư viện, đã suy đoán ra, đối phương sưu tập bảo vật mục đích là vì cứu chữa thân nhân, dựa theo đạo lý, sưu tập nhiều như vậy bảo vật linh tính, coi như nặng chứng cũng có thể tiếp tục kiên trì, làm sao mới mấy ngày ngắn ngủi, liền sắp không được?"),t("br"),t("br"),n._v("Bảo vật không có sự sống, linh tính nhưng có thể sống sót, dùng cái này xâu lệnh, chỉ cần không xuất hiện quá nghiêm trọng biến cố, kiên trì mấy năm, mấy chục năm, không vấn đề quá lớn."),t("br"),t("br"),n._v("Chính là bởi vì hiểu những này, mặc dù thu phục đối phương, lại không lo lắng ra tay."),t("br"),t("br"),n._v("“Ta cũng không biết, nguyên bản cũng tốt tốt, hai ngày trước sinh mệnh đột nhiên suy yếu, bảo vật linh tính cũng vô dụng, mong rằng thiếu gia ra tay!” Ngụy Trường Phong hốc mắt ửng hồng."),t("br"),t("br"),n._v("Nhiều năm như vậy, vì nữ nhi có thể sống sót, chuyện gì cũng có thể làm, thật sắp không kiên trì được nữa, hắn chỉ sợ đều không muốn sống."),t("br"),t("br"),n._v("Vị thiếu gia này, một cái nhìn ra tình huống của mình, lại phô bày chư bao nhiêu thần kỳ thủ đoạn, có lẽ... Thật có thể cứu trợ nữ nhi."),t("br"),t("br"),n._v("Cái này đã là hy vọng duy nhất."),t("br"),t("br"),n._v("“Mang ta xem một chút!”"),t("br"),t("br"),n._v("Hiểu nói vô dụng, nhất định phải tận mắt thấy mới có thể xác định, Trương Huyền nói."),t("br"),t("br"),n._v("“Ta đã đưa nàng mang tới, tại gian phòng của ta...” Ngụy Trường Phong vội vàng một chỉ."),t("br"),t("br"),n._v("Sợ thiếu gia đi tới, lại đi qua nhìn, thời gian không còn kịp nữa, trực tiếp đem nữ nhi mang đi qua, cùng hắn ở cùng một chỗ."),t("br"),t("br"),n._v("“Ừm!” Trương Huyền gật đầu, đi vào."),t("br"),t("br"),n._v("Tôn Cường tìm cái tiểu viện này, chỉ có bảy, tám cái gian phòng, hơn nữa mỗi cái đều không rộng lắm, đẩy cửa đi vào, mười mấy mét vuông lớn nhỏ, hơi có vẻ âm u."),t("br"),t("br"),n._v("Gian phòng một bên là cái không lớn giường gỗ, phía trên nằm ngang một cái mười bốn, mười lăm tuổi nữ hài, thân thể gầy yếu, vẻ mặt trắng bệch, tóc có chút ố vàng, hai mắt nhắm nghiền, đã lâm vào hôn mê."),t("br"),t("br"),n._v("“Bệnh thật nặng!”"),t("br"),t("br"),n._v("Trương Huyền mày nhăn lại."),t("br"),t("br"),n._v("Trước mắt cô gái này, gần tức giận nhiều hơi thở ít, Minh Lý Chi Nhãn chiếu xuống, toàn thân sinh cơ suy yếu, trong cơ thể tất cả kinh mạch, cũng bị mất sức sống, xem ra bất cứ lúc nào cũng sẽ một ngủ lại không cách nào tỉnh lại."),t("br"),t("br"),n._v("Khó trách Ngụy Trường Phong lo lắng, trước mắt vị này, quả thực đã đến bên bờ sinh tử, lại không cứu chữa, có thể hay không kiên trì đến trời tối đều khó mà nói."),t("br"),t("br"),n._v("“Trước đó, nàng còn có thể tỉnh lại nói chuyện, một khi không chịu nổi, nuốt bảo vật linh tính, liền có thể kiên trì một đoạn thời gian, nhưng... Từ hôm qua bắt đầu, những này linh tính, đã không còn tác dụng, một mực ngủ mê mệt, lại không tỉnh lại...”"),t("br"),t("br"),n._v("Ngụy Trường Phong tràn đầy lo lắng, đem triệu chứng nói một lần."),t("br"),t("br"),n._v("“Ừm!” Trương Huyền gật gật đầu, lại nhìn kỹ một lần, bất đắc dĩ lắc đầu."),t("br"),t("br"),n._v("Hắn đối y thuật lý giải, chỉ dừng lại ở tứ tinh trái phải, cho dù có Minh Lý Chi Nhãn, cũng tìm không ra xác thực triệu chứng, trừ phi, có thể tiếp tục xem sách, đối đủ loại chứng bệnh hiểu rõ càng nhiều."),t("br"),t("br"),n._v("Bất quá, hiển nhiên thời gian không còn kịp nữa."),t("br"),n._v("Kế trước mắt, chỉ có thể mượn nhờ thư viện."),t("br"),t("br"),n._v("Bàn tay duỗi ra, đối với cổ tay của đối phương đáp tới."),t("br"),t("br"),n._v("Nữ hài làn da rét run, như cùng một cái khối băng."),t("br"),t("br"),n._v("Tinh thần tập trung, Trương Huyền trong đầu thở nhẹ!"),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Một quyển sách xuất hiện."),t("br"),t("br"),n._v("Người tu luyện hôn mê vô chủ xem ý thức, liền cùng bảo vật đồng dạng, chỉ cần chạm đến, đồng dạng có thể sinh ra đối ứng sách báo."),t("br"),t("br"),n._v("Vội vàng mở ra."),t("br"),t("br"),n._v("“Ngụy Như Yên, Thanh Nguyên thành người, Tiên Thiên độc thể...”"),t("br"),t("br"),n._v("Trong thư tịch nội dung, khắc sâu vào trong óc."),t("br"),t("br"),n._v("“Tiên Thiên độc thể? Nha đầu này lại là Tiên Thiên thể chất?” Trương Huyền sững sờ."),t("br"),t("br"),n._v("Tiên Thiên thể chất mười phần hiếm thấy, cùng nhau đi tới, cũng là gặp qua Triệu Nhã cùng Viên Đào hai người, không nghĩ đến cái này trong mê ngủ, bất cứ lúc nào cũng sẽ tử vong nữ hài cũng thế."),t("br"),t("br"),n._v("“Tiên Thiên độc thể, thích hợp tu luyện độc sư chức nghiệp, có điều, hiện tại bộ dáng này... Có thể không có thể còn sống sót, cũng chưa biết chừng!”"),t("br"),t("br"),n._v("Lắc đầu."),t("br"),t("br"),n._v("Tiên Thiên thể chất mặc dù lợi hại, nhưng không kích hoạt trước đó, giống như người bình thường, đồng dạng có sinh lão bệnh tử, không có bất kỳ cái gì thực lực."),t("br"),t("br"),n._v("Cô gái này, rõ ràng không có kích hoạt, có cùng không có, liền không khác nhau quá nhiều."),t("br"),t("br"),n._v("Lúc trước tại Hồng Liên sơn mạch Độc điện, từng thấy trên sách ghi chép qua loại này thể chất, chẳng qua đều nói vậy không rõ, liền xem như hắn, cũng không biết như thế nào kích hoạt, kích hoạt sau lại có gì loại hiệu quả."),t("br"),t("br"),n._v("Không đi quản đối phương thể chất, tiếp tục hướng xuống nhìn lại."),t("br"),t("br"),n._v("“... Mẫu thai lúc, bị Thánh vực cường giả đả thương cơ sở, linh hồn, thân thể đều hứng chịu tới tổn thương, thuộc về thần hồn bệnh thiếu máu. Thiếu sót:”"),t("br"),t("br"),n._v("“Thần hồn bệnh thiếu máu?” Trương Huyền lông mày nhảy một cái."),t("br"),t("br"),n._v("Thần hồn bệnh thiếu máu, tên như ý nghĩa, tinh thần, hồn phách, máu (thân thể), đều cùng thường nhân khác biệt, có cực lớn hao tổn. Bình thường là tại mẫu thai thời điểm, cung cấp không đủ dẫn đến, loại người này, tinh khí thần không đủ để chống đỡ thân thể sống sót, dựa theo tình huống bình thường, rất khó còn may mắn còn sống sót, thậm chí, sinh đều sinh không ra tới."),t("br"),t("br"),n._v("Trước mắt vị này, chẳng những thuận lợi ra đời, còn sống như thế lớn... Làm sao làm được?"),t("br"),t("br"),n._v("“Như Yên loại tình huống này, dựa theo bình thường đạo lý, ra đời cũng khó khăn, là thê tử của ta, hao hết toàn thân tinh nguyên, giữ được tính mạng, lúc này mới thuận lợi sinh sản, bất quá... Nàng cũng lại không sống sót!”"),t("br"),t("br"),n._v("Dường như hiểu Trương Huyền ý nghĩ, Ngụy Trường Phong dường như nhớ tới cái gì, thần sắc mang theo đau thương: “Như Yên ký thác tính mạng của nàng cùng hi vọng, coi như để cho ta chết, cũng không thể để hắn có việc!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Hắn có thể lý giải trước mắt vị này tâm cảnh."),t("br"),t("br"),n._v("Vợ vì nữ nhi thuận lợi sống sót, hao phí toàn thân tinh huyết, thậm chí do đó tử vong, làm vì phụ thân, nếu như ngay cả nữ nhi lại không bảo vệ, về sau có mặt mũi nào đi gặp chết đi thân nhân?"),t("br"),t("br"),n._v("Cho nên, những năm này, coi như tốn hao lại lớn đánh đổi, đều không chối từ."),t("br"),t("br"),n._v("“Thiếu gia, Như Yên... Còn có thể cứu?”"),t("br"),t("br"),n._v("Nói xong, Ngụy Trường Phong nhịn không được nhìn qua, trong mắt tràn đầy lo lắng."),t("br"),t("br"),n._v("“Trong cơ thể nàng sinh cơ không sai biệt lắm toàn bộ cắt đứt, muốn cứu... Rất khó!” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Đem vị này ngụy Như Yên thư tịch xem hết, hắn cũng cảm thấy có chút khó giải quyết."),t("br"),t("br"),n._v("Cái này đã không chỉ có là thần hồn bệnh thiếu máu vấn đề, những năm này, đồ đại bổ ăn nhiều hơn, quá bổ không tiêu nổi, trong cơ thể sinh cơ cơ bản bị phá hư sạch sẽ."),t("br"),t("br"),n._v("Lại thêm thần hồn suy yếu, có thể nói đã đến mức đèn cạn dầu."),t("br"),t("br"),n._v("Khó trách lục tinh đỉnh phong y sư đều không có cách, chỉ có thể dựa vào bảo vật linh tính kéo dài tính mạng, liền xem như hắn, tìm tới nguyên nhân cùng thiếu hụt, cũng đều là vô kế khả thi, có loại chó cắn con nhím, không thể nào ăn xuống cảm giác."),t("br"),t("br"),n._v("“Còn xin thiếu gia ra tay, chỉ cần có thể cứu sống Như Yên, coi như để cho ta làm trâu làm ngựa, đều sẽ không tiếc!”"),t("br"),t("br"),n._v("Nghe thiếu gia cũng không hoàn toàn từ chối, Ngụy Trường Phong vội nói."),t("br"),t("br"),n._v("“Ngươi đã hiệu trung với ta, ta có thể làm đến đương nhiên sẽ không chối từ, có điều, nàng hiện tại không riêng gì thân thể suy yếu, hồn phách cũng quá suy yếu, muốn cứu chữa, trước hết để hắn tỉnh lại, mà muốn tỉnh lại, thân thể cần càng cường tráng hơn mới được...”"),t("br"),t("br"),n._v("Trương Huyền xoa xoa lông mày."),t("br"),t("br"),n._v("Đối phương rơi vào hôn mê, là thân thể lại không cách nào gắng sức hồn phách vận chuyển, thời khắc này thân thể, thật giống như một cái bao tải thủng, linh hồn như nước, căn bản dung nạp không được."),t("br"),t("br"),n._v("Nhưng... Không có linh hồn, người không thanh tỉnh, thân thể bên trên cũng không có cách nào điều trị."),t("br"),t("br"),n._v("Bởi vì bổ sung khí huyết, cần ý chí phối hợp, ý niệm không kiên trì nổi, đồng dạng không có công hiệu."),t("br"),t("br"),n._v("Bổ sung thân thể, cần linh hồn tỉnh lại, linh hồn tỉnh lại cần thân thể chống đỡ... Trước mắt vị này ngụy Như Yên, là cái lưỡng nan tình huống."),t("br"),t("br"),n._v("Liền xem như hắn, đều cảm thấy đau đầu cực kỳ."),t("br"),t("br"),n._v("“Vậy làm sao bây giờ?”"),t("br"),t("br"),n._v("Ngụy Trường Phong nhìn tới."),t("br"),t("br"),n._v("Bệnh lâu thành y, hắn ngày ngày cùng dược liệu liên hệ, mặc dù không khảo hạch qua y sư, tài nghệ chân chính, nhưng không cần một chút ngũ tinh, lục tinh y sư kém. Nữ nhi loại tình huống này, kỳ thật không cần thiếu gia nói, cũng biết."),t("br"),t("br"),n._v("Chẳng lẽ, coi như thiếu gia, đều không có cách nào?"),t("br"),t("br"),n._v("Thật để cho mình người tóc bạc, trơ mắt đưa tóc đen người?"),t("br"),t("br"),n._v("“Đừng vội, ta trước tiên có thể nghĩ biện pháp, không để cho nàng chết... Bất quá, hẳn là cũng không kiên trì được bao lâu. Biện pháp tốt nhất, là có thể tìm tới một gốc gia tăng hồn phách dược liệu, chỉ phải nghĩ biện pháp để hắn hồn phách tại bụi dược liệu này bên trên tẩm bổ, chữa trị tổn thương, có thể tỉnh lại, còn lại liền sẽ dễ dàng rất nhiều!”"),t("br"),t("br"),n._v("Suy tư một lát, Trương Huyền nói."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-795"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-797"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);