(window.webpackJsonp=window.webpackJsonp||[]).push([[2075],{2431:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-4-lam-mat-mat"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-4-lam-mat-mat"}},[n._v("#")]),n._v(" Chương 4: Làm mất mặt")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v(" “Ta...” "),t("br"),t("br"),n._v(" Vừa nãy Thiên Đạo Thư Viện chấn động, sự chú ý đều tập trung ở thức hải, hiện tại còn một mảnh mơ hồ, hơn nữa, đối phương quyền pháp là cái gì cũng không biết, để hắn chỉ điểm, chỉ điểm cái cọng lông a!"),t("br"),t("br"),n._v(" “Kính xin Trương lão sư góp ý!” "),t("br"),t("br"),n._v(" Lưu Dương bước lên trước, khom người đến cùng."),t("br"),t("br"),n._v(" Một bên Vương Dĩnh cũng chớp con mắt, muốn nhìn một chút tự mình mới vừa biết vị này “Cao nhân” làm sao chỉ điểm."),t("br"),t("br"),n._v(" “Ạch!” "),t("br"),t("br"),n._v(" Thấy ánh mắt mọi người đều tập trung lại đây, nếu không nói, chỉ có mất mặt, Trương Huyền đang muốn tin khẩu nói bậy, đột nhiên nhớ tới thư viện trong thư tịch Lưu Dương khuyết điểm."),t("br"),t("br"),n._v(" Hắn cũng không biết mặt trên ghi lại đến cùng là thật là giả, bất quá, hiện ở loại tình huống này, để hắn nói cái khác khẳng định cũng nhớ không nổi đến, không thể làm gì khác hơn là nhắm mắt: “Ngươi bộ quyền pháp này, khuyết điểm rất nhiều, có tới mười hai nơi!” "),t("br"),t("br"),n._v(" “Ha ha, hắn nói khuyết điểm rất nhiều, mười hai nơi? Ta không nghe lầm chứ!” "),t("br"),t("br"),n._v(" “Đây là ta nghe qua buồn cười nhất chuyện cười!” "),t("br"),t("br"),n._v(" “Bộ này Phi Hoa Quyền tuy rằng cấp bậc không cao, nhưng là toàn bộ vương quốc truyền lưu rộng nhất một bộ quyền lộ, cũng được xưng là cơ sở quyền, đi qua mấy ngàn năm trước tinh giản mài giũa, mặc dù không nói mười phân vẹn mười, khuyết điểm tuyệt đối là quyền pháp bên trong ít nhất, hắn lại còn nói, có mười hai nơi?” "),t("br"),t("br"),n._v(" “Ăn nói bừa bãi, phỏng chừng cái gì đều không nhìn ra, cố ý lung tung nói!” "),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v(" Mọi người xung quanh, vốn cho là hắn có thể nói ra cái gì, vừa mở miệng nhất thời tất cả đều nở nụ cười."),t("br"),t("br"),n._v(" Phi Hoa Quyền là Thiên Huyền vương quốc trụ cột nhất một trong những quyền pháp, cùng trên Địa cầu Thái Tổ trường quyền như thế, chỉ cần luyện công, cơ bản đều muốn học tập, trăm ngàn năm qua đi qua vô số cường giả mài giũa, tuy rằng uy lực không lớn, muốn nói khuyết điểm rất ít, tuyệt đối là phải tính đến, thậm chí còn được người gọi là “Không chút tì vết chi quyền”."),t("br"),t("br"),n._v(" Như vậy quyền pháp, cái tên này lại vừa mở miệng nói có mười hai nơi thiếu hụt... Đùa gì thế!"),t("br"),t("br"),n._v(" Đừng nói một sư tư sát hạch không điểm gia hỏa, coi như Viện trưởng lại đây, cũng tìm không ra nhiều như vậy khuyết điểm đi!"),t("br"),t("br"),n._v(" “Ồ? Trương lão sư cũng thật là đại tài, dĩ nhiên có thể ở Phi Hoa Quyền trông được ra mười hai nơi thiếu hụt, chúng ta xin lắng tai nghe!” "),t("br"),t("br"),n._v(" Tào Hùng hiển nhiên cũng không nghĩ tới Trương Huyền như vậy xốc nổi, vừa mở miệng cứ như vậy nói, hưng phấn con mắt tỏa ánh sáng, cố ý để hắn lúng túng."),t("br"),t("br"),n._v(" Đem mọi người đối thoại nghe vào trong tai, Trương Huyền khuôn mặt bất biến, nhưng trong lòng không khỏi có chút nôn nóng, hắn làm sao đều không có nghĩ đến, cái này gọi Lưu Dương gia hỏa, lại đánh ra một người người đều biết đến quyền pháp!"),t("br"),t("br"),n._v(" Đổi lại tiền thân, khẳng định cũng sẽ biết được, mấu chốt là tự mình vừa xuyên qua không lâu, đối phương ký ức còn không có triệt để dung hợp!"),t("br"),t("br"),n._v(" Bất quá, tuy rằng kiếp trước chỉ là cái thư viện nhân viên quản lý, nhưng bất kể nói thế nào đều là người xuyên việt, có không giống nhau kiến thức, hơn nữa da mặt dày, mí mắt một phen: “Thế nào, không tin? Đây không phải ta không chỉ điểm, là các ngươi không tin, không theo ta chỉ điểm làm... Vậy thì không trách ta! Vương Dĩnh, chúng ta đi, này loại cá cược, không cá cược cũng được!” "),t("br"),t("br"),n._v(" “Chậm đã, chúng ta tin tưởng! Kính xin chỉ điểm đi!” "),t("br"),t("br"),n._v(" Thấy hắn phải đi, Tào Hùng vội vàng ngăn cản."),t("br"),t("br"),n._v(" Hắn hiện tại xem như là nhìn ra rồi, đối phương là cố ý nói ra khoa trương số liệu, để nhóm người mình không phản đối, sau đó hảo nhân cơ hội trốn, bất quá, thật vất vả nắm lấy cơ hội giáo huấn hắn một trận, làm sao có khả năng từ bỏ!"),t("br"),t("br"),n._v(" “Chuyện này...” "),t("br"),t("br"),n._v(" Thấy trượt không xong, Trương Huyền không thể làm gì khác hơn là đem thư viện tạo ra thư tịch lần thứ hai lấy ra."),t("br"),t("br"),n._v(" “Chết thì chết đi!” "),t("br"),t("br"),n._v(" Ngược lại đã không còn đường lui, con mắt rơi thư tịch ghi lại khuyết điểm bên trên, rất mau đem mười hai nơi lần thứ hai nhìn một lần, rơi xuống cái cuối cùng bên trên, cắn răng mở miệng: “Ngươi vừa nãy đánh quyền pháp là cánh phải, ta cho cái đề nghị, ngươi bây giờ đánh cánh tả, sau đó dùng quyền trái công kích bia đá thử xem!” "),t("br"),t("br"),n._v(" Nếu là người “xuyên việt” phúc lợi, vạn nhất là chính xác đây này? Lại nói coi như không chính xác, hiện tại cũng không có biện pháp nào khác, ngược lại hắn cũng nghĩ không ra tốt hơn lời giải thích."),t("br"),t("br"),n._v(" “Đánh cánh tả?” "),t("br"),t("br"),n._v(" “Này giời ạ cũng gọi là chỉ điểm?” "),t("br"),t("br"),n._v(" “Đùa gì thế, tay trái vốn là chán, đánh cánh tả, không phải đem bộ quyền pháp này đánh phế bỏ sao?” "),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v(" Mọi người đầu tiên là sững sờ, lập tức ồ lên, từng cái từng cái lần thứ hai nhìn về phía Trương Huyền, tràn đầy xem thường."),t("br"),t("br"),n._v(" Tay trái sức mạnh so với tay phải yếu, đây là người người đều biết, Lưu Dương vừa nãy đánh quyền, tiếng gió rít gào, rõ ràng không phải thuận tay trái, hiện tại để hắn đánh cánh tả... Đùa gì thế."),t("br"),t("br"),n._v(" “Lưu Dương, dựa theo Trương lão sư nói thử xem, cũng tốt để hắn hết hi vọng!” "),t("br"),t("br"),n._v(" Tào Hùng hiển nhiên không nghĩ tới Trương Huyền sẽ như vậy nói, hưng phấn sắp nhảy lên, trên miệng nhưng dặn dò một câu."),t("br"),t("br"),n._v(" Mặc dù là vừa chiêu thu học sinh, nhưng hắn nhưng hết sức rõ ràng vị này Lưu Dương, một mực am hiểu tay phải, cũng không phải là thuận tay trái, đánh cánh tả, thật muốn đánh xuống, phỏng chừng liền 30 kg đều không có!"),t("br"),t("br"),n._v(" Không chỉ điểm có thể đánh ra 62 kí lô lực quyền, chỉ điểm sau liền 30 đều đánh không ra, lần này cái này Trương Huyền sẽ danh tiếng quét rác, mất mặt xấu hổ!"),t("br"),t("br"),n._v(" Nhìn hắn còn có thể hay không thể lại giống như trước lớn lối như vậy!"),t("br"),t("br"),n._v(" “Rõ!” Lưu Dương cũng cười lạnh, quyền pháp lần thứ hai triển khai."),t("br"),t("br"),n._v(" Hắn có phải hay không thuận tay trái biết đến rõ ràng nhất, để hắn đánh cánh tả... Thực sự là nói hưu nói vượn."),t("br"),t("br"),n._v(" Phi Hoa Quyền là từ nhỏ liền luyện tập cơ sở quyền, tả hữu điên đảo mặc dù có chút không quen, rất nhanh vẫn là thích ứng lại đây, quyền phong vù vù, mặc dù coi như cũng là không sai, nhưng bất luận nối liền vẫn là sức mạnh, tựa hồ cũng so với vừa nãy chênh lệch rất nhiều."),t("br"),t("br"),n._v(" “Lẽ nào... Ta cái này xuyên qua phúc lợi là giả?” "),t("br"),n._v(" Trương Huyền tràn đầy căng thẳng."),t("br"),t("br"),n._v(" đọc ngantruyen.com/"),t("br"),n._v(" Nhân gia xuyên qua phúc lợi, có cái lão gia gia, tiện tay chỉ điểm, chỉ cần theo đối phương nói, khẳng định không thành vấn đề, tự mình rót tốt, một cái thư viện, mặt trên viết ra đồ ngổn ngang, vạn nhất là giả, bị học viện khai trừ, khẳng định là tất nhiên!"),t("br"),t("br"),n._v(" Hô!"),t("br"),t("br"),n._v(" Rất nhanh một bộ quyền pháp luyện xong, Lưu Dương đi tới bia đá trước mặt, giơ lên quyền trái, một hồi đập phá đi lên."),t("br"),t("br"),n._v(" Vù!"),t("br"),t("br"),n._v(" Liên tiếp con số xuất hiện."),t("br"),t("br"),n._v(" “Một, ha ha, mới mười mấy kg...” "),t("br"),t("br"),n._v(" Thấy phía trên xuất hiện cái “Một” chữ, Tào Hùng cười ha ha, tràn đầy vui sướng tâm ý, bất quá lời còn chưa nói hết, một hồi câm ở yết hầu, cả người con ngươi sắp rơi trên mặt đất."),t("br"),t("br"),n._v(" “123?” "),t("br"),t("br"),n._v(" “123 kg?” "),t("br"),t("br"),n._v(" Xung quanh vốn định cười nhạo mọi người, cũng toàn sợ choáng váng giống như vậy, liên tục run rẩy."),t("br"),t("br"),n._v(" Vừa nãy Lưu Dương đánh ra 62 kg, hiện tại trực tiếp biến thành 123, tăng trưởng tiếp cận gấp đôi! Cũng chính là... Tăng phúc trăm phần trăm!"),t("br"),t("br"),n._v(" Thật hay giả?"),t("br"),t("br"),n._v(" Coi như học viện thầy giáo sát hạch đệ nhất lão sư, lần thứ nhất chỉ điểm, cũng không thể khiến người ta một hồi tiến bộ gấp đôi đi!"),t("br"),t("br"),n._v(" “Ta... Ta... Đây là ta đánh ra tới?” "),t("br"),t("br"),n._v(" Người trong cuộc Lưu Dương cũng bối rối."),t("br"),t("br"),n._v(" Ngây ngốc nhìn trước mắt bia đá, tràn đầy không thể tin được."),t("br"),t("br"),n._v(" Hắn không phải thuận tay trái, đã sớm biết, có thể làm mộng đều không có nghĩ đến, tay trái sức mạnh lớn như vậy, là tay phải gấp đôi!"),t("br"),t("br"),n._v(" Mọi người giật mình, Trương Huyền nhưng cao hứng kém chút nhảy lên."),t("br"),t("br"),n._v(" “Là thật!” "),t("br"),t("br"),n._v(" Cho tới giờ khắc này, hắn cuối cùng đã rõ ràng rồi thư viện xuất hiện thiếu hụt, tuyệt đối là sự thật."),t("br"),t("br"),n._v(" Có thể một chút nhìn ra người khác công pháp tu luyện cùng thiếu hụt... Cái này xuyên qua phúc lợi, thật muốn nghịch thiên a!"),t("br"),t("br"),n._v(" “Thế nào? Ngươi chỉ điểm tăng trưởng 20%, ta tăng trưởng trăm phần trăm, Tào lão sư, còn có lời gì nói?” Trương Huyền cười hì hì."),t("br"),t("br"),n._v(" “Ta...” Tào Hùng sắc mặt tái xanh, cảm giác toàn bộ mặt đều rát."),t("br"),t("br"),n._v(" Nguyên bản đưa ra tỷ thí, muốn cho đối phương xấu mặt, kết quả, xấu mặt đã biến thành tự mình."),t("br"),t("br"),n._v(" Từ trong lồng ngực lấy ra một cái ngọc bài, cắn răng một cái ở phía trên nhỏ lên máu tươi: “Lưu Dương, ta đã đem ngươi lui đi, ngươi bây giờ có thể bái Trương lão sư vi sư!” "),t("br"),t("br"),n._v(" Nói xong nhìn về phía Trương Huyền: “Ngươi cũng không cần đắc ý, lần này để ngươi đi rồi đại vận, lần sau tỷ thí, ta nhất định khiến ngươi khó coi...” "),t("br"),t("br"),n._v(" Nói xong đang không ngừng lưu, xoay người rời đi."),t("br"),t("br"),n._v(" Lần này mất mặt quá mức rồi, không riêng làm mất đi cái học viên, còn bị ngay mặt làm mất mặt."),t("br"),t("br"),n._v(" Bại bởi người khác ngược lại cũng thôi, then chốt cái tên này là thầy giáo sát hạch không điểm, toàn trường kém nhất lão sư..."),t("br"),t("br"),n._v(" “Lão sư, lão sư...” "),t("br"),t("br"),n._v(" Thấy Tào Hùng xoay người rời đi, Lưu Dương sắc mặt khó coi."),t("br"),t("br"),n._v(" Tuy rằng vừa nãy Trương Huyền chỉ điểm hắn đánh ra thành tích tốt, có thể sâu trong nội tâm, giống như Tào Hùng, cho là hắn chỉ là vận khí, mười phần không tin có loại năng lực này."),t("br"),t("br"),n._v(" “Tốt, ngươi bây giờ là đệ tử của ta, mau nhanh thân phận nghiệm chứng đi!” "),t("br"),t("br"),n._v(" Trương Huyền mới mặc kệ hắn nghĩ cái gì, một hồi đánh cược để hắn nhiều một học sinh, tràn đầy cao hứng, tiện tay ném qua một cái ngọc bài."),t("br"),t("br"),n._v(" Lưu Dương tuy rằng trong lòng không cam lòng, nhưng cũng biết có chơi có chịu, nếu như mình không tiếp thu vị này Trương Huyền làm lão sư, các lão sư khác khẳng định cũng sẽ không tiếp thu, không thể làm gì khác hơn là đem huyết dịch nhỏ ở trên ngọc bài, hoàn thành nghiệm chứng."),t("br"),t("br"),n._v(" “Ngày mai lại đây đi học!” "),t("br"),t("br"),n._v(" Xử lý xong Lưu Dương, Trương Huyền cũng không phí lời, lần thứ hai quay đầu nhìn về phía Vương Dĩnh, nhàn nhạt nói một câu, nhấc chân đi ra căng tin."),t("br"),t("br"),n._v(" Một lần nữa trở lại lớp học của chính mình, Trương Huyền không chút do dự nào, tinh thần chìm đắm thức hải, cẩn thận quan sát ngày đến thư viện."),t("br"),t("br"),n._v(" Nghiên cứu một hồi, rốt cục để hắn hiểu được."),t("br"),t("br"),n._v(" Chỉ cần có người ở trước mặt hắn triển khai võ kỹ, công pháp, liền có thể tự động tạo ra khuyết điểm thư tịch."),t("br"),t("br"),n._v(" “Ha ha, lần này thật phát tài! Có vật này nơi tay, bất kỳ cái gì khuyết điểm đều có thể một chút nhìn thấu, lần sau thầy giáo sát hạch, ta không tin vẫn là không điểm!” "),t("br"),t("br"),n._v(" Trong lòng một trận cười lớn, làm như người xuyên việt Trương Huyền, sinh ra hào tình vạn trượng."),t("br"),t("br"),n._v("Convert by: Hoang Chau"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-7"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-9"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);