(window.webpackJsonp=window.webpackJsonp||[]).push([[2077],{2432:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-791-gia-ca"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-791-gia-ca"}},[n._v("#")]),n._v(" Chương 791: Giá cả")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 791: Giá cả"),t("br"),t("br"),n._v("“Không phải giá cả vấn đề, mà là những dược liệu này, lão sư cũng không bán ra, mong được tha thứ!” Tôn Nguyên khoát tay áo."),t("br"),t("br"),n._v("Ngụy Trường Phong cắn răng."),t("br"),t("br"),n._v("Đối phương chỉ muốn bán ra, táng gia bại sản, cũng khẳng định mua sắm, nhưng... Nhân gia không bán, cũng không thể đoạt chứ?"),t("br"),t("br"),n._v("Không nói trước, hắn chưa hẳn có thể là đối thủ, chỉ nói nơi này là danh sư học viện, liền để hắn không dám có ý khác."),t("br"),t("br"),n._v("“Ta là vì cứu chữa nữ nhi của ta, nàng nhu cầu cấp bách cái này ôn nhuận linh hồn Thánh dược, mong rằng Vưu viện trưởng tạo thuận lợi...”"),t("br"),t("br"),n._v("Hít sâu một hơi, Ngụy Trường Phong quỳ rạp xuống đất."),t("br"),t("br"),n._v("“Ai!”"),t("br"),t("br"),n._v("Nhìn thấy động tác của hắn, Trương Huyền than thở."),t("br"),t("br"),n._v("Vì nữ nhi, hắn đã liên tục hai lần quỳ xuống."),t("br"),t("br"),n._v("Đường đường Thánh vực cường giả, vì cứu người, cái gì tôn nghiêm đều từ bỏ, cái gì đều có thể không cần, cái này... Chính là cha mẹ!"),t("br"),t("br"),n._v("Đáng thương lòng cha mẹ trong thiên hạ!"),t("br"),t("br"),n._v("Vì con cái, cái gì đều có thể làm, cái gì đều có thể từ bỏ, loại này thích, đơn thuần mà vô tư, vĩ đại mà nặng nề."),t("br"),t("br"),n._v("“Ta nói qua không bán, coi như ngươi quỳ chết tại đây, cũng sẽ không thay đổi, hay là đi thôi!”"),t("br"),t("br"),n._v("Vẻ mặt không thay đổi, Vưu phó viện trưởng khoát tay áo."),t("br"),t("br"),n._v("“Vưu viện trưởng...”"),t("br"),t("br"),n._v("Vẻ mặt tăng thấu đỏ, Ngụy Trường Phong móng tay nắm đến trong thịt."),t("br"),t("br"),n._v("Tất cả mọi người là Thánh vực cường giả, cúi đầu không thấy ngẩng đầu thấy, không nghĩ tới đều quỳ xuống cầu khẩn, đối phương đều không nhúc nhích."),t("br"),t("br"),n._v("Thấy đối phương không cho thể diện, Trương Huyền lắc đầu, đang định tiến lên hỗ trợ khuyên mấy câu, liền nghe đến viện lạc bên ngoài vang lên lần nữa tiếng đập cửa."),t("br"),t("br"),n._v("Đông đông đông đông!"),t("br"),t("br"),n._v("Tôn Nguyên nghi ngờ nhìn thoáng qua, đi về phía Door, không lâu sau, một người trung niên theo ở phía sau đi đến."),t("br"),t("br"),n._v("“Vưu viện trưởng, gia sư luyện đan cần một gốc Thánh vực cấp bậc Hàm Nguyên thảo!”"),t("br"),t("br"),n._v("Trung niên nhân ôm quyền."),t("br"),t("br"),n._v("“Hàm Nguyên thảo?”"),t("br"),t("br"),n._v("Vưu phó viện trưởng nhẹ gật đầu: “Tôn Nguyên, đi cho Trác sư đào tới!”"),t("br"),t("br"),n._v("“Vâng!” Gật gật đầu, Tôn Nguyên đi vào dược viên, không lâu sau, liền đem một gốc màu xanh nhạt thực đào đi qua."),t("br"),t("br"),n._v("“Đa tạ!” Trung niên nhân gật gật đầu, cổ tay khẽ đảo, mấy viên linh thạch thượng phẩm xuất hiện ở trước mắt: “Đây là tiền thuốc!”"),t("br"),t("br"),n._v("Quang mang lấp lánh, không nhiều không ít, vừa vặn mười cái."),t("br"),t("br"),n._v("Vưu viện trưởng đem linh thạch thu hồi, trung niên nhân thì cầm dược liệu xoay người rời đi."),t("br"),t("br"),n._v("Một trận giao dịch, không quá mấy phút."),t("br"),t("br"),n._v("“Vưu viện trưởng, không nói không bán ư? Vì cái gì vị này Trác sư tới, trực tiếp bán ra?”"),t("br"),t("br"),n._v("Thấy đối phương tuỳ tiện mua đi, mà bản thân quỳ xuống đều không bán, Ngụy Trường Phong nắm đấm xiết chặt, trên đầu gân xanh toác ra."),t("br"),t("br"),n._v("Trương Huyền cũng lông mày nhăn lại."),t("br"),t("br"),n._v("Nếu như là đối phương nguyên tắc, khái không bán ra, liền xem như hắn, cũng không tiện nhiều lời, nhưng... Vị này Trác sư tới, trực tiếp bán ra, đối Ngụy Trường Phong nhưng bất luận thế nào không bán..."),t("br"),t("br"),n._v("Khác nhau đối đãi, cũng có chút có chút quá phận đi!"),t("br"),t("br"),n._v("“Trác sư lão sư, là Đan viện Lục Phong viện trưởng, hắn mua sắm là dùng đến luyện chế cấp sáu đan dược, tự nhiên sẽ bán ra, mà ngươi... Dường như chúng ta còn không có cái này giao tình, không phải muốn bán ra đi!”"),t("br"),t("br"),n._v("Mí mắt vừa nhấc, Vưu viện trưởng khẽ nói."),t("br"),t("br"),n._v("“Ta...” Ngụy Trường Phong cắn răng."),t("br"),t("br"),n._v("Quả thực, hắn cùng đối phương chỉ là lần đầu tiên gặp mặt, không có gì giao tình, đối phương bán cho ngươi là nhân tình, không bán cho ngươi thị công đạo, quả thực không yêu cầu vốn liếng."),t("br"),t("br"),n._v("“Không thể nói như thế, dược vật lớn nhất công hiệu, là cứu người. Thầy thuốc tấm lòng của cha mẹ, không có khả năng bởi vì giao tình yêu ghét, sẽ trở ngại chứng bệnh, để một bệnh nhân, do đó tử vong đi! Ngụy các chủ nữ nhi, ngàn cân treo sợi tóc, liền đợi đến Thập Diệp hoa kéo dài tính mạng, mong rằng Vưu viện trưởng, cho thành toàn!”"),t("br"),t("br"),n._v("Lại nhìn không được, tiến lên một bước, Trương Huyền ôm quyền nói."),t("br"),t("br"),n._v("“Chúng ta nói chuyện, có ngươi chen vào nói phần? Ngươi là ai?”"),t("br"),t("br"),n._v("Vưu phó viện trưởng lông mày trầm xuống."),t("br"),t("br"),n._v("Hắn lục tinh danh sư, địa vị tôn sùng, lại thêm thân là y sư học viện phó viện trưởng, lúc nào bị người nói dạy, huống chi là người hai mươi tuổi tất cả thanh niên."),t("br"),t("br"),n._v("“Tại hạ Trương Huyền, là học viện năm nay mới vừa đi tới tân sinh!” Trương Huyền nói."),t("br"),t("br"),n._v("“Ta còn tưởng rằng là ai, nguyên lai ngươi chính là cái kia Trương Huyền?”"),t("br"),t("br"),n._v("Sửng sốt một chút, Vưu phó viện trưởng lập tức cười lạnh: “Ta nghe qua ngươi, mặc dù chịu đến Mi viện trưởng, Triệu viện trưởng đám người coi trọng, nhưng mà ở trước mặt ta, nhưng mà cái vãn bối, vẫn không nói gì tư cách!”"),t("br"),t("br"),n._v("“Một cái tân sinh, đi tới học viện, không học tập cho giỏi, ỷ vào mấy phần thiên phú, khắp nơi rêu rao, huyên náo người người oán trách, quả thực có nhục danh sư danh tiếng. Muốn là đệ tử của ta, đã sớm trục xuất sư môn!”"),t("br"),t("br"),n._v("Nói đến đây, nhìn về phía Trương Huyền, lộ ra một chút khinh bỉ, khoát tay áo: “Được rồi, thừa dịp ta tâm tình còn không có hoàn toàn xấu thấu, lập tức rời đi, nếu không, đừng trách ta không khách khí.”"),t("br"),t("br"),n._v("“Không khách khí?”"),t("br"),t("br"),n._v("“Không sai! Tân sinh liền muốn có tân sinh bộ dạng, liền muốn tuân thủ tân sinh quy tắc, không biết tốt xấu, không biết đại cục, không tuân theo trưởng ấu, không tuân thủ lễ nghi... Nếu như lại ở đây lải nhải, ta không ngại thay ngươi lão sư, giáo huấn ngươi một trận!”"),t("br"),t("br"),n._v("Hai tay chắp sau lưng, Vưu phó viện trưởng hất lên ống tay áo."),t("br"),t("br"),n._v("“Thay lão sư ta giáo huấn ta? Không nói trước, ngươi là không tư cách này, cho dù có... Cũng không tới phiên ngươi!”"),t("br"),t("br"),n._v("Thấy đối phương ngữ khí bất thiện, đối với hắn ôm có thành kiến rất lớn, Trương Huyền lắc đầu, tiến về phía trước một bước: “Bôi nhọ nhục danh sư danh tiếng, không phải ngươi định đoạt, mà từ Danh Sư đường cùng học viện quyết định! Lại nói, nơi này mặc dù là ngươi biệt viện, nhưng cũng là học viện tài sản chung, đuổi ta đi... Ngươi có tư cách này? Ta nhớ được...”"),t("br"),n._v(" Nhẹ nhàng cười một tiếng, Trương Huyền thần sắc lạnh nhạt nhìn qua: “Ngươi... Còn không phải viện trưởng chứ?”"),t("br"),t("br"),n._v("Người khác kính hắn, hắn kính người khác."),t("br"),t("br"),n._v("Mới mở miệng liền có nhục danh sư danh tiếng, trục xuất sư môn... Coi như hắn tính khí khá hơn nữa, cũng có chút nhịn không được."),t("br"),t("br"),n._v("Danh sư, nặng nhất thanh danh."),t("br"),t("br"),n._v("Nguyên nhân chính là như thế, dù là biết rõ cứu chữa Ngụy Như Yên, sẽ hao tổn linh hồn, nguyên thần bị hao tổn, cũng sẽ không tiếc."),t("br"),t("br"),n._v("Đối phương mới mở miệng, liền khắp nơi rêu rao, trục xuất sư môn lời nói, tương đương với trần trụi nhục mạ, đã xúc phạm ranh giới cuối cùng của hắn."),t("br"),t("br"),n._v("“Làm càn! Ngươi cũng đã biết tại nói chuyện với người nào?”"),t("br"),t("br"),n._v("Không nghĩ tới một cái tân sinh, lại dám phản bác, Vưu phó viện trưởng tức giận sắc mặt trầm xuống."),t("br"),t("br"),n._v("“Nói chuyện với người nào, ta tự nhiên biết, lục tinh danh sư Vưu Hư, y sư học viện ‘Phó’ viện trưởng! Ta biết cùng ai nói chuyện, cái kia ngươi cũng đã biết lại cùng ai nói chuyện?”"),t("br"),t("br"),n._v("Sống lưng thẳng, cả người như là một cây trường thương, mang theo lăng lệ khí tức, Trương Huyền một cỗ bức người ngạo khí."),t("br"),t("br"),n._v("“Tại hạ là Mạc đường chủ, Mi, Triệu, Vệ ba vị viện trưởng khách quý, cầm trong tay ba cái trưởng lão lệnh bài; Có lục tinh luyện khí sư, lục tinh kinh hồng sư hai đại chức nghiệp! Đối ta tùy ý vũ nhục, miệng ra uy hiếp... Ta nhìn không phải ta làm càn, mà là ngươi đi?”"),t("br"),t("br"),n._v("Danh sư tôn nghiêm không thể nhục."),t("br"),t("br"),n._v("Huống chi, hắn loại này trời nhận danh sư!"),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Vưu phó viện trưởng híp mắt lại."),t("br"),t("br"),n._v("Vị này Trương Huyền sự tình, hắn nghe qua, vừa đến học viện, liền làm dư luận xôn xao, lão sinh tân sinh vỡ tổ không nói, lão sư tầm đó, cũng đều điên truyền."),t("br"),t("br"),n._v("Đối với loại này không tuân thủ quy củ người, bản thân hắn liền không thích, muốn giáo huấn một lần, cho cái ra oai phủ đầu, không nghĩ tới, thế mà bị ở trước mặt phản bác."),t("br"),t("br"),n._v("Đối phương nói không sai, cầm trong tay ba đại viện trưởng trưởng lão lệnh bài, tương đương với những trưởng lão này đích thân tới, thật đem hắn đuổi đi, tương đương với không nể mặt bọn họ, quả thực có sai lầm ổn thoả."),t("br"),t("br"),n._v("Hắn mặc dù cũng là lục tinh thượng phẩm danh sư, nhưng cùng rất nhiều viện trưởng so, vẫn là kém rất nhiều."),t("br"),t("br"),n._v("“Tại hạ mang theo thuộc hạ tới, chỉ là muốn mua sắm Thánh dược, có điều kiện gì, nâng chính là, đường đường lục tinh danh sư, nhưng nhằm vào cá nhân ta, tùy ý vũ nhục, nếu như ta đem trong chuyện này báo danh học viện, không biết học viện có thể hay không điều tra?”"),t("br"),t("br"),n._v("Không để ý tới đối phương phẫn nộ, Trương Huyền hừ lạnh."),t("br"),t("br"),n._v("“Ngươi... Ngươi rất tốt!”"),t("br"),t("br"),n._v("Không nghĩ tới bản thân một lúc sinh tức giận ngữ, bị đối phương bắt cái tay cầm, Vưu Hư phó viện trưởng, sắc mặt tái xanh."),t("br"),t("br"),n._v("Danh sư coi trọng thân phận, đường đường lục tinh danh sư, không bán thì không bán, mở miệng đối một vị lục tinh luyện khí sư tiến hành vũ nhục, quả thực làm trái danh sư phong độ cùng uy nghiêm."),t("br"),t("br"),n._v("Thật muốn báo lên tới học viện, lớn trừng phạt chắc chắn sẽ không có, nhưng nhớ một lần qua, là tránh không khỏi, truyền đi, làm không cẩn thận sẽ càng thêm mất mặt."),t("br"),t("br"),n._v("Đã sớm nghe nói nhà này sẽ quỷ dị cực kỳ, không nghĩ tới, nói cũng sắc bén như vậy."),t("br"),t("br"),n._v("Biết tiếp tục cùng đối phương đấu võ mồm đi xuống, làm không cẩn thận còn phải ăn thiệt thòi, Vưu Hư phó viện trưởng không trong vấn đề này tiếp tục dây dưa: “Thập Diệp hoa, là ta vất vả bồi dưỡng, nếu quả thật nếu mà muốn, cũng không phải là không thể bán ra!”"),t("br"),t("br"),n._v("Thấy hắn đột nhiên đổi giọng, Trương Huyền cùng Ngụy Trường Phong liếc mắt nhìn nhau, từng người nghi hoặc."),t("br"),t("br"),n._v("“Bất quá, giá cả có thể sẽ cao chút!”"),t("br"),t("br"),n._v("Vưu phó viện trưởng nói."),t("br"),t("br"),n._v("“Mời nói!” Ngụy Trường Phong ôm quyền."),t("br"),t("br"),n._v("Mặc dù không biết đối phương vì cái gì đột nhiên cải biến ý, nhưng có thể bán ra, dù sao cũng so mua không được, ở đây cãi lộn mạnh."),t("br"),t("br"),n._v("“Ta cần một gốc Vân Vụ hoa, nếu như ngươi có thể tại trong hai ngày, cho ta lấy tới một gốc, có thể đem Thập Diệp hoa, trực tiếp đưa ngươi!”"),t("br"),t("br"),n._v("Vưu phó viện trưởng nói."),t("br"),t("br"),n._v("“Vân Vụ hoa?” Trương Huyền cau mày."),t("br"),t("br"),n._v("Hắn mặc dù là tứ tinh y sư, đối không ít dược liệu, đều mười phần lý giải, nhưng chưa từng nghe qua loại thuốc này vật."),t("br"),t("br"),n._v("Đang muốn tuân hỏi một chút, đến cùng là cái gì, chỉ thấy một bên Ngụy Trường Phong, sắc mặt trắng nhợt, thân thể run rẩy."),t("br"),t("br"),n._v("“Ngươi biết thứ này?” Nhịn không được hỏi."),t("br"),t("br"),n._v("“Vân Vụ hoa, là Hồng Viễn sơn mạch Vân Vụ lĩnh đặc hữu một loại cây cối, cũng không phải là dược liệu... Rang nung, nước sôi đun sôi, sẽ sinh ra nồng đậm hơi nước, tựa như mây mù, mười phần đẹp, là một loại trân quý lá trà!”"),t("br"),t("br"),n._v("Ngụy Trường Phong nói."),t("br"),t("br"),n._v("“Lá trà? Dùng một gốc trà, đổi lấy Thập Diệp hoa?”"),t("br"),t("br"),n._v("Trương Huyền sững sờ."),t("br"),t("br"),n._v("Thập Diệp hoa là Thánh vực cấp những dược vật khác, giá trị ít nhất đều muốn mười cái linh thạch thượng phẩm trở lên, dùng một gốc lá trà liền trao đổi... Có phần quá giá rẻ đi!"),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Ngụy Trường Phong gật gật đầu, muốn nói cái gì, cuối cùng ngừng lại, mà là ngẩng đầu nhìn về phía trước mắt Vưu phó viện trưởng: “Tốt, ta đáp ứng!”"),t("br"),t("br"),n._v("“Vậy là tốt rồi, ta liền chờ tin tức tốt của ngươi!”"),t("br"),t("br"),n._v("Vưu phó viện trưởng nhẹ nhàng cười một tiếng, khoát tay áo."),t("br"),t("br"),n._v("“Yên tâm! Chỉ hy vọng ngươi có thể tuân thủ ước định, đến lúc đó, đem Thập Diệp hoa cho ta!”"),t("br"),t("br"),n._v("Hít sâu một hơi, Ngụy Trường Phong nắm đấm xiết chặt, lập tức gọi Trương Huyền một tiếng: “Thiếu gia, chúng ta đi thôi!”"),t("br"),t("br"),n._v("Thấy bọn họ thương nghị hết, Trương Huyền cũng không nhiều lời, hai người đi ra sân nhỏ."),t("br"),t("br"),n._v("Mới vừa vừa rời đi, một bên Tôn Nguyên nhịn không được nhìn về phía lão giả trước mắt: “Lão sư, Vân Vụ lĩnh, Thánh thú tụ tập, coi như Mi viện trưởng loại này tinh thông thuần thú, lại thực lực mạnh mẽ người tiến đến, đều rất khó trở về... Ngươi để Ngụy các chủ đi hái Vân Vụ hoa, có phải hay không... Quá nguy hiểm?”"),t("br"),t("br"),n._v("“Nguy hiểm? Ta đương nhiên biết nguy hiểm. Bất quá, Ngụy Trường Phong làm những chuyện như vậy, ngươi cũng không phải chưa từng nghe qua... Loại này gieo họa, chết tốt nhất! Còn cái kia Trương Huyền, mắt không trưởng ấu, không biết tôn ti, cùng đi tốt nhất, không đi, cũng cho hắn biết, lục tinh danh sư... Không phải có thể tuỳ tiện đắc tội!”"),t("br"),t("br"),n._v("Vưu phó viện trưởng cười lạnh."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-799"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-801"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);