(window.webpackJsonp=window.webpackJsonp||[]).push([[2079],{2435:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-793-trinh-duong-thu-đo"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-793-trinh-duong-thu-đo"}},[n._v("#")]),n._v(" Chương 793: Trịnh Dương thu đồ")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 793: Trịnh Dương thu đồ"),t("br"),t("br"),n._v("“Của ta?” Trương Huyền sững sờ, mang chén trà tay khẽ vung, vẩy ra một mảnh nước đọng: “Cái gì của ta?”"),t("br"),t("br"),n._v("“Vừa rồi ta đi thương hội chuyên môn hỏi thăm, thiếu gia cũng biết, ta trước kia mua qua phòng ốc, đối với mấy cái này môn đạo biết đến rất rõ ràng, chỉ chốc lát liền tra được phủ đệ chủ nhân... Tên liền gọi Trương Huyền!”"),t("br"),t("br"),n._v("Tôn Cường dường như cũng không thể tin được, tiếp tục nói: “Ta rất kỳ quái, liền chuyên môn hỏi thăm vị này Trương Huyền chức nghiệp cùng thân phận, lấy được trả lời là... Danh sư học viện năm nhất tân sinh! Cho nên, ta hoài nghi tân sinh bên trong, có phải hay không có cái cùng thiếu gia trùng tên, nếu như không có... Hẳn là thiếu gia ngươi!”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trương Huyền nháy con mắt, sắp choáng."),t("br"),t("br"),n._v("Hắn chưa từng tới bao giờ, càng không tiền mua như thế lớn phủ đệ, thế nào lại là hắn?"),t("br"),t("br"),n._v("Đổi lại trước kia, hắn cũng không xác thực nhận tân sinh bên trong có phải hay không có cùng mình trùng tên, nhưng hôm qua mới vừa thành lập học hội, tân sinh danh sách, ngón tay chạm đến qua, đã hình thành thư tịch, vừa mới tra duyệt một chút, đừng nói cùng hắn trùng tên, hài âm (âm đọc gần giống hoặc giống nhau) đều không có."),t("br"),t("br"),n._v("Đến cùng chuyện gì xảy ra?"),t("br"),t("br"),n._v("“Ngươi hỏi thăm sao? Tòa phủ đệ này là ai mua sắm, mua mua phòng ốc chủ nhân, xuất hiện qua không?” Trương Huyền nhịn không được nói."),t("br"),t("br"),n._v("Đã năm nhất tân sinh liền bản thân một cái Trương Huyền, như vậy thì khẳng định là có người giả mạo bản thân, đến mua phòng."),t("br"),t("br"),n._v("Nhưng... Mục đích làm như vậy là cái gì?"),t("br"),t("br"),n._v("“Ta hỏi thăm, nghe nói mua phòng ốc người, chưa hề xuất hiện qua, nhưng mà để đăng ký họ tên là cái này, chỉ nói là, chờ trùng tu xong, vị chủ nhân này sẽ tới tiếp thu...” Tôn Cường cũng không hiểu ra sao."),t("br"),t("br"),n._v("Càng đánh nghe, lại càng thấy đến kỳ quái."),t("br"),t("br"),n._v("Tòa phủ đệ này, hắn một lòng muốn mua, kết quả không có mua bên trên, lúc nào, đã về đến thiếu gia danh nghĩa?"),t("br"),t("br"),n._v("“Cái này...” Trương Huyền tràn đầy cổ quái."),t("br"),t("br"),n._v("Gặp qua mua đồ cho sai tiền, chưa từng nghe nói qua, mua phòng ốc trèo lên sai tên."),t("br"),t("br"),n._v("“Lạc Thất Thất, có phải hay không là ngươi...”"),t("br"),t("br"),n._v("Nhịn không được quay đầu."),t("br"),t("br"),n._v("Hồng Viễn thành, muốn nói nguyện ý vì hắn làm việc, bất kể đánh đổi, chỉ sợ cũng chỉ có người học sinh này."),t("br"),t("br"),n._v("“Thế nào lại là ta, ta coi như muốn mua, cũng không nhiều tiền như vậy ah...”"),t("br"),t("br"),n._v("Lạc Thất Thất lắc đầu."),t("br"),t("br"),n._v("“Cũng phải!” Trương Huyền cười khổ."),t("br"),t("br"),n._v("Tòa phủ đệ này, như thế rộng lớn, vị trí lại không sai, giá trị ít nhất phải mấy chục viên, thậm chí mấy trăm miếng linh thạch thượng phẩm, một cái tay cầm ra nhiều như vậy... Chỉ sợ bản thân người học sinh này lai lịch kỳ quái, cũng rất khó lấy ra."),t("br"),t("br"),n._v("“Cái kia... Có phải hay không là Ngọc Phi Nhi bọn họ?”"),t("br"),t("br"),n._v("Muốn nói tại Hồng Viễn thành có năng lực như thế, Ngọc Phi Nhi, Hình Viễn đám người khẳng định có thể."),t("br"),t("br"),n._v("Dù sao một vị là đường đường Lục công chúa, một vị gia thế không ít."),t("br"),t("br"),n._v("“Phi Nhi cùng Hình Viễn bọn họ, tiếp nhận tẩy cân phạt tủy, hiện tại đang lúc bế quan tu luyện, tranh thủ giống như ta, mượn cơ hội đột phá Quy Nhất cảnh, sẽ không có công phu chơi đùa những sự tình này...”"),t("br"),t("br"),n._v("Lạc Thất Thất lắc đầu."),t("br"),t("br"),n._v("Mấy người bọn hắn có công phu này, khẳng định sớm liền đến thăm hỏi Trương sư, không đến mức đến bây giờ đều không có lộ diện."),t("br"),t("br"),n._v("Liền bái phỏng thời gian đều không có, lại làm sao có thể chuyên môn đi mua mua phòng ốc? Chơi đùa nhiều như vậy?"),t("br"),t("br"),n._v("Lại nói Ngọc Phi Nhi là nàng bạn gái thân, thật muốn làm loại sự tình này, cũng không cần thiết giấu diếm nàng."),t("br"),t("br"),n._v("“Cái này... Này sẽ là ai?” Trương Huyền không hiểu ra sao."),t("br"),t("br"),n._v("Mạc Cao Viễn, Mi trưởng lão những người này, tự nhiên càng không khả năng."),t("br"),t("br"),n._v("Đường đường lục tinh danh sư, tự trọng thân phận, coi như muốn cùng mình giao hảo, cũng có rất nhiều biện pháp, không có khả năng chuyên môn mua sắm một tòa phủ đệ, tỉ mỉ trang trí, viết lên tên của mình."),t("br"),t("br"),n._v("“Trước mặc kệ, đã đối phương ghi tên của ta, liền khẳng định sẽ phơi bày thân phận, nếu không, tiền không phải là mất trắng?” Nghĩ một lát, nghĩ không ra như thế về sau, Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Đã người này làm như vậy, sớm muộn cũng sẽ qua tìm đến mình, nếu không chẳng khác nào tiền mất trắng, hắn nhưng không tin có đần như vậy người, nguyện ý vung tiền như rác."),t("br"),t("br"),n._v("“Vậy lão sư, chúng ta bây giờ còn vào cái viện này ư?”"),t("br"),t("br"),n._v("Lạc Thất Thất nhìn qua."),t("br"),t("br"),n._v("“Trước không cần vào, đăng ký tên của ta, chỉ cần trùng tu xong, hẳn là không bao lâu, liền sẽ đi tìm đến, đến lúc đó quang minh chính đại đi vào, miễn cho phiền phức!” Trương Huyền khoát tay áo."),t("br"),t("br"),n._v("Đây đều là phủ đệ của hắn, lúc nào đi tìm đều được, cũng không cần phải hiện tại lén lút đi vào, để cho người ta lo nghĩ, nhiều tốn nhiều sức lực."),t("br"),t("br"),n._v("“Ừm!” Lạc Thất Thất gật gật đầu."),t("br"),t("br"),n._v("“Tôn Cường, ngươi trở về đem Vương Dĩnh, Trịnh Dương đám người tìm trở về, tạm thời không cần đi tìm chỗ ở, lưu tâm nhiều một chút tòa phủ đệ này!” Trương Huyền bàn giao."),t("br"),t("br"),n._v("“Đúng, thiếu gia!” Tôn Cường gật gật đầu."),t("br"),t("br"),n._v("“Tốt, chúng ta về học viện trước đi!”"),t("br"),t("br"),n._v("Biết phủ đệ chủ nhân lại là bản thân, tiếp tục đợi ở chỗ này cũng không có ý nghĩa gì, giao phó xong Tôn Cường, cùng Lạc Thất Thất, Hồ Yêu Yêu hai người một lần nữa trở lại học viện."),t("br"),t("br"),n._v("Vừa về tới chỗ ở, Trương Huyền lập tức khoanh chân ngồi xuống."),t("br"),t("br"),n._v("Điều chỉnh trọn vẹn hai canh giờ, lúc này mới cảm thấy mệt mỏi biến mất, lần nữa khôi phục."),t("br"),t("br"),n._v("Ngẩng đầu hướng nhìn ra ngoài, đã nhanh đến nửa đêm, màn đêm đen kịt như là màu mực đem bốn phía toàn bộ nhuộm thành đáy nồi, thời tiết có chút u ám, không có sao, lộ ra càng thêm âm u."),t("br"),t("br"),n._v("“Vừa vặn không có việc gì, đi y sư học viện nhìn xem sách!”"),t("br"),t("br"),n._v("Một cái ý nghĩ xông ra."),t("br"),t("br"),n._v("Hiện tại trời tối như vậy, vừa vặn cũng không có việc gì, không bằng đi y sư học viện nhìn xem sách, một khi thành công, cứu chữa Ngụy Như Yên, liền sẽ đơn giản không ít."),t("br"),t("br"),n._v("“Vu hồn ly thể!”"),t("br"),n._v(" Cũng không có gì có thể chần chờ, tinh thần khẽ động, Vu hồn từ lông mày tâm nhảy ra ngoài, nhẹ nhàng nhoáng một cái, nhanh chân hướng y sư bay vút đi."),t("br"),t("br"),n._v("Có Vệ Nhiễm Tuyết kinh nghiệm, biết liền xem như Vu hồn, cũng phải cẩn thận, một Luffy không nhanh, hao tốn tiếp cận nửa canh giờ, cái này mới đi đến y sư tháp lân cận."),t("br"),t("br"),n._v("“Y sư học viện tàng thư khố ở vị trí này...”"),t("br"),t("br"),n._v("Bởi vì tới ban ngày qua một lần, chuyên môn nhìn kỹ vị trí, lại tới, liền lộ ra xe nhẹ đường quen, thời gian không lâu, đi tới tàng thư khố gần."),t("br"),t("br"),n._v("Nguy nga cao lớn tàng thư khố, tản mát ra khí tức đặc biệt, cho người ta một loại nặng nề cùng trang nghiêm hương vị."),t("br"),t("br"),n._v("Cùng Kinh Hồng viện Tàng Thư các đồng dạng, cần học phần cùng thẻ học viên mới có thể tiến nhập, Trương Huyền cũng không vội vã, an tĩnh các ở bên ngoài."),t("br"),t("br"),n._v("Y sư học viện cùng kinh hồng học viện khác biệt, người sau nhân viên thưa thớt, đọc sách cũng là không nhiều. Trước người thân là bài danh thứ năm học viện, học sinh đủ có mấy vạn, mỗi ngày ra ra vào vào, sang đây xem sách, không biết bao nhiêu, chỉ cần chờ đợi, khẳng định có cơ hội."),t("br"),t("br"),n._v("Quả nhiên, cũng không lâu lắm, liền có người từ bên trong đi ra, nhân cơ hội này, một lần hành động chui vào."),t("br"),t("br"),n._v("Đi vào phòng lập tức nhìn thấy vô số thư tịch."),t("br"),t("br"),n._v("“Bắt đầu đi!”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Trương Huyền cũng không nói nhảm, ánh mắt hướng giá sách quét tới, trong nháy mắt vô số thư tịch, tới tấp đi vào trong óc."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Trương Huyền trở lại học viện, Tôn Cường bên này, cũng đến chỗ ở."),t("br"),t("br"),n._v("Chờ đến trời tối, chỉ thấy Vương Dĩnh đám người, toàn bộ ủ rũ cúi đầu đi trở về."),t("br"),t("br"),n._v("Tại danh sư học viện lân cận, muốn tìm được một tòa thích hợp phủ đệ, quả thực rất khó, nếu không, Tôn Cường cũng không có khả năng nhiều ngày như vậy, đều không thu hoạch được gì."),t("br"),t("br"),n._v("“Trịnh Dương tiểu thiếu gia đâu? Không cùng các ngươi cùng một chỗ ư?”"),t("br"),t("br"),n._v("Đang định cùng mọi người nói, không cần tiếp tục tìm, đột nhiên nhướng mày, Tôn Cường nhịn không được hỏi."),t("br"),t("br"),n._v("Thiếu gia ba cái học sinh, Vương Dĩnh, Lưu Dương đều đã trở về, Trịnh Dương vẫn còn không thấy tung tích."),t("br"),t("br"),n._v("“Chúng ta cũng không biết, buổi sáng sau khi ra cửa, hắn liền bản thân đi, chẳng qua không vội vã, hẳn là rất nhanh sẽ trở về đi!”"),t("br"),t("br"),n._v("Lưu Dương nói."),t("br"),t("br"),n._v("“Đúng vậy a, Trịnh Dương vì người cơ cảnh, lại lấy được lão sư chân truyền, sẽ không có sự tình!” Vương Dĩnh cũng nhẹ gật đầu."),t("br"),t("br"),n._v("Trương sư rất nhiều học sinh thân truyền bên trong, thích ứng năng lực mạnh nhất là Viên Đào, thứ yếu chính là Trịnh Dương, hắn ra ngoài không gieo họa người khác là tốt lắm rồi, làm sao sẽ mất?"),t("br"),t("br"),n._v("Đang nói chuyện, chỉ thấy một bóng người đi trở về, không phải Trịnh Dương thì là ai."),t("br"),t("br"),n._v("“Cường thúc!”"),t("br"),t("br"),n._v("Đi tới gần, Trịnh Dương con mắt tỏa ánh sáng, trên mặt lộ ra nồng đậm hưng phấn."),t("br"),t("br"),n._v("“Ngươi tìm tới chỗ ở?” Nhìn hắn bộ dáng này, Tôn Cường nghi ngờ nhìn qua."),t("br"),t("br"),n._v("“Không có... Nhưng mà gặp phải một việc... Muốn cùng Cường thúc thương nghị một chút!” Trịnh Dương vò đầu."),t("br"),t("br"),n._v("“Cùng ta thương nghị? Nói đi, chuyện gì?” Tôn Cường hỏi."),t("br"),t("br"),n._v("Trương Huyền trước đó đã thông báo, để cái này mấy cái học sinh, nghe theo Tôn Cường an bài, Trịnh Dương đám người, cũng đều lấy “Cường thúc” xưng hô."),t("br"),t("br"),n._v("“Là như vậy, hôm nay ta đi ra thời điểm, gặp một cái... Muốn cùng ta học tập thương pháp gia hỏa, ta nghĩ thu làm học sinh, để hắn ở đây ở vài ngày, không biết có thể hay không?”"),t("br"),t("br"),n._v("Trịnh Dương có chút lúng túng nói."),t("br"),t("br"),n._v("“Thu học sinh?” Tôn Cường trừng to mắt."),t("br"),t("br"),n._v("Chính ngươi vẫn là học sinh, liền muốn thu học sinh?"),t("br"),t("br"),n._v("“Đúng vậy a, người này thương pháp cũng không tệ lắm, có cơ sở nhất định, ta tùy tiện chỉ điểm mấy lần, để hắn rất là hưng phấn, nhất định phải bái ta làm thầy, đưa cho không ít học phí... Ta cũng không tiện cự tuyệt...” Trịnh Dương do dự một chút, nói."),t("br"),t("br"),n._v("Hôm nay hắn ra ngoài tìm nhà, vừa vặn gặp một cái ác bá, chọc giận xuống ra tay, đem người kia giáo huấn hết, người thanh niên này liền tìm tới, nói cùng với hắn học thương."),t("br"),t("br"),n._v("Hơn nửa năm đó, hắn một mực cùng Trương sư khổ luyện không ngừng, sớm đã đạt tới quỷ thần khó lường cảnh giới, nghe được có người muốn học, nhịn không được chỉ điểm vài câu, không nghĩ đến cái này thanh niên ngộ tính cực cao, thế mà vừa học liền biết."),t("br"),t("br"),n._v("Mặc dù bản thân thương pháp yếu đi chút, may mắn cơ sở còn có thể, lập tức để hắn sinh ra thu đồ tâm tư."),t("br"),t("br"),n._v("Bất quá... Hắn hiện tại vẫn là học sinh, không xuất sư, muốn thu đồ, phải cùng sư phụ thương nghị một chút."),t("br"),t("br"),n._v("Sư phụ vắng mặt, tự nhiên trưng cầu một chút Cường thúc ý kiến."),t("br"),t("br"),n._v("“Cho học phí? Cho bao nhiêu?” Tôn Cường nhìn qua."),t("br"),t("br"),n._v("“Đều ở nơi này!”"),t("br"),t("br"),n._v("Trịnh Dương đưa tới một chiếc nhẫn."),t("br"),t("br"),n._v("Tôn Cường nghi ngờ nhận lấy, chân khí nhúc nhích, hướng phía trong xem xét, lập tức con mắt một chút trợn tròn: “Hai vạn linh thạch trung phẩm, nhiều như vậy đan dược... Đều là hắn hướng ngươi bái sư học phí?”"),t("br"),t("br"),n._v("Trữ vật giới chỉ bên trong, một đống lớn đồ vật, lại có hai vạn linh thạch trung phẩm, cùng không biết bao nhiêu dược vật."),t("br"),t("br"),n._v("Tổng giá trị cộng lại, tuyệt đối không dưới năm vạn linh thạch trung phẩm..."),t("br"),t("br"),n._v("Nhiều tiền như vậy, liền cùng hắn học tập thương pháp?"),t("br"),t("br"),n._v("Cái này Trịnh Dương, từ nơi nào bắt cái oan đại đầu tới?"),t("br"),t("br"),n._v("“Đúng vậy a, ta nghĩ thu... Chính là sợ lão sư không đồng ý, mong rằng Cường thúc, hỗ trợ nói ngọt hai câu...” Trịnh Dương có chút xấu hổ."),t("br"),t("br"),n._v("“Yên tâm đi, dạy nhiều như vậy học phí, thiếu gia khẳng định sẽ đồng ý, lại nói, thiếu gia thân là danh sư, tự nhiên cũng hi vọng truyền thừa của mình có thể phát dương quang đại, không cần lo lắng!”"),t("br"),t("br"),n._v("Tôn Cường cười nói."),t("br"),t("br"),n._v("Cùng thiếu gia tiếp xúc lâu như vậy, cái gì tập tính hắn còn không biết?"),t("br"),t("br"),n._v("Chỉ cần học phí giao đủ... Đừng nói thu một cái đồ đệ mà thôi, coi như lại đến là cái tám cái, cũng sẽ không nhiều lời."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-801"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-803"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);