(window.webpackJsonp=window.webpackJsonp||[]).push([[208],{560:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1164-nhu-that-nhu-ao"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1164-nhu-that-nhu-ao"}},[n._v("#")]),n._v(" Chương 1164: Như thật như ảo")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Lúc này Trương Cửu Tiêu, một thân trường bào màu xanh nhạt, có vẻ mười phần nho nhã, trước ngực mang theo không phải danh sư huy chương, mà là giám bảo sư, bảy viên sao lấp lánh tỏa ánh sáng, đại biểu thân phận."),t("br"),t("br"),n._v("Bên cạnh cùng thứ nhất lên, là cái lão giả, hơn sáu mươi tuổi bộ dáng, đồng dạng mang theo thất tinh giám bảo sư huy chương, một thân tu vi đã đạt đến Thánh vực tứ trọng, khí độ bất phàm."),t("br"),t("br"),n._v("“Cái tên này... Còn là giám bảo sư?”"),t("br"),t("br"),n._v("Trương Huyền sững sờ."),t("br"),t("br"),n._v("Hắn thấy tận mắt Trương Cửu Tiêu lăng không viết, biết tại thư hoạ bên trên trình độ không thấp, không nghĩ tới giám bảo bên trên cũng đạt tới thất tinh cấp bậc."),t("br"),t("br"),n._v("Không hổ là Thanh Nguyên đế quốc đệ nhất thiên tài, quả nhiên không tưởng tượng đơn giản như vậy."),t("br"),t("br"),n._v("“Mục hội trưởng, đã lâu không gặp!”"),t("br"),t("br"),n._v("Thấy lão giả, Ngô sư cười nghênh đón tiếp lấy."),t("br"),t("br"),n._v("“Ngô Như Phong, không mời ngươi, cũng không biết tới thấy ta, có phải hay không sớm đã quên lão bằng hữu ân tình?” Lão giả nện cho đối phương bên dưới, cười mắng một tiếng."),t("br"),t("br"),n._v("“Làm sao có thể, ta cũng vừa trở về không lâu, xử lý một ít chuyện...” Ngô sư đi tới trước mặt."),t("br"),t("br"),n._v("“Nhìn tới vị này hẳn là giám bảo sư công hội hội trưởng...” Xem hai người thấp giọng hàn huyên, quan hệ rất quen, Trương Huyền nhẹ gật đầu."),t("br"),t("br"),n._v("Thân là thất tinh giám bảo sư, lại bị Ngô sư xưng hô “Hội trưởng” rất dễ dàng đoán ra thân phận."),t("br"),t("br"),n._v("Đang nghĩ cẩn thận quan sát vị hội trưởng này, lập tức cảm thấy một ánh mắt, nhanh chằm chằm tới, Trương Huyền ngẩng đầu, chỉ thấy Trương Cửu Tiêu nhìn xem bản thân, trong mắt mang theo từng tia từng tia địch ý."),t("br"),t("br"),n._v("Mặc dù không phải rất mãnh liệt, nhưng hắn tâm cảnh đạt tới 2 5.1, có thể so với bát tinh danh sư, còn là rất nhẹ nhàng liền có thể cảm ứng ra tới."),t("br"),t("br"),n._v("“Địch ý? Không có đắc tội qua hắn a?”"),t("br"),t("br"),n._v("Trương Huyền nghi ngờ."),t("br"),t("br"),n._v("Hai người chỉ thấy qua một mặt, sợ gây ra hiểu lầm, còn chuyên hô lên “Cố gắng lên” lấy đó cổ vũ, hữu hảo như vậy thái độ, không báo chi lấy lý liền thôi, mang địch ý... Làm gì?"),t("br"),t("br"),n._v("“Tại hạ Trương Cửu Tiêu, không biết các hạ xưng hô như thế nào?”"),t("br"),t("br"),n._v("Chính cảm khái người tốt khó xử, thanh niên đi tới trước mặt, ôm quyền hỏi."),t("br"),t("br"),n._v("“Trương Huyền!”"),t("br"),t("br"),n._v("“Trương Huyền? Thế nhưng là Hồng Viễn danh sư học viện vị kia tân nhiệm viện trưởng? Ta nghe nói qua sự tích của ngươi, là một thiên tài!”"),t("br"),t("br"),n._v("Nghe được tên, Trương Cửu Tiêu bừng tỉnh hiểu ra: “Khó trách về mặt tâm cảnh, bất phàm như thế, có thể tuỳ tiện thông qua luyện tâm cầu, thống ngự một cái học viện, tâm cảnh không mạnh, quả thực làm không được!”"),t("br"),t("br"),n._v("Trước đó vẫn muốn gặp phải vị kia lục tinh danh sư là ai, giờ phút này mới rốt cục rõ ràng."),t("br"),t("br"),n._v("Vị này Trương Huyền rất nhiều sự tích, hắn nghe nói qua một chút, quả thực được xưng tụng thiên tài trong thiên tài."),t("br"),t("br"),n._v("Bất quá, thiên tài đi nữa, cũng chỉ là lục tinh danh sư mà thôi, cùng hắn so sánh, còn là kém xa tít tắp."),t("br"),t("br"),n._v("“Là học viện rất nhiều thầy trò nâng đỡ, có chút cố mà làm, đang định từ chức...” Trương Huyền khoát tay áo."),t("br"),t("br"),n._v("Danh sư học viện viện trưởng, Chương Dẫn Khâu tới, hắn đã sớm ý định sa thải, chỉ là mọi người không cho phép thôi."),t("br"),t("br"),n._v("“Từ chức? Cũng đúng, như ngươi loại này tuổi tác, thiếu hụt rèn luyện, trực tiếp làm danh sư học viện viện trưởng, quả thực sẽ để cho học viện, thanh danh không tốt lắm!”"),t("br"),t("br"),n._v("Trương Cửu Tiêu nhẹ gật đầu."),t("br"),t("br"),n._v("“Ây...” Không nghĩ tới bản thân tự khiêm nhường lời nói, đối phương thật coi chuyện, Trương Huyền bất đắc dĩ lắc đầu, lập tức mất đi cùng đối phương tán gẫu hứng thú, đang nghĩ rời khỏi, liền nghe đến thanh âm của đối phương tiếp tục vang lên."),t("br"),t("br"),n._v("“Ngô sư có thể mang ngươi tới này cái phẩm giám hội, nhìn tới mười phần chú trọng... Chỉ là không biết, Trương sư có thể học qua giám bảo?”"),t("br"),t("br"),n._v("“Giám bảo... Hơi học qua một chút!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Không biết đạt đến mấy tinh?” Nghe được học qua, Trương Cửu Tiêu sửng sốt một chút, nhìn qua."),t("br"),t("br"),n._v("“Hổ thẹn, chỉ có ngũ tinh!” Trương Huyền cười khổ."),t("br"),t("br"),n._v("So sánh những nghề nghiệp khác, giám bảo sư sách, xem ít, chỉ đạt tới ngũ tinh cấp bậc, không tính là cao minh bao nhiêu."),t("br"),t("br"),n._v("“Ngũ tinh? Thật ra thì lấy tuổi của ngươi, cũng xem là không tệ... Nói rõ có phương diện này thiên phú, về sau học tập cho giỏi, vượt qua ta cũng là có khả năng!”"),t("br"),t("br"),n._v("Nhẹ gật đầu, Trương Cửu Tiêu khẽ nói: “Ngũ tinh giám bảo sư, dựa theo đạo lý, là không có tư cách tới này lần thịnh hội, có điều, hẳn là Ngô sư cưng chiều, mang ngươi tới tăng trưởng thoáng cái kiến thức đi!”"),t("br"),t("br"),n._v("“Xem như thế đi!” Trương Huyền, thuận miệng lên tiếng."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Trương Cửu Tiêu nói: “Lần này đánh giá đại hội, mời không ít cường giả tới, đều là một phương nghề nghiệp nhân vật thủ lĩnh, đến lúc đó, có thể sẽ lấy ra không ít bảo vật, ngươi ta nhận biết cũng coi như có duyên phận, lại nói đều họ Trương, như vậy đi, một lát nữa, đi theo đằng sau ta, nếu có cái gì không nhận ra, hoặc là xem không hiểu, có thể trực tiếp hỏi, tất cả mọi người là danh sư, chỉ điểm ngươi vài câu, thuận tiện cho ngươi tăng lên một chút giám bảo kiến thức, cũng là nên! Có lẽ sau lần này, bừng tỉnh hiểu ra, thừa cơ xung kích lục tinh giám bảo sư, cũng chưa biết chừng.”"),t("br"),t("br"),n._v("“Làm phiền!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Không cần khách khí! Ta mặc dù là danh sư, nhưng ở giám bảo trên chức nghiệp, cũng xuống cực lớn công phu, tại Thanh Nguyên đế quốc giám bảo sư công hội, xem như có chút danh tiếng, vẫn còn có chút mặt mũi!” Trương Cửu Tiêu vẻ mặt thản nhiên, trong giọng nói để lộ ra không lọt dấu vết ngạo khí."),t("br"),t("br"),n._v("Trước đó xông luyện tâm cầu, bị trước mắt vị này đả kích thoáng cái, hiện tại lật về một thành, suy nghĩ thông suốt, không nói ra được thư sướng."),t("br"),n._v(" “Ừm...”"),t("br"),t("br"),n._v("Trương Huyền gật đầu, đang nghĩ nói tiếp, chỉ thấy Ngô sư cùng vừa rồi vị kia Mục hội trưởng, đồng loạt nhìn lại, người sau tràn đầy vui vẻ: “Vào xem lấy tán gẫu, quên nói một tiếng, vị này Trương Cửu Tiêu, ba năm trước đây cùng ta học tập giám bảo, ngắn như vậy thời gian, liền đạt tới thất tinh, thiên phú mạnh, có thể nói kinh khủng... Các ngươi đều là danh sư, hẳn là không cần giới thiệu đi!”"),t("br"),t("br"),n._v("“Trương sư thiên tài tuyệt đỉnh, tự nhiên không cần!”"),t("br"),t("br"),n._v("Ngô sư nhẹ gật đầu, gọi Trương Huyền một tiếng: “Mục hội trưởng, vị này là bằng hữu của ta, Trương Huyền Trương sư, chớ nhìn hắn tuổi không lớn lắm, nhưng cũng là một vị hiếm thấy thiên tài, vốn ý định đi tìm ngươi khảo hạch giám bảo sư, đã đụng phải, liền trước thời hạn nhận thức một chút!”"),t("br"),t("br"),n._v("“Bằng hữu? Ta còn tưởng rằng là vãn bối của ngươi...”"),t("br"),t("br"),n._v("Mục hội trưởng sững sờ."),t("br"),t("br"),n._v("Ngô sư tuổi tác đều bảy, tám trăm tuổi, vốn tưởng là mang theo một cái hai mươi tuổi thanh niên, là đệ tử mới thu hoặc là vãn bối, không nghĩ tới là bạn."),t("br"),t("br"),n._v("Có thể xưng hô như vậy, nói rõ là ngang hàng trao đổi, đủ thấy chú trọng."),t("br"),t("br"),n._v("“Trương sư đại tài, xưng hô bằng hữu, đều là ta với cao, sao có thể là vãn bối, ta cũng không dám...” Ngô sư vội vàng xua tay."),t("br"),t("br"),n._v("Trước mắt vị này chính là trời nhận danh sư, trời nhận Thánh giả, xưng hô bằng hữu quả thực xem như với cao, thật muốn nói là vãn bối, chính hắn trước muốn hù chết."),t("br"),t("br"),n._v("“Ồ?” Thấy bạn già thận trọng như thế, không giống ra vẻ khiêm tốn, Mục hội trưởng nghi ngờ nhìn Trương Huyền một cái, do trước đó coi thường, cũng biến thành ngưng trọng: “Đã Ngô sư coi trọng như thế, đó cũng là ta Mục mỗ người bằng hữu!”"),t("br"),t("br"),n._v("Ngô sư là hắn nhiều năm bạn già, từ trước tới nay thanh cao, chú trọng quy củ, có thể nói như vậy, đủ để chứng minh, trước mắt người thanh niên này, là thực sự không đơn giản."),t("br"),t("br"),n._v("“Mục hội trưởng khách khí!”"),t("br"),t("br"),n._v("Trương Huyền ôm quyền."),t("br"),t("br"),n._v("“Cái này có cái gì khách khí, ta cùng Ngô sư thế nhưng là bạn cũ lâu năm!” Mục hội trưởng cười cười, quay đầu nhìn về phía Trương Cửu Tiêu: “Cửu Tiêu, mặc dù các ngươi tuổi tác gần như, nhưng vị này Trương sư, nếu là bằng hữu của ta, cũng chính là sư thúc của ngươi, kêu thúc!”"),t("br"),t("br"),n._v("“Thúc...” Trương Cửu Tiêu thân thể nhoáng một cái, suýt chút nữa hộc máu."),t("br"),t("br"),n._v("Mới vừa nói hết muốn bảo bọc đối phương, để hắn theo ở phía sau học tập, kết quả là đến như vậy một câu... Kìm nén sắc mặt đỏ lên, không có ngất đi."),t("br"),t("br"),n._v("“Ừm!” Mục hội trưởng nhẹ gật đầu: “Ngô sư, Trương tiểu huynh đệ, những người khác hầu như đều đến đông đủ, mời vào bên trong!”"),t("br"),t("br"),n._v("“Được!” Ngô sư nhẹ gật đầu."),t("br"),t("br"),n._v("Mấy người theo sát tại sau lưng, hướng đại sảnh đi vào."),t("br"),t("br"),n._v("Đại sảnh đường kính chừng hơn ba mươi mét, trung tâm một cái hình chữ nhật cái bàn, hai bên quả nhiên đã ngồi đầy người, phần lớn đều râu bạc trắng bồng bềnh, cùng vị này Mục hội trưởng tuổi tác tương đương."),t("br"),t("br"),n._v("Hai vị người quen cũng ngồi ở bên trong, theo thứ tự là khải linh sư công hội Nguyễn hội trưởng cùng kinh hồng sư công hội Vệ hội trưởng."),t("br"),t("br"),n._v("Mục hội trưởng mấy bước đi tới cái bàn phía trước nhất, ngồi xuống, ngay sau đó gọi: “Ngồi đi!”"),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu cùng Ngô sư song song ngồi tại hạ tay."),t("br"),t("br"),n._v("“Cửu Tiêu, ngươi đứng tại đằng sau ta!”"),t("br"),t("br"),n._v("Thấy Trương Cửu Tiêu cũng muốn ngồi xuống, Mục hội trưởng nhíu nhíu mày, nói."),t("br"),t("br"),n._v("“Vâng!” Da mặt co lại, Trương Cửu Tiêu gật gật đầu, đứng tại hội trưởng sau lưng, một mặt u oán."),t("br"),t("br"),n._v("Vốn nghĩ, nhờ vào thất tinh giám bảo sư thân phận, có thể ở trước mặt đối phương, lật về một thành, kết quả, còn chưa bắt đầu biểu hiện ra... Liền bị đối phương ép không ngẩng đầu được lên."),t("br"),t("br"),n._v("“Đã tất cả mọi người đến đông đủ, bằng hữu, có thể hay không đưa ngươi đồ vật lấy ra?”"),t("br"),t("br"),n._v("An bài xong, nhìn về phía bàn dài ngồi đối diện một người, Mục hội trưởng nói."),t("br"),t("br"),n._v("Người này mang theo một tấm mặt nạ đồng xanh, không nhìn thấy dung mạo, xuyên thấu qua trần trụi làn da, đó có thể thấy được, tuổi tác tại bốn mươi đến năm mươi ở giữa."),t("br"),t("br"),n._v("“Các vị đều là Thanh Nguyên thành nhân vật có mặt mũi, lại có tiếng sư đường Ngô đường chủ tọa trấn, uy tín ta tự nhiên tin tưởng, bất quá... Cảnh cáo nói ở phía trước, đồ vật chỉ làm cho các ngươi đánh giá, nghĩ biện pháp phá giải phía trên lưu lại trận văn, còn, bên trong phong ấn cái gì, các ngươi nhìn ra cái gì, ta đều không có ý định bán ra... Đương nhiên, chỉ cần có thể giúp ta giải quyết vấn đề, tất có có hậu lễ đưa tặng!”"),t("br"),t("br"),n._v("Mặt nạ đồng xanh mở miệng."),t("br"),t("br"),n._v("Thanh âm có chút khàn khàn, nghe xong liền biết thay đổi thanh tuyến, cố ý ngụy trang để cho người ta nghe không hiểu."),t("br"),t("br"),n._v("“Ngươi yên tâm, ta có thể đem bọn họ tìm đến, khẳng định là đáng giá tin tưởng!” Mục hội trưởng khoát tay áo."),t("br"),t("br"),n._v("“Ta tin được Mục hội trưởng nhân phẩm!” Mặt nạ đồng xanh nhẹ gật đầu, cổ tay khẽ đảo, một cái quyển trục xuất hiện ở trước mặt mọi người, nhẹ nhàng mở ra, một bộ sơn thủy, lập tức xuất hiện tại trước mặt, xanh biếc ngọn núi, gần ngay trước mắt, trên núi chim thú kêu to, nước chảy róc rách, cho người ta như thật như ảo cảm giác."),t("br"),t("br"),n._v("Liền phảng phất, thật có một ngọn núi đứng sừng sững ở cách đó không xa, trên mặt thậm chí có thể cảm nhận được từ đó toả ra ôn nhuận khí tức."),t("br"),t("br"),n._v("“Đây là... Bát tinh cấp bậc thư hoạ, đã đạt đến như thật như ảo cảnh giới...”"),t("br"),t("br"),n._v("Nhìn thấy trên quyển trục tác phẩm hội họa, một cái lão giả đột nhiên đứng dậy, kích động hốc mắt thấu đỏ, thân thể không ngừng run rẩy. Vị lão giả này người mặc thư họa sư trường bào, trước ngực bảy viên sao lấp lánh, hẳn là chính là Ngô sư trước đó nói, thư họa sư công hội hội trưởng."),t("br"),t("br"),n._v("“Như thật như ảo?” Trương Huyền nghi ngờ."),t("br"),t("br"),n._v("“Như thật như ảo là thư hoạ tám cảnh năng lực, đạt tới loại cảnh giới này, làm ra bức tranh, như là thật, người bình thường nhìn không ra thật giả, mấu chốt nhất là... Thư hoạ có thể tự động hấp thu linh khí, duy trì bên trong linh tính sống sót, thậm chí tu luyện, thời gian lâu dài, đều có thể nhảy ra, cùng sinh mệnh đồng dạng... Nói cách khác, đạt tới loại cảnh giới này thư hoạ, đã cùng thật, giống như thật không thật, như ảo không huyễn!”"),t("br"),t("br"),n._v("Ngô sư thấp giọng giải thích."),t("br"),t("br"),n._v("“Mạnh như vậy?” Trương Huyền khiếp sợ, trong mắt lặng lẽ hiện lên từng đạo hoa văn, nhìn về phía trước mắt bức tranh nhìn sang."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1172"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1174"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);