(window.webpackJsonp=window.webpackJsonp||[]).push([[2080],{2434:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-794-hang-hai-tiet-chan-duong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-794-hang-hai-tiet-chan-duong"}},[n._v("#")]),n._v(" Chương 794: Hăng hái Tiết Chân Dương")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 794: Hăng hái Tiết Chân Dương"),t("br"),t("br"),n._v("“Vậy liền quá tốt rồi...”"),t("br"),t("br"),n._v("Nghe được Tôn Cường nói lão sư không có vấn đề, Trịnh Dương thở phào nhẹ nhõm, vội vàng gật đầu, tràn đầy hưng phấn quay đầu hướng nhìn ra ngoài: “Vào đi!”"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Nghe được hắn, ngoài cửa một thanh niên đi đến, cầm trong tay một chuôi trường thương, toàn thân khí tức ngưng tụ, như là phá không mà ra lợi kiếm."),t("br"),t("br"),n._v("Chừng ba mươi tuổi, trên mặt góc cạnh rõ ràng, cho người ta một loại cương nghị cảm giác."),t("br"),t("br"),n._v("“Cường thúc, Vương Dĩnh, Lưu Dương, đây chính là ta mới thu học sinh!” Trịnh Dương hướng mọi người giới thiệu."),t("br"),t("br"),n._v("“Học sinh Tiết Chân Dương, gặp qua chư vị!”"),t("br"),t("br"),n._v("Thanh niên liền ôm quyền."),t("br"),t("br"),n._v("“Tiết Chân Dương? Tu vi của ngươi... Dường như so Trịnh Dương mạnh hơn không ít chứ? Vì sao phải bái hắn làm thầy?” Tôn Cường lông mày nhíu một cái."),t("br"),t("br"),n._v("Hắn mặc dù không nhìn ra tu vi của đối phương đến cùng cao bao nhiêu, nhưng tuyệt đối phải vượt qua Trịnh Dương."),t("br"),t("br"),n._v("Mạnh như thế người, vì sao phải bái một cái mười mấy tuổi trẻ con làm thầy?"),t("br"),t("br"),n._v("“Nghe đạo có tuần tự, thuật nghiệp hữu chuyên công, ta tu vi mặc dù so Trịnh lão sư mạnh, nhưng đối thương pháp lý giải kém xa tít tắp, có thể hướng hắn học tập, là vinh hạnh của ta!” Tiết Chân Dương một mặt cung kính."),t("br"),t("br"),n._v("Vị này Tiết Chân Dương, chính là danh sư học viện Chân Dương hội hội trưởng, vị kia muốn muốn khiêu chiến Trương Huyền gia hỏa."),t("br"),t("br"),n._v("Hắn một lòng muốn đem Lạc Diệp thương pháp tu luyện tới đệ thập trọng, rời đi học viện rèn luyện, trong lúc vô tình thấy được Trịnh Dương thi triển thương pháp."),t("br"),t("br"),n._v("Mặc dù người sau tu vi kém xa hắn, nhưng đối thương pháp lý giải, đã đạt tới nhân thương hợp nhất cảnh giới, uy lực kinh người."),t("br"),t("br"),n._v("Hàn huyên vài câu, phát hiện hắn đối thương pháp lý giải, càng là đạt đến từ phức tạp về đơn giản, nhắm thẳng vào đại đạo cảnh giới, lúc này mới không nhịn được muốn cùng hắn học tập."),t("br"),t("br"),n._v("Có thể đoán được, chỉ phải nghiêm túc học tập, hẳn là không bao lâu, liền có thể hoàn toàn lĩnh ngộ Lạc Diệp thương pháp đệ thập trọng, đem Trương Huyền quét ngang, một lần nữa chấn hưng Chân Dương hội uy danh!"),t("br"),t("br"),n._v("Danh sư hướng so thực lực bản thân thấp người học tập, không phải cái gì sỉ nhục, trái lại, từ xưa đến nay."),t("br"),t("br"),n._v("Khổng sư đều từng hướng tu là yếu hơn mình người học tập, đối với danh sư tới nói, đệ tử không cần không bằng thầy, thầy không cần hiền tại đệ tử, nghe đạo có tuần tự, thuật nghiệp hữu chuyên công, như thế mà thôi."),t("br"),t("br"),n._v("“Cũng phải!”"),t("br"),t("br"),n._v("Tôn Cường nhẹ gật đầu."),t("br"),t("br"),n._v("Làm danh sư quản gia, thiếu gia cũng phải danh sư, biết thế giới này đối sư đạo tôn sùng."),t("br"),t("br"),n._v("Rõ ràng người sở trường liền có thể làm thầy."),t("br"),t("br"),n._v("Trịnh Dương nhận được thiếu gia thân truyền, giảng dạy cái không biết tên tiểu nhân vật, cũng không tính cái gì."),t("br"),t("br"),n._v("“Được rồi, mấy ngày nay mọi người không cần đi tìm phủ đệ, vẫn như cũ tu luyện đi. Trịnh Dương, ngươi nếu muốn dạy hắn thương pháp cũng được, đến lúc đó ta sẽ giúp ngươi hướng thiếu gia báo cáo!”"),t("br"),t("br"),n._v("Gặp cái tên này là chân tâm thật ý muốn bái sư, Tôn Cường không nói thêm lời."),t("br"),t("br"),n._v("“Ừm!” Trịnh Dương gật gật đầu, nhìn Tiết Chân Dương một cái: “Ngươi mấy ngày nay liền cùng ta học tập cho giỏi, chờ học tốt được, ta sẽ dẫn ngươi gặp sư phụ ta, lão nhân gia ông ta đối thương pháp lý giải, lợi hại hơn ta gấp mười lần! Đến lúc đó, đừng cho ta mất mặt!”"),t("br"),t("br"),n._v("Vừa nói, Trịnh Dương trong mắt bên cạnh lộ ra vẻ sùng bái."),t("br"),t("br"),n._v("“Lão sư yên tâm, ta sẽ thật tốt cố gắng, sẽ không để cho sư tổ thất vọng!” Tiết Chân Dương gật đầu, trong lòng cũng tràn đầy kích động."),t("br"),t("br"),n._v("Khó trách một mực nghe nói cao thủ tại dân gian, tùy tiện gặp phải một người, thương pháp liền lợi hại như vậy, còn có so với hắn lợi hại gấp mười lần lão sư..."),t("br"),t("br"),n._v("Có thể cùng bọn hắn học tập, đột phá hoa rơi thương đệ thập trọng, tuyệt đối không khó, thậm chí... Sẽ còn đi cao hơn, đến lúc đó, tuyệt đối có thể đánh vị kia Trương Huyền hoa rơi nước chảy, cho hắn biết... Thương pháp một đạo, ai mới thật sự là Vương Giả!"),t("br"),t("br"),n._v("“Ừm, ta hiện tại cùng ngươi Vương Dĩnh sư bá đối luyện một chút, ngươi thừa cơ nhìn xem ta đối thương pháp vận dụng!”"),t("br"),t("br"),n._v("Trịnh Dương trường thương lấy ra, nhìn lại."),t("br"),t("br"),n._v("[ truyen cua t"),t("br"),n._v("ｕi đốt net ]  Võ kỹ truyền thụ, không chỉ có chiêu thức, lĩnh ngộ, trọng yếu nhất vẫn là đối chiến."),t("br"),t("br"),n._v("Chỉ có đối chiến, mới có thể phát huy võ kỹ kỹ xảo cùng năng lực, để cho người ta lĩnh ngộ càng sâu."),t("br"),t("br"),n._v("“Được!” Tiết Chân Dương hai mắt tỏa ánh sáng."),t("br"),t("br"),n._v("“Vương Dĩnh, phiền toái...”"),t("br"),t("br"),n._v("Lông mày giương lên, trường thương duỗi ra, nhẹ nhàng lắc một cái, phát ra chói tai vang lên, thẳng tắp hướng cách đó không xa Vương Dĩnh đâm tới."),t("br"),t("br"),n._v("Bọn họ sư tỷ đệ thường xuyên luận bàn, đối với song phương chiêu số cùng thực lực hết sức quen thuộc, nhìn thấy thương này đâm tới, Vương Dĩnh nhẹ nhàng cười một tiếng, dừng bước, cả người tựa như huyễn ảnh, tránh thoát phong mang, đối với mũi thương, một chân đá tới."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Thối ảnh tựa như điện, Vương Dĩnh cả người, giống như là xuyên tiêu hồ điệp, rõ ràng đang thi triển võ kỹ, nhưng cho người ta một loại khác nhau khả năng thưởng thức cái đẹp, không gì sánh được."),t("br"),t("br"),n._v("“Thật là lợi hại thân pháp, thật là lợi hại thối công...”"),t("br"),t("br"),n._v("Con ngươi co vào, Tiết Chân Dương thân thể không tự chủ được run rẩy."),t("br"),t("br"),n._v("Vị này Vương Dĩnh sư bá, tu vi kém xa hắn, nhưng đối thân pháp, thối công lĩnh ngộ, coi như học viện những cái kia võ kỹ lão sư, đều kém xa tít tắp."),t("br"),t("br"),n._v("Thậm chí... Lão sư hắn võ kỹ học viện viện trưởng cần Trường Thanh, đều tựa hồ thi triển không ra, đơn giản như vậy, nhưng không có sai lầm chút nào, nhắm thẳng vào đại đạo ảo diệu chiêu số."),t("br"),t("br"),n._v("Hô hô hô hô!"),t("br"),t("br"),n._v("Rất nhanh, hai người giao thủ xong xuôi, Tiết Chân Dương nhìn như si như say."),t("br"),t("br"),n._v("Bất kể là thương pháp, vẫn là thân pháp, thối công, đều để tâm hắn sinh rung động, bội phục không thôi."),t("br"),t("br"),n._v("“Trịnh Dương, ngươi đã thu học sinh, không bằng ta cũng cho ngươi uy uy nhận!”"),t("br"),t("br"),n._v("Gặp Trịnh Dương thu thương, một bên Lưu Dương cười nói."),t("br"),n._v("Bọn họ đều là người trẻ tuổi, thiếu niên tâm tính, nhìn thấy đồng bạn thu học sinh, tất cả đều lòng ngứa ngáy khó nhịn."),t("br"),t("br"),n._v("“Được!” Trịnh Dương cũng không nói nhảm, trường thương như là trường xà, lần nữa đối Lưu Dương đâm đi qua."),t("br"),t("br"),n._v("Tiết Chân Dương vội vàng nhìn về phía vị này Lưu Dương sư bá."),t("br"),t("br"),n._v("Vị sư bá này, tốc độ cũng không nhanh, cước pháp cũng không lợi hại, xem ra cũng không am hiểu thối công, nhưng song quyền nhưng như là vũ khí, Trịnh Dương lão sư công kích, còn chưa tới đến gần, liền có thể làm ra phán đoán, dùng quyền Lực tướng thương mang trận mở."),t("br"),t("br"),n._v("Quyền phong gào thét, chân khí từ huyệt đạo bên trong phóng xuất ra, như là rồng ngâm hổ gầm."),t("br"),t("br"),n._v("“Thật mạnh quyền pháp... Cùng cấp bậc xuống, am hiểu quyền pháp Viên Cương tới, chỉ sợ đều không đủ một quyền đánh...”"),t("br"),t("br"),n._v("Tiết Chân Dương kích động thân thể có chút cứng ngắc."),t("br"),t("br"),n._v("Vốn cho rằng, lần này đi ra, không biết muốn đi bao lâu thời gian, mới có thể trải nghiệm thương pháp chân ý, lĩnh ngộ cấp bậc cao hơn võ kỹ, nằm mơ đều không nghĩ tới, mới ra ngoài một ngày, liền gặp nhiều như vậy kỳ nhân."),t("br"),t("br"),n._v("Mới vừa biết sư phụ cùng hai vị này sư bá, mặc dù thực lực không bằng bản thân, nhưng đối võ kỹ lĩnh ngộ, tuyệt đối vượt xa học viện rất nhiều danh sư."),t("br"),t("br"),n._v("Thậm chí, so một ít trưởng lão đều còn đáng sợ hơn!"),t("br"),t("br"),n._v("Lợi hại!"),t("br"),t("br"),n._v("Rất nhanh, lão sư cùng Lưu Dương sư bá tỷ thí xong, hai người nhìn nhau cười một tiếng, quay đầu nhìn về phía cách đó không xa Tiết Chân Dương: “Thế nào, nhưng có cái gì lĩnh ngộ?”"),t("br"),t("br"),n._v("“Lão sư, hai vị sư bá, tại hạ... Có một chuyện muốn nhờ, không biết có thể?”"),t("br"),t("br"),n._v("Hít sâu một hơi, ngăn chặn kích động tâm cảnh, Tiết Chân Dương cung kính ôm quyền."),t("br"),t("br"),n._v("“Ồ?” Thấy hắn như thế cẩn thận, mọi người nhìn qua."),t("br"),t("br"),n._v("“Là như vậy, tại hạ lấy cái tổ chức, trong đó có không ít thuộc hạ, cũng đối võ kỹ có rất cao trình độ cùng nghiên cứu, lão sư cùng hai vị sư bá, có thể hay không cho bọn hắn đi bên trên mấy tiết khóa... Cho bọn hắn truyền thụ một chút võ kỹ kỹ xảo?”"),t("br"),t("br"),n._v("Tiết Chân Dương vội nói."),t("br"),t("br"),n._v("“Giảng bài?” Vương Dĩnh, Trịnh Dương ngang nhau người liếc mắt nhìn nhau: “Chúng ta cũng không phải danh sư, như thế nào giảng bài?”"),t("br"),t("br"),n._v("Giảng bài, cơ bản đều là danh sư gây nên, bọn họ mặc dù đối võ kỹ lý giải cực sâu, cũng không có cái nghề nghiệp này, cho người khác giảng bài... Khó tránh khỏi có chút khinh thường đi!"),t("br"),t("br"),n._v("“Mấy vị mặc dù không phải danh sư, nhưng đối võ kỹ lý giải, lại là rất nhiều danh sư đều không đạt tới, ba vị chỉ muốn đi qua giảng bài, ta có thể để cho bọn họ giao nạp học phí...” Chần chờ một chút, Tiết Chân Dương nói."),t("br"),t("br"),n._v("Nếu như có thể đem ba vị này mời đi qua giảng giải võ kỹ, những thuộc hạ kia tất nhiên đột nhiên tăng mạnh, Chân Dương hội trở thành học viện thứ nhất học hội, ở trong tầm tay!"),t("br"),t("br"),n._v("Trương Huyền mang tới sỉ nhục, cũng hoàn toàn có thể tuyết rõ ràng!"),t("br"),t("br"),n._v("“Giao nạp học phí? Các ngươi có thể giao nạp bao nhiêu?”"),t("br"),t("br"),n._v("Vương Dĩnh đám người còn chưa mở miệng, một bên Tôn Cường, con mắt đã sáng lên."),t("br"),t("br"),n._v("Không quản lý việc nhà không biết củi gạo quý, hắn chịu trách nhiệm cuộc sống của mọi người, biết tầm quan trọng của tiền."),t("br"),t("br"),n._v("Hiện tại thiếu gia kiếm lấy linh thạch, đều không đủ tự mình tu luyện, chưa nói xong muốn dưỡng lấy bọn hắn."),t("br"),t("br"),n._v("Nếu như Vương Dĩnh đám người, có thể thừa cơ lời ít tiền, thiếu gia khẳng định sẽ cao hứng phi thường, cũng vừa tốt có thể giải quyết, hiện tại khốn khó."),t("br"),t("br"),n._v("“Cái này... Ta sẽ để bọn hắn mỗi người giao nạp một trăm viên linh thạch trung phẩm, ta có... Hơn năm trăm thuộc hạ, năm vạn linh thạch trung phẩm, hẳn là có thể đủ gom góp đến...”"),t("br"),t("br"),n._v("Tiết Chân Dương nói."),t("br"),t("br"),n._v("Chân Dương hội là học viện nổi danh đại học biết, ánh sáng thuộc hạ liền có hơn hai ngàn người, có điều, thích hợp ba người này võ kỹ, đoán chừng cũng là năm trăm trái phải."),t("br"),t("br"),n._v("“Năm trăm người? Một người một trăm?” Tôn Cường mắt sáng rực lên."),t("br"),t("br"),n._v("Lần này, liền muốn hơn năm vạn linh thạch trung phẩm."),t("br"),t("br"),n._v("“Như vậy đi, mỗi người hai trăm linh thạch, ta để bọn hắn không chỉ truyền thụ võ kỹ, còn giảng giải tu luyện cơ sở, ngươi yên tâm, bọn họ giảng giải cơ sở cùng đối tu luyện lý giải, tuyệt đối vật siêu chỗ giá trị!”"),t("br"),t("br"),n._v("Chần chờ một chút, Tôn Cường nói."),t("br"),t("br"),n._v("“Tu luyện cơ sở?” Tiết Chân Dương sững sờ."),t("br"),t("br"),n._v("“Không sai, Vương Dĩnh, ngươi cho hắn trước nói vài câu, liền Tăng Thọ cảnh phương pháp tu luyện đi!” Tôn Cường quay đầu nói."),t("br"),t("br"),n._v("“Vâng!” Vương Dĩnh gật gật đầu, suy tư một chút, đôi môi khẽ mở, chậm rãi mở miệng."),t("br"),t("br"),n._v("Tăng Thọ cảnh là Hóa Phàm đệ nhất trọng, đây là toàn bộ Hóa Phàm cơ sở, tu luyện không thích đáng, sau này tiến bộ giảm bớt không nói, sẽ còn giới hạn một người thành tựu."),t("br"),t("br"),n._v("Vương Dĩnh mặc dù nhìn sách không nhiều, nhưng tiếp xúc chính là phiên bản đơn giản hóa Thiên Đạo công pháp, lại thêm Trương Huyền tự mình giảng bài chỉ điểm, đối cấp bậc này lý giải, hết sức kinh người."),t("br"),t("br"),n._v("Chỉ nghe vài câu, Tiết Chân Dương liền kích động như là co giật, khó tự kiềm chế."),t("br"),t("br"),n._v("Loại này từ cạn tới sâu phương thức tu luyện, đừng nói là hắn, liền xem như học viện lợi hại nhất giảng bài lão sư, đều kém xa tít tắp!"),t("br"),t("br"),n._v("Có thể nói, loại tu luyện này phương thức, đã nhắm thẳng vào đại đạo, nhằm vào tu luyện hạch tâm, để cho người ta nghe tới vài câu, liền được ích lợi không nhỏ, khó mà tự chế."),t("br"),t("br"),n._v("“Quá tốt rồi, nếu như... Của ta hội viên nghe loại này giảng giải, trước đó không đánh vững chắc cơ sở, khẳng định cũng có thể càng thêm vững chắc, tu vi tiến bộ càng nhanh!”"),t("br"),t("br"),n._v("Càng nghĩ càng kích động, lần nữa nhìn về phía Vương Dĩnh các người như là nhìn xem một tòa bảo sơn."),t("br"),t("br"),n._v("Mặc dù không biết dạng gì danh sư, mới có thể dạy ra lợi hại như thế học sinh, nhưng hắn biết... Bản thân lần này tuyệt đối là kiếm bộn rồi!"),t("br"),t("br"),n._v("Nếu để cho bọn họ tiến đến giảng bài, có thể đoán được, Chân Dương hội người, không chỉ võ kỹ tăng nhiều, tu vi cũng sẽ tiến nhanh, đến lúc đó... Tất nhiên sẽ có liên tục không ngừng học viên, thỉnh cầu lấy gia nhập Chân Dương hội!"),t("br"),t("br"),n._v("Nghiền ép Trương Huyền, rửa sạch nhục nhã, ở trong tầm tay!"),t("br"),t("br"),n._v("“Ha ha, Trương Huyền, ngươi lấy cái gì cùng ta đấu?”"),t("br"),t("br"),n._v("Con mắt tỏa ánh sáng, dường như thấy được Chân Dương hội huy hoàng ngày mai, Tiết Chân Dương trong lòng một tiếng la hét, cả người hăng hái sắp phát nổ."),t("br"),t("br"),n._v("Đen nhánh trong con ngươi, lộ ra tràn đầy tự tin!"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-802"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-804"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);