(window.webpackJsonp=window.webpackJsonp||[]).push([[2088],{2443:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-801-tu-truong-thanh-khiep-so"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-801-tu-truong-thanh-khiep-so"}},[n._v("#")]),n._v(" Chương 801: Tu Trường Thanh khiếp sợ")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 801: Tu Trường Thanh khiếp sợ"),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Nghe nói như thế, nhìn thấy đối phương mặt mũi khinh thường ánh mắt khi dễ, Tu Trường Thanh không chỉ râu ria là đen, toàn bộ mặt đều xanh!"),t("br"),t("br"),n._v("Võ kỹ học viện, mặc dù tại mười đại học viện bên trong, xếp tại y sư học viện đằng sau, chỉ có hạng sáu, nhưng cũng là danh xứng với thực quái vật khổng lồ, nhất là hắn, tại thập đại trường lão bên trong, đơn đả độc đấu, coi như không phải thứ nhất, cũng tuyệt đối có thể xếp tiến lên ba!"),t("br"),t("br"),n._v("Như thế đỉnh phong nhân vật, không biết ngược lại cũng thôi, thế mà còn hỏi mình đòi tiền?"),t("br"),t("br"),n._v("Đòi tiền thì cũng thôi đi, còn nói mình là kẻ nghèo hèn?"),t("br"),t("br"),n._v("Nghèo em gái ngươi ah!"),t("br"),t("br"),n._v("Đến võ kỹ học viện, liền viện trưởng cũng không nhận ra, từ chỗ nào cái xó xỉnh xuất hiện?"),t("br"),t("br"),n._v("“Ta tới xem một chút, cũng không phải là nghe giảng bài!” Cố nén da mặt co rúm, Tu Trường Thanh khoát tay áo."),t("br"),t("br"),n._v("Thân là viện trưởng, muốn có viện trưởng phong độ, tự nhiên không có khả năng bởi vì đối phương nói một câu, liền rùm beng đến đầu rơi máu chảy."),t("br"),t("br"),n._v("“Nhìn xem?” Mập mạp một mặt điểm khả nghi nhìn qua."),t("br"),t("br"),n._v("Tu Trường Thanh nhẹ gật đầu, vuốt râu, một bộ cao nhân bộ dáng tự nhiên sinh ra."),t("br"),t("br"),n._v("Nếu như vậy, đối phương tại nhìn không ra thân phận của hắn, thật sự không nói được."),t("br"),t("br"),n._v("“Được rồi!”"),t("br"),t("br"),n._v("Mập mạp dường như rõ ràng cái gì, đem cái sọt để dưới đất, không còn lấy tiền: “Ngươi đi vào đi!”"),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Thấy đối phương rốt cục lĩnh ngộ, lúc này mới hài lòng nhẹ gật đầu, Tu Trường Thanh bước chân nâng lên, còn không có rảo bước tiến lên cửa chính, ngay sau đó nghe được cái tên này tận lực đè thấp nói thầm tiếng vang lên."),t("br"),t("br"),n._v("“Cái gì đồ chơi đây! Không có tiền liền không có tiền, giả trang cái gì đầu to tỏi, nhìn xem... Thật có mặt nói, còn không phải đi học trộm? Gặp qua không biết xấu hổ, chưa thấy qua không biết xấu hổ như vậy! Còn danh sư đây... Ah phi!”"),t("br"),t("br"),n._v("Mí mắt xoay loạn, mập mạp khinh thường chi ý, lộ rõ trên mặt."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Một cái lảo đảo, Tu Trường Thanh suýt chút nữa không một cái lão huyết phun ra, hôn mê ngay tại chỗ."),t("br"),t("br"),n._v("Hắn là viện trưởng,"),t("br"),t("br"),n._v("Thập đại trường lão một trong, bị người ở trước mặt chửi không biết xấu hổ, giả ngu... Lửa giận hừng hực sắp bốc cháy lên, đều muốn điên rồi."),t("br"),t("br"),n._v("Gặp qua hai, chưa thấy qua như thế hai."),t("br"),t("br"),n._v("Ta vừa rồi cao nhân phong độ, ngươi chẳng lẽ không thấy được? Liền không suy đoán ra thân phận của ta?"),t("br"),t("br"),n._v("“Tốt, ta đưa tiền!” Biết cùng loại người này so đo, khẳng định muốn tươi sống chết, thật muốn làm ầm ĩ lên, ngược lại làm mất thân phận, Tu Trường Thanh cắn răng một cái, ném ra một cái linh thạch, rơi vào cái sọt."),t("br"),t("br"),n._v("“Linh thạch thượng phẩm?”"),t("br"),t("br"),n._v("Nhìn thấy rơi xuống linh thạch, lóng lánh ánh sáng, linh khí xao động, như là rồng ngâm hổ gầm, mập mạp mắt sáng rực lên."),t("br"),t("br"),n._v("Còn tưởng rằng cái tên này thật là một cái quỷ nghèo, không nghĩ tới người đứng đầu lấy ra một cái thượng phẩm... Náo loạn nửa ngày, không phải không tiền, mà là thổ tài chủ!"),t("br"),t("br"),n._v("“Cái này ta có thể vào chứ?”"),t("br"),t("br"),n._v("Tu Trường Thanh hừ một tiếng."),t("br"),t("br"),n._v("“Cho ngươi thối tiền lẻ, một cái linh thạch thượng phẩm, giá trị một vạn viên trung phẩm, đi học giá cả, là 100 viên linh thạch trung phẩm, ta tìm ngươi... 9900 viên linh thạch trung phẩm!”"),t("br"),t("br"),n._v("Mập mạp trong miệng nhắc tới, bắt đầu ở trong cái sọt xoay loạn, dường như muốn lấy ra đối ứng linh thạch."),t("br"),t("br"),n._v("“Không cần tìm!” Tu Trường Thanh khoát tay áo."),t("br"),t("br"),n._v("Thân là thập đại trường lão, Thánh vực một tầng cường giả đỉnh phong, một cái linh thạch thượng phẩm, ở trong mắt những người khác, vô cùng trân quý, đối với hắn mà nói, còn không tính là gì."),t("br"),t("br"),n._v("“Quá tốt rồi! Vừa vặn cũng không có tiền tìm...” Mập mạp con mắt tỏa ánh sáng."),t("br"),t("br"),n._v("“...” Lần nữa nhoáng một cái, Tu Trường Thanh cảm thấy ngực khó chịu."),t("br"),t("br"),n._v("Đây đều là người gì..."),t("br"),t("br"),n._v("Quá không biết xấu hổ đi!"),t("br"),t("br"),n._v("Tiết Chân Dương sao có thể tìm kẻ như vậy đến học viện, đây không phải mất đi võ kỹ học viện tên tuổi ư?"),t("br"),t("br"),n._v("Càng nghĩ càng giận, cố nén một bàn tay đem đối phương quất chết xúc động, nhanh chân hướng trong viện đi đến, mới tiến vào bên trong, liền nghe sau lưng mập mạp lần nữa hô lên: “Vị này khuôn mặt có chút lão nhân bạn học, cho tiền boa 9900 linh thạch! Mọi người muốn nhiều hướng hắn học tập!”"),t("br"),t("br"),n._v("Thân thể cứng ngắc, Tu Trường Thanh lông mày nhảy loạn, suýt chút nữa không tại chỗ điên mất."),t("br"),t("br"),n._v("Tiền boa?"),t("br"),t("br"),n._v("Còn nhiều hướng ta học tập?"),t("br"),t("br"),n._v("Hắn vốn là chỉ là muốn lặng lẽ tới, nhìn xem rốt cục chuyện gì xảy ra, thuận tiện giáo huấn một lần Tiết Chân Dương, hiện tại được rồi, đường đường võ kỹ học viện viện trưởng, cũng tới nghe giảng bài, không chỉ có cho học phí, đưa cho tiền boa..."),t("br"),t("br"),n._v("Cái này mẹ nó nhảy vào hồng nguyên hồ cũng rửa không sạch đi!"),t("br"),t("br"),n._v("Quả nhiên, ý nghĩ này còn không có kết thúc, liền thấy phía trước nghe giảng bài tất cả học viên ánh mắt đồng loạt chằm chằm đi qua."),t("br"),t("br"),n._v("“Là Tu viện trưởng...”"),t("br"),t("br"),n._v("“Tu viện trưởng cũng tới, không chỉ đóng học phí, còn cho nhiều như vậy tiền boa?”"),t("br"),t("br"),n._v("“Lợi hại, nhìn đến hắn cũng thừa nhận Trịnh lão sư, Lưu lão sư cùng Vương lão sư lợi hại!”"),t("br"),t("br"),n._v("“Đúng vậy a...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nhìn thấy viện trưởng không chỉ tự mình đến, đưa cho tiền boa, tất cả mọi người một hồi xôn xao."),t("br"),t("br"),n._v("Hai cái võ kỹ học viện học viên, nhịn không được chạy tới, đi tới gần: “Viện trưởng, ngươi cũng tới, vị trí phía trước tốt, đến bên này nghe đi!”"),t("br"),n._v(" Con mắt tối sầm lại, Tu Trường Thanh thân thể nhoáng một cái."),t("br"),t("br"),n._v("Vị trí tốt..."),t("br"),t("br"),n._v("Con mẹ nó chứ là đến gây chuyện, không phải tới nghe khóa!"),t("br"),t("br"),n._v("“Đúng vậy a, viện trưởng, hội trưởng ở bên kia chờ ngươi đấy, chúng ta nhanh lên một chút đi đi, nếu không, nhiều người nhìn như vậy, chậm trễ đi học, nhiều thật không tiện...”"),t("br"),t("br"),n._v("Một cái khác học viên cũng nói."),t("br"),t("br"),n._v("“Hội trưởng? Tốt, ta ngược lại muốn xem xem hắn làm cái quỷ gì!”"),t("br"),t("br"),n._v("Tức đến sắp thổ huyết, nghe được Tiết Chân Dương ở phía trước chờ lấy, Tu Trường Thanh nhẹ gật đầu."),t("br"),t("br"),n._v("Tiếp tục đợi ở chỗ này, bị những học sinh khác vây xem, khẳng định càng mất mặt, còn không bằng tìm tới chính mình cái này nghiệt đồ giáo huấn một lần lại nói."),t("br"),t("br"),n._v("Đi theo hai vị học sinh sau lưng, chen vào đám người, rất mau tới đến đài cao gần."),t("br"),t("br"),n._v("“Lão sư, ngươi cũng tới...”"),t("br"),t("br"),n._v("Nhìn thấy hắn, Tiết Chân Dương một mặt hưng phấn nhìn qua, tràn đầy cung kính."),t("br"),t("br"),n._v("“Ngươi đến cùng làm cái quỷ gì?”"),t("br"),t("br"),n._v("Khuôn mặt trầm xuống, cũng nhịn không được nữa, Tu Trường Thanh liền muốn bộc phát."),t("br"),t("br"),n._v("“Trước đừng nóng giận, lão sư, ngươi nghe một chút lại nói...” Không trả lời lão sư lời nói, Tiết Chân Dương một chỉ trên đài."),t("br"),t("br"),n._v("“Nghe một chút?” Sửng sốt một chút, mặc dù tức giận, nhìn thấy chung quanh tất cả đều là học sinh, Tu Trường Thanh vẫn là cưỡng ép nhịn xuống, ngẩng đầu nhìn lên."),t("br"),t("br"),n._v("Hiện đang giảng bài chính là cái nữ hài, mười sáu mười bảy tuổi bộ dáng, dung mạo có chút tuấn mỹ, so với học sinh bên trong cái kia thứ gì Hồ Yêu Yêu, Đổng Hân, đều không kém chút nào."),t("br"),t("br"),n._v("“... Thối công, là đem chân, cùng thân pháp hợp hai làm một, chúng ta đều biết, đi đường, chạy bộ đều dùng chính là chân, nếu như, có thể đem những này cũng làm làm quá trình tu luyện, thối công tất nhiên tiến nhanh...”"),t("br"),t("br"),n._v("“Chúng ta tu luyện trên đùi công pháp, chú trọng nhiều nhất là kỹ xảo, Toàn Phong thối, Hàn Dương thối, Đại Lực Kim Cương thối... Rất nhiều cước pháp, chỗ nào cũng có, toàn sách là sách, thực tế là nhưng tìm nhầm phương hướng, chân công hiệu là đi đường, đem loại này công hiệu phát huy đến cực hạn, chính là tốt nhất...”"),t("br"),t("br"),n._v("Nữ hài đọc nhấn rõ từng chữ rõ ràng, chậm rãi mà nói."),t("br"),t("br"),n._v("Vừa mới bắt đầu không để ý lắm, càng nghe càng cảm thấy kinh hãi, Tu Trường Thanh da mặt cũng bắt đầu hút động."),t("br"),t("br"),n._v("Người trong nghề vừa ra tay, liền biết có hay không."),t("br"),t("br"),n._v("Hắn là võ kỹ học viện viện trưởng, càng là lục tinh đỉnh phong danh sư, đối võ kỹ có đặc biệt đã hiểu cùng lĩnh ngộ, đối phương giảng giải thối công, mặc dù nghe cơ sở, nhưng chu đáo, nhắm thẳng vào bản chất."),t("br"),t("br"),n._v("Thậm chí không ít địa phương đã hiểu, so với hắn... Đều muốn khắc sâu!"),t("br"),t("br"),n._v("Có thể nói... Coi như hắn giảng giải, đều kém xa đối phương!"),t("br"),t("br"),n._v("“Cái này... Là cơ sở? Cơ sở thối công, có thể có nhiều như vậy biến hóa?” Lại nghe một hồi, lông mày nhịn không được nhíu lại."),t("br"),t("br"),n._v("Cùng đối phương nói đồng dạng, võ kỹ coi trọng quỷ dị, ngoài dự liệu, để cho người ta khó lòng phòng bị, mà đối phương giảng giải lại là đường đường đại đạo, đường đường chính chính, không có chút nào bàng môn tà đạo."),t("br"),t("br"),n._v("Nhưng chẳng biết tại sao, càng như vậy, dường như càng có thể phát huy ra lực lượng mạnh nhất."),t("br"),t("br"),n._v("“... Đi đường kéo theo toàn thân, nếu như đem đi đường lực lượng, dùng bàn chân truyền ra ngoài đâu? Đây chính là ta hôm nay muốn truyền thụ cho thối công, có thể trong nháy mắt, đem lực lượng toàn thân tập trung lại, để đối thủ cảm nhận được ngươi toàn bộ tu vi...”"),t("br"),t("br"),n._v("Trên đài nữ hài tiếp tục giảng giải."),t("br"),t("br"),n._v("“Hai chân, có thể kéo theo toàn thân, bình thường võ kỹ, một cái chân chống đỡ, mặt khác một cái chân công kích, lực lượng rất hiển nhiên muốn phân tán, nếu như đem toàn bộ lực lượng phóng xuất ra...”"),t("br"),t("br"),n._v("Tu Trường Thanh con mắt càng ngày càng sáng."),t("br"),t("br"),n._v("Cẩn thận nghe xuống dưới, một lát sau, vỗ đùi: “Đúng a! Như vậy thi triển thối công lời nói, tất cả vấn đề toàn bộ có thể giải quyết... Không chỉ lực lượng mạnh hơn, tốc độ cũng càng nhanh, công kích uy lực cũng càng thêm cường đại!”"),t("br"),t("br"),n._v("“Công kích chiêu số mặc dù đơn giản, nhưng nhắm thẳng vào đại đạo hạch tâm, thể hiện hết thảy võ kỹ cơ sở nhất biến hóa...”"),t("br"),t("br"),n._v("Đem nữ hài giảng giải nội dung, ở trong lòng chảy xuôi một lần, càng nghe càng cảm thấy hưng phấn, con mắt cũng càng ngày càng sáng."),t("br"),t("br"),n._v("Đối phương nói võ kỹ, cấp bậc khả năng không hắn biết cao, nhưng là thực sự cơ sở, dựa theo đối phương giảng giải tu luyện, liền xem như hắn, đều được ích lợi vô cùng, đối về sau học tập, có cực lớn dẫn dắt."),t("br"),t("br"),n._v("“Lão sư, thế nào?”"),t("br"),t("br"),n._v("Nhìn thấy lão sư biểu lộ, Tiết Chân Dương cười nhìn lại."),t("br"),t("br"),n._v("Hắn xin mời Trịnh lão sư, Vương sư bá đám người tới giảng bài, đồng thời cấp cho giảng bài phí, liền đoán đến lão sư sẽ tức giận, do đó, sớm liền làm ra chuẩn bị."),t("br"),t("br"),n._v("Quả nhiên cùng nghĩ tới đồng dạng, lão sư nghe một hồi, cũng rơi vào trong đó, không cách nào tự kềm chế."),t("br"),t("br"),n._v("Nói thật, bản thân trong lúc vô tình gặp phải mấy vị này, mặc dù không phải danh sư, nhưng đối võ kỹ đã hiểu, cùng đối tu luyện lĩnh ngộ, thực sự quá sâu sắc, dựa theo bọn họ giảng giải phương pháp tu luyện, tiến bộ nhanh chóng không nói, càng có thể đem cơ sở buộc nhà tù, không có kẽ hở!"),t("br"),t("br"),n._v("Có thể đoán được, cẩn thận nghe giảng, nghiêm ngặt tu luyện, sau này tiến bộ, tuyệt đối sẽ càng lúc càng lớn, để cho người ta sợ hãi thán phục!"),t("br"),t("br"),n._v("“Lợi hại!” Tu Trường Thanh nhẫn gật đầu không ngừng: “Không nghĩ tới có người có thể đem cơ sở võ kỹ, đã hiểu như thế thấu triệt... Một trăm linh thạch trung phẩm nghe được loại này chương trình học, xem như kiếm lời!”"),t("br"),t("br"),n._v("Linh thạch trung phẩm, đối với bọn hắn những danh sư này tới nói, không tính là gì, chỉ cần là ở trường lão sinh, ai không có mấy trăm hơn ngàn."),t("br"),t("br"),n._v("Như thế điểm linh thạch, liền nghe đến như thế đặc sắc khóa, thật sự là lợi nhuận phát."),t("br"),t("br"),n._v("Có thể tưởng tượng, cái này tiết khóa qua đi, không ít người đều sẽ đối võ kỹ có nặng lĩnh ngộ mới, từ đó thực lực tăng nhiều."),t("br"),t("br"),n._v("“Đúng vậy a!” Tiết Chân Dương gật đầu."),t("br"),t("br"),n._v("Nếu như không phải sợ hãi thán phục tại đối phương chương trình học, đường đường danh sư học viện cao tài sinh, lại làm sao có thể bái một cái so với chính mình tiểu nhiểu tuổi như vậy nhân vi sư."),t("br"),t("br"),n._v("“Chân Dương, cô gái này lai lịch gì?”"),t("br"),t("br"),n._v("Cảm khái một câu, Tu Trường Thanh nhịn không được nhìn qua."),t("br"),t("br"),n._v("“Cụ thể lai lịch gì ta cũng không biết, nhưng mà hôm qua du lịch thời điểm, ở bên ngoài trường gặp gỡ... Làm sao, lão sư động thu đồ chi tâm?” Tiết Chân Dương nhìn qua."),t("br"),t("br"),n._v("“Thu đồ?” Tu Trường Thanh lắc đầu: “Có thể đối võ kỹ đã hiểu sâu như vậy, ta cái nào có tư cách làm lão sư của nàng, mà là muốn mời nàng làm võ kỹ học viện...”"),t("br"),t("br"),n._v("“Khách tọa trưởng lão!”"),t("br"),t("br"),n._v("(Đầu tháng, cầu nguyệt phiếu!)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-809"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-811"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);