(window.webpackJsonp=window.webpackJsonp||[]).push([[2089],{2442:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-802-ta-khong-ban"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-802-ta-khong-ban"}},[n._v("#")]),n._v(" Chương 802: Ta không bán!")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("Chương 802: Ta không bán!"),i("br"),i("br"),n._v("“Khách tọa trưởng lão?” Tiết Chân Dương lông mày nhảy một cái, con mắt trợn tròn."),i("br"),i("br"),n._v("Khách tọa trưởng lão, mặc dù không bằng trưởng lão có thực quyền, nhưng ở địa vị bên trên, thậm chí càng cao thêm một bậc."),i("br"),i("br"),n._v("Bình thường đều là mời mời một ít nhân vật lợi hại đảm nhiệm, dùng đến đề cao học viện danh dự cùng danh vọng."),i("br"),i("br"),n._v("Thật giống như Hồng Viễn đế quốc Hoàng đế bệ hạ, liền đảm nhiệm lấy học viện khách tọa trưởng lão, ngẫu nhiên cũng sẽ tới cho học sinh đi học, truyền thụ trên việc tu luyện lĩnh ngộ cùng kỹ xảo."),i("br"),i("br"),n._v("Để một cái mười sáu mười bảy tuổi nữ hài, làm khách tọa trưởng lão... Có phải hay không có chút quá khoa trương?"),i("br"),i("br"),n._v("“Không sai, nàng có tư cách này cùng năng lực!” Tu Trường Thanh vẻ mặt nghiêm túc gật đầu."),i("br"),i("br"),n._v("Hắn ý nghĩ này, tự nhiên không phải nói lung tung, là trải qua nghĩ sâu tính kỹ."),i("br"),i("br"),n._v("Đối phương truyền thụ nội dung, nhắm thẳng vào đại đạo, lời ít mà ý nhiều, để cho người ta suy nghĩ sâu xa, tu luyện cũng mười phần đơn giản, dễ dàng vào tay."),i("br"),i("br"),n._v("Nếu như có thể đến võ kỹ học viện đương khách tọa trưởng lão, nhất định có thể thu hút càng nhiều học sinh trước tới tu luyện, dần dà, toàn bộ học viện, đều lại bởi vậy lớn mạnh!"),i("br"),i("br"),n._v("Về sau siêu việt y sư học viện, thuần thú học viện, trận pháp học viện rất nhiều học viện, trở thành danh sư học viện thứ nhất, cũng chưa biết chừng!"),i("br"),i("br"),n._v("Danh sư, trọng yếu nhất chính là truyền đến thụ nghiệp giải thích nghi hoặc, rất nhiều người đối tự thân võ kỹ, cũng không coi trọng; Lại thêm võ kỹ tu luyện mười phần rườm rà phức tạp, cần đêm ngày khổ luyện, mấu chốt còn không tính phụ tu chức nghiệp, cùng tăng lên tên sư cấp bậc không quan hệ..."),i("br"),i("br"),n._v("Do đó, rõ ràng là gia tăng sức chiến đấu lợi hại ngành học, nhưng chọn người cực ít, tại toàn bộ danh sư học viện, đều xếp tại đếm ngược."),i("br"),i("br"),n._v("Gần như chỉ ở Thiên Công viện, Thư Họa viện, Ma âm viện, Kinh Hồng viện những này lại ngành học phía trước."),i("br"),i("br"),n._v("Nói đến đều có chút bi thương."),i("br"),i("br"),n._v("Cho tới nay, tráng đại học viện đều là giấc mộng của hắn, hiện tại cơ hội tới, làm sao không kích động?"),i("br"),i("br"),n._v("“Vậy thì tốt, lão sư tới trước gian phòng chờ, Vương sư bá khóa lập tức liền giảng xong, ta vậy thì mang bọn họ chạy tới, loại sự tình này, vẫn là lão sư tự mình nói còn tốt...” Tiết Chân Dương gật đầu nói."),i("br"),i("br"),n._v("Trịnh lão sư, Vương sư bá những người này, đều là hắn tìm đến, nếu như có thể trở thành học viện khách tọa trưởng lão, cũng theo có ánh sáng."),i("br"),i("br"),n._v("Có loại này chỗ dựa, tại học viện vị trí cũng đem càng ngày càng ổn, sau đó giáo huấn một cái vừa tới học viện tân sinh, còn không dễ như trở bàn tay?"),i("br"),i("br"),n._v("Nhẹ nhõm thêm vui vẻ?"),i("br"),i("br"),n._v("“Ừm!” Tu Trường Thanh không nói thêm gì nữa, đứng dậy đi vào cách đó không xa gian phòng, không lâu sau, cửa phòng mở ra, Tiết Chân Dương mang theo Vương Dĩnh đám người đi đến."),i("br"),i("br"),n._v("“Trịnh lão sư, Vương sư bá, Lưu sư bá, vị này là tại hạ tu vi ân sư, võ kỹ học viện Tu viện trưởng!”"),i("br"),i("br"),n._v("Một đi vào phòng, Tiết Chân Dương cho hai bên giới thiệu: “Vị này là Trịnh Dương lão sư, ta cùng hắn học tập thương pháp. Vị kia là Vương Dĩnh, Vương sư bá, vừa rồi giảng bài thời điểm, ngươi đã thấy. Vị này là Lưu Dương, Lưu sư bá!”"),i("br"),i("br"),n._v("“Viện trưởng?”"),i("br"),i("br"),n._v("Trịnh Dương đám người nhìn nhau, từng người mở to hai mắt nhìn."),i("br"),i("br"),n._v("Vừa rồi nghe nói có người muốn thấy bọn họ, còn tưởng rằng là ai, không nghĩ tới lại là đường đường võ kỹ học viện viện trưởng, toàn bộ giật nảy mình."),i("br"),i("br"),n._v("“Chư vị không cần khiếp sợ, ta mời các ngươi tới, là muốn mời các ngươi làm học viện khách tọa trưởng lão, không biết có thể?”"),i("br"),i("br"),n._v("Tu Trường Thanh cười cười, nói."),i("br"),i("br"),n._v("“Khách tọa trưởng lão?”"),i("br"),i("br"),n._v("Trịnh Dương đám người ngây người."),i("br"),i("br"),n._v("Bọn họ liền học đều không chút bên trên một đám gia hỏa, muốn làm đường đường danh sư học viện khách tọa trưởng lão... Thật hay giả?"),i("br"),i("br"),n._v("Cái này vinh dự thật là quá lớn đi!"),i("br"),i("br"),n._v("“Đúng!” Tu Trường Thanh gật đầu: “Các ngươi có thể hay không nguyện ý?”"),i("br"),i("br"),n._v("“Đương nhiên nguyện ý!” Trịnh Dương đám người đồng thời gật đầu, nguyên một đám mặt mũi hưng phấn."),i("br"),i("br"),n._v("“Vậy là tốt rồi, ta vậy thì trình báo, chậm nhất xế chiều hôm nay, liền sẽ đem đại biểu các ngươi thân phận lệnh bài cho đưa tới!”"),i("br"),i("br"),n._v("Thấy đối phương đồng ý, Tu Trường Thanh nhẫn gật đầu không ngừng."),i("br"),i("br"),n._v("Hàn huyên một hồi, Tu Trường Thanh xoay người rời đi."),i("br"),i("br"),n._v("Rất nhanh chỉ còn lại có, Trịnh Dương đám người."),i("br"),i("br"),n._v("Ba người đưa mắt nhìn nhau, dường như đến bây giờ đều có chút không dám tin tưởng."),i("br"),i("br"),n._v("“Cái này... Chúng ta biến thành học viện trưởng lão, Trương lão sư vẫn là học sinh... Trở về nên nói như thế nào?”"),i("br"),i("br"),n._v("Không biết qua bao lâu, Vương Dĩnh nhịn không được nói."),i("br"),i("br"),n._v("“Cái này...” Trịnh Dương, Lưu Dương vò đầu."),i("br"),i("br"),n._v("Nói thật, bọn họ cũng không biết làm sao bây giờ."),i("br"),i("br"),n._v("Hiện tại lão sư Trương Huyền, vẫn chỉ là học viện học sinh, mà bọn họ nhưng thành trưởng lão... Suy nghĩ một chút đều cảm thấy hoang đường."),i("br"),i("br"),n._v("“Cái này... Nếu không có thể giấu diếm một ngày giấu diếm một ngày đi...” Trầm mặc một lát, Trịnh Dương nói."),i("br"),i("br"),n._v("“Được!” Vương Dĩnh, Lưu Dương hai người đồng thời gật đầu."),i("br"),i("br"),n._v("Không phải bọn họ muốn che giấu, mà là... Thật không biết nên nói như thế nào lối ra."),i("br"),i("br"),n._v("Để cho người ta cảm thấy thực sự quá khó mà tin nổi."),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("“Ngươi nói cái gì? Tu viện trưởng, đem Tiết Chân Dương tìm đến giảng bài tiểu gia hỏa, biến thành khách tọa trưởng lão?”"),i("br"),i("br"),n._v("Liếc mắt nhìn nhau, Đổng Hân, Long Thương Nguyệt từng người không ngất đi."),i("br"),i("br"),n._v("Còn tưởng rằng Tu viện trưởng quá khứ, sẽ dạy dạy bảo một chút Tiết Chân Dương cử động, không nghĩ tới... Thế mà đem mấy tên này biến thành khách tọa trưởng lão?"),i("br"),i("br"),n._v("Cái này sao có thể?"),i("br"),i("br"),n._v("Đùa giỡn đi!"),i("br"),i("br"),n._v("“Thật sự, không chỉ có như thế, ta còn nghe nói, Tu viện trưởng từ Chân Dương hội vừa về đến, liền bế quan tu luyện, trời sắp tối rồi mới xuất quan... Nghe nói đối võ kỹ có lĩnh ngộ mới, cùng Trần viện phó tỷ thí, một chiêu liền đem hắn đánh bại...”"),i("br"),i("br"),n._v("Một người học viên nói."),i("br"),i("br"),n._v("“Một chiêu đem Trần viện phó đánh bại?”"),i("br"),i("br"),n._v("“Trần viện phó mặc dù thực lực không bằng Tu viện trưởng, nhưng cũng không sai biệt nhiều, bọn họ đoạn thời gian trước luận bàn qua, dùng không dưới mười chiêu, hiện tại một chiêu...”"),i("br"),n._v("Đổng Hân một đôi đôi mắt thanh tú viết đầy không thể tưởng tượng nổi: “Chẳng lẽ, những người kia giảng bài nội dung thật lợi hại như vậy?”"),i("br"),i("br"),n._v("“Dù sao ta nghe nói, nghe qua khóa người, đều có khác biệt mức độ đột phá, từ đó thực lực tăng nhiều!” Học viên nói."),i("br"),i("br"),n._v("“Còn có loại sự tình này...”"),i("br"),i("br"),n._v("Long Thương Nguyệt, Đổng Hân từng người nắm đấm xiết chặt: “Nhìn đến, về sau... Phải cẩn thận Chân Dương hội!”"),i("br"),i("br"),n._v("Đồng dạng một màn, còn phát sinh ở cái khác học hội, không ít lão sư, thậm chí đều bị khiếp sợ."),i("br"),i("br"),n._v("Để ba cái không đủ hai mươi thiếu niên làm khách tọa trưởng lão, đây chính là toàn bộ danh sư học viện, đều chưa bao giờ có sự tình."),i("br"),i("br"),n._v("Trong nháy mắt huyên náo đầy phong cách trường học mưa, không ít tân sinh cũng biết."),i("br"),i("br"),n._v("Bất quá, bọn họ chỉ biết là võ kỹ học viện khách tọa trưởng lão sự tình, cũng không biết xác thực họ tên, tự nhiên càng không biết ba người này lai lịch."),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Học viện chấn động, đều đang vì ba vị khách tọa trưởng lão khiếp sợ, Trương Huyền bên này rốt cục trở lại học viện."),i("br"),i("br"),n._v("“Vưu phó viện trưởng nói là trong hai ngày, đem Vân Vụ hoa mang đến, về thời gian còn kịp!”"),i("br"),i("br"),n._v("Tiến vào học viện, Trương Huyền chưa có trở về tinh anh khu chỗ ở của mình, mà là trực tiếp đi y sư học viện."),i("br"),i("br"),n._v("Ngụy Như Yên mặc dù dùng hồn lực tẩm bổ, ổn định lại, tạm thời không vấn đề quá lớn, nhưng kiên trì không lâu, nhất định phải nhanh cứu chữa mới được."),i("br"),i("br"),n._v("Nói cách khác, nhất định phải đạt được Thập Diệp hoa, vì cứu người, cũng vì... Chết đi Ngụy Trường Phong!"),i("br"),i("br"),n._v("Xe nhẹ đường quen, rất mau tới đến y sư tháp, tìm tới Vưu phó viện trưởng nơi ở, gõ cửa một cái."),i("br"),i("br"),n._v("Trước đó vị kia Tôn Nguyên lão sư đi ra."),i("br"),i("br"),n._v("“Ngươi...” Thấy là vị này Trương Huyền, Tôn Nguyên một hồi kinh ngạc."),i("br"),i("br"),n._v("Vân Vụ lĩnh nguy hiểm, hắn biết đến rõ rõ ràng ràng, đi làm sao có thể không có việc gì?"),i("br"),i("br"),n._v("“Ta tới gặp Vưu phó viện trưởng!”"),i("br"),i("br"),n._v("Trương Huyền nói."),i("br"),i("br"),n._v("Biết rõ Vân Vụ lĩnh nguy hiểm, cái này phó viện trưởng, còn để Ngụy Trường Phong ngắt lấy Vân Vụ hoa, chỉ sợ tồn tại cố ý tâm tư."),i("br"),i("br"),n._v("Mặc dù có chút tức giận, vì Ngụy Như Yên, vẫn là nhịn xuống."),i("br"),i("br"),n._v("Thập Diệp hoa, loại này có thể khôi phục linh hồn Thánh dược, toàn bộ Hồng Viễn thành khả năng chỉ có vị này phó viện trưởng mới có, trường phong đã chết, không thể để cho kiên trì của hắn cùng hi vọng rơi vào khoảng không."),i("br"),i("br"),n._v("“Mời vào...”"),i("br"),i("br"),n._v("Tôn Nguyên sửng sốt một chút, vẫn là gật đầu, để hắn đi vào."),i("br"),i("br"),n._v("Rất nhanh nhìn thấy Vưu phó viện trưởng."),i("br"),i("br"),n._v("“Vưu viện trưởng, đây là Vân Vụ hoa, dựa theo ước định của ngươi, trong vòng hai ngày nhận được!” Trương Huyền không có đi vòng, cổ tay khẽ đảo, đem Ngụy Trường Phong liều mạng đi tính mạng, có được đóa hoa đưa tới."),i("br"),i("br"),n._v("“Nhận được?”"),i("br"),i("br"),n._v("Vưu phó viện trưởng sững sờ, tiếp nhận hộp ngọc, tiện tay mở ra."),i("br"),i("br"),n._v("Một cỗ mây mù chi khí, từ trong hộp bay lên, uyển như bụi mù, cho người ta một loại sảng khoái cảm giác."),i("br"),i("br"),n._v("Vân Vụ hoa, coi như không nung, cũng cho người một loại khác nhau hưởng thụ, bằng không thì cũng không có khả năng như thế nổi danh, thậm chí Hồng Viễn hoàng thất cũng làm thành bảo vật."),i("br"),i("br"),n._v("“Quả thực là Vân Vụ hoa!”"),i("br"),i("br"),n._v("Vưu phó viện trưởng nhẹ gật đầu."),i("br"),i("br"),n._v("“Cái kia... Phải chăng có thể cho ta Thập Diệp hoa?” Trương Huyền nói."),i("br"),i("br"),n._v("“Không thể!”"),i("br"),i("br"),n._v("Vưu phó viện trưởng lắc đầu: “Ta muốn là tươi mới Vân Vụ hoa, mục đích là vì nuôi dưỡng. Ngươi bây giờ cho ta một cái hái xuống, đã tử vong, như thế nào cho ngươi? Như vậy đi, nếu như ngươi thật muốn Thập Diệp hoa, cho ta lấy một cây sống Vân Vụ hoa đến!”"),i("br"),i("br"),n._v("“Sống?” Trương Huyền sầm mặt lại."),i("br"),i("br"),n._v("Đối phương nói trao đổi thời điểm, hắn liền ở bên cạnh, căn bản chưa nói qua cái gì sống!"),i("br"),i("br"),n._v("Bây giờ lại tìm lấy cớ này, rất rõ ràng, liền không muốn đem Thập Diệp hoa cho hắn!"),i("br"),i("br"),n._v("“Tốt, sống liền sống...”"),i("br"),i("br"),n._v("Mặc dù có chút lửa giận, Trương Huyền vẫn gật đầu, đem hộp ngọc nắm ở lòng bàn tay, tinh thần khẽ động, một cỗ Thiên Đạo chân khí truyền vào đi vào."),i("br"),i("br"),n._v("Cùng thời khắc đó, vận chuyển lực lượng, từ trong giới chỉ lấy ra một giọt địa mạch linh dịch, lặng lẽ nhỏ ở gốc."),i("br"),i("br"),n._v("Bụi dược liệu này cứ việc bị rút ra tiếp cận một ngày một đêm, nhưng đặt ở trong hộp ngọc, bảo trì lại mới mẻ."),i("br"),i("br"),n._v("Lại dùng bên trên có thể tẩm bổ vạn vật Thiên Đạo chân khí cùng địa mạch linh dịch, hẳn là có thể cứu sống."),i("br"),i("br"),n._v("Quả nhiên, thoạt nhìn có chút cúi Thập Diệp hoa, tại chân khí cùng địa mạch linh dịch trợ giúp bên dưới, lần nữa khôi phục lại, ngạo nghễ dựng đứng, tản mát ra kinh người mùi thơm."),i("br"),i("br"),n._v("“Sống lại?” Tôn Nguyên nhịn không được ngẩn ngơ."),i("br"),i("br"),n._v("Ngay cả Vưu phó viện trưởng cũng sững sờ tại nguyên chỗ."),i("br"),i("br"),n._v("Nói thật, hắn căn bản là không có muốn đem Thập Diệp hoa bán ra, cái gọi là Vân Vụ hoa loại hình, đều là mượn cớ mà thôi."),i("br"),i("br"),n._v("Vốn cho rằng, nói muốn sống, có thể để cho biết khó mà lui, không nghĩ tới thời gian nháy mắt, cái tên này liền làm được."),i("br"),i("br"),n._v("“Được rồi, Vân Vụ hoa đã sống, Vưu phó viện trưởng có thể trồng!” Thở phào nhẹ nhõm, Trương Huyền lần nữa đem hộp ngọc đưa tới."),i("br"),i("br"),n._v("“Không sai, thứ này là sống lại, bất quá... Thập Diệp hoa còn không có hoàn toàn hoàn thiện, ta cũng không thể cho ngươi!”"),i("br"),i("br"),n._v("Vưu phó viện trưởng lần nữa lắc đầu: “Không bằng như vậy đi, bụi dược liệu này đại khái mười năm sau, có thể hoàn toàn hoàn thiện, đến lúc đó, ta có thể phân cho ngươi một gốc...”"),i("br"),i("br"),n._v("“Mười năm sau?”"),i("br"),i("br"),n._v("Híp mắt lại, Trương Huyền tràn đầy lửa giận: “Trước ngươi nói, trong hai ngày lấy ra Vân Vụ hoa liền trao đổi, lấy ra về sau, ngươi còn nói muốn sống, hiện tại dược liệu sống, ngươi còn nói muốn chờ mười năm, nếu như mười năm sau, từ chối nữa đâu?”"),i("br"),i("br"),n._v("“Thế nào, ngươi chẳng lẽ còn hoài nghi ta hay sao? Làm một cái học sinh, hoài nghi lão sư, vô dài vô ấu, còn thể thống gì? Hừ, như ngươi loại này nhân phẩm, để cho ta như thế nào đem Thập Diệp hoa cho ngươi?”"),i("br"),i("br"),n._v("Vưu phó viện trưởng sắc mặt trầm xuống, hất lên ống tay áo: “Ngươi đi đi, ta không bán!”"),i("br"),i("br"),n._v("Convert by: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-810"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-812"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);