(window.webpackJsonp=window.webpackJsonp||[]).push([[2090],{2444:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-803-muoi-van-hoc-phan-【-canh-3-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-803-muoi-van-hoc-phan-【-canh-3-】"}},[n._v("#")]),n._v(" Chương 803: Mười vạn học phần? 【 Canh [3] 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 803: Mười vạn học phần? "),t("br"),t("br"),n._v("“Không bán?” Nghe được đối phương quả nhiên nói ra lời này, Trương Huyền khuôn mặt xanh xám."),t("br"),t("br"),n._v("Nếu như không phải cái tên này, Ngụy Trường Phong cũng sẽ không chết!"),t("br"),t("br"),n._v("Nói nhiều như vậy, thế mà còn là không bán?"),t("br"),t("br"),n._v("Quả nhiên danh sư vô sỉ lên, so Vân Vụ lĩnh Thánh thú đều còn đáng sợ hơn!"),t("br"),t("br"),n._v("“Đúng!” Vưu phó viện trưởng khoát tay áo: “Không thành thục liền bán ra, ta còn không có cái thói quen này, ngươi đi đi!”"),t("br"),t("br"),n._v("Biết được không đến Thập Diệp hoa, Ngụy Như Yên sinh mệnh liền tràn ngập nguy hiểm, Trương Huyền cố nén sắp nổ tung lồng ngực: “Ngươi ý tứ, không thành thục liền không bán, nếu như Thập Diệp hoa thành thục, liền sẽ bán ra?”"),t("br"),t("br"),n._v("“Đúng!” Vưu phó viện trưởng gật đầu: “Dược liệu không thành thục, để cho ta như thế nào bán ra? Ngươi hẳn là rõ ràng đạo lý trong đó, cũng không phải là cố ý làm khó!”"),t("br"),t("br"),n._v("“Cũng thế, đã không thành thục không bán, cái kia... Ta hiện tại liền để thứ này hoàn thiện!”"),t("br"),t("br"),n._v("Lạnh lùng nhìn đối phương một cái, Trương Huyền quay người lại đi tới dược viên Thập Diệp hoa gần, ngón tay một điểm, mấy giọt địa mạch linh dịch chui vào, ngay sau đó Thiên Đạo chân khí, hướng phía trong truyền vào."),t("br"),t("br"),n._v("Đơn thuần dùng địa mạch linh dịch tẩm bổ lời nói, mặc dù cũng có thể để Thập Diệp hoa nhanh chóng hoàn thiện, nhưng ít nhất phải một ngày tầm đó thời gian, dùng Thiên Đạo chân khí phụ trợ lại khác biệt, tốc độ sẽ tăng nhanh không ít."),t("br"),t("br"),n._v("Quả nhiên, chân khí cùng linh dịch đi vào, Thập Diệp hoa mắt trần có thể thấy tăng trưởng, chừng mười phút đồng hồ về sau, mười cái nụ hoa liền hoàn toàn nở rộ, tản mát ra kinh người mùi thơm."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Tôn Nguyên trừng to mắt."),t("br"),t("br"),n._v("Thập Diệp hoa, đây chính là Thánh dược, tình huống bình thường, cần mười năm mới có thể hoàn thiện, đối phương chỉ dùng mười phút đồng hồ... Làm sao làm được?"),t("br"),t("br"),n._v("“Đã thành thục, Vưu phó viện trưởng, hiện tại có thể bán chứ?”"),t("br"),t("br"),n._v("Trương Huyền đứng dậy."),t("br"),t("br"),n._v("Vưu phó viện trưởng sắc mặt tái xanh."),t("br"),t("br"),n._v("Nói lấy ra Vân Vụ hoa trao đổi, nhân gia lấy ra rồi; Nói không phải sống, đối phương làm thành sống; Nói dược liệu không thành thục, tại chỗ thúc... Cái tên này không phải đến mua dược, là cố ý đánh mặt a?"),t("br"),t("br"),n._v("“Làm sao? Thập Diệp hoa thành thục, Vưu phó viện trưởng lại không muốn bán?” Thấy hắn cái bộ dáng này, Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“A! Cái này gốc Thập Diệp hoa, là ta một tay nuôi lớn, ta nói không bán, ngươi chẳng lẽ còn muốn cường thủ hào đoạt hay sao?” Vưu phó viện trưởng lông mày giương lên."),t("br"),t("br"),n._v("“Cường thủ hào đoạt một vị phó viện trưởng, ta đương nhiên không dám!” Trương Huyền lắc đầu, ánh mắt như điện, lần nữa nhìn qua: “Ta hỏi lại ngươi một lần cuối cùng, ngươi xác định không bán cho ta?”"),t("br"),t("br"),n._v("“Không sai, của ta Thập Diệp hoa, coi như nát trong đất, cũng sẽ không bán cho như ngươi loại này không biết lễ phép, không tuân quy củ người!” Vưu phó viện trưởng khẽ nói."),t("br"),t("br"),n._v("“Rất tốt!”"),t("br"),t("br"),n._v("Không tại nhiều nói, Trương Huyền xoay người rời đi, rất nhanh biến mất tại viện lạc."),t("br"),t("br"),n._v("“Đi?”"),t("br"),t("br"),n._v("Còn tưởng rằng cái tên này, sẽ làm ra chuyện khác người gì, không nghĩ tới nói đi là đi, Tôn Nguyên sững sờ tại nguyên chỗ, một lát sau, thực sự nhịn không được nhìn về phía trước mắt vị viện trưởng này: “Lão sư, một gốc Thập Diệp hoa mà thôi, đã muốn mua, liền bán cho hắn được rồi, cần gì gây như thế cương...”"),t("br"),t("br"),n._v("“Làm càn! Làm sao, cánh cứng cáp rồi, muốn muốn giáo huấn lão sư?” Vưu phó viện trưởng lông mày vung lên."),t("br"),t("br"),n._v("“Học sinh không dám!” Tôn Nguyên ôm quyền: “Ta nhưng mà nghe nói qua cái tên này không ít chuyện, nghe nói phi thường tà tính, ta sợ... Hoàn toàn đắc tội, dẫn tới phiền phức!”"),t("br"),t("br"),n._v("“Truyện cười! Ta Vưu Hư, đường đường Thánh vực cường giả, y sư học viện phó viện trưởng, gió to sóng lớn gì chưa thấy qua? Chẳng lẽ còn sợ một cái học sinh?” Vưu phó viện trưởng cười lạnh."),t("br"),t("br"),n._v("“Nhưng mà...” Tôn Nguyên tràn đầy lo lắng."),t("br"),t("br"),n._v("Cái tên này có thể để cho Mạc Cao Viễn, Mi viện trưởng, Triệu viện trưởng, Vệ Nhiễm Tuyết viện trưởng, coi trọng như thế, tất nhiên có nguyên nhân, bản thân vị lão sư này, tùy tiện đắc tội, có thể hay không do đó đắc tội mấy vị viện trưởng, thậm chí Danh Sư đường đường chủ?"),t("br"),t("br"),n._v("  “Không có gì nhưng mà, như vậy... Ngươi cùng đi qua nhìn một chút, nếu như tiểu tử này, tại Mi viện trưởng đám người trước mặt nói xấu ta, lan ra cái gì không thật tin tức, liền lập tức bắt lại, dựa theo nói xấu cấp cao danh sư tội danh xử lý!”"),t("br"),t("br"),n._v("Vưu phó viện trưởng hừ lạnh."),t("br"),t("br"),n._v("“Cái này...” Tôn Nguyên chần chờ."),t("br"),t("br"),n._v("“Còn không mau đi! Cấp thấp danh sư, vọng nghị cấp cao, là tội lỗi lớn! Cái tên này cấp bậc không bằng ta, nếu như dám ở trước mặt người khác nói lung tung, liền là muốn chết!” Vưu phó viện trưởng vẫy tay."),t("br"),t("br"),n._v("“Đúng!” Thấy lão sư nói như vậy, Tôn Nguyên không có cách nào, chỉ tốt nhẹ gật đầu, từ trong sân lui ra ngoài."),t("br"),t("br"),n._v("Cấp thấp danh sư, không có chứng cứ rõ ràng, liền loạn nghị cấp cao, xem như trái lệ, vị này Trương Huyền chỉ có tứ tinh, mà lão sư của mình là lục tinh đỉnh phong, chênh lệch hai cái cấp bậc, thực có can đảm nói lung tung, quả thực có thể làm trận bắt đi."),t("br"),t("br"),n._v("Rời đi sân nhỏ, nhìn chung quanh một vòng, vừa vặn trông thấy vừa đi ra Trương sư, còn chưa đi xa, lặng lẽ đi theo."),t("br"),t("br"),n._v("Mặc dù đối lão sư làm pháp, không quá đồng ý, nhưng sư mệnh khó vi phạm, vẫn là tuân thủ một cách nghiêm chỉnh."),t("br"),t("br"),n._v("“Hắn là trước tìm Mi viện trưởng vẫn là Triệu viện trưởng?”"),t("br"),t("br"),n._v("Xa xa theo, trong lòng phỏng đoán."),t("br"),t("br"),n._v("Vị này Trương Huyền sự tình, hắn nghe nói không ít, bất kể Mi viện trưởng, Triệu viện trưởng vẫn là Vệ viện trưởng, đều đem xem như khách quý, nếu quả thật muốn tìm phiền toái, khẳng định muốn tìm bọn hắn bên trong một cái."),t("br"),t("br"),n._v("Bất quá, ba người coi như địa vị tôn sùng, cùng bọn hắn không thuộc về cùng một cái học viện, nhiều nhất làm cái hòa sự lão, hẳn là sẽ không đem lão sư hắn làm gì."),t("br"),t("br"),n._v("“Ta cảm thấy tìm Triệu viện trưởng khả năng tương đối lớn, luyện khí sư học viện xếp hạng thứ hai, đối với hắn cũng càng thêm coi trọng...”"),t("br"),t("br"),n._v("Đích thì thầm một tiếng, đang phỏng đoán đối phương biết tìm ai cáo trạng, liền thấy phía trước thanh niên thân thể xoay một cái, thẳng tắp hướng y sư tháp đi vào."),t("br"),t("br"),n._v("“Ừm? Đi y sư tháp làm cái gì?”"),t("br"),t("br"),n._v("Con mắt chớp chớp."),t("br"),t("br"),n._v("Dựa theo ý nghĩ của hắn, người thanh niên này, không nên tìm mấy vị viện trưởng khiếu nại, tìm sư phụ của mình phiền phức ư? Làm sao ngược lại chạy đến y sư tháp?"),t("br"),t("br"),n._v("“Đi qua nhìn một chút!”"),t("br"),t("br"),n._v("Vội vã đi theo."),t("br"),t("br"),n._v("Y sư tháp dòng người như thoi đưa, Trương Huyền đi vào đám người liền không thấy tung tích, Tôn Nguyên tìm nửa ngày đều không tìm được, thực sự nhịn không được, tìm trước đài."),t("br"),t("br"),n._v("Một cái nữ hài đứng tại trong quầy, thấy hắn tới vội vàng ôm quyền: “Tôn lão sư...”"),t("br"),t("br"),n._v("Chính là ngày đó mang Trương Huyền đi biệt viện nữ hài."),t("br"),n._v(" “Ừm, ngươi có thể thấy vị kia gọi Trương Huyền tân sinh?” Tôn Nguyên hỏi."),t("br"),t("br"),n._v("“Trương Huyền?” Nữ hài sững sờ."),t("br"),t("br"),n._v("“Chính là ngày đó ngươi mang theo đi gặp Vưu phó viện trưởng cái kia!” Tôn Nguyên cau mày."),t("br"),t("br"),n._v("“Hắn... Hắn chính là Trương Huyền?” Nữ hài che miệng, con mắt trợn tròn, giống như là thấy được chợt như bình thường hưng phấn."),t("br"),t("br"),n._v("Nói thật, thời gian vài ngày, trong sân trường bất kể lão sư vẫn là tân sinh, không biết “Trương Huyền” cái tên này người đã không nhiều lắm."),t("br"),t("br"),n._v("“Khụ khụ! Ngươi có thể thấy?” Thấy nữ hài trong mắt tràn đầy ngôi sao nhỏ, Tôn Nguyên tràn đầy im lặng, nhịn không được khoát tay áo."),t("br"),t("br"),n._v("Đều là cái gì đây!"),t("br"),t("br"),n._v("Ngươi một cái cấp cao học sinh, đến mức sùng bái cấp thấp sao?"),t("br"),t("br"),n._v("Sùng bái về sùng bái, không cần biểu hiện rõ ràng như vậy đi!"),t("br"),t("br"),n._v("“Thấy được, hắn mới vừa tại ta chỗ này, lĩnh cái bảng biểu, nói muốn đi xông một chút nghi vấn khó xử lý tường, kiếm chút học phần!” Nữ hài gật đầu."),t("br"),t("br"),n._v("“Đi nghi vấn khó xử lý tường lợi nhuận học phần?” Tôn Nguyên sửng sốt một chút, nhịn không được lắc đầu."),t("br"),t("br"),n._v("Nghi vấn khó xử lý tường, lại gọi nghi vấn khó xử lý tạp chứng tường, mỗi cái Y Sư công hội đều có."),t("br"),t("br"),n._v("Bình thường đều đem khó mà giải quyết nghi vấn khó xử lý tạp chứng thả ở phía trên, cổ vũ hậu nhân hoàn thành, mà trên thực tế, có thể hoàn thành... Có thể đếm được trên đầu ngón tay, hầu như làm không được."),t("br"),t("br"),n._v("Vậy thì cùng luyện khí sư học viện ghi chép thông đạo đồng dạng, hàng năm muốn đi xông không biết bao nhiêu, nhưng lại có mấy cái có thể thành công?"),t("br"),t("br"),n._v("“Đúng vậy a!” Nữ hài gật đầu."),t("br"),t("br"),n._v("“Ta còn tưởng rằng sẽ đi tìm Triệu viện trưởng, không nghĩ tới đến xông nghi vấn khó xử lý tường, cái tên này đến cùng làm gì?”"),t("br"),t("br"),n._v("Tràn đầy kỳ quái, Tôn Nguyên đang không biết tiểu tử kia làm cái quỷ gì, liền nghe đến một hồi oanh minh, tựa như động đất, bàn chân đều có chút run rẩy."),t("br"),t("br"),n._v("“Đây là thế nào?”"),t("br"),t("br"),n._v("Y sư học viện, luôn luôn chắc chắn, trừ phi phát sinh biến cố, nếu không sẽ không lay động."),t("br"),t("br"),n._v("“Nhanh đi qua nhìn một chút, nghi vấn khó xử lý tường sập...”"),t("br"),t("br"),n._v("“Nghi vấn khó xử lý tường? Đây chính là câu thông y sư tổng bộ, làm sao sẽ sập?”"),t("br"),t("br"),n._v("“Ta nghe nói, vừa rồi có người xông vào trong đó, không đến mười phút đồng hồ liền phá giải trong đó tất cả nghi vấn khó xử lý tạp chứng, vận chuyển quá nhanh, bức tường không chịu nổi, liền sập...”"),t("br"),t("br"),n._v("“Không chịu nổi?”"),t("br"),t("br"),n._v("“Đúng vậy a, nghi vấn khó xử lý tường vận chuyển, xử lý đáp án cùng tin tức, dùng chính là linh thạch thượng phẩm, mấy trăm đạo nghi vấn khó xử lý tạp chứng, mười phút đồng hồ phá giải, tốc độ có thể tưởng tượng được... Vận chuyển quá nhanh, tốc độ quá mạnh, có thể chịu được mới là lạ...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Đang đang kỳ quái, liền nghe đến liên tiếp tiếng nghị luận vang lên, Tôn Nguyên ngay sau đó liền thấy vô số người hướng nghi vấn khó xử lý tường phương hướng dũng mãnh lao tới."),t("br"),t("br"),n._v("“Phá giải tốc độ quá nhanh, nghi vấn khó xử lý tường vận tải không được, sụp đổ?”"),t("br"),t("br"),n._v("Tôn Nguyên trừng to mắt, suýt chút nữa không mù."),t("br"),t("br"),n._v("Thật hay giả?"),t("br"),t("br"),n._v("Nghi vấn khó xử lý trên tường nghi vấn khó xử lý tạp chứng, hắn đi phá giải qua, phi thường khó, đừng nói là hắn, coi như lão sư hắn, đều khó mà hoàn thành, làm sao có thể có người mười phút đồng hồ toàn bộ phá giải xong?"),t("br"),t("br"),n._v("“Chẳng lẽ là... Trương Huyền?”"),t("br"),t("br"),n._v("Đột nhiên nhớ tới nữ hài lời nói, nhịn không được nhìn lại, chỉ thấy người sau cũng trợn tròn tròng mắt."),t("br"),t("br"),n._v("“Hôm nay nhưng còn có những người khác đi xông nghi vấn khó xử lý tường?” Vội vàng hỏi."),t("br"),t("br"),n._v("“Không có, liền hắn một cái...” Nữ hài run rẩy."),t("br"),t("br"),n._v("“Thật là hắn?”"),t("br"),t("br"),n._v("Trái tim một chút băng lãnh, rốt cuộc kìm nén không được: “Đi qua nhìn một chút!”"),t("br"),t("br"),n._v("Hai người vội vã hướng nghi vấn khó xử lý tường tiến đến, quả nhiên thấy trước đó cao lớn thông đạo, giờ phút này đã biến thành một vùng phế tích, không ngừng phả ra khói xanh."),t("br"),t("br"),n._v("Một người trung niên mặt mày xám xịt từ phế tích đi tới, không nói ra được chật vật."),t("br"),t("br"),n._v("“Hồ sư, xảy ra chuyện gì?” Tôn Nguyên vội vàng nghênh đón tiếp lấy."),t("br"),t("br"),n._v("Vị này Hồ sư, chính là đồng môn của hắn sư đệ, chịu trách nhiệm chăm sóc nghi vấn khó xử lý tường."),t("br"),t("br"),n._v("“Là cái kia Trương Huyền, chạy tới, xông nghi vấn khó xử lý tường, mới vừa đem bên trong nghi vấn khó xử lý tạp chứng toàn bộ giải quyết... Bởi vì tích lũy học phần quá nhiều, tính toán không ra, cho nên... Thông đạo liền sập!”"),t("br"),t("br"),n._v("Hồ sư bờ môi run rẩy, vội vàng giải thích."),t("br"),t("br"),n._v("“Tích lũy học phần quá nhiều?” Tôn Nguyên sững sờ."),t("br"),t("br"),n._v("“Đúng vậy a, nghi vấn khó xử lý tường tình huống ngươi cũng không phải không biết, vì cổ vũ học viên đến xông, giải quyết càng nhanh, lấy được học phần càng cao. Một canh giờ giải quyết một cái, có thể được một cái học phần, giải quyết hai cái, liền có thể đến ba phút, giải quyết ba cái, thì đến sáu phần, hiện lên một thêm hai thêm ba thêm bốn loại này tăng lên...”"),t("br"),t("br"),n._v("Hồ sư giải thích."),t("br"),t("br"),n._v("Tôn Nguyên gật đầu."),t("br"),t("br"),n._v("Nghi vấn khó xử lý tường quả thực có loại quy định này."),t("br"),t("br"),n._v("Giải quyết thời gian càng nhanh, nan đề càng nhiều, lấy được học phần cũng là càng cao, đây cũng là vì đề cao học sinh chữa bệnh tốc độ, có thể tại càng trong thời gian ngắn chữa bệnh cứu người."),t("br"),t("br"),n._v("“Vị này Trương Huyền, trong vòng năm phút đồng hồ giải quyết vượt qua năm trăm đạo nghi vấn khó xử lý tạp chứng... Học phần quá nhiều, học viện tất cả học phần tính gộp lại đều không đủ, lại thêm cơ quan vận chuyển quá nhanh, liền biến thành như vậy...”"),t("br"),t("br"),n._v("Hồ sư giải thích."),t("br"),t("br"),n._v("“Học phần không đủ?”"),t("br"),t("br"),n._v("Thân thể nhoáng một cái, Tôn Nguyên mắt tối sầm lại, suýt chút nữa không đã hôn mê."),t("br"),t("br"),n._v("“Ngươi nói là... Hắn, hắn kiếm lấy, vượt qua mười vạn học phần?”"),t("br"),t("br"),n._v("(Canh [3] đến! Cao trào bắt đầu, đắc tội trương điếu bột tử, sẽ dọa đến hắn hoài nghi nhân sinh. Phát xong chương tiết, lão nhai mới phát hiện, đã phát sách ròng rã một năm, ba trăm sáu mươi lăm ngày, trong lúc đó, lão nhai bắt đầu chạy bốn, cũng có đứa bé thứ hai, chiếu cố phụ nữ có thai, đi làm, hầu hạ trong tháng... Đủ loại sự tình, vội vàng rối tinh rối mù! Làm một cái không phải toàn chức tác gia, hai trăm bốn mươi mốt vạn chữ, chưa hề quịt canh... Đây là lão nhai cho mình, cũng là cho độc giả bài thi. Cảm tạ mọi người cùng nhau đi tới, cũng cảm tạ mình một đường kiên trì, Thiên Đạo thư viện cố sự rất dài, hi vọng có thể cùng các ngươi tiếp tục đi tới đích, cám ơn!)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-811"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-813"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);