(window.webpackJsonp=window.webpackJsonp||[]).push([[2091],{2446:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-804-sup-đo-ton-nguyen"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-804-sup-đo-ton-nguyen"}},[n._v("#")]),n._v(" Chương 804: Sụp đổ Tôn Nguyên")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("Chương 804: Sụp đổ Tôn Nguyên"),i("br"),i("br"),n._v("Mười đại học viện, mỗi mười năm, đều có nhất định số lượng học phần, cung cấp cho học sinh."),i("br"),i("br"),n._v("Căn cứ học viên bao nhiêu, đứng đầu không đứng đầu, học phần hoàn thành tỉ lệ như thế nào... Rất nhiều điều kiện, tiến hành đánh giá."),i("br"),i("br"),n._v("Y sư học viện là bài danh thứ năm cỡ lớn học viện, căn cứ nhiều năm qua đánh giá, thời gian mười năm phía trong, có thể chi phối mười vạn học phần."),i("br"),i("br"),n._v("Học sinh đọc sách, trong trường học sử dụng, cơ bản có thể thu về, hình thành tuần hoàn."),i("br"),i("br"),n._v("Vị này Trương Huyền, có thể để cho nghi vấn khó xử lý tường, không cách nào phân phối học phần, cứ thế mà đổ sụp... Nói rõ nhận được học phần số lượng, vượt qua mười vạn!"),i("br"),i("br"),n._v("Nói cách khác... Vượt qua toàn bộ y sư học viện mười năm học phần tổng hợp!"),i("br"),i("br"),n._v("“Lão sư chưởng quản học phần cùng nhiệm vụ phân phối, học phần không đủ phát... Nguy rồi, xảy ra đại sự...”"),i("br"),i("br"),n._v("Nghĩ đến một việc, Tôn Nguyên khóe miệng giật một cái, nhanh muốn khóc."),i("br"),i("br"),n._v("Lão sư của hắn, Vưu Hư, đang chưởng quản lấy học viện y sư khảo hạch cùng học phần phân phối, mười năm học phần, đều bị một người nhận được, cái khác làm nhiệm vụ, lại không học phần học sinh, có thể hay không chạy tới tìm phiền toái?"),i("br"),i("br"),n._v("Nhẹ, nhục mắng một trận, nặng, chỉ sợ đều dám động thủ!"),i("br"),i("br"),n._v("Học phần liên quan học viên tu hành, không bỏ ra nổi đến, vi phạm với toàn bộ học viện quy tắc, làm chủ quản phó viện trưởng, liền muốn làm trước chịu đến trừng phạt!"),i("br"),i("br"),n._v("Còn tưởng rằng cái tên này sẽ chạy tới tìm Triệu viện trưởng cáo trạng, không nghĩ tới, nhân gia còn chưa có đi, chỉ xông cái nghi vấn khó xử lý tường, liền đem lão sư hắn, đẩy lên nơi đầu sóng ngọn gió, ai cũng cứu không được!"),i("br"),i("br"),n._v("Y sư học viện, muốn là bởi vì việc này, gây nên vô số học sinh bất mãn, bắt đầu lui khóa lời nói, thực lực tổng hợp, tất nhiên hạ thấp, làm không cẩn thận sẽ từ xếp hạng thứ năm, ngã xuống mấy cái cấp bậc."),i("br"),i("br"),n._v("Đến lúc đó, không cần nhiều lời, cái này phó viện trưởng khẳng định không làm nổi."),i("br"),i("br"),n._v("“Đúng vậy a!” Hồ sư cũng ý thức được điểm ấy, một mặt cười khổ."),i("br"),i("br"),n._v("“Đúng rồi, vị này Trương Huyền, không ở nơi này ư?”"),i("br"),i("br"),n._v("[ truyen cua tui | N"),i("br"),n._v("et]  Biết đối phương khẳng định là cố ý tìm phiền toái, Tôn Nguyên dạo qua một vòng, không phát hiện tung tích, nhịn không được hỏi."),i("br"),i("br"),n._v("“Hắn? Hắn giải quyết xong nghi vấn khó xử lý tường liền đi, hình như trừ bệnh chứng đài...” Hồ sư nói."),i("br"),i("br"),n._v("Khóe miệng giật một cái, Tôn Nguyên thân thể nhoáng một cái: “Chứng bệnh đài? Hắn đi cái kia làm gì?”"),i("br"),i("br"),n._v("“Chứng bệnh đài bố trí có cơ quan, có thể để cho phụ tu y đạo danh sư, cảm nhận được bệnh chứng thần kỳ, thuận tiện cũng có thể đo lường phương thuốc chuẩn xác... Hắn quá khứ, hẳn là muốn đo lường một chút bản thân y đạo thực lực đi...” Hồ sư nói."),i("br"),i("br"),n._v("Chứng bệnh đài cùng nghi vấn khó xử lý tường đồng dạng, cũng là Y Sư công hội có đồ vật, có thể để cho học tập y đạo danh sư, kiểm nghiệm hữu hiệu phương thuốc, mà không phải loạn kê đơn thuốc."),i("br"),i("br"),n._v("“Hắn liền nghi vấn khó xử lý tường đều có thể xông qua... Còn dùng kiểm nghiệm thực lực ư?”"),i("br"),i("br"),n._v("Con mắt tối sầm lại, Tôn Nguyên suýt chút nữa không khóc."),i("br"),i("br"),n._v("Nghi vấn khó xử lý tường, thế nhưng là toàn bộ học viện khó khăn nhất, tổng kết tất cả nghi vấn khó xử lý tạp chứng, nơi này nhẹ nhõm qua ải, mười phút đồng hồ giải quyết hơn năm trăm triệu chứng... Như thế y thuật, còn cần đến trừ bệnh chứng đài đo lường?"),i("br"),i("br"),n._v("“Cái kia... Nguy rồi!”"),i("br"),i("br"),n._v("Hồ sư cũng kịp phản ứng, đang muốn nói gì, lần nữa nghe được một tiếng oanh minh, nơi xa bụi mù dâng lên, dường như lại có cái gì sụp đổ."),i("br"),i("br"),n._v("“Là chứng bệnh đài, nhanh đi qua nhìn một chút...”"),i("br"),i("br"),n._v("Bờ môi co lại, Tôn Nguyên nhanh muốn điên rồi, mang theo Hồ sư đám người vội vã chạy tới, chỉ nhìn thoáng qua, đã cảm thấy thân thể kìm lòng không được nhoáng một cái."),i("br"),i("br"),n._v("Chứng bệnh đài, lớn như vậy thông đạo đã đổ sụp thành phế tích, một cái lão sư đứng ở bên cạnh, ánh mắt đờ đẫn."),i("br"),i("br"),n._v("“Chu sư, chuyện gì xảy ra?”"),i("br"),i("br"),n._v("Tôn Nguyên đi tới gần."),i("br"),i("br"),n._v("Vị này Chu sư, cũng là đồng môn của hắn sư đệ, Vưu phó viện trưởng học sinh thân truyền, chịu trách nhiệm chứng bệnh đài sửa chữa cùng đo lường."),i("br"),i("br"),n._v("“Một vị tân sinh, nói muốn tới đo lường, liền để tiến vào, ai ngờ... Đi vào không đến ba phút, liền biến thành như vậy...”"),i("br"),i("br"),n._v("Bờ môi run rẩy, Chu sư đến bây giờ đều không thể tin được."),i("br"),i("br"),n._v("“Cái kia tân sinh kêu cái gì, hắn ở đâu?”"),i("br"),i("br"),n._v("Biết bây giờ không phải là xoắn xuýt tại sao lại sập vấn đề, Tôn Nguyên vội vàng hỏi."),i("br"),i("br"),n._v("“Hắn gọi Trương Huyền, người... Đi !”"),i("br"),i("br"),n._v("Chu sư nói."),i("br"),i("br"),n._v("“Tiền Bối lâm?”"),i("br"),i("br"),n._v("Tôn Nguyên sững sờ: “Tiền Bối lâm là tập hợp tiền bối ý niệm rừng bia, phía trên viết đầy bọn họ một đời, nổi danh nhất cứu người sự tích, cùng giải quyết nghi vấn khó xử lý tạp chứng phương thuốc! Dùng để để cho hậu nhân giám thưởng học tập, đi nơi đó làm gì...”"),i("br"),i("br"),n._v("Nếu như nói nghi vấn khó xử lý tường, chứng bệnh đài, là vì đo lường y thuật, kiếm lấy học phần, Tiền Bối lâm thì là dùng để chiêm ngưỡng."),i("br"),i("br"),n._v("Chỉ cần là học viện nổi danh đại y sư, đều sẽ đúc bia lưu danh, đem hắn trong cuộc đời giải quyết nghi vấn khó xử lý chứng bệnh, cùng phương thuốc viết lên, để hậu nhân sùng bái cùng chiêm ngưỡng..."),i("br"),i("br"),n._v("Nơi này cùng học phần không quan hệ, nhưng mà khích lệ hậu nhân vị trí, chạy tới làm gì?"),i("br"),i("br"),n._v("“Còn là đi qua nhìn một chút...” Hồ sư nhịn không được nói."),i("br"),i("br"),n._v("“Ừm!” Tôn Nguyên liền vội vàng gật đầu."),i("br"),i("br"),n._v("Nơi này mặc dù cùng khảo hạch học phần không quan hệ, nhưng vị này Trương Huyền thực sự quá tà tính, còn là đi qua nhìn một chút cho thỏa đáng."),i("br"),i("br"),n._v("Mấy người vội vã chạy tới, vẫn như cũ không đi tới gần, lần nữa nghe tới mặt đất lay động, ngay sau đó là vô số bia đá tiếng nổ."),i("br"),i("br"),n._v("Đung đưa kịch liệt, chấn mặt đất xuất hiện vết rách, toàn bộ y sư tháp, đều xuất hiện kịch liệt lay động."),i("br"),n._v(" “Chuyện gì xảy ra?”"),i("br"),i("br"),n._v("Vội vàng đi tới gần, thấy Tiền Bối lâm đầy đất phế tích, Tôn Nguyên đám người tất cả đều điên rồi."),i("br"),i("br"),n._v("Đây chính là hiển lộ rõ ràng tiền bối sự tích huy hoàng địa phương, làm sao cũng có thể phá hư?"),i("br"),i("br"),n._v("Chẳng lẽ... Ra tay công kích?"),i("br"),i("br"),n._v("Nghi vấn khó xử lý tường, chứng bệnh đài, là bởi vì vượt quan quá nhanh, cơ quan không chịu nổi, bản thân đổ sụp, coi như trách cứ, cũng không trách được trên đầu của hắn, chỉ có thể trách cơ quan bản thân thiếu hụt, không cân nhắc đến có người nhanh như vậy giải quyết nhiều như vậy nan đề."),i("br"),i("br"),n._v("Nhưng... Tiền Bối lâm, không liên quan khảo hạch, sập như thế nát, sẽ không phải là động thủ đi!"),i("br"),i("br"),n._v("Tùy ý ra tay phá hư y sư học viện đồ vật, nhất là tiền bối lưu lại tài phú, tuyệt đối là tội ác tày trời, đầy đủ đưa lên danh sư toà án, bị trực tiếp thẩm phán!"),i("br"),i("br"),n._v("“Cái này, cái này sao có thể...”"),i("br"),i("br"),n._v("Đang đang kỳ quái, chỉ thấy một người trung niên một mặt đờ đẫn đi tới, tràn đầy hoảng hốt, tựa như bị cái gì đả kích, nhanh muốn điên rồi."),i("br"),i("br"),n._v("“Diệp sư...”"),i("br"),i("br"),n._v("Tôn Nguyên vội vàng hô."),i("br"),i("br"),n._v("Vẫn như cũ là sư đệ của hắn, chịu trách nhiệm Tiền Bối lâm bảo vệ."),i("br"),i("br"),n._v("Bởi vì y sư tháp bên này về Vưu phó viện trưởng chưởng quản, chủ yếu cương vị lão sư, cơ bản đều là học sinh của hắn."),i("br"),i("br"),n._v("Nhìn thấy Tôn Nguyên, Hồ sư bọn người tại, Diệp sư cái này mới phản ứng được, quay đầu nhìn về phía trước mắt phế tích: “Đều sai, không nghĩ tới đều sai...”"),i("br"),i("br"),n._v("“Diệp sư, đến cùng xảy ra chuyện gì? Là có người hay không dùng man lực công kích Tiền Bối lâm?” Tôn Nguyên nhịn không được hỏi."),i("br"),i("br"),n._v("“Man lực?” Diệp sư lắc đầu: “Chính các ngươi nhìn liền biết...”"),i("br"),i("br"),n._v("Cũng không giải thích, lấy ra một cái ghi chép ngọc tinh, đưa tới."),i("br"),i("br"),n._v("Tôn Nguyên, Hồ sư đám người gấp vội cúi đầu nhìn lại."),i("br"),i("br"),n._v("Chỉ thấy bên trong ánh sáng lóe lên, xuất hiện Tiền Bối lâm hoàn hảo không chút tổn hại bộ dạng, ngay sau đó một thanh niên đi tới, không phải Trương Huyền thì là ai!"),i("br"),i("br"),n._v("Vị này Trương Huyền, mấy bước đi tới cái thứ nhất trước tấm bia đá."),i("br"),i("br"),n._v("Đây là y sư học viện vừa mới thành lập lúc, vị thứ nhất viện trưởng bi văn, phía trên trình bày hắn cả đời cống hiến, lớn nhất một cái chính là giải quyết khuyển yêu chứng."),i("br"),i("br"),n._v("Khuyển yêu chứng là năm đó lưu hành một loại quỷ dị chứng bệnh, bị loài chó linh thú cắn một cái, trong vòng ba tháng liền sẽ phát tác, coi như Thánh vực cường giả đều khó mà đào thoát, vừa bắt đầu toàn thân lột xác, sau đó lông dài, mấy ngày ngắn ngủi, liền sẽ mất lý trí, biến thành gặp người liền cắn quái vật."),i("br"),i("br"),n._v("Lúc đó bệnh chứng này lan tràn, làm hại bốn phương, vị viện trưởng này, đi qua ròng rã nửa năm khổ tâm nghiên cứu, rốt cục chế biến ra phương thuốc."),i("br"),i("br"),n._v("Bất quá, cái này phương thuốc nhưng mà dự phòng, mà không thể trị tận gốc, vì giải quyết tai hoạ ngầm, học viện điều đi vô số cao thủ, đem đã bị bệnh người tu luyện chém giết hoả táng, khống chế chứng bệnh lan tràn, cái này mới chính thức giải quyết."),i("br"),i("br"),n._v("Mặc dù lúc ấy chém giết rất nhiều, nhưng tương đương cứu càng nhiều người, do đó, vị viện trưởng này cũng được người sùng bái, chảy truyền tới."),i("br"),i("br"),n._v("Đang đang nghi ngờ, chỉ thấy vị kia Trương Huyền, nhìn bia đá một cái, bờ môi mở ra, phát ra âm thanh."),i("br"),i("br"),n._v("“Khuyển yêu chứng, là loài chó linh thú, máu trong cơ thể có độc tạo thành. Chỉ cần sử dụng quỳ ma hai tiền, lá đỏ bốn tiền, tử hiểu căn nửa lượng phối hợp loài chó linh thú tâm đầu huyết, rèn luyện một canh giờ, liền có thể giải quyết! Ngươi đem người bệnh toàn bộ chém giết hoả táng, dùng phương thuốc, giá cả vẫn là ta cái này gấp mấy chục lần, thật không biết có gì có thể tự hào, nhưng lưu truyền hậu thế! Chẳng lẽ là muốn cho hậu nhân cảm thấy cử động của ngươi mất mặt ư?”"),i("br"),i("br"),n._v("Tạch tạch!"),i("br"),i("br"),n._v("Nghe được hắn, trong tấm bia đá tiền bối lưu lại ý niệm, dường như biết phương thuốc của hắn càng thích hợp điều trị chứng bệnh, năm đó chém giết, quả thực sai, một hồi kịch liệt lay động, ầm vang nổ tung."),i("br"),i("br"),n._v("Ngay sau đó, Trương Huyền đi về phía cái thứ hai bia đá, cùng cái thứ nhất đồng dạng, nhìn thoáng qua, một lần nữa nói ra một cái phương thuốc, ẩn chứa tiền bối ý niệm bi văn, lần nữa cảm thấy áy náy, chủ động nổ tung."),i("br"),i("br"),n._v("Một đường hướng về phía trước, một đường mở miệng."),i("br"),i("br"),n._v("Từ nhìn bia đá, đến bắt đầu nói chuyện, liền hai cái hô hấp đều không có, nhưng mỗi lần mở miệng, tất nhiên có một cái bia đá nổ tung."),i("br"),i("br"),n._v("Nói cách khác... Hắn nói phương thuốc, đều muốn so trên tấm bia đá lưu lại, cao cấp hơn, càng thêm có hiệu, còn... Lại càng dễ phối chế!"),i("br"),i("br"),n._v("“Lấy y đạo giao đấu tiền bối, để bọn hắn cảm thấy không bằng... Chủ động hủy đi dấu vết lưu lại...”"),i("br"),i("br"),n._v("Tôn Nguyên, Hồ sư đám người các từ khóe miệng quất loạn."),i("br"),i("br"),n._v("Rốt cuộc biết vì cái gì Diệp sư tinh thần đều có chút hoảng hốt!"),i("br"),i("br"),n._v("Thủ hộ Tiền Bối lâm, sớm đã đối với mấy cái này tiền bối phục sát đất, cảm giác đến cử động của bọn hắn tất cả đều là chính xác, kết quả... Có người mà nói toàn bộ là sai lầm, hơn nữa còn chiếm được bọn họ công nhận... Gặp phải những này, không tại chỗ sụp đổ, cũng rất không tệ."),i("br"),i("br"),n._v("“Nghi vấn khó xử lý tường, chứng bệnh đài, Tiền Bối lâm... Toàn bộ y sư tháp trọng yếu nhất bốn dạng, ba loại đều hoàn toàn phế đi, lão sư đến cùng đắc tội một cái dạng gì quái thai?”"),i("br"),i("br"),n._v("Đem Tiền Bối lâm như thế nào phá hủy tình huống nhìn một lần, Tôn Nguyên nhịn không được tự lẩm bẩm."),i("br"),i("br"),n._v("Nói ra tiền bối thiếu hụt, dẫn đến bi văn tiêu diệt, không những chưa từng có, còn có công lao!"),i("br"),i("br"),n._v("Nếu là truyền đến Y Sư công hội, khẳng định còn lại nhận ca ngợi..."),i("br"),i("br"),n._v("Nhìn một chút, biết phương thuốc sai lầm... Cái tên này đến cùng làm sao làm được?"),i("br"),i("br"),n._v("“Đúng rồi, Trương Huyền đâu? Tiền Bối lâm đổ sụp, lại đi nơi nào?”"),i("br"),i("br"),n._v("Sau khi hết khiếp sợ, Tôn Nguyên cái này mới phản ứng được, nhịn không được vội vàng nhìn chung quanh."),i("br"),i("br"),n._v("“Tôn lão sư, không xong, Trương sư đi tầng thứ hai, định xông y sư tháp, khảo hạch y sư...”"),i("br"),i("br"),n._v("Đột nhiên, không biết ai hô lên, lời nói còn không có kết thúc, liền nghe đến đại địa một hồi lay động, vô số đá vụn bay lượn mà xuống."),i("br"),i("br"),n._v("Lập tức nhìn thấy trước mắt rộng lớn gian phòng, trong nháy mắt lật úp."),i("br"),i("br"),n._v("“Y sư tháp... Xong!”"),i("br"),i("br"),n._v("Tôn Nguyên khóc."),i("br"),i("br"),n._v("Convert by: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-812"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-814"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);