(window.webpackJsonp=window.webpackJsonp||[]).push([[2095],{2450:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-808-ty-thi-noi-dung"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-808-ty-thi-noi-dung"}},[n._v("#")]),n._v(" Chương 808: Tỷ thí nội dung")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 808: Tỷ thí nội dung"),t("br"),t("br"),n._v("Mặc dù buồn bực, nhưng dù sao cũng là sống tiếp cận một ngàn năm lão quái vật, buồn bực qua đi, bắt đầu suy tư đối sách."),t("br"),t("br"),n._v("“Có thể thông qua nghi vấn khó xử lý tường, nói rõ nghi vấn khó xử lý tạp chứng, khó không được hắn!”"),t("br"),t("br"),n._v("“Vượt quan chứng bệnh đài, nói rõ đối mới chứng bệnh, đã hiểu cực sâu...”"),t("br"),t("br"),n._v("“Nói tiền bối ý niệm xấu hổ, cam nguyện hủy diệt bi văn, nói rõ đối y đạo lĩnh ngộ, đạt đến cao thâm cảnh giới khó lường...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Ý niệm loạn chuyển, Vưu Hư khuôn mặt càng ngày càng khó coi."),t("br"),t("br"),n._v("Làm sao tính toán, cùng đối phương so, đều không có gì phần thắng."),t("br"),t("br"),n._v("“Trừ phi...”"),t("br"),t("br"),n._v("Đột nhiên, trong lòng hơi động, không biết nghĩ tới điều gì, trong ánh mắt lóe lên một đạo tàn nhẫn chi ý, nắm đấm xiết chặt."),t("br"),t("br"),n._v("“Tốt, quyết định như vậy đi!”"),t("br"),t("br"),n._v("Mới vừa làm ra quyết định kỹ càng, chỉ thấy đối diện Trương Huyền nhìn lại: “Đã Vưu phó viện trưởng như thế hào phóng, vậy ta liền quyết định như thế nào tỷ thí, ý của ta là...”"),t("br"),t("br"),n._v("“Chậm đã!”"),t("br"),t("br"),n._v("Lời còn chưa dứt, lại không để ý tới vừa rồi khí thế, Vưu Hư đem một tay giơ lên: “Ngươi đã khiêu chiến ta, như thế nào giao đấu tự nhiên do ta quyết định!”"),t("br"),t("br"),n._v("Nói đùa, thật muốn đối phương đến, tất nhiên chết chắc!"),t("br"),t("br"),n._v("“Đây chính là các ngươi nói có phong độ?”"),t("br"),t("br"),n._v("Mới vừa rồi còn tràn đầy ủng hộ rất nhiều lão sinh, nghe nói như thế, toàn bộ đều ngẩn ở đây tại chỗ, trên mặt nóng bỏng, giống như là bị quất một cái tát đồng dạng."),t("br"),t("br"),n._v("Nói xong có phong độ, lập tức đổi giọng, đường đường lục tinh danh sư, có thể muốn chút mặt nói chuyện tính toán điểm số ư?"),t("br"),t("br"),n._v("“Tốt, ngươi nói!”"),t("br"),t("br"),n._v("Cũng không nghĩ tới hắn sẽ sửa miệng, Trương Huyền lông mày nhíu một cái."),t("br"),t("br"),n._v("Đối phương như thế nào quyết định, hắn đều không để ý, mục đích hôm nay chỉ có một cái, vì... Ngụy Trường Phong báo thù!"),t("br"),t("br"),n._v("“Vậy thì tốt, chúng ta là y sư, y sư không chỉ chữa bệnh cứu người, còn muốn gánh vác xã hội trách nhiệm.”"),t("br"),t("br"),n._v("Vưu phó viện trưởng nhìn qua: “Trong đó... Giải độc, cũng là chúng ta y sư nhất định phải học hội cùng có thể giải quyết. Yêu cầu của ta rất đơn giản, cùng ngươi tỷ thí giải độc?”"),t("br"),t("br"),n._v("“Giải độc?” Trương Huyền lông mày vung lên."),t("br"),t("br"),n._v("Y sư tỷ thí, mặc dù không thể so với danh sư như thế, chia làm không biết bao nhiêu chủng loại, nhưng cũng có thể tuỳ tiện nói ra mấy trăm loại, mà giải độc, đang ở trong đó."),t("br"),t("br"),n._v("Độc sư cùng y sư hai cái chức nghiệp, tương hỗ là thiên địch, muốn đạt tới cấp bậc cao hơn, thu hoạch được người khác công nhận, nhất định phải biết giải độc, nếu không, đừng nói không cách nào đối kháng độc sư, bị người hạ bàn tay đen cũng không biết."),t("br"),t("br"),n._v("Từ trước tới nay, bị độc chết y sư chỗ nào cũng có, nhiều vô số kể."),t("br"),t("br"),n._v("“Không sai, y sư không chỉ muốn trị bệnh, còn muốn biết giải độc, nếu không, lợi hại hơn nữa, cũng chỉ là cái cái thùng rỗng!”"),t("br"),t("br"),n._v("Vưu Hư nhìn qua: “Cho nên, đây cũng là y thuật một loại, hoàn toàn có thể đem ra tỷ thí.”"),t("br"),t("br"),n._v("“Tự tìm cái chết, cái tên này đáng giận!”"),t("br"),t("br"),n._v("Nghe được Vưu Hư đột nhiên lật lọng, nói ra tỷ thí giải độc, dưới đài vốn là khí sắp nổ tung, hận không thể đi lên cuồng đánh cái tên này một trận Chung Đỉnh Thuần, nhịn không được nổi giận."),t("br"),t("br"),n._v("“Làm sao vậy?” Mi trưởng lão nhìn qua."),t("br"),t("br"),n._v("“Giải độc tỷ thí, nguy cơ trùng trùng, sinh tử khó liệu!” Chung Đỉnh Thuần giải thích."),t("br"),t("br"),n._v("“Sinh tử khó liệu? Có ý tứ gì?”"),t("br"),t("br"),n._v("Triệu viện trưởng đám người tràn đầy nghi hoặc."),t("br"),t("br"),n._v("Bọn họ nghe qua giải độc tỷ thí, nhưng cụ thể quy tắc cũng không rõ ràng."),t("br"),t("br"),n._v("“Muốn giải độc, đầu tiên muốn có thuốc độc, tiếp theo... Còn muốn có chịu thể! Nói cách khác, trước muốn có người trúng độc, mới có thể giải!” Chung Đỉnh Thuần nói."),t("br"),t("br"),n._v("“Có người trúng độc?”"),t("br"),t("br"),n._v("Nếu là tỷ thí, khẳng định dùng là lợi hại nhất độc dược, có thể giải quyết còn tốt, không giải quyết được đâu?"),t("br"),t("br"),n._v("Há không tại chỗ liền sẽ có người bị độc chết?"),t("br"),t("br"),n._v("“Đúng vậy a, loại này tỷ thí quá nguy hiểm, y sư rất ít đi làm, cái tên này quả thực làm trái chuẩn tắc...” Chung Đỉnh Thuần cắn răng."),t("br"),t("br"),n._v("Độc thứ này, là loạn giải sao?"),t("br"),t("br"),n._v("Hơi không cẩn thận thế nhưng là sẽ chết người đấy."),t("br"),t("br"),n._v("“Trước đừng có gấp, coi như trúng độc cũng chưa hẳn là nhân loại, cũng có khả năng dùng thử đan thú loại hình làm thí nghiệm. Y sư chăm sóc người bị thương, sao có thể cầm nhân mạng nói đùa?” Mi trưởng lão nói."),t("br"),t("br"),n._v("Coi như thử độc, cũng chưa chắc cần nhân loại đi, linh thú cũng có tương đồng hiệu quả."),t("br"),t("br"),n._v("“Không phải chuyện này...” Chung Đỉnh Thuần đang muốn giải thích, liền nghe đến trên đài Trương Huyền, thanh âm nhàn nhạt vang lên."),t("br"),t("br"),n._v("“Tốt, giải độc liền giải độc, nói một chút quy tắc!”"),t("br"),t("br"),n._v("“Rất đơn giản!”"),t("br"),t("br"),n._v("Thấy hắn đồng ý, vưu Hư Tùng khẩu khí, nhẹ nhàng cười một tiếng: “Ngươi ta đều ra một phần độc dược, lẫn nhau dùng... Ai có thể tại trong vòng thời gian quy định giải độc, ai liền chiến thắng!”"),t("br"),t("br"),n._v("“Lấy thân thí nghiệm thuốc?” Trương Huyền cau mày."),t("br"),t("br"),n._v("“Không sai, quy tắc ta xách ra, dám vẫn là không dám? Không dám lời nói... Hôm nay giao đấu, coi như ngươi thua!” Vưu Hư hừ lạnh."),t("br"),t("br"),n._v("“Lẫn nhau phục dùng độc dược? Nếu như giải không được, chẳng phải sẽ làm trận hạ độc chết?”"),t("br"),t("br"),n._v("“Đúng vậy a, cái này quy tắc, thật là đáng sợ đi!”"),t("br"),t("br"),n._v("“Đây cũng không phải là y sư sinh tử đấu, mà là lấy mệnh tương bác...”"),t("br"),t("br"),n._v("Nghe được quy tắc, tất cả mọi người xôn xao."),t("br"),n._v("Ngay cả Vệ Nhiễm Tuyết, Triệu viện trưởng mấy người cũng đều là sững sờ."),t("br"),t("br"),n._v("Khó trách Chung Đỉnh Thuần nghe được cuộc thi đấu này, vội vã như thế, không nghĩ tới quy tắc ác như vậy!"),t("br"),t("br"),n._v("Có thể giải quyết tốt nhất, không giải quyết được đâu?"),t("br"),t("br"),n._v("Phải biết không ít độc dược, đều là vào máu là chết, hơi dính bên trên liền chết, muốn phải giải quyết, nào có dễ dàng như vậy."),t("br"),t("br"),n._v("Đang đang khiếp sợ, cảm thấy vị này Trương Huyền khẳng định sẽ không đồng ý thời điểm, liền nghe đến thanh âm của hắn vang lên."),t("br"),t("br"),n._v("“Tốt, ta đáp ứng!”"),t("br"),t("br"),n._v("“Không thể đáp ứng, quá nguy hiểm...”"),t("br"),t("br"),n._v("Sắc mặt trắng nhợt, Mi trưởng lão nhịn không được hô lên."),t("br"),t("br"),n._v("Hắn nhưng là nghe Mạc Cao Viễn nói qua Dương sư sự tích, vạn nhất Trương sư bị độc chết, chỉ sợ toàn bộ danh sư học viện, đều khó có thể chịu đựng đối phương lửa giận!"),t("br"),t("br"),n._v("“Đáng giận, khinh người quá đáng! Hắn là Thánh vực cường giả, cùng Trương sư căn bản là hai loại sinh mệnh cấp độ, trao đổi độc dược... Trương sư coi như muốn hạ độc, cũng rất khó có tác dụng gì đi!”"),t("br"),t("br"),n._v("Vệ Nhiễm Tuyết cũng khí sắc mặt đỏ lên."),t("br"),t("br"),n._v("Độc dược uy lực, cùng người tu luyện cấp bậc cùng thực lực có quan hệ rất lớn, thật giống như một giọt nước có thể chết đuối con kiến, dùng tới đối phó voi nhưng không có nửa điểm tác dụng đồng dạng."),t("br"),t("br"),n._v("Thánh vực cường giả, sinh mệnh cấp độ phát sinh biến hóa, tuỳ tiện hạ độc chết Tằm Phong cảnh cao thủ dược vật, coi như ăn, cũng có thể là chuyện gì không có."),t("br"),t("br"),n._v("Mà đưa cho Kiều Thiên cảnh người ăn, liền hẳn phải chết không nghi ngờ."),t("br"),t("br"),n._v("Cấp độ khác biệt, thực lực chênh lệch dị, sức chống cự cũng sẽ khác biệt, làm sao công bằng?"),t("br"),t("br"),n._v("“Chung Đỉnh Thuần? Đây chính là các ngươi y sư học viện phó viện trưởng? Lục tinh thượng phẩm danh sư?” Triệu Bính Tuất vẻ mặt cũng biến thành xanh xám, vừa quay đầu tới."),t("br"),t("br"),n._v("Trước đó nghe được đối phương lật lọng, đã cảm thấy có sai lầm danh sư thân phận, có điều, dược liệu là của hắn, thực sự không muốn mua, ai cũng nói không nên lời cái gì."),t("br"),t("br"),n._v("Nhưng bây giờ, mới mở miệng ỷ vào Thánh vực cường giả thân phận, cùng người ta giao đấu độc dược, muốn đưa người vào chỗ chết, đã không phải là bán hay không sự tình, mà là nhân phẩm!"),t("br"),t("br"),n._v("Bởi vì thuộc về hai cái học viện, bình thường cùng vị này Vưu Hư tiếp xúc thời gian không nhiều, không biết vì người như thế nào, nhưng giờ phút này nghe được, cái tên này muốn mở miệng muốn đem một cái học sinh giết chết, thật sự là danh sư?"),t("br"),t("br"),n._v("Danh sư, cho dù có chút nhân phẩm xác thực không ra sao, nhưng cũng sẽ không làm như thế có sai lầm uy nghiêm sự tình đi!"),t("br"),t("br"),n._v("“Ta...” Chung Đỉnh Thuần vẻ mặt đỏ lên đối phương làm như vậy, mặc dù dựa theo quy tắc, không tính là trái lệ, nhưng tương đương đánh mất danh sư nên có phong độ."),t("br"),t("br"),n._v("Mất mặt ném đến nhà bà ngoại."),t("br"),t("br"),n._v("“Ngươi cũng không cần quái Chung viện trưởng, vị này Vưu Hư, ta trước kia tiếp xúc qua, cũng không phải là như thế. Nhưng mà hiện tại lớn tuổi, đối tử vong càng ngày càng e ngại, chỉ muốn có thể sống sót, cái gì tôn nghiêm, phong độ, đều không tính là gì!”"),t("br"),t("br"),n._v("Mi trưởng lão chen vào nói."),t("br"),t("br"),n._v("Vị này Vưu Hư, tuổi tác đã đến Thánh vực có thể sống cuối cùng, một cái bất cứ lúc nào cũng sẽ chết người, chỉ muốn có thể sống sót, làm sao có thể cùng ngươi giảng đạo lý?"),t("br"),t("br"),n._v("Còn sống mới là lớn nhất!"),t("br"),t("br"),n._v("“Có ít người, đối mặt tử vong, tình nguyện lựa chọn thanh danh, duy trì cả đời hình ảnh; Cũng có chút người, nhiều sống một ngày là một ngày, danh khí bất quá là sau lưng sự tình, chết liền cái gì cũng không biết!”"),t("br"),t("br"),n._v("Mi trưởng lão hừ một tiếng: “Cái tên này vì kéo dài tuổi thọ, khắp nơi săn giết linh thú, lấy ra máu của bọn nó, nể tình đồng nghiệp, lại bởi vì lão viện trưởng sự tình, quá mức bi thương, liền không đi tìm phiền toái, không nghĩ tới đến bây giờ cũng không biết hối cải!”"),t("br"),t("br"),n._v("“Săn giết linh thú?” Chung Đỉnh Thuần lần đầu tiên nghe nói, nhịn không được nhìn lại."),t("br"),t("br"),n._v("Thầy thuốc tấm lòng của cha mẹ, học y, không chỉ muốn chăm sóc người bị thương, cứu tính mạng người, ngay cả bị thương linh thú gặp gỡ, có thể cứu cũng đều sẽ ra tay."),t("br"),t("br"),n._v("Khắp nơi săn giết linh thú, chỉ vì mình sống sót... Đây thật là y sư gây nên?"),t("br"),t("br"),n._v("“Không sai, ta cũng là mấy ngày nay dò xét Tử Dương thú tiền bối tin tức, trong lúc vô tình biết được! Nghe nói gần đây trong một năm, các ngươi vị này Vưu Hư phó viện trưởng, ánh sáng Bán Thánh cấp bậc Thánh thú, liền săn giết hơn hai mươi đầu!”"),t("br"),t("br"),n._v("Mi trưởng lão nói."),t("br"),t("br"),n._v("“Hơn hai mươi đầu? Giết nhiều như vậy Bán Thánh làm gì?” Chung Đỉnh Thuần giật nảy mình."),t("br"),t("br"),n._v("Chuyện này, coi như hắn là y sư học viện viện trưởng, đều không biết."),t("br"),t("br"),n._v("“Ta cũng không biết!” Mi trưởng lão lắc đầu."),t("br"),t("br"),n._v("Nói thật, mới vừa biết tin tức này thời điểm, cũng rất kỳ quái, chỉ là giữa đồng nghiệp, chỉ cần không tạo thành lớn tổn thương, không tiện xem kỹ mà thôi."),t("br"),t("br"),n._v("“Bán Thánh Thánh thú, coi như tại Hồng Viễn sơn mạch, cũng có thể coi là được một phương Vương Giả, coi như hắn là Thánh giả, muốn săn giết, cũng không dễ dàng như vậy đi! Chừng một năm hơn hai mươi đầu...”"),t("br"),t("br"),n._v("Chung Đỉnh Thuần vẫn còn có chút không thể tin được."),t("br"),t("br"),n._v("Bán Thánh cấp bậc Thánh thú, đối chiến Tòng Thánh cấp bậc cường giả, đều không hề rơi xuống hạ phong một chút nào, coi như không bằng Chung Đỉnh Thuần, chạy trốn vẫn là có thể."),t("br"),t("br"),n._v("Làm sao có thể chừng một năm, săn giết nhiều như vậy?"),t("br"),t("br"),n._v("Lại nói, Hồng Viễn sơn mạch, muốn tìm nhiều như vậy Thánh thú, cũng rất khó đi!"),t("br"),t("br"),n._v("“Hình như cùng Vân Vụ lĩnh có quan hệ, có điều, hơn hai mươi đầu Bán Thánh Thánh thú tin tức, chuẩn xác không sai, ngươi không tin, có thể đợi bọn họ tỷ thí xong, tìm cái tên này đơn độc hỏi một chút!” Mi trưởng lão nói."),t("br"),t("br"),n._v("“Ừm!” Cố nén nghi ngờ trong lòng, Chung Đỉnh Thuần gật gật đầu."),t("br"),t("br"),n._v("Vân Vụ lĩnh nắm giữ toàn bộ Hồng Viễn sơn mạch tất cả Thánh thú, nếu như cùng cái này có quan hệ, tin tức hẳn là tám chín phần mười."),t("br"),t("br"),n._v("“Trước đừng quản những này, tình huống hiện tại, ngươi thân là viện trưởng, có thể hay không ngăn cản?” Gặp hai người còn có tâm tư đàm luận cái gì Thánh thú, Vệ Nhiễm Tuyết thực sự nhịn không được."),t("br"),t("br"),n._v("Việc cấp bách là hai người giao đấu, làm không cẩn thận sẽ chết người đấy."),t("br"),t("br"),n._v("“Y sư sinh tử đấu, là đi qua Y Sư công hội tổng bộ đồng ý, một khi bắt đầu, lại không cách nào ngừng! Chúng ta nhúng tay, rất dễ dàng gây nên mâu thuẫn...” Chung Đỉnh Thuần lắc đầu."),t("br"),t("br"),n._v("Không phải hắn không muốn quản, mà là không quản được!"),t("br"),t("br"),n._v("Y sư sinh tử đấu, thông qua huy chương trao đổi tổng bộ, lúc này ngăn cản, chẳng khác nào đang đối kháng với toàn bộ công hội, coi như hắn là thập đại danh sư một trong, cũng làm không được điểm ấy."),t("br"),t("br"),n._v("Quy củ, chính là quy củ, không thể phá."),t("br"),t("br"),n._v("Nếu không phải như thế, hắn có thể đợi được Vưu Hư ở phía trên diễu võ giương oai?"),t("br"),t("br"),n._v("Khẳng định đã sớm xông đi lên trước đánh một trận!"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-816"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-818"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);