(window.webpackJsonp=window.webpackJsonp||[]).push([[2099],{2453:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-811-nhieu-chuyen-con-lua"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-811-nhieu-chuyen-con-lua"}},[n._v("#")]),n._v(" Chương 811: Nhiều chuyện con lừa")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 811: Nhiều chuyện con lừa"),t("br"),t("br"),n._v("Làm lục tinh đỉnh phong y sư, đối thân thể khống chế đạt đến cực kỳ cao thâm cảnh giới, trong cơ thể bên trong không trúng độc, sao có thể không rõ ràng!"),t("br"),t("br"),n._v("Mới vừa lần này, là hắn biết, kịch độc đã xâm nhập tim phổi, đạt đến liền xem như hắn, đều không thể điều trị chỗ sâu."),t("br"),t("br"),n._v("Nói cách khác... Hắn trong lúc vô tình, đã trúng kịch độc!"),t("br"),t("br"),n._v("Chu Thiên lô đỉnh giải độc đại pháp không phải đem độc giải sao?"),t("br"),t("br"),n._v("Đây rốt cuộc chuyện gì xảy ra?"),t("br"),t("br"),n._v("“Không sai, ngươi căn bản không giải...” Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Không giải? Làm sao có thể!”"),t("br"),t("br"),n._v("Thân thể run rẩy, Vưu Hư vội vàng vận chuyển chân khí, muốn áp chế độc trong người tính, nhưng cảm thấy chút nào tác dụng đều không có: “Chẳng lẽ, nọc độc của ngươi không tại những cái kia Thanh Cam linh dịch bên trong? Ngươi... Giở trò lừa bịp!”"),t("br"),t("br"),n._v("Hắn đem uống vào nước tiểu tất cả đều tống ra, giọt nước không dư thừa, còn trúng độc, chẳng lẽ cái tên này dùng Thánh thú nước tiểu nhưng mà chướng nhãn pháp, kỳ thật chân chính kịch độc, tại mặt khác địa phương?"),t("br"),t("br"),n._v("“Giở trò lừa bịp?”"),t("br"),t("br"),n._v("Trương Huyền lạnh lùng nhìn qua, thanh âm mang theo chất vấn, nhắm thẳng vào đáy lòng: “Ta hướng ngươi cầu bán Thập Diệp hoa, thái độ thành khẩn, nói không có chút nào vi lễ chỗ. Ngươi không bán thì cũng thôi đi, nhưng dụ dỗ Ngụy Trường Phong đi Vân Vụ lĩnh thu thập Vân Vụ hoa, làm hại hắn gặp phải Thánh thú vây công chết thảm... Nhưng mà ta giở trò lừa bịp?”"),t("br"),t("br"),n._v("Vừa nói vừa đi về phía trước, trên người mang theo một cỗ khí tức, để cho người ta không dám cùng chi chính diện tiếp xúc."),t("br"),t("br"),n._v("“Để cho người ta đi hái Vân Vụ hoa? Cái này... Đây không phải để cho người ta chịu chết ư?”"),t("br"),t("br"),n._v("“Chẳng lẽ hai người mâu thuẫn là cái này?”"),t("br"),t("br"),n._v("“Ta trước đó còn tưởng rằng, vị này Trương Huyền khiêu chiến phó viện trưởng, chỉ là vì nổi danh, không nghĩ tới, là người sau vô lễ trước đây...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nghe được trên đài cuồn cuộn như là chuông vang thanh âm, phía dưới một hồi xôn xao."),t("br"),t("br"),n._v("Ở đây không ít người, đều biết Trương Huyền khiêu chiến Vưu phó viện trưởng, nhưng lại không biết nguyên nhân, nghe được giờ mới hiểu được tới."),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Nghe được dưới đài chỉ trích, cùng Ngụy Trường Phong chết ngữ, Vưu Hư trong lòng hoảng hốt, kìm lòng không được lui về phía sau một bước."),t("br"),t("br"),n._v("“Ta được đến Vân Vụ hoa, ngươi nhất định muốn sống mới được, tốt, ta đem Vân Vụ hoa cứu sống, ngươi lần nữa lật lọng... Nhưng mà ta giở trò lừa bịp?”"),t("br"),t("br"),n._v("[ truyen cua tui đốt Ｎet"),t("br"),n._v("]  Trương Huyền tiếp tục hướng phía trước, ánh mắt như điện."),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Vưu Hư thân thể phát run, không cách nào phản bác, lần nữa lui về phía sau."),t("br"),t("br"),n._v("“Ngươi nói Thập Diệp hoa, không thành thục, không thể bán ra, ta dùng phương pháp đặc thù, tiêu hao chân khí để hắn hoàn thiện, vẫn như cũ không bán, đưa danh sư tín dự với không để ý... Đây cũng là ta giở trò lừa bịp?”"),t("br"),t("br"),n._v("Nói như đao, Trương Huyền giống như là muốn đâm thủng đối phương linh hồn, thanh âm càng là tựa như chuông lớn, vang vọng toàn bộ đài cao."),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Liên tục nói ba cái “Ta”, Vưu Hư bờ môi run rẩy, muốn giải thích, nhưng giải thích không ra."),t("br"),t("br"),n._v("Đối phương nói một chút sai lầm đều không có, coi như muốn phản bác, cũng không thể nào cãi lại."),t("br"),t("br"),n._v("“Còn có loại sự tình này?”"),t("br"),t("br"),n._v("“Nguyên lai Trương sư tìm Vưu phó viện trưởng mua thuốc... Không bán còn cố ý làm khó, cái này có chút quá phận!”"),t("br"),t("br"),n._v("“Vốn là ta còn thật bội phục Vưu phó viện trưởng, không nghĩ tới là như vậy người, quả thực chính là y sư học viện bại hoại!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Đem Trương Huyền lời nói nghe vào trong tai, dưới đài một hồi xôn xao."),t("br"),t("br"),n._v("Trước đó, không ít lão sinh đều cảm thấy, Trương Huyền làm vì một cái tân sinh, khiêu chiến phó viện trưởng không biết trời cao đất rộng, coi thường tôn ti, nghe được thế mới biết, tất cả đều là người sau ép!"),t("br"),t("br"),n._v("Đường đường lục tinh danh sư, lật lọng, nói không giữ lời, đừng nói Trương sư, đổi lại bọn họ, cũng tuyệt đối sẽ lên cơn giận dữ, khó mà tha thứ."),t("br"),t("br"),n._v("“Ta bồi dưỡng ra tới dược vật, muốn bán liền bán, không muốn bán cũng không có gì! Chẳng lẽ cũng bởi vì không bán ra cho ngươi, liền muốn cõng lên tội danh?”"),t("br"),t("br"),n._v("Thấy dưới đài dư luận thiên về một bên, Vưu Hư ổn định bước chân, răng cắn chặt."),t("br"),t("br"),n._v("“Thập Diệp hoa ngươi thật sự muốn bán liền bán, cái này do ngươi, ta nói không nên lời cái gì! Bất quá... Y sư sinh tử đấu, ta muốn khiêu chiến liền khiêu chiến, ngươi làm gì được ta?”"),t("br"),t("br"),n._v("Trương Huyền khuôn mặt lạnh lùng."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Vưu Hư toàn thân cứng ngắc."),t("br"),t("br"),n._v("Tỷ thí giải độc, cũng không là đối thủ, tỷ thí cái khác, khẳng định càng không hi vọng!"),t("br"),t("br"),n._v("Nói cách khác... Y sư sinh tử đấu, hắn căn bản cũng không có thể có thể thắng lợi."),t("br"),t("br"),n._v("Lần này trước mặt nhiều người như vậy, coi như đối phương không dám giết hắn, cũng không chịu nổi tùy thời khiêu chiến ah!"),t("br"),t("br"),n._v("Khiêu chiến một lần lột một lần da, đến mấy lần trước, không chết cũng phải chết."),t("br"),t("br"),n._v("“Lần này... Ta nhận thua! Ngươi nói làm sao bồi thường, ta bồi thường chính là, cùng lắm thì gốc kia Thập Diệp hoa cho ngươi.” Cắn răng một cái, Vưu Hư nói."),t("br"),t("br"),n._v("“Bồi thường?” Trương Huyền hướng về phía trước: “Ngươi bồi thường nổi Ngụy Trường Phong mạng ư?”"),t("br"),t("br"),n._v("Vưu Hư lui về phía sau: “Vậy ngươi muốn như thế nào?”"),t("br"),t("br"),n._v("“Rất đơn giản... Vì Ngụy Trường Phong đền mạng!”"),t("br"),t("br"),n._v("Trương Huyền lạnh lùng nhìn qua."),t("br"),t("br"),n._v("Ngụy Trường Phong bị giết, tất cả đều bởi vì cái tên này, cho nên... Hắn chỉ có một cái ý nghĩ —— giết người thì đền mạng!"),t("br"),n._v(" Hắn vì nữ nhi, vì thân vì phụ thân chấp niệm, bị Thánh thú đánh chết tươi, mà ngươi cái này kẻ cầm đầu, lại làm sao có thể sống trên đời!"),t("br"),t("br"),n._v("“Đền mạng?” Vưu Hư vẻ mặt dữ tợn: “Ta đường đường y sư học viện phó viện trưởng, lục tinh thượng phẩm danh sư, vì hắn một tiểu nhân vật đền mạng? Dựa vào cái gì!”"),t("br"),t("br"),n._v("“Dựa vào cái gì? Ha ha!” Trương Huyền cười to lên: “Vậy ta sẽ nói cho ngươi biết, dựa vào cái gì!”"),t("br"),t("br"),n._v("“Chỉ bằng ta, một mình giải quyết nghi vấn khó xử lý tường năm trăm linh ba đạo nghi vấn khó xử lý tạp chứng!”"),t("br"),t("br"),n._v("“Chỉ bằng ta, chứng bệnh đài lưu lại bốn trăm ba mươi hai loại chẩn bệnh phương pháp!”"),t("br"),t("br"),n._v("“Chỉ bằng ta, một lần nữa cho ra Tiên Bối lâm rất nhiều chứng bệnh, tân trị liệu phương án, để rất nhiều truyền thế chứng bệnh, lại càng dễ giải quyết!”"),t("br"),t("br"),n._v("“Chỉ bằng ta, xung kích lục tinh y sư, không hề sai lầm, lưu lại giải quyết bảy mươi ba loại bệnh chứng chính xác nhất đơn thuốc!”"),t("br"),t("br"),n._v("“Ta mặc dù hủy y sư tháp, nhưng nhưng lưu lại càng nhiều điều trị tật bệnh phương thuốc, để tổng bộ nguyện ý vì ta mở ra y sư sinh tử đấu, ngươi thế mà hỏi ta dựa vào cái gì?”"),t("br"),t("br"),n._v("“Ta bằng chính là y sư thân phận, đường đường chính chính; Bằng là sinh tử đấu, ta thắng ngươi!”"),t("br"),t("br"),n._v("Mỗi nói một câu, Trương Huyền liền tiến về phía trước một bước, âm thanh lớn như là hồng lưu, phụ trợ hắn tựa như trên chín tầng trời tới thần chỉ, không thể coi thường cùng vũ nhục."),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Liên tiếp lui về phía sau, Vưu Hư dưới chân đột nhiên mềm nhũn, lúc này mới phát hiện đã thối lui đến bên trên đài cao."),t("br"),t("br"),n._v("Không sai, đối phương không có đùa nghịch bất kỳ thủ đoạn nào, dựa vào là thực lực, dựa vào là sinh tử đấu, là đường đường chính chính tỷ thí, hắn thua không nhận lời nói, phản cũng không có bất kỳ cái gì lý do cùng lấy cớ."),t("br"),t("br"),n._v("“Tự sát đi!”"),t("br"),t("br"),n._v("Thấy đối phương vẻ mặt trắng bệch, lời nói đều nói không nên lời, biết lòng tự tin đã bị triệt để tiêu diệt, Trương Huyền bàn tay lớn vẫy một cái."),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Thân thể run lên, Vưu Hư mặt xám như tro, trong nháy mắt cảm giác đến nhân sinh của mình, cực kỳ thất bại, cùng hắn còn sống, còn không bằng chết càng tốt hơn."),t("br"),t("br"),n._v("Bàn tay giơ lên, không chần chờ chút nào, thẳng tắp đối đầu của mình đánh ra."),t("br"),t("br"),n._v("“Dừng tay!”"),t("br"),t("br"),n._v("Ngay tại bàn tay sắp đập vào trên đầu thời điểm, bầu trời đột nhiên vang lên hét lớn một tiếng, một bóng người từ không trung thẳng tắp rơi xuống."),t("br"),t("br"),n._v("Nương theo tiếng quát, nguyên bản một mặt ảm nhiên Vưu Hư bỗng nhiên giật cả mình, lập tức nhìn về phía Trương Huyền, con mắt thấu đỏ."),t("br"),t("br"),n._v("“Ngươi cũng dám đối ta thi triển sư ngôn thiên bẩm? Ta muốn giết ngươi...”"),t("br"),t("br"),n._v("Giờ phút này, ngu nữa cũng biết, đối phương vừa rồi thi triển sư ngôn thiên bẩm, mượn nhờ thiên địa áp lực, để hắn thất thần, thân bất do kỷ, nếu là bóng người tiếng la ban đêm nửa bước, chỉ sợ đã tự sát thân vong."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Toàn thân khẽ động, Vưu Hư hướng Trương Huyền vọt tới, lực lượng cuồng bạo bắn tung toé mà ra, mang theo vô địch nghiền ép."),t("br"),t("br"),n._v("Thánh vực nhất trọng đỉnh phong khí tức toàn bộ phóng thích, toàn bộ đài cao tựa hồ cũng có chút không chịu nổi, sắp sụp đổ."),t("br"),t("br"),n._v("Mặt không đổi sắc, Trương Huyền hai tay chắp sau lưng, ngạo nghễ đứng ở đài cao, tự mang một cỗ khí tức, tựa hồ đối với công kích của hắn, chẳng thèm ngó tới."),t("br"),t("br"),n._v("“Được rồi, Vưu viện trưởng!”"),t("br"),t("br"),n._v("Liền tại công kích sắp rơi vào Trương Huyền trên người thời điểm, vừa rồi hét lớn vang lên lần nữa, bóng người vung tay lên, Vưu Hư đầy trời công kích lập tức tan thành mây khói."),t("br"),t("br"),n._v("Thấy công kích biến mất, Trương Huyền than thở một tiếng, thầm nghĩ đáng tiếc."),t("br"),t("br"),n._v("Y sư sinh tử đấu, nếu là tổng bộ đồng ý, tự nhiên chịu đến tổng bộ giám sát cùng trừng trị, vừa rồi Vưu Hư một chưởng kia, thực có can đảm rơi xuống, đoán chừng đã bị tổng bộ cách không gạt bỏ!"),t("br"),t("br"),n._v("Đáng tiếc, bị người ngăn cản."),t("br"),t("br"),n._v("Quay đầu hướng bóng người nhìn sang, là cái lão giả râu bạc trắng, người mặc danh sư phục, lông mi mang theo uy nghiêm, vừa nhìn liền biết bất phàm."),t("br"),t("br"),n._v("“Ngươi chính là Trương Huyền?” Ông lão nhàn nhạt nhìn qua, mang theo một cỗ cao cao tại thượng khí thế."),t("br"),t("br"),n._v("Nhìn thấy cái tên này ngăn cản bản thân chém giết Vưu Hư, còn ở nơi này diễu võ giương oai, Trương Huyền cau mày."),t("br"),t("br"),n._v("“Tại hạ là luyện đan học viện viện trưởng, Lục Phong!”"),t("br"),t("br"),n._v("Tự giới thiệu mình một chút, lục Phong viện trưởng hai mắt mang theo áp bách, cho người ta không được cãi lại lực lượng: “Vưu Hư, bất kể nói thế nào đều là học viện lão sư, đường đường phó viện trưởng, coi như chợt có sai lầm, cũng tội không đáng chết. Như vậy đi, ta để hắn đem Thập Diệp hoa bồi thường cho ngươi, lại bồi thường ngươi một ít linh thạch, chuyện này như vậy coi như không có gì!”"),t("br"),t("br"),n._v("“Coi như không có gì?”"),t("br"),t("br"),n._v("Trương Huyền sầm mặt lại."),t("br"),t("br"),n._v("Nếu như không phải lão gia hỏa này ngăn cản, cái này Vưu Hư, khẳng định đã chết!"),t("br"),t("br"),n._v("Thập Diệp hoa cùng linh thạch... Hai thứ đồ này, có thể đổi lấy Ngụy Trường Phong mạng?"),t("br"),t("br"),n._v("“Không sai, học viện có quy củ của học viện, ngươi thân là học sinh, khiêu chiến lão sư, đã vi phạm quy tắc, vi phạm với thầy trò ở giữa lễ nghi. Chỉ cần hôm nay chuyện này cứ tính như thế, học viện cũng sẽ đối ngươi theo nhẹ xử phạt...”"),t("br"),t("br"),n._v("Bàn tay lớn vẫy một cái, lục Phong viện trưởng trong giọng nói mang theo nhân từ."),t("br"),t("br"),n._v("“Theo nhẹ xử phạt?” Trương Huyền ánh mắt lạnh lùng: “Nếu như ta không cần bồi thường, chỉ lấy mạng của hắn đâu?”"),t("br"),t("br"),n._v("“Muốn mạng? Hừ, nơi này là danh sư học viện, còn không cho phép ngươi một cái học sinh muốn làm gì thì làm?”"),t("br"),t("br"),n._v("Thấy mình tự mình ra mặt, một cái tân sinh, không sợ cảm động đến rơi nước mắt ngược lại cũng thôi, thế mà còn dám trước mặt mọi người đụng chạm, lục Phong viện trưởng sắc mặt tái xanh."),t("br"),t("br"),n._v("“Lại nói, Vưu Hư, cũng không chỉ là y sư, càng là Danh Sư đường đăng kí lục tinh thượng phẩm danh sư, xử trí như thế nào, cần Danh Sư đường cùng học viện cùng thương nghị, còn chưa tới phiên ngươi một cái tứ tinh tiểu nhân vật, ở đây khoa tay múa chân!”"),t("br"),t("br"),n._v("“Ngươi là học viện viện trưởng?” Trương Huyền híp mắt lại."),t("br"),t("br"),n._v("“Không phải!”"),t("br"),t("br"),n._v("Không nghĩ tới hắn lại như vậy hỏi, Lục Phong sững sờ, lập tức nói: “Bất quá, lão viện trưởng mất tích, luyện đan học viện lại là đệ nhất đại viện, danh sư học viện chư nhiều chuyện, đều do ta quyết định, xem như tạm thay viện trưởng chức. Yên tâm, ta làm ra quyết định, giữ lời nói, coi như Vưu Hư, cũng không dám phản bác!”"),t("br"),t("br"),n._v("“Đã ngươi nha không phải viện trưởng, ở đây cắm vào nói cái gì? Bờ sông có cỏ xanh, không cần nhiều chuyện con lừa! Cho ta cút sang một bên.”"),t("br"),t("br"),n._v("Nghe nói đối phương không phải viện trưởng, Trương Huyền lông mày giương lên, hất lên ống tay áo."),t("br"),t("br"),n._v("“Nếu không, tin hay không, ta hiện tại liền đi khiêu chiến ngươi luyện đan học viện?”"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-819"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-821"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);