(window.webpackJsonp=window.webpackJsonp||[]).push([[21],{375:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-995-hong-vien-đoc-đien"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-995-hong-vien-đoc-đien"}},[n._v("#")]),n._v(" Chương 995: Hồng Viễn Độc điện")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 995: Hồng Viễn Độc điện"),t("br"),t("br"),n._v("Hòn đảo đường kính chừng hơn trăm cây số, từ xa nhìn lại, xanh mơn mởn, tràn đầy đủ loại cao to cây cối, trận pháp bao phủ xuống, linh khí nồng đậm, thỉnh thoảng có lợi hại Thánh thú ở trên trời bay lượn."),t("br"),t("br"),n._v("“Hơn trăm cây số hòn đảo, có thể toàn bộ che giấu, bố trí đại trận này người, thực lực tuyệt sẽ không thấp hơn thất tinh!”"),t("br"),t("br"),n._v("Trong mắt tràn đầy khiếp sợ, Trương Huyền nhịn không được nói."),t("br"),t("br"),n._v("Hắn hiện tại là lục tinh đỉnh phong trận pháp sư, muốn bố trí ra to lớn như vậy trận pháp đều rất khó làm được, đem Độc điện xây dựng ở nơi này vị tiền bối kia cái kia mạnh bao nhiêu?"),t("br"),t("br"),n._v("“Hồng Viễn Độc điện khai phái tông sư, không chỉ có là một vị thất tinh độc sư, càng là một vị thất tinh đỉnh phong cấp bậc trận pháp đại tông sư, cho dù là hắn, để bố trí cái này bí ẩn đại trận, cũng là hao tốn ròng rã hơn 50 năm!”"),t("br"),t("br"),n._v("Nhìn ra hắn khiếp sợ, Hứa trưởng lão cười nói."),t("br"),t("br"),n._v("“50 năm?” Trương Huyền khóe miệng giật một cái."),t("br"),t("br"),n._v("Theo xuyên qua đến bây giờ, tính toán đâu ra đấy cũng không đến thời gian một năm, 50 năm thời gian, chỉ vì một cái trận pháp, đánh chết cũng sẽ không đi làm."),t("br"),t("br"),n._v("“Đúng vậy a, trận pháp này lấy lòng đất núi lửa làm lực lượng nguồn suối, làm cho cả hòn đảo, núp ở bát ngát mặt biển bên trong, nếu như tìm không thấy cửa vào, coi như từ phía trên bay qua, cũng chỉ thấy một vùng biển mênh mông.”"),t("br"),t("br"),n._v("Hứa lớn tràn đầy tự hào: “Lúc trước có không ít thất tinh danh sư, thậm chí trận pháp sư đi ngang qua nơi đây, đều không có phát hiện! Nguyên nhân chính là như vậy, Độc điện mới giữ vững truyền thừa vạn năm, không bị đến một lần công kích.”"),t("br"),t("br"),n._v("“Lợi hại!” Trương Huyền nhẹ gật đầu, trong mắt hoa văn nhúc nhích, hướng trước mắt đại trận nhìn sang."),t("br"),t("br"),n._v("Không thể không nói đối phương thiết kế rất tốt, mượn nhờ hòn đảo được trời ưu ái ưu thế, cùng chung quanh nước biển môi trường, đem che giấu trận pháp, kết hợp hoàn mỹ. Coi như Minh Lý Chi Nhãn, cũng chỉ là nhìn thấy một thứ đại khái, trong thời gian ngắn, không cách nào phân tích ra phương pháp phá giải."),t("br"),t("br"),n._v("Thật muốn phá giải lời nói, khả năng đại khái cần nghiên cứu... Ít nhất hơn mười phút!"),t("br"),t("br"),n._v("Một cái trận pháp có thể để cho hắn nghiên cứu thời gian dài như vậy, đã coi như là cực kỳ nghịch thiên."),t("br"),t("br"),n._v("“Đi xuống đi!”"),t("br"),t("br"),n._v("Nhìn thấy trên mặt hắn khiếp sợ, Hứa trưởng lão cười cười, điều khiển lấy phi hành Thánh thú chậm rãi hướng trước mắt hòn đảo bay đi."),t("br"),t("br"),n._v("Sau mười mấy phút, tại một cái cực lớn trên đài cao rơi xuống."),t("br"),t("br"),n._v("“Hứa trưởng lão!” Mấy cái thanh niên tiến lên đón, nhìn thấy theo trong phòng đi ra không phải Tiết trưởng lão, mà là một cái chưa từng thấy qua trung niên nhân, tất cả đều tràn đầy kỳ quái."),t("br"),t("br"),n._v("“Vị này Tôn độc sư là ân nhân cứu mạng của ta, các ngươi trước an bài ở lại.”"),t("br"),t("br"),n._v("Hứa trưởng lão quay đầu khai báo: “Ta hiện tại muốn đi gặp điện chủ!”"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Nghe được là Hứa trưởng lão ân nhân cứu mạng, mấy cái thanh niên lần nữa nhìn về phía Trương Huyền, lộ ra vẻ kính sợ, đồng loạt gật đầu đáp ứng."),t("br"),t("br"),n._v("“Tôn độc sư, ngươi trước cùng bọn hắn tìm địa phương ở lại, ta đi trước tìm điện chủ, đem Tĩnh Viễn thành chuyện nói rõ chi tiết một chút.”"),t("br"),t("br"),n._v("Đi về phía trước hai bước, Hứa trưởng lão không dừng được, cho Trương Huyền truyền âm: “Độc điện người khả năng tính khí đều có chút không tốt, có điều, tất cả mọi người bái phục thực lực mạnh, nếu như đến chỗ ở, có người tìm phiền toái, có thể không cần lưu tình.”"),t("br"),t("br"),n._v("“Ừm!” Trương Huyền nhẹ gật đầu."),t("br"),t("br"),n._v("Một trưởng lão bị giết chết, quả thực cái kia hướng điện chủ báo cáo."),t("br"),t("br"),n._v("Còn có người muốn tìm phiền toái lời nói, hắn cũng không có ý định làm thật là thành thật người."),t("br"),t("br"),n._v("“Ta sẽ hướng điện chủ bẩm báo chuyện của ngươi, sau đó giúp ngươi xin khảo hạch độc sư danh ngạch!”"),t("br"),t("br"),n._v("Nói một câu, Hứa trưởng lão vội vã đi thẳng về phía trước, chỉ chốc lát liền biến mất ở trước mắt."),t("br"),t("br"),n._v("“Tôn độc sư, mời tới bên này!”"),t("br"),t("br"),n._v("Thấy hắn rời khỏi, mấy cái thanh niên, đi tới trước mặt."),t("br"),t("br"),n._v("Cũng không nhiều lời, đi theo mấy người sau lưng, Trương Huyền sải bước đi đi qua."),t("br"),t("br"),n._v("Rời khỏi đài cao không xa, là một cái cực lớn thành trấn, đủ loại kiến trúc san sát, thoạt nhìn có chút cũ kỹ, duy trì vài ngàn năm trước hình thái."),t("br"),t("br"),n._v("Trên đường phố người đến người đi, làm cái gì cũng có, tựa như một cái thành phố khổng lồ."),t("br"),t("br"),n._v("Đi lại trong đó, ngẫu nhiên có thể nhìn thấy ven đường trong phòng bay ra nhàn nhạt sương mù, mùi không sai, nhưng mà ngửi bên trên một cái, liền sẽ để người cảm thấy đầu mê muội, u ám buồn ngủ."),t("br"),t("br"),n._v("Không cần nhìn liền biết, bên trong tất nhiên ẩn chứa kịch độc."),t("br"),t("br"),n._v("Không chỉ có những này, ven đường thỉnh thoảng bò qua tới một cái cái độc trùng, rắn độc, màu sắc tươi đẹp, sắc thái sặc sỡ, nhìn chằm chằm người đi đường, tựa như lúc nào cũng sẽ phát động công kích, nếu như không phải độc sư, lần thứ nhất nhìn thấy những này, cũng có thể tại chỗ dọa ngất đi."),t("br"),t("br"),n._v("“Yên tâm đi, những này độc trùng, sẽ không dễ dàng cắn người...”"),t("br"),t("br"),n._v("Nhìn ra sự lo lắng của hắn, một thanh niên cười giải thích, chẳng qua lời còn chưa dứt, liền nghe đến phía trước một tiếng vù vù, một người trung niên nhảy hướng về phía trước chạy tới, mà hắn trên mông mang theo một con rắn độc, sắc nhọn răng cắn vào thịt bên trong, bất luận thế nào đều không buông ra."),t("br"),t("br"),n._v("“Ta thao ngươi bà ngoại, đây là ai rắn? Lại không ra, ta hôm nay lấy về lột da nấu!”"),t("br"),t("br"),n._v("“Dám ăn của ta rắn, có tin ta hay không hiện tại liền hạ độc chết ngươi...”"),t("br"),t("br"),n._v("Ngay sau đó, lại gào lên một tiếng vang lên, một bóng người lao ra, cùng vừa rồi cái kia bị rắn cắn gia hỏa, đánh lên."),t("br"),t("br"),n._v("“Cái này, cái này... Những này độc trùng bình thường vẫn là đều rất hữu nghị...”"),t("br"),t("br"),n._v("Mới vừa nói xong không cắn người, liền xuất hiện một màn này, thanh niên mang theo xấu hổ, vội vàng giải thích: “Yên tâm đi, mặc dù độc trùng không quá đáng tin, chúng ta độc sư đồng dạng vẫn là lẫn nhau kính trọng, quê nhà hòa thuận, sẽ không dễ dàng ra tay...”"),t("br"),t("br"),n._v("“Ừm!” Nhẹ gật đầu, Trương Huyền còn không có tiếp tục hướng phía trước, liền nghe đến trong đường phố càng thêm lanh lảnh tiếng gầm gừ vang lên."),t("br"),t("br"),n._v("“Mẹ cái gà, là ai tại lão nương trên gối đầu hạ độc? Tốt nhất đừng để ta điều tra ra, nếu không ta sẽ giết hắn không thể!”"),t("br"),t("br"),n._v("Tiếng nói kết thúc, liền từ trong phòng thoát ra một cái bánh bao mặt phu nhân."),t("br"),t("br"),n._v("Cái này nhân thân vật liệu thoạt nhìn cũng không tệ lắm, nhưng mà mặt nhưng sưng tấy cùng dưa hấu đồng dạng lớn nhỏ, thậm chí không ít chỗ cũng bắt đầu chảy mủ, dữ tợn cùng mới từ trong mộ địa bò ra tới cương thi đồng dạng."),t("br"),t("br"),n._v("“Ai tại ta món canh bên trong hạ độc?”"),t("br"),n._v("“Dám ở lão tử giày bên trong phóng độc trùng, chán sống rồi hay sao?”"),t("br"),t("br"),n._v("“Dám hướng ta trong lỗ mũi bỏ vào thuốc, có tin ta hay không đem móng vuốt của ngươi chặt?”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Liên tiếp thanh âm liên miên không ngớt, lập tức nhìn thấy nguyên một đám bị độc hình thù kỳ quái cái tên này, từ trong phòng lao ra."),t("br"),t("br"),n._v("Mới vừa nói xong quê nhà hòa thuận liền gây ra động tĩnh lớn như vậy, thanh niên trên mặt có chút xấu hổ."),t("br"),t("br"),n._v("“Khụ khụ, bọn họ đây đều là nói đùa, càng nhiều hơn chính là thí nghiệm bản thân vừa mới chế biến ra thuốc, không sao, không sao...”"),t("br"),t("br"),n._v("“Không sao? Vậy bọn hắn là...”"),t("br"),t("br"),n._v("Trương Huyền mặt mũi nghi ngờ chỉ về phía trước, lập tức mọi người liền thấy hai vị độc sư đánh hừng hực khí thế, hận không thể đem đối phương xé nát ăn đi."),t("br"),t("br"),n._v("“...” Thanh niên."),t("br"),t("br"),n._v("Đi dọc theo đường phố trong chốc lát, Trương Huyền liền thấy rõ."),t("br"),t("br"),n._v("Độc sư không có có danh sư đường quy củ nhiều như vậy, chỉ cần chế biến ra mới độc dược, cơ bản đều sẽ cầm người bên cạnh làm thí nghiệm, có đôi khi hôm nay nhìn thấy người này là một bộ dáng, ngày mai nhìn thấy liền sẽ mặt khác một bộ dáng, thậm chí sẽ bị độc giới tính đều thay đổi."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, ở chỗ này, ngươi có thể sẽ phát phát hiện mình hàng xóm hôm nay là cái lão đầu, ngày mai là cái trung niên người, ngày mốt liền biến thành nũng nịu tiểu nương tử, càng có khả năng qua mấy ngày, biến thành toàn thân u lớn quái thú..."),t("br"),t("br"),n._v("Đương nhiên, nếu như hỏi họ tên lời nói, có thể là cùng một cái, chẳng qua là dùng thuốc khác biệt, bị độc đến thay đổi hình dạng mà thôi."),t("br"),t("br"),n._v("“May mắn đều là độc sư, nếu là chạy đến Hồng Viễn thành làm như vậy, đoán chừng không bao lâu, liền sẽ bị người đánh chết tươi...”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu, mặt mũi bất đắc dĩ."),t("br"),t("br"),n._v("Cũng chính là ở đây mới làm như vậy, Hồng Viễn thành có danh sư đường, có danh sư học viện, loại này chưa đồng ý liền lung tung hạ độc hành vi, là vi phạm quy củ, một khi phát hiện, có mười cái mạng cũng không đủ giết."),t("br"),t("br"),n._v("Hỗn loạn như thế, Hứa trưởng lão thế mà còn nói khoác không biết ngượng mà nói, đều rất hữu nghị... Ngày ngày bị độc hoàn toàn thay đổi, hôm nay ngủ, cũng không biết ngày mai tỉnh lại sẽ bị ai hạ độc, ngày ngày nơm nớp lo sợ... Cái này cũng kêu hữu nghị?"),t("br"),t("br"),n._v("“Được rồi, Tôn độc sư, ngươi trước hết ở chỗ này đi!”"),t("br"),t("br"),n._v("Đi trong chốc lát, nhìn thấy vô số kỳ kỳ quái quái gia hỏa, thanh niên ngừng lại."),t("br"),t("br"),n._v("Trương Huyền nhìn lại, là một sạch sẽ sân nhỏ, mặc dù kiến trúc thoạt nhìn có chút cũ cũ, nhưng mà bốn phía không có trước đó như vậy huyên náo, mười phần yên tĩnh."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Dù sao tại đây dạo chơi một thời gian khẳng định không dài, đối chỗ ở cũng không có gì có thể kén chọn."),t("br"),t("br"),n._v("Đang muốn đẩy cửa đi vào sân nhỏ, chỉ thấy mấy cái thanh niên đi tới."),t("br"),t("br"),n._v("“Lý Viên, vị này lạ mặt ah, hình như trước đến giờ chưa thấy qua?”"),t("br"),t("br"),n._v("“Đây là Hứa trưởng lão mới vừa từ bên ngoài mang tới.”"),t("br"),t("br"),n._v("Đem Trương Huyền đưa tới thanh niên, Lý Viên nói."),t("br"),t("br"),n._v("“Hứa trưởng lão mang tới, chẳng lẽ muốn thu vì đệ tử? Có thể để cho cái này lão ngoan cố động tâm, thiên phú nhất định không sai, đến, để ta xem một chút có phải hay không có bản lãnh này...”"),t("br"),t("br"),n._v("Người thanh niên này nhẹ nhàng cười một tiếng, ngón tay búng một cái, một làn khói trần, trực tiếp giống như Trương Huyền lao đến."),t("br"),t("br"),n._v("“Lưu Húc, đừng làm ẩu, vị này Tôn độc sư cũng không phải là Hứa trưởng lão đệ tử...”"),t("br"),t("br"),n._v("Lý Viên giải thích một tiếng, có điều, lời nói còn chưa nói xong, người sau khói mù liền đã đi tới Trương Huyền trước mặt, muốn ngăn cản cũng ngăn không được."),t("br"),t("br"),n._v("Đang muốn nhắc nhở một chút, để hắn cẩn thận, chỉ thấy cái này bề ngoài xấu xí trung niên nhân, nhẹ nhàng cười một tiếng, huyễn ảnh, xuất hiện tại Lưu Húc trước mặt."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Một tiếng khung xương bị đánh nát tiếng âm vang lên, Lưu Húc liền hô cũng không kịp, liền chạy đến bay ra ngoài, trực tiếp treo ở cách đó không xa trên chạc cây, hôn mê bất tỉnh."),t("br"),t("br"),n._v("“Ah...”"),t("br"),t("br"),n._v("Mọi người tất cả đều ngẩn ngơ."),t("br"),t("br"),n._v("Tất cả mọi người là độc sư, dựa theo tình huống bình thường, đối phương dùng độc đánh lén, ngươi có lẽ dùng tương đồng phương thức tiến hành đánh trả, kết quả cái tên này không nói hai lời, một quyền đem người đánh bay... Có phần quá bạo lực đi!"),t("br"),t("br"),n._v("“Ngươi... Thân là độc sư, thế mà dùng loại này man lực giải quyết chuyện, quả thực có nhục lịch sự!”"),t("br"),t("br"),n._v("Lưu Húc sau lưng một thanh niên đi tới, bàn tay lớn vẫy một cái, nghĩa chính ngôn từ nhìn qua: “Ta và ngươi so là dùng độc, ngươi có dám tiếp...”"),t("br"),t("br"),n._v("Lời còn chưa dứt, liền thấy một cái bàn chân trực tiếp đối lấy khuôn mặt đạp tới."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Cùng Lưu Húc đồng dạng vù vù cũng không kịp, bay rớt ra ngoài, treo ở cách đó không xa trên chạc cây."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Còn lại mấy cái thanh niên tất cả đều sắc mặt đại biến, rõ ràng đã khiêu chiến hắn dùng độc, cái tên này thế mà còn không theo lẽ thường ra bài."),t("br"),t("br"),n._v("“Cùng một chỗ động thủ...” Trong đó một vị hô lên."),t("br"),t("br"),n._v("Bành bành bành bành!"),t("br"),t("br"),n._v("Mấy hơi thở về sau, vừa rồi khí thế hùng hổ tới mấy cái thanh niên, tất cả đều treo ở trên cây."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Đưa Trương Huyền tới Lý Viên đám người, trợn mắt hốc mồm."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1003"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1005"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);