(window.webpackJsonp=window.webpackJsonp||[]).push([[2100],{2454:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-812-boi-phan"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-812-boi-phan"}},[n._v("#")]),n._v(" Chương 812: Bối phận")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 812: Bối phận"),t("br"),t("br"),n._v("“Cái gì?”"),t("br"),t("br"),n._v("“Nói Lục Phong viện trưởng là nhiều chuyện con lừa?”"),t("br"),t("br"),n._v("“Còn muốn khiêu chiến luyện đan học viện?”"),t("br"),t("br"),n._v("Nghe được trên đài hai người cãi lộn, phía dưới tất cả mọi người điên rồi."),t("br"),t("br"),n._v("Lão viện trưởng sau khi mất tích, danh sư học viện chuyện lớn chuyện nhỏ, cơ bản đều do vị này Lục Phong viện trưởng quyết sách, mặc dù không phải trên danh nghĩa viện trưởng, nhưng cũng không xê xích gì nhiều."),t("br"),t("br"),n._v("Lại thêm, hắn vẫn là thập đại trường lão đứng đầu, Mi trưởng lão bọn người lấy hắn như Thiên Lôi sai đâu đánh đó, tại học viện nắm giữ địa vị cực cao... Một cái tân sinh, trước mặt mọi người mắng hắn là nhiều chuyện con lừa, còn bảo hắn cút sang một bên, quả thực chính là coi trời bằng vung."),t("br"),t("br"),n._v("“Trương sư làm sao như thế lỗ mãng?”"),t("br"),t("br"),n._v("Vệ Nhiễm Tuyết, Triệu Bính Tuất đám người tất cả đều nhoáng một cái."),t("br"),t("br"),n._v("Vốn nghĩ Lục Phong viện trưởng ra mặt, rất nhanh liền có thể giải quyết, không nghĩ tới vị này Trương sư như thế cương liệt, ngạnh kháng lên."),t("br"),t("br"),n._v("Lục Phong viện trưởng, luôn luôn cương trực công chính, xem quy củ như Thái Sơn, ở trước mặt nhục mạ, không khác không nể mặt mũi, không nổi điên mới là lạ."),t("br"),t("br"),n._v("Quả nhiên, lời còn chưa dứt, chỉ thấy vị này Lục viện trưởng sắc mặt trầm xuống, híp mắt lại, thanh âm như là sấm rền."),t("br"),t("br"),n._v("“Ngươi nói cái gì?”"),t("br"),t("br"),n._v("“Ngươi điếc, vẫn là mù, nghe không được cuối cùng cũng có thể nhìn thấy đi! Ta cùng Vưu Hư, tiến hành sinh tử đấu, đều lấy lục tinh y sư thân phận, xem như ngang hàng, không có thầy trò phân chia... Cho nên, cũng không cần cái gì thầy trò đại nghĩa tới dọa ta.”"),t("br"),t("br"),n._v("“Mặt khác, chúng ta còn không có phân biệt thắng bại, ngươi liền xông lại làm loạn, cưỡng ép ngăn cản... Cùng ta giảng quy củ, ngươi đây là tuân thủ quy củ?”"),t("br"),t("br"),n._v("Ánh mắt không sợ chút nào, Trương Huyền cười lạnh."),t("br"),t("br"),n._v("Hắn là học sinh, dựa theo quy củ đụng chạm đối phương quả thực phạm vào tối kỵ, nhưng hắn cùng Vưu Hư lấy lục tinh y sư thân phận giao đấu, thuộc về ngang hàng tỷ thí."),t("br"),t("br"),n._v("Nói cách khác, chỉ muốn tỷ thí không kết thúc, thân phận của hắn bây giờ cũng không phải là học sinh, mà là lục tinh y sư."),t("br"),t("br"),n._v("Đối phương liền xem như lục tinh danh sư, cũng đồng dạng ngang hàng mà thôi."),t("br"),t("br"),n._v("Có tư cách gì ngăn cản?"),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Khuôn mặt xanh xám, Lục Phong tức giận bờ môi run rẩy: “Nhanh mồm nhanh miệng, mặc kệ ngươi hôm nay nói thế nào, vị này Vưu Hư ta chắc chắn bảo vệ, chuyện này, ta sẽ đích thân dâng thư Danh Sư đường, cùng Y Sư công hội hoà giải!”"),t("br"),t("br"),n._v("“Ngươi muốn bảo vệ hắn?” Trương Huyền híp mắt lại."),t("br"),t("br"),n._v("Mặc dù có chút kỳ quái, cái tên này tốn hao như thế đánh đổi bảo vệ Vưu Hư, nhưng tỉ mỉ nghĩ lại cũng là hiểu được."),t("br"),t("br"),n._v("Lúc trước cùng Ngụy Trường Phong mua sắm dược vật thời điểm, tận mắt thấy vị này Lục Phong viện trưởng, phái người đến mua dược."),t("br"),t("br"),n._v("Giá cả tiện nghi không nói, giao dịch cũng mười phần thuận lợi."),t("br"),t("br"),n._v("Một cái luyện đan một cái dưỡng dược, hai người tất nhiên thường xuyên câu thông, giao lưu, quan hệ không tệ, nếu không, cũng không có khả năng tình nguyện đắc tội Y Sư công hội tổng bộ, cũng muốn đánh gãy sinh tử đấu."),t("br"),t("br"),n._v("“Không sai!”"),t("br"),t("br"),n._v("Hừ một tiếng, Lục Phong hơi vung tay cánh tay, mang theo vô địch uy thế: “Vưu viện trưởng, theo ta đi! Ta ngược lại muốn xem xem, ở tên này sư học viện, ai dám ngăn cản ta!”"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Thấy Lục Phong viện trưởng tự mình ra mặt, thái độ cường ngạnh, Vưu Hư thở phào nhẹ nhõm, vội vàng theo sau lưng."),t("br"),t("br"),n._v("Sinh tử đấu, hắn đã thua, tiếp tục lưu lại nơi này, khẳng định xui xẻo, có thể rời đi tốt nhất."),t("br"),t("br"),n._v("“Đi? Ta không bảo đi, ai dám đi?”"),t("br"),t("br"),n._v("Lông mày giương lên, Trương Huyền hừ lạnh một tiếng, lập tức nhìn về phía Vưu Hư: “Ngươi hại chết Ngụy Trường Phong, đưa danh sư tôn nghiêm cùng đạo nghĩa không để ý, còn mặt mũi nào làm gương sáng cho người khác, quỳ xuống cho ta!”"),t("br"),t("br"),n._v("“Quỳ xuống?”"),t("br"),t("br"),n._v("Vưu Hư sững sờ, chính muốn nói chuyện, liền cảm giác chỗ đầu gối, một cỗ đặc thù lực lượng, mãnh liệt kích thích huyệt vị, đau đớn kịch liệt lập tức đánh tới."),t("br"),t("br"),n._v("Phù phù!"),t("br"),t("br"),n._v("Chưa kịp trả lời, đầu gối mềm nhũn, quỳ trên mặt đất."),t("br"),t("br"),n._v("Thiên Đạo chân khí tại đối phương trong cơ thể che giấu, nhất niệm là độc, đem hắn chế ngự, dễ như trở bàn tay."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Sắc mặt tái xanh, Lục Phong cả người tựa như đáy nồi."),t("br"),t("br"),n._v("Mới vừa nói xong muốn mang về, liền để Vưu Hư quỳ rạp xuống đất."),t("br"),t("br"),n._v("Quả thực chính là ở trước mặt đánh mặt, hơn nữa còn là “Ba ba ba” cái chủng loại kia."),t("br"),t("br"),n._v("Lúc nào, hắn đường đường Đan viện viện trưởng tên tuổi, không đáng giá như vậy?"),t("br"),t("br"),n._v("“Trương Huyền, ngươi thật muốn cùng ta đối nghịch?”"),t("br"),t("br"),n._v("Ánh mắt âm sâm, lạnh lùng nhìn qua, tựa hồ muốn thanh niên lôi kéo đập tan."),t("br"),t("br"),n._v("“Đối nghịch?”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu: “Ngươi còn không đáng cho ta đối nghịch! Nói thật, ngươi xuất hiện trước, ta cũng không biết ngươi là cái nào rễ hành!”"),t("br"),t("br"),n._v("Người mang Thiên Đạo thư viện, Thiên Đạo bên dưới không võ không phá, một cái nho nhỏ Đan viện viện trưởng mà thôi, không chủ động xuất hiện, cũng không biết là ai, còn đối nghịch... Ngươi thật suy nghĩ nhiều!"),t("br"),t("br"),n._v("“Ngươi...” Lục Phong tức giận nhanh muốn điên."),t("br"),t("br"),n._v("“Được rồi, chẳng muốn cùng ngươi nói nhảm. Y sư sinh tử đấu, còn không có kết thúc, ai cũng không thể dẫn hắn đi, đừng nói ngươi chỉ là cái đại viện trưởng, coi như viện trưởng đích thân đến, cũng vô dụng! Nếu không... Chỉ có thể mang đi cái thi thể!”"),t("br"),t("br"),n._v("Tùy ý khoát tay áo, Trương Huyền lười phải tiếp tục giải thích."),t("br"),t("br"),n._v("Thiên Đạo chân khí xâm nhập đối phương cốt tủy, vị này Vưu Hư sống chết chỉ ở bản thân một ý niệm, chẳng cần biết ngươi là ai, muốn mang đi, nằm mơ!"),t("br"),t("br"),n._v("“Ngươi dám!”"),t("br"),t("br"),n._v("Lục Phong cắn răng."),t("br"),t("br"),n._v("“Vì sao không dám?” Trương Huyền lắc đầu, nhìn về phía Vưu Hư: “Hắn nói ta không dám, đến, nôn cái máu cho hắn nhìn xem!”"),t("br"),t("br"),n._v("Tiếng nói kết thúc, quỳ trên mặt đất Vưu Hư, lập tức cảm thấy nội tạng trong nháy mắt bị một cỗ đặc thù lực lượng khuấy động, cũng nhịn không được nữa, “Oa!” Một ngụm máu tươi cuồng bắn ra, nhuộm đỏ một mảnh."),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Thân thể nhoáng một cái, Lục Phong mắt tối sầm lại, suýt chút nữa không điên rồi."),t("br"),t("br"),n._v("Quả thực chính là trắng trợn khiêu khích!"),t("br"),t("br"),n._v("“Thế nào, không phục? Đến, Vưu Hư, tiếp tục, nôn đến hắn phục mới thôi!”"),t("br"),t("br"),n._v("Hai tay một lưng, Trương Huyền lông mày giương lên."),t("br"),t("br"),n._v("“Phốc! Phốc! Phốc!”"),t("br"),t("br"),n._v("Nghe được mệnh lệnh của hắn, Vưu Hư từng cái máu tươi hướng ra phía ngoài không ngừng phun tung tóe, mới phun ra mấy thanh, liền vẻ mặt trắng bệch, xem ra bất cứ lúc nào cũng sẽ ngủm."),t("br"),t("br"),n._v("“Dừng tay!”"),t("br"),t("br"),n._v("Thấy bạn cũ tiếp tục nôn đi xuống, khẳng định sẽ chết, Lục Phong tức giận răng đều nhanh cắn đi, nhưng không có biện pháp nào."),t("br"),t("br"),n._v("Lúc này, ngu nữa cũng biết, vừa rồi y sư sinh tử đấu lúc, vị này Trương sư, tất nhiên xuống vô cùng lợi hại độc dược, nếu không, người sau cũng không có khả năng như thế nghe lời, nói quỳ liền quỳ, nói nôn liền nôn."),t("br"),t("br"),n._v("Liền phản bác dũng khí đều không có."),t("br"),t("br"),n._v("“Ngừng!”"),t("br"),t("br"),n._v("Trương Huyền vẫy tay."),t("br"),t("br"),n._v("Vưu Hư lúc này mới đình chỉ hộc máu, thân thể mềm nhũn, miệng lớn thở hổn hển, chỉ cảm thấy nửa cái mạng già đều nhanh không có."),t("br"),t("br"),n._v("Từ khi trở thành danh sư, sống tiếp cận một ngàn năm, cái nào nhận qua loại khuất nhục này."),t("br"),t("br"),n._v("Thấy hắn dừng tay, Lục Phong vội vàng hướng về phía trước, lấy ra một cái giải độc đan dược, này xuống dưới."),t("br"),t("br"),n._v("Vưu Hư thở hổn hển mấy thanh, nhịn không được lắc đầu."),t("br"),t("br"),n._v("Cái này giải độc đan dược mặc dù lợi hại, nhưng đối trên người hắn trúng độc tới nói, không có chút nào hiệu quả."),t("br"),t("br"),n._v("Đối phương lưu ở trong cơ thể hắn độc, đã chui vào trong cơ thể bí ẩn nhất, chân khí đều tu luyện không đến địa phương, bệnh nguy kịch, dược thạch vô dụng."),t("br"),t("br"),n._v("“Đáng giận!”"),t("br"),t("br"),n._v("Đứng dậy, Lục Phong lần nữa nhìn về phía Trương Huyền, răng cắn “Khanh khách!” Rung động: “Rất tốt, ngươi rất có bản lĩnh! Bất quá, ở đây, ta phải nhắc nhở ngươi một câu, ngươi y sư sinh tử đấu thắng, nhưng Vưu Hư thân là lục tinh thượng phẩm danh sư, chịu học viện che chở, lấy thân phận của ngươi, còn không có tư cách quyết định sinh tử của hắn!”"),t("br"),t("br"),n._v("Trương Huyền cau mày."),t("br"),t("br"),n._v("Đối phương ngữ khí mặc dù không khách khí, nhưng hắn biết nói không sai."),t("br"),t("br"),n._v("Y sư sinh tử đấu, nhiều nhất bảo hắn thân bại danh liệt, giết chết, vẫn chưa được, bởi vì... Đối phương loại trừ y sư, còn có danh sư thân phận."),t("br"),t("br"),n._v("Mỗi một cái danh sư, đều là xã hội trụ cột của quốc gia, Danh Sư đường tốn hao vô số đại giới bồi dưỡng ra được, nhất là đạt đến lục tinh thượng phẩm, không làm ra tội ác tày trời, vi phạm nhân tộc sự tình, cùng cấp bậc ai cũng không tư cách xử tử."),t("br"),t("br"),n._v("Trừ phi... Thất tinh danh sư tự mình mở miệng, hoặc là đi qua giáo sư toà án thẩm phán."),t("br"),t("br"),n._v("Nguyên nhân chính là như thế, hắn mới lợi dụng sư ngôn thiên bẩm, muốn mê hoặc đối phương tự sát."),t("br"),t("br"),n._v("Nếu là thật chết rồi, nói thành xấu hổ tự sát, không có chứng cứ, ai cũng không thể đem hắn thế nào, mà bây giờ, nếm qua một lần thiếu, còn muốn mê hoặc thành công, rất khó."),t("br"),t("br"),n._v("“Ta có thể không giết hắn! Bất quá, ta muốn mang hắn đi, còn người khác có hay không động thủ, liền chuyện không liên quan đến ta!” Trương Huyền vẫy tay."),t("br"),t("br"),n._v("Mặc dù rất muốn chém giết cái tên này, nhưng càng muốn bảo Ngụy Như Yên tự mình động thủ, tự tay mình giết bản thân cừu nhân giết cha."),t("br"),t("br"),n._v("“Mang đi? Nơi này là danh sư học viện, ngươi muốn dẫn đi một cái phó viện trưởng, có phải hay không cảm thấy quá ngây thơ rồi?”"),t("br"),t("br"),n._v("Vung tay lên, Lục Phong cười lạnh."),t("br"),t("br"),n._v("“Ngươi đây là không có ý định tuân thủ sinh tử đấu quy định?” Trương Huyền cau mày."),t("br"),t("br"),n._v("“Quy củ tự nhiên tuân thủ, có điều, sinh tử đấu kết thúc, thân phận của ngươi, vẫn như cũ là danh sư học viện học sinh, ta đường đường đại diện viện trưởng, thập đại trường lão đứng đầu, ngăn cản một cái học sinh, không tính là vi phạm quy củ đi!”"),t("br"),t("br"),n._v("Lục Phong mắt sáng lên."),t("br"),t("br"),n._v("Vừa rồi hai bên đang tiến hành y sư sinh tử đấu, hắn nhúng tay là vi phạm quy củ, hiện tại tỷ thí kết thúc, đối phương lần nữa biến trở về học sinh thân phận, hắn đường đường viện trưởng, địa vị tôn sùng, bất kể ngăn cản, vẫn là giáo huấn, cũng không tính là không tuân theo quy định."),t("br"),t("br"),n._v("Trái lại còn nghĩa chính ngôn từ."),t("br"),t("br"),n._v("Trương Huyền vẻ mặt khó coi."),t("br"),t("br"),n._v("Đối phương nói không sai, bất kể nói thế nào, hắn đều chỉ là cái học sinh, thầy trò có thứ tự, tôn ti có khác, ở trong học viện, lão sư ngăn cản, còn thật không có bất kỳ biện pháp nào."),t("br"),t("br"),n._v("Dùng độc dược uy hiếp Vưu Hư coi như cũng được, thật muốn hạ độc chết, tương đương với lấy tứ tinh danh sư thân phận, độc chết lục tinh danh sư, phạm thượng, tất nhiên phải bị Danh Sư đường điều tra."),t("br"),t("br"),n._v("Đến lúc đó, càng thêm phiền phức."),t("br"),t("br"),n._v("Không hổ là Đan viện viện trưởng, lục tinh thượng phẩm danh sư, chơi quy củ, so với hắn còn muốn lưu."),t("br"),t("br"),n._v("“Đem Vưu viện trưởng lưu lại, cũng đem độc giải, còn đánh cược chiến thắng, ta sẽ bảo hắn cấp cho đền bù... Như vậy, ta liền sẽ thả ngươi rời đi, xem như hôm nay chuyện gì cũng chưa từng xảy ra. Nếu không, thân là lão sư, giáo huấn một cái không nghe lời học sinh, truyền đến Danh Sư đường, không có người sẽ thay ngươi nói chuyện!”"),t("br"),t("br"),n._v("Lục Phong khẽ nói."),t("br"),t("br"),n._v("“Ngươi là quyết tâm muốn tìm ta phiền phức?”"),t("br"),t("br"),n._v("Thấy đối phương không có ý định nhường đường, chỉ cần mình không đáp ứng liền sẽ tùy thời động thủ, Trương Huyền ánh mắt như băng."),t("br"),t("br"),n._v("Quả nhiên, danh sư vô sỉ lên, so với hắn đều lợi hại hơn."),t("br"),t("br"),n._v("Lưu manh không đáng sợ, liền sợ lưu manh có văn hóa!"),t("br"),t("br"),n._v("“Tìm phiền toái? Ta cũng không phải là tìm làm phiền ngươi, chỉ là giáo dục vãn bối mà thôi, nếu như ngươi bối phận cao hơn ta, tự nhiên cũng có thể tùy ý giáo huấn ta!” Lục Phong nở nụ cười."),t("br"),t("br"),n._v("Bối phận đè chết người."),t("br"),t("br"),n._v("Ngươi là học sinh, ta là lão sư, liền cái thân phận này, liền có thể ép ngươi không ngóc đầu lên được, ai cũng nói không là cái gì!"),t("br"),t("br"),n._v("Trương Huyền muốn nói cái gì, lại không biết trả lời như thế nào, chính xoắn xuýt nên xử lý như thế nào, liền nghe đến dưới đài một cái ông lão gấp gáp tiếng gọi ầm ĩ vang lên."),t("br"),t("br"),n._v("“Trịnh trưởng lão, Vương trưởng lão, Lưu trưởng lão, các ngươi cũng tới, không cần đi lên...”"),t("br"),t("br"),n._v("Ngay sau đó tiếng gió truyền đến, mấy thân ảnh nhảy tới."),t("br"),t("br"),n._v("Lập tức một cái thanh âm nhàn nhạt vang lên: “Lục viện trưởng, tại hạ ba người là võ kỹ học viện tân chiêu tới khách tọa trưởng lão, hưởng thụ học viện đặc biệt đãi ngộ, không biết... Có thể hay không cùng ngươi cùng thế hệ?”"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-820"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-822"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);