(window.webpackJsonp=window.webpackJsonp||[]).push([[2101],{2455:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-813-nghiet-suc-con-khong-bai-kien-su-to"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-813-nghiet-suc-con-khong-bai-kien-su-to"}},[n._v("#")]),n._v(" Chương 813: Nghiệt súc, còn không bái kiến sư tổ!")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("Chương 813: Nghiệt súc, còn không bái kiến sư tổ!"),i("br"),i("br"),n._v("Nghe được thanh âm, Trương Huyền kìm lòng không được quay đầu, lập tức bắt đầu sững sờ."),i("br"),i("br"),n._v("Không là người khác, chính là Trịnh Dương, Vương Dĩnh mấy cái."),i("br"),i("br"),n._v("Những tiểu tử này, không phải ở bên ngoài trường tu luyện ư? Lúc nào chạy đến nơi đây?"),i("br"),i("br"),n._v("Chạy đến nơi đây ngược lại cũng thôi, nhiều người như vậy, làm sao trà trộn vào đến, mà không bị cầm?"),i("br"),i("br"),n._v("Hắn tràn đầy kỳ quái, đối diện Lục Phong cũng không nghĩ tới, mấy người kia chạy tới chuyên môn hỏi ra lời này, sửng sốt một chút, nhẹ nhàng cười một tiếng: “Ta nghe Tu viện trưởng nói qua ba người các ngươi, tuổi không lớn lắm, đối võ kỹ cùng tu luyện nhưng lý giải cực sâu, bắt đầu từ đơn giản đến phức tạp, nhắm thẳng vào đại đạo... Là thiên tài trong thiên tài. Có thể chịu đến Tu viện trưởng coi trọng, đủ để chứng minh năng lực!”"),i("br"),i("br"),n._v("“Đã các ngươi là võ kỹ học viện khách tọa trưởng lão, cũng là danh sư học viện trưởng lão, tự nhiên cùng ta cùng thế hệ!”"),i("br"),i("br"),n._v("Khách tọa trưởng lão địa vị, muốn so với bình thường trưởng lão còn cao hơn."),i("br"),i("br"),n._v("Hắn mặc dù là viện trưởng, cũng chỉ là trưởng lão bối phận, nói cách khác, cùng đối phương tương đồng, coi như tuổi tác có khoảng cách, lại là thực sự cùng thế hệ người."),i("br"),i("br"),n._v("“Là cùng thế hệ là tốt rồi!”"),i("br"),i("br"),n._v("Trịnh Dương đám người nhẹ gật đầu, xoay người nhìn về phía cách đó không xa Trương Huyền, đồng loạt đi tới trước mắt, quỳ rạp xuống đất: “Học sinh Trịnh Dương (Vương Dĩnh, Lưu Dương), gặp qua lão sư!”"),i("br"),i("br"),n._v("“A?”"),i("br"),i("br"),n._v("“Võ kỹ học viện tân mời ba vị khách tọa trưởng lão, lại là... Trương Huyền Trương sư học sinh?”"),i("br"),i("br"),n._v("“Trương sư học sinh, cùng Lục Phong viện trưởng cùng thế hệ, cái kia... Lục viện trưởng chẳng phải thấp một đời...”"),i("br"),i("br"),n._v("“Đây rốt cuộc làm sao cái tình huống?”"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Cái này không chỉ tất cả lão sinh sửng sốt, học viện chư nhiều vị lão sư, trưởng lão, viện trưởng cũng đều ngớ ra tại nguyên chỗ."),i("br"),i("br"),n._v("Võ kỹ học viện mời khách tọa trưởng lão sự tình, rất nhiều người đều biết, cũng không ít người đi nghe giảng bài, ba người này tuổi tác mặc dù không lớn, nhưng đối tu luyện lý giải chi sâu, để cho người ta khâm phục không ngớt."),i("br"),i("br"),n._v("Mời làm trưởng lão, quả thực là danh xứng với thực."),i("br"),i("br"),n._v("Vốn cho rằng đối tu vi giải triệt để như vậy, lại là một vị nào đó ẩn thế đại năng truyền thừa, nằm mơ đều không nghĩ tới... Lão sư là vị này Trương Huyền!"),i("br"),i("br"),n._v("Hắn vẫn là tân sinh... Học sinh của mình nhưng thành học viện trưởng lão..."),i("br"),i("br"),n._v("Đây rốt cuộc làm sao cái tình huống?"),i("br"),i("br"),n._v("Những người khác chỉ là khiếp sợ, trên đài Lục Phong viện trưởng thật người cũng như tên, sắp điên (phong)."),i("br"),i("br"),n._v("Mới vừa phách lối mà nói, nếu như ngươi bối phận cao, có thể tùy ý giáo huấn ta, kết quả... Ba người này liền xông ra."),i("br"),i("br"),n._v("Dựa theo trưởng lão cùng thế hệ lời nói, Trương Huyền là khách tọa trưởng lão lão sư, há không nên gọi hắn vì... Thúc?"),i("br"),i("br"),n._v("Vẻ mặt đen nhánh, chỉ cảm thấy một hơi đặt ở ngực, Lục Phong sắp thở không ra."),i("br"),i("br"),n._v("Cái này gọi chuyện gì!"),i("br"),i("br"),n._v("Sớm biết như thế, liền không nói ba người này cùng hắn ngang hàng... Cái này được rồi, nâng lên đá đập chân của mình, muốn cự tuyệt cũng khó khăn."),i("br"),i("br"),n._v("Vẻ mặt máu ứ đọng, nhẫn nhịn một hồi lúc này mới lên tiếng: “Trương Huyền mặc dù là Lão sư của các ngươi, nhưng hắn vẫn như cũ là học viện học viên...”"),i("br"),i("br"),n._v("“Nếu là học viên, khẳng định có lão sư, cái kia ta muốn hỏi một chút Lục viện trưởng, gia sư, bái ai là sư phụ?” Trịnh Dương nhìn qua."),i("br"),i("br"),n._v("Lục Phong khuôn mặt cứng đờ."),i("br"),i("br"),n._v("Cái này Trương Huyền gần đây huyên náo xôn xao, hắn tự nhiên cũng nghe qua, đi tới học viện liền hôn mê, căn bản không có bái sư!"),i("br"),i("br"),n._v("Nói cách khác... Hắn mặc dù vào học viện, nhưng không có nhận sư phụ."),i("br"),i("br"),n._v("“Không bái...” Khuôn mặt khó coi, lắc đầu."),i("br"),i("br"),n._v("“Cái kia gia sư thẻ học viên nhưng có?” Trịnh Dương tiếp tục hỏi."),i("br"),i("br"),n._v("Lục Phong lần nữa dừng lại."),i("br"),i("br"),n._v("Thẻ học viên, cần bái sư về sau mới có thể nhận lấy, không bái sư học viên, mặc dù có thể nghe giảng bài, nhưng thẻ học viên làm bên trên, liên quan rất nhiều học viện, khá là phiền toái, ít nhất cũng phải khai giảng một, sau hai mươi ngày."),i("br"),i("br"),n._v("Hiện tại mới khai giảng năm, sáu ngày mà thôi... Tự nhiên không có khả năng xử lý đi ra!"),i("br"),i("br"),n._v("“Đã không có thẻ học viên, cũng không bái sư, vì cái gì nói là ngươi danh sư học viện học viên? Có chứng cứ gì?” Trịnh Dương tiếp tục nói."),i("br"),i("br"),n._v("Lục Phong á khẩu không trả lời được."),i("br"),i("br"),n._v("Nhân gia liền thẻ học viên đều không có, càng không bái sư, nói cách khác, học tịch đều không có... Không có học tịch, sao có thể được xưng tụng học sinh?"),i("br"),i("br"),n._v("Không phải học sinh, lại là khách tọa trưởng lão lão sư, bối phận còn cao hơn ngươi, ngươi dám ngăn trở, chính là đại nghịch bất đạo, vũ nhục trưởng bối!"),i("br"),i("br"),n._v("“Hắn mặc dù không có thẻ học viên, nhưng ở trong học viện ở lại, còn tổ kiến học hội...”"),i("br"),i("br"),n._v("Cái tên này đến học viện không đủ sáu ngày, đã kiếm sập học viện hai nơi, chơi đùa ra vô số sóng gió, bây giờ lại nói với ta, hắn không phải học sinh... Làm cái gì?"),i("br"),i("br"),n._v("Nếu như không phải học sinh, đã sớm đánh ra, có thể nào tùy ý như thế làm ẩu?"),i("br"),i("br"),n._v("“Được rồi, Lục Phong hiền chất, tại học viện ở lại, ta là cầm Mi viện trưởng lệnh bài, tổ kiến học hội, thì là vô số tân sinh nâng đỡ, không cần đến xoắn xuýt!”"),i("br"),i("br"),n._v("Trương Huyền mở miệng: “Ngươi ngăn cản ta, biết là vì học viện tốt, cũng không trách ngươi. Bất quá, vị này Vưu Hư, giao đấu thua, ta vẫn còn muốn mang đi, ngươi làm vãn bối, cũng không cần tranh chấp!”"),i("br"),i("br"),n._v("“Hiền... Chất?”"),i("br"),i("br"),n._v("Nghe được đối phương ông cụ non lời nói, Lục Phong viện trưởng suýt chút nữa không tại chỗ ngất đi."),i("br"),i("br"),n._v("Cho cái bậc thang, ngươi liền lên ah!"),i("br"),i("br"),n._v("Muốn nói ngươi là viện trưởng, còn không lập tức chỉ điểm thiên hạ?"),i("br"),i("br"),n._v("“Trịnh trưởng lão, Vương trưởng lão bọn họ lại là... Trương sư học sinh?”"),i("br"),i("br"),n._v("“Xem ra vẫn là thân truyền...”"),i("br"),i("br"),n._v("Triệu Bính Tuất, Mi viện trưởng mấy người cũng nguyên một đám mặt mũi choáng váng."),i("br"),i("br"),n._v("Vốn cho rằng Lục Phong viện trưởng nhúng tay, vị này Vưu Hư, khẳng định muốn cứu đi, nằm mơ đều không nghĩ tới, giết ra ba vị khách tọa trưởng lão!"),i("br"),i("br"),n._v("Rất hố người chính là, vị này Trương Huyền trực tiếp xưng hô Lục Phong viện trưởng vì hiền chất... Tính như vậy, bọn họ chẳng phải cũng là vãn bối?"),i("br"),i("br"),n._v("“Bớt ở chỗ này cùng ta bằng tư luận bối... Bất kể nói thế nào, ngươi cũng chỉ là cái tứ tinh danh sư, hôm nay coi như nói ra hoa đến, cũng đừng hòng đem người mang đi...”"),i("br"),i("br"),n._v("Biết nói tiếp, khẳng định sẽ được đưa tới trong mương, rốt cuộc bò không được, Lục Phong khẽ nói."),i("br"),i("br"),n._v("Mỗi người tu luyện, bởi vì thể chất khác biệt, thiên phú khác biệt, bái sư cũng không giống nhau, nếu như vậy luận lời nói, đâu đâu cũng có sư phụ."),i("br"),i("br"),n._v("“Được rồi, Lục Phong viện trưởng, đường đường danh sư, học viện đại viện trưởng, ở đây dây dưa đến, dây dưa đi, còn thể thống gì?”"),i("br"),i("br"),n._v("Lời còn chưa dứt, một cái thanh âm nhàn nhạt vang lên."),i("br"),i("br"),n._v("Ngẩng đầu nhìn lại, chỉ thấy một cái nhạt như như hoa lan thân ảnh, rơi xuống."),i("br"),n._v("Trương Huyền ánh mắt sáng lên."),i("br"),i("br"),n._v("Không là người khác, chính là bảo hắn nhìn lần đầu tiên liền tâm động không ngừng mỹ nữ lão sư, Lạc Nhược Hi."),i("br"),i("br"),n._v("“Lạc sư!”"),i("br"),i("br"),n._v("Thấy là nàng, Lục Phong viện trưởng vội vàng xoay người, ôm quyền nói: “Chuyện này liên quan danh sư học viện tôn nghiêm, nếu như tùy ý một ngoại nhân, mang đi học viện phó viện trưởng, về sau còn thế nào hướng Danh Sư đường bàn giao?”"),i("br"),i("br"),n._v("Lạc Nhược Hi đôi mi thanh tú nhăn lại, khoát tay áo: “Như thế nào hướng Danh Sư đường bàn giao, Mộc Viên tự sẽ đi nói, không cần đến ngươi hao tâm tổn trí.”"),i("br"),i("br"),n._v("“Cái này... Vâng!”"),i("br"),i("br"),n._v("Vẻ mặt khó coi, Lục Phong viện trưởng vẫn gật đầu."),i("br"),i("br"),n._v("Vị này Lạc sư mặc dù tuổi không lớn lắm, nhưng có thể là Thánh Nhân quý tộc Lạc gia người, liền Mộc Viên Mộc sư đều tùy tùng giống như trọng đãi có gia, hắn cũng không dám nói nhảm."),i("br"),i("br"),n._v("Thấy Lục Phong không tại nhiều nói, Lạc Nhược Hi lúc này mới nhìn về phía cách đó không xa Trương Huyền: “Ngươi có phải hay không... Đã lĩnh ngộ Minh Lý Chi Nhãn?”"),i("br"),i("br"),n._v("Không nghĩ tới nàng có thể như vậy hỏi, Trương Huyền sửng sốt một chút, cũng không giấu diếm: “Xem như thế đi!”"),i("br"),i("br"),n._v("Mới vừa rồi cùng Vưu Hư tiến hành y sư sinh tử đấu thời điểm, từng sử dụng qua, hẳn là lúc kia bị phát hiện."),i("br"),i("br"),n._v("“Lục tinh danh sư mới có thể lĩnh ngộ Minh Lý Chi Nhãn?”"),i("br"),i("br"),n._v("“Nghe nói coi như Thánh Nhân quý tộc thiên chi kiêu tử, đều không thể lĩnh ngộ, Trương sư nắm giữ?”"),i("br"),i("br"),n._v("“Nắm giữ loại thiên phú này, chỉ cần không vẫn lạc, ít nhất thành tựu đều là bát tinh danh sư...”"),i("br"),i("br"),n._v("“Đúng vậy a, lịch sử ghi chép bên trên, lĩnh ngộ Minh Lý Chi Nhãn thiên tài, tổng cộng mười vị, trong đó sáu vị đạt đến cửu tinh, hai vị vẫn lạc, một vị bởi vì trọng thương, tu vi không cách nào tiến bộ, nhưng cũng đạt tới bát tinh cấp bậc. Còn có một vị, khảo hạch thất tinh về sau, đã mất đi tung tích... Nghe nói, là cùng Dị Linh tộc nhân chiến đấu, trước khi chết, bạo phát cửu tinh danh sư thực lực. Bởi vì không thông qua Danh Sư đường khảo hạch, tình huống cụ thể ai cũng không biết! Nhưng có một chút có thể xác nhận, chỉ muốn lĩnh ngộ loại thiên phú này, sau này thành tựu, tuyệt không thua kém bát tinh!”"),i("br"),i("br"),n._v("“Đáng sợ...”"),i("br"),i("br"),n._v("Nghe được Trương Huyền thừa nhận, dưới đài một hồi xôn xao."),i("br"),i("br"),n._v("Minh Lý Chi Nhãn, mặc dù mệnh danh lục tinh danh sư có cơ hội mở ra, nhưng chân chính có thể kích hoạt loại thiên phú này người, ít càng thêm ít."),i("br"),i("br"),n._v("Nhìn chung Danh Sư đường ghi chép, chỉ đếm được trên đầu ngón tay."),i("br"),i("br"),n._v("Trước mắt vị này Trương Huyền thế mà lĩnh ngộ... Khó trách bất kể luyện khí, kinh hồng vẫn là y thuật, đều bộc phát ra vượt qua thường nhân năng lực."),i("br"),i("br"),n._v("Loại thiên tư này, liền xem như Thánh Nhân quý tộc, đều khó gặp."),i("br"),i("br"),n._v("“Minh Lý Chi Nhãn?”"),i("br"),i("br"),n._v("Thân thể nhoáng một cái, Lục Phong viện trưởng càng là giật nảy mình."),i("br"),i("br"),n._v("Dưới đài rất nhiều lão sinh, đều biết loại thiên phú này mạnh mẽ, hắn làm sao không hiểu biết!"),i("br"),i("br"),n._v("Có thể nói, về sau thành tựu không thể đoán trước."),i("br"),i("br"),n._v("Vì cái này muốn chết Vưu Hư, đắc tội như vậy một vị siêu cấp thiên tài... Thật đáng giá không?"),i("br"),i("br"),n._v("Khó trách, vị này Lạc sư sẽ thay hắn ra mặt, nắm giữ loại thiên phú này, về sau thành tựu vô hạn, coi như Thánh Nhân quý tộc, cũng phải cùng giao hảo, không dám đắc tội."),i("br"),i("br"),n._v("“Quả nhiên!”"),i("br"),i("br"),n._v("Nhẹ gật đầu, Lạc Nhược Hi thân thể mềm mại nhoáng một cái, bay xuống đài cao, Trương Huyền chính kỳ quái, đi lên ngăn cản Lục Phong, liền vì hỏi câu nói này, lập tức nghe được nữ hài thanh âm nhàn nhạt tại vang lên bên tai."),i("br"),i("br"),n._v("“Ta buổi tối tới tìm ngươi!”"),i("br"),i("br"),n._v("Nói xong, thân thể mềm mại đi vào đám người, mấy cái vặn xoay, biến mất không thấy gì nữa."),i("br"),i("br"),n._v("“Tìm ta?”"),i("br"),i("br"),n._v("Trương Huyền cau mày."),i("br"),i("br"),n._v("Nắm giữ Thiên Đạo thư viện, hắn đối với bất kỳ người nào đều có thể xem thấu, có thể cô gái này, nhưng tràn đầy sương mù."),i("br"),i("br"),n._v("“Được rồi!”"),i("br"),i("br"),n._v("Biết đối phương bất kể muốn làm gì, ban đêm tự sẽ biết, cũng không có gì có thể xoắn xuýt, Trương Huyền không nghĩ nhiều nữa."),i("br"),i("br"),n._v("Có Lạc Nhược Hi mở miệng, Lục Phong không ngăn cản nữa, bàn giao Trịnh Dương đám người mang theo đã bị “Độc” chết đi sống lại Vưu Hư, hướng dưới đài đi đến."),i("br"),i("br"),n._v("Biết cái tên này bảo Lục Phong viện trưởng đều bị thiệt thòi, dưới đài lão sinh nhao nhao nhường ra một con đường."),i("br"),i("br"),n._v("Biết lần này huyên náo như thế lớn, danh sư học viện, có lẽ còn muốn đến rất khó, Trương Huyền nhịn không được lắc đầu, có chút thất lạc."),i("br"),i("br"),n._v("Mười đại học viện, hiện tại mới khiêu chiến luyện khí, kinh hồng, y sư ba cái, còn có bảy cái không đi qua, cứ như vậy đi, thực sự cảm giác quá mức ăn thiệt thòi."),i("br"),i("br"),n._v("“Xem ra, về sau lại đến học viện, cũng chỉ có thể khiêu chiến...”"),i("br"),i("br"),n._v("Không phải học viện học sinh, không có học phần cùng thẻ học viên, liền không thể tự do ra vào học tập, càng không thể an ổn khảo hạch."),i("br"),i("br"),n._v("Mà muốn phụ tu chức nghiệp đi đến lục tinh, chỉ có thể một cái biện pháp, cái kia chính là khiêu chiến!"),i("br"),i("br"),n._v("Tỷ như, muốn khảo hạch luyện đan sư, hoàn toàn có thể đi khiêu chiến toàn bộ học viện, chỉ nếu không có ai hơn được bản thân, tự nhiên có thể được đến lục tinh huy chương."),i("br"),i("br"),n._v("“Chỉ muốn an ổn kiểm tra chút, vì sao cứ như vậy khó đâu?”"),i("br"),i("br"),n._v("Nghĩ đến một khi khiêu chiến, khẳng định gà bay chó chạy, toàn bộ danh sư học viện, còn lớn hơn loạn, Trương Huyền nhịn không được lắc đầu."),i("br"),i("br"),n._v("Nói thật, hắn chỉ muốn làm cái học sinh, an ổn học tập, an ổn kiểm tra chút, kết quả... Vị này đại viện trưởng không cho phép, tước đoạt hắn đem học sinh quyền lợi!"),i("br"),i("br"),n._v("Vậy được rồi, đi về nghỉ trước mấy ngày, quay đầu liền lần lượt học viện khiêu chiến một lần."),i("br"),i("br"),n._v("Thuận tiện xoạt xoạt phụ tu chức nghiệp, vì khảo hạch lục tinh danh sư làm chuẩn bị."),i("br"),i("br"),n._v("Trong lòng cảm khái, bước nhanh ra ngoài đi đến, mới đi không xa, liền thấy một cái cầm thương thanh niên, một mặt hưng phấn lao đến, ngăn tại trước mặt."),i("br"),i("br"),n._v("“Ta rốt cục đột phá Lạc Diệp thương pháp đệ thập trọng, đi đến nhân thương hợp nhất, Trương Huyền, ta muốn cùng ngươi tỷ thí, có dám hay không tiếp?”"),i("br"),i("br"),n._v("Thanh niên trường thương lay động, phát ra tiếng long ngâm hổ khiếu, mang theo lăng lệ không có thể ngang hàng khí thế."),i("br"),i("br"),n._v("Không là người khác, chính là vị kia Tiết Chân Dương."),i("br"),i("br"),n._v("Hôm qua nghe Trịnh Dương giảng bài về sau, vẫn bế quan, rốt cục tại vừa rồi, đột phá ràng buộc."),i("br"),i("br"),n._v("Nghe được Trương Huyền ở chỗ này, lập tức lao đến."),i("br"),i("br"),n._v("Thương pháp đột phá, hắn muốn rửa sạch nhục nhã, trọng chấn chân dương nổi danh tiếng!"),i("br"),i("br"),n._v("Ba!"),i("br"),i("br"),n._v("Tiếng rống kết thúc, đối diện Trương Huyền vẫn chưa trả lời liền cảm thấy cái ót bị người mạnh mẽ quất một cái tát."),i("br"),i("br"),n._v("Vội vàng quay đầu nhìn lại, chỉ thấy trịnh Dương lão sư vẻ mặt trầm thấp nhìn qua, một mặt không vui: “Nghiệt súc, còn không bái kiến sư tổ!”"),i("br"),i("br"),n._v("(Ps: Gần đây tình huống còn hài lòng? Hài lòng, xin quăng hai tấm vé tháng, cảm ơn mọi người. Mặt khác, các ngươi biết cái gì là mạnh nhất trang bức a? Hôm nay sẽ tại công chúng số bên trên dùng một cái M V cho các ngươi công bố trang bức mạnh nhất dáng vẻ, trang bức đều tại trong lúc vô hình, Wechat lục soát “Hoành Tảo Thiên Nhai”, chú ý về sau, kiểm tra lịch sử tin tức hoặc là hồi phục mạnh nhất trang bức, là được nhìn thấy."),i("br"),i("br"),n._v("Convert by: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-821"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-823"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);