(window.webpackJsonp=window.webpackJsonp||[]).push([[2103],{2457:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-815-ho-yeu-yeu-than-phan-chan-that"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-815-ho-yeu-yeu-than-phan-chan-that"}},[n._v("#")]),n._v(" Chương 815: Hồ Yêu Yêu thân phận chân thật")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 815: Hồ Yêu Yêu thân phận chân thật"),t("br"),t("br"),n._v("Khế đất bên trên ghi rõ kỹ càng địa chỉ, đúng là hắn cùng Lạc Thất Thất, Hồ Yêu Yêu hai người nhìn thấy toà kia."),t("br"),t("br"),n._v("Trước đó liền nghi hoặc, đến cùng là ai muốn đưa phủ đệ mình, muốn rất nhiều người, làm sao đều ngờ tới là vị này!"),t("br"),t("br"),n._v("Hắn cùng Hoài Vương gia, không có gì gặp nhau, duy nhất chính là Trịnh Dương bọn người ở tại đối phương nơi đó ở mấy ngày, thuận tiện Viên gia người, đem Viên Đào tiếp đi."),t("br"),t("br"),n._v("Nói trắng ra là cũng là một mặt mà thôi, gặp mặt một lần, đưa tặng giá trị hơn trăm linh thạch thượng phẩm biệt thự?"),t("br"),t("br"),n._v("Cái này... Thủ bút này thật là quá lớn."),t("br"),t("br"),n._v("Bất quá, suy nghĩ kỹ một chút, người hắn quen biết bên trong, chỉ sợ cũng chỉ có người này mới có như thế đại thủ đoạn."),t("br"),t("br"),n._v("Mạc Cao Viễn đám người, mặc dù thân là lục tinh danh sư, luận tài sản cùng một cái quyền thế ngập trời vương gia so, vẫn là kém rất nhiều."),t("br"),t("br"),n._v("“Vâng!” Quản gia Ngọc Thanh nhẹ gật đầu, một mặt cung kính."),t("br"),t("br"),n._v("“Vô công bất thụ lộc, lễ vật này nhận lấy thì ngại...” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Mặc dù hắn đối cái này biệt thự có ý nghĩ, nhưng là cùng đối phương không có gì gặp nhau, vô duyên vô cớ thực sự khó mà tiếp nhận."),t("br"),t("br"),n._v("“Vương gia bàn giao, cần phải để Trương sư nhận lấy, nếu không, thuộc hạ trở về, có thể sẽ bị quét dọn đi ra ngoài, thậm chí chịu đến hình phạt!”"),t("br"),t("br"),n._v("Ngọc Thanh vội vàng nói: “Lại nói, tòa phủ đệ này, là dùng Trương sư họ tên mua sắm, Trương sư nếu như chối từ, thật không biết xử lý như thế nào!”"),t("br"),t("br"),n._v("Trương Huyền cau mày."),t("br"),t("br"),n._v("Hắn mới vừa nhìn, khế đất cùng khế nhà bên trên chủ nhân một cột, quả thực đã ghi tên của mình."),t("br"),t("br"),n._v("Nói cách khác, vị này vương gia, sớm đoán được bản thân có thể sẽ chối từ, vì phòng ngừa ngoài ý muốn, liền sang tên đều tiết kiệm."),t("br"),t("br"),n._v("“Thôi được! Tôn Cường, đón lấy đi. Đợi lúc rảnh rỗi, ta sẽ chuyên thăm hỏi vương gia, bày tỏ cảm kích!”"),t("br"),t("br"),n._v("Chần chờ một chút, Trương Huyền nhẹ gật đầu."),t("br"),t("br"),n._v("Vừa đến, hắn đúng lúc không chỗ ở, thứ hai, đây là Ngô Dương Tử chỗ ở cũ, tìm kiếm bảo tàng cũng muốn tiến vào bên trong, đã đối phương thành ý đưa tiễn, cũng cũng không cần phải đẩy tới đẩy lui."),t("br"),t("br"),n._v("Cùng lắm thì tìm thời cơ, hỗ trợ chỉ điểm một hai, hoặc là giải quyết cái tai hoạ ngầm, cũng coi như hồi báo."),t("br"),t("br"),n._v("Kỳ thật, mục đích của đối phương, suy nghĩ kỹ một chút, cũng có thể rõ ràng."),t("br"),t("br"),n._v("Nhìn trúng cũng không phải là bản thân, mà là Viên Đào."),t("br"),t("br"),n._v("Viên Đào người mang Long Tê huyết thống, một khi trở lại Thánh Nhân quý tộc, tất nhiên chịu đến dốc sức bồi dưỡng, trở thành cao thủ tuyệt thế, bất quá thời gian dài ngắn mà thôi."),t("br"),t("br"),n._v("Làm hắn nhập môn ân sư, địa vị có thể tưởng tượng được."),t("br"),t("br"),n._v("Viên Đào không cách nào kết giao, giao tốt chính mình, tương đương với kéo lên Thánh Nhân Viên gia đường dây này, Hồng Viễn đế quốc sẽ càng ngày càng vững chắc."),t("br"),t("br"),n._v("Thực sự không thu, đối phương khẳng định sẽ còn muốn những biện pháp khác, còn không bằng trực tiếp cầm, chấm dứt phiền phức."),t("br"),t("br"),n._v("“Đa tạ Trương sư!”"),t("br"),t("br"),n._v("Vốn cho rằng còn nhiều hơn nói vài lời, thấy Trương sư trực tiếp đáp ứng, Ngọc Thanh quản gia hưng phấn gật gật đầu, đem khế nhà, khế đất chuyển giao cho Tôn Cường, lúc này mới ôm quyền: “Thuộc hạ liền cáo từ!”"),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Trương Huyền khoát tay áo: “Đã có địa phương mới, cùng đi chứ!”"),t("br"),t("br"),n._v("Không có gì có thể chần chờ, mang theo mọi người xoay người rời đi."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Hoàng Hưng đám người thấy vừa vặn cự tuyệt vị này Trương sư, nhân gia Hoài Vương gia liền tặng cho một tòa cự đại phủ đệ, tất cả đều hai mặt nhìn nhau, nói không ra lời."),t("br"),t("br"),n._v("Vốn là bọn họ nhận được Lục Phong viện trưởng bàn giao, là muốn vị này Trương Huyền mất mặt, hiện tại... Nhân gia không những không mất mặt, còn tương đương quất một cái tát..."),t("br"),t("br"),n._v("Thật muốn đem tin tức này bẩm báo Lục Phong viện trưởng, người sau có thể hay không thẹn quá hoá giận, đem bọn hắn đánh bên trên một chầu?"),t("br"),t("br"),n._v("Cái này gọi chuyện gì!"),t("br"),t("br"),n._v("Sớm biết liền không lội cái này bãi nước đục!"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Không để ý tới tràn đầy xoắn xuýt Hoàng Hưng đám người, Trương Huyền, Tôn Cường đám người, rời đi học viện, không lâu sau, liền đến đến phủ đệ gần."),t("br"),t("br"),n._v("“Thiếu gia...”"),t("br"),t("br"),n._v("Còn chưa tới đến gần, đại môn liền “Kẹt kẹt!” Mở ra, một đám hộ vệ, hạ nhân vội vã tiến lên đón."),t("br"),t("br"),n._v("“Vương gia lo lắng thiếu gia ở không quen, chuyên môn phái chúng ta tới hầu hạ, nha hoàn, đầu bếp nữ, hạ nhân, hộ vệ... Đầy đủ mọi thứ!”"),t("br"),t("br"),n._v("Một quản gia bộ dáng người tiến lên đón."),t("br"),t("br"),n._v("“Làm phiền vương gia phí tâm!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Cái này Hoài Vương gia, thật toán dụng tâm, nhà không nói, liền hạ nhân đều tiễn, khá hào phóng."),t("br"),t("br"),n._v("Bất quá, như thế lớn phủ đệ, không có hạ nhân, chỉ riêng để Tôn Cường, Trịnh Dương đám người quản lý lời nói, cũng quả thực phiền phức."),t("br"),t("br"),n._v("Dù sao ân huệ cũng đều nhận, nhiều một chút ít một chút, cũng không sao cả."),t("br"),t("br"),n._v("Chỉ cần đến lúc đó, nghĩ biện pháp trả lại là được."),t("br"),t("br"),n._v("Cùng tại người quản gia này đi vào sân nhỏ, Trương Huyền nhịn không được cảm khái."),t("br"),t("br"),n._v("Khó trách trước đó đi tới bên ngoài viện, liền nghe được có người làm việc thanh âm, toàn bộ phủ đệ, rực rỡ hẳn lên, hẳn là vừa vặn trang trí hết, phú quý mà không mất đi cao nhã, lộng lẫy mà không mất đi xa hoa."),t("br"),t("br"),n._v("Diêm nha cao trác, câu tâm đấu giác, bước nếu như bên trong, tựa như đi vào to lớn lâm viên."),t("br"),t("br"),n._v("Trong viện linh khí nồng đậm, vừa nhìn liền biết bố trí tụ linh trận pháp, mức độ đậm đặc, so với tinh anh khu biệt viện, đều không kém chút nào."),t("br"),t("br"),n._v("“Lợi hại!”"),t("br"),t("br"),n._v("Trương Huyền hài lòng nhẹ gật đầu."),t("br"),t("br"),n._v("Nói thật, vị này vương gia quả thực dụng tâm, trước đó hắn cho rằng mua tòa phủ đệ này tốn hao không ít, hiện tại xem ra, trang trí tốn hao khẳng định càng nhiều."),t("br"),t("br"),n._v("Nếu là hắn mua sắm, khẳng định kiếm không thành như vậy."),t("br"),t("br"),n._v("“Tôn Cường, ngươi cùng Tử Dực Thiên Hùng thú đi đem Ngụy Như Yên nhận lấy!”"),t("br"),n._v(" Đi đến ở giữa nhất đại sảnh, hết thảy an bài ổn thoả, Trương Huyền phân phó một tiếng."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Tôn Cường nhẹ gật đầu, xoay người rời đi."),t("br"),t("br"),n._v("Để Trịnh Dương đám người đem Vưu Hư trước giam lại, Trương Huyền cái này mới đi ra khỏi cửa phòng, đi tới phía trước một hoa viên."),t("br"),t("br"),n._v("Thong thả tới lui mấy bước, nhẹ nhàng cười một tiếng, đem Thập Diệp hoa lấy ra ngoài, cắm ở trên mặt đất."),t("br"),t("br"),n._v("Đây là toàn bộ phủ đệ, linh khí hội tụ ở giữa, đem dược liệu để ở chỗ này, có thể tốt hơn duy trì mới mẻ, không đến mức khô héo."),t("br"),t("br"),n._v("Để tốt về sau, tinh thần khẽ động, lại nhỏ mấy giọt địa mạch linh dịch, lúc này mới thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Dùng Thập Diệp hoa ôn dưỡng linh hồn, nhất định phải duy trì nó hoạt tính mới được, nếu không, một khi đóa hoa khô héo, trong đó tẩm bổ linh hồn, cũng sẽ tùy tử vong."),t("br"),t("br"),n._v("Lại từ trữ vật giới chỉ bên trong lấy ra mấy chục cây trận kỳ, cắm ở bốn phía, bố thành trận pháp, phòng ngừa bên ngoài người bất ngờ xông vào."),t("br"),t("br"),n._v("Làm xong những này, lúc này mới thở phào nhẹ nhõm, đang muốn đi trở về phòng, chỉ thấy Lạc Thất Thất cùng Hồ Yêu Yêu đi tới."),t("br"),t("br"),n._v("“Lão sư, tòa phủ đệ này, thật là của ngươi?”"),t("br"),t("br"),n._v("Lạc Thất Thất đến bây giờ cũng không tin."),t("br"),t("br"),n._v("Cũng khó trách, nơi này thực sự quá hào hoa, thậm chí so với hoàng cung đều không hề kém, vô duyên vô cớ liền đưa cho lão sư, đổi lại ai đều sẽ cảm giác đến giật mình."),t("br"),t("br"),n._v("Trương Huyền gật đầu: “Đi bên trong nói đi!”"),t("br"),t("br"),n._v("Nói đi vào đại sảnh."),t("br"),t("br"),n._v("Hai người nối đuôi nhau mà vào."),t("br"),t("br"),n._v("“Hồ Yêu Yêu, ngươi xác định nơi này chính là Ngô Dương Tử chỗ ở cũ?” Tinh thần khẽ động, Trương Huyền truyền âm qua."),t("br"),t("br"),n._v("“Xác định!” Hồ Yêu Yêu gật đầu."),t("br"),t("br"),n._v("Thấy nàng không chút do dự, Trương Huyền cau mày."),t("br"),t("br"),n._v("Hắn cùng Lạc Thất Thất tìm kiếm Ngô Dương Tử chỗ ở cũ mục đích, là vì tìm kiếm người sau lưu lại bảo tàng, mà ở trong đó, không biết thay chủ mấy lần, đủ loại trang hoàng, sửa chữa lại, cũng không biết kinh nghiệm bao nhiêu hồi, cho dù có bảo tàng, chỉ sợ cũng sớm bị người đào đi đi!"),t("br"),t("br"),n._v("“Trương sư như thế cố chấp tìm kiếm Ngô Dương Tử chỗ ở cũ, nếu ta đoán không lầm, có phải là vì hắn năm đó lưu lại bảo tàng chứ?”"),t("br"),t("br"),n._v("Chính đang suy tư, liền nghe đến Hồ Yêu Yêu thanh âm nhàn nhạt vang lên."),t("br"),t("br"),n._v("“Bảo tàng? Cái gì bảo tàng?” Trương Huyền ra vẻ không biết."),t("br"),t("br"),n._v("“Các ngươi cũng không cần giấu diếm ta, hai vị tìm tới Ngô Dương Tử tiền bối thi thể sự tình, mặc dù luyện khí học viện, không có truyền ra ngoài, nhưng ta yêu nghiệt biết, có không ít luyện khí học viện học viên, đã biết tin tức!”"),t("br"),t("br"),n._v("Hồ Yêu Yêu nhìn qua: “Tìm được tiền bối thi thể, bảo tàng sự tình, khẳng định cũng liền biết rồi!”"),t("br"),t("br"),n._v("“Ngô Dương Tử tiền bối, đã mất tích hơn hai nghìn năm, coi như tìm tới thi thể, người từ lâu qua đời, làm sao có thể có cái gì bảo tàng tin tức...” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("“Ha ha! Trước đó, hỏi thăm ta Ngô Dương Tử chỗ ở cũ, ta liền biết các ngươi đánh ý định gì, chỉ là các ngươi không nói, ta cũng không nói thẳng ra mà thôi!”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Hồ Yêu Yêu lần nữa khôi phục như yêu nghiệt thần thái, nói: “Các ngươi yên tâm, Ngô Dương Tử tiền bối từng lưu lại bảo tàng sự tình, toàn bộ Hồng Viễn thành, chỉ có một mình ta biết, không cần lo lắng tiết ra ngoài!”"),t("br"),t("br"),n._v("“Ồ? Đã ngươi nói như vậy, ta cũng muốn hỏi hỏi, ngươi đến cùng cùng vị này Ngô Dương Tử tiền bối, có quan hệ gì, đối chuyện của hắn biết đến cặn kẽ như vậy?”"),t("br"),t("br"),n._v("Thấy đối phương đem lời nói làm rõ, cũng không giấu diếm, Trương Huyền chẳng muốn ngụy trang, nhìn lại."),t("br"),t("br"),n._v("Nữ nhân này, khẳng định nơi này là Ngô Dương Tử chỗ ở cũ, liền sinh lòng nghi hoặc, hiện tại lại một cái nói ra bảo tàng, để hắn rất là sinh nghi."),t("br"),t("br"),n._v("“Ta không nói ra, các ngươi cũng sẽ không tin tưởng...”"),t("br"),t("br"),n._v("Nhẹ gật đầu, Hồ Yêu Yêu đột nhiên hít sâu một hơi, chân khí tại thể nội vận chuyển."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Thân thể mềm mại thon dài chấn động, toàn thân cơ bắp, giống như là tản mát ra ánh sáng, khí tức tăng nhiều."),t("br"),t("br"),n._v("“Ngũ Diệu Kim Thân?”"),t("br"),t("br"),n._v("Lạc Thất Thất lên tiếng kinh hô, nhịn không được cùng Trương Huyền liếc mắt nhìn nhau."),t("br"),t("br"),n._v("Hai người bọn họ đều tu luyện qua, không phải cái khác, chính là Ngô Dương Tử độc môn bí tịch... Ngũ Diệu Kim Thân!"),t("br"),t("br"),n._v("Bộ này luyện thể công pháp, không phải nói sớm đã thất truyền sao?"),t("br"),t("br"),n._v("Vì cái gì cái này Hồ Yêu Yêu biết?"),t("br"),t("br"),n._v("Hơn nữa nhìn bộ dáng, đắm chìm trong phía trên đã có thời gian không ngắn, đã đạt đến đệ nhị trọng cảnh giới, so với Trương Huyền, đều không kém chút nào."),t("br"),t("br"),n._v("“Không sai, đây là Ngũ Diệu Kim Thân!” Ngừng vận chuyển, Hồ Yêu Yêu gật gật đầu: “Là ta tiền bối, lưu lại độc môn bí tịch.”"),t("br"),t("br"),n._v("“Ngươi tiền bối? Ngũ Diệu Kim Thân không phải... Chẳng lẽ...” Lạc Thất Thất sững sờ."),t("br"),t("br"),n._v("“Ta kỳ thật cũng không họ Hồ, mà là họ... Ngô!” Hồ Yêu Yêu nói: “Ngô Dương Tử, chính là của ta Tiên Tổ!”"),t("br"),t("br"),n._v("“Đã ngươi là Ngô Dương Tử tiền bối hậu nhân, vì sao phải sửa họ Hồ?” Trương Huyền kỳ quái."),t("br"),t("br"),n._v("Ngô Dương Tử 4 thân là luyện khí đại tông sư, địa vị tôn sùng, được người tôn kính, làm hắn hậu nhân, vì sao phải mai danh ẩn tích? Không dám công bố tại thế?"),t("br"),t("br"),n._v("“Không mai danh ẩn tích, sợ là chúng ta cái này một nhánh, sớm đã bị người đuổi tận giết tuyệt!”"),t("br"),t("br"),n._v("Hồ Yêu Yêu hừ lạnh."),t("br"),t("br"),n._v("“Đuổi tận giết tuyệt?” Trương Huyền cau mày: “Nơi này là danh sư học viện vị trí, có người dám đối với các ngươi động thủ?”"),t("br"),t("br"),n._v("Hồng Viễn đế quốc, có danh sư học viện, càng nổi tiếng sư đường, có người nào dám đối Ngô Dương Tử sau người hạ thủ?"),t("br"),t("br"),n._v("“Nào chỉ là dám... Ngươi thật chẳng lẽ cho rằng, Ngô Dương Tử Tiên Tổ, dễ dàng như vậy bị người bắt đi? Liền truyền lại tin tức cơ hội đều không có?”"),t("br"),t("br"),n._v("Hồ Yêu Yêu nhịn không được nói."),t("br"),t("br"),n._v("“Cái này...” Trương Huyền chần chờ."),t("br"),t("br"),n._v("Ngô Dương Tử bị bắt Địa Cung, hắn tiến vào, một mực có chút kỳ quái."),t("br"),t("br"),n._v("Trong đó khôi lỗi, thực lực mạnh nhất cũng chỉ Thánh vực nhất trọng, loại thực lực này tại Hồng Viễn đế quốc xem như không yếu, nhưng là muốn đối phó một cái bất cứ lúc nào cũng sẽ đột phá Thất Tinh luyện khí sư, chỉ sợ vẫn là rất khó làm được."),t("br"),t("br"),n._v("Chẳng lẽ... Trong đó có cái gì bí mật không muốn người biết?"),t("br"),t("br"),n._v("(Tám giờ tối nay, lão nhai sẽ công chúng số phát Trương Huyền 40 mét chổi lông gà hình ảnh, Đài Loan hoạ sĩ vẽ, thoạt nhìn cũng không tệ lắm, mọi người có thể nhìn xem. Wechat lục soát “Hoành Tảo Thiên Nhai” tăng thêm chú ý kiểm tra lịch sử ghi chép là đủ.)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-823"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-825"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);