(window.webpackJsonp=window.webpackJsonp||[]).push([[2104],{2459:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-816-đi-luyen-đan-hoc-vien-choi-đua"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-816-đi-luyen-đan-hoc-vien-choi-đua"}},[n._v("#")]),n._v(" Chương 816: Đi luyện đan học viện chơi đùa")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 816: Đi luyện đan học viện chơi đùa"),t("br"),t("br"),n._v("Luyện khí sư, là thượng cửu lưu chức nghiệp, đối tu vi yêu cầu mười phần nghiêm khắc, Ngô Dương Tử có thể có như thế đại danh khí, thực lực chân chính, kém cỏi nhất cũng phải Thánh vực nhất trọng đỉnh phong, lại thêm thân là luyện khí sư, đủ loại bảo vật dùng mãi không cạn..."),t("br"),t("br"),n._v("Không nói cái khác, coi như Mi trưởng lão, Lục Phong viện trưởng những người này cùng một chỗ tới, như muốn chém giết, chỉ sợ cũng không dễ dàng như vậy!"),t("br"),t("br"),n._v("Một đám Thánh vực nhất trọng khôi lỗi, đem hắn bắt vào Địa Cung, trước khi chết đều không có đi ra, bản thân liền có rất nhiều không thể tưởng tượng nổi, hiện tại nghe Hồ Yêu Yêu khẩu khí, dường như biết chút ít cái gì."),t("br"),t("br"),n._v("Ngô Dương Tử bị bắt, cũng không đơn giản như vậy."),t("br"),t("br"),n._v("“Đến cùng chuyện gì xảy ra?” Trương Huyền nhịn không được nhìn qua."),t("br"),t("br"),n._v("“Chuyện này, liên quan quá lớn, ta không chứng cớ xác thật, tạm thời còn không thể nhiều lời. Cho thấy thân phận, chỉ là muốn hỏi, là không phải là vì tìm kiếm bảo tàng, nếu như đúng vậy, ta có lẽ có thể giúp một tay!”"),t("br"),t("br"),n._v("Hồ Yêu Yêu lắc đầu."),t("br"),t("br"),n._v("“Đã ngươi là Ngô Dương Tử tiền bối hậu nhân, cũng không có gì có thể giấu diếm ngươi, chúng ta tìm chỗ ở cũ, quả thực là vì tiền bối lưu lại bảo tàng!”"),t("br"),t("br"),n._v("Đối phương thừa nhận thân phận, Trương Huyền cũng không có gì có thể giấu diếm, nhẹ gật đầu: “Bất quá, hắn cho tin tức, mười phần mơ hồ, chỉ nói tại chỗ ở cũ lưu lại đồ vật, cụ thể là cái gì, lại ở nơi nào, chúng ta cũng không rõ ràng!”"),t("br"),t("br"),n._v("Mặc dù thừa nhận, Ngô Dương Tử lưu lại khẩu quyết nhưng không nói ra."),t("br"),t("br"),n._v("Không phải hắn không tin Hồ Yêu Yêu, mà là liên quan quá lớn, hết thảy đều phải cẩn thận cho thỏa đáng."),t("br"),t("br"),n._v("“Vật lưu lại là cái gì, ta hẳn phải biết một chút, bất quá... Nơi này ta cũng đã tới rất nhiều lần, cũng không tìm được bảo tàng dấu vết!”"),t("br"),t("br"),n._v("Hồ Yêu Yêu cũng vẻ mặt vô cùng nghi hoặc."),t("br"),t("br"),n._v("Thân là Ngô Dương Tử hậu nhân, tổ tông liền lưu truyền bảo tàng tin tức, chỉ là... Cụ thể mai táng địa phương, chỉ có Tiên Tổ một người biết được, coi như nàng, nhiều lần tới, đều không có phát giác được bất cứ dị thường nào, chớ nói chi là tìm tới đồ vật."),t("br"),t("br"),n._v("“Trước không vội vã, đã nơi này là phủ đệ của ta, cuối cùng có thời gian tìm kiếm!”"),t("br"),t("br"),n._v("Khoát tay áo, Trương Huyền không trong vấn đề này tiếp tục dây dưa."),t("br"),t("br"),n._v("Ngô Dương Tử từng nói, tại nhà cũ tam tinh Địa Long chỗ, còn có có thể khiến người ta tàng thai phá Thánh đồ vật, chỉ cần tìm được, để lên ba cây trận kỳ, vật phẩm sẽ tự động nhảy ra."),t("br"),t("br"),n._v("Tam tinh Địa Long là cái gì, hắn đến bây giờ đều không rõ ràng, vị trí cụ thể tự nhiên cũng liền không thể nào dò xét."),t("br"),t("br"),n._v(" Bất quá, đã tìm đến lúc đó, chỉ phải thật tốt tìm kiếm, luôn có thể thành công, nhiều nhất chậm trễ chút thời gian mà thôi."),t("br"),t("br"),n._v("“Chỉ sợ... Cái gọi là bảo vật, không chỉ tàng thai phá Thánh đơn giản như vậy!”"),t("br"),t("br"),n._v("Rõ ràng những này, hắn cũng biết, Ngô Dương Tử bảo tàng, chắc chắn sẽ không chỉ có tàng thai phá Thánh đơn giản như vậy, nếu quả thật dễ dàng như vậy, liền không khả năng xuất hiện Hồ Yêu Yêu nói, hậu nhân mai danh ẩn tích, không dám bại lộ chân diện mục."),t("br"),t("br"),n._v("Lại hỏi Hồ Yêu Yêu một chút liên quan tới nhà cũ tin tức, niên đại xa xưa, lại thêm tòa phủ đệ này ít nhất thay đổi mười mấy cái chủ nhân, liền xem như hậu bối, cũng không thể nào biết được hơn hai ngàn năm trước tình cảnh."),t("br"),t("br"),n._v("Suy nghĩ một chút cũng có thể rõ ràng."),t("br"),t("br"),n._v("Hồ Yêu Yêu nếu là biết những này, bảo tàng khả năng sớm đã bị cầm đi, lại làm sao có thể lưu đến bây giờ."),t("br"),t("br"),n._v("“Được rồi, các ngươi trước tiên tìm một nơi nghỉ ngơi, ta cứu chữa Ngụy Như Yên lại nói.”"),t("br"),t("br"),n._v("Hàn huyên một hồi, Tôn Cường cùng Thiên Hùng thú tướng Ngụy Như Yên mang đi qua, Trương Huyền không nói thêm lời, nghênh đón tiếp lấy."),t("br"),t("br"),n._v("Đi qua linh hồn tẩm bổ, Ngụy Như Yên trạng thái so hai ngày trước muốn tốt hơn nhiều, có điều, vẫn như cũ nằm ở ngủ mê mệt, không có sáng suốt."),t("br"),t("br"),n._v("Đem hắn đặt ở linh khí tập trung địa phương, chuẩn bị ổn thoả, Trương Huyền bắt đầu cứu chữa."),t("br"),t("br"),n._v("Vu hồn ly thể, vận dụng pháp quyết, đem đối phương linh hồn, chuyển dời đến Thập Diệp hoa bên trên, toàn bộ chuẩn bị ổn thoả, lúc này mới thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Thập Diệp hoa tẩm bổ hồn phách, cần thời gian nhất định, ít nhất cũng phải ba ngày, cho nên cũng không vội vã."),t("br"),t("br"),n._v("Lấy ra địa mạch linh dịch, đem Ngụy Như Yên thân thể tẩm bổ một phen, Trương Huyền lông mày nhăn lại."),t("br"),t("br"),n._v("“Địa mạch linh dịch, chỉ có thể duy trì nhục thể của nàng, không chịu đến tổn thương, muốn để nàng khôi phục sinh cơ, còn cần Đoạn Tục đan trợ giúp!”"),t("br"),t("br"),n._v("Đoạn Tục đan, là bảy cấp đan dược, thực lực của hắn bây giờ, khẳng định là luyện chế không ra được."),t("br"),t("br"),n._v("Hơn nữa, luyện chế thứ này đan phương, rất nhiều dược liệu, càng là đồng dạng đều không có."),t("br"),t("br"),n._v("“Xem ra, vẫn là muốn đi luyện đan học viện đi dạo...” Thở ra một hơi."),t("br"),t("br"),n._v("Kế trước mắt, vẫn là muốn đi luyện đan công hội một chuyến, đem bản thân luyện đan năng lực, học được lục tinh cấp bậc, hướng tổng bộ xin Đoạn Tục đan đan phương, sau đó lại tìm một vị lục tinh luyện đan sư, hỗ trợ luyện chế..."),t("br"),t("br"),n._v("“Khoảng cách ban đêm còn sớm, đi một chuyến luyện đan học viện!”"),t("br"),t("br"),n._v("Theo Vân Vụ lĩnh trở lại học viện thời điểm, ngày mới sáng lên không lâu, khiêu chiến Y Sư công hội, cùng Vưu Hư sinh tử đấu, nói đến phức tạp, trên thực tế cũng là chừng nửa canh giờ."),t("br"),t("br"),n._v("Về sau đi tới tòa phủ đệ này, an bài ở lại, cứu chữa Ngụy Như Yên, một phen chơi đùa, hiện tại cũng chỉ vừa vặn giữa trưa, khoảng cách Lạc Nhược Hi ước định ban đêm, còn có một đoạn thời gian rất dài."),t("br"),t("br"),n._v("Tục ngữ nói báo thù không cách đêm, tại trên đài cao như là đã nói muốn đi luyện đan học viện, nhàn rỗi cũng là nhàn rỗi, không đi qua lượn một vòng, há không ra vẻ mình không đầy nghĩa khí?"),t("br"),t("br"),n._v("Nói chuyện không tính toán gì hết?"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Luyện đan học viện rộng rãi gian phòng bên trong, Lục Phong trầm thấp mặt ngồi ở trong đó."),t("br"),t("br"),n._v("Phía dưới, bốn cái ông lão vây quanh cái bàn, nguyên một đám cũng đều sắc mặt xanh xám."),t("br"),t("br"),n._v("“Tìm các ngươi tới, thương nghị một chút, làm sao đem Vưu phó viện trưởng cứu ra!” Bàn tay lớn vẫy một cái, Lục Phong nhìn quanh một vòng."),t("br"),t("br"),n._v("“Viện trưởng, Vưu phó viện trưởng rơi vào như thế tình cảnh, cũng là hắn gieo gió gặt bão, danh sư, lấy tin lập gốc, nói không giữ lời, đồng thời hại chết một vị Thánh giả, nộp lên đến giáo sư toà án lời nói, đầy đủ phán hình!”"),t("br"),t("br"),n._v("Một vị lão giả lắc đầu: “Lại nói, hắn là y sư sinh tử đấu, đường đường chính chính bại bởi người khác, coi như muốn cứu, cũng không có lấy cớ, ta nhìn... Vẫn là thôi đi!”"),t("br"),t("br"),n._v("Nếu như Trương Huyền tại đây, khẳng định có thể nhận ra, chính là vị kia Chu phó viện trưởng, Chu Kình."),t("br"),t("br"),n._v("Lúc trước công khai khóa thời điểm, chính là vị này, tự mình cho Lạc Nhược Hi bố trí sân bãi."),t("br"),n._v("“Quên đi? Nói đùa cái gì! Nếu như không phải Vưu phó viện trưởng trồng trọt Thánh vực dược liệu, ngươi có thể luyện chế ra cấp sáu đỉnh phong đan dược? Thuật luyện đan của ngươi, có thể đề cao nhiều như vậy?” Một cái ông lão ngắt lời hắn."),t("br"),t("br"),n._v("“Không sai, Vưu phó viện trưởng đối với chúng ta luyện đan học viện, có ân tình, không thể tùy ý như vậy bắt đi, huống chi, vẫn là cái học sinh!”"),t("br"),t("br"),n._v("Lại một cái lão giả nói."),t("br"),t("br"),n._v("“Chuyện này nhất định phải nghiêm túc xử lý, nếu không, ai cũng có thể làm loạn, danh sư học viện, còn thể thống gì?” Cái cuối cùng lão giả nói."),t("br"),t("br"),n._v("Luyện đan sư học viện cùng học viện khác đồng dạng, cũng có bốn vị phó viện trưởng."),t("br"),t("br"),n._v("“Ta cũng biết Vưu Hư đối với chúng ta có ân, nhưng... Chuyện này lỗi tại hắn, đối phương cũng là dựa theo quy củ làm việc, y sư học viện đều không nói gì, chúng ta nếu là lại tìm phiền toái lời nói, ta sợ... Sẽ bị những người khác nói độ lượng thấp!”"),t("br"),t("br"),n._v("Chu phó viện trưởng cau mày."),t("br"),t("br"),n._v("“Độ lượng thấp? Hừ, cái tên này mắt không trưởng ấu, tại học viện ngang nhiên không cho viện trưởng mặt mũi, không hảo hảo dạy dỗ, chẳng phải người người đều cho là chúng ta luyện đan học viện dễ khi dễ?”"),t("br"),t("br"),n._v("Cái thứ nhất lão giả nói."),t("br"),t("br"),n._v("“Rõ ràng là viện trưởng nhúng tay...” Chu phó viện trưởng cau mày, nói còn chưa dứt lời, liền nghe đến trên đài Lục Phong viện trưởng bàn tay lớn vẫy một cái."),t("br"),t("br"),n._v("“Đủ rồi! Để các ngươi tới, là thương nghị sự tình xử lý như thế nào, không phải cãi nhau.”"),t("br"),t("br"),n._v("Lục Phong mặt mũi buồn bực."),t("br"),t("br"),n._v("Hắn đường đường danh sư học viện đại diện viện trưởng, bị một người hai mươi tuổi gia hỏa xem thường không nói, còn làm tiến thoái lưỡng nan, suy nghĩ một chút đã cảm thấy kìm nén hỏa."),t("br"),t("br"),n._v("“Vị này Trương Huyền, vừa đến học viện, liền huyên náo dư luận xôn xao, gà chó không yên, mặc kệ hắn thiên phú thế nào, về sau tiềm lực như thế nào, chúng ta danh sư học viện, đều không cho phép có loại này siêu thoát quản giáo tồn tại!”"),t("br"),t("br"),n._v("Thấy mọi người yên tĩnh, Lục Phong viện trưởng khoát tay áo."),t("br"),t("br"),n._v("“Lại nói, y sư học viện, hắn đã công khai thừa nhận, không phải học sinh, ta đã phái người quét dọn tinh anh khu chỗ ở của hắn, từ hôm nay trở đi, liền để hắn dọn ra ngoài, không cho phép bước vào nửa bước!”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Chu phó viện trưởng tràn đầy ở gấp: “Viện trưởng, bất kể nói thế nào, Trương Huyền đều chiếm được Mi viện trưởng, Triệu viện trưởng đám người coi trọng, làm như vậy... Có thể hay không để bọn hắn mấy vị bất mãn?”"),t("br"),t("br"),n._v("Coi như đối phương không phải học viện học viên, nhận được Mi viện trưởng đám người coi trọng, ở chỗ này cũng không tính là gì, trực tiếp đuổi đi, há không phải là đem da mặt đều xé toang?"),t("br"),t("br"),n._v("“Bất mãn? Ta là dựa theo học viện quy củ làm việc, có thể có gì bất mãn?” Lục Phong cười lạnh: “Học viện chung quanh nhà, tấc đất tấc vàng, trước đuổi ra ngoài, để hắn không chỗ có thể ở, lại nghĩ biện pháp dạy dỗ!”"),t("br"),t("br"),n._v("Chu phó viện trưởng sắc mặt tái xanh, chính muốn tiếp tục khuyên can, liền nghe phía ngoài vang lên tiếng đập cửa."),t("br"),t("br"),n._v("“Viện trưởng, năm thứ hai học viên, Hoàng Hưng cầu kiến!”"),t("br"),t("br"),n._v("“Vào đi!”"),t("br"),t("br"),n._v("Nghe được là quản lý tinh anh khu Hoàng Hưng, biết sự tình khẳng định xong xuôi, Lục Phong nhẹ gật đầu."),t("br"),t("br"),n._v("“Hoàng Hưng gặp qua chư vị viện trưởng!”"),t("br"),t("br"),n._v("Vừa vào cửa, nhìn thấy luyện đan sư học viện chính phó năm vị viện trưởng đều tại, Hoàng Hưng giật nảy mình, vội vàng ôm quyền."),t("br"),t("br"),n._v("“Để ngươi làm sự tình thế nào?”"),t("br"),t("br"),n._v("Lục Phong nhìn qua."),t("br"),t("br"),n._v("“Bẩm báo viện trưởng... Ta đem Trương sư đồ vật đã quét dọn ra ngoài, đem biệt viện thu hồi...” Khóe miệng giật một cái, Hoàng Hưng vội vàng trả lời."),t("br"),t("br"),n._v("“Làm không tệ!” Hài lòng nhẹ gật đầu, Lục Phong vuốt râu."),t("br"),t("br"),n._v("Một cái tân sinh muốn cùng hắn đấu, vẫn là thiếu chút hỏa hầu."),t("br"),t("br"),n._v("Mặc dù băn khoăn danh sư thân phận, không thể làm mặt ra tay, nhưng bằng mượn hắn tại danh sư học viện thậm chí Hồng Viễn thành, nhiều năm tích lũy uy vọng, để một tiểu nhân vật không vượt qua nổi, vẫn là rất nhẹ nhàng."),t("br"),t("br"),n._v("“Hắn hiện tại thế nào? Có phải hay không chật vật khắp nơi tìm chỗ ở? Nói cho thương hội, ai cũng không cho phép thuê, thậm chí bán ra, nếu không, ta đem sẽ tự mình đi tìm phiền phức!”"),t("br"),t("br"),n._v("Lục Phong vung vung tay."),t("br"),t("br"),n._v("“Cái này...” Hoàng Hưng chần chờ một chút: “Bẩm báo viện trưởng, hắn... Không đi tìm chỗ ở!”"),t("br"),t("br"),n._v("“Không tìm? Chẳng lẽ còn muốn vu vạ tinh anh khu không đi?” Lục Phong cau mày."),t("br"),t("br"),n._v("“Không phải... Là, ta vừa đem Trương sư đồ vật dời ra ngoài, Hoài Vương gia liền đưa hắn một tòa cự đại phủ đệ, chiếm diện tích hơn ba trăm mẫu, trang trí tráng lệ, có thể so với hoàng cung... Sau đó, sau đó hắn liền chuyển vào đi!”"),t("br"),t("br"),n._v("Hoàng Hưng vẻ mặt đỏ lên."),t("br"),t("br"),n._v("“Hoài Vương gia... Tặng phủ đệ?” Lục Phong con mắt trợn tròn."),t("br"),t("br"),n._v("Mới vừa nói đối phương bị đuổi đi không chỗ ở, nhân gia liền đưa một tòa biệt thự, thật hay giả?"),t("br"),t("br"),n._v("“Vâng!” Hoàng Hưng gật đầu."),t("br"),t("br"),n._v("Mặc dù không muốn nói, nhưng sự thật chính là như thế... Vị này Lục viện trưởng thu nhân gia chỗ ở làm pháp, chẳng những không có bất luận cái gì trứng dùng, còn hoàn toàn đem người đắc tội."),t("br"),t("br"),n._v("Sắc mặt tái xanh, Lục Phong đang định mảnh hỏi một chút chuyện gì xảy ra, chỉ thấy một thanh niên vội vã đi đến."),t("br"),t("br"),n._v("“Viện trưởng... Không xong!”"),t("br"),t("br"),n._v("Đúng là hắn học sinh thân truyền."),t("br"),t("br"),n._v("“Làm sao vậy?” Thấy đối phương hốt hoảng như vậy, Lục Phong cau mày."),t("br"),t("br"),n._v("“Là... Trương Huyền, hắn đến, hắn đến luyện đan học viện!”"),t("br"),t("br"),n._v("Thanh niên gấp gáp hô lên."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-824"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-826"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);