(window.webpackJsonp=window.webpackJsonp||[]).push([[2111],{2463:function(n,h,t){"use strict";t.r(h);var c=t(44),g=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-822-phuong-ngo-tuy"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-822-phuong-ngo-tuy"}},[n._v("#")]),n._v(" Chương 822: Phượng Ngô tủy")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 822: Phượng Ngô tủy"),t("br"),t("br"),n._v("Đoạn Tục đan, đoạn chi đều có thể lại nối tiếp, phi thường nổi danh, nguyên nhân chính là như thế, luyện chế, rất khó!"),t("br"),t("br"),n._v("Dược vật phẩm cấp, tại bảy cấp bên trong, đều bài bên trên khá cao, bình thường thất tinh luyện đan sư, đều khó mà hoàn thành, bọn họ làm sao có thể làm đến?"),t("br"),t("br"),n._v("Thượng cửu lưu chức nghiệp, vượt qua ngũ phẩm, mỗi một cái cấp bậc chi ở giữa chênh lệch liền sẽ càng lúc càng lớn."),t("br"),t("br"),n._v("Thất tinh cùng lục tinh, mặc dù chỉ cách xa một cái cấp bậc, nhưng cùng Thánh giả cùng linh cảnh một dạng, không thể so sánh nổi."),t("br"),t("br"),n._v("Thậm chí cùng là thất tinh, hạ phẩm cùng thượng phẩm, cũng có thiên địa khác biệt."),t("br"),t("br"),n._v("Nói cách khác... Bọn họ mặc dù đều đạt đến lục tinh luyện đan sư đỉnh phong, bất cứ lúc nào cũng sẽ đột phá, nhưng muốn luyện chế thất tinh thượng phẩm luyện đan sư mới có thể luyện chế Đoạn Tục đan, còn là không thể nào làm được."),t("br"),t("br"),n._v("“Cái này... Chúng ta chỉ sợ có lòng không đủ lực!”"),t("br"),t("br"),n._v("Cười khổ một tiếng, Lỗ đan sư nhịn không được lắc đầu."),t("br"),t("br"),n._v("“Ta biết độ khó, mới hi vọng các ngươi liên thủ!” Trương Huyền nói."),t("br"),t("br"),n._v("“Liên thủ? Chúng ta... Không phối hợp qua, sao có thể liên thủ?” Mọi người lắc đầu."),t("br"),t("br"),n._v("Luyện đan không phải chiến đấu, không phải thi triển võ kỹ, có thể phối hợp, có thể lẫn nhau chiếu ứng... Mỗi một vị luyện đan sư, đều có phương pháp của mình cùng trình tự, cùng một chỗ luyện chế lời nói, thường thường sẽ xuất hiện sai lầm, nhẹ thì dược liệu toàn bộ phế, nặng thì nổ lô!"),t("br"),t("br"),n._v("Liên hợp luyện đan cũng có, nhưng phần lớn đều là đồng môn sư huynh đệ, phối hợp vượt qua mười năm, mấy chục năm, lẫn nhau lý giải, tâm ý tương thông, mới có thể thân mật vô gian, không tỳ vết chút nào."),t("br"),t("br"),n._v("Giống như bọn họ như vậy, đều đắm chìm trong thế giới của mình mấy trăm năm, có thuộc tại phong cách của mình, thật muốn liên thủ, không luyện được đan dược ngược lại là thứ yếu... Làm không cẩn thận, toàn bộ danh sư học viện đều sẽ bị nổ bay."),t("br"),t("br"),n._v("Đoạn Tục đan, thân là bảy cấp khá cao đan dược, trong đó năng lượng ẩn chứa, đã cùng Thánh vực tứ trọng cường giả tương đương, một khi không may xuất hiện, chỉ bằng mấy người bọn hắn Thánh vực nhất trọng tu vi muốn áp chế... Gần như không có khả năng làm đến."),t("br"),t("br"),n._v("“Cái này không cần lo lắng, ta chỗ này có mấy bộ thủ pháp luyện đan, hi vọng các ngươi có thể mau chóng học được học quen, còn như thế nào phối hợp, đến lúc đó chỉ cần nghiêm ngặt nghe theo ta, liền sẽ không có vấn đề gì!”"),t("br"),t("br"),n._v("Trương Huyền lấy ra mấy quyển sách đưa tới."),t("br"),t("br"),n._v("Đúng là hắn giảng xong khóa, cẩn thận cân nhắc xong Đoạn Tục đan phương pháp luyện chế, viết ra."),t("br"),t("br"),n._v("“Thủ pháp?”"),t("br"),t("br"),n._v("Lỗ đan sư đám người tiếp nhận, cúi đầu nhìn sang."),t("br"),t("br"),n._v("Bọn họ mỗi người cầm tới đều không giống nhau, bất kể tên, hay là luyện đan thủ pháp, đều tự thành hệ thống."),t("br"),t("br"),n._v("“Yên Vũ luyện đan pháp...”"),t("br"),t("br"),n._v("Rất mau đem bản thân nhìn một lần, Lỗ đan sư thần sắc càng ngày càng ngưng trọng."),t("br"),t("br"),n._v("Bộ này Yên Vũ luyện đan pháp, coi trọng gió nhẹ hóa mưa, nhuận vật im ắng, thủ pháp cao chi rườm rà, bình sinh chưa bao giờ thấy qua, nhờ vào hắn lục tinh đỉnh phong luyện đan sư hiểu biết, có thể đoán được, chỉ cần luyện thành, cả người luyện đan trình độ, tuyệt đối sẽ tăng lên một mảng lớn!"),t("br"),t("br"),n._v("Chỉ cần tu vi đi đến, xung kích thất tinh luyện đan sư, đều dễ như trở bàn tay!"),t("br"),t("br"),n._v("Trọng yếu nhất chính là... Cùng trong cơ thể hắn tu luyện công pháp, hoàn mỹ phối hợp, bộ này thủ pháp, không chỉ luyện đan, thực lực cũng có thể tốt hơn phát huy."),t("br"),t("br"),n._v("“Người có nghề pháp, cùng ta linh quy chân khí hợp nhau lại càng tăng thêm sức mạnh...”"),t("br"),t("br"),n._v("“Bản này cũng cùng công pháp của ta hoàn mỹ dung hợp...”"),t("br"),t("br"),n._v("Những người khác nhìn xong, cũng từng cái con mắt tỏa ánh sáng, nhịn không được hô lên."),t("br"),t("br"),n._v("Quay đầu nhìn sang, Lỗ đan sư đem mấy vị bằng hữu thư tịch tất cả đều lật một lần, lông mày nhịn không được cuồng loạn."),t("br"),t("br"),n._v("Cái này mấy bộ thủ pháp, thế mà cùng mấy người bọn họ tu luyện pháp quyết, lực lượng, hoàn mỹ phù hợp, tìm không ra chút nào thiếu sót."),t("br"),t("br"),n._v("Nói cách khác... Là Trương sư chuyên môn vì mấy người bọn họ, thiết kế ra được thủ pháp!"),t("br"),t("br"),n._v("Chuyên môn thiết kế thủ pháp luyện đan, đo thân mà làm... Cái này muốn đối luyện đan sâu sắc bao nhiêu lý giải, mới có thể làm đến?"),t("br"),t("br"),n._v("Coi như một chút thất tinh đỉnh phong nhất luyện đan sư cũng làm không được đi!"),t("br"),t("br"),n._v("“Cái này, cái này...”"),t("br"),t("br"),n._v("Hắn phát hiện vấn đề này, những người khác cũng phát hiện, nguyên một đám mở to hai mắt nhìn, lần nữa nhìn về phía Trương Huyền, tràn đầy sùng bái."),t("br"),t("br"),n._v("Trước đó nghe giảng bài, chẳng qua là cảm thấy bội phục, mà bây giờ, là thật bị trước mắt vị thanh niên này học thức thuyết phục."),t("br"),t("br"),n._v("“Dược liệu Trương sư có thể chuẩn bị xong? Theo ta được biết, Đoạn Tục đan chủ dược, Đoạn Tục thảo, nhưng mà rất ít gặp Thánh dược!”"),t("br"),t("br"),n._v("Sau khi hết khiếp sợ, Lỗ đan sư nhịn không được nhìn qua."),t("br"),t("br"),n._v("Không bột đố gột nên hồ."),t("br"),t("br"),n._v("Luyện đan, dược liệu là trọng yếu nhất, có điều, bảy cấp đan dược dược liệu, phong hào đế quốc tìm ra được không khó, nhưng ở Hồng Viễn thành, nhưng cực kỳ không dễ dàng."),t("br"),t("br"),n._v("Nhất là làm làm chủ dược Đoạn Tục thảo, thưa thớt, không cách nào trồng trọt không nói, còn nhiều sinh ra ở độc trùng ẩn hiện chỗ, coi như Thánh vực ba, bốn nặng cường giả, muốn có được, đều không dễ dàng như vậy."),t("br"),t("br"),n._v("“Đoạn Tục thảo, ta đã hướng luyện đan sư công hội tổng bộ xin.”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Nhận được Đoạn Tục đan đan phương, là hắn biết làm làm chủ dược Đoạn Tục thảo, tại Hồng Viễn thành là rất khó mua được, liền trực tiếp hướng tổng bộ tiến hành xin."),t("br"),t("br"),n._v("“Hướng tổng bộ xin?”"),t("br"),t("br"),n._v("Lỗ đan sư cùng mấy vị khác đan sư liếc mắt nhìn nhau, đồng thời sửng sốt: “Đoạn Tục thảo giá trị cao như vậy, có thể dựa vào xin nhận được?”"),t("br"),t("br"),n._v("Đoạn Tục thảo loại cấp bậc này Thánh dược, coi như hướng tổng bộ mua sắm, vẫn như cũ cần phải hao phí cực lớn đánh đổi, ít nhất mấy trăm thậm chí bên trên ngàn linh thạch thượng phẩm, xin... Làm sao có thể!"),t("br"),t("br"),n._v("Hướng tổng bộ mua sắm cùng hướng tổng bộ xin, cái này là hai chuyện khác nhau."),t("br"),t("br"),n._v("Xin bình thường là chỉ tổng bộ trợ giúp, không thu lệ phí, mà mua sắm, thì là trả giá đắt."),t("br"),n._v("Thứ này có thể dựa vào thân mời tới được? Nếu như đơn giản như vậy, luyện đan sư tổng bộ, chỉ sợ sớm đã bị phía dưới luyện đan sư xin đảo bế."),t("br"),t("br"),n._v("“Ta đúng lúc giải quyết một cái gia hỏa vấn đề, hắn mở miệng cam đoan giúp ta xin... Có lẽ có thể chứ!” Trương Huyền suy nghĩ một chút, nói."),t("br"),t("br"),n._v("Đưa tin tường, chỉ có thể tiếp thu tin tức, mà luyện đan sư học viện đưa tin ngọc bình phong, hoàn toàn có thể đem đối diện hình ảnh truyền tới."),t("br"),t("br"),n._v("Trước đó, khảo hạch luyện đan sư thời điểm, từng có người đem luyện chế đan dược hình ảnh truyền tới, ra tay giải quyết mấy chỗ sai lầm, nhận được đối phương bội phục, lúc này mới hét lớn giúp hắn xin."),t("br"),t("br"),n._v("Nghe đối phương khẩu khí rất đơn giản, chẳng lẽ, tại lừa gạt mình?"),t("br"),t("br"),n._v("Lỗ đan sư sững sờ, khẽ nhíu mày: “Liền xem như tổng bộ luyện đan sư, không có địa vị nhất định, muốn xin dược liệu cũng rất khó. Ngươi nói vị này luyện đan sư nhưng biết tên?”"),t("br"),t("br"),n._v("“Hình như gọi cái gì... Ngô Hóa Vũ! Lúc ấy truyền lại hình ảnh luyện đan sư có hơn mười vị, liền hắn nói nhiều...”"),t("br"),t("br"),n._v("Cẩn thận nhớ lại một chút, Trương Huyền đạo, nói còn chưa dứt lời, chỉ thấy trước mắt mấy cái ông lão, tròng mắt nhanh rơi trên mặt đất, nguyên một đám dọa đến phát run."),t("br"),t("br"),n._v("“Làm sao vậy?”"),t("br"),t("br"),n._v("Thấy bọn họ bộ dáng này, Trương Huyền mày nhăn lại."),t("br"),t("br"),n._v("“Trương sư... Nhưng biết Ngô Hóa Vũ là ai?” Lỗ đan sư bờ môi run rẩy."),t("br"),t("br"),n._v("“Ta làm sao biết là ai, nói nhảm nhiều, người lại đần, mười phần ngốc hàng, làm sao, các ngươi nhận biết?” Trương Huyền nhịn không được nói."),t("br"),t("br"),n._v("“Ngốc hàng?”"),t("br"),t("br"),n._v("“Khụ khụ...”"),t("br"),t("br"),n._v("Suýt chút nữa sặc nước bọt mà chết, Lỗ đan sư thấy trước mắt vị này quả thực không biết, đành phải cười khổ lắc đầu: “Ngô Hóa Vũ Ngô đan sư, là Thanh Nguyên phong hào đế quốc luyện đan sư phân bộ hội trưởng... Thất tinh đỉnh phong luyện đan sư!”"),t("br"),t("br"),n._v("Mấy người khác cũng vội vàng gật đầu, nguyên một đám nhanh muốn khóc."),t("br"),t("br"),n._v("Coi như ngươi không biết Ngô hội trưởng thì cũng thôi đi, nói thẳng nhân gia ngốc hàng..."),t("br"),t("br"),n._v("Đây chính là Thanh Nguyên phong hào đế quốc luyện đan người số một... Tại trong miệng ngươi không phải là bất cứ cái gì!"),t("br"),t("br"),n._v("Mấu chốt nhất, còn giúp hắn giải quyết vấn đề..."),t("br"),t("br"),n._v("Trước mắt vị này... Đến cùng là cái dạng gì quái thai?"),t("br"),t("br"),n._v("“Hội trưởng? Tên kia là hội trưởng? Khó trách...”"),t("br"),t("br"),n._v("Trương Huyền giờ mới hiểu được tới, nhẹ gật đầu, không trong vấn đề này tiếp tục dây dưa: “Nếu là hội trưởng, nói giúp ta xin, liền khẳng định có thể xin đến, còn cái khác phụ trợ dược liệu, cấp bậc không cao, ta liệt kê một cái danh sách, có thể còn muốn phiền phức mấy vị giúp ta tìm tìm một cái!”"),t("br"),t("br"),n._v("Nói xong tay lấy ra giấy trắng, lấy ra bút lông đem còn lại dược liệu cùng nhau ghi đi ra."),t("br"),t("br"),n._v("“Các ngươi là luyện đan sư, con đường rộng, trước giúp ta mua lại, giá trị không đủ, ta có thể dùng cấp sáu đan dược hối đoái...”"),t("br"),t("br"),n._v("Đem phương thuốc đưa tới, Trương Huyền nói."),t("br"),t("br"),n._v("Làm phiền người khác hỗ trợ tìm dược, tự nhiên không thể còn để bọn hắn bỏ tiền."),t("br"),t("br"),n._v("Đúng lúc trong tay hắn, còn thừa lại không ít cấp sáu đan dược, hoàn toàn có thể dùng đến hối đoái dược liệu."),t("br"),t("br"),n._v("“Còn lại những dược liệu này, giá trị cũng không cao...”"),t("br"),t("br"),n._v("Tiếp nhận phương thuốc, Lỗ đan sư nhìn một lần, mở miệng nói: “Đến không cần Trương sư bỏ tiền, chúng ta trong tay tư tàng đến một chút, có lẽ có thể. Một để báo đáp giảng bài chi ân, thứ hai, cũng coi như đổi thành chống đỡ Trương sư truyền thụ cho thủ pháp luyện đan.”"),t("br"),t("br"),n._v("Làm luyện đan sư công hội túc lão, trên người tư tàng dược vật khẳng định không ít, Đoạn Tục đan liền xem như bảy cấp đan dược, chủ dược có lời nói, cái khác bọn họ vẫn có thể kiếm ra không ít."),t("br"),t("br"),n._v("Làm lục tinh đỉnh phong luyện đan sư, danh sư học viện túc lão, nghe xong đối phương khóa, lại lấy không độc môn thủ pháp luyện đan, không báo lại vài thứ, thực sự nhận lấy thì ngại."),t("br"),t("br"),n._v("Mấy bụi dược liệu mà thôi, bọn họ có, trực tiếp cho mất cũng không tính là gì."),t("br"),t("br"),n._v("“Vậy xin đa tạ rồi!”"),t("br"),t("br"),n._v("Ánh mắt sáng lên, Trương Huyền vội vàng ôm quyền."),t("br"),t("br"),n._v("“Trương sư trước không vội cảm kích, ta lời còn chưa nói hết...” Lỗ đan sư liên tục vẫy tay: “Chúng ta mấy cái là có thể đem còn lại dược liệu gom góp, có điều, còn có nhất vị, có thể còn cần Trương sư bản thân đi tìm.”"),t("br"),t("br"),n._v("“Cái gì?” Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Chính là... Ngươi viết cuối cùng một dạng, !” Lỗ đan sư nói."),t("br"),t("br"),n._v("“Phượng Ngô tủy?” Trương Huyền cau mày: “Cái này... Các ngươi tìm không thấy?”"),t("br"),t("br"),n._v("Loại thuốc này vật, hắn vừa rồi cũng nhìn thấy, không phải là Thánh dược, cũng không phải linh dược, thậm chí, liền dược liệu cũng không bằng."),t("br"),t("br"),n._v("Vốn cho rằng là tốt nhất tìm, liền ghi tại cuối cùng, không nghĩ mấy vị này túc lão, thế mà lại nói tìm không thấy."),t("br"),t("br"),n._v("“Phượng Ngô tủy là Phượng Ngô thụ tinh túy, nắm giữ cầm máu, sinh cơ tác dụng, cùng cái khác dược liệu so ra, quả thực không tính là trân quý, nhưng mười phần thưa thớt... Chúng ta mấy cái quả thực không có, hơn nữa toàn bộ luyện đan sư học viện cùng y sư học viện, chỉ sợ cũng không tìm tới!”"),t("br"),t("br"),n._v("Lỗ đan sư nói."),t("br"),t("br"),n._v("“Luyện đan sư học viện đều không có... Cái kia đi nơi nào tìm?” Trương Huyền cau mày."),t("br"),t("br"),n._v("Luyện đan sư học viện, y sư học viện hẳn là danh sư học viện, dược liệu rất đầy đủ hết, nếu như bọn họ cũng không tìm tới, hai mắt đen thui, khẳng định thì càng không tìm được."),t("br"),t("br"),n._v("“Trương sư chớ sốt ruột, nói thật, Phượng Ngô tủy chúng ta không có, nhưng lại biết nơi nào có, Trương sư nếu là mặt mũi lớn lời nói, có lẽ có thể tìm đến!” Lỗ đan sư nói."),t("br"),t("br"),n._v("“Ồ?” Trương Huyền nhìn qua: “Mong rằng Lỗ đan sư báo cho...”"),t("br"),t("br"),n._v("“Phượng ngô loại này cây cối, lớn nhất công hiệu, cũng không phải là dược dụng mà là rèn luyện đàn ngọc, nếu như ta nhớ không sai, Ma âm viện liền trồng một gốc, nếu như ngươi đi tìm Tưởng Thanh Cầm viện trưởng, cũng có thể nhận được!”"),t("br"),t("br"),n._v("Lỗ đan sư nói."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-830"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-832"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);