(window.webpackJsonp=window.webpackJsonp||[]).push([[2112],{2464:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-823-luc-hac-chi-canh"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-823-luc-hac-chi-canh"}},[n._v("#")]),n._v(" Chương 823: Lục Hạc chi cảnh")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 823: Lục Hạc chi cảnh"),t("br"),t("br"),n._v("“Ma âm viện?” Trương Huyền không hiểu."),t("br"),t("br"),n._v("“Đúng vậy a, phượng ngô loại này cây cối, có thể để cho Phượng Hoàng dừng rơi, đợi hắn trưởng thành, đốn ngã về sau, đặt ở dòng nước bên trong ngâm bảy bảy bốn mươi chín ngày, chế ra Thất huyền cầm, tiếng mỹ động lòng người, mang theo cực mạnh lực xuyên thấu, là vô số Ma âm sư tha thiết ước mơ bảo vật.”"),t("br"),t("br"),n._v("Lỗ đan sư giải thích: “Ma âm viện cái này gốc, là Tưởng Thanh Cầm viện trưởng, năm trăm năm trước tự mình trồng, niên đại xa xưa, ngày ngày có linh khí bổ dưỡng, mười phần trân quý. Có thể có được trong đó gỗ tủy lời nói, tuyệt đối có thể để cho Đoạn Tục đan dược hiệu tiến thêm một bước!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Đối với Ma âm chức nghiệp, hắn một chữ cũng không biết, không nghĩ tới Phượng Ngô tủy còn có dạng này lai lịch."),t("br"),t("br"),n._v("“Tốt, ta đi Ma âm viện đi nghĩ biện pháp mua sắm Phượng Ngô tủy!”"),t("br"),t("br"),n._v("Biết danh sư học viện, liền có thể tìm tới, Trương Huyền thở phào nhẹ nhõm."),t("br"),t("br"),n._v("“Mua sắm? Tưởng Thanh Cầm viện trưởng, luôn luôn không màng danh lợi, ta sợ... Chưa hẳn có thể mua được!” Lỗ đan sư lắc đầu."),t("br"),t("br"),n._v("Trương Huyền cau mày."),t("br"),t("br"),n._v("“Dù sao mặt của chúng ta khẳng định là không đủ, Trương sư vẫn là bản thân đi xem một chút đi, chúng ta cùng vị viện trưởng này không đánh qua liên hệ gì, mảnh nói lời, cũng nói không nên lời như thế về sau! Tóm lại, Trương sư thật muốn mua, ý kiến của ta là trước đưa danh thiếp thăm hỏi, thái độ thành khẩn chút, xác suất thành công có thể sẽ lớn chút.”"),t("br"),t("br"),n._v("Lỗ đan sư cười khan một tiếng: “Nếu là theo tới luyện đan sư học viện như vậy... Ta sợ sẽ hoàn toàn ngược lại!”"),t("br"),t("br"),n._v("Tưởng Thanh Cầm viện trưởng, rất ít cùng người tiếp xúc, bọn họ mặc dù là luyện đan học viện túc lão, nhưng cùng đối phương không quá tiếp xúc nhiều."),t("br"),t("br"),n._v("Tiếp xúc ít, có thể nhìn ra đối phương tính khí cùng bản tính, không dễ dàng ở chung."),t("br"),t("br"),n._v("Cung kính đối đãi lời nói, có lẽ còn có cơ hội, theo vị này Trương sư trước kia cử động, vọt thẳng đi qua trắng trợn phá hư... Sợ sẽ chỉ càng thêm phiền phức."),t("br"),t("br"),n._v("“Yên tâm đi, ta vốn là cũng không phải cái người lỗ mãng!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Lỗ đan sư đám người tập thể im lặng."),t("br"),t("br"),n._v("Ngươi không lỗ mãng, làm luyện khí học viện, y sư học viện cùng luyện đan học viện hầu như đều phế đi, thật muốn lỗ mãng, danh sư học viện vẫn tồn tại ư?"),t("br"),t("br"),n._v("“Tôn Cường, chuẩn bị một chút, chúng ta bây giờ liền đi Ma âm viện!”"),t("br"),t("br"),n._v("Xem khoảng cách Lạc Nhược Hi cùng hắn thời gian ước định còn sớm, Trương Huyền nói một tiếng."),t("br"),t("br"),n._v("“Vâng!” Tôn Cường vội vàng chạy tới."),t("br"),t("br"),n._v("Một chủ một ngã nhào, hỏi rõ phương hướng, nhanh chân hướng Ma âm viện đi đến."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Ma âm viện."),t("br"),t("br"),n._v("Quan lại như hoa Phượng Ngô thụ bên dưới, một cái ông lão, đang an tĩnh đánh đàn."),t("br"),t("br"),n._v("Tiếng đàn lượn lờ, sáu cái tiên hạc, nghe âm mà múa, xoay quanh tại sân nhỏ, phát ra từng tiếng lanh lảnh thanh minh."),t("br"),t("br"),n._v("Hồi lâu, tiếng đàn kết thúc."),t("br"),t("br"),n._v("Ông lão lắc đầu, thở dài một tiếng: “Đáng tiếc, vẫn là kém một chút!”"),t("br"),t("br"),n._v("“Là viện trưởng yêu cầu quá cao, Lục Hạc cộng minh, đã là vô số người tha thiết ước mơ mục tiêu, thật muốn đạt tới Thất Hạc, chỉ sợ đã đi đến thất tinh Ma âm chi cảnh!”"),t("br"),t("br"),n._v("Cách đó không xa một cái ông lão, vuốt râu nói."),t("br"),t("br"),n._v("Mới vừa đánh đàn, chính là viện trưởng Tưởng Thanh Cầm, mà vị này, thì là Ma âm viện Ninh viện phó, Ninh Hải."),t("br"),t("br"),n._v("“Những ngày gần đây, từ đầu đến cuối cảm giác bình chướng đang ở trước mắt, đáng tiếc, chính là không cách nào đột phá!” Tưởng Thanh Cầm lắc đầu."),t("br"),t("br"),n._v("Cách đó không xa sân nhỏ, vừa rồi phi hành tiên hạc đã rơi xuống, đứng tại cách đó không xa, an tĩnh loạn chuyển, chừng mười mấy đầu nhiều."),t("br"),t("br"),n._v("Ma âm, cùng những nghề nghiệp khác một dạng, cũng chia làm rất nhiều cảnh giới, có thể để cho tiên hạc phi hành càng nhiều, nói rõ cảnh giới càng cao."),t("br"),t("br"),n._v("Tưởng Thanh Cầm tấu vang tiếng đàn, có thể để cho Lục Hạc nhảy múa, đã coi như là mười phần đáng sợ."),t("br"),t("br"),n._v("“Đàn tùy tâm sinh, coi như lo lắng, cũng vô dụng, còn không bằng chạy không tâm tình, có lẽ liền có thể một lần hành động đột phá!” Ninh viện phó cười nói."),t("br"),t("br"),n._v("“Như thế... Bất quá, vừa qua học viện sự tình nhiều như vậy, muốn chạy không tâm cảnh nào có dễ dàng như vậy!” Tưởng Thanh Cầm lắc đầu."),t("br"),t("br"),n._v("Hắn cũng muốn chạy không tâm cảnh, hoàn toàn rời xa huyên náo, đáng tiếc, thân là Ma âm viện viện trưởng, việc vặt phong phú, muốn muốn như vậy làm, nói nghe thì dễ."),t("br"),t("br"),n._v("Lại thêm vừa qua, toàn bộ danh sư học viện gà bay chó chạy, không được an sinh, hắn thân ở hắn vị, cũng không có cách nào."),t("br"),t("br"),n._v("“Cũng phải!” Ninh viện phó gật đầu, ngón tay rơi vào trước mặt cổ cầm bên trên: “Dù sao viện trưởng cái này khổ sai sự tình, cho không ta cũng không dám! Ta liền muốn nhàn vân dã hạc, mỗi ngày đánh đàn tự nhạc, cười nhìn gió mây!”"),t("br"),t("br"),n._v("“Ngươi nha...” Nhìn thấy bạn cũ cái bộ dáng này, Tưởng Thanh Cầm cười cười."),t("br"),t("br"),n._v("Bên người vị này Ninh viện phó thực lực không thể so với hắn thấp, hoàn toàn có tư cách làm viện trưởng, chỉ là không nguyện ý mà thôi."),t("br"),t("br"),n._v("Đang muốn nói chuyện, chỉ thấy một người đệ tử vội vã đi đến."),t("br"),t("br"),n._v("“Lão sư, Trương Huyền Trương sư cầu kiến!” Nói đến đưa tới một cái danh thiếp."),t("br"),t("br"),n._v("“Trương Huyền?”"),t("br"),t("br"),n._v("Tưởng Thanh Cầm sững sờ, lập tức khóe miệng giật một cái: “Hắn đến chỗ của ta làm gì? Dường như chúng ta... Không có gì mâu thuẫn ah!”"),t("br"),t("br"),n._v("“Làm sao vậy? Cái này Trương Huyền rất đặc thù?” Thấy Tưởng Thanh Cầm bộ dáng này, Ninh viện phó nghi hoặc."),t("br"),t("br"),n._v("Hắn mặc dù mệnh danh phó viện trưởng, nhưng trên thực tế, học viện sự tình, cơ bản mặc kệ, ngày ngày đánh đàn tự nhạc, Trương Huyền động tĩnh huyên náo như thế lớn, nhưng cũng không cảm kích."),t("br"),t("br"),n._v("“Nào chỉ là đặc thù, cái tên này quả thực chính là phá hư chi vương, đi đến đâu, chỗ nào gà chó không yên...” Tưởng Thanh Cầm đem cái tên này sự tình đơn giản nói một lần."),t("br"),t("br"),n._v("“Còn có lợi hại như vậy thiên tài?” Ninh viện phó sững sờ."),t("br"),t("br"),n._v("Hắn nghe qua một hạng chức nghiệp ngưu bức, cũng nghe qua hai hạng, có thể hai mươi tuổi tân sinh, xông mấy cái học viện, bó tay toàn tập, cũng có chút không thể tưởng tượng nổi."),t("br"),t("br"),n._v("“Đúng vậy a! Chúng ta Ma âm viện cùng hắn không có chút nào thù hận, lúc này chạy tới đây làm gì?” Tưởng Thanh Cầm vẻ mặt có chút không dễ nhìn."),t("br"),t("br"),n._v("Hắn làm người đạm bạc, rất nhiều chuyện đều không để trong lòng, có thể... Cái tên này bị Lục Phong truyền như lang như hổ, coi như hắn cũng là có chút e ngại."),t("br"),t("br"),n._v("Vô duyên vô cớ, liền chạy tới Ma âm viện... Không phải là đến phá Ma âm viện a!"),t("br"),n._v(" “Dựa theo ngươi mới vừa nói cái nào sự tình đến xem... Ngược lại thật sự là có khả năng!”"),t("br"),t("br"),n._v("Ninh viện phó lông mày cũng phải nhảy một cái."),t("br"),t("br"),n._v("“Như vậy đi, liền nói ta bây giờ tại bế quan, không có cách nào gặp nhau...”"),t("br"),t("br"),n._v("Tưởng Thanh Cầm khoát tay áo, dặn dò đệ tử."),t("br"),t("br"),n._v("“Như vậy không ổn! Ngươi thật không thấy lời nói, cái tên này thật muốn tới cứng, ta sợ Ma âm viện sẽ cùng học viện khác một dạng, chết thảm...”"),t("br"),t("br"),n._v("Ninh viện phó gấp vội vàng cắt đứt."),t("br"),t("br"),n._v("“Vậy làm sao bây giờ?” Tưởng Thanh Cầm cau mày."),t("br"),t("br"),n._v("Nói thật, vị này Trương Huyền chính là tai tinh, đi đến đâu, cái nào xui xẻo, muốn là có thể lựa chọn, nhất định có thể trốn xa hơn trốn xa hơn..."),t("br"),t("br"),n._v("“Nếu không... Trước hết để cho hắn đi vào, đã đưa danh thiếp, mà không phải xông vào, có lẽ có thương nghị chỗ trống!”"),t("br"),t("br"),n._v("Ninh viện phó nói: “Nếu quả thật như như ngươi nói vậy lợi hại, đối Ma âm cũng có nghiên cứu lời nói, hoàn toàn có thể để cho hắn chỉ điểm học sinh, để chúng ta Ma âm viện thực lực tăng nhiều, cũng chưa biết chừng!”"),t("br"),t("br"),n._v("“Cái này...” Tưởng Thanh Cầm ánh mắt sáng lên: “Không sai, nếu như đem hắn xem như cơ hội, đối Ma âm viện tới nói, có lẽ thật là một lớn ích lợi!”"),t("br"),t("br"),n._v("Cảm khái hết, vội vàng dặn dò: “Như vậy, ngươi để hắn đi vào!”"),t("br"),t("br"),n._v("Vị này Trương Huyền mặc dù rất đáng sợ, nhưng Ma âm sư khảo hạch, không cần vượt quan loại hình chuyện phiền toái, cũng không cần tu vi chống, nếu như cái tên này thật là có bản lĩnh, bản thân cũng chưa hẳn không thể hỗ trợ xin lục tinh Ma âm sư."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Vị này thông bẩm đệ tử, nhẹ gật đầu, không lâu sau, chỉ thấy Trương Huyền cùng một tên mập đi đến."),t("br"),t("br"),n._v("“Trương Huyền gặp qua Tưởng viện trưởng, Ninh viện phó!”"),t("br"),t("br"),n._v("Đi tới gần, Trương Huyền ôm quyền."),t("br"),t("br"),n._v("Trên đường tới, đã hướng thông bẩm đệ tử hỏi thăm, biết Phượng Ngô thụ bên dưới hai người thân phận."),t("br"),t("br"),n._v("“Trương sư không cần khách khí, chuyện của ngươi ta nhưng mà nghe lỗ tai đều mài ra vết chai, không biết hiện tại tới, cần làm chuyện gì! Chúng ta Ma âm viện nhỏ, chịu không được chơi đùa, mong rằng hạ thủ lưu tình.”"),t("br"),t("br"),n._v("Tưởng Thanh Cầm nói."),t("br"),t("br"),n._v("“Ây... Tưởng viện trưởng nói gì vậy...”"),t("br"),t("br"),n._v("Không nghĩ tới đối phương mới mở miệng liền cho hắn phòng hờ, Trương Huyền mang theo xấu hổ, vội vàng ôm quyền: “Kỳ thật lần này tới, là có chuyện muốn nhờ!”"),t("br"),t("br"),n._v("“Ồ?”"),t("br"),t("br"),n._v("Liếc mắt nhìn nhau, hai người tràn đầy nghi hoặc, chần chờ một chút, Tưởng Thanh Cầm vẻ mặt thận trọng dặn dò nói: “Chỉ cần không phá hư Ma âm viện, không chạy tới vượt quan, còn có, không cho học sinh của ta gia nhập Huyền Huyền hội... Ta có thể làm được... Tuyệt đối sẽ hết sức hỗ trợ!”"),t("br"),t("br"),n._v("“Khụ khụ!” Trương Huyền một hồi bất đắc dĩ."),t("br"),t("br"),n._v("Đây đều là cái gì ah!"),t("br"),t("br"),n._v("Hắn chính là bình thường khảo hạch, bình thường vượt quan, hiện tại cũng truyền thành gì, kiếm được bản thân cùng không phá hư mất hứng giống như..."),t("br"),t("br"),n._v("Bản thân lại không phải là người như thế!"),t("br"),t("br"),n._v("“Tại hạ lần này tới, là muốn hướng Tưởng viện trưởng, cầu lấy một chút Phượng Ngô tủy...”"),t("br"),t("br"),n._v("Trong lòng tràn đầy buồn bực, Trương Huyền đành phải đem mục đích nói ra."),t("br"),t("br"),n._v("“Phượng Ngô tủy? Không được! Tuyệt đối không được!”"),t("br"),t("br"),n._v("Nghe được hắn, Tưởng viện trưởng trực tiếp nổ tung, vội vàng vẫy tay."),t("br"),t("br"),n._v("Nhìn đối phương thái độ rất tốt, còn tưởng rằng muốn từ bỏ làm loạn, không nghĩ tới mới mở miệng liền Phượng Ngô tủy... Danh sư học viện người nào không biết, cái này gốc Phượng Ngô thụ là mệnh căn của ta?"),t("br"),t("br"),n._v("Ngươi mở miệng liền muốn... Đây là muốn mạng của ta!"),t("br"),t("br"),n._v("Còn không bằng đem ta giết được rồi!"),t("br"),t("br"),n._v("“Cái này... Ta sẵn lòng thanh toán thù lao, thậm chí sẵn lòng giá cao mua sắm!” Biết thứ này khẳng định rất khó chiếm được, Trương Huyền cũng không do dự, vội vàng nói."),t("br"),t("br"),n._v("“Giá cao mua sắm cũng không được...” Tưởng viện trưởng lắc đầu."),t("br"),t("br"),n._v("“Vậy được rồi!”"),t("br"),t("br"),n._v("Thấy đối phương thái độ kiên quyết, Trương Huyền một mặt bất đắc dĩ: “Cái kia không biết chúng ta Ma âm viện Tàng Thư khố ở nơi nào, lại như thế nào khảo hạch Ma âm sư? Đã tới, ta muốn thuận tiện kiểm tra một chút...”"),t("br"),t("br"),n._v("“Kiểm tra Ma âm sư?”"),t("br"),t("br"),n._v("Một nghĩ tới tên này khảo hạch những nghề nghiệp khác, tạo thành oanh động cùng tai nạn, Tưởng viện trưởng khóe miệng quất loạn, vội vàng vẫy tay: “Không được!”"),t("br"),t("br"),n._v("“Không được? Tưởng viện trưởng đây là không cho ta khảo hạch Ma âm sư?” Trương Huyền nghi hoặc."),t("br"),t("br"),n._v("“Không phải...” Tưởng Thanh Cầm vẻ mặt khó coi, lại không biết như thế nào mở miệng."),t("br"),t("br"),n._v("Đối phương thật muốn khảo hạch lời nói, hắn còn thật không có cách nào."),t("br"),t("br"),n._v("Khảo hạch tự do, cũng không thể bởi vì sợ, liền cự tuyệt một vị thiên tài thông qua khảo hạch chứ?"),t("br"),t("br"),n._v("Thật muốn như vậy, cũng không xứng làm một cái danh sư."),t("br"),t("br"),n._v("“Viện trưởng, ngươi không phải không cách nào đột phá Lục Hạc chi cảnh ư? Đã cái tên này truyền như thế tà dị, không bằng, để hắn chỉ điểm, nếu như có thể để ngươi tại chỗ đột phá đến Thất Hạc, đem Phượng Ngô tủy cho hắn một chút lại như thế nào! Làm không được... Vậy liền thật không tiện, để hắn trở về, đừng lại đánh Ma âm viện biện pháp!”"),t("br"),t("br"),n._v("Một bên Ninh viện phó thấy bạn cũ nghĩ không ra biện pháp, lặng lẽ truyền âm."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Tưởng Thanh Cầm sững sờ, lập tức ánh mắt sáng lên."),t("br"),t("br"),n._v("Đúng a!"),t("br"),t("br"),n._v("Đã lợi hại như vậy, có thể để cho luyện đan sư học viện, sở hữu thầy trò đều cam nguyện làm học sinh của ngươi, dạy Vệ Nhiễm Tuyết sinh lòng bội phục, có lẽ Ma âm bên trên, cũng có thể làm được điểm ấy!"),t("br"),t("br"),n._v("Thật muốn có thể đột phá đến Thất Hạc chi cảnh, liền đại biểu đạt đến thất tinh Ma âm sư trình độ... Phượng Ngô tủy đây tính toán là cái gì!"),t("br"),t("br"),n._v("(Trước mấy ngày phát Trương Huyền cầm trong tay chổi lông gà hình ảnh, tất cả mọi người nói quá xấu, rất nhai rất đau lòng, chuyên môn tìm mấy cái khác biệt phong cách làm bỏ phiếu, bậc thầy hi vọng chúng ta phải huyền huyền là dạng gì, mời tới bỏ phiếu đi! Wechat lục soát “Hoành Tảo Thiên Nhai”, tăng thêm chú ý, kiểm tra lịch sử tin tức là đủ.)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-831"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-833"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);