(window.webpackJsonp=window.webpackJsonp||[]).push([[2113],{2468:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-824-bay-đi-tien-hac"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-824-bay-đi-tien-hac"}},[n._v("#")]),n._v(" Chương 824: Bay đi, tiên hạc!")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 824: Bay đi, tiên hạc!"),t("br"),t("br"),n._v("“Phượng Ngô tủy cho ngươi một chút cũng không sao, thậm chí có thể không thu lấy bất luận cái gì thù lao!”"),t("br"),t("br"),n._v("Nghĩ đến điểm này, Tưởng Thanh Cầm không do dự nữa, trên mặt lần nữa khôi phục tự tin."),t("br"),t("br"),n._v("“Ồ?” Biết đối phương đột nhiên thay đổi chủ ý, khẳng định có nói sau, Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Ta hiện tại biểu diễn đàn ngọc, chỉ có thể để Lục Hạc nhảy múa, nếu như ngươi có thể tại trong vòng một canh giờ, để cho ta đột phá được Thất Hạc, Phượng Ngô tủy có thể cho ngươi, không thu bất luận cái gì phí tổn!”"),t("br"),t("br"),n._v("Tưởng Thanh Cầm đem mục đích của mình nói ra."),t("br"),t("br"),n._v("“Lục Hạc? Thất Hạc? Đó là cái gì?” Trương Huyền đầu óc mơ hồ."),t("br"),t("br"),n._v("Ma âm chức nghiệp, kết nối va đều không tiếp xúc qua, đối phương nói quá mức chuyên nghiệp, hoàn toàn nghe không hiểu."),t("br"),t("br"),n._v("“Ngươi... Không biết?”"),t("br"),t("br"),n._v("Còn tưởng rằng đối phương luôn mồm muốn khảo hạch Ma âm sư, đã có không kém gì năng lực của hắn, nghe hỏi ra như thế nông cạn lời nói, Tưởng Thanh Cầm mắt tối sầm lại, suýt chút nữa không hộc máu."),t("br"),t("br"),n._v("Cái đồ chơi này, là cái học đồ đều hiểu... Ngươi nhưng lại không biết, tình cảm đối Ma âm chức nghiệp hoàn toàn không biết gì cả ah!"),t("br"),t("br"),n._v("Cái gì cũng đều không hiểu, liền dám càn rỡ, muốn khảo hạch Ma âm sư?"),t("br"),t("br"),n._v("“Ừm!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Được rồi, Ma âm bên trong đàn ngọc lực công kích mạnh nhất, uy lực cũng lớn nhất, rất nhiều Ma âm sư, đều tu luyện cái này, cũng là phán xét cấp bậc tiêu chuẩn. Tiếng đàn lĩnh ngộ càng cao, càng có thể gây nên thiên địa cộng minh, để càng nhiều tiên hạc nhảy múa. Dưới tình huống bình thường, nhất tinh Ma âm sư, có thể để cho một cái tiên hạc nhảy múa, nhị tinh có thể để cho hai cái... Cứ thế mà suy ra! Dần dà, cái này cũng thành khảo hạch Ma âm sư tiêu chuẩn.”"),t("br"),t("br"),n._v("Cố nén buồn bực, Tưởng Thanh Cầm giảng giải học đồ đều biết tri thức: “Ta hiện tại là lục tinh Ma âm sư đỉnh phong, vẫn như cũ chỉ có thể để sáu cái tiên hạc nhảy múa, bảy cái không thể nào làm được! Nếu như ngươi có thế để cho của ta tiếng đàn, bảy cái tiên hạc đồng thời bay lượn... Ta liền đem Phượng Ngô tủy cho ngươi.”"),t("br"),t("br"),n._v("“Bảy cái tiên hạc đồng thời nhảy múa?”"),t("br"),t("br"),n._v("Trương Huyền cau mày."),t("br"),t("br"),n._v("“Không sai!”"),t("br"),t("br"),n._v("Tưởng Thanh Cầm gật đầu: “Ta bây giờ cách đột phá chi kém một tia thời cơ, nếu có thể giúp ta làm đến, sự tình khác đều tốt nói, làm không được... Mong rằng ngươi lập tức rời đi Ma âm viện, không muốn tại đến rồi!”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Cúi đầu trầm tư, Trương Huyền một lát sau nhìn qua: “Ngươi ý tứ biểu diễn đàn ngọc thời điểm, chỉ cần có thể để bảy cái tiên hạc nhảy múa là được... Đúng không!”"),t("br"),t("br"),n._v("“Không sai!”"),t("br"),t("br"),n._v("Tưởng Thanh Cầm gật đầu."),t("br"),t("br"),n._v("“Cái này đơn giản! Một canh giờ... Năm phút đồng hồ đều không dùng đến!” Mỉm cười, Trương Huyền trên mặt lộ ra vượt qua tuổi tác mạnh mẽ tự tin."),t("br"),t("br"),n._v("Còn tưởng rằng bao lớn sự tình, nguyên lai cứ như vậy, vài phút cấp cho ngươi!"),t("br"),t("br"),n._v("“Năm phút đồng hồ đều không dùng đến?”"),t("br"),t("br"),n._v("Hai đại viện trưởng đồng thời ngẩn ngơ."),t("br"),t("br"),n._v("Thật hay giả?"),t("br"),t("br"),n._v("Vì theo Lục Hạc đột phá được Thất Hạc, Tưởng Thanh Cầm khổ tu năm trăm năm đều làm không được, ngươi năm phút đồng hồ... Liền có thể chỉ điểm ta đột phá?"),t("br"),t("br"),n._v("Ngươi không phải liền Lục Hạc, Thất Hạc cũng đều không hiểu sao? Làm sao có thể nhanh như vậy chỉ điểm ta đột phá?"),t("br"),t("br"),n._v("Thật muốn có thể làm được, đừng nói Phượng Ngô tủy, viên này Phượng Ngô thụ, trực tiếp đào đi đều vô sự."),t("br"),t("br"),n._v("“Đương nhiên!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Vậy thì tốt, ngươi nói một chút ta đến cùng nơi nào có vấn đề, nhiều năm như vậy, cũng còn không cách nào đột phá?”"),t("br"),t("br"),n._v("Trong lòng mặc dù chần chờ, nghĩ tới đối diện cái tên này bất bại chiến tích, Tưởng Thanh Cầm vẫn là lựa chọn tin tưởng, ánh mắt lấp lánh nhìn qua."),t("br"),t("br"),n._v("Ngay cả một bên Ninh viện phó cũng đầy là hiếu kỳ."),t("br"),t("br"),n._v("Dám như thế khoác lác, khẳng định có một ít năng lực đặc thù, để hắn đều cảm thấy không thể tưởng tượng nổi."),t("br"),t("br"),n._v("Chẳng lẽ là cái này... Lĩnh ngộ Minh Lý Chi Nhãn danh sư năng lực?"),t("br"),t("br"),n._v("“Đánh đàn đi!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Được!”"),t("br"),t("br"),n._v("Biết rất nhiều chỉ điểm, nhất định phải biểu hiện ra thực lực, mới có thể nhìn ra, Tưởng Thanh Cầm cũng không do dự, ngón tay vạch một cái, trước mắt đàn ngọc trong nháy mắt vang lên."),t("br"),t("br"),n._v("Thanh âm nhàn nhạt phảng phất từ phía trên bên cạnh mà đến, vang vọng toàn bộ sân nhỏ."),t("br"),t("br"),n._v("Tiếng đàn vừa tiến vào trong tai, liền khiến người ta say mê không ngớt, cho người ta một loại vũ hóa thành tiên, ngạo cười thương khung cảm giác."),t("br"),t("br"),n._v("Ma âm, là một loại công kích hồn phách chức nghiệp, tu luyện tới cao thâm ra, tựa như thiên quân vạn mã, lôi đình gió lớn, cường giả Thánh vực đều khó mà chống chọi."),t("br"),t("br"),n._v("Truyền thuyết, Ma âm sư, vốn là gọi Tiên Âm sư, hai vạn năm trước, một vị cửu tinh chức nghiệp giả, gặp phải vây công, cầm trong tay đàn ngọc, lấy tiếng đàn dẫn xuất ma linh, tru diệt đến đây tiến công trăm vạn hùng binh."),t("br"),t("br"),n._v("Trận chiến kia, Ma âm chức nghiệp vang vọng thiên hạ, người người mới biết, cái này không đáng chú ý phụ tu, đáng sợ như thế, mới được “Ma” tên."),t("br"),t("br"),n._v("“Lợi hại...”"),t("br"),t("br"),n._v("Thiên Đạo chân khí xoay một cái, trong cơ thể áp lực biến mất, Trương Huyền kìm lòng không được hoảng sợ."),t("br"),t("br"),n._v("Nếu như không phải sớm phòng bị, chỉ sợ lần này, đã bị tiếng đàn mê hoặc, không cách nào nhúc nhích."),t("br"),t("br"),n._v("Xem ra, cho dù là hạ cửu lưu chức nghiệp, chỉ cần tự thành hệ thống, đều có không có thể thay thế năng lực, nếu không, cũng không có khả năng truyền thừa vài vạn năm, kéo dài không suy."),t("br"),t("br"),n._v("“Ừm?” Tưởng Thanh Cầm lông mày giương lên."),t("br"),t("br"),n._v("Vốn định cho người thanh niên này một hạ mã uy, cho nên trong, thêm một chút năng lực công kích."),t("br"),t("br"),n._v("Vốn cho rằng, y theo đối phương tu vi, tất nhiên sẽ trầm mê trong đó, khó mà tự kềm chế, không nghĩ tới một cái nháy mắt, liền ánh mắt trong suốt, kìm lòng không được âm thầm bội phục."),t("br"),t("br"),n._v("Khó trách có thể làm cho cả danh sư học viện như gặp phải đại địch, Lục Phong viện trưởng phát điên, thanh niên này, tuổi không lớn lắm, quả thực có thường nhân khó mà chống lại thiên phú và năng lực."),t("br"),t("br"),n._v("Hô hô hô!"),t("br"),t("br"),n._v("Trong lòng bội phục, không tiếp tục công kích, tiếng đàn lập tức trở nên nhu hòa, một bên không ngừng đi dạo tiên hạc, nghe được tiếng đàn, tiếng kêu to bên trong, uyển chuyển nhảy múa."),t("br"),n._v(" Một đầu, hai đầu, ba đầu..."),t("br"),t("br"),n._v("Càng ngày càng nhiều, không lâu sau, liền có sáu đầu bay về phía không trung, dọc theo tiếng đàn không ngừng múa."),t("br"),t("br"),n._v("Nhìn thấy tiếng đàn quả nhiên có thể dẫn tới tiên hạc nhảy múa, Trương Huyền nhịn không được tán thưởng."),t("br"),t("br"),n._v("Hắn tinh thông vu hồn sư, mặc dù có đôi khi nói chuyện, cũng mang theo Ma âm hiệu quả, để cho người ta linh hồn mê muội, nhưng muốn để nhiều như vậy tiên hạc, dựa theo âm luật múa, vẫn là làm không được."),t("br"),t("br"),n._v("“Trương sư, đây chính là sáu hạc nhảy múa, hiện tại ta muốn đi đến bảy hạc, chính là làm không được, ngươi nói ta đáp làm như thế nào biểu diễn, mới có thể đột phá?”"),t("br"),t("br"),n._v("Thấy sáu hạc bay lên, Tưởng Thanh Cầm cười cười, thanh âm vang vang vang lên."),t("br"),t("br"),n._v("Hắn mặc dù tuổi tác cực lớn, thanh âm nhưng nghe như là ca hát, cho người ta một loại thoải mái dễ chịu cảm giác, lại phối hợp tiếng đàn, không nói ra được mỹ diệu."),t("br"),t("br"),n._v("“Được!”"),t("br"),t("br"),n._v("Nghe được đối phương, Trương Huyền mỉm cười, không tại nhiều nghe, quay người lại, hướng cách đó không xa rất nhiều tiên hạc địa phương đi đến."),t("br"),t("br"),n._v("Cái viện này, chừng mười mấy đầu, ném đi bay lên sáu cái, cũng còn có mười đầu nhiều, đều là Tưởng Thanh Cầm từ nhỏ nuôi nhốt, mười phần nghe lời, giờ phút này đều an tĩnh đứng tại chỗ, tựa hồ tại hưởng thụ nhạc khúc, tùy thời đều có thể bay vọt lên."),t("br"),t("br"),n._v("“Bắt đầu đi!”"),t("br"),t("br"),n._v("Khoảng cách những tiên hạc này, còn có xa hơn mười thước, Trương Huyền hoạt động một chút gân cốt, duỗi lưng một cái, hít sâu một hơi."),t("br"),t("br"),n._v("“Thiên Đạo thân pháp, Thiên Đạo quyền pháp!”"),t("br"),t("br"),n._v("Trong lòng la hét, bàn chân tại mặt đất đạp mạnh, thân ảnh nhoáng một cái, trong nháy mắt hóa thành một đạo huyễn ảnh."),t("br"),t("br"),n._v("Bành bành bành bành!"),t("br"),t("br"),n._v("Liên tiếp mười cái nắm đấm vào thịt thanh âm, yên tĩnh đứng tại chỗ mười đầu tiên hạc, trong nháy mắt bị đánh chạy đến bay ra ngoài, nguyên một đám kề ở trên tường, đập ra lớn nhỏ không đều cái hố."),t("br"),t("br"),n._v("“...” Tưởng Thanh Cầm mắt tối sầm lại."),t("br"),t("br"),n._v("Vốn định vị này Trương sư, muốn đưa ra ý kiến gì, tiến hành chỉ điểm, nhìn thấy hắn một chiêu đem bản thân tiên hạc, đánh lông tóc bay loạn, đầu cắm vào ở trên tường, tức giận đến suýt chút nữa không tại chỗ ngất đi."),t("br"),t("br"),n._v("Đinh!"),t("br"),t("br"),n._v("Một lúc tình thế cấp bách, dây đàn đều chặt đứt một cây, nhịn không được nhìn qua, tựa như muốn giết người: “Trương sư, ngươi muốn làm gì?”"),t("br"),t("br"),n._v("Cũng khó trách hắn tức giận, những tiên hạc này, từ nhỏ nuôi đến lớn, đều có tình cảm, mặc dù đối phương lưu lại đúng mực, không có đánh chết, có thể... Không nói hai lời liền đem nó đánh liểng xiểng, tức giận đến suýt chút nữa sắp bể mất."),t("br"),t("br"),n._v("“Tưởng viện trưởng đừng có gấp, ngươi tiếp tục biểu diễn... Ta lập tức liền để bảy hạc nhảy múa!” Không để ý tới phẫn nộ của hắn, Trương Huyền quay đầu nói."),t("br"),t("br"),n._v("“Lập tức?” Tưởng Thanh Cầm tràn đầy nghi hoặc."),t("br"),t("br"),n._v("Ta để ngươi chỉ cho ta điểm, ngươi không nhìn ta, đi đánh hạc... Như vậy có thể để cho ta đột phá?"),t("br"),t("br"),n._v("“Tin tưởng ta, lập tức!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Hừ, nếu như không đạt tới, thương thế của ngươi của ta tiên hạc, khẳng định muốn đòi một lời giải thích...”"),t("br"),t("br"),n._v("Khí sắc mặt tái xanh, Tưởng Thanh Cầm vẫn là đem đứt rời dây đàn nối liền, lần nữa nhẹ nhàng phất một cái, tiếng đàn tiếp tục vang lên."),t("br"),t("br"),n._v("Cái tên này sáng chế quá nhiều kỳ tích, lại thêm vây ở Lục Hạc chi cảnh, thời gian quá lâu, thực sự quá muốn đột phá, thấy đối phương như thế lời thề son sắt, nhịn không được sinh ra thử một chút ý nghĩ."),t("br"),t("br"),n._v("Leng keng! Leng keng!"),t("br"),t("br"),n._v("Tựa như Cao Sơn Lưu Thủy, uyển chuyển thanh âm, tiếp tục chảy xuôi."),t("br"),t("br"),n._v("Vừa rồi không bị đòn sáu cái tiên hạc, nghe được thanh âm, tiếp tục bay lên, uyển chuyển nhảy múa."),t("br"),t("br"),n._v("Nhìn thấy vị này Tưởng viện trưởng có thể nhanh như vậy khống chế cảm xúc, đem tiếng đàn diễn tấu giọt nước không lọt, hân hoan nhảy nhót, Trương Huyền nhịn không được âm thầm gật đầu, nhẹ nhàng cười một tiếng, vẫy bàn tay lớn một cái: “Bay đi, tiên hạc!”"),t("br"),t("br"),n._v("Nương theo tiếng la của hắn, trước đó bị hắn đánh tiên hạc, giãy dụa lấy đứng lên, đột nhiên một tiếng lanh lảnh kêu to, trong đó một đầu, bút thẳng bay lên, xông vào bầu trời sáu đầu bên trong."),t("br"),t("br"),n._v("“Thành công?”"),t("br"),t("br"),n._v("Một bên Ninh viện phó con mắt một chút trợn tròn."),t("br"),t("br"),n._v("Không phải nói chỉ điểm ư?"),t("br"),t("br"),n._v("Một câu nói không nói, liền đến từng cái “Bay đi, tiên hạc”... Làm sao lại đi đến Thất Hạc cảnh?"),t("br"),t("br"),n._v("Hơn nữa... Nghe thanh âm, Tưởng viện trưởng tiếng đàn không có bất kỳ cái gì tiến bộ hoặc là biến hóa a?"),t("br"),t("br"),n._v("Ngạc nhiên còn không có kết thúc, chỉ thấy lại một cái tiên hạc, lung lay hai lần, dường như theo trạng thái hôn mê, tỉnh táo lại, cánh lóe lên, cũng một tiếng vang lên, gia nhập bay lượn trạng thái."),t("br"),t("br"),n._v("“Bát Hạc...”"),t("br"),t("br"),n._v("Ninh viện phó bờ môi run rẩy."),t("br"),t("br"),n._v("Mới vừa còn nói là Thất Hạc cảnh, làm sao chớp mắt liền Bát Hạc?"),t("br"),t("br"),n._v("Tám hạc cái này có thể đại biểu bát tinh Ma âm sư..."),t("br"),t("br"),n._v("Tưởng viện trưởng tiếng đàn chưa biến, Trương sư cũng không chỉ điểm, làm sao cảnh giới liền đề cao?"),t("br"),t("br"),n._v("Trong lòng còn đang kỳ quái, chỉ thấy trước đó bị đánh vào bức tường tiên hạc, tất cả đều quơ quơ, lắc đầu, nguyên một đám cánh chớp động, xông tới bầu trời."),t("br"),t("br"),n._v("“Chín hạc, mười hạc, mười một hạc...”"),t("br"),t("br"),n._v("Con mắt một ngất."),t("br"),t("br"),n._v("Ninh viện phó không thở nổi."),t("br"),t("br"),n._v("Ma âm sư, cấp bậc cao nhất chính là chín hạc chi cảnh, thời gian nháy mắt, không trung liền bay ra ngoài mười một con, không chỉ có như thế, đằng sau cái kia vài đầu tiên hạc, vẫn như cũ hướng lên bầu trời không ngừng bay lượn..."),t("br"),t("br"),n._v("Cái này đều làm sao vậy?"),t("br"),t("br"),n._v("Chẳng lẽ, cái này Trương sư thật thần kỳ như thế? Không cần chỉ điểm, chỉ muốn nhìn lên một cái, liền có thể khiến người ta đột phá?"),t("br"),t("br"),n._v("Trong lòng khiếp sợ, đang muốn cảm thụ bạn cũ tiếng đàn, có phải hay không càng ngày càng cao thâm, chỉ thấy dây đàn lần nữa đứt đoạn, bạn cũ giống như là rõ ràng cái gì, vẻ mặt cứng ngắc như là sắt thép, đánh đàn hai tay cũng ngừng lại."),t("br"),t("br"),n._v("“Trương Huyền... Ta thao ngươi mỗ mỗ...”"),t("br"),t("br"),n._v("Luôn luôn nho nhã Tưởng Thanh Cầm cũng nhịn không được nữa, như sói gào đồng dạng gào thét trong nháy mắt vang lên."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-832"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-834"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);