(window.webpackJsonp=window.webpackJsonp||[]).push([[2114],{2469:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-825-khao-hach-ma-am-su-【-hai-hop-mot-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-825-khao-hach-ma-am-su-【-hai-hop-mot-】"}},[n._v("#")]),n._v(" Chương 825: Khảo hạch Ma âm sư 【 hai hợp một 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 825: Khảo hạch Ma âm sư "),t("br"),t("br"),n._v("“Làm sao vậy?”"),t("br"),t("br"),n._v("Không nghĩ tới bạn cũ như thế nổi giận, Ninh viện phó bối rối."),t("br"),t("br"),n._v("Hắn cùng vị này Tưởng viện trưởng, ở chung được không dưới năm trăm năm, chưa hề thấy hắn bởi vì lúc nào mà là tức giận, thậm chí một vị thân truyền, làm chuyện sai lầm, đem hắn trục xuất sư môn, đều mặt không đổi sắc..."),t("br"),t("br"),n._v("Bây giờ lại không để ý đến thân phận chửi ầm lên, đến cùng làm sao vậy?"),t("br"),t("br"),n._v("“Soạt!”"),t("br"),t("br"),n._v("Nương theo tiếng đàn kết thúc, bầu trời phi hành tiên hạc, tất cả đều rơi trên mặt đất, nguyên một đám ngửa mặt lên trời vang lên, tựa hồ làm sự tình gì, tâm tình vui vẻ."),t("br"),t("br"),n._v("“Viện trưởng, ngươi đây là đột phá, vẫn là không đột phá...”"),t("br"),t("br"),n._v("Ninh viện phó nhịn không được nhìn lại."),t("br"),t("br"),n._v("Ma âm, không giống thượng cửu lưu chức nghiệp, muốn muốn đạt tới cấp bậc cao hơn, tu vi muốn sớm đột phá, mười phần trực quan, không cần suy nghĩ nhiều."),t("br"),t("br"),n._v("Đối phương tiếng đàn dẫn đến như vậy nhiều tiên hạc, rõ ràng là cảnh giới tăng lên, có thể chẳng biết tại sao, mang đến cho hắn một cảm giác, ý cảnh dừng lại tại nguyên chỗ, nhịn không được hơi nghi hoặc một chút."),t("br"),t("br"),n._v("“Đột phá cái rắm!”"),t("br"),t("br"),n._v("Tưởng viện trưởng đứng dậy, cả người như là gào thét sư tử, hận không thể đem cách đó không xa thanh niên xé nát."),t("br"),t("br"),n._v("“Không đột phá? Cái kia vì cái gì những tiên hạc này...” Ninh viện phó cũng không hiểu."),t("br"),t("br"),n._v("Không đột phá... Không trung chỉ có thể bay lượn sáu cái tiên hạc, đây là Ma âm sư, không đổi định lý, làm sao sẽ xuất hiện mười sáu con cùng bay lượn cục diện?"),t("br"),t("br"),n._v("“Vì cái gì? Hắn đem ta những tiên hạc này thuần phục...”"),t("br"),t("br"),n._v("Tưởng Thanh Cầm cả người đều phải nổ."),t("br"),t("br"),n._v("Nghe đối phương lời thề son sắt, còn thật sự cho rằng, có thể để cho hắn đột phá, ai ngờ cái tên này, không biết dùng thủ đoạn gì, thời gian nháy mắt, liền thuần phục hắn luôn rồi còn lại mười đầu tiên hạc..."),t("br"),t("br"),n._v("Bị thuần phục linh thú, chỉ nghe theo chủ nhân mệnh lệnh, tự nhiên để bọn chúng bay liền bay..."),t("br"),t("br"),n._v("“Còn có thể như vậy...”"),t("br"),t("br"),n._v("Trước mắt cũng là một đen, Ninh viện phó thiếu chút nữa ngất đi."),t("br"),t("br"),n._v("Viện trưởng nói tới Thất Hạc nhảy múa, là chỉ cảnh giới đột phá, con mẹ nó ngươi thuần phục tiên hạc có cái rắm dùng?"),t("br"),t("br"),n._v("Thuần phục tiên hạc, để bọn chúng bay... Cái này nếu có thể đại biểu cảnh giới, ta cũng có thể làm cho mấy trăm con tiên hạc cùng một chỗ đây..."),t("br"),t("br"),n._v("“Tưởng viện trưởng, ngươi mới vừa nói, chỉ cần để bảy cái tiên hạc phi hành, liền cho ta Phượng Ngô tủy, hiện tại mười sáu con một bay lên... Cũng không phải là muốn giựt nợ chứ!”"),t("br"),t("br"),n._v("Không nghĩ tới bản thân vất vả để nhiều như vậy tiên hạc đồng thời bay, đối phương chẳng những không hài lòng, còn tức giận, Trương Huyền nhướng mày."),t("br"),t("br"),n._v("“Quỵt nợ... Quy TJ ngươi tê dại da!”"),t("br"),t("br"),n._v("Răng suýt chút nữa cắn nát, Tưởng Thanh Cầm cả người đều sắp điên rồi."),t("br"),t("br"),n._v("Gặp qua vô sỉ, chưa thấy qua vô sỉ như vậy."),t("br"),t("br"),n._v("Ngươi đây là trộm đổi khái niệm được không?"),t("br"),t("br"),n._v("Ý của ta là giúp ta đột phá, mà ngươi đây... Nhưng thuần phục tiên hạc..."),t("br"),t("br"),n._v("Căn bản là không có hoàn thành, còn hỏi ta muốn Phượng Ngô tủy..."),t("br"),t("br"),n._v("“Ninh viện phó, ngươi cho ta phân xử thử, vừa rồi hắn có phải hay không nói, chỉ cần Thất Hạc phi hành, liền cho ta Phượng Ngô tủy? Chẳng lẽ viện trưởng, liền có thể khi dễ người?”"),t("br"),t("br"),n._v("Không nghĩ tới đường đường danh sư, thế mà mở miệng mắng người, Trương Huyền khẽ nói."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Ninh viện phó khóe miệng giật một cái."),t("br"),t("br"),n._v("Vừa rồi bạn cũ nói rất đúng “nếu như ngươi có thế để cho của ta tiếng đàn, bảy cái tiên hạc đồng thời bay lượn... Ta liền đem Phượng Ngô tủy cho ngươi!”"),t("br"),t("br"),n._v("Mặc dù muốn nói là ý cảnh tăng lên, nhưng... Theo mặt chữ bên trên lý giải... Đối phương nói đích thật không sai!"),t("br"),t("br"),n._v("Hiện tại không chỉ Thất Hạc, mười sáu con hạc cũng bay, chẳng lẽ còn muốn chống chế?"),t("br"),t("br"),n._v("“Trương sư, Tưởng viện trưởng nói tới Thất Hạc, là chỉ cảnh giới... Đây là Ma âm sư thường dùng thuyết pháp, ngươi thuần phục tiên hạc, dùng chính là thuần thú sư thủ đoạn, cùng Ma âm khác biệt, cũng không thể, cầm thuần thú năng lực chạy đến Ma âm học viện... Đến đổi đồ vật đi!”"),t("br"),t("br"),n._v("Chần chờ một chút, Ninh viện phó nói."),t("br"),t("br"),n._v("Khi nhục người, là ngươi khi dễ chúng ta được không."),t("br"),t("br"),n._v("Nhân gia nói là cảnh giới, ngươi thuần thú... Không mang theo như vậy chơi."),t("br"),t("br"),n._v("“Ách!”"),t("br"),t("br"),n._v("Trương Huyền vò đầu, giờ mới hiểu được tới."),t("br"),t("br"),n._v("Trước đó cho rằng, muốn cho tiên hạc bay lên là được, không phải đại sự gì, hiện tại xem ra, cũng không phải là đơn giản như vậy."),t("br"),t("br"),n._v("Ngôn ngữ bên trên xuất hiện chỗ nhầm lẫn, khó trách..."),t("br"),t("br"),n._v("Hắn liền nói không dễ dàng như vậy đây!"),t("br"),t("br"),n._v("“Cảnh giới tăng lên... Nói sớm không được sao, như vậy... Các ngươi Ma âm viện Tàng Thư khố ở nơi nào, có thể hay không để cho ta vào xem một hồi?”"),t("br"),t("br"),n._v("Chần chờ một chút, Trương Huyền nói."),t("br"),t("br"),n._v("Đừng nói để bảy cái tiên hạc phi hành, để một ngàn đầu cùng một chỗ bay, hắn cũng có thể làm được, nhưng... Để lục tinh đỉnh phong Ma âm sư cảnh giới tăng lên, lại không được."),t("br"),t("br"),n._v("Trước hết đọc sách mới được."),t("br"),t("br"),n._v("“Tàng Thư khố?”"),t("br"),t("br"),n._v("Nghe hắn lần thứ hai nói lời này, Ninh viện phó kỳ quái."),t("br"),t("br"),n._v("“Là... Tưởng viện trưởng tình huống, ta có thể nhìn ra một chút, có điều, còn có chút nghĩ không thông, điều tra thêm tư liêu, có lẽ liền có thể Linh Quang lóe lên, biết như thế nào giải quyết!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Thiên Đạo thư viện là đó có thể thấy được thiếu hụt, nhưng khuyết thiếu đem đối ứng tri thức dưới tình huống, là không có cách nào giải quyết."),t("br"),t("br"),n._v("Chỉ có đọc sách, để bản thân cảnh giới, cũng đi đến cùng đối phương tương đồng trình độ, thậm chí cao hơn, mới có thể tuỳ tiện hóa giải."),t("br"),t("br"),n._v("Bất quá, loại sự tình này không thể nói thẳng, nếu không, khẳng định sẽ bị xem như bị điên ném đi, chỉ có thể giả bộ ngớ ngẩn."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Ninh viện phó nhìn về phía còn không có nguôi giận viện trưởng."),t("br"),t("br"),n._v("“Có muốn hay không để hắn đi Tàng Thư khố?”"),t("br"),t("br"),n._v("Nhịn không được truyền âm qua."),t("br"),t("br"),n._v("“Đọc sách muốn có thể giải quyết vấn đề của ta, ta làm sao đến mức bị nhốt năm trăm năm?” Tưởng Thanh Cầm căn bản không tin."),t("br"),t("br"),n._v("“Vậy ta liền cự tuyệt...”"),t("br"),t("br"),n._v("Ninh viện phó nói."),t("br"),t("br"),n._v("“Trước không vội... Đi Tàng Thư khố đọc sách, không tính là gì! Như vậy đi... Ngươi nói cho hắn biết, để hắn đi cũng không phải là không thể được, nếu như vẫn như cũ không thể để cho ta cảnh giới tăng lên, Phượng Ngô tủy sự tình, như vậy coi như không có gì!”"),t("br"),t("br"),n._v("Tưởng Thanh Cầm nói: “Còn có... Đọc sách liền đọc sách, không cho phép ở bên trong gây rối, cũng không thể kiếm phá hư!”"),t("br"),t("br"),n._v("Tàng Thư khố thư tịch, chính là để dùng cho người xem, xem cùng không nhìn, không phải cái đại sự gì."),t("br"),t("br"),n._v("Chỉ cần không nháo sự tình là tốt rồi."),t("br"),t("br"),n._v("Đối với cái này, động một chút lại muốn vượt quan, động một chút lại muốn khảo hạch gia hỏa, hắn cũng có chút kính sợ."),t("br"),t("br"),n._v("“Được!” Ninh viện phó gật gật đầu, đem ý tứ cùng Trương Huyền nói."),t("br"),t("br"),n._v("“Yên tâm đi, khẳng định không nháo sự tình...” Nghe được có thể đọc sách, Trương Huyền thở phào nhẹ nhõm."),t("br"),t("br"),n._v("“Tàng Thư khố liền ở phía sau, ta dẫn ngươi đi qua!”"),t("br"),t("br"),n._v("Thấy hắn đồng ý, Ninh viện phó đi đầu dẫn đường."),t("br"),t("br"),n._v("Biết đọc sách thời gian khẳng định không ngắn, Trương Huyền để Tôn Cường trước quay về chỗ ở, bản thân thì đi theo phó viện trưởng sau lưng đi tới."),t("br"),t("br"),n._v("Ma âm viện Tàng Thư khố, cùng luyện đan sư, y sư học viện Tàng Thư khố so ra kém rất nhiều, chỉ cùng Kinh Hồng viện không kém bao nhiêu, từ phó viện trưởng dẫn đầu, không cần học phần cũng có thể đi vào."),t("br"),t("br"),n._v("“Đa tạ phó viện trưởng...”"),t("br"),t("br"),n._v("Tiến vào bên trong, nhìn thấy trước mắt lít nha lít nhít thư tịch, Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("“Ngươi ngay ở chỗ này đọc sách đi, có gì cần, có thể để người ta nói với ta!”"),t("br"),t("br"),n._v("Biết đọc sách không có gì được ở lại, Ninh viện phó nhấc tay cáo từ."),t("br"),t("br"),n._v("Đáp ứng một tiếng, Trương Huyền không tiếp tục để ý, con mắt rơi ở trước mắt lít nha lít nhít trên giá sách."),t("br"),t("br"),n._v("Nắm giữ đọc sách liền có thể thu vào năng lực, liền không trước đó khó khăn như vậy lần lượt lật sách, tinh thần cao độ tập trung, con mắt theo từng quyển từng quyển thư tịch chảy xuôi mà đi."),t("br"),t("br"),n._v("Ma âm học viện vô số thư tịch chảy vào trong óc."),t("br"),t("br"),n._v("Cái này Tàng Thư khố cùng trước đó kinh hồng học viện một dạng, suốt ngày đều tới không được một cái học sinh, Trương Huyền đúng lúc cũng vui vẻ thanh nhàn, không đến mức kinh thế hãi tục."),t("br"),t("br"),n._v("Một bản, hai quyển... Một trăm bản, hai trăm bản... Một ngàn bản, hai ngàn bản..."),t("br"),t("br"),n._v("Mấy canh giờ sau, liền đem toàn bộ thư khố, mấy trăm vạn thư tịch xem không sai biệt lắm."),t("br"),t("br"),n._v("Trong lòng đối Ma âm cũng có càng sâu hiểu rõ, trong đầu thuận lợi tạo thành một đến sáu tinh Thiên Đạo bí tịch."),t("br"),t("br"),n._v("“Học tập!”"),t("br"),n._v(" Thấy Tàng Thư khố bên trong vẫn như cũ không có người, lười phải tiếp tục động đậy, tìm cái rộng rãi địa phương, khoanh chân ngồi xuống, tinh thần đắm chìm trong óc, bắt đầu theo đệ nhất tinh bắt đầu học tập."),t("br"),t("br"),n._v("Sau ba canh giờ, một ngụm trọc khí phun ra."),t("br"),t("br"),n._v("Hắn hiện tại, đối Ma âm lĩnh ngộ, đã đạt đến lục tinh, coi như so với trước đó Tưởng Thanh Cầm viện trưởng, đều chỉ mạnh không yếu."),t("br"),t("br"),n._v("“Đã đạt đến lục tinh Ma âm sư, vậy liền... Kiểm tra một chút!”"),t("br"),t("br"),n._v("Đều đạt đến loại cảnh giới này, tự nhiên muốn khảo hạch một chút."),t("br"),t("br"),n._v("Khảo hạch lục tinh danh sư, cần tinh thông sáu loại phụ tu chức nghiệp, đồng thời đều đi đến lục tinh cấp bậc."),t("br"),t("br"),n._v("Hắn hiện tại luyện khí sư, y sư, luyện đan sư, kinh hồng sư bốn dạng đi đến, còn cần hai loại."),t("br"),t("br"),n._v("Đã Ma âm đều học tập, lại hữu cơ biết, không bằng cùng nhau nghiên cứu được rồi."),t("br"),t("br"),n._v("Nhấc chân đi ra Tàng Thư khố, nhìn quanh hai bên một vòng, thẳng tắp hướng khảo hạch Ma âm sư Ma âm các đi tới."),t("br"),t("br"),n._v("Ma âm các cùng y sư tháp khác biệt, là cái viên hoàn hình nơi chốn, như là kiếp trước sân thượng, muốn muốn khảo hạch Ma âm sư người, cần ngồi tại rất vị trí giữa, biểu diễn âm nhạc, bức tường hồi âm xao động, xông tới bầu trời, thu hút trong học viện nuôi nhốt tiên hạc, vì đó quanh quẩn một chỗ."),t("br"),t("br"),n._v("Sau đó, thông qua tiên hạc số lượng đánh giá đẳng cấp."),t("br"),t("br"),n._v("Đi tới vòng tròn phía ngoài gian phòng, chỉ thấy một cái Ma âm học viện thanh niên, nằm sấp trên bàn ngủ gật."),t("br"),t("br"),n._v("Hẳn là chịu trách nhiệm Ma âm các khảo hạch làm việc ngoài giờ học viên."),t("br"),t("br"),n._v("“Vị niên trưởng này, ta muốn khảo hạch Ma âm sư!”"),t("br"),t("br"),n._v("Đi tới bên cạnh, Trương Huyền ôm quyền."),t("br"),t("br"),n._v("“Khảo hạch Ma âm sư?”"),t("br"),t("br"),n._v("Xoa xoa con mắt, thanh niên nhìn thoáng qua: “Mấy tinh?”"),t("br"),t("br"),n._v("“Trước kia chưa hề khảo hạch qua, định theo nhất tinh bắt đầu khảo hạch...” Trương Huyền nói."),t("br"),t("br"),n._v("“Theo nhất tinh kiểm tra? Có thể, đây là khảo hạch nhất tinh dùng nhạc phổ, sử dụng nhạc khúc, nhất định phải từ trong đó lựa chọn, đi vào, chỉ cần có tiên hạc tới, Ma âm các sẽ tự động phán định có hay không thông qua!”"),t("br"),t("br"),n._v("Thanh niên nói từ trong phòng lấy ra một cái nhạc phổ."),t("br"),t("br"),n._v("Khảo hạch Ma âm sư, có riêng biệt âm nhạc, chỉ cần có thể sử dụng cái này âm nhạc phát huy ra nhất tinh trình độ, coi như thông qua."),t("br"),t("br"),n._v("“Được!” Nhìn qua rất nhiều thư, đã biết khảo hạch quy tắc, Trương Huyền cũng không nhiều lời, gãi đầu một cái: “Cái này... Ta còn không có đàn ngọc, có thể hay không cho ta mượn một cái?”"),t("br"),t("br"),n._v("“Không đàn? Không đàn ngươi qua đây kiểm tra cái gì...”"),t("br"),t("br"),n._v("Thanh niên con mắt trợn tròn."),t("br"),t("br"),n._v("Đối với Ma âm sư tới nói, đàn ngọc chính là ăn cơm đũa, viết chữ bút, ngươi liền cái này đều không có, liền muốn khảo hạch... Nói đùa cái gì!"),t("br"),t("br"),n._v("Học đàn, liền đàn đều không có, liền muốn kiểm tra cấp, trò đùa không phải như vậy mở a!"),t("br"),t("br"),n._v("“Cái này... Ta nhớ được, khảo hạch Ma âm sư người, nếu như không có phù hợp đàn ngọc lời nói, Ma âm học viện, là có thể tạm thời mượn dùng...”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Nhìn xong nhiều như vậy thư, đối học viện quy định sớm đã rõ như lòng bàn tay."),t("br"),t("br"),n._v("Người tham gia khảo hạch, nếu như không có đàn ngọc, học viện có thể mượn một cái sử dụng, không tính là vi phạm quy định."),t("br"),t("br"),n._v("“Mượn là có thể mượn... Học viện đàn ngọc, đẳng cấp không cao không nói, cơ bản đều là không biết để đã bao nhiêu năm, hơi dùng sức, liền sẽ hư đồ cổ, dùng cái này kiểm tra lời nói, rất dễ dàng ảnh hưởng thành tích... Ngươi khẳng định muốn dùng?”"),t("br"),t("br"),n._v("Thanh niên nghi hoặc nhìn qua."),t("br"),t("br"),n._v("Học viện đàn ngọc, cùng dùng chung vũ khí một dạng, thống nhất chế tạo, không có bất kỳ cái gì kỹ thuật hàm lượng, lại thêm nhiều năm không có người dùng qua, cổ xưa không chịu nổi, có thể không thể sử dụng đều còn khó nói, dùng cái khảo hạch này... Thông qua tỷ lệ sẽ hạ thấp rất nhiều."),t("br"),t("br"),n._v("Nguyên nhân chính là như thế, không ít người cũng không nguyện ý sử dụng."),t("br"),t("br"),n._v("“Xác định!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Hắn hiện tại đi mua đàn ngọc khẳng định không còn kịp rồi, mua được còn cần đủ loại thích ứng, phiền phức cực kỳ, lại thêm bình thường cũng không thể nào dùng, còn không bằng dùng học viện dùng chung, miễn cho phiền phức."),t("br"),t("br"),n._v("“Cái kia được!”"),t("br"),t("br"),n._v("Thấy gặp được một cái không sợ chết, thanh niên mí mắt vừa nhấc, đi tiến gian phòng chỗ sâu, không lâu sau, lấy ra một cái hết sức bình thường cổ cầm cùng mấy cái dây đàn."),t("br"),t("br"),n._v("“Cẩn thận chút sử dụng, cái này là dự bị dây đàn, một khi đứt đoạn, cũng tốt thay thế... Ngươi có một cái canh giờ, trong vòng một canh giờ, một đầu hạc đều không có dẫn tới, nói rõ không qua ải, có thể đi về!”"),t("br"),t("br"),n._v("Thanh niên thản nhiên nói."),t("br"),t("br"),n._v("Hắn đối cái tên này căn bản không coi trọng."),t("br"),t("br"),n._v("Nào có học tập Ma âm, liền đàn đều không có?"),t("br"),t("br"),n._v("Đàn đều không có, nói rõ trình độ chẳng ra sao cả, hàng năm loại này tới nhờ nhiệt độ, cũng không phải là không có."),t("br"),t("br"),n._v("“Vâng!” Tiếp nhận cổ cầm, đưa trước khảo hạch cần thiết phí tổn, Trương Huyền nhấc chân đi vào Ma âm các."),t("br"),t("br"),n._v("Ma âm các khảo hạch, lấy tiên hạc định đẳng cấp, trận pháp dùng tương đối ít, lại thêm bình thường khảo hạch người không nhiều, cũng không cần học phần, chỉ cần tốn hao một ít linh thạch là đủ."),t("br"),t("br"),n._v("Dọc theo thông đạo hướng phía trong tiến lên, chung quanh đều là nặng nề trùng điệp bức tường, phía trên có khắc đặc thù hoa văn, tiếng đàn xao động ở phía trên, sẽ bị phản bắn đi ra, hình thành to lớn lôi âm."),t("br"),t("br"),n._v("Loại thanh âm này, chỉ có tiên hạc mới có thể nghe được, để làm chi nhảy múa."),t("br"),t("br"),n._v("Toàn bộ Ma âm các không có bất kỳ ai, Trương Huyền cũng vừa được vui thanh nhàn, không lâu sau liền đến được tận cùng bên trong nhất sân khấu, cổ tay khẽ đảo, đem cổ cầm đáp lên trên kệ."),t("br"),t("br"),n._v("Ngón tay cùng thư tịch đụng một cái, khảo hạch nhạc khúc đi vào trong óc."),t("br"),t("br"),n._v("“Rất đơn giản ca khúc...”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng."),t("br"),t("br"),n._v("Học được lục tinh cấp bậc Thiên Đạo Ma âm, hắn giờ phút này, chỉ bằng vào Ma âm lời nói, so với Tưởng viện trưởng đều chỉ mạnh không yếu, khảo hạch nhất tinh dùng nhạc khúc, đối với hắn mà nói đơn giản đến cực điểm."),t("br"),t("br"),n._v("“Thử một chút cổ cầm...”"),t("br"),t("br"),n._v("Ngón tay đáp lên dây đàn bên trên, nhẹ nhàng phất một cái, một đạo âm thanh trong trẻo lập tức vang lên, phát ra tựa như nước suối gõ nham thạch leng keng thanh âm."),t("br"),t("br"),n._v("Cái này học viện phối dùng cổ cầm, mặc dù cảm nhận kém chút, nhưng cũng có thể dùng."),t("br"),t("br"),n._v("“Bắt đầu đi!”"),t("br"),t("br"),n._v("Dù sao đều là khảo hạch, Trương Huyền cũng không có gì có thể xoắn xuýt, tĩnh tâm trầm tư, ngón tay nhẹ nhàng gạt, nhạc phổ bên trên tiếng đàn, lập tức chảy xuôi ra."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Ma âm viện, viện trưởng chỗ ở."),t("br"),t("br"),n._v("“Hắn đọc sách... Không náo ra động tĩnh gì chứ?”"),t("br"),t("br"),n._v("Xem đi vào sân nhỏ bạn cũ, Tưởng Thanh Cầm nhịn không được hỏi."),t("br"),t("br"),n._v("“Không có...” Ninh viện phó lắc đầu."),t("br"),t("br"),n._v("Nói thật, lúc trước hắn cũng có chút lo lắng, rời đi Tàng Thư khố sau liền không đi xa, quan sát được mấy canh giờ, phát hiện không xuất hiện biến cố, lúc này mới yên tâm lại."),t("br"),t("br"),n._v("“Vậy là tốt rồi...” Tưởng Thanh Cầm thở phào nhẹ nhõm."),t("br"),t("br"),n._v("“Bất quá...” Mày nhăn lại, Ninh viện phó muốn nói lại thôi."),t("br"),t("br"),n._v("“Làm sao vậy?” Tưởng Thanh Cầm vội vàng nhìn qua."),t("br"),t("br"),n._v("Nói thật, cái tên này truyền thuyết quá ác liệt, hắn đều làm cho nhất kinh nhất sạ."),t("br"),t("br"),n._v("“Hắn... Buổi sáng hôm nay nhìn xong thư, liền đi Ma âm các, đoán không sai, hẳn là đi khảo hạch Ma âm sư!”"),t("br"),t("br"),n._v("Ninh viện phó nói."),t("br"),t("br"),n._v("Tưởng Thanh Cầm da mặt nhảy một cái: “Hắn không phải cái gì cũng sẽ không ư? Khảo hạch cái gì Ma âm sư?”"),t("br"),t("br"),n._v("“Ta cũng không rõ ràng... Sợ làm phá hư, lặng lẽ đi theo, trốn ở bên ngoài quan sát, phát hiện... Chịu trách nhiệm khảo hạch mười lăm con tiên hạc, thế mà lục tục ngo ngoe đều bay vào, lại không đi ra!”"),t("br"),t("br"),n._v("Nhíu nhíu mày, Ninh viện phó sẽ thấy nói ra."),t("br"),t("br"),n._v("Ma âm các, cũng dưỡng một chút tiên hạc được tiến hành Ma âm sư khảo hạch, tổng cộng mười lăm con."),t("br"),t("br"),n._v("“Cũng bay tiến vào? Coi như hắn khảo hạch lục tinh Ma âm sư, sáu đầu là đủ rồi, mười lăm con cũng bay đi vào, làm gì?” Tưởng Thanh Cầm kỳ quái."),t("br"),t("br"),n._v("Khảo hạch lục tinh, cũng chỉ cần sáu đầu tiên hạc, mười lăm con đều bay qua... Làm sao nghe xong đã cảm thấy không đáng tin cậy đâu?"),t("br"),t("br"),n._v("“Ta cũng không biết chuyện gì xảy ra, liền đến cùng ngươi thương nghị...” Ninh viện phó gật đầu nói."),t("br"),t("br"),n._v("“Thương nghị? Có gì có thể thương nghị, nhanh đi qua nhìn một chút, cái tên này sẽ không phải đem tiên hạc tất cả đều đánh chết, nướng ăn đi...”"),t("br"),t("br"),n._v("Nhớ tới trước đó thanh niên làm ra đủ loại quái sự, Tưởng Thanh Cầm sắc mặt tái xanh, cũng nhịn không được nữa, đột nhiên đứng dậy, đang muốn cùng bạn cũ đi qua nhìn một chút, liền nghe được cách đó không xa cửa sân, “Kẹt kẹt!” Một tiếng mở ra, một cái đầu chui đi vào."),t("br"),t("br"),n._v("“Cái kia... Tưởng viện trưởng, ta muốn khảo hạch Ma âm sư, cái kia... Hạc không đủ, có thể mượn vài đầu cho ta không?”"),t("br"),t("br"),n._v("Trương Huyền một mặt lúng túng thanh âm vang lên."),t("br"),t("br"),n._v("“Mượn vài đầu?”"),t("br"),t("br"),n._v("Tưởng Thanh Cầm một cái lảo đảo, Ninh viện phó cũng trừng to mắt."),t("br"),t("br"),n._v("Hạc không đủ..."),t("br"),t("br"),n._v("Đây chính là ròng rã mười lăm con... Ma âm viện khảo hạch nhiều năm như vậy, đều đầy đủ, làm sao được ngươi liền không đủ?"),t("br"),t("br"),n._v("Ngươi... Đến cùng làm gì?"),t("br"),t("br"),n._v("Hai đại viện trưởng, buồn bực nhanh muốn khóc."),t("br"),t("br"),n._v("(Đại chương tiết, đi người khác hai chương còn nhiều thêm, nguyệt phiếu cho một tấm đi, hắc hắc! Mặt khác, công chúng số phát bốn tờ Trương Huyền hình ảnh, tấm thứ nhất bỏ phiếu khá nhiều, so cái khác ba cái cái cộng lại đều nhiều. Xem ra mọi người đối tấm thứ nhất ưa thích không rời (*). Nói thật, cái này một tấm ta cũng thật thích, lạnh lùng soái đẹp trai, phong lưu phóng khoáng, hơn người. Kỳ thật nói cho các ngươi biết một cái bí mật, bức tranh này mảnh là rất nhai trong gương chiếu, chính là lấy điểm mỹ nhan, nói cách khác, là rất nhai bản thân. Không tin các ngươi nhìn kỹ một chút, hẳn là có thể nhìn ra hình dáng. Wechat lục soát “hstysds” Hoặc là “Hoành Tảo Thiên Nhai” tăng thêm chú ý, kiểm tra lịch sử tin tức là đủ.)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-833"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-835"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);