(window.webpackJsonp=window.webpackJsonp||[]).push([[2117],{2472:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-828-tu-duong-thu-bi-phat-hien"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-828-tu-duong-thu-bi-phat-hien"}},[n._v("#")]),n._v(" Chương 828: Tử Dương thú bị phát hiện")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 828: Tử Dương thú bị phát hiện"),t("br"),t("br"),n._v("“Hội trưởng, Trương sư từ Tưởng viện trưởng chỗ ở sau khi ra ngoài, liền đi Hoài Vương gia đưa tặng phủ đệ, trong lúc đó tìm một chuyến Lạc lão sư, đến bây giờ đều không có đi ra!”"),t("br"),t("br"),n._v("Trận pháp học viện, trọng yếu nhất khu vực một cái rộng rãi trong biệt viện, một người nữ sinh hướng về phía trước xinh đẹp thân ảnh bẩm báo."),t("br"),t("br"),n._v("“Không đi ra?”"),t("br"),t("br"),n._v("Đổng Hân xoay người lại."),t("br"),t("br"),n._v("Y sư học viện sự tình về sau, nàng liền phái người chuyên môn dò xét vị này Trương Huyền, muốn tìm tìm nhược điểm của hắn, đáng tiếc, đối phương vội vàng không thấy được người, liền xem như nàng, đến bây giờ đều không hiểu ra sao."),t("br"),t("br"),n._v("Nữ sinh gật đầu."),t("br"),t("br"),n._v("“Đi xuống đi!”"),t("br"),t("br"),n._v("Khoát tay áo, Đổng Hân hai tay chắp sau lưng, thướt tha thân ảnh, phác hoạ ra một đạo tốt đẹp đường vòng cung, trong viện gió xuân hiu hiu xuống, lộ ra khác phong thái."),t("br"),t("br"),n._v("Không hổ là danh sư học viện hoa khôi của trường bảng xếp hạng khá cao nhân vật, nhất cử nhất động, đều để lộ ra khác mị lực."),t("br"),t("br"),n._v("Nếu như nói Hồ Yêu Yêu là một đóa hoa hồng có gai tốn, nàng chính là thanh nhã bách hợp, băng lãnh phía dưới, mang theo khác dụ hoặc."),t("br"),t("br"),n._v("“Không vào hang cọp làm sao bắt được cọp con, muốn tra ra cái tên này nhược điểm, chỉ sợ chỉ có thể tự mình đi qua!”"),t("br"),t("br"),n._v("Lông mày giương lên."),t("br"),t("br"),n._v("Nàng làm việc cẩn thận, không thích nhìn tư liệu, nghe người khác nói, trước đó món đồ kia, mặc dù Hồ Yêu Yêu đám người, đều nói rõ chi tiết, nàng vẫn là chưa tin, hao tốn thật nhiều ngày, bất chấp nguy hiểm chạy tới, thực địa dò xét."),t("br"),t("br"),n._v("Bây giờ nghĩ biết vị này Trương Huyền nhược điểm ở nơi nào, chỉ bằng vào thuộc hạ báo cáo khẳng định là không được, muốn tra tra rõ ràng, còn cần tự mình động thủ."),t("br"),t("br"),n._v("“Đi qua nhìn một chút!”"),t("br"),t("br"),n._v("Đổi một cái bình thường quần áo, thân thể mềm mại xoay một cái, Đổng Hân thẳng tắp hướng học viện bên ngoài bay vút đi."),t("br"),t("br"),n._v("Hoài Vương gia đưa tặng Trương Huyền tòa phủ đệ kia, đã sớm tìm người đánh tra rõ ràng, biết chính xác vị trí, không lâu sau đi tới gần."),t("br"),t("br"),n._v("“Không nghĩ tới, nơi này còn bố trí không ít trận pháp... Bất quá, khó không được ta!”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, thân thể nhoáng một cái, đi vào trong viện."),t("br"),t("br"),n._v("Nàng là danh sư học viện lớp năm đỉnh phong nhất học viên một trong, Hóa Phàm cửu trọng Tằm Phong cảnh cường giả, càng là trận pháp sư học viện viện trưởng Trần Thừa Tuần học sinh thân truyền."),t("br"),t("br"),n._v("Đối với trận pháp lý giải đạt đến cực cao cấp, tòa phủ đệ này bên trong trận pháp, phòng vệ một chút tứ tinh, ngũ tinh trận pháp sư có khả năng, muốn muốn ngăn cản nàng, căn bản làm không được."),t("br"),t("br"),n._v("Thân thể mềm mại tại trong phủ đệ xuyên qua, không lâu sau, liền đến đến một cái rộng lớn sân nhỏ."),t("br"),t("br"),n._v("Một cái trận pháp đang tại vận chuyển, mê vụ đem cả viện bao phủ ở bên trong, để cho người ta thấy không rõ lắm."),t("br"),t("br"),n._v("“Đây là... Tứ tinh đỉnh phong Phong Tức trận?”"),t("br"),t("br"),n._v("Nhìn thoáng qua, nhận ra được."),t("br"),t("br"),n._v("“Trận pháp này mục đích là vì che đậy khí tức, che giấu bí mật... Chẳng lẽ, đây chính là hắn nhược điểm?”"),t("br"),t("br"),n._v("Đổng Hân ánh mắt sáng lên."),t("br"),t("br"),n._v("Phong Tức trận, loại trừ che đậy khí tức bên ngoài, không có quá tác dụng lớn chỗ, đối phương trịnh trọng như vậy việc trong sân, bố trí loại trận pháp này, muốn nói không phải là vì che giấu cái gì, nàng đều không tin!"),t("br"),t("br"),n._v("“Nghĩ biện pháp vào xem...”"),t("br"),t("br"),n._v("Nắm đấm xiết chặt."),t("br"),t("br"),n._v("Đối phương bí mật đang ở trước mắt, không vào xem, chẳng khác nào lãng phí cơ hội, Đổng Hân răng ngà cắn chặt, đen nhánh hai mắt, ánh sáng không ngừng lấp lóe."),t("br"),t("br"),n._v("“Tứ tinh Phong Tức trận, giải quyết không khó, có điều, hơi không cẩn thận, liền sẽ đụng vào trận kỳ, gây nên biến hóa...”"),t("br"),t("br"),n._v("Nàng là lục tinh trận pháp sư, giải quyết Phong Tức trận không khó, nhưng muốn thần không biết quỷ không hay đi vào, cũng không rất dễ dàng."),t("br"),t("br"),n._v("Cần phải cẩn thận suy tính, nghiêm túc nghiên cứu mới được."),t("br"),t("br"),n._v("Thân thể nhoáng một cái, giấu ở sân nhỏ góc một khỏa nồng đậm trên cành cây, một cái la bàn xuất hiện tại lòng bàn tay, căn cứ chung quanh khí tràng, nhìn chằm chằm phía dưới cẩn thận suy tính."),t("br"),t("br"),n._v("“Ly ba, tốn năm...”"),t("br"),t("br"),n._v("Tinh thần cao độ tập trung, trong đầu không ngừng suy tính, suy tính càng nhiều, bội phục chi ý cũng càng ngày càng đậm."),t("br"),t("br"),n._v("“Bố trí cái này Phong Tức trận người, thực lực rất cao, trận pháp cùng toàn bộ phủ đệ hoàn mỹ dung hợp, vòng vòng đan xen, khí tức liên kết, liền xem như ta, đều làm không được điểm ấy...”"),t("br"),t("br"),n._v("Trước mắt trận pháp, mặc dù chỉ là tứ tinh đỉnh phong, nhưng cùng bốn phía hoàn mỹ phù hợp, phát huy ra ngũ tinh trận pháp mới có uy lực."),t("br"),t("br"),n._v("Có thể đem một cái bình thường tứ tinh trận pháp bố trí ra như thế trình độ, thực lực cao, so với nàng đều chỉ mạnh không yếu."),t("br"),t("br"),n._v("“Bất quá, tứ tinh trận pháp chính là tứ tinh, coi như bố trí khá hơn nữa, tìm tới sinh môn, đồng dạng có thể thần không biết quỷ không hay trà trộn vào đi...”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, không tỳ vết chút nào trên mặt lộ ra sự tự tin mạnh mẽ."),t("br"),t("br"),n._v("Nàng tại trên trận pháp thiên phú, vượt xa thường nhân, nếu không phải như thế, cũng không có khả năng bị đường đường trận pháp sư học viện viện trưởng thu làm thân truyền."),t("br"),t("br"),n._v("Trận pháp này phát huy ra tứ tinh trận pháp toàn bộ uy lực, nhưng... Nhận hạn chế cấp bậc, nàng đồng dạng có thể nhẹ nhõm phá giải!"),t("br"),t("br"),n._v("Hít sâu một hơi, đang muốn tìm đến sinh môn, tiến vào bên trong, đột nhiên cảm giác tới mặt đất một hồi lay động, một cỗ kịch liệt sóng năng lượng, từ trong trận pháp ở giữa lan truyền ra."),t("br"),t("br"),n._v("Dường như có cái gì siêu cấp cường giả, muốn đột phá trận pháp ràng buộc, từ đó chạy ra."),t("br"),t("br"),n._v("Oanh! Oanh! Oanh!"),t("br"),t("br"),n._v("Vội vàng nấp kỹ thân hình, lần nữa nghe được bên trong ba động truyền ra, ngay sau đó, một cỗ lực lượng khổng lồ hướng bốn phía dập dờn, Phong Tức trận bị lực lượng xông lên, phát ra đôm đốp thanh âm, lập tức vỡ vụn."),t("br"),t("br"),n._v("“Dùng sức mạnh cưỡng ép đem ngũ tinh hiệu quả trận pháp đánh sụp... Bên trong xông trận vị này, chỉ sợ đã đạt đến Thánh vực!”"),t("br"),t("br"),n._v("Con ngươi co rụt lại."),t("br"),t("br"),n._v("Cái này Phong Tức trận mặc dù chỉ là tứ tinh đỉnh phong, nhưng bố trí người, thủ pháp mười phần xảo diệu, phối hợp chung quanh địa thế, trận pháp, để hắn nắm giữ ngũ tinh uy lực."),t("br"),t("br"),n._v("Uy lực như thế, đều bị một chút chấn vỡ, đủ thấy vây khốn vị kia thực lực, tất nhiên đạt đến mười phần mức đáng sợ."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),n._v(" Trong lòng khiếp sợ, chỉ thấy vỡ vụn trong trận pháp, một bóng người cao to, thẳng tắp hướng ra phía ngoài thoát ra."),t("br"),t("br"),n._v("“Đây là... Tử Dương thú tiền bối?”"),t("br"),t("br"),n._v("Toàn thân cứng đờ."),t("br"),t("br"),n._v("Làm tại học viện chờ đợi không dưới năm năm lão sinh, lão viện trưởng thú sủng, cũng từng gặp vài lần... Không mất tích, bị Dị Linh tộc nhân bắt đi ư?"),t("br"),t("br"),n._v("Tại sao lại ở chỗ này?"),t("br"),t("br"),n._v("Chẳng lẽ..."),t("br"),t("br"),n._v("Toàn thân cứng đờ, ở sâu trong nội tâm sinh ra vô cùng khủng hoảng."),t("br"),t("br"),n._v("“Muốn chạy trốn, Ngoan Nhân, cho ta đem nó đánh xuống!”"),t("br"),t("br"),n._v("Đang đang sợ hãi, liền nghe đến một cái thanh âm nhàn nhạt vang lên, lập tức một cỗ phóng lên tận trời sát lục chi khí, di tán toàn bộ sân nhỏ."),t("br"),t("br"),n._v("Lạch cạch!"),t("br"),t("br"),n._v("Vừa vặn bay lên Tử Dương thú tiền bối, hô cũng không kịp, liền rơi trên mặt đất, không ngừng giật giật."),t("br"),t("br"),n._v("“Là... Dị Linh tộc!”"),t("br"),t("br"),n._v("Dọa đến ngừng thở, Đổng Hân một cử động nhỏ cũng không dám, toàn thân cứng ngắc như là đá."),t("br"),t("br"),n._v("“Mấy người các ngươi, lên cho ta, đánh chết nha!”"),t("br"),t("br"),n._v("Ngay sau đó lần nữa nghe được Trương Huyền tiếng âm vang lên, lập tức hai mươi đầu trái phải Thánh vực cấp bậc Dị Linh tộc nhân, điên cuồng hướng Tử Dương thú tiền bối vọt tới."),t("br"),t("br"),n._v("Lốp bốp!"),t("br"),t("br"),n._v("Một hồi quyền cước vào thịt, vừa rồi còn sinh long hoạt hổ Tử Dương thú tiền bối, thời gian nháy mắt, liền bị đánh ngã xuống đất ngất đi, không ngừng giật giật, xem ra bất cứ lúc nào cũng sẽ cúp máy."),t("br"),t("br"),n._v("Gắt gao che miệng, Đổng Hân toàn thân lỗ chân lông toàn bộ đóng chặt, giấu ở trong lá cây, một điểm động tĩnh cũng không dám lấy ra."),t("br"),t("br"),n._v("Hơn hai mươi đầu cường giả Thánh vực, liền Tử Dương thú tiền bối, cũng không là đối thủ, nàng một khi bị phát hiện, có thể đoán được, tất nhiên sẽ bị tại chỗ giết chết!"),t("br"),t("br"),n._v("“Lại choáng...”"),t("br"),t("br"),n._v("Xuyên thấu qua lá cây khe hở, thấy rõ ràng Trương Huyền đi ra, lạnh hừ một tiếng, cổ tay khẽ đảo, đem Tử Dương thú không biết thu tới nơi nào, lập tức nhìn xem chung quanh bị phá hư trận pháp, như có điều suy nghĩ."),t("br"),t("br"),n._v("“Trận pháp này, quá yếu... Xem ra cần học tập chút lợi hại trận pháp mới được, đi trận pháp học viện đi!”"),t("br"),t("br"),n._v("Nhìn quanh một vòng, Trương Huyền lẩm bẩm một câu, xoay người đi ra ngoài."),t("br"),t("br"),n._v("Sợ thanh niên sẽ đi mà quay lại, giấu ở cây bên trong một cử động nhỏ cũng không dám, Đổng Hân né tròn ba canh giờ, thấy trời sắp tối rồi, lúc này mới hoạt động một chút thoáng cứng ngắc thân thể, vội vã hướng ra phía ngoài vọt tới."),t("br"),t("br"),n._v("Hôm nay nhìn thấy sự tình, thực sự quá lớn!"),t("br"),t("br"),n._v("Lớn đã để nàng hoảng sợ tình trạng!"),t("br"),t("br"),n._v("Khó trách, vị này Trương Huyền thiên tài như thế, cái gì đều sẽ... Nguyên lai, không phải nhân loại!"),t("br"),t("br"),n._v("Nghe nói Dị Linh tộc nhân vương tộc, ngụy trang thành nhân loại, cùng nhân loại đồng dạng, Thánh vực cấp bậc danh sư, đều không phát hiện được, vốn cho rằng là lời nói vô căn cứ... Không nghĩ tới thật sự!"),t("br"),t("br"),n._v("Dị Linh tộc nhân lẫn vào danh sư học viện, còn thành danh sư... Tin tức này một khi truyền ra ngoài, chỉ sợ toàn bộ học viện đều sẽ bạo tạc, thậm chí, Danh Sư đường đều sẽ nổ tung."),t("br"),t("br"),n._v("“Không được, chuyện này nhất định phải lập tức báo cáo mới được...”"),t("br"),t("br"),n._v("Nhớ tới nhìn thấy sự tình, Đổng Hân cũng nhịn không được nữa, vội vã hướng danh sư học viện chạy như bay."),t("br"),t("br"),n._v("Chuyện này liên quan thực sự quá lớn, nàng một cái học sinh khẳng định là không cách nào giấu diếm, chỉ có thể mau chóng báo cáo."),t("br"),t("br"),n._v("Nhưng... Như thế kinh thế hãi tục, thật muốn nói ra đến, sẽ có mấy người tin tưởng?"),t("br"),t("br"),n._v("Vị này Trương Huyền, sâu học viện cháo viện trưởng, Triệu viện trưởng, Vệ viện trưởng thậm chí Tưởng viện trưởng coi trọng, càng cùng danh sư học viện Mạc đường chủ giao hảo... Cùng tổng bộ tới vị kia Lạc sư, quan hệ cũng không tệ..."),t("br"),t("br"),n._v("Không có chứng cớ xác thật, liền trực tiếp chỉ ra chỗ sai, đừng nói để cho người ta không tin, làm không cẩn thận, sẽ còn bị xem như phỉ báng, hủy bỏ danh sư tư cách!"),t("br"),t("br"),n._v("“Trước tìm lão sư thương nghị...”"),t("br"),t("br"),n._v("Từng đạo suy nghĩ tại trong óc lấp lóe."),t("br"),t("br"),n._v("Có thể từ danh sư học viện trổ hết tài năng, tổ kiến Đổng Hân biết, trở thành học sinh bên trong nổi danh học tỷ, nàng tuyệt không phải làm việc người lỗ mãng."),t("br"),t("br"),n._v("Vị này Trương Huyền tiến vào học viện trong khoảng thời gian ngắn, lấy được mấy vạn học viên tín nhiệm, càng làm cho bốn đại viện trưởng tính cả Mạc đường chủ thuyết phục... Địa vị tuyệt không phải nàng có thể so sánh!"),t("br"),t("br"),n._v("Không có chứng cớ dưới tình huống, tùy tiện nói ra, một khi bị cắn ngược, không những không có thể giải quyết vấn đề, làm không cẩn thận, sẽ còn bị thủ tiêu tư cách, thậm chí... Gặp phải ám sát!"),t("br"),t("br"),n._v("Do đó, chuyện này, không thể lỗ mãng, nhất định phải cẩn thận cẩn thận hơn!"),t("br"),t("br"),n._v("“Lão sư coi như tin tưởng ta, địa vị không bằng Triệu viện trưởng, cũng không quyền lên tiếng... Đúng, tìm Lục Phong viện trưởng!”"),t("br"),t("br"),n._v("Một bóng người hiện lên ở trong óc."),t("br"),t("br"),n._v("Lão sư hắn Trần Thừa Tuần địa vị tại học viện mặc dù cũng không thấp, nhưng không sánh được Triệu Bính Tuất, một khi đối phương ngăn cản, khẳng định khó mà có hiệu quả."),t("br"),t("br"),n._v("Trừ phi... Tìm thân là đại viện trưởng Lục Phong!"),t("br"),t("br"),n._v("Lục Phong cùng vị này Trương Huyền bản thân liền không hợp nhau, đem chuyện này nói cho hắn nghe lời nói, có lẽ có thể tạo được hiệu quả tốt hơn."),t("br"),t("br"),n._v("“Trước tìm lão sư, để lão sư tìm tới Lục Phong viện trưởng, sau đó... Coi bọn hắn mặt đem chuyện này nói ra!”"),t("br"),t("br"),n._v("Đổng Hân cắn răng một cái."),t("br"),t("br"),n._v("Chuyện này liên quan quá lớn, không phải tùy tiện nói một chút là được, nhất định phải cẩn thận cân nhắc."),t("br"),t("br"),n._v("Lúc này danh sư học viện, muốn nói ai có thể dựa nhất, chỉ có Lục Phong viện trưởng."),t("br"),t("br"),n._v("Hắn cùng cái này Trương Huyền không những quan hệ không tốt, còn có mâu thuẫn, đem tin tức này nói ra, tất nhiên sẽ gây nên cao độ coi trọng."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Nghĩ tới những thứ này, không do dự nữa, thân thể mềm mại nhoáng một cái, Đổng Hân thẳng tắp hướng trận pháp sư học viện viện trưởng chỗ ở bay vút đi."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-836"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-838"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);