(window.webpackJsonp=window.webpackJsonp||[]).push([[212],{565:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1168-khong-su-hu-tuong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1168-khong-su-hu-tuong"}},[n._v("#")]),n._v(" Chương 1168: Khổng sư hư tượng")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Ngươi...” Nghe được quát tháo, Trương Cửu Tiêu thân thể nhoáng một cái, suýt chút nữa hộc máu."),t("br"),t("br"),n._v("Hắn vừa mới để người ta mài mực, thời gian nháy mắt lần nữa rơi vào trên người mình... Mặt mũi này đánh cũng quá nhanh đi!"),t("br"),t("br"),n._v("Kìm nén sắc mặt đỏ lên, sầu riêng kết ra trái cây mất một chỗ, Trương Cửu Tiêu đang nghĩ nổi giận, liền nghe đến thanh âm của đối phương tiếp tục vang lên."),t("br"),t("br"),n._v("“Làm sao? Không đồng ý? Ta phải giải quyết phong ấn, ở đây nhiều người như vậy, khẳng định cũng đều muốn nhìn một chút... Trọng yếu như vậy chuyện, hẳn là việc nghĩa chẳng từ đi...”"),t("br"),t("br"),n._v("Khuôn mặt biến thành màu đen, Trương Cửu Tiêu muốn nói giờ phút này tất cả đều giấu ở ngực, cũng lại không nói ra được."),t("br"),t("br"),n._v("Dường như... Lời này, là vừa rồi hắn nói đối phương, một chữ đều không sai..."),t("br"),t("br"),n._v("Vừa rồi hăm hở, chỉ điểm giang sơn, mà bây giờ... Đổi cái thân phận, mặt đều sưng lên."),t("br"),t("br"),n._v("“Tự gây nghiệt thì không thể sống...”"),t("br"),t("br"),n._v("Tràn đầy buồn bực, nhưng cũng biết, vừa rồi như thế oán giận đối phương, đối phương nói ra lời giống vậy, không gì đáng trách, tức giận đến sắp nổ tung, còn là đi tới trước mặt, bắt đầu mài mực."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Thấy hắn lại không nhiều lời, Trương Huyền thoả mãn nhẹ gật đầu."),t("br"),t("br"),n._v("Nói thật, hắn không phải mang thù người, bởi vì có thù tại chỗ liền báo."),t("br"),t("br"),n._v("Đối phương địch ý đối với hắn, vừa thấy mặt liền đã nhìn ra, đã như vậy, cũng không cần thiết làm người tốt..."),t("br"),t("br"),n._v("  Huống chi, cái tên này gia tộc, còn muốn cưới Lạc Nhược Hi..."),t("br"),t("br"),n._v("Lúc trước Phùng Huân chỉ là thổ lộ thoáng cái, liền bị đánh thành đầu heo, suýt chút nữa chết, mặc dù không biết cái tên này gia tộc ở đâu, tạm thời thực lực cũng không có tư cách tìm phiền toái, nhưng mà... Không trở ngại, trước thu chút lợi tức."),t("br"),t("br"),n._v("Thấy đối phương mài mực xong xuôi, Trương Huyền đang nghĩ dính đầy bút lông, loại bỏ bức họa này ý cảnh, ý niệm có thể đi vào, đột nhiên trong lòng hơi động, ngẩng đầu nhìn về phía cách đó không xa Mông Xung hội trưởng."),t("br"),t("br"),n._v("“Mông hội trưởng, không biết ngươi nơi này nhưng có mang theo người thư hoạ sách? Lục cảnh, thất cảnh đều muốn, số lượng càng nhiều càng tốt, tìm một cái thích hợp nhất điểm, kích hoạt linh tính, mới có thể làm đến hai không lẫn nhau tổn thương... Ta muốn thấy nhìn, có hay không tương tự sách, có thể tham khảo một chút!”"),t("br"),t("br"),n._v("“Lục cảnh, thất cảnh sách?”"),t("br"),t("br"),n._v("Không nghĩ tới tại đây giây phút, trước mắt vị này muốn nhìn sách, Mông hội trưởng lắc đầu: “Trên người của ta không mang bao nhiêu, có điều, thư họa sư công hội cách nơi này không xa, ta có thể quá khứ lấy bên trên một chút tới!”"),t("br"),t("br"),n._v("“Vậy làm phiền...”"),t("br"),t("br"),n._v("Trương Huyền ôm quyền."),t("br"),t("br"),n._v("“Không cần khách khí!” Lên tiếng, Mông Xung hội trưởng xoay người ra khỏi phòng, thời gian không dài, lần nữa trở về, cổ tay khẽ đảo, một đống thư tịch rơi vào trước mắt, lít nha lít nhít chừng mấy vạn vốn nhiều."),t("br"),t("br"),n._v("Hắn là thư họa sư công hội hội trưởng, lấy chút sách, không có người sẽ nói cái gì."),t("br"),t("br"),n._v("Trương Huyền đi tới trước mặt, rất nhanh quét một lần, lúc này mới làm bộ lấy ra một bản, tiện tay mở ra, giả bộ như cẩn thận đọc bộ dạng, trên thực tế ý thức lại đi vào thư viện."),t("br"),t("br"),n._v("“Thiên Đạo thư hoạ đệ lục trọng, đệ thất trọng...”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, mở ra vừa mới hình thành đệ lục trọng sách."),t("br"),t("br"),n._v("Hắn chỉ học qua đệ ngũ trọng, mặc dù nhờ vào Thiên Đạo công pháp thần kỳ, cũng có thể làm ra linh trí phá Phàm cấp cái khác tác phẩm hội họa, trên thực tế cũng không học qua đệ lục trọng công pháp."),t("br"),t("br"),n._v("Sau nửa canh giờ, đệ lục trọng dung hội quán thông."),t("br"),t("br"),n._v("Lại sau nửa canh giờ, đệ thất trọng cũng dung hội quán thông."),t("br"),t("br"),n._v("Trương Huyền lúc này mới thở ra một hơi, để quyển sách trên tay xuống tịch hai mắt tỏa ánh sáng."),t("br"),t("br"),n._v("Hắn giờ phút này, đối thư hoạ đã hiểu, đã đạt đến thất tinh đỉnh phong, thậm chí có thể so với bát tinh sơ cấp thư hoạ đại tông sư."),t("br"),t("br"),n._v("“Trương sư... Có thể bắt đầu chưa?”"),t("br"),t("br"),n._v("Gặp trước mắt vị này, cầm lấy một bản không đáng chú ý sách, ngồi xuống liền là một canh giờ, mặt nạ đồng xanh tràn đầy cuống cuồng."),t("br"),t("br"),n._v("“Được rồi!”"),t("br"),t("br"),n._v("Gật gật đầu, Trương Huyền hít sâu một hơi, hai bước đi tới tác phẩm hội họa trước mặt, Minh Lý Chi Nhãn nhúc nhích, lần nữa nhìn sang."),t("br"),t("br"),n._v("Nếu như nói vừa rồi bức họa này, hoàn toàn xem không hiểu, tất cả dựa vào thư viện cho ra thiếu hụt, tiến hành đã hiểu, hiện tại đã có thể thấy được cụ thể chuyện gì xảy ra, rất nhiều nơi, vô sự tự thông, hoàn toàn hiểu."),t("br"),t("br"),n._v("“Ta muốn bắt đầu...”"),t("br"),t("br"),n._v("Không nói thêm lời, Trương Huyền bút lông tại trên nghiên mực nhẹ nhàng tiếp xúc, liếm đã no đầy đủ bút, tìm đúng vị trí, đầu bút lông nhẹ nhàng rơi xuống."),t("br"),t("br"),n._v("Hô hô hô hô!"),t("br"),t("br"),n._v("Thời gian nháy mắt, một bức tranh tranh liền xuất hiện ở trước mắt, an tĩnh lơ lửng giữa không trung, cũng không rơi xuống."),t("br"),t("br"),n._v("“Thật là cao minh hư không lâm sách!”"),t("br"),t("br"),n._v("Nhìn thấy hắn vẽ tranh, một bên Mông Xung hội trưởng con ngươi co rụt lại."),t("br"),t("br"),n._v("Ngoài nghề xem náo nhiệt, trong nghề xem môn đạo."),t("br"),t("br"),n._v("Đồng dạng là hư không lâm sách, vừa rồi Trương Cửu Tiêu, chỉ là da lông, mà trước mắt vị này, ẩn chứa tốt đẹp ý cảnh ở bên trong, đã đạt đến vô cùng cao xa chi cảnh."),t("br"),t("br"),n._v("“Cao minh? So với ngươi làm sao?” Một bên Mục hội trưởng nhìn qua."),t("br"),t("br"),n._v("“Nói thật... Ta kém xa tít tắp, đối phương hư không lâm sách, mỗi một bút vạch một cái, đều kèm theo ý vị, cho người ta một loại, như thật như ảo cảm giác, tựa như lúc nào cũng sẽ độc lập đi ra, biến thành chân chính đồ vật... Đã đến gần vô hạn thư hoạ bát cảnh, ta kém rất nhiều...”"),t("br"),t("br"),n._v("Mông Xung cười khổ."),t("br"),t("br"),n._v("Mặc dù không muốn thừa nhận, nhưng cũng biết, đối phương vượt qua hắn thực sự quá nhiều."),t("br"),t("br"),n._v("Khó trách có thể một cái nhìn ra bức họa này vấn đề, loại sách này tranh năng lực, Thanh Nguyên đế quốc, chỉ sợ đã không người có thể đưa ra phải."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Thấy cảnh này, Trương Cửu Tiêu càng là khóe miệng co giật, cảm thấy sắp điên rồi."),t("br"),t("br"),n._v("Mới vừa gặp được đối phương thời điểm, còn một mặt đắc ý nói muốn cho cùng chỉ điểm, bây giờ mới biết, người ta bất kể giám bảo còn là thư hoạ, đều so với hắn còn cao minh hơn không biết gấp bao nhiêu lần..."),t("br"),n._v("Tại chuyên gia trước mặt khoe khoang... Thật sự là mất mặt ném chết rồi..."),t("br"),t("br"),n._v("Hô hô hô!"),t("br"),t("br"),n._v("Mọi người trong lúc khiếp sợ, Trương Huyền dưới ngòi bút không ngừng, một cánh cửa bộ dáng hình vẽ xuất hiện ở trước mắt mọi người."),t("br"),t("br"),n._v("“Cửa?”"),t("br"),t("br"),n._v("“Vẽ cửa làm gì?”"),t("br"),t("br"),n._v("“Ngươi vậy thì không biết, bộ này sơn thủy đồ, tại chúng ta nhìn tới, tựa như chiếu vào phía bên ngoài cửa sổ đồng dạng, mặc dù thoạt nhìn rất gần, trên thực tế, lại có nhất định khoảng cách... Muốn ý thức đến gần lời nói, chỉ có một cái phương pháp, mở ra môn hộ, đi tới trước mặt, nếu không, vĩnh viễn làm không được!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Thấy rõ ràng hắn vẽ ra đồ vật, mọi người đầu tiên là khiếp sợ, lập tức bừng tỉnh hiểu ra."),t("br"),t("br"),n._v("Ở đây đều là Thanh Nguyên thành đỉnh phong nhất nhân vật, mặc dù nhìn không ra phong ấn đến cùng như thế nào giải quyết, loại thủ đoạn này, còn có thể đã hiểu."),t("br"),t("br"),n._v("Sơn thủy đồ, đạt đến như thật như ảo cảnh giới, thoạt nhìn rất gần, trên thực tế cực xa, muốn khải linh đối phương linh trí, chỉ có đi tới trước mặt mới có thể làm đến, tự nhiên là cần một cánh cửa..."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Cửa hoàn thành, Trương Huyền thu bút, lông mày giương lên, bàn tay bỗng nhiên hướng phía dưới đè ép."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Trên không lơ lửng mực nước lập tức rơi xuống trên bức họa."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Một tiếng nổ vang, trước đó khoảng cách rất xa sơn mạch, lập tức xuất hiện ở trước mắt mọi người, hình như hắn vẽ cái cửa này, mở ra thông đạo, người người đều có thể trực tiếp chui vào tranh bên trong."),t("br"),t("br"),n._v("“Tốt rồi, ta muốn bắt đầu...”"),t("br"),t("br"),n._v("Làm xong những này tại, Trương Huyền thở phào nhẹ nhõm, quay đầu nhìn Ngô sư một cái: “Ngô sư, làm phiền ngươi giúp ta thủ hộ!”"),t("br"),t("br"),n._v("Mặc dù mọi người không có khả năng đánh lén, nhưng vẫn là phải cẩn thận một chút."),t("br"),t("br"),n._v("“Được!”"),t("br"),t("br"),n._v("Ngô sư đi tới trước mặt."),t("br"),t("br"),n._v("Trương Huyền lúc này mới khoanh chân ngồi dưới đất, tinh thần khẽ động, một đạo ý niệm dọc theo mới vừa vẽ ra tới cửa, đi vào."),t("br"),t("br"),n._v("Đi vào cửa lớn, lập tức cảm thấy nồng nhuận khí ẩm, đập vào mặt, Trương Huyền ý niệm nhanh chóng tiến lên, rất mau tới đến giữa sườn núi."),t("br"),t("br"),n._v("Mây trắng bao phủ xuống ngọn núi, một mảnh đen kịt, cái gì đều không nhìn thấy, tiến vào bên trong thật giống như đi vào đêm tối, Trương Huyền biết đây là linh tính không có nhen lửa nguyên nhân, cũng không ngừng nghỉ, vận chuyển khải linh phương pháp, ngón tay bỗng nhiên nhấn tới."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Từng đạo ánh sáng chiếu rọi mà lên, liên tục hơn hai mươi cái ngọn lửa thắp sáng, lúc này mới đem chung quanh nhen lửa, có điều, khoảng cách hoàn toàn kích hoạt linh tính, còn kém một tia."),t("br"),t("br"),n._v("Dù vậy, ánh sáng cũng đem mây mù ở dưới hắc ám toàn bộ chiếu sáng, toàn bộ ngọn núi hoàn chỉnh xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Là một mảnh nồng đậm rừng rậm, thoạt nhìn cùng địa phương khác không có gì khác biệt, có điều, ngọn núi phía trên, dương quang phóng xuống, một cái phai mờ cái bóng, chậm rãi hiện lên, cho người ta một loại muốn quỳ lễ cảm giác."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Nhìn một cái, Trương Huyền con ngươi co rụt lại."),t("br"),t("br"),n._v("Cái này hư ảnh, hắn gặp qua."),t("br"),t("br"),n._v("Khổng sư!"),t("br"),t("br"),n._v("Lúc này Khổng sư, yên tĩnh thản nhiên, cho người ta một loại an lành yên bình cảm giác, lại cho người ta một loại, xuyên qua thời gian, bất cứ lúc nào cũng sẽ biến mất cảm giác."),t("br"),t("br"),n._v("“Đây là... Vô Bi lão nhân nhìn thấy cảnh tượng!”"),t("br"),t("br"),n._v("Nắm đấm xiết chặt."),t("br"),t("br"),n._v("Bức họa này, là Vô Bi lão nhân vẽ ra tới, Khổng sư hư ảnh, tự nhiên cũng là giả, nhưng... Trước mắt cái này hư ảnh, thần vận mười phần, cho người ta một loại quỳ bái cảm giác, tuyệt không phải phán đoán liền có thể tưởng tượng ra tới."),t("br"),t("br"),n._v("Nói cách khác..."),t("br"),t("br"),n._v("Vị này Vô Bi lão nhân, từng tận mắt thấy qua tràng cảnh này, ký ức quá mức khắc sâu, lúc này mới đem hắn vẽ ra."),t("br"),t("br"),n._v("“Ngô sư nói, vị này Vô Bi lão nhân, lúc còn trẻ, mười phần bình thường, một trăm tám mươi tuổi về sau, đột nhiên đốn ngộ, không chỉ tu vi đột nhiên tăng mạnh, tại thư hoạ bên trên cũng như Thần dạy... Chẳng lẽ, là bởi vì thấy được Khổng sư hư ảnh, đột nhiên khai khiếu gây nên?”"),t("br"),t("br"),n._v("Ngô sư từng đã nói với hắn vị này Vô Bi lão nhân, kinh nghiệm có thể nói thần tích."),t("br"),t("br"),n._v("Nếu như nói chưa từng có cơ may lớn gì, đánh chết cũng không tin."),t("br"),t("br"),n._v("Có lẽ... Chính là bởi vì thấy được Khổng sư hư ảnh, một khi đốn ngộ, mới thành bát tinh thư họa sư, về sau thực lực đạt tới, cảm niệm ân tình, đem trong lòng nhận thấy, lưu tại trên giấy."),t("br"),t("br"),n._v("“Bất quá, Khổng sư sự tình, dính dáng quá lớn, làm xong tranh về sau, biết tin tức một khi rò rỉ, tất nhiên sẽ có vô số phiền phức... Có lẽ, phiền phức đã tới, lúc này mới dùng mây trắng đem hắn che lấp, kết quả... Còn là bị độc thủ, mới vừa làm xong những này, liền mất tích không thấy...”"),t("br"),t("br"),n._v("Trương Huyền phỏng đoán."),t("br"),t("br"),n._v("Đi qua bốn lần trời nhận danh sư, suy nghĩ của hắn rõ ràng có trật tự, mặc dù thoạt nhìn rất nhiều chuyện, không có đầu mối, nhưng nghĩ một lát, vẫn như cũ có thể suy đoán ra một chút tới."),t("br"),t("br"),n._v("Khổng sư hư tượng, tại sao lại tại đây cái trên dãy núi xuất hiện, xuất hiện địa điểm lại tại nơi nào... Đây đều là vấn đề, bị người hữu tâm phát hiện, tất nhiên sẽ gây nên rất lớn rung chuyển."),t("br"),t("br"),n._v("Thân là bát tinh thư họa sư, Xuất Khiếu cảnh cường giả, Vô Bi lão nhân không có khả năng không biết."),t("br"),t("br"),n._v("Đáng tiếc, làm ra tác phẩm, chính mình cũng không đành lòng từ bỏ, xoắn xuýt trọn ba mươi năm... Kết quả, còn là không có chạy trốn vận mệnh."),t("br"),t("br"),n._v("“Khổng sư xuất hiện ở đây, nói rõ cùng Khưu Ngô cung đồng dạng, nơi này tất nhiên có lưu lại di tích... Xem trước một chút, hư ảnh xuất hiện ở nơi nào!”"),t("br"),t("br"),n._v("Đem những này nghĩ thông suốt, Trương Huyền vội vàng ngẩng đầu hướng Khổng sư hư ảnh xuất hiện địa phương nhìn sang."),t("br"),t("br"),n._v("“Ở nơi đó...”"),t("br"),t("br"),n._v("Rất mau tìm đến vị trí cụ thể, Trương Huyền ánh mắt sáng lên, vội vã bay lượn mà đi."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1176"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1178"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);