(window.webpackJsonp=window.webpackJsonp||[]).push([[2120],{2475:function(n,h,t){"use strict";t.r(h);var g=t(44),c=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-75-thuong-than-tho-huyet"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-75-thuong-than-tho-huyet"}},[n._v("#")]),n._v(" Chương 75: Thượng Thần thổ huyết")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v(" “Trước ta cùng Trương lão sư thảo luận qua giáo viên sát hạch rất nhiều vấn đề, hắn chẳng những có thể rõ nét trả lời, mỗi một vấn đề còn đều có thể nói ra bảy, tám cái đáp án, những cái này đáp án ngay cả ta đều không đáp lại được, bác học trình độ, để người khâm phục! Lúc đó liền kỳ quái, đơn giản như vậy giáo viên sát hạch, hắn làm sao có khả năng một phần không được, nguyên lai... Công bố chính là giả thành tích!” "),t("br"),t("br"),n._v(" Trầm Bích Như cũng nhớ ra cái gì đó, không nhịn được nói chen vào, trong đôi mắt đẹp mang theo lửa giận."),t("br"),t("br"),n._v(" Tối ngày hôm qua ăn cơm, nàng chuyên môn thi qua Trương Huyền, kết quả đối phương đối đáp trôi chảy, lúc đó nàng liền cảm thấy kỳ quái, nghe được vừa nãy đối thoại rốt cục “Bỗng nhiên tỉnh ngộ”."),t("br"),t("br"),n._v(" Hắn khẳng định trả lời chính xác, nhưng phòng giáo dục không cho hắn phân!"),t("br"),t("br"),n._v(" Nói cách khác, của hắn điền bài thi không có bất cứ vấn đề gì, có thể... Phê duyệt giáo dục lão sư, cứ thế là đem phán thành không điểm!"),t("br"),t("br"),n._v(" Tại sao?"),t("br"),t("br"),n._v(" Tại sao phải làm như vậy?"),t("br"),t("br"),n._v(" “Khó trách Trương lão sư vẫn không muốn nói, mọi người đều là đồng sự, một khi nói ra khẳng định ảnh hưởng quan hệ, càng ảnh hưởng học viện danh dự! Vì cái này, hắn tình nguyện một người mang ‘Rác rưởi’ bêu danh!” Vương hoằng tộc trưởng càng nghĩ càng “Rõ ràng”, “Bỗng nhiên tỉnh ngộ”."),t("br"),t("br"),n._v(" Ngươi không thấy vừa nãy Trương lão sư vẫn do dự có muốn hay không nói sao?"),t("br"),t("br"),n._v(" Nếu như không phải là bị bức đến bất đắc dĩ, khả năng đồng dạng sẽ không đem chuyện này công bố tại chúng, dù cho bị nhiều hơn nữa người hiểu lầm, đều yên lặng chịu đựng... Thậm chí, các ngươi như vậy chèn ép hắn, hắn cũng chỉ là bày ra tu vi, vẫn chưa nói tỉ mỉ, cụ thể chi tiết đều là chúng ta đoán được..."),t("br"),t("br"),n._v(" Cái gì gọi là làm người gương sáng?"),t("br"),t("br"),n._v(" Đây mới là!"),t("br"),t("br"),n._v(" Cái gì là sư đức như núi, không tranh với đời?"),t("br"),t("br"),n._v(" Đây mới là!"),t("br"),t("br"),n._v(" “Chuyện không công bình một khi truyền ra, tất cả mọi người đều sẽ nghi vấn học viện giáo viên sát hạch độ chuẩn xác, cũng biết hoài nghi các lão sư khác chân thực dạy học trình độ... Cứ như vậy, học viện chỉ biết rơi vào hỗn loạn! Hắn lựa chọn không nói, bản thân yên lặng chịu đựng, tình nguyện bị người hiểu lầm, phần này lòng dạ, phần này rộng lượng...” "),t("br"),t("br"),n._v(" Vương gia một trưởng lão càng nói càng kích động."),t("br"),t("br"),n._v(" Trên thế giới dĩ nhiên có như thế vĩ đại lão sư..."),t("br"),t("br"),n._v(" Buồn cười chúng ta trước còn hiểu lầm hắn cái gì đều sẽ không, là tên rác rưởi!"),t("br"),t("br"),n._v(" Nói nói, mọi người đồng loạt đem con mắt nhìn về phía cách đó không xa Thượng Thần trưởng lão, từng cái từng cái tràn đầy lửa giận."),t("br"),t("br"),n._v(" Ngươi xem một chút người ta, ngươi nhìn lại mình một chút!"),t("br"),t("br"),n._v(" Đồng dạng là lão sư, chênh lệch làm sao lớn như vậy chứ?"),t("br"),t("br"),n._v(" Trương lão sư vì nhớ tình nghĩa, chịu nhục, đều không bán đi đồng sự, mà các ngươi..."),t("br"),t("br"),n._v(" “Ta...” "),t("br"),t("br"),n._v(" Thượng Thần sững sờ ở tại chỗ, cả người nhanh muốn điên."),t("br"),t("br"),n._v(" Mẹ nó, chuyện gì thế này?"),t("br"),t("br"),n._v(" Cái gì làm giả? Cái gì nhớ cảm tình..."),t("br"),t("br"),n._v(" Ngươi lần trước sát hạch là Chân Khí cảnh, hơn nữa giáo viên sát hạch quấn mặt lung ta lung tung, không chiếm được một phần là bình thường... Ta tự mình thẩm vấn duyệt phê chữa, không thể phạm sai lầm, làm sao liền thành ta cố ý hãm hại?"),t("br"),t("br"),n._v(" Trương Huyền, ngươi thật hắn mẹ tôn tử, có bản lĩnh, ngươi cho ta đem lời nói rõ ràng ra..."),t("br"),t("br"),n._v(" Thượng Thần trưởng lão chỉ cảm thấy một ngụm máu tươi nén tại bên miệng, bất cứ lúc nào cũng sẽ phun mạnh mà ra."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v(" Nhìn thấy như vậy náo nhiệt một màn, Trương Huyền cũng trợn mắt ngoác mồm."),t("br"),t("br"),n._v(" Kỳ thực ta vừa nãy ý tứ là muốn nói phòng giáo vụ sát hạch không chính xác, làm sao... Một hồi họa phong biến thành như vậy?"),t("br"),t("br"),n._v(" Phát tán của các ngươi tư duy cũng quá lợi hại!"),t("br"),t("br"),n._v(" Bất quá, như vậy, khặc khặc... Ta còn thực sự rất yêu thích..."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v(" “Các ngươi nói ta hãm hại hắn, cũng phải có lý do chứ! Ta đường đường phòng giáo dục chủ nhiệm, học viện trưởng lão, hãm hại hắn một cái sơ cấp giáo sư làm gì?” "),t("br"),t("br"),n._v(" Không hổ là thầy chủ nhiệm, Thượng Thần trưởng lão rất nhanh tìm tới vấn đề vị trí."),t("br"),t("br"),n._v(" Làm bất cứ chuyện gì, cũng phải có động cơ, có lý do."),t("br"),n._v("  Thân phận của hắn cao quý, vô duyên vô cớ hãm hại một cái phổ thông giáo sư, thực sự không còn gì để nói a!"),t("br"),t("br"),n._v(" “Chuyện này còn dùng nhiều lời sao? Tôn tử của ngươi Thượng Bân muốn xung kích học viện trẻ tuổi minh tinh giáo sư, mà Trương lão sư trẻ tuổi như vậy thì có loại tu vi này cùng năng lực, nhất định sẽ bộc lộ tài năng, vượt xa cho hắn, vì phòng ngừa chuyện như vậy phát sinh, lúc này mới cố ý chèn ép!” "),t("br"),t("br"),n._v(" Lần này nói chuyện chính là Mạc trưởng lão, càng nói càng tức, râu thổi đến mức cao cao: “Những việc này tuy rằng ta không có chứng cớ xác thật, nhưng ngày hôm nay ta thế nhưng nhìn từ đầu tới đuôi! Học tâm tra hỏi, bên dưới đại tiền đặt cược, đủ loại bức bách... Thượng Thần, ngươi thủ đoạn thật là lợi hại, thiệt thòi chúng ta nhiều như vậy năm giao tình, ta không tường thực sự là mắt bị mù!” "),t("br"),t("br"),n._v(" Mạc trưởng lão luôn luôn ghét cái ác như kẻ thù, ngày hôm nay là nghe lời của đối phương, chuyên môn lại đây quét sạch giáo sư bại hoại, kết quả nhưng nhìn thấy vẫn còn trưởng lão vẫn dùng các loại phương pháp chèn ép Trương Huyền lão sư!"),t("br"),t("br"),n._v(" Mà Trương lão sư vẫn dùng sự thực chứng minh hắn là chính xác."),t("br"),t("br"),n._v(" Này vẫn chưa thể chứng minh vấn đề sao?"),t("br"),t("br"),n._v(" Nếu như này cũng không thể chứng minh, ta không tường chẳng phải mắt mù?"),t("br"),t("br"),n._v(" “Ta cũng có thể chứng minh, ngày hôm nay Thượng Thần trưởng lão cố ý chèn ép Trương lão sư, ta tận mắt nhìn thấy!” "),t("br"),t("br"),n._v(" “Ngay cả chúng ta tại trước mặt đều rõ ràng như thế, sau lưng còn không biết dùng thủ đoạn gì!” "),t("br"),t("br"),n._v(" “Thực sự là biết người biết mặt nhưng không biết lòng, đường đường thầy chủ nhiệm, vì hắn tôn tử, lại có thể làm như vậy, thực sự là cầm thú!” "),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v(" Mạc trưởng lão mà nói... Vừa mở miệng, không ít người nhất thời cùng gió phụ họa, từng cái từng cái căm phẫn sục sôi."),t("br"),t("br"),n._v(" “...” "),t("br"),t("br"),n._v(" Thượng Thần trưởng lão chỉ cảm thấy choáng váng đầu hoa mắt, sắp điên rồi."),t("br"),t("br"),n._v(" Ta... Thật sự không chèn ép qua hắn có được hay không, liền ngày hôm nay hắn gọt đi mặt mũi của ta, ta mới tìm hắn để gây sự..."),t("br"),t("br"),n._v(" Nếu như nói ta trước chỉ biết là hắn trình độ không cao, cũng không quen biết, các ngươi có tin hay không?"),t("br"),t("br"),n._v(" Bất quá, nhìn thấy mọi người phẫn nộ biểu cảm, hắn cũng biết, tiếp tục xoắn xuýt cái đề tài này khẳng định vô dụng."),t("br"),t("br"),n._v(" Hắn bây giờ, chính là hoàng bùn rơi vào đũng quần, không phải cứt cũng là cứt!"),t("br"),t("br"),n._v(" Quay đầu nhìn về phía chuyện này người khởi xướng Trương Huyền, liền thấy hắn vẫn như cũ hai tay chắp ở sau lưng, một mặt trách trời thương người, tựa hồ đem chuyện này bí ẩn nói ra, có chút không đành lòng."),t("br"),t("br"),n._v(" Không đành lòng ngươi muội a!"),t("br"),t("br"),n._v(" Này đều là ngươi hồ nói được lắm không được!"),t("br"),t("br"),n._v(" Chính ngươi không bản lĩnh, giáo viên sát hạch đạt được không điểm, làm sao đột nhiên, liền biến thành là chúng ta phòng giáo dục gian dối, ta cố ý chèn ép?"),t("br"),t("br"),n._v(" Này theo ta cùng phòng giáo dục có một mao tiền quan hệ?"),t("br"),t("br"),n._v(" Phốc!"),t("br"),t("br"),n._v(" Cũng không nhịn được nữa, ngực máu tươi trực tiếp phun ra ngoài."),t("br"),t("br"),n._v(" “Hừ, còn không thấy ngại thổ huyết, làm ra loại này mất mặt xấu hổ sự tình, nếu như ta đã sớm đập đầu chết!” "),t("br"),t("br"),n._v(" “Thực sự là không cần mặt mũi, người như thế làm sao có thể lên làm Hồng Thiên học viện phòng giáo dục chủ nhiệm?” "),t("br"),t("br"),n._v(" “Còn trưởng lão đây, người như thế phẩm đức có vấn đề, ta cảm thấy cũng có thể khai trừ giáo sư tư cách...” "),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v(" Nhìn thấy hắn thổ huyết, mọi người không những không có đồng tình, còn đều tức giận oa oa kêu loạn."),t("br"),t("br"),n._v(" “Tại sao lại như vậy?” "),t("br"),t("br"),n._v(" Một bên Thượng Bân cùng Tào Hùng cũng triệt để mộng bức tại chỗ."),t("br"),t("br"),n._v(" Mới vừa rồi còn trí quấn nắm chắc, có chứa rất lớn phần thắng, làm sao đột nhiên biến thành như vậy?"),t("br"),t("br"),n._v(" Đây là làm cái gì?"),t("br"),t("br"),n._v(" Ói ra ngụm máu tươi, Thượng Thần trưởng lão thoải mái rất nhiều, trong đầu cũng biến thành tỉnh táo, nhìn quanh một vòng, hai tay rung lên, nhìn về phía Trương Huyền, trên mặt không hề che giấu chút nào nồng nặc sự thù hận: “Nói ngươi giáo viên sát hạch được không điểm là ta cố ý chèn ép, ta hiện tại cũng không cách nào giải thích, không có cách nào nói nhiều, cái kia tẩu hỏa nhập ma người học sinh kia đây? Đây chính là tại trong lớp của ngươi phát sinh đi, ngươi giải thích thế nào?” "),t("br"),t("br"),n._v(" Ngươi dù có ngàn vạn loại lý do, ngàn vạn cái mượn cớ, một học sinh tại trong lớp tại chỗ tẩu hỏa nhập ma, đây là không cách nào biện giải sự thực, ta xem ngươi giải thích thế nào!"),t("br"),t("br"),n._v(" Trương Huyền trong lòng “Hồi hộp” một hồi."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-83"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-85"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);