(window.webpackJsonp=window.webpackJsonp||[]).push([[2121],{2476:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-831-truong-huyen-nhin-ta-khong-đanh-chet-nguoi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-831-truong-huyen-nhin-ta-khong-đanh-chet-nguoi"}},[n._v("#")]),n._v(" Chương 831: Trương Huyền nhìn ta không đánh chết ngươi!")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 831: Trương Huyền nhìn ta không đánh chết ngươi!"),t("br"),t("br"),n._v("“Sáu, bảy ngày? Cũng không xa!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Mặc dù Tử Dực Thiên Hùng thú tốc độ cực nhanh, nhưng một cái qua lại sáu, bảy ngày, chỉ sợ liền Hồng Viễn đế quốc biên giới cũng bay không đến, nói như vậy lên, cũng không coi là xa."),t("br"),t("br"),n._v("Lạc Nhược Hi cười cười: “Thế nào, có hứng thú hay không, cùng ta cùng một chỗ?”"),t("br"),t("br"),n._v("“Đương nhiên!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Không nói trước, có thể cùng nàng cùng một chỗ, chỉ nói Khổng sư Phong Thánh đài, chính là bất luận cái gì danh sư đều không thể cự tuyệt."),t("br"),t("br"),n._v("Nếu có thể đi qua nhìn một chút, tuyệt đối sẽ không chối từ."),t("br"),t("br"),n._v("“Lúc nào xuất phát?” Xác định được, Trương Huyền hỏi."),t("br"),t("br"),n._v("“Nếu như thuận tiện, hiện tại liền có thể xuất phát...” Lạc Nhược Hi nói."),t("br"),t("br"),n._v("“Được!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Đoạn Tục thảo không đưa tới, Đoạn Tục đan cũng là không luyện được, Ngụy Như Yên có Thập Diệp hoa cùng địa mạch linh dịch tẩm bổ, tính mạng không vấn đề gì, năm, sáu ngày thời gian mà thôi, không đáng kể chút nào."),t("br"),t("br"),n._v("Bàn giao Tôn Cường đám người, chiếu cố mọi người và sân nhỏ, bản thân thì cùng Lạc Nhược Hi, bước lên Thiên Hùng thú sống lưng, thẳng tắp hướng Phong Thánh đài phương hướng bay đi."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Trương Huyền bên này rời đi Hồng Viễn thành, bên kia Đổng Hân, rốt cuộc tìm được lão sư của mình, trận pháp sư học viện viện trưởng, Trần Thừa Tuần."),t("br"),t("br"),n._v("“Chuyện gì?”"),t("br"),t("br"),n._v("Gặp vị này đắc ý nhất học sinh, một bộ vội vã bộ dáng, không có trước kia ổn trọng cùng lạnh lùng, Trần Thừa Tuần tràn đầy nghi hoặc."),t("br"),t("br"),n._v("“Học sinh có chuyện, mười phần to lớn... Hi vọng lão sư, có thể dẫn ta đi gặp Lục Phong viện trưởng!” Đổng Hân cắn răng, nói."),t("br"),t("br"),n._v("“Gặp Lục Phong? Đến cùng chuyện gì, như thế phiền phức?” Trần Thừa Tuần lông mày nhíu một cái."),t("br"),t("br"),n._v("Đi tìm Lục Phong viện trưởng, sự tình liền lên lên tới toàn bộ danh sư học viện, đến cùng chuyện gì, hưng sư động chúng như vậy?"),t("br"),t("br"),n._v("“Việc quan hệ danh sư học viện an nguy... Còn có Tử Dương thú tiền bối cùng Dị Linh tộc nhân!” Đổng Hân nói."),t("br"),t("br"),n._v("“Tử Dương thú?”"),t("br"),t("br"),n._v("Trần Thừa Tuần đột nhiên đứng dậy, trong mắt tràn đầy không thể tin được: “Ngươi có tin tức của nó?”"),t("br"),t("br"),n._v("“Vâng!” Đổng Hân gật đầu."),t("br"),t("br"),n._v("“Tốt, cái này đích xác là đại sự, ngươi đi theo ta, ta dẫn ngươi đi gặp Lục Phong...”"),t("br"),t("br"),n._v("Không dám tự tiện làm chuyên, Trần Thừa Tuần mang theo bản thân vị này môn sinh đắc ý, nhanh chân hướng luyện đan sư học viện đi tới."),t("br"),t("br"),n._v("Không lâu sau, đi tới Lục Phong nơi ở."),t("br"),t("br"),n._v("Hai đại viện trưởng, ngồi trong phòng, đem chung quanh toàn bộ phong tỏa, lúc này mới nhìn về phía cô bé trước mắt."),t("br"),t("br"),n._v("“Có chuyện gì, cứ việc nói đi!” Lục Phong viện trưởng ánh mắt sáng ngời."),t("br"),t("br"),n._v("Mộc sư đã từng nói, ai tìm tới Tử Dương thú, ai liền là chân chính viện trưởng, hắn hiện tại không cách nào trừng trị vị kia ác nhân Trương Huyền, bởi vì không cái thân phận này, một khi tìm tới... Những người khác, liền lại nói không nên lời nửa câu nhiều lời."),t("br"),t("br"),n._v("Cái gì Mi Trúc, cái gì Triệu Bính Tuất, đều sẽ không dám phản bác."),t("br"),t("br"),n._v("“Là như vậy, ta hôm nay... Thấy được Tử Dương thú tiền bối!” Đổng Hân cắn răng, nói."),t("br"),t("br"),n._v("“Ngươi thấy được? Nó ở đâu? Còn an toàn?”"),t("br"),t("br"),n._v("Lục Phong, Trần Thừa Tuần liếc mắt nhìn nhau, tất cả đều ánh mắt sáng lên, vội vàng nhìn qua."),t("br"),t("br"),n._v("Hôm nay nhìn thấy, đã nói lên tại Hồng Viễn thành, có cơ hội đi cứu."),t("br"),t("br"),n._v("“Nó... Tại Trương Huyền ngoài học viện phủ đệ.” Đổng Hân nói."),t("br"),t("br"),n._v("“Trương Huyền phủ đệ? Tử Dương thú tiền bối làm sao sẽ ở đó?” Lục Phong sững sờ."),t("br"),t("br"),n._v("Cái tên này phủ đệ, là Hoài Vương gia đưa, trước đó cũng đã biết, mười phần xa hoa, Tử Dương thú tiền bối, bảy tám ngày trước mất tích, tại sao lại ở chỗ này xuất hiện?"),t("br"),t("br"),n._v("“Là như vậy, ta... Trong lúc vô tình đi tòa phủ đệ này, phát hiện Tử Dương thú tiền bối, bị vây ở một cái trận pháp bên trong, muốn chạy trốn, nhưng thủy chung trốn không thoát...”"),t("br"),t("br"),n._v("Đổng Hân không có chút nào giấu diếm, sẽ tại Trương Huyền phủ đệ nhìn thấy một màn, toàn bộ nói ra."),t("br"),t("br"),n._v("Còn những khôi lỗi kia, tránh khoảng cách hơi xa, không thấy rõ, còn tưởng rằng là chân chính Dị Linh tộc nhân."),t("br"),t("br"),n._v("“Ngươi nói... Trương Huyền mang theo hai mươi vị Dị Linh tộc nhân, vây công Tử Dương thú tiền bối? Xem ra... Hắn vẫn là những này Dị Linh tộc nhân thủ lĩnh?”"),t("br"),t("br"),n._v("Sầm mặt lại, Lục Phong đột nhiên đứng dậy."),t("br"),t("br"),n._v("Tin tức này là tại quá hoảng sợ."),t("br"),t("br"),n._v("Coi như hắn loại tâm tính này, đều có chút không dám tin tưởng."),t("br"),t("br"),n._v("“Ngươi tin tức này có thể thật sự?” Trần Thừa Tuần cũng nhìn chằm chằm nhìn qua."),t("br"),t("br"),n._v("Khống chế hơn mười vị Dị Linh tộc nhân, đánh Tử Dương thú, phóng thích sát lục khí tức... Không có chỗ nào không nói rõ, vị này Trương Huyền, có thể là một cái nhân tộc gian tế, thậm chí còn có thể là... Dị Linh tộc nhân!"),t("br"),t("br"),n._v("“Học sinh nguyện ý lấy danh sư thân phận thề, nói câu câu là thật, như có nửa câu lời nói dối, cam nguyện hủy bỏ danh sư tư cách...”"),t("br"),t("br"),n._v("Bàn tay giơ lên, Đổng Hân vẻ mặt nghiêm túc."),t("br"),t("br"),n._v("Lấy danh sư danh nghĩa thề, là nặng nhất lời thề, một khi phạm sai lầm, coi như Danh Sư đường không truy cứu, cũng sẽ tín niệm bị hao tổn, trên tâm cảnh lại khó tiến bộ."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Thấy nàng thề, Lục Phong cùng Trần Thừa Tuần liếc mắt nhìn nhau, thần sắc càng ngày càng ngưng trọng."),t("br"),n._v(" Một lát sau, Lục Phong viện trưởng, giống như là nghĩ thông cái gì, bừng tỉnh đại ngộ: “Khó trách, cái tên này chừng hai mươi tuổi, liền có thể tinh thông nhiều như vậy phụ tu chức nghiệp, thậm chí so với chúng ta những viện trưởng này đều lợi hại... Thì ra là thế, thì ra là thế!”"),t("br"),t("br"),n._v("Dường như thấy hai người không hiểu, nắm đấm xiết chặt, nói tiếp: “Dị Linh tộc nhân tuổi thọ so với nhân loại dài hơn nhiều, cái tên này thoạt nhìn hai mươi tuổi, chỉ sợ là cái sống không biết bao nhiêu năm lão quái vật! Nếu không, làm sao có thể đem luyện khí, luyện đan, y đạo, kinh hồng các loại chức nghiệp, học được như thế trình độ?”"),t("br"),t("br"),n._v("“Cái này...” Trần Thừa Tuần chần chờ một chút, nhịn không được nhẹ gật đầu: “Không sai! Vừa đến liền làm toàn bộ học viện đại loạn, mê tất cả học sinh thần hồn điên đảo, trước đó ta đã cảm thấy không bình thường, không nghĩ tới tên này, có loại thân phận này!”"),t("br"),t("br"),n._v("Vị này Trương Huyền, quá nhiều để cho người ta không thể tưởng tượng nổi, coi như lĩnh ngộ Minh Lý Chi Nhãn cũng giải thích không thông, nếu như là Dị Linh tộc nhân, cũng rất dễ dàng giải thích."),t("br"),t("br"),n._v("“Dị Linh tộc, làm hại nhân loại, với tư cách danh sư, gặp được nhất định phải chém giết...”"),t("br"),t("br"),n._v("Mắt sáng lên, Lục Phong viện trưởng răng cắn “Khanh khách!” Rung động."),t("br"),t("br"),n._v("Dị Linh tộc cùng nhân tộc cừu hận, đã sâu tận xương tủy, không cách nào sửa đổi, đã cái tên này là Dị Linh tộc, cái kia cũng chỉ có một kết quả... Phải chết!"),t("br"),t("br"),n._v("“Không sai, chém giết nhân tộc gian tế, chúng ta không thể đổ cho người khác!”"),t("br"),t("br"),n._v("Trần Thừa Tuần lên tiếng, ngay sau đó mày nhăn lại: “Bất quá, đối phương đã lấy được Mi viện trưởng, Triệu viện trưởng đám người tín nhiệm, tìm không thấy chứng cứ, trực tiếp ra tay, ta sợ sẽ gây nên không cần thiết mâu thuẫn!”"),t("br"),t("br"),n._v("“Bọn họ hồ đồ ah!”"),t("br"),t("br"),n._v("Lục Phong viện trưởng lạnh hừ một tiếng, trên người lộ ra sự tự tin mạnh mẽ: “Ta đã sớm nhìn ra cái tên này không phải cái thứ tốt, bọn họ chính là không tin, cái này ta nhìn còn có lời gì nói! Như vậy, chúng ta bây giờ liền đi tìm Mộc sư, đem chuyện này cùng hắn nói tỉ mỉ, sau đó, lại đem thập đại trường lão cùng vị kia Trương Huyền toàn bộ tìm đến, ở trước mặt vạch trần... Dị Linh tộc nhân, người người phải trừ diệt...”"),t("br"),t("br"),n._v("“Không sai, có Mộc sư chủ trì, ở trước mặt vạch trần, liền coi như bọn họ tại muốn nói cái gì, cũng cũng không nói ra được!”"),t("br"),t("br"),n._v("Trần Thừa Tuần gật đầu."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Nhẹ gật đầu, Lục Phong hài lòng xoay đầu lại, nhìn về phía cách đó không xa nữ hài: “Ngươi gọi Đổng Hân đúng không, làm được rất không tệ, một lát nữa gặp Mộc sư, còn muốn đem nhìn thấy sự tình lặp lại lần nữa... Ta biết ngươi cũng có luyện đan phụ tu, thiên phú mặc dù không bằng trận pháp, nhưng cũng xem là tốt. Chỉ cần chuyện này làm tốt, ta có thể thu làm thân truyền, đơn độc truyền thụ cho ngươi luyện đan!”"),t("br"),t("br"),n._v("“Học sinh gặp qua lão sư!” Ánh mắt sáng lên, Đổng Hân vội vàng quỳ gối."),t("br"),t("br"),n._v("Một khi đối phương trở thành viện trưởng, nàng chính là viện Nagato sinh, lại thêm, thân là hai đại viện trưởng thân truyền, đến lúc đó Đổng Hân biết, trở thành học viện đệ nhất học được, ở trong tầm tay!"),t("br"),t("br"),n._v("Ha ha!"),t("br"),t("br"),n._v("Hồ Yêu Yêu, Long Thương Nguyệt, Tiết Chân Dương, các ngươi lấy cái gì cùng ta đấu?"),t("br"),t("br"),n._v("Còn Trương Huyền... Thân là Dị Linh tộc nhân, thân phận bị vạch trần, tương đương với hoàn toàn xong, càng thêm không đáng giá nhắc tới."),t("br"),t("br"),n._v("“Ừm, ta hiện tại liền đi tìm Mộc sư...”"),t("br"),t("br"),n._v("Nhẹ gật đầu, Lục Phong viện trưởng, đang muốn tiến lên, đột nhiên ngừng lại: “Trước không vội! Vị này Trương Huyền cùng Lạc sư quan hệ không tệ, một khi hiện tại đi tìm Mộc sư, tin tức truyền ra, ta sợ cái tên này sẽ sớm chuẩn bị. Một khi chạy trốn, còn muốn cầm liền khó khăn!”"),t("br"),t("br"),n._v("Có thể che đậy khí tức, ngụy trang danh sư, nói rõ đối phương không phải nhân tộc gian tế, chính là Dị Linh tộc bên trong Vương Giả."),t("br"),t("br"),n._v("Mạnh như thế người, sớm biết tin tức trốn đi, đừng nói là hắn, liền xem như phong hào đế quốc Danh Sư đường lên đường, đều chưa hẳn có thể bắt đến."),t("br"),t("br"),n._v("Nhất định phải chú ý cẩn thận mới được."),t("br"),t("br"),n._v("“Đúng vậy a!” Trần Thừa Tuần cũng nhẹ gật đầu."),t("br"),t("br"),n._v("Mặc dù khả năng này không lớn, nhưng cũng phải phòng ngừa rắc rối có thể xuất hiện."),t("br"),t("br"),n._v("“Trần viện trưởng, ngươi trước dò xét vị kia Trương Huyền ở đâu, phái người theo dõi, đừng để hắn chạy trốn... Đợi có hoàn toàn chắc chắn về sau, lại bẩm báo Mộc sư, để hắn phán quyết!”"),t("br"),t("br"),n._v("Suy nghĩ một chút, Lục Phong viện trưởng nói."),t("br"),t("br"),n._v("Mặc dù hận trong lòng không được đem cái tên này, lập tức bắt lấy chém giết, nhưng thân là lục tinh thượng phẩm danh sư, vẫn có thể bảo trì bình thản."),t("br"),t("br"),n._v("“Được!” Trần viện trưởng lên tiếng, xoay người đi ra ngoài, không lâu sau, lần nữa đi trở về, đã phái hai vị trưởng lão, tiến đến theo dõi vị kia Trương Huyền."),t("br"),t("br"),n._v("Sau gần nửa canh giờ, bên trong một cái bóng người đi tới hồi bẩm: “Trần viện trưởng, Lục viện trưởng, Trương Huyền... Vắng mặt phủ đệ, cũng vắng mặt Hồng Viễn thành, buổi trưa, liền cùng Lạc sư ngồi linh thú phi hành, rời đi...”"),t("br"),t("br"),n._v("“Rời đi?” Ba người ngẩn ngơ."),t("br"),t("br"),n._v("“Vâng, một canh giờ đi về trước, cụ thể đi nơi nào, không có nói tỉ mỉ, cũng không có bàn giao, không thể nào dò xét...”"),t("br"),t("br"),n._v("Bóng người nói."),t("br"),t("br"),n._v("Bọn họ đi Trương Huyền phủ đệ, bằng vào thực lực, hơi dò xét, liền có thể xác định Trương Huyền vắng mặt, sau đó hỏi thăm ra hai người rời đi, cũng không khó."),t("br"),t("br"),n._v("Nhưng mà, đi nơi nào, liền Tôn Cường cũng không biết, chớ nói chi là bọn họ."),t("br"),t("br"),n._v("“Một canh giờ trước đi ra?”"),t("br"),t("br"),n._v("Vẻ mặt khó coi."),t("br"),t("br"),n._v("Linh thú phi hành một canh giờ, ít nhất mấy ngàn cây số đi xuống, rốt cuộc không đuổi kịp."),t("br"),t("br"),n._v("“Hắn những học sinh kia còn tại?” Quay đầu nhìn qua."),t("br"),t("br"),n._v("“Đều tại!” Bóng người gật đầu: “Hơn nữa căn cứ tin tức, hắn là bị Lạc sư mời, mới ra cửa.”"),t("br"),t("br"),n._v("“Vậy là tốt rồi, đoán chừng đi làm cái gì chuyện, chỉ cần những học sinh này vẫn còn, tin tức liền không rò rỉ, các ngươi lưu tâm quan sát, một khi trở về, lập tức bẩm báo!”"),t("br"),t("br"),n._v("Hất lên ống tay áo, Lục Phong hai mắt nheo lại, mang theo đại viện trưởng khí thế cùng quyết đoán."),t("br"),t("br"),n._v("“Vâng!” Bóng người gật gật đầu, xoay người rời đi."),t("br"),t("br"),n._v("“Người vắng mặt, chúng ta làm sao bây giờ?” Trần Thừa Tuần nhịn không được hỏi."),t("br"),t("br"),n._v("“Làm sao bây giờ, chờ lấy! Một khi trở về, liền tổ chức mười đại viện trưởng hội nghị, ngay trước Mộc sư trước mặt, vạch trần bộ mặt thật của hắn!” Ánh mắt phát lạnh, Lục Phong cười lạnh một tiếng, trong mắt sát cơ sôi trào."),t("br"),t("br"),n._v("Để ngươi phách lối, để ngươi không nể mặt ta, để ngươi làm cho luyện đan sư học viện, sắp duy trì không đi xuống..."),t("br"),t("br"),n._v("Trương Huyền, ngày lành của ngươi đến rồi đầu!"),t("br"),t("br"),n._v("Nhìn ta Lục Phong, không đánh chết ngươi!"),t("br"),t("br"),n._v("(Công chúng số phát Phong Thánh đài hình ảnh, mọi người có thể nhìn xem, mặt khác, xâu cổ vì cái gì đối Lạc Nhược Hi ưa thích không rời (*)? Phía trước đã có làm nền, mọi người có thể đi công chúng số nhắn lại, suy đoán một chút ah. Wechat lục soát “hstysds” hoặc là “Hoành Tảo Thiên Nhai” tăng thêm chú ý là đủ.)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-839"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-841"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);