(window.webpackJsonp=window.webpackJsonp||[]).push([[2122],{2477:function(n,h,c){"use strict";c.r(h);var g=c(44),t=Object(g.a)({},(function(){var n=this,h=n.$createElement,c=n._self._c||h;return c("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[c("h1",{attrs:{id:"chuong-832-phong-thanh-đai"}},[c("a",{staticClass:"header-anchor",attrs:{href:"#chuong-832-phong-thanh-đai"}},[n._v("#")]),n._v(" Chương 832: Phong Thánh đài")]),n._v(" "),c("hr"),n._v(" "),c("p",[c("br"),c("br"),n._v("Chương 832: Phong Thánh đài"),c("br"),c("br"),n._v("Bên này thương nghị vạch trần thân phận của hắn, Thiên Hùng thú trên lưng, Trương Huyền cùng Lạc Nhược Hi đứng tại phía trước cửa sổ, nhìn trước mắt bay lượn mà qua mây trắng, thần tình lạnh nhạt."),c("br"),c("br"),n._v("“Phong Thánh đài, là Khổng sư phong Thánh chỗ, không chỉ tại trên vách núi đá lưu lại tự tay viết chữ viết... Nghe nói còn để lại một đạo ý niệm, tặng cho người hữu duyên, các ngươi đều là thiên nhận danh sư, có lẽ có thể có cơ hội lấy được!”"),c("br"),c("br"),n._v("Lạc Nhược Hi cười cười, nói."),c("br"),c("br"),n._v("“Một đạo ý niệm?” Trương Huyền ánh mắt sáng lên."),c("br"),c("br"),n._v("Khổng sư địa vị có thể tưởng tượng được, danh sư thuỷ tổ, một đạo ý niệm trân quý mức độ, đủ có thể khiến vô số danh sư, vì đó đổ xô tới."),c("br"),c("br"),n._v("“Đúng vậy a, mặc dù chỉ là mới vừa đột phá Thánh vực lưu lại, không tính là quá mạnh, nhưng cũng là Khổng sư... Không thể khinh thường, nếu như ngươi có thể được đến, đem hắn dung hợp, đột phá Thánh vực, ở trong tầm tay!”"),c("br"),c("br"),n._v("Lạc Nhược Hi gật đầu."),c("br"),c("br"),n._v("“Đột phá Thánh vực?” Trương Huyền cười khổ: “Ta hiện tại mới Quy Nhất cảnh đỉnh phong mà thôi, khoảng cách Thánh vực, còn kém xa lắm!”"),c("br"),c("br"),n._v("Mặc dù hắn cũng muốn đột phá, nhưng khoảng cách Thánh vực còn có rất lớn một khoảng cách."),c("br"),c("br"),n._v("Quy Nhất cảnh đằng sau còn có Đạp Hư cảnh, Tằm Phong cảnh, thậm chí còn có Bán Thánh, Tòng Thánh!"),c("br"),c("br"),n._v("Bốn Đại cảnh giới, coi như hắn có đầy đủ bí tịch, có thể ngưng tụ đầy đủ Thiên Đạo công pháp, sưu tập địa mạch linh dịch, cũng chưa chắc đầy đủ."),c("br"),c("br"),n._v("Thiên Đạo công pháp càng hướng (về) sau đối với linh khí yêu cầu càng cao, đối linh thạch, linh dịch tiêu hao, so bình thường Thánh vực cao đâu chỉ gấp trăm lần, nghìn lần!"),c("br"),c("br"),n._v("Lại thêm, Tàm Phong cùng Bán Thánh là một cái ngưỡng cửa, Bán Thánh cùng Tòng Thánh, cũng là một cái ngưỡng cửa."),c("br"),c("br"),n._v("Hai đại môn hạm, không biết khốn chết bao nhiêu người, hắn cho dù có Thiên Đạo công pháp, cũng không dám nói, trăm phần trăm liền có thể thành công."),c("br"),c("br"),n._v("“Lần trước tại Thanh Tắc sơn thấy ngươi thời điểm, mới Trọc Thanh cảnh đỉnh phong đi, hai tháng không đến, liền đạt đến Quy Nhất cảnh đỉnh phong, cứ thế mà tăng lên ba cái đại cảnh giới, loại này tốc độ tiến bộ, ngay cả ta, đều rất ít gặp đến! Thánh vực mặc dù mạnh mẽ, đối với ngươi mà nói, không khó lắm.”"),c("br"),c("br"),n._v("Lạc Nhược Hi nói."),c("br"),c("br"),n._v("Lần thứ nhất lúc gặp mặt, đối phương mới Trọc Thanh cảnh đỉnh phong, lần thứ hai nhìn thấy thời điểm, liền đi đến Kiều Thiên cảnh đỉnh phong... Vốn cho rằng cái này đã rất nhanh, không nghĩ tới hôm qua nhìn thấy, đã là Quy Nhất cảnh đỉnh phong."),c("br"),c("br"),n._v("Như thế tốc độ tiến bộ, quả thực nghe rợn cả người."),c("br"),c("br"),n._v("“Ta... Cũng là vận khí tốt mà thôi...”"),c("br"),c("br"),n._v("Xấu hổ cười một tiếng, Trương Huyền không trong vấn đề này tiếp tục dây dưa, nhớ tới cái gì, nhịn không được hỏi: “Đúng rồi, ngươi có Đạp Hư cảnh công pháp bí tịch không? Cấp bậc không yêu cầu, càng nhiều càng tốt!”"),c("br"),c("br"),n._v("Danh sư học viện công pháp bí tịch, quản lý muốn so bình thường thư tịch nghiêm ngặt, trận pháp bảo vệ, Vu hồn đều khó mà đi vào, lại thêm trông coi cơ bản đều là lão sư, có trước đó Kinh Hồng viện kinh nghiệm, liền xem như hắn, đều không dám tùy tiện tiến lên."),c("br"),c("br"),n._v("Một cái Vệ Nhiễm Tuyết thiếu chút nữa để hắn treo, vạn nhất toát ra mấy cái am hiểu linh hồn, một phen công kích, đoán chừng trốn đều trốn không thoát."),c("br"),c("br"),n._v("Do đó, những ngày gần đây, khắp nơi học những nghề nghiệp khác, cũng không đi tìm công pháp."),c("br"),c("br"),n._v("Không phải tìm không thấy, mà là không dám."),c("br"),c("br"),n._v("Giờ phút này, đúng lúc chạy đi, không có việc gì, tuân hỏi một chút, nếu như đối phương nếu như mà có, vừa vặn học một chút, thuận tiện tăng thực lực lên."),c("br"),c("br"),n._v("“Đạp Hư cảnh công pháp? Ta có, chẳng qua chỉ có một bản, mà lại là gia tộc công pháp, không cho phép truyền ra ngoài...” Lạc Nhược Hi lắc đầu."),c("br"),c("br"),n._v("“Là ta lỗ mãng!” Nghe Trương Huyền có chút xấu hổ."),c("br"),c("br"),n._v("Căn cứ biết đến tin tức, trước mắt vị này vô cùng có khả năng đến từ Thánh Nhân quý tộc Lạc gia, gia tộc công pháp, bảo hộ rất nghiêm, tùy tiện cầu lấy, thuộc về đại bất kính."),c("br"),c("br"),n._v("Thật giống như trước đó Mộc sư, bởi vì tiết lộ công pháp, từ đó bị trục xuất gia tộc."),c("br"),c("br"),n._v("“Không có gì lỗ mãng, ngươi muốn xung kích Đạp Hư cảnh lời nói, ta ngược lại thật ra có thể cho ngươi chỉ điểm...”"),c("br"),c("br"),n._v("Lạc Nhược Hi cười cười: “Đạp hư, là Hóa Phàm bát trọng cảnh giới, toàn thân cao thấp cùng tự nhiên càng thêm phù hợp, có thể làm đến đạp hư mà đi...”"),c("br"),c("br"),n._v("Trương Huyền cẩn thận lắng nghe, con mắt nhịn không được tỏa ánh sáng."),c("br"),c("br"),n._v("Không hổ là Thánh Nhân quý tộc đi ra cường giả, đối Đạp Hư cảnh giảng giải, cẩn thận nhập vi, hầu như không có bất kỳ cái gì sai lầm!"),c("br"),c("br"),n._v("Dựa theo phương pháp của nàng tu luyện, coi như không có công pháp, cũng có thể đột phá cảnh giới, càng đi càng xa."),c("br"),c("br"),n._v("Bất quá, Trương Huyền cũng không lo lắng."),c("br"),c("br"),n._v("Hắn đột phá Quy Nhất cảnh cũng là hơn hai ngày điểm thời gian, tùy tiện xung kích lời nói, rất dễ dàng để tu vi trở nên phù phiếm."),c("br"),c("br"),n._v("Liền xem như Thiên Đạo công pháp, cũng cần đem cơ sở buộc nhà tù, nếu không, tiến bộ càng nhanh, càng giống như lâu đài trên không, trong kính Hoa Nguyệt, dễ dàng tiêu tán."),c("br"),c("br"),n._v("Trò chuyện càng nhiều, Trương Huyền được lợi càng nhiều."),c("br"),c("br"),n._v("Một phen trò chuyện, để hắn đối tu luyện về sau, có khắc sâu hơn lý giải."),c("br"),c("br"),n._v("Đi Phong Thánh đài, cần tiếp cận ba ngày thời gian, trong khoảng thời gian này bận quá, chưa kịp tu luyện, thông qua nói chuyện phiếm, để hắn có lĩnh ngộ mới, đúng lúc thừa cơ củng cố tu vi."),c("br"),c("br"),n._v("Địa mạch linh dịch dọc theo kinh mạch hướng trong cơ thể chảy xuôi, Thiên Đạo chân khí, đi qua thiên chuy bách luyện, càng thêm tinh thuần cùng cứng cỏi."),c("br"),c("br"),n._v("Thời gian thoáng một cái đã qua, đi qua ba ngày rèn luyện, Quy Nhất cảnh đỉnh phong thực lực, đã hoàn toàn củng cố, khí tức cả người cũng so trước đó sắc bén rất nhiều."),c("br"),c("br"),n._v("Mặc dù lực lượng không có đột phá, vẫn như cũ là 14 triệu đỉnh, nhưng đối chân khí vận dụng càng thêm linh hoạt, chiến đấu cũng tăng cường không ít."),c("br"),c("br"),n._v("Nhất là khí hải, so trước đó rộng lớn tiếp cận gấp đôi, tinh thuần chân khí xao động trong đó, tựa như tụ tập hồng thủy, bất cứ lúc nào cũng sẽ bộc phát ra lực lượng kinh người."),c("br"),c("br"),n._v("Ba ngày khổ tu, đối Quy Nhất cảnh có lĩnh ngộ sâu hơn, hắn giờ phút này, chỉ cần cơ hội cho phép, tùy thời đều có thể xông phá cửa ải, một lần hành động đi đến Hóa Phàm bát trọng Đạp Hư cảnh!"),c("br"),c("br"),n._v("“Nhìn xem quyển kia kim sắc thư tịch!”"),c("br"),c("br"),n._v("Đáp ứng Tưởng viện trưởng, thu hắn làm đồ, trong đầu liền thêm ra một cái kim sắc thư tịch, một mực không thời gian nhìn kỹ, lúc này tu luyện hoàn tất, đúng lúc nghiên cứu một chút."),c("br"),n._v("Quyển sách này, cùng trước đó không quá tương đồng, không có sáng như vậy, thoạt nhìn có chút tối bụi, mặc dù độ sáng ít đi, nhưng cho người ta một loại càng thêm vô cùng mênh mông cảm giác."),c("br"),c("br"),n._v("“Xem ra Thiên Đạo thư viện tấn cấp, Thiên Đạo chi sách cũng tấn cấp...”"),c("br"),c("br"),n._v("Âm thầm gật đầu."),c("br"),c("br"),n._v("Trước đó kim sắc thư tịch, có thể để cho hắn trong nháy mắt trí nhớ vô số tri thức; Có thể biến thành cục gạch, đem người chụp chết, thậm chí Ngoan Nhân loại này cường giả đều khó mà đào thoát; Có thể tăng lên tâm cảnh khắc độ, trực tiếp gia tăng một cái đại cấp bậc..."),c("br"),c("br"),n._v("Không biết... Sau khi tấn cấp, trở nên thoáng u ám thư tịch, sẽ có cái gì không năng lực giống nhau."),c("br"),c("br"),n._v("Đang muốn nghiên cứu một chút, liền nghe đến một cái thanh âm thanh thúy vang lên: “Phong Thánh đài đến!”"),c("br"),c("br"),n._v("“Đến?”"),c("br"),c("br"),n._v("Không để ý tới tiếp tục nghiên cứu, mở to mắt, chỉ thấy Lạc Nhược Hi đứng tại bên cửa sổ bên trên, ánh mắt nhìn ở bên ngoài, an tĩnh như là một bộ bức tranh tuyệt mỹ."),c("br"),c("br"),n._v("Đi tới gần, nhìn về phía trước, một tòa núi cao nguy nga xuất hiện ở trước mắt, đứng sừng sững ở bóng loáng bình nguyên bên trên, như cùng một cái cự long, trực trùng vân tiêu."),c("br"),c("br"),n._v("Đỉnh núi dường như bị người nào san bằng, từ xa nhìn lại hình như một cái cự đại tế đàn, một cái trắng tinh Bàn Sơn đường, thẳng tắp hướng lên xoay quanh, nối thẳng đỉnh núi."),c("br"),c("br"),n._v("“Đây chính là Phong Thánh đài?”"),c("br"),c("br"),n._v("Trương Huyền líu lưỡi."),c("br"),c("br"),n._v("Cái này tòa núi cao, quỷ phủ thần công, giống như liền Tiếp Thiên đài cao, không phải tận mắt nhìn thấy, khó mà tin được."),c("br"),c("br"),n._v("“Nghe nói Khổng sư lúc ấy ở đây ngồi ngay ngắn ba ngày, một lần hành động đột phá, thiên hàng Thánh âm, vạn chuông cùng vang lên.”"),c("br"),c("br"),n._v("Tựa hồ đối với Khổng sư phong Thánh lúc tình cảnh biết đến khá nhiều, Lạc Nhược Hi mỉm cười nói: “Ngọn núi này, trước kia là dạng gì, không người biết được, có điều, Khổng sư lần nữa phong Thánh về sau, liền biến thành như vậy, như là một tòa cao cao tế đàn, câu thông đại địa cùng trời xanh! Ở trên đây tu luyện, tâm cảnh mở rộng, lại càng dễ đột phá, do đó, hàng năm tới đây tìm kiếm đột phá người tu luyện, nhiều vô số kể.”"),c("br"),c("br"),n._v("Trương Huyền gật đầu."),c("br"),c("br"),n._v("Minh Lý Chi Nhãn chiếu rọi xuống, nơi xa trắng tinh Bàn Sơn dọc đường, bóng người xoay quanh, quả nhiên cùng đối phương nói đồng dạng, có không ít người tới cầu đạo."),c("br"),c("br"),n._v("Còn có thể thành công hay không, vậy liền nhìn cơ duyên."),c("br"),c("br"),n._v("“Bởi vì là Khổng sư phong Thánh địa phương, Danh Sư đường đem nơi này bố trí là Thánh địa, muốn qua, chỉ có thể theo chân núi từng bước một đi lên, không có thể phi hành, cường giả Thánh vực cũng không ngoại lệ... Chúng ta vẫn là đi chân núi đi!”"),c("br"),c("br"),n._v("Lạc Nhược Hi nói."),c("br"),c("br"),n._v("Khổng sư phong Thánh chỗ, trang nghiêm uy nghiêm, không có khả năng để cho người ta bay thẳng đi lên, muốn lên núi, chỉ có một cái... Dọc theo đường vòng quanh núi đi bộ tiến lên!"),c("br"),c("br"),n._v("Đây cũng là đối Khổng sư cùng danh sư nghề nghiệp tôn trọng."),c("br"),c("br"),n._v("Biết quy củ, Trương Huyền cũng không vượt qua, để Tử Dực Thiên Hùng thú, hướng chân núi bay đi."),c("br"),c("br"),n._v("Còn chưa tới đến gần, liền thấy bóng người như thoi đưa, vô số người mặc danh sư trường bào người, dọc theo đường núi, loạng choạng hướng về phía trước."),c("br"),c("br"),n._v("Những người này, cơ bản đều là Tằm Phong cảnh cường giả, còn có chút là Bán Thánh, Tòng Thánh, có là danh sư, cũng có là những nghề nghiệp khác người tu luyện, xem ra đều là chuyên tới, tìm kiếm cơ duyên."),c("br"),c("br"),n._v("“Những thứ này... Đều là Hồng Viễn đế quốc cường giả?” Trương Huyền nghi hoặc."),c("br"),c("br"),n._v("Đánh mắt nhìn đi, toàn bộ đường núi chừng trên vạn người, hầu như tất cả đều là Tằm Phong cảnh, Bán Thánh, Tòng Thánh... Hồng Viễn đế quốc chỗ nào toát ra nhiều cường giả như vậy?"),c("br"),c("br"),n._v("“Tự nhiên không phải, Phong Thánh đài đứng sừng sững ở Hồng Viễn đế quốc biên giới, chung quanh kết nối lấy mười cái nhất đẳng đế quốc, thậm chí, còn có phong hào đế quốc người cũng sẽ đến đây, những người này, có thể có một phần trăm là Hồng Viễn, là tốt lắm rồi!”"),c("br"),c("br"),n._v("Lạc Nhược Hi nói."),c("br"),c("br"),n._v("“Ah!” Trương Huyền lên tiếng."),c("br"),c("br"),n._v("Những ngày này thông qua đọc sách, hắn cũng gần như biết xung quanh tình huống."),c("br"),c("br"),n._v("Hồng Viễn đế quốc là cái nhất đẳng đế quốc, phía trên là Thanh Nguyên phong hào đế quốc."),c("br"),c("br"),n._v("Thanh Nguyên đế quốc phía dưới, dạng này nhất đẳng quốc gia, chân có vài chục cái nhiều, diện tích lãnh thổ bao la, địa giới hàm cái mấy chục triệu cây số, nhân khẩu càng là vượt qua vạn ức."),c("br"),c("br"),n._v("Hồng Viễn đế quốc, mặc dù có danh sư học viện, văn danh thiên hạ, nhưng muốn nói diện tích, không tính là nhất đẳng trong đế quốc lớn nhất, chỉ có thể coi là được trung du."),c("br"),c("br"),n._v("Nguyên nhân chính là như thế, cường giả cũng không phải nhiều nhất."),c("br"),c("br"),n._v("Hồng Viễn thành người mạnh nhất, chẳng qua Thánh vực nhất trọng đỉnh phong, mà một chút cường đại nhất đẳng đế quốc, lại có Thánh vực nhị trọng, thậm chí tam trọng cường giả tọa trấn."),c("br"),c("br"),n._v("Cường giả khác biệt, cùng làm nhất đẳng đế quốc, cũng là có chênh lệch rất lớn."),c("br"),c("br"),n._v("“Đúng rồi, danh sư học viện chỉ có Hồng Viễn đế quốc có, vẫn là cái khác nhất đẳng đế quốc cũng có?”"),c("br"),c("br"),n._v("Trương Huyền hỏi tiếp."),c("br"),c("br"),n._v("“Danh sư học viện, là chuyên môn bồi dưỡng danh sư địa phương, Hồng Viễn đế quốc mặc dù không phải độc nhất, nhất đẳng đế quốc phạm vi bên trong, nhưng cũng không nhiều. Thanh Nguyên phong hào đế quốc phía dưới, mười mấy cái nhất đẳng đế quốc, chỉ có bốn chỗ dạng này học viện.”"),c("br"),c("br"),n._v("Lạc Nhược Hi nói: “Bất quá, cách nhau cực xa, không ít người cũng không biết được!”"),c("br"),c("br"),n._v("“Bốn chỗ?” Trương Huyền líu lưỡi."),c("br"),c("br"),n._v("Trước đó vẫn cho là, nhất đẳng đế quốc phạm vi, liền một tòa Hồng Viễn danh sư học viện, không nghĩ tới còn có tới tương đồng ba chỗ."),c("br"),c("br"),n._v("Một cái Hồng Viễn học viện, liền có mười vạn danh sư, học viện khác chắc hẳn cũng không kém."),c("br"),c("br"),n._v("Nhìn như vậy đến, danh sư đại lục, còn thật là lớn, chí ít so kiếp trước trái đất, lớn mấy ngàn lần, mấy vạn lần không thôi."),c("br"),c("br"),n._v("Đáng sợ!"),c("br"),c("br"),n._v("Convert by: ThấtDạ"),c("br"),c("br"),c("br"),c("br")]),n._v(" "),c("hr"),n._v(" "),c("p",[c("a",{attrs:{href:"./chap-840"}},[n._v("Chương trước")]),n._v("  |  "),c("a",{attrs:{href:"./chap-842"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=t.exports}}]);