(window.webpackJsonp=window.webpackJsonp||[]).push([[2125],{2480:function(n,h,i){"use strict";i.r(h);var g=i(44),c=Object(g.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-835-hung-ho-doa-nguoi"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-835-hung-ho-doa-nguoi"}},[n._v("#")]),n._v(" Chương 835: Hùng hổ dọa người")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("Chương 835: Hùng hổ dọa người"),i("br"),i("br"),n._v("Ba người này, từng cái râu tóc bạc trắng, một bộ danh sư trường bào, chiết xạ ánh sáng, còn không rơi xuống, liền xao động ra khí tức kinh người."),i("br"),i("br"),n._v("“Thất tinh danh sư...”"),i("br"),i("br"),n._v("Nhìn thấy đối phương trước ngực lấp lánh huy chương, Trương Huyền lông mày giương lên."),i("br"),i("br"),n._v("Mấy cái này rõ ràng đều là thất tinh cấp bậc danh sư, mặc dù nhìn không ra cụ thể đẳng cấp, nhưng rõ ràng vượt qua Hồng Viễn đế quốc phạm vi."),i("br"),i("br"),n._v("“Làm phiền chư vị đợi lâu...”"),i("br"),i("br"),n._v("Ba người hạ xuống, một người trong đó ông lão, mỉm cười, ngắm nhìn bốn phía."),i("br"),i("br"),n._v("Thanh âm không lớn, nhưng tựa như tại vang lên bên tai, để trước đó thanh âm huyên náo lập tức ngừng lại."),i("br"),i("br"),n._v("“Tiền bối chuyện này!”"),i("br"),i("br"),n._v("“Có thể để cho tiền bối chỉ điểm, là vinh hạnh của chúng ta!”"),i("br"),i("br"),n._v("Phía dưới mọi người đồng loạt ôm quyền."),i("br"),i("br"),n._v("Vách đá trước lĩnh hội, cơ bản đều là Bán Thánh, Tòng Thánh cấp bậc, liền xem như danh sư, cũng chỉ lục tinh hạ phẩm, trung phẩm, cùng phía trước cái này ba cái, vẫn là kém rất lớn một đoạn, không biết cụ thể họ tên dưới tình huống, xưng Hô tiền bối, thuộc về hậu bối cấp bậc lễ nghĩa."),i("br"),i("br"),n._v("“Tất cả mọi người là trong tu luyện người, không cần thiết khách khí, tại hạ trước tự giới thiệu mình một chút, ta gọi ngũ nhưng, những năm này, chịu trách nhiệm phong Thánh học được rất nhiều công việc, là Thanh Trúc đế quốc một vị danh sư, mọi người có thể xưng hô ta là Ngũ sư!”"),i("br"),i("br"),n._v("Ông lão vuốt râu, tự giới thiệu."),i("br"),i("br"),n._v("“Ngũ sư? Chẳng lẽ là... Phong Thánh học hội đương đại hội trưởng...”"),i("br"),i("br"),n._v("“Ngươi nghe nói qua?”"),i("br"),i("br"),n._v("“Cái kia là tự nhiên, vị này Ngũ sư, không chỉ đối Khổng sư bản này phong Thánh tâm đắc có rất cao lĩnh ngộ, càng là Thanh Trúc học viện viện trưởng!”"),i("br"),i("br"),n._v("“Thanh Trúc học viện? Ngươi nói chính là... Thanh Trúc nhất đẳng đế quốc cái kia danh sư học viện?”"),i("br"),i("br"),n._v("“Đúng vậy a, Thanh Nguyên phong hào đế quốc cấp dưới bốn mươi sáu cái nhất đẳng đế quốc, tổng cộng liền bốn chỗ học viện... Thanh Trúc mặc dù bài danh không phải cao nhất, nhưng cũng không phải Hồng Viễn danh sư học viện có thể so sánh được, làm một viện trưởng, học vấn cùng thực lực, không thể nghi ngờ!”"),i("br"),i("br"),n._v("“Nói như vậy, có thể nghe lớp của hắn, chúng ta kiếm lợi lớn...”"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Nghe được tự giới thiệu, phía dưới lập tức nghị luận ầm ĩ, đều lộ ra chấn kinh chi sắc."),i("br"),i("br"),n._v("Phong Thánh học hội mỗi ngày đều sẽ đến giảng bài, có điều, bình thường thời điểm, đều là chút bình thường hội viên. Hôm nay đường đường hội trưởng, thất tinh cấp bậc một vị viện trưởng đích thân tới, để không ít người đều trở nên khiếp sợ."),i("br"),i("br"),n._v("Thanh Trúc học viện, tại bốn đại học viện bên trong, mặc dù không phải xếp hàng thứ nhất, nhưng cũng không phải đếm ngược, làm một viện trưởng, thực lực cùng địa vị, có thể tưởng tượng được."),i("br"),i("br"),n._v("Tự mình cho bọn hắn giảng giải Khổng sư lưu lại tâm đắc, quả thực vinh hạnh cực kỳ."),i("br"),i("br"),n._v("“Bên cạnh ta vị này, là Vương Liêu Vương sư cùng Tôn Kình tôn sư, các vị có cái gì không hiểu, có thể hướng bọn họ kể ra, ta sẽ căn cứ khó dễ mức độ tiến hành giải đáp!” Ngũ sư cười cười."),i("br"),i("br"),n._v("“Vương Liêu? Ta nghe nói qua, một phần  truyền vang mấy chục cái nhất đẳng đế quốc, nghe nói thiên văn chương này, để ít nhất hơn ba trăm người có cảm giác ngộ, từ đó đột phá ràng buộc, là Thanh Trúc đế quốc, thập đại danh sư một trong!”"),i("br"),i("br"),n._v("“Vị này Tôn Kình ta cũng nghe qua, cũng là Thanh Trúc đế quốc thập đại danh sư một trong, một mình sáng tạo , gây nên muôn người đều đổ xô ra đường, ta từng tốn giá cao mua qua một bản, không nghĩ tới có thể ở đây nhìn thấy chân nhân...”"),i("br"),i("br"),n._v("Nghe được cái này Ngũ sư giới thiệu hai cái tên, phía dưới lần nữa xôn xao."),i("br"),i("br"),n._v("Hai cái này, cũng không phải hạng người vô danh, trái lại còn tiếng tăm lừng lẫy."),i("br"),i("br"),n._v("Nhưng mà... Thanh Trúc đế quốc, cách nơi này, ngồi Thánh thú cũng phải nửa tháng lâu, làm sao đột nhiên tới nơi này?"),i("br"),i("br"),n._v("“Đã tất cả mọi người nhận biết, ta nói nhảm liền không nói nhiều, trước cho mọi người giảng một cái, Khổng sư lần này tâm đắc nguồn gốc!”"),i("br"),i("br"),n._v("Giới thiệu xong bản thân, Ngũ sư thì nhìn quanh một tuần, bắt đầu giảng giải."),i("br"),i("br"),n._v("“Mọi người đều biết, Khổng sư vị trí thời kì, chính là Dị Linh tộc công chiếm đại lục, chiếm vị trí chủ đạo! Khi đó, nhân loại ăn bữa hôm lo bữa mai, sinh mệnh tràn ngập nguy hiểm. Sinh ra ở như vậy một thời đại, Khổng sư mặc dù đột phá Thánh vực, thành là thiên địa công nhận Thánh Nhân, vẫn như cũ lo lắng nhân tộc, muốn vì nhân tộc xuất lực... Bởi vậy, cái này tâm đắc, tuy có đột phá Thánh vực thích cảm giác, nhưng cũng có ưu quốc ưu dân, gian nan khổ cực thiên hạ tâm tình rất phức tạp, dung nhập trong đó!”"),i("br"),i("br"),n._v("“Ưu quốc ưu dân, gian nan khổ cực thiên hạ?” Trương Huyền nháy con mắt."),i("br"),i("br"),n._v("Khổng sư lưu lại thứ này, chỉ là vì nhắc nhở hậu nhân, tu luyện cần cẩn thận, không cần thiết lỗ mãng, lúc nào có loại cảm tình này... Cái này liên tưởng có chút quá rộng đi!"),i("br"),i("br"),n._v("Bất quá, suy nghĩ một chút cũng là giật mình, những này chuyên môn nghiên cứu “Học vấn gia”, bình thường đều sẽ đem thứ đơn giản, làm rất phức tạp, từ đó biểu hiện bản thân có học vấn, trên thực tế... Tác giả bản thân nhìn thấy, cũng sẽ một câu mmp, không rõ ràng cho lắm."),i("br"),i("br"),n._v("Tựa như kiếp trước, lỗ tấn đại văn hào một câu “Tại của ta hậu viên, có thể trông thấy ngoài tường có hai gốc cây, một gốc là cây táo, còn có một gốc cũng là cây táo.”"),i("br"),i("br"),n._v("Hậu thế đủ loại giải thích, nói hắn đầy đủ biểu hiện ra nhàm chán, cô tịch loại hình vân vân, đủ loại có tài văn chương, đủ loại lợi hại... Mà trên thực tế, chính là cái câu có vấn đề."),i("br"),i("br"),n._v("Trước mắt vị này Ngũ sư, rõ ràng cũng có cái này “Học vấn gia” tiềm chất."),i("br"),i("br"),n._v("“... Cho nên, mang theo loại cảm tình này lại đi xem bản này tâm đắc, liền có thể cái này chính thể sẽ Khổng sư tâm cảnh!”"),i("br"),i("br"),n._v("http://ngantruyen.com"),i("br"),n._v("/ Ngũ sư tiếp tục nói: “Tựa như câu này ‘Cơm sơ ăn uống nước, khom cánh tay mà gối chi, vui cũng ở trong đó vậy.’ Chính là bị Dị Linh tộc nhân chèn ép xuống, nhân tộc hình dáng chân thực khắc hoạ. Phiên dịch tới, chính là mặc dù ăn thô lương, uống nước lạnh, uốn lên cánh tay làm cái gối, nhưng Khổng sư vì nhân loại mà cố gắng, tự giải trí, cũng không cô đơn...”"),i("br"),i("br"),n._v("Thanh âm lang lãng, giảng giải trước mắt tâm đắc."),i("br"),i("br"),n._v("Nghe một hồi, Trương Huyền mí mắt xoay loạn."),i("br"),i("br"),n._v("Cũng không phải nói đối phương giảng sai, mà là, bọn họ đang nhìn tâm đắc thời điểm, trực tiếp đem Khổng sư cất cao đến ngước nhìn tình trạng, căn bản không theo bản chất xuất phát, hiểu như vậy, mặc dù cũng không tệ, nhưng cùng Khổng sư chân chính ý cảnh, càng ngày càng xa."),i("br"),i("br"),n._v("Vừa mới bắt đầu, còn cảm thấy cái tên này thất tinh danh sư đến cùng làm sao kiểm tra, hoàn toàn hữu danh vô thực, cẩn thận tính toán một cái, cũng là hiểu được."),i("br"),n._v(" Kiếp trước chuyên gia, liền chân thực chuyên gia ư?"),i("br"),i("br"),n._v("Cũng không có rất nhiều đầu óc bị côn trùng khoan sao?"),i("br"),i("br"),n._v("Danh sư đại lục, Khổng sư lực ảnh hưởng thực sự quá lớn, chỉ cần là danh sư, đều sẽ đem hắn xem như thần tượng, loại quan niệm này ăn sâu bén rễ, chỉ cần là hắn vật lưu lại, nhất định phải hướng cao đại thượng phương hướng đến nghiên cứu, mới có thể để cho vô số người tin phục."),i("br"),i("br"),n._v("Nếu không, chính là ly kinh phản đạo, không tôn trọng sư đạo."),i("br"),i("br"),n._v("Hắn không giống, nắm giữ Thiên Đạo thư viện, bất kỳ cái gì sự tình đều không mù quáng tin phục, lại thêm xuyên việt mà đến, đối Khổng sư không như thế lớn kính sợ cảm giác, cũng chính là cái gọi là ngoài cuộc tỉnh táo trong cuộc u mê, ngược lại so những người khác nhìn càng xa, hiểu rõ càng thêm rõ ràng."),i("br"),i("br"),n._v("Ầm ầm!"),i("br"),i("br"),n._v("Trong lòng đang cảm khái, phía trước một cái Tòng Thánh, dường như cảm ngộ cái gì, toàn thân một hồi oanh minh, tại chỗ đột phá."),i("br"),i("br"),n._v("Trương Huyền nháy con mắt."),i("br"),i("br"),n._v("Vốn cho rằng đối phương nói ý nghĩ hão huyền, khẳng định không có gì hiệu quả, không nghĩ tới thực sự có người đột phá."),i("br"),i("br"),n._v("“Ta biết rồi, là tín niệm!”"),i("br"),i("br"),n._v("Trong lòng hơi động, hiểu được."),i("br"),i("br"),n._v("Có lẽ, cái này Ngũ sư, cũng biết giải thích như vậy là không đúng, có chênh lệch chút ít cách ý tứ, nhưng danh sư đối Khổng sư đều có một loại sùng bái cùng kiên định tín niệm, chỉ cần đem loại này tín niệm mở rộng, có lẽ liền có thể đột phá ràng buộc."),i("br"),i("br"),n._v("Thánh vực cùng Tòng Thánh, chênh lệch không chỉ có là tu vi, còn có tâm cảnh!"),i("br"),i("br"),n._v("Khó trách cái này phong Thánh học hội hắn thấy rõ ràng rất xé, y nguyên có thể để cho vô số người duy trì sùng bái."),i("br"),i("br"),n._v("“Không sai, đã có người đột phá... Tốt, ta hiện tại liền cho mọi người giảng giải, cái thứ nhất khó có thể lý giải được câu ‘Tĩnh mà không lo, khô mà ý loạn. Bình chân như vại, chọn Tĩnh Di chi. Thỏa sức vô địch tiến lực lượng, cũng có thể phá đi...’, ý tứ rất đơn giản, chỉ cần tâm cảnh trầm ổn, yên tĩnh không lo, coi như chân khí không đủ, đồng dạng có thể đột phá...”"),i("br"),i("br"),n._v("Thấy có người đột phá, hài lòng vuốt râu, Ngũ sư tiếp tục giảng giải."),i("br"),i("br"),n._v("“Cái này...” Trương Huyền nhịn không được cười lên."),i("br"),i("br"),n._v("Khổng sư lưu lại câu nói này, bản thân liền có vấn đề, chính là thư viện nói tới chín cái thiếu hụt một trong."),i("br"),i("br"),n._v("Tâm cảnh tuy quan trọng, cũng phải cùng lực lượng tương dung, làm đến tri hành hợp nhất."),i("br"),i("br"),n._v("Chỉ có tâm cảnh đi đến, chân khí sẽ một cách tự nhiên vận chuyển, làm đến vô vi mà trị, mới có thể thuận theo tự nhiên đột phá."),i("br"),i("br"),n._v("Kỳ thật câu nói này, Khổng sư là động viên hậu bối, không cần vào xem lấy tu vi chân khí, còn cần trên tâm cảnh ma luyện, kết quả đến đối phương trong miệng, biến thành đột phá bí pháp..."),i("br"),i("br"),n._v("Khó trách... Rõ ràng có chín nơi sai lầm, lại đều không có người nhìn ra!"),i("br"),i("br"),n._v("Không phải những người này quá đần, mà là bọn họ đem Khổng sư nghĩ tới quá mức vĩ đại, từ đó xuyên tạc ý tứ."),i("br"),i("br"),n._v("“Vị bằng hữu này, tại hạ giảng giải có thể có vấn đề gì, để ngươi như thế bật cười?”"),i("br"),i("br"),n._v("Đang tại lắc đầu, cảm giác đối phương cái này lý giải không nghe cũng được, liền nghe đến một cái thanh âm nhàn nhạt vang lên."),i("br"),i("br"),n._v("Ngẩng đầu lên, chỉ thấy Ngũ sư mày nhăn lại, thẳng tắp nhìn lại."),i("br"),i("br"),n._v("Tất cả mọi người tập trung tinh thần nghe giảng, vị này nhưng ở nơi nào bật cười, để hắn tràn đầy không vui."),i("br"),i("br"),n._v("Mặc dù thất tinh danh sư có dung người chi lượng, nhưng nơi này là Phong Thánh đài Thánh địa, không cho người tùy ý khinh nhờn."),i("br"),i("br"),n._v("Không nghĩ tới, bị đối phương nhìn thấy, Trương Huyền mang theo xấu hổ, gãi đầu một cái, liền ôm quyền: “Tại hạ nghe được chỗ tinh diệu, có chút bừng tỉnh đại ngộ cảm giác, lúc này mới bật cười, chỗ thất lễ, mong được tha thứ!”"),i("br"),i("br"),n._v("Đối phương coi như sai, cùng bản thân cũng không có quan hệ gì, không muốn đi làm cái kia mục tiêu công kích."),i("br"),i("br"),n._v("“Bừng tỉnh đại ngộ? Ha ha, ngươi một cái Quy Nhất cảnh tiểu gia hỏa, nghe hiểu được ư?”"),i("br"),i("br"),n._v("“Khổng sư bản này tâm đắc, là nhằm vào Tòng Thánh cường giả, còn bừng tỉnh đại ngộ, ngươi biết giảng là cái gì không?”"),i("br"),i("br"),n._v("“Bừng tỉnh đại ngộ, đến, ngươi đột phá cái cho ta xem một chút...”"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Nghe được hắn, chung quanh rất nhiều người tu luyện, tất cả đều nở nụ cười."),i("br"),i("br"),n._v("Những người này đều là Tòng Thánh cường giả, cũng không dám nói có thể nghe hiểu, ngươi một cái Quy Nhất cảnh tiểu tử, còn nghe được chỗ tinh diệu... Thật là nói khoác không biết ngượng!"),i("br"),i("br"),n._v("Da mặt thế nào dày như vậy đâu?"),i("br"),i("br"),n._v("Thấy hắn nói như vậy, Ngũ sư cũng là nhướng mày: “Ồ? Nếu là chỗ tinh diệu, xin hỏi nơi nào tinh diệu? Không ngại nói ra, cùng mọi người nghiên cứu thảo luận một cái?”"),i("br"),i("br"),n._v("“Cái này... Ta liền không bêu xấu!” Trương Huyền liên tục vẫy tay."),i("br"),i("br"),n._v("“Ngươi ý tứ, là ta ở đây bêu xấu?” Ngũ sư vẻ mặt khó coi."),i("br"),i("br"),n._v("“Ta... Ta không phải ý tứ này?”"),i("br"),i("br"),n._v("Không nghĩ tới hắn lại như vậy lý giải, Trương Huyền ngây ngốc một chút, vội nói: “Ta nói là, ta trình độ thấp, nói ra cũng là làm trò hề cho thiên hạ, còn không bằng không nói...”"),i("br"),i("br"),n._v("“Ngươi nghe ta khóa, có điều tâm đắc, nói ra là làm trò hề cho thiên hạ, ý của ngươi là của ta khóa... Không đáng giá nhắc tới, làm trò hề cho thiên hạ?” Ngũ sư khẽ nói."),i("br"),i("br"),n._v("“Ngươi hiểu lầm, ta là nói mình trình độ thấp...”"),i("br"),i("br"),n._v("Trương Huyền lắc đầu."),i("br"),i("br"),n._v("“Trình độ thấp, nhưng có thể nghe được chỗ tinh diệu, kìm lòng không được bật cười, vậy ta khóa chẳng phải trình độ càng thấp hơn?” Ngũ sư lông mày vung lên."),i("br"),i("br"),n._v("Convert by: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-843"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-845"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);